exports.isaiah =
  { 1:  { 1:  [ {class: "vs", text: "The vision of Isaiah the son of Amoz, which he saw concerning Judah and Jerusalem, in the days of Uzziah, Jotham, Ahaz, and Hezekiah, kings of Judah."} ]
        , 2:  [ {class: "q", text: "Hear, heavens,"}
              , {class: "q2", text: "and listen, earth; for Yahweh has spoken:"}
              , {class: "q", text: "“I have nourished and brought up children"}
              , {class: "q2", text: "and they have rebelled against me."}
              ]
        , 3:  [ {class: "q", text: "The ox knows his owner,"}
              , {class: "q2", text: "and the donkey his master’s crib;"}
              , {class: "q2", text: "but Israel doesn’t know."}
              , {class: "q2", text: "My people don’t consider.”"}
              ]
        , 4:  [ {class: "q", text: "Ah sinful nation,"}
              , {class: "q2", text: "a people loaded with iniquity,"}
              , {class: "q2", text: "offspring of evildoers,"}
              , {class: "q2", text: "children who deal corruptly!"}
              , {class: "q", text: "They have forsaken Yahweh."}
              , {class: "q2", text: "They have despised the Holy One of Israel."}
              , {class: "q2", text: "They are estranged and backward."}
              ]
        , 5:  [ {class: "q", text: "Why should you be beaten more,"}
              , {class: "q2", text: "that you revolt more and more?"}
              , {class: "q", text: "The whole head is sick,"}
              , {class: "q2", text: "and the whole heart faint."}
              ]
        , 6:  [ {class: "q", text: "From the sole of the foot even to the head there is no soundness in it:"}
              , {class: "q2", text: "wounds, welts, and open sores."}
              , {class: "q2", text: "They haven’t been closed, bandaged, or soothed with oil."}
              ]
        , 7:  [ {class: "q", text: "Your country is desolate."}
              , {class: "q2", text: "Your cities are burned with fire."}
              , {class: "q2", text: "Strangers devour your land in your presence"}
              , {class: "q2", text: "and it is desolate,"}
              , {class: "q2", text: "as overthrown by strangers."}
              ]
        , 8:  [ {class: "q", text: "The daughter of Zion is left like a shelter in a vineyard,"}
              , {class: "q2", text: "like a hut in a field of melons,"}
              , {class: "q2", text: "like a besieged city."}
              ]
        , 9:  [ {class: "q", text: "Unless Yahweh of Armies had left to us a very small remnant,"}
              , {class: "q2", text: "we would have been as Sodom."}
              , {class: "q2", text: "We would have been like Gomorrah."}
              ]
        , 10: [ {class: "q", text: "Hear Yahweh’s word, you rulers of Sodom!"}
              , {class: "q2", text: "Listen to the law of our God, you people of Gomorrah!"}
              ]
        , 11: [ {class: "q", text: "“What are the multitude of your sacrifices to me?”, says Yahweh."}
              , {class: "q2", text: "“I have had enough of the burnt offerings of rams"}
              , {class: "q2", text: "and the fat of fed animals."}
              , {class: "q2", text: "I don’t delight in the blood of bulls,"}
              , {class: "q2", text: "or of lambs,"}
              , {class: "q2", text: "or of male goats."}
              ]
        , 12: [ {class: "q", text: "When you come to appear before me,"}
              , {class: "q2", text: "who has required this at your hand, to trample my courts?"}
              ]
        , 13: [ {class: "q", text: "Bring no more vain offerings."}
              , {class: "q2", text: "Incense is an abomination to me."}
              , {class: "q2", text: "New moons, Sabbaths, and convocations:"}
              , {class: "q2", text: "I can’t stand evil assemblies."}
              ]
        , 14: [ {class: "q", text: "My soul hates your New Moons and your appointed feasts."}
              , {class: "q2", text: "They are a burden to me."}
              , {class: "q2", text: "I am weary of bearing them."}
              ]
        , 15: [ {class: "q", text: "When you spread out your hands, I will hide my eyes from you."}
              , {class: "q2", text: "Yes, when you make many prayers, I will not hear."}
              , {class: "q2", text: "Your hands are full of blood."}
              ]
        , 16: [ {class: "q", text: "Wash yourselves. Make yourself clean."}
              , {class: "q2", text: "Put away the evil of your doings from before my eyes."}
              , {class: "q2", text: "Cease to do evil."}
              ]
        , 17: [ {class: "q", text: "Learn to do well."}
              , {class: "q2", text: "Seek justice."}
              , {class: "q2", text: "Relieve the oppressed."}
              , {class: "q", text: "Defend the fatherless."}
              , {class: "q2", text: "Plead for the widow.”"}
              ]
        , 18: [ {class: "q", text: "“Come now, and let’s reason together,” says Yahweh:"}
              , {class: "q2", text: "“Though your sins are as scarlet, they shall be as white as snow."}
              , {class: "q2", text: "Though they are red like crimson, they shall be as wool."}
              ]
        , 19: [ {class: "q", text: "If you are willing and obedient,"}
              , {class: "q2", text: "you will eat the good of the land;"}
              ]
        , 20: [ {class: "q2", text: "but if you refuse and rebel, you will be devoured with the sword;"}
              , {class: "q2", text: "for the mouth of Yahweh has spoken it.”"}
              ]
        , 21: [ {class: "q", text: "How the faithful city has become a prostitute!"}
              , {class: "q2", text: "She was full of justice."}
              , {class: "q", text: "Righteousness lodged in her,"}
              , {class: "q2", text: "but now there are murderers."}
              ]
        , 22: [ {class: "q", text: "Your silver has become dross,"}
              , {class: "q2", text: "your wine mixed with water."}
              ]
        , 23: [ {class: "q", text: "Your princes are rebellious and companions of thieves."}
              , {class: "q2", text: "Everyone loves bribes and follows after rewards."}
              , {class: "q2", text: "They don’t defend the fatherless,"}
              , {class: "q2", text: "neither does the cause of the widow come to them."}
              ]
        , 24: [ {class: "q", text: "Therefore the Lord, Yahweh of Armies,"}
              , {class: "q2", text: "the Mighty One of Israel, says:"}
              , {class: "q", text: "“Ah, I will get relief from my adversaries,"}
              , {class: "q2", text: "and avenge myself on my enemies."}
              ]
        , 25: [ {class: "q", text: "I will turn my hand on you,"}
              , {class: "q2", text: "thoroughly purge away your dross,"}
              , {class: "q2", text: "and will take away all your tin."}
              ]
        , 26: [ {class: "q", text: "I will restore your judges as at the first,"}
              , {class: "q2", text: "and your counselors as at the beginning."}
              , {class: "q", text: "Afterward you shall be called ‘The city of righteousness,"}
              , {class: "q2", text: "a faithful town.’"}
              ]
        , 27: [ {class: "q", text: "Zion shall be redeemed with justice,"}
              , {class: "q2", text: "and her converts with righteousness."}
              ]
        , 28: [ {class: "q", text: "But the destruction of transgressors and sinners shall be together,"}
              , {class: "q2", text: "and those who forsake Yahweh shall be consumed."}
              ]
        , 29: [ {class: "q", text: "For they shall be ashamed of the oaks which you have desired,"}
              , {class: "q2", text: "and you shall be confounded for the gardens that you have chosen."}
              ]
        , 30: [ {class: "q", text: "For you shall be as an oak whose leaf fades,"}
              , {class: "q2", text: "and as a garden that has no water."}
              ]
        , 31: [ {class: "q", text: "The strong will be like tinder,"}
              , {class: "q2", text: "and his work like a spark."}
              , {class: "q", text: "They will both burn together,"}
              , {class: "q2", text: "and no one will quench them.”"}
              ]
        }
  , 10: { 1:  [ {class: "vs", text: "Woe to those who decree unrighteous decrees, and to the writers who write oppressive decrees;"} ]
        , 2:  [ {class: "vs", text: "to deprive the needy from justice, and to rob the poor among my people of their rights, that widows may be their plunder, and that they may make the fatherless their prey!"} ]
        , 3:  [ {class: "vs", text: "What will you do in the day of visitation, and in the desolation which will come from afar? To whom will you flee for help? Where will you leave your wealth?"} ]
        , 4:  [ {class: "endpara", text: "They will only bow down under the prisoners,"}
              , {class: "q2", text: "and will fall under the slain."}
              , {class: "q", text: "For all this his anger is not turned away,"}
              , {class: "q2", text: "but his hand is stretched out still."}
              ]
        , 5:  [ {class: "vs", text: "Alas Assyrian, the rod of my anger, the staff in whose hand is my indignation!"} ]
        , 6:  [ {class: "vs", text: "I will send him against a profane nation, and against the people who anger me I will give him a command to take the plunder and to take the prey, and to tread them down like the mire of the streets."} ]
        , 7:  [ {class: "vs", text: "However he doesn’t mean so, neither does his heart think so; but it is in his heart to destroy, and to cut off not a few nations."} ]
        , 8:  [ {class: "vs", text: "For he says, “Aren’t all of my princes kings?"} ]
        , 9:  [ {class: "vs", text: "Isn’t Calno like Carchemish? Isn’t Hamath like Arpad? Isn’t Samaria like Damascus?”"} ]
        , 10: [ {class: "vs", text: "As my hand has found the kingdoms of the idols, whose engraved images exceeded those of Jerusalem and of Samaria;"} ]
        , 11: [ {class: "vs", text: "shall I not, as I have done to Samaria and her idols, so do to Jerusalem and her idols?"} ]
        , 12: [ {class: "vs", text: "Therefore it will happen that when the Lord has performed his whole work on Mount Zion and on Jerusalem, I will punish the fruit of the willful proud heart of the king of Assyria, and the insolence of his arrogant looks."} ]
        , 13: [ {class: "vs", text: "For he has said, “By the strength of my hand I have done it, and by my wisdom; for I have understanding. I have removed the boundaries of the peoples, and have robbed their treasures. Like a valiant man I have brought down their rulers."} ]
        , 14: [ {class: "vs", text: "My hand has found the riches of the peoples like a nest, and like one gathers eggs that are abandoned, I have gathered all the earth. There was no one who moved their wing, or that opened their mouth, or chirped.”"} ]
        , 15: [ {class: "vs", text: "Should an ax brag against him who chops with it? Should a saw exalt itself above him who saws with it? As if a rod should lift those who lift it up, or as if a staff should lift up someone who is not wood."} ]
        , 16: [ {class: "vs", text: "Therefore the Lord, Yahweh of Armies, will send among his fat ones leanness; and under his glory a burning will be kindled like the burning of fire."} ]
        , 17: [ {class: "vs", text: "The light of Israel will be for a fire, and his Holy One for a flame; and it will burn and devour his thorns and his briers in one day."} ]
        , 18: [ {class: "vs", text: "He will consume the glory of his forest, and of his fruitful field, both soul and body. It will be as when a standard bearer faints."} ]
        , 19: [ {class: "vs", text: "The remnant of the trees of his forest shall be few, so that a child could write their number."} ]
        , 20: [ {class: "vs", text: "It will come to pass in that day that the remnant of Israel, and those who have escaped from the house of Jacob will no more again lean on him who struck them, but shall lean on Yahweh, the Holy One of Israel, in truth."} ]
        , 21: [ {class: "vs", text: "A remnant will return, even the remnant of Jacob, to the mighty God."} ]
        , 22: [ {class: "vs", text: "For though your people, Israel, are like the sand of the sea, only a remnant of them will return. A destruction is determined, overflowing with righteousness."} ]
        , 23: [ {class: "vs", text: "For the Lord, Yahweh of Armies, will make a full end, and that determined, throughout all the earth."} ]
        , 24: [ {class: "vs", text: "Therefore the Lord, Yahweh of Armies, says “My people who dwell in Zion, don’t be afraid of the Assyrian, though he strike you with the rod, and lift up his staff against you, as Egypt did."} ]
        , 25: [ {class: "vs", text: "For yet a very little while, and the indignation against you will be accomplished, and my anger will be directed to his destruction.”"} ]
        , 26: [ {class: "vs", text: "Yahweh of Armies will stir up a scourge against him, as in the slaughter of Midian at the rock of Oreb. His rod will be over the sea, and he will lift it up like he did against Egypt."} ]
        , 27: [ {class: "vs", text: "It will happen in that day that his burden will depart from off your shoulder, and his yoke from off your neck, and the yoke shall be destroyed because of the anointing oil."} ]
        , 28: [ {class: "vs", text: "He has come to Aiath. He has passed through Migron. At Michmash he stores his baggage."} ]
        , 29: [ {class: "vs", text: "They have gone over the pass. They have taken up their lodging at Geba. Ramah trembles. Gibeah of Saul has fled."} ]
        , 30: [ {class: "vs", text: "Cry aloud with your voice, daughter of Gallim! Listen, Laishah! You poor Anathoth!"} ]
        , 31: [ {class: "vs", text: "Madmenah is a fugitive. The inhabitants of Gebim flee for safety."} ]
        , 32: [ {class: "vs", text: "This very day he will halt at Nob. He shakes his hand at the mountain of the daughter of Zion, the hill of Jerusalem."} ]
        , 33: [ {class: "vs", text: "Behold, the Lord, Yahweh of Armies, will lop the boughs with terror. The tall will be cut down, and the lofty will be brought low."} ]
        , 34: [ {class: "vs", text: "He will cut down the thickets of the forest with iron, and Lebanon will fall by the Mighty One."} ]
        }
  , 11: { 1:  [ {class: "q", text: "A shoot will come out of the stock of Jesse,"}
              , {class: "q2", text: "and a branch out of his roots will bear fruit."}
              ]
        , 2:  [ {class: "q", text: "Yahweh’s Spirit will rest on him:"}
              , {class: "q2", text: "the spirit of wisdom and understanding,"}
              , {class: "q2", text: "the spirit of counsel and might,"}
              , {class: "q2", text: "the spirit of knowledge and of the fear of Yahweh."}
              ]
        , 3:  [ {class: "q", text: "His delight will be in the fear of Yahweh."}
              , {class: "q", text: "He will not judge by the sight of his eyes,"}
              , {class: "q2", text: "neither decide by the hearing of his ears;"}
              ]
        , 4:  [ {class: "q", text: "but he will judge the poor with righteousness,"}
              , {class: "q2", text: "and decide with equity for the humble of the earth."}
              , {class: "q", text: "He will strike the earth with the rod of his mouth;"}
              , {class: "q2", text: "and with the breath of his lips he will kill the wicked."}
              ]
        , 5:  [ {class: "q", text: "Righteousness will be the belt of his waist,"}
              , {class: "q2", text: "and faithfulness the belt of his waist."}
              ]
        , 6:  [ {class: "q", text: "The wolf will live with the lamb,"}
              , {class: "q2", text: "and the leopard will lie down with the young goat,"}
              , {class: "q2", text: "the calf, the young lion, and the fattened calf together;"}
              , {class: "q2", text: "and a little child will lead them."}
              ]
        , 7:  [ {class: "q", text: "The cow and the bear will graze."}
              , {class: "q2", text: "Their young ones will lie down together."}
              , {class: "q2", text: "The lion will eat straw like the ox."}
              ]
        , 8:  [ {class: "q", text: "The nursing child will play near a cobra’s hole,"}
              , {class: "q2", text: "and the weaned child will put his hand on the viper’s den."}
              ]
        , 9:  [ {class: "q", text: "They will not hurt nor destroy in all my holy mountain;"}
              , {class: "q2", text: "for the earth will be full of the knowledge of Yahweh,"}
              , {class: "q2", text: "as the waters cover the sea."}
              ]
        , 10: [ {class: "vs", text: "It will happen in that day that the nations will seek the root of Jesse, who stands as a banner of the peoples; and his resting place will be glorious."} ]
        , 11: [ {class: "vs", text: "It will happen in that day that the Lord will set his hand again the second time to recover the remnant that is left of his people from Assyria, from Egypt, from Pathros, from Cush, from Elam, from Shinar, from Hamath, and from the islands of the sea."} ]
        , 12: [ {class: "vs", text: "He will set up a banner for the nations, and will assemble the outcasts of Israel, and gather together the dispersed of Judah from the four corners of the earth."} ]
        , 13: [ {class: "vs", text: "The envy also of Ephraim will depart, and those who persecute Judah will be cut off. Ephraim won’t envy Judah, and Judah won’t persecute Ephraim."} ]
        , 14: [ {class: "vs", text: "They will fly down on the shoulders of the Philistines on the west. Together they will plunder the children of the east. They will extend their power over Edom and Moab, and the children of Ammon will obey them."} ]
        , 15: [ {class: "vs", text: "Yahweh will utterly destroy the tongue of the Egyptian sea; and with his scorching wind he will wave his hand over the River, and will split it into seven streams, and cause men to march over in sandals."} ]
        , 16: [ {class: "vs", text: "There will be a highway for the remnant that is left of his people from Assyria, like there was for Israel in the day that he came up out of the land of Egypt."} ]
        }
  , 12: { 1:  [ {class: "vs", text: "In that day you will say, “I will give thanks to you, Yahweh; for though you were angry with me, your anger has turned away and you comfort me."} ]
        , 2:  [ {class: "vs", text: "Behold, God is my salvation. I will trust, and will not be afraid; for Yah, Yahweh, is my strength and song; and he has become my salvation.”"} ]
        , 3:  [ {class: "vs", text: "Therefore with joy you will draw water out of the wells of salvation."} ]
        , 4:  [ {class: "vs", text: "In that day you will say, “Give thanks to Yahweh! Call on his name. Declare his doings among the peoples. Proclaim that his name is exalted!"} ]
        , 5:  [ {class: "vs", text: "Sing to Yahweh, for he has done excellent things! Let this be known in all the earth!"} ]
        , 6:  [ {class: "vs", text: "Cry aloud and shout, you inhabitant of Zion; for the Holy One of Israel is great among you!”"} ]
        }
  , 13: { 1:  [ {class: "vs", text: "The burden of Babylon, which Isaiah the son of Amoz saw."} ]
        , 2:  [ {class: "vs", text: "Set up a banner on the bare mountain! Lift up your voice to them! Wave your hand, that they may go into the gates of the nobles."} ]
        , 3:  [ {class: "vs", text: "I have commanded my consecrated ones; yes, I have called my mighty men for my anger, even my proudly exulting ones."} ]
        , 4:  [ {class: "vs", text: "The noise of a multitude is in the mountains, as of a great people; the noise of an uproar of the kingdoms of the nations gathered together! Yahweh of Armies is mustering the army for the battle."} ]
        , 5:  [ {class: "vs", text: "They come from a far country, from the uttermost part of heaven, even Yahweh, and the weapons of his indignation, to destroy the whole land."} ]
        , 6:  [ {class: "vs", text: "Wail, for Yahweh’s day is at hand! It will come as destruction from the Almighty."} ]
        , 7:  [ {class: "vs", text: "Therefore all hands will be feeble, and everyone’s heart will melt."} ]
        , 8:  [ {class: "vs", text: "They will be dismayed. Pangs and sorrows will seize them. They will be in pain like a woman in labor. They will look in amazement one at another. Their faces will be faces of flame."} ]
        , 9:  [ {class: "vs", text: "Behold, the day of Yahweh comes, cruel, with wrath and fierce anger; to make the land a desolation, and to destroy its sinners out of it."} ]
        , 10: [ {class: "vs", text: "For the stars of the sky and its constellations will not give their light. The sun will be darkened in its going out, and the moon will not cause its light to shine."} ]
        , 11: [ {class: "vs", text: "I will punish the world for their evil, and the wicked for their iniquity. I will cause the arrogance of the proud to cease, and will humble the arrogance of the terrible."} ]
        , 12: [ {class: "vs", text: "I will make people more rare than fine gold, even a person than the pure gold of Ophir."} ]
        , 13: [ {class: "vs", text: "Therefore I will make the heavens tremble, and the earth will be shaken out of its place in Yahweh of Armies’ wrath, and in the day of his fierce anger."} ]
        , 14: [ {class: "vs", text: "It will happen that like a hunted gazelle, and like sheep that no one gathers, they will each turn to their own people, and will each flee to their own land."} ]
        , 15: [ {class: "vs", text: "Everyone who is found will be thrust through. Everyone who is captured will fall by the sword."} ]
        , 16: [ {class: "vs", text: "Their infants also will be dashed in pieces before their eyes. Their houses will be ransacked, and their wives raped."} ]
        , 17: [ {class: "vs", text: "Behold, I will stir up the Medes against them, who will not value silver, and as for gold, they will not delight in it."} ]
        , 18: [ {class: "vs", text: "Their bows will dash the young men in pieces; and they shall have no pity on the fruit of the womb. Their eyes will not spare children."} ]
        , 19: [ {class: "vs", text: "Babylon, the glory of kingdoms, the beauty of the Chaldeans’ pride, will be like when God overthrew Sodom and Gomorrah."} ]
        , 20: [ {class: "vs", text: "It will never be inhabited, neither will it be lived in from generation to generation. The Arabian will not pitch a tent there, neither will shepherds make their flocks lie down there."} ]
        , 21: [ {class: "vs", text: "But wild animals of the desert will lie there, and their houses will be full of jackals. Ostriches will dwell there, and wild goats will frolic there."} ]
        , 22: [ {class: "vs", text: "Wolves will cry in their fortresses, and jackals in the pleasant palaces. Her time is near to come, and her days will not be prolonged."} ]
        }
  , 14: { 1:  [ {class: "vs", text: "For Yahweh will have compassion on Jacob, and will yet choose Israel, and set them in their own land. The foreigner will join himself with them, and they will unite with the house of Jacob."} ]
        , 2:  [ {class: "vs", text: "The peoples will take them, and bring them to their place. The house of Israel will possess them in Yahweh’s land for servants and for handmaids. They will take as captives those whose captives they were; and they shall rule over their oppressors."} ]
        , 3:  [ {class: "vs", text: "It will happen in the day that Yahweh will give you rest from your sorrow, from your trouble, and from the hard service in which you were made to serve,"} ]
        , 4:  [ {class: "vs", text: "that you will take up this parable against the king of Babylon, and say, “How the oppressor has ceased! The golden city has ceased!”"} ]
        , 5:  [ {class: "vs", text: "Yahweh has broken the staff of the wicked, the scepter of the rulers,"} ]
        , 6:  [ {class: "vs", text: "who struck the peoples in wrath with a continual stroke, who ruled the nations in anger, with a persecution that no one restrained."} ]
        , 7:  [ {class: "vs", text: "The whole earth is at rest, and is quiet. They break out in song."} ]
        , 8:  [ {class: "vs", text: "Yes, the cypress trees rejoice with you, with the cedars of Lebanon, saying, “Since you are humbled, no lumberjack has come up against us.”"} ]
        , 9:  [ {class: "vs", text: "Sheol from beneath has moved for you to meet you at your coming. It stirs up the departed spirits for you, even all the rulers of the earth. It has raised up from their thrones all the kings of the nations."} ]
        , 10: [ {class: "vs", text: "They all will answer and ask you, “Have you also become as weak as we are? Have you become like us?”"} ]
        , 11: [ {class: "vs", text: "Your pomp is brought down to Sheol, with the sound of your stringed instruments. Maggots are spread out under you, and worms cover you."} ]
        , 12: [ {class: "vs", text: "How you have fallen from heaven, shining one, son of the dawn! How you are cut down to the ground, who laid the nations low!"} ]
        , 13: [ {class: "vs", text: "You said in your heart, “I will ascend into heaven! I will exalt my throne above the stars of God! I will sit on the mountain of assembly, in the far north!"} ]
        , 14: [ {class: "vs", text: "I will ascend above the heights of the clouds! I will make myself like the Most High!”"} ]
        , 15: [ {class: "vs", text: "Yet you shall be brought down to Sheol, to the depths of the pit."} ]
        , 16: [ {class: "vs", text: "Those who see you will stare at you. They will ponder you, saying, “Is this the man who made the earth to tremble, who shook kingdoms,"} ]
        , 17: [ {class: "vs", text: "who made the world like a wilderness, and overthrew its cities, who didn’t release his prisoners to their home?”"} ]
        , 18: [ {class: "vs", text: "All the kings of the nations, sleep in glory, everyone in his own house."} ]
        , 19: [ {class: "vs", text: "But you are cast away from your tomb like an abominable branch, clothed with the slain, who are thrust through with the sword, who go down to the stones of the pit; like a dead body trodden under foot."} ]
        , 20: [ {class: "vs", text: "You will not join them in burial, because you have destroyed your land. You have killed your people. The offspring of evildoers will not be named forever."} ]
        , 21: [ {class: "vs", text: "Prepare for slaughter of his children because of the iniquity of their fathers, that they not rise up and possess the earth, and fill the surface of the world with cities."} ]
        , 22: [ {class: "vs", text: "“I will rise up against them,” says Yahweh of Armies, “and cut off from Babylon name and remnant, and son and son’s son,” says Yahweh."} ]
        , 23: [ {class: "vs", text: "“I will also make it a possession for the porcupine, and pools of water. I will sweep it with the broom of destruction,” says Yahweh of Armies."} ]
        , 24: [ {class: "vs", text: "Yahweh of Armies has sworn, saying, “Surely, as I have thought, so shall it happen; and as I have purposed, so shall it stand:"} ]
        , 25: [ {class: "vs", text: "that I will break the Assyrian in my land, and tread him under foot on my mountains. Then his yoke will leave them, and his burden leave their shoulders."} ]
        , 26: [ {class: "vs", text: "This is the plan that is determined for the whole earth. This is the hand that is stretched out over all the nations."} ]
        , 27: [ {class: "vs", text: "For Yahweh of Armies has planned, and who can stop it? His hand is stretched out, and who can turn it back?”"} ]
        , 28: [ {class: "vs", text: "This burden was in the year that king Ahaz died."} ]
        , 29: [ {class: "vs", text: "Don’t rejoice, O Philistia, all of you, because the rod that struck you is broken; for out of the serpent’s root an adder will emerge, and his fruit will be a fiery flying serpent."} ]
        , 30: [ {class: "vs", text: "The firstborn of the poor will eat, and the needy will lie down in safety; and I will kill your root with famine, and your remnant will be killed."} ]
        , 31: [ {class: "vs", text: "Howl, gate! Cry, city! You are melted away, Philistia, all of you; for smoke comes out of the north, and there is no straggler in his ranks."} ]
        , 32: [ {class: "vs", text: "What will they answer the messengers of the nation? That Yahweh has founded Zion, and in her the afflicted of his people will take refuge."} ]
        }
  , 15: { 1:  [ {class: "vs", text: "The burden of Moab.</p><p> For in a night, Ar of Moab is laid waste, and brought to nothing. For in a night Kir of Moab is laid waste, and brought to nothing."} ]
        , 2:  [ {class: "vs", text: "They have gone up to Bayith, and to Dibon, to the high places, to weep. Moab wails over Nebo and over Medeba. Baldness is on all of their heads. Every beard is cut off."} ]
        , 3:  [ {class: "vs", text: "In their streets, they clothe themselves in sackcloth. In their streets and on their housetops, everyone wails, weeping abundantly."} ]
        , 4:  [ {class: "vs", text: "Heshbon cries out with Elealeh. Their voice is heard even to Jahaz. Therefore the armed men of Moab cry aloud. Their souls tremble within them."} ]
        , 5:  [ {class: "vs", text: "My heart cries out for Moab! Her nobles flee to Zoar, to Eglath Shelishiyah; for they go up by the ascent of Luhith with weeping; for on the way to Horonaim, they raise up a cry of destruction."} ]
        , 6:  [ {class: "vs", text: "For the waters of Nimrim will be desolate; for the grass has withered away, the tender grass fails, there is no green thing."} ]
        , 7:  [ {class: "vs", text: "Therefore they will carry away the abundance they have gotten, and that which they have stored up, over the brook of the willows."} ]
        , 8:  [ {class: "vs", text: "For the cry has gone around the borders of Moab, its wailing to Eglaim, and its wailing to Beer Elim."} ]
        , 9:  [ {class: "vs", text: "For the waters of Dimon are full of blood; for I will bring yet more on Dimon, a lion on those of Moab who escape, and on the remnant of the land."} ]
        }
  , 16: { 1:  [ {class: "endpara", text: "Send the lambs for the ruler of"}
              , {class: "qs", text: "to the wilderness, to the mountain of the daughter of Zion."}
              ]
        , 2:  [ {class: "vs", text: "For it will be that as wandering birds, as a scattered nest, so will the daughters of Moab be at the fords of the Arnon."} ]
        , 3:  [ {class: "vs", text: "Give counsel! Execute justice! Make your shade like the night in the middle of the noonday! Hide the outcasts! Don’t betray the fugitive!"} ]
        , 4:  [ {class: "vs", text: "Let my outcasts dwell with you! As for Moab, be a hiding place for him from the face of the destroyer. For the extortionist is brought to nothing. Destruction ceases. The oppressors are consumed out of the land."} ]
        , 5:  [ {class: "vs", text: "A throne will be established in loving kindness. One will sit on it in truth, in the tent of David, judging, seeking justice, and swift to do righteousness."} ]
        , 6:  [ {class: "vs", text: "We have heard of the pride of Moab, that he is very proud; even of his arrogance, his pride, and his wrath. His boastings are nothing."} ]
        , 7:  [ {class: "vs", text: "Therefore Moab will wail for Moab. Everyone will wail. You will mourn for the raisin cakes of Kir Hareseth, utterly stricken."} ]
        , 8:  [ {class: "vs", text: "For the fields of Heshbon languish with the vine of Sibmah. The lords of the nations have broken down its choice branches, which reached even to Jazer, which wandered into the wilderness. Its shoots were spread abroad. They passed over the sea."} ]
        , 9:  [ {class: "vs", text: "Therefore I will weep with the weeping of Jazer for the vine of Sibmah. I will water you with my tears, Heshbon, and Elealeh: for on your summer fruits and on your harvest the battle shout has fallen."} ]
        , 10: [ {class: "vs", text: "Gladness is taken away, and joy out of the fruitful field; and in the vineyards there will be no singing, neither joyful noise. Nobody will tread out wine in the presses. I have made the shouting stop."} ]
        , 11: [ {class: "vs", text: "Therefore my heart sounds like a harp for Moab, and my inward parts for Kir Heres."} ]
        , 12: [ {class: "vs", text: "It will happen that when Moab presents himself, when he wearies himself on the high place, and comes to his sanctuary to pray, that he will not prevail."} ]
        , 13: [ {class: "vs", text: "This is the word that Yahweh spoke concerning Moab in time past."} ]
        , 14: [ {class: "vs", text: "But now Yahweh has spoken, saying, “Within three years, as a worker bound by contract would count them, the glory of Moab shall be brought into contempt, with all his great multitude; and the remnant will be very small and feeble.”"} ]
        }
  , 17: { 1:  [ {class: "vs", text: "The burden of Damascus.</p><p> “Behold, Damascus is taken away from being a city, and it will be a ruinous heap."} ]
        , 2:  [ {class: "vs", text: "The cities of Aroer are forsaken. They will be for flocks, which shall lie down, and no one shall make them afraid."} ]
        , 3:  [ {class: "vs", text: "The fortress shall cease from Ephraim, and the kingdom from Damascus, and the remnant of Syria. They will be as the glory of the children of Israel,” says Yahweh of Armies."} ]
        , 4:  [ {class: "vs", text: "“It will happen in that day that the glory of Jacob will be made thin, and the fatness of his flesh will become lean."} ]
        , 5:  [ {class: "vs", text: "It will be like when the harvester gathers the wheat, and his arm reaps the grain. Yes, it will be like when one gleans grain in the valley of Rephaim."} ]
        , 6:  [ {class: "vs", text: "Yet gleanings will be left there, like the shaking of an olive tree, two or three olives in the top of the uppermost bough, four or five in the outermost branches of a fruitful tree,” says Yahweh, the God of Israel."} ]
        , 7:  [ {class: "vs", text: "In that day, people will look to their Maker, and their eyes will have respect for the Holy One of Israel."} ]
        , 8:  [ {class: "vs", text: "They will not look to the altars, the work of their hands; neither shall they respect that which their fingers have made, either the Asherah poles, or the incense altars."} ]
        , 9:  [ {class: "vs", text: "In that day, their strong cities will be like the forsaken places in the woods and on the mountain top, which were forsaken from before the children of Israel; and it will be a desolation."} ]
        , 10: [ {class: "vs", text: "For you have forgotten the God of your salvation, and have not remembered the rock of your strength. Therefore you plant pleasant plants, and set out foreign seedlings."} ]
        , 11: [ {class: "vs", text: "In the day of your planting, you hedge it in. In the morning, you make your seed blossom, but the harvest flees away in the day of grief and of desperate sorrow."} ]
        , 12: [ {class: "vs", text: "Ah, the uproar of many peoples, who roar like the roaring of the seas; and the rushing of nations, that rush like the rushing of mighty waters!"} ]
        , 13: [ {class: "vs", text: "The nations will rush like the rushing of many waters: but he will rebuke them, and they will flee far off, and will be chased like the chaff of the mountains before the wind, and like the whirling dust before the storm."} ]
        , 14: [ {class: "vs", text: "At evening, behold, terror! Before the morning, they are no more. This is the portion of those who plunder us, and the lot of those who rob us."} ]
        }
  , 18: { 1:  [ {class: "vs", text: "Ah, the land of the rustling of wings, which is beyond the rivers of Ethiopia;"} ]
        , 2:  [ {class: "vs", text: "that sends ambassadors by the sea, even in vessels of papyrus on the waters, saying, “Go, you swift messengers, to a nation tall and smooth, to a people awesome from their beginning onward, a nation that measures out and treads down, whose land the rivers divide!”"} ]
        , 3:  [ {class: "vs", text: "All you inhabitants of the world, and you dwellers on the earth, when a banner is lifted up on the mountains, look! When the trumpet is blown, listen!"} ]
        , 4:  [ {class: "vs", text: "For Yahweh said to me, “I will be still, and I will see in my dwelling place, like clear heat in sunshine, like a cloud of dew in the heat of harvest.”"} ]
        , 5:  [ {class: "vs", text: "For before the harvest, when the blossom is over, and the flower becomes a ripening grape, he will cut off the sprigs with pruning hooks, and he will cut down and take away the spreading branches."} ]
        , 6:  [ {class: "vs", text: "They will be left together for the ravenous birds of the mountains, and for the animals of the earth. The ravenous birds will eat them in the summer, and all the animals of the earth will eat them in the winter."} ]
        , 7:  [ {class: "vs", text: "In that time, a present will be brought to Yahweh of Armies from a people tall and smooth, even from a people awesome from their beginning onward, a nation that measures out and treads down, whose land the rivers divide, to the place of the name of Yahweh of Armies, Mount Zion."} ]
        }
  , 19: { 1:  [ {class: "vs", text: "The burden of Egypt.</p><p> “Behold, Yahweh rides on a swift cloud, and comes to Egypt. The idols of Egypt will tremble at his presence; and the heart of Egypt will melt within it."} ]
        , 2:  [ {class: "vs", text: "I will stir up the Egyptians against the Egyptians, and they will fight everyone against his brother, and everyone against his neighbor; city against city, and kingdom against kingdom."} ]
        , 3:  [ {class: "vs", text: "The spirit of Egypt will fail within it. I will destroy its counsel. They will seek the idols, the charmers, those who have familiar spirits, and the wizards."} ]
        , 4:  [ {class: "vs", text: "I will give over the Egyptians into the hand of a cruel lord. A fierce king will rule over them,” says the Lord, Yahweh of Armies."} ]
        , 5:  [ {class: "vs", text: "The waters will fail from the sea, and the river will be wasted and become dry."} ]
        , 6:  [ {class: "vs", text: "The rivers will become foul. The streams of Egypt will be diminished and dried up. The reeds and flags will wither away."} ]
        , 7:  [ {class: "vs", text: "The meadows by the Nile, by the brink of the Nile, and all the sown fields of the Nile, will become dry, be driven away, and be no more."} ]
        , 8:  [ {class: "vs", text: "The fishermen will lament, and all those who fish in the Nile will mourn, and those who spread nets on the waters will languish."} ]
        , 9:  [ {class: "vs", text: "Moreover those who work in combed flax, and those who weave white cloth, will be confounded."} ]
        , 10: [ {class: "vs", text: "The pillars will be broken in pieces. All those who work for hire will be grieved in soul."} ]
        , 11: [ {class: "vs", text: "The princes of Zoan are utterly foolish. The counsel of the wisest counselors of Pharaoh has become stupid. How do you say to Pharaoh, “I am the son of the wise, the son of ancient kings?”"} ]
        , 12: [ {class: "vs", text: "Where then are your wise men? Let them tell you now; and let them know what Yahweh of Armies has purposed concerning Egypt."} ]
        , 13: [ {class: "vs", text: "The princes of Zoan have become fools. The princes of Memphis are deceived. They have caused Egypt to go astray, who are the cornerstone of her tribes."} ]
        , 14: [ {class: "vs", text: "Yahweh has mixed a spirit of perverseness in the middle of her; and they have caused Egypt to go astray in all of its works, like a drunken man staggers in his vomit."} ]
        , 15: [ {class: "vs", text: "Neither shall there be any work for Egypt, which head or tail, palm branch or rush, may do."} ]
        , 16: [ {class: "vs", text: "In that day the Egyptians will be like women. They will tremble and fear because of the shaking of Yahweh of Armies’s hand, which he shakes over them."} ]
        , 17: [ {class: "vs", text: "The land of Judah will become a terror to Egypt. Everyone to whom mention is made of it will be afraid, because of the plans of Yahweh of Armies, which he determines against it."} ]
        , 18: [ {class: "vs", text: "In that day, there will be five cities in the land of Egypt that speak the language of Canaan, and swear to Yahweh of Armies. One will be called “The city of destruction.”"} ]
        , 19: [ {class: "vs", text: "In that day, there will be an altar to Yahweh in the middle of the land of Egypt, and a pillar to Yahweh at its border."} ]
        , 20: [ {class: "vs", text: "It will be for a sign and for a witness to Yahweh of Armies in the land of Egypt; for they will cry to Yahweh because of oppressors, and he will send them a savior and a defender, and he will deliver them."} ]
        , 21: [ {class: "vs", text: "Yahweh will be known to Egypt, and the Egyptians will know Yahweh in that day. Yes, they will worship with sacrifice and offering, and will vow a vow to Yahweh, and will perform it."} ]
        , 22: [ {class: "vs", text: "Yahweh will strike Egypt, striking and healing. They will return to Yahweh, and he will be entreated by them, and will heal them."} ]
        , 23: [ {class: "vs", text: "In that day there will be a highway out of Egypt to Assyria, and the Assyrian shall come into Egypt, and the Egyptian into Assyria; and the Egyptians will worship with the Assyrians."} ]
        , 24: [ {class: "vs", text: "In that day, Israel will be the third with Egypt and with Assyria, a blessing within the earth;"} ]
        , 25: [ {class: "vs", text: "because Yahweh of Armies has blessed them, saying, “Blessed be Egypt my people, Assyria the work of my hands, and Israel my inheritance.”"} ]
        }
  , 2:  { 1:  [ {class: "vs", text: "This is what Isaiah the son of Amoz saw concerning Judah and Jerusalem."} ]
        , 2:  [ {class: "q", text: "It shall happen in the latter days, that the mountain of Yahweh’s house shall be established on the top of the mountains,"}
              , {class: "q2", text: "and shall be raised above the hills;"}
              , {class: "q2", text: "and all nations shall flow to it."}
              ]
        , 3:  [ {class: "q", text: "Many peoples shall go and say,"}
              , {class: "q2", text: "“Come, let’s go up to the mountain of Yahweh,"}
              , {class: "q2", text: "to the house of the God of Jacob;"}
              , {class: "q2", text: "and he will teach us of his ways,"}
              , {class: "q2", text: "and we will walk in his paths.”"}
              , {class: "q", text: "For the law shall go out of Zion,"}
              , {class: "q2", text: "and Yahweh’s word from Jerusalem."}
              ]
        , 4:  [ {class: "q", text: "He will judge between the nations,"}
              , {class: "q2", text: "and will decide concerning many peoples."}
              , {class: "q2", text: "They shall beat their swords into plowshares,"}
              , {class: "q2", text: "and their spears into pruning hooks."}
              , {class: "q", text: "Nation shall not lift up sword against nation,"}
              , {class: "q2", text: "neither shall they learn war any more."}
              ]
        , 5:  [ {class: "q", text: "House of Jacob, come, and let’s walk in the light of Yahweh."} ]
        , 6:  [ {class: "q", text: "For you have forsaken your people, the house of Jacob,"}
              , {class: "q2", text: "because they are filled from the east,"}
              , {class: "q2", text: "with those who practice divination like the Philistines,"}
              , {class: "q2", text: "and they clasp hands with the children of foreigners."}
              ]
        , 7:  [ {class: "q", text: "Their land is full of silver and gold,"}
              , {class: "q2", text: "neither is there any end of their treasures."}
              , {class: "q", text: "Their land also is full of horses,"}
              , {class: "q2", text: "neither is there any end of their chariots."}
              ]
        , 8:  [ {class: "q", text: "Their land also is full of idols."}
              , {class: "q2", text: "They worship the work of their own hands,"}
              , {class: "q2", text: "that which their own fingers have made."}
              ]
        , 9:  [ {class: "q", text: "Man is brought low,"}
              , {class: "q2", text: "and mankind is humbled;"}
              , {class: "q2", text: "therefore don’t forgive them."}
              ]
        , 10: [ {class: "q", text: "Enter into the rock,"}
              , {class: "q2", text: "and hide in the dust,"}
              , {class: "q", text: "from before the terror of Yahweh,"}
              , {class: "q2", text: "and from the glory of his majesty."}
              ]
        , 11: [ {class: "q", text: "The lofty looks of man will be brought low,"}
              , {class: "q2", text: "the arrogance of men will be bowed down,"}
              , {class: "q2", text: "and Yahweh alone will be exalted in that day."}
              ]
        , 12: [ {class: "q", text: "For there will be a day of Yahweh of Armies for all that is proud and arrogant,"}
              , {class: "q2", text: "and for all that is lifted up;"}
              , {class: "q2", text: "and it shall be brought low:"}
              ]
        , 13: [ {class: "q2", text: "for all the cedars of Lebanon, that are high and lifted up,"}
              , {class: "q2", text: "for all the oaks of Bashan,"}
              ]
        , 14: [ {class: "q2", text: "for all the high mountains,"}
              ,  {class: "q2", text: "for all the hills that are lifted up,"}
              ]
        , 15: [ {class: "q2", text: "for every lofty tower,"}
              ,  {class: "q2", text: "for every fortified wall,"}
              ]
        , 16: [ {class: "q2", text: "for all the ships of Tarshish,"}
              ,  {class: "q2", text: "and for all pleasant imagery."}
              ]
        , 17: [ {class: "q", text: "The loftiness of man shall be bowed down,"}
              , {class: "q2", text: "and the arrogance of men shall be brought low;"}
              , {class: "q2", text: "and Yahweh alone shall be exalted in that day."}
              ]
        , 18: [ {class: "q", text: "The idols shall utterly pass away."} ]
        , 19: [ {class: "q", text: "Men shall go into the caves of the rocks,"}
              , {class: "q2", text: "and into the holes of the earth,"}
              , {class: "q2", text: "from before the terror of Yahweh,"}
              , {class: "q2", text: "and from the glory of his majesty,"}
              , {class: "q2", text: "when he arises to shake the earth mightily."}
              ]
        , 20: [ {class: "q", text: "In that day, men shall cast away their idols of silver"}
              , {class: "q2", text: "and their idols of gold,"}
              , {class: "q2", text: "which have been made for themselves to worship,"}
              , {class: "q2", text: "to the moles and to the bats,"}
              ]
        , 21: [ {class: "q2", text: "to go into the caverns of the rocks,"}
              , {class: "q2", text: "and into the clefts of the ragged rocks,"}
              , {class: "q2", text: "from before the terror of Yahweh,"}
              , {class: "q2", text: "and from the glory of his majesty,"}
              , {class: "q2", text: "when he arises to shake the earth mightily."}
              ]
        , 22: [ {class: "q", text: "Stop trusting in man, whose breath is in his nostrils;"}
              , {class: "q2", text: "for of what account is he?"}
              ]
        }
  , 20: { 1:  [ {class: "vs", text: "In the year that Tartan came to Ashdod, when Sargon the king of Assyria sent him, and he fought against Ashdod and took it;"} ]
        , 2:  [ {class: "vs", text: "at that time Yahweh spoke by Isaiah the son of Amoz, saying, “Go, and loosen the sackcloth from off your waist, and take your sandals from off your feet.” He did so, walking naked and barefoot."} ]
        , 3:  [ {class: "vs", text: "Yahweh said, “As my servant Isaiah has walked naked and barefoot three years for a sign and a wonder concerning Egypt and concerning Ethiopia,"} ]
        , 4:  [ {class: "vs", text: "so the king of Assyria will lead away the captives of Egypt and the exiles of Ethiopia, young and old, naked and barefoot, and with buttocks uncovered, to the shame of Egypt."} ]
        , 5:  [ {class: "vs", text: "They will be dismayed and confounded, because of Ethiopia their expectation, and of Egypt their glory."} ]
        , 6:  [ {class: "vs", text: "The inhabitants of this coast land will say in that day, ‘Behold, this is our expectation, where we fled for help to be delivered from the king of Assyria. And we, how will we escape?’”"} ]
        }
  , 21: { 1:  [ {class: "vs", text: "The burden of the wilderness of the sea.</p><p> As whirlwinds in the South sweep through, it comes from the wilderness, from an awesome land."} ]
        , 2:  [ {class: "vs", text: "A grievous vision is declared to me. The treacherous man deals treacherously, and the destroyer destroys. Go up, Elam; attack! I have stopped all of Media’s sighing."} ]
        , 3:  [ {class: "vs", text: "Therefore my thighs are filled with anguish. Pains have seized me, like the pains of a woman in labor. I am in so much pain that I can’t hear. I so am dismayed that I can’t see."} ]
        , 4:  [ {class: "vs", text: "My heart flutters. Horror has frightened me. The twilight that I desired has been turned into trembling for me."} ]
        , 5:  [ {class: "vs", text: "They prepare the table. They set the watch. They eat. They drink. Rise up, you princes, oil the shield!"} ]
        , 6:  [ {class: "vs", text: "For the Lord said to me, “Go, set a watchman. Let him declare what he sees."} ]
        , 7:  [ {class: "vs", text: "When he sees a troop, horsemen in pairs, a troop of donkeys, a troop of camels, he shall listen diligently with great attentiveness.”"} ]
        , 8:  [ {class: "vs", text: "He cried like a lion: “Lord, I stand continually on the watchtower in the daytime, and every night I stay at my post."} ]
        , 9:  [ {class: "vs", text: "Behold, here comes a troop of men, horsemen in pairs.” He answered, “Fallen, fallen is Babylon; and all the engraved images of her gods are broken to the ground."} ]
        , 10: [ {class: "vs", text: "You are my threshing, and the grain of my floor!” That which I have heard from Yahweh of Armies, the God of Israel, I have declared to you."} ]
        , 11: [ {class: "vs", text: "The burden of Dumah.</p><p> One calls to me out of Seir, “Watchman, what of the night? Watchman, what of the night?”"} ]
        , 12: [ {class: "vs", text: "The watchman said, “The morning comes, and also the night. If you will inquire, inquire. Come back again.”"} ]
        , 13: [ {class: "vs", text: "The burden on Arabia.</p><p> In the forest in Arabia you will lodge, you caravans of Dedanites."} ]
        , 14: [ {class: "vs", text: "They brought water to him who was thirsty. The inhabitants of the land of Tema met the fugitives with their bread."} ]
        , 15: [ {class: "vs", text: "For they fled away from the swords, from the drawn sword, from the bent bow, and from the heat of battle."} ]
        , 16: [ {class: "vs", text: "For the Lord said to me, “Within a year, as a worker bound by contract would count it, all the glory of Kedar will fail,"} ]
        , 17: [ {class: "vs", text: "and the residue of the number of the archers, the mighty men of the children of Kedar, will be few; for Yahweh, the God of Israel, has spoken it.”"} ]
        }
  , 22: { 1:  [ {class: "vs", text: "The burden of the valley of vision.</p><p> What ails you now, that you have all gone up to the housetops?"} ]
        , 2:  [ {class: "vs", text: "You that are full of shouting, a tumultuous city, a joyous town; your slain are not slain with the sword, neither are they dead in battle."} ]
        , 3:  [ {class: "vs", text: "All your rulers fled away together. They were bound by the archers. All who were found by you were bound together. They fled far away."} ]
        , 4:  [ {class: "vs", text: "Therefore I said, “Look away from me. I will weep bitterly. Don’t labor to comfort me for the destruction of the daughter of my people."} ]
        , 5:  [ {class: "vs", text: "For it is a day of confusion, and of treading down, and of perplexity, from the Lord, Yahweh of Armies, in the valley of vision, a breaking down of the walls, and a crying to the mountains.”"} ]
        , 6:  [ {class: "vs", text: "Elam carried his quiver, with chariots of men and horsemen; and Kir uncovered the shield."} ]
        , 7:  [ {class: "vs", text: "Your choicest valleys were full of chariots, and the horsemen set themselves in array at the gate."} ]
        , 8:  [ {class: "vs", text: "He took away the covering of Judah; and you looked in that day to the armor in the house of the forest."} ]
        , 9:  [ {class: "vs", text: "You saw the breaches of David’s city, that they were many; and you gathered together the waters of the lower pool."} ]
        , 10: [ {class: "vs", text: "You counted the houses of Jerusalem, and you broke down the houses to fortify the wall."} ]
        , 11: [ {class: "vs", text: "You also made a reservoir between the two walls for the water of the old pool. But you didn’t look to him who had done this, neither did you have respect for him who planed it long ago."} ]
        , 12: [ {class: "vs", text: "In that day, the Lord, Yahweh of Armies, called to weeping, to mourning, to baldness, and to dressing in sackcloth;"} ]
        , 13: [ {class: "vs", text: "and behold, joy and gladness, killing cattle and killing sheep, eating meat and drinking wine: “Let’s eat and drink, for tomorrow we will die.”"} ]
        , 14: [ {class: "vs", text: "Yahweh of Armies revealed himself in my ears, “Surely this iniquity will not be forgiven you until you die,” says the Lord, Yahweh of Armies."} ]
        , 15: [ {class: "vs", text: "The Lord, Yahweh of Armies says, “Go, get yourself to this treasurer, even to Shebna, who is over the house, and say,"} ]
        , 16: [ {class: "vs", text: "‘What are you doing here? Who has you here, that you have dug out a tomb here?’ Cutting himself out a tomb on high, chiseling a habitation for himself in the rock!”"} ]
        , 17: [ {class: "vs", text: "Behold, Yahweh will overcome you and hurl you away violently. Yes, he will grasp you firmly."} ]
        , 18: [ {class: "vs", text: "He will surely wind you around and around, and throw you like a ball into a large country. There you will die, and there the chariots of your glory will be, you shame of your lord’s house."} ]
        , 19: [ {class: "vs", text: "I will thrust you from your office. You will be pulled down from your station."} ]
        , 20: [ {class: "vs", text: "It will happen in that day that I will call my servant Eliakim the son of Hilkiah,"} ]
        , 21: [ {class: "vs", text: "and I will clothe him with your robe, and strengthen him with your belt. I will commit your government into his hand; and he will be a father to the inhabitants of Jerusalem, and to the house of Judah."} ]
        , 22: [ {class: "vs", text: "I will lay the key of David’s house on his shoulder. He will open, and no one will shut. He will shut, and no one will open."} ]
        , 23: [ {class: "vs", text: "I will fasten him like a nail in a sure place. He will be for a throne of glory to his father’s house."} ]
        , 24: [ {class: "vs", text: "They will hang on him all the glory of his father’s house, the offspring and the issue, every small vessel, from the cups even to all the pitchers."} ]
        , 25: [ {class: "vs", text: "“In that day,” says Yahweh of Armies, “the nail that was fastened in a sure place will give way. It will be cut down and fall. The burden that was on it will be cut off, for Yahweh has spoken it.”"} ]
        }
  , 23: { 1:  [ {class: "vs", text: "The burden of Tyre.</p><p> Howl, you ships of Tarshish! For it is laid waste, so that there is no house, no entering in. From the land of Kittim it is revealed to them."} ]
        , 2:  [ {class: "vs", text: "Be still, you inhabitants of the coast, you whom the merchants of Sidon that pass over the sea, have replenished."} ]
        , 3:  [ {class: "vs", text: "On great waters, the seed of the Shihor, the harvest of the Nile, was her revenue. She was the market of nations."} ]
        , 4:  [ {class: "vs", text: "Be ashamed, Sidon; for the sea has spoken, the stronghold of the sea, saying, “I have not travailed, nor given birth, neither have I nourished young men, nor brought up virgins.”"} ]
        , 5:  [ {class: "vs", text: "When the report comes to Egypt, they will be in anguish at the report of Tyre."} ]
        , 6:  [ {class: "vs", text: "Pass over to Tarshish! Wail, you inhabitants of the coast!"} ]
        , 7:  [ {class: "vs", text: "Is this your joyous city, whose antiquity is of ancient days, whose feet carried her far away to travel?"} ]
        , 8:  [ {class: "vs", text: "Who has planned this against Tyre, the giver of crowns, whose merchants are princes, whose traders are the honorable of the earth?"} ]
        , 9:  [ {class: "vs", text: "Yahweh of Armies has planned it, to stain the pride of all glory, to bring into contempt all the honorable of the earth."} ]
        , 10: [ {class: "vs", text: "Pass through your land like the Nile, daughter of Tarshish. There is no restraint any more."} ]
        , 11: [ {class: "vs", text: "He has stretched out his hand over the sea. He has shaken the kingdoms. Yahweh has ordered the destruction of Canaan’s strongholds."} ]
        , 12: [ {class: "vs", text: "He said, “You shall rejoice no more, you oppressed virgin daughter of Sidon. Arise, pass over to Kittim. Even there you will have no rest.”"} ]
        , 13: [ {class: "vs", text: "Behold, the land of the Chaldeans. This people didn’t exist. The Assyrians founded it for those who dwell in the wilderness. They set up their towers. They overthrew its palaces. They made it a ruin."} ]
        , 14: [ {class: "vs", text: "Howl, you ships of Tarshish, for your stronghold is laid waste!"} ]
        , 15: [ {class: "vs", text: "It will come to pass in that day that Tyre will be forgotten seventy years, according to the days of one king. After the end of seventy years it will be to Tyre like in the song of the prostitute."} ]
        , 16: [ {class: "vs", text: "Take a harp; go about the city, you prostitute that has been forgotten. Make sweet melody. Sing many songs, that you may be remembered."} ]
        , 17: [ {class: "vs", text: "It will happen after the end of seventy years that Yahweh will visit Tyre. She will return to her wages, and will play the prostitute with all the kingdoms of the world on the surface of the earth."} ]
        , 18: [ {class: "vs", text: "Her merchandise and her wages will be holiness to Yahweh. It will not be treasured nor laid up; for her merchandise will be for those who dwell before Yahweh, to eat sufficiently, and for durable clothing."} ]
        }
  , 24: { 1:  [ {class: "vs", text: "Behold, Yahweh makes the earth empty, makes it waste, turns it upside down, and scatters its inhabitants."} ]
        , 2:  [ {class: "vs", text: "It will be as with the people, so with the priest; as with the servant, so with his master; as with the maid, so with her mistress; as with the buyer, so with the seller; as with the creditor, so with the debtor; as with the taker of interest, so with the giver of interest."} ]
        , 3:  [ {class: "vs", text: "The earth will be utterly emptied and utterly laid waste; for Yahweh has spoken this word."} ]
        , 4:  [ {class: "vs", text: "The earth mourns and fades away. The world languishes and fades away. The lofty people of the earth languish."} ]
        , 5:  [ {class: "vs", text: "The earth also is polluted under its inhabitants, because they have transgressed the laws, violated the statutes, and broken the everlasting covenant."} ]
        , 6:  [ {class: "vs", text: "Therefore the curse has devoured the earth, and those who dwell therein are found guilty. Therefore the inhabitants of the earth are burned, and few men are left."} ]
        , 7:  [ {class: "vs", text: "The new wine mourns. The vine languishes. All the merry-hearted sigh."} ]
        , 8:  [ {class: "vs", text: "The mirth of tambourines ceases. The sound of those who rejoice ends. The joy of the harp ceases."} ]
        , 9:  [ {class: "vs", text: "They will not drink wine with a song. Strong drink will be bitter to those who drink it."} ]
        , 10: [ {class: "vs", text: "The confused city is broken down. Every house is shut up, that no man may come in."} ]
        , 11: [ {class: "vs", text: "There is a crying in the streets because of the wine. All joy is darkened. The mirth of the land is gone."} ]
        , 12: [ {class: "vs", text: "The city is left in desolation, and the gate is struck with destruction."} ]
        , 13: [ {class: "vs", text: "For it will be so within the earth among the peoples, as the shaking of an olive tree, as the gleanings when the vintage is done."} ]
        , 14: [ {class: "vs", text: "These shall lift up their voice. They will shout for the majesty of Yahweh. They cry aloud from the sea."} ]
        , 15: [ {class: "vs", text: "Therefore glorify Yahweh in the east, even the name of Yahweh, the God of Israel, in the islands of the sea!"} ]
        , 16: [ {class: "vs", text: "From the uttermost part of the earth have we heard songs. Glory to the righteous!</p><p> But I said, “I pine away! I pine away! woe is me!” The treacherous have dealt treacherously. Yes, the treacherous have dealt very treacherously."} ]
        , 17: [ {class: "vs", text: "Fear, the pit, and the snare, are on you who inhabit the earth."} ]
        , 18: [ {class: "vs", text: "It will happen that he who flees from the noise of the fear will fall into the pit; and he who comes up out of the middle of the pit will be taken in the snare; for the windows on high are opened, and the foundations of the earth tremble."} ]
        , 19: [ {class: "vs", text: "The earth is utterly broken. The earth is torn apart. The earth is shaken violently."} ]
        , 20: [ {class: "vs", text: "The earth will stagger like a drunken man, and will sway back and forth like a hammock. Its disobedience will be heavy on it, and it will fall and not rise again."} ]
        , 21: [ {class: "vs", text: "It will happen in that day that Yahweh will punish the army of the high ones on high, and the kings of the earth on the earth."} ]
        , 22: [ {class: "vs", text: "They will be gathered together, as prisoners are gathered in the pit, and will be shut up in the prison; and after many days they will be visited."} ]
        , 23: [ {class: "vs", text: "Then the moon will be confounded, and the sun ashamed; for Yahweh of Armies will reign on Mount Zion, and in Jerusalem; and glory will be before his elders."} ]
        }
  , 25: { 1:  [ {class: "vs", text: "Yahweh, you are my God. I will exalt you! I will praise your name, for you have done wonderful things, things planned long ago, in complete faithfulness and truth."} ]
        , 2:  [ {class: "vs", text: "For you have made a city into a heap, a fortified city into a ruin, a palace of strangers to be no city. It will never be built."} ]
        , 3:  [ {class: "vs", text: "Therefore a strong people will glorify you. A city of awesome nations will fear you."} ]
        , 4:  [ {class: "vs", text: "For you have been a stronghold to the poor, a stronghold to the needy in his distress, a refuge from the storm, a shade from the heat, when the blast of the dreaded ones is like a storm against the wall."} ]
        , 5:  [ {class: "vs", text: "As the heat in a dry place you will bring down the noise of strangers; as the heat by the shade of a cloud, the song of the dreaded ones will be brought low."} ]
        , 6:  [ {class: "vs", text: "In this mountain, Yahweh of Armies will make all peoples a feast of choice meat, a feast of choice wines, of choice meat full of marrow, of well refined choice wines."} ]
        , 7:  [ {class: "vs", text: "He will destroy in this mountain the surface of the covering that covers all peoples, and the veil that is spread over all nations."} ]
        , 8:  [ {class: "vs", text: "He has swallowed up death forever! The Lord Yahweh will wipe away tears from off all faces. He will take the reproach of his people away from off all the earth, for Yahweh has spoken it."} ]
        , 9:  [ {class: "vs", text: "It shall be said in that day, “Behold, this is our God! We have waited for him, and he will save us! This is Yahweh! We have waited for him. We will be glad and rejoice in his salvation!”"} ]
        , 10: [ {class: "vs", text: "For Yahweh’s hand will rest in this mountain.</p><p> Moab will be trodden down in his place, even like straw is trodden down in the water of the dunghill."} ]
        , 11: [ {class: "vs", text: "He will spread out his hands in the middle of it, like one who swims spreads out hands to swim, but his pride will be humbled together with the craft of his hands."} ]
        , 12: [ {class: "vs", text: "He has brought the high fortress of your walls down, laid low, and brought to the ground, even to the dust."} ]
        }
  , 26: { 1:  [ {class: "endpara", text: "In that day, this song will be sung in the land of Judah:"}
              , {class: "q", text: "“We have a strong city."}
              , {class: "q2", text: "God appoints salvation for walls and bulwarks."}
              ]
        , 2:  [ {class: "q", text: "Open the gates, that the righteous nation may enter:"}
              , {class: "q2", text: "the one which keeps faith."}
              ]
        , 3:  [ {class: "q", text: "You will keep whoever’s mind is steadfast in perfect peace,"}
              , {class: "q2", text: "because he trusts in you."}
              ]
        , 4:  [ {class: "q", text: "Trust in Yahweh forever;"}
              , {class: "q2", text: "for in Yah, Yahweh, is an everlasting Rock."}
              ]
        , 5:  [ {class: "q", text: "For he has brought down those who dwell on high, the lofty city."}
              ,  {class: "q2", text: "He lays it low."}
              , {class: "q2", text: "He lays it low even to the ground."}
              , {class: "q2", text: "He brings it even to the dust."}
              ]
        , 6:  [ {class: "q", text: "The foot shall tread it down,"}
              , {class: "q2", text: "even the feet of the poor"}
              , {class: "q2", text: "and the steps of the needy.”"}
              ]
        , 7:  [ {class: "q", text: "The way of the just is uprightness."}
              , {class: "q2", text: "You who are upright make the path of the righteous level."}
              ]
        , 8:  [ {class: "q", text: "Yes, in the way of your judgments, Yahweh, we have waited for you."}
              , {class: "q2", text: "Your name and your renown are the desire of our soul."}
              ]
        , 9:  [ {class: "q", text: "With my soul I have desired you in the night."}
              , {class: "q2", text: "Yes, with my spirit within me I will seek you earnestly;"}
              , {class: "q2", text: "for when your judgments are in the earth, the inhabitants of the world learn righteousness."}
              ]
        , 10: [ {class: "q", text: "Let favor be shown to the wicked,"}
              ,  {class: "q2", text: "yet he will not learn righteousness."}
              , {class: "q", text: "In the land of uprightness he will deal wrongfully,"}
              , {class: "q2", text: "and will not see Yahweh’s majesty."}
              ]
        , 11: [ {class: "q", text: "Yahweh, your hand is lifted up, yet they don’t see;"}
              , {class: "q2", text: "but they will see your zeal for the people, and be disappointed."}
              , {class: "q2", text: "Yes, fire will consume your adversaries."}
              ]
        , 12: [ {class: "q", text: "Yahweh, you will ordain peace for us,"}
              , {class: "q2", text: "for you have also done all our work for us."}
              ]
        , 13: [ {class: "q", text: "Yahweh our God, other lords besides you have had dominion over us,"}
              ,  {class: "q2", text: "but we will only acknowledge your name."}
            ]
        , 14: [ {class: "q", text: "The dead shall not live."}
              , {class: "q2", text: "The departed spirits shall not rise."}
              , {class: "q", text: "Therefore you have visited and destroyed them,"}
              , {class: "q2", text: "and caused all memory of them to perish."}
              ]
        , 15: [ {class: "q", text: "You have increased the nation, O Yahweh."}
              , {class: "q2", text: "You have increased the nation!"}
              , {class: "q", text: "You are glorified!"}
              , {class: "q2", text: "You have enlarged all the borders of the land."}
              ]
        , 16: [ {class: "q", text: "Yahweh, in trouble they have visited you."}
              , {class: "q2", text: "They poured out a prayer when your chastening was on them."}
              ]
        , 17: [ {class: "q", text: "Just as a woman with child, who draws near the time of her delivery,"}
              , {class: "q2", text: "is in pain and cries out in her pangs,"}
              , {class: "q2", text: "so we have been before you, Yahweh."}
              ]
        , 18: [ {class: "q", text: "We have been with child."}
              , {class: "q2", text: "We have been in pain."}
              , {class: "q", text: "We gave birth, it seems, only to wind."}
              , {class: "q2", text: "We have not worked any deliverance in the earth;"}
              , {class: "q2", text: "neither have the inhabitants of the world fallen."}
              ]
        , 19: [ {class: "q", text: "Your dead shall live."}
              , {class: "q2", text: "My dead bodies shall arise."}
              , {class: "q", text: "Awake and sing, you who dwell in the dust;"}
              , {class: "q2", text: "for your dew is like the dew of herbs,"}
                , {class: "q2", text: "and the earth will cast out the departed spirits."}
                ]
        , 20: [ {class: "q", text: "Come, my people, enter into your rooms,"}
              , {class: "q2", text: "and shut your doors behind you."}
              , {class: "q", text: "Hide yourself for a little moment,"}
              , {class: "q2", text: "until the indignation is past."}
              ]
        , 21: [ {class: "q", text: "For, behold, Yahweh comes out of his place to punish the inhabitants of the earth for their iniquity."}
              , {class: "q2", text: "The earth also will disclose her blood, and will no longer cover her slain."}
              ]
        }
  , 27: { 1:  [ {class: "vs", text: "In that day, Yahweh with his hard and great and strong sword will punish leviathan, the fleeing serpent, and leviathan the twisted serpent; and he will kill the dragon that is in the sea."} ]
        , 2:  [ {class: "vs", text: "In that day, sing to her, “A pleasant vineyard!"} ]
        , 3:  [ {class: "vs", text: "I, Yahweh, am its keeper. I will water it every moment. Lest anyone damage it, I will keep it night and day."} ]
        , 4:  [ {class: "vs", text: "Wrath is not in me, but if I should find briers and thorns, I would do battle! I would march on them and I would burn them together."} ]
        , 5:  [ {class: "vs", text: "Or else let him take hold of my strength, that he may make peace with me. Let him make peace with me.”"} ]
        , 6:  [ {class: "vs", text: "In days to come, Jacob will take root. Israel will blossom and bud. They will fill the surface of the world with fruit."} ]
        , 7:  [ {class: "vs", text: "Has he struck them as he struck those who struck them? Or are they killed like those who killed them were killed?"} ]
        , 8:  [ {class: "vs", text: "In measure, when you send them away, you contend with them. He has removed them with his rough blast in the day of the east wind."} ]
        , 9:  [ {class: "vs", text: "Therefore by this the iniquity of Jacob will be forgiven, and this is all the fruit of taking away his sin: that he makes all the stones of the altar as chalk stones that are beaten in pieces, so that the Asherah poles and the incense altars shall rise no more."} ]
        , 10: [ {class: "vs", text: "For the fortified city is solitary, a habitation deserted and forsaken, like the wilderness. The calf will feed there, and there he will lie down, and consume its branches."} ]
        , 11: [ {class: "vs", text: "When its boughs are withered, they will be broken off. The women will come and set them on fire, for they are a people of no understanding. Therefore he who made them will not have compassion on them, and he who formed them will show them no favor."} ]
        , 12: [ {class: "vs", text: "It will happen in that day that Yahweh will thresh from the flowing stream of the Euphrates to the brook of Egypt; and you will be gathered one by one, children of Israel."} ]
        , 13: [ {class: "vs", text: "It will happen in that day that a great trumpet will be blown; and those who were ready to perish in the land of Assyria, and those who were outcasts in the land of Egypt, shall come; and they will worship Yahweh in the holy mountain at Jerusalem."} ]
        }
  , 28: { 1:  [ {class: "vs", text: "Woe to the crown of pride of the drunkards of Ephraim, and to the fading flower of his glorious beauty, which is on the head of the fertile valley of those who are overcome with wine!"} ]
        , 2:  [ {class: "vs", text: "Behold, the Lord has a mighty and strong one. Like a storm of hail, a destroying storm, and like a storm of mighty waters overflowing, he will cast them down to the earth with his hand."} ]
        , 3:  [ {class: "vs", text: "The crown of pride of the drunkards of Ephraim will be trodden under foot."} ]
        , 4:  [ {class: "vs", text: "The fading flower of his glorious beauty, which is on the head of the fertile valley, shall be like the first-ripe fig before the summer; which someone picks and eats as soon as he sees it."} ]
        , 5:  [ {class: "vs", text: "In that day, Yahweh of Armies will become a crown of glory and a diadem of beauty to the residue of his people,"} ]
        , 6:  [ {class: "vs", text: "and a spirit of justice to him who sits in judgment, and strength to those who turn back the battle at the gate."} ]
        , 7:  [ {class: "vs", text: "They also reel with wine, and stagger with strong drink. The priest and the prophet reel with strong drink. They are swallowed up by wine. They stagger with strong drink. They err in vision. They stumble in judgment."} ]
        , 8:  [ {class: "vs", text: "For all tables are completely full of filthy vomit and filthiness."} ]
        , 9:  [ {class: "vs", text: "Whom will he teach knowledge? To whom will he explain the message? Those who are weaned from the milk, and drawn from the breasts?"} ]
        , 10: [ {class: "vs", text: "For it is precept on precept, precept on precept; line on line, line on line; here a little, there a little."} ]
        , 11: [ {class: "vs", text: "But he will speak to this nation with stammering lips and in another language,"} ]
        , 12: [ {class: "vs", text: "to whom he said, “This is the resting place. Give rest to weary,” and “This is the refreshing;” yet they would not hear."} ]
        , 13: [ {class: "vs", text: "Therefore Yahweh’s word will be to them precept on precept, precept on precept; line on line, line on line; here a little, there a little; that they may go, fall backward, be broken, be snared, and be taken."} ]
        , 14: [ {class: "vs", text: "Therefore hear Yahweh’s word, you scoffers, that rule this people in Jerusalem:"} ]
        , 15: [ {class: "vs", text: "“Because you have said, ‘We have made a covenant with death, and we are in agreement with Sheol. When the overflowing scourge passes through, it won’t come to us; for we have made lies our refuge, and we have hidden ourselves under falsehood.’”"} ]
        , 16: [ {class: "vs", text: "Therefore the Lord Yahweh says, “Behold, I lay in Zion for a foundation a stone, a tried stone, a precious cornerstone of a sure foundation. He who believes shall not act hastily."} ]
        , 17: [ {class: "vs", text: "I will make justice the measuring line, and righteousness the plumb line. The hail will sweep away the refuge of lies, and the waters will overflow the hiding place."} ]
        , 18: [ {class: "vs", text: "Your covenant with death shall be annulled, and your agreement with Sheol shall not stand. When the overflowing scourge passes through, then you will be trampled down by it."} ]
        , 19: [ {class: "vs", text: "As often as it passes through, it will seize you; for morning by morning it will pass through, by day and by night; and it will be nothing but terror to understand the message.”"} ]
        , 20: [ {class: "vs", text: "For the bed is too short to stretch out on, and the blanket is too narrow to wrap oneself in."} ]
        , 21: [ {class: "vs", text: "For Yahweh will rise up as on Mount Perazim. He will be angry as in the valley of Gibeon; that he may do his work, his unusual work, and bring to pass his act, his extraordinary act."} ]
        , 22: [ {class: "vs", text: "Now therefore don’t be scoffers, lest your bonds be made strong; for I have heard a decree of destruction from the Lord, Yahweh of Armies, on the whole earth."} ]
        , 23: [ {class: "vs", text: "Give ear, and hear my voice! Listen, and hear my speech!"} ]
        , 24: [ {class: "vs", text: "Does he who plows to sow plow continually? Does he keep turning the soil and breaking the clods?"} ]
        , 25: [ {class: "vs", text: "When he has leveled its surface, doesn’t he plant the dill, and scatter the cumin seed, and put in the wheat in rows, the barley in the appointed place, and the spelt in its place?"} ]
        , 26: [ {class: "vs", text: "For his God instructs him in right judgment and teaches him."} ]
        , 27: [ {class: "vs", text: "For the dill are not threshed with a sharp instrument, neither is a cart wheel turned over the cumin; but the dill is beaten out with a stick, and the cumin with a rod."} ]
        , 28: [ {class: "vs", text: "Bread flour must be ground; so he will not always be threshing it. Although he drives the wheel of his threshing cart over it, his horses don’t grind it."} ]
        , 29: [ {class: "vs", text: "This also comes out from Yahweh of Armies, who is wonderful in counsel, and excellent in wisdom."} ]
        }
  , 29: { 1:  [ {class: "vs", text: "Woe to Ariel! Ariel, the city where David encamped! Add year to year; let the feasts come around;"} ]
        , 2:  [ {class: "vs", text: "then I will distress Ariel, and there will be mourning and lamentation. She shall be to me as an altar hearth."} ]
        , 3:  [ {class: "vs", text: "I will encamp against you all around you, and will lay siege against you with posted troops. I will raise siege works against you."} ]
        , 4:  [ {class: "vs", text: "You will be brought down, and will speak out of the ground. Your speech will mumble out of the dust. Your voice will be as of one who has a familiar spirit, out of the ground, and your speech will whisper out of the dust."} ]
        , 5:  [ {class: "vs", text: "But the multitude of your foes will be like fine dust, and the multitude of the ruthless ones like chaff that blows away. Yes, it will be in an instant, suddenly."} ]
        , 6:  [ {class: "vs", text: "She will be visited by Yahweh of Armies with thunder, with earthquake, with great noise, with whirlwind and storm, and with the flame of a devouring fire."} ]
        , 7:  [ {class: "vs", text: "The multitude of all the nations that fight against Ariel, even all who fight against her and her stronghold, and who distress her, will be like a dream, a vision of the night."} ]
        , 8:  [ {class: "vs", text: "It will be like when a hungry man dreams, and behold, he eats; but he awakes, and his hunger isn’t satisfied; or like when a thirsty man dreams, and behold, he drinks; but he awakes, and behold, he is faint, and he is still thirsty. The multitude of all the nations that fight against Mount Zion will be like that."} ]
        , 9:  [ {class: "vs", text: "Pause and wonder! Blind yourselves and be blind! They are drunken, but not with wine; they stagger, but not with strong drink."} ]
        , 10: [ {class: "vs", text: "For Yahweh has poured out on you a spirit of deep sleep, and has closed your eyes, the prophets; and he has covered your heads, the seers."} ]
        , 11: [ {class: "vs", text: "All vision has become to you like the words of a book that is sealed, which men deliver to one who is educated, saying, “Read this, please;” and he says, “I can’t, for it is sealed;”"} ]
        , 12: [ {class: "vs", text: "and the book is delivered to one who is not educated, saying, “Read this, please;” and he says, “I can’t read.”"} ]
        , 13: [ {class: "vs", text: "The Lord said, “Because this people draws near with their mouth and honors me with their lips, but they have removed their heart far from me, and their fear of me is a commandment of men which has been taught;"} ]
        , 14: [ {class: "vs", text: "therefore, behold, I will proceed to do a marvelous work among this people, even a marvelous work and a wonder; and the wisdom of their wise men will perish, and the understanding of their prudent men will be hidden.”"} ]
        , 15: [ {class: "vs", text: "Woe to those who deeply hide their counsel from Yahweh, and whose deeds are in the dark, and who say, “Who sees us?” and “Who knows us?”"} ]
        , 16: [ {class: "vs", text: "You turn things upside down! Should the potter be thought to be like clay; that the thing made should say about him who made it, “He didn’t make me;” or the thing formed say of him who formed it, “He has no understanding?”"} ]
        , 17: [ {class: "vs", text: "Isn’t it yet a very little while, and Lebanon will be turned into a fruitful field, and the fruitful field will be regarded as a forest?"} ]
        , 18: [ {class: "vs", text: "In that day, the deaf will hear the words of the book, and the eyes of the blind will see out of obscurity and out of darkness."} ]
        , 19: [ {class: "vs", text: "The humble also will increase their joy in Yahweh, and the poor among men will rejoice in the Holy One of Israel."} ]
        , 20: [ {class: "vs", text: "For the ruthless is brought to nothing, and the scoffer ceases, and all those who are alert to do evil are cut off—"} ]
        , 21: [ {class: "vs", text: "who cause a person to be indicted by a word, and lay a snare for the arbiter in the gate, and who deprive the innocent of justice with false testimony."} ]
        , 22: [ {class: "vs", text: "Therefore Yahweh, who redeemed Abraham, says concerning the house of Jacob: “Jacob shall no longer be ashamed, neither shall his face grow pale."} ]
        , 23: [ {class: "vs", text: "But when he sees his children, the work of my hands, in the middle of him, they will sanctify my name. Yes, they will sanctify the Holy One of Jacob, and will stand in awe of the God of Israel."} ]
        , 24: [ {class: "vs", text: "They also who err in spirit will come to understanding, and those who grumble will receive instruction.”"} ]
        }
  , 3:  { 1:  [ {class: "q", text: "For, behold, the Lord, Yahweh of Armies, takes away from Jerusalem and from Judah supply and support,"}
              , {class: "q2", text: "the whole supply of bread,"}
              , {class: "q2", text: "and the whole supply of water;"}
              ]
        , 2:  [ {class: "q2", text: "the mighty man,"}
              , {class: "q2", text: "the man of war,"}
              , {class: "q2", text: "the judge,"}
              , {class: "q2", text: "the prophet,"}
              , {class: "q2", text: "the diviner,"}
              , {class: "q2", text: "the elder,"}
              ]
        , 3:  [ {class: "q2", text: "the captain of fifty,"}
              , {class: "q2", text: "the honorable man,"}
              , {class: "q2", text: "the counselor,"}
              , {class: "q2", text: "the skilled craftsman,"}
              , {class: "q2", text: "and the clever enchanter."}
              ]
        , 4:  [ {class: "q", text: "I will give boys to be their princes,"}
              , {class: "q2", text: "and children shall rule over them."}
              ]
        , 5:  [ {class: "q", text: "The people will be oppressed,"}
              , {class: "q2", text: "everyone by another,"}
              , {class: "q2", text: "and everyone by his neighbor."}
              , {class: "q", text: "The child will behave himself proudly against the old man,"}
              , {class: "q2", text: "and the wicked against the honorable."}
              ]
        , 6:  [ {class: "q", text: "Indeed a man shall take hold of his brother in the house of his father, saying,"}
              , {class: "q2", text: "“You have clothing, you be our ruler,"}
              , {class: "q2", text: "and let this ruin be under your hand.”"}
              ]
        , 7:  [ {class: "q", text: "In that day he will cry out, saying, “I will not be a healer;"}
              , {class: "q2", text: "for in my house is neither bread nor clothing."}
                , {class: "q2", text: "You shall not make me ruler of the people.”"}
                ]
        , 8:  [ {class: "q", text: "For Jerusalem is ruined, and Judah is fallen;"}
              , {class: "q2", text: "because their tongue and their doings are against Yahweh,"}
              , {class: "q", text: "to provoke the eyes of his glory."}
              ]
        , 9:  [ {class: "q", text: "The look of their faces testify against them."}
              , {class: "q2", text: "They parade their sin like Sodom."}
              , {class: "q2", text: "They don’t hide it."}
              , {class: "q2", text: "Woe to their soul!"}
              , {class: "q2", text: "For they have brought disaster upon themselves."}
              ]
        , 10: [ {class: "q", text: "Tell the righteous “Good!”"}
              , {class: "q2", text: "For they shall eat the fruit of their deeds."}
              ]
        , 11: [ {class: "q", text: "Woe to the wicked!"}
              , {class: "q2", text: "Disaster is upon them;"}
              , {class: "q2", text: "for the deeds of his hands will be paid back to him."}
              ]
        , 12: [ {class: "q", text: "As for my people, children are their oppressors,"}
              ,  {class: "q2", text: "and women rule over them."}
              , {class: "q2", text: "My people, those who lead you cause you to err,"}
              , {class: "q2", text: "and destroy the way of your paths."}
              ]
        , 13: [ {class: "q", text: "Yahweh stands up to contend,"}
              , {class: "q2", text: "and stands to judge the peoples."}
              ]
        , 14: [ {class: "q", text: "Yahweh will enter into judgment with the elders of his people"}
              , {class: "q2", text: "and their leaders:"}
              , {class: "q2", text: "“It is you who have eaten up the vineyard."}
              , {class: "q2", text: "The plunder of the poor is in your houses."}
              ]
        , 15: [ {class: "q2", text: "What do you mean that you crush my people,"}
              , {class: "q2", text: "and grind the face of the poor?” says the Lord, Yahweh of Armies."}
              ]
        , 16: [ {class: "q", text: "Moreover Yahweh said, “Because the daughters of Zion are arrogant,"}
              , {class: "q2", text: "and walk with outstretched necks and flirting eyes,"}
              , {class: "q2", text: "walking to trip as they go,"}
              , {class: "q2", text: "jingling ornaments on their feet;"}
              ]
        , 17: [ {class: "q", text: "therefore the Lord brings sores on the crown of the head of the women of Zion,"}
              , {class: "q2", text: "and Yahweh will make their scalps bald.”"}
              ]
        , 18: [ {class: "vs", text: "In that day the Lord will take away the beauty of their anklets, the headbands, the crescent necklaces,"} ]
        , 19: [ {class: "vs", text: "the earrings, the bracelets, the veils,"} ]
        , 20: [ {class: "vs", text: "the headdresses, the ankle chains, the sashes, the perfume containers, the charms,"} ]
        , 21: [ {class: "vs", text: "the signet rings, the nose rings,"} ]
        , 22: [ {class: "vs", text: "the fine robes, the capes, the cloaks, the purses,"} ]
        , 23: [ {class: "vs", text: "the hand mirrors, the fine linen garments, the tiaras, and the shawls."} ]
        , 24: [ {class: "q", text: "It shall happen that instead of sweet spices, there shall be rottenness;"}
              , {class: "q2", text: "instead of a belt, a rope;"}
              , {class: "q2", text: "instead of well set hair, baldness;"}
              , {class: "q2", text: "instead of a robe, a wearing of sackcloth;"}
              , {class: "q2", text: "and branding instead of beauty."}
              ]
        , 25: [ {class: "q", text: "Your men shall fall by the sword,"}
              , {class: "q2", text: "and your mighty in the war."}
              ]
        , 26: [ {class: "q", text: "Her gates shall lament and mourn."}
              , {class: "q2", text: "She shall be desolate and sit on the ground."}
              ]
        }
  , 30: { 1:  [ {class: "vs", text: "“Woe to the rebellious children”, says Yahweh, “who take counsel, but not from me; and who make an alliance, but not with my Spirit, that they may add sin to sin,"} ]
        , 2:  [ {class: "vs", text: "who set out to go down into Egypt, and have not asked my advice, to strengthen themselves in the strength of Pharaoh, and to take refuge in the shadow of Egypt!"} ]
        , 3:  [ {class: "vs", text: "Therefore the strength of Pharaoh will be your shame, and the refuge in the shadow of Egypt your confusion."} ]
        , 4:  [ {class: "vs", text: "For their princes are at Zoan, and their ambassadors have come to Hanes."} ]
        , 5:  [ {class: "vs", text: "They shall all be ashamed because of a people that can’t profit them, that are not a help nor profit, but a shame, and also a reproach.”"} ]
        , 6:  [ {class: "vs", text: "The burden of the animals of the South.</p><p> Through the land of trouble and anguish, of the lioness and the lion, the viper and fiery flying serpent, they carry their riches on the shoulders of young donkeys, and their treasures on the humps of camels, to an unprofitable people."} ]
        , 7:  [ {class: "vs", text: "For Egypt helps in vain, and to no purpose; therefore I have called her Rahab who sits still."} ]
        , 8:  [ {class: "vs", text: "Now go, write it before them on a tablet, and inscribe it in a book, that it may be for the time to come forever and ever."} ]
        , 9:  [ {class: "vs", text: "For it is a rebellious people, lying children, children who will not hear Yahweh’s law;"} ]
        , 10: [ {class: "vs", text: "who tell the seers, “Don’t see!” and the prophets, “Don’t prophesy to us right things. Tell us pleasant things. Prophesy deceits."} ]
        , 11: [ {class: "vs", text: "Get out of the way. Turn away from the path. Cause the Holy One of Israel to cease from before us.”"} ]
        , 12: [ {class: "vs", text: "Therefore the Holy One of Israel says, “Because you despise this word, and trust in oppression and perverseness, and rely on it,"} ]
        , 13: [ {class: "vs", text: "therefore this iniquity shall be to you like a breach ready to fall, swelling out in a high wall, whose breaking comes suddenly in an instant."} ]
        , 14: [ {class: "vs", text: "He will break it as a potter’s vessel is broken, breaking it in pieces without sparing, so that there won’t be found among the broken pieces a piece good enough to take fire from the hearth, or to dip up water out of the cistern.”"} ]
        , 15: [ {class: "vs", text: "For thus said the Lord Yahweh, the Holy One of Israel, “You will be saved in returning and rest. Your strength will be in quietness and in confidence.” You refused,"} ]
        , 16: [ {class: "vs", text: "but you said, “No, for we will flee on horses;” therefore you will flee; and, “We will ride on the swift;” therefore those who pursue you will be swift."} ]
        , 17: [ {class: "vs", text: "One thousand will flee at the threat of one. At the threat of five, you will flee until you are left like a beacon on the top of a mountain, and like a banner on a hill."} ]
        , 18: [ {class: "vs", text: "Therefore Yahweh will wait, that he may be gracious to you; and therefore he will be exalted, that he may have mercy on you, for Yahweh is a God of justice. Blessed are all those who wait for him."} ]
        , 19: [ {class: "vs", text: "For the people will dwell in Zion at Jerusalem. You will weep no more. He will surely be gracious to you at the voice of your cry. When he hears you, he will answer you."} ]
        , 20: [ {class: "vs", text: "Though the Lord may give you the bread of adversity and the water of affliction, yet your teachers won’t be hidden any more, but your eyes will see your teachers;"} ]
        , 21: [ {class: "vs", text: "and when you turn to the right hand, and when you turn to the left, your ears will hear a voice behind you, saying, “This is the way. Walk in it.”"} ]
        , 22: [ {class: "vs", text: "You shall defile the overlaying of your engraved images of silver, and the plating of your molten images of gold. You shall cast them away as an unclean thing. You shall tell it, “Go away!”"} ]
        , 23: [ {class: "vs", text: "He will give the rain for your seed, with which you will sow the ground; and bread of the increase of the ground will be rich and plentiful. In that day, your livestock will feed in large pastures."} ]
        , 24: [ {class: "vs", text: "The oxen likewise and the young donkeys that till the ground will eat savory feed, which has been winnowed with the shovel and with the fork."} ]
        , 25: [ {class: "vs", text: "There will be brooks and streams of water on every lofty mountain and on every high hill in the day of the great slaughter, when the towers fall."} ]
        , 26: [ {class: "vs", text: "Moreover the light of the moon will be like the light of the sun, and the light of the sun will be seven times brighter, like the light of seven days, in the day that Yahweh binds up the fracture of his people, and heals the wound they were struck with."} ]
        , 27: [ {class: "vs", text: "Behold, Yahweh’s name comes from far away, burning with his anger, and in thick rising smoke. His lips are full of indignation. His tongue is as a devouring fire."} ]
        , 28: [ {class: "vs", text: "His breath is as an overflowing stream that reaches even to the neck, to sift the nations with the sieve of destruction. A bridle that leads to ruin will be in the jaws of the peoples."} ]
        , 29: [ {class: "vs", text: "You will have a song, as in the night when a holy feast is kept, and gladness of heart, as when one goes with a flute to come to Yahweh’s mountain, to Israel’s Rock."} ]
        , 30: [ {class: "vs", text: "Yahweh will cause his glorious voice to be heard, and will show the descent of his arm, with the indignation of his anger, and the flame of a devouring fire, with a blast, storm, and hailstones."} ]
        , 31: [ {class: "vs", text: "For through Yahweh’s voice the Assyrian will be dismayed. He will strike him with his rod."} ]
        , 32: [ {class: "vs", text: "Every stroke of the rod of punishment, which Yahweh will lay on him, will be with the sound of tambourines and harps. He will fight with them in battles, brandishing weapons."} ]
        , 33: [ {class: "vs", text: "For his burning place has long been ready. Yes, for the king it is prepared. He has made its pyre deep and large with fire and much wood. Yahweh’s breath, like a stream of sulfur, kindles it."} ]
        }
  , 31: { 1:  [ {class: "q", text: "Woe to those who go down to Egypt for help,"}
              , {class: "q2", text: "and rely on horses,"}
              , {class: "q2", text: "and trust in chariots because they are many,"}
              , {class: "q2", text: "and in horsemen because they are very strong,"}
              , {class: "q2", text: "but they don’t look to the Holy One of Israel,"}
              , {class: "q2", text: "and they don’t seek Yahweh!"}
              ]
        , 2:  [ {class: "q", text: "Yet he also is wise, and will bring disaster,"}
              , {class: "q2", text: "and will not call back his words, but will arise against the house of the evildoers,"}
              , {class: "q2", text: "and against the help of those who work iniquity."}
              ]
        , 3:  [ {class: "q", text: "Now the Egyptians are men, and not God;"}
              , {class: "q2", text: "and their horses flesh, and not spirit."}
              , {class: "q", text: "When Yahweh stretches out his hand, both he who helps shall stumble,"}
              , {class: "q2", text: "and he who is helped shall fall,"}
              , {class: "q2", text: "and they all shall be consumed together."}
              ]
        , 4:  [ {class: "q", text: "For Yahweh says to me,"}
              , {class: "q", text: "“As the lion and the young lion growling over his prey,"}
              , {class: "q2", text: "if a multitude of shepherds is called together against him,"}
              , {class: "q2", text: "will not be dismayed at their voice,"}
              ,  {class: "q2", text: "nor abase himself for their noise,"}
              , {class: "q2", text: "so Yahweh of Armies will come down to fight on Mount Zion and on its heights."}
              ]
        , 5:  [ {class: "q", text: "As birds hovering, so Yahweh of Armies will protect Jerusalem."}
              , {class: "q2", text: "He will protect and deliver it."}
              , {class: "q2", text: "He will pass over and preserve it.”"}
              ]
        , 6:  [ {class: "q", text: "Return to him from whom you have deeply revolted, children of Israel."} ]
        , 7:  [ {class: "vs", text: "For in that day everyone shall cast away his idols of silver and his idols of gold—sin which your own hands have made for you."} ]
        , 8:  [ {class: "endpara", text: "“The Assyrian will fall by the sword, not of man;"}
              , {class: "q2", text: "and the sword, not of mankind, shall devour him."}
              , {class: "q", text: "He will flee from the sword,"}
              , {class: "q2", text: "and his young men will become subject to forced labor."}
              ]
        , 9:  [ {class: "q", text: "His rock will pass away by reason of terror,"}
              , {class: "q2", text: "and his princes will be afraid of the banner,”"}
              , {class: "q", text: "says Yahweh, whose fire is in Zion,"}
              , {class: "q2", text: "and his furnace in Jerusalem."}
              ]
        }
  , 32: { 1:  [ {class: "q", text: "Behold, a king shall reign in righteousness,"}
              , {class: "q2", text: "and princes shall rule in justice."}
              ]
        , 2:  [ {class: "q", text: "A man shall be as a hiding place from the wind,"}
              , {class: "q2", text: "and a covert from the storm,"}
              , {class: "q2", text: "as streams of water in a dry place,"}
              , {class: "q2", text: "as the shade of a large rock in a weary land."}
              ]
        , 3:  [ {class: "q", text: "The eyes of those who see will not be dim,"}
              , {class: "q2", text: "and the ears of those who hear will listen."}
              ]
        , 4:  [ {class: "q", text: "The heart of the rash will understand knowledge,"}
              , {class: "q2", text: "and the tongue of the stammerers will be ready to speak plainly."}
              ]
        , 5:  [ {class: "q", text: "The fool will no longer be called noble,"}
              , {class: "q2", text: "nor the scoundrel be highly respected."}
              ]
        , 6:  [ {class: "q", text: "For the fool will speak folly,"}
              , {class: "q2", text: "and his heart will work iniquity,"}
              , {class: "q2", text: "to practice profanity,"}
              , {class: "q2", text: "and to utter error against Yahweh,"}
              , {class: "q2", text: "to make empty the soul of the hungry,"}
              , {class: "q2", text: "and to cause the drink of the thirsty to fail."}
              ]
        , 7:  [ {class: "q", text: "The ways of the scoundrel are evil."}
              , {class: "q2", text: "He devises wicked plans to destroy the humble with lying words,"}
              , {class: "q2", text: "even when the needy speaks right."}
              ]
        , 8:  [ {class: "q", text: "But the noble devises noble things;"}
              , {class: "q2", text: "and he will continue in noble things."}
              ]
        , 9:  [ {class: "q", text: "Rise up, you women who are at ease! Hear my voice!"}
              , {class: "q2", text: "You careless daughters, give ear to my speech!"}
              ]
        , 10: [ {class: "q", text: "For days beyond a year you will be troubled, you careless women;"}
              , {class: "q2", text: "for the vintage will fail."}
              , {class: "q2", text: "The harvest won’t come."}
              ]
        , 11: [ {class: "q", text: "Tremble, you women who are at ease!"}
              , {class: "q2", text: "Be troubled, you careless ones!"}
              , {class: "q2", text: "Strip yourselves, make yourselves naked,"}
              , {class: "q2", text: "and put sackcloth on your waist."}
              ]
        , 12: [ {class: "q", text: "Beat your breasts for the pleasant fields,"}
              , {class: "q2", text: "for the fruitful vine."}
              ]
        , 13: [ {class: "q", text: "Thorns and briers will come up on my people’s land;"}
              ,  {class: "q2", text: "yes, on all the houses of joy in the joyous city."}
              ]
        , 14: [ {class: "q", text: "For the palace will be forsaken."}
              , {class: "q2", text: "The populous city will be deserted."}
              , {class: "q2", text: "The hill and the watchtower will be for dens forever,"}
              , {class: "q2", text: "a delight for wild donkeys,"}
              , {class: "q2", text: "a pasture of flocks,"}
              ]
        , 15: [ {class: "q", text: "until the Spirit is poured on us from on high,"}
              , {class: "q2", text: "and the wilderness becomes a fruitful field,"}
              , {class: "q2", text: "and the fruitful field is considered a forest."}
              ]
        , 16: [ {class: "q", text: "Then justice will dwell in the wilderness;"}
              , {class: "q2", text: "and righteousness will remain in the fruitful field."}
              ]
        , 17: [ {class: "q", text: "The work of righteousness will be peace,"}
              , {class: "q2", text: "and the effect of righteousness, quietness and confidence forever."}
              ]
        , 18: [ {class: "q", text: "My people will live in a peaceful habitation,"}
              , {class: "q2", text: "in safe dwellings,"}
              , {class: "q2", text: "and in quiet resting places,"}
              ]
        , 19: [ {class: "q", text: "though hail flattens the forest,"}
              , {class: "q2", text: "and the city is leveled completely."}
              ]
        , 20: [ {class: "q", text: "Blessed are you who sow beside all waters,"}
              , {class: "q2", text: "who send out the feet of the ox and the donkey."}
              ]
        }
  , 33: { 1:  [ {class: "q", text: "Woe to you who destroy, but you weren’t destroyed,"}
              , {class: "q2", text: "and who betray, but nobody betrayed you!"}
              , {class: "q", text: "When you have finished destroying, you will be destroyed;"}
              , {class: "q2", text: "and when you have finished betrayal, you will be betrayed."}
              ]
        , 2:  [ {class: "q", text: "Yahweh, be gracious to us. We have waited for you."}
              , {class: "q2", text: "Be our strength every morning,"}
              , {class: "q2", text: "our salvation also in the time of trouble."}
              ]
        , 3:  [ {class: "q", text: "At the noise of the thunder, the peoples have fled."}
              , {class: "q2", text: "When you lift yourself up, the nations are scattered."}
              ]
        , 4:  [ {class: "q", text: "Your plunder will be gathered as the caterpillar gathers."}
              , {class: "q2", text: "Men will leap on it as locusts leap."}
              ]
        , 5:  [ {class: "q", text: "Yahweh is exalted, for he dwells on high."}
              , {class: "q2", text: "He has filled Zion with justice and righteousness."}
              ]
        , 6:  [ {class: "q", text: "There will be stability in your times, abundance of salvation, wisdom, and knowledge."}
              , {class: "q2", text: "The fear of Yahweh is your treasure."}
              ]
        , 7:  [ {class: "q", text: "Behold, their valiant ones cry outside;"}
              , {class: "q2", text: "the ambassadors of peace weep bitterly."}
              ]
        , 8:  [ {class: "q", text: "The highways are desolate."}
              , {class: "q2", text: "The traveling man ceases."}
              , {class: "q2", text: "The covenant is broken."}
              , {class: "q2", text: "He has despised the cities."}
              , {class: "q2", text: "He doesn’t respect man."}
              ]
        , 9:  [ {class: "q", text: "The land mourns and languishes."}
              , {class: "q2", text: "Lebanon is confounded and withers away."}
              , {class: "q2", text: "Sharon is like a desert, and Bashan and Carmel are stripped bare."}
              ]
        , 10: [ {class: "q", text: "“Now I will arise,” says Yahweh."}
              , {class: "q2", text: "“Now I will lift myself up."}
              , {class: "q2", text: "Now I will be exalted."}
              ]
        , 11: [ {class: "q", text: "You will conceive chaff."}
              , {class: "q2", text: "You will give birth to stubble."}
              , {class: "q2", text: "Your breath is a fire that will devour you."}
              ]
        , 12: [ {class: "q", text: "The peoples will be like the burning of lime,"}
              , {class: "q2", text: "like thorns that are cut down and burned in the fire."}
              ]
        , 13: [ {class: "q", text: "Hear, you who are far off, what I have done;"}
              , {class: "q2", text: "and, you who are near, acknowledge my might.”"}
              ]
        , 14: [ {class: "q", text: "The sinners in Zion are afraid."}
              , {class: "q2", text: "Trembling has seized the godless ones."}
              , {class: "q", text: "Who among us can live with the devouring fire?"}
              , {class: "q2", text: "Who among us can live with everlasting burning?"}
              ]
        , 15: [ {class: "q", text: "He who walks righteously"}
              , {class: "q2", text: "and speaks blamelessly,"}
              , {class: "q2", text: "he who despises the gain of oppressions,"}
              , {class: "q2", text: "who gestures with his hands, refusing to take a bribe,"}
              , {class: "q2", text: "who stops his ears from hearing of blood,"}
              , {class: "q2", text: "and shuts his eyes from looking at evil—"}
              ]
        , 16: [ {class: "q", text: "he will dwell on high."}
              , {class: "q2", text: "His place of defense will be the fortress of rocks."}
              , {class: "q2", text: "His bread will be supplied."}
              , {class: "q2", text: "His waters will be sure."}
              ]
        , 17: [ {class: "q", text: "Your eyes will see the king in his beauty."}
              , {class: "q2", text: "They will see a distant land."}
              ]
        , 18: [ {class: "q", text: "Your heart will meditate on the terror."}
              , {class: "q2", text: "Where is he who counted?"}
              , {class: "q2", text: "Where is he who weighed?"}
              , {class: "q2", text: "Where is he who counted the towers?"}
              ]
        , 19: [ {class: "q", text: "You will no longer see the fierce people,"}
              , {class: "q2", text: "a people of a deep speech that you can’t comprehend,"}
              , {class: "q2", text: "with a strange language that you can’t understand."}
              ]
        , 20: [ {class: "q", text: "Look at Zion, the city of our appointed festivals."}
              , {class: "q2", text: "Your eyes will see Jerusalem, a quiet habitation,"}
              , {class: "q2", text: "a tent that won’t be removed."}
              , {class: "q", text: "Its stakes will never be plucked up,"}
              , {class: "q2", text: "nor will any of its cords be broken."}
              ]
        , 21: [ {class: "q", text: "But there Yahweh will be with us in majesty,"}
              , {class: "q2", text: "a place of wide rivers and streams,"}
              , {class: "q2", text: "in which no galley with oars will go,"}
              , {class: "q2", text: "neither will any gallant ship pass by there."}
              ]
        , 22: [ {class: "q", text: "For Yahweh is our judge."}
              , {class: "q2", text: "Yahweh is our lawgiver."}
              , {class: "q2", text: "Yahweh is our king."}
              , {class: "q2", text: "He will save us."}
              ]
        , 23: [ {class: "q", text: "Your rigging is untied."}
              , {class: "q2", text: "They couldn’t strengthen the foot of their mast."}
              , {class: "q2", text: "They couldn’t spread the sail."}
              , {class: "q", text: "Then the prey of a great plunder was divided."}
              , {class: "q2", text: "The lame took the prey."}
              ]
        , 24: [ {class: "q", text: "The inhabitant won’t say, “I am sick.”"}
              , {class: "q2", text: "The people who dwell therein will be forgiven their iniquity."}
              ]
        }
  , 34: { 1:  [ {class: "q", text: "Come near, you nations, to hear!"}
              , {class: "q2", text: "Listen, you peoples."}
              , {class: "q2", text: "Let the earth and all it contains hear,"}
              , {class: "q2", text: "the world, and everything that comes from it."}
              ]
        , 2:  [ {class: "q", text: "For Yahweh is enraged against all the nations,"}
              , {class: "q2", text: "and angry with all their armies."}
              , {class: "q", text: "He has utterly destroyed them."}
              , {class: "q2", text: "He has given them over for slaughter."}
              ]
        , 3:  [ {class: "q", text: "Their slain will also be cast out,"}
              , {class: "q2", text: "and the stench of their dead bodies will come up."}
              , {class: "q2", text: "The mountains will melt in their blood."}
              ]
        , 4:  [ {class: "q", text: "All of the army of the sky will be dissolved."}
              , {class: "q2", text: "The sky will be rolled up like a scroll,"}
              , {class: "q2", text: "and all its armies will fade away,"}
              , {class: "q2", text: "as a leaf fades from off a vine or a fig tree."}
              ]
        , 5:  [ {class: "q", text: "For my sword has drunk its fill in the sky."}
              , {class: "q2", text: "Behold, it will come down on Edom,"}
              , {class: "q2", text: "and on the people of my curse, for judgment."}
              ]
        , 6:  [ {class: "q", text: "Yahweh’s sword is filled with blood."}
              , {class: "q2", text: "It is covered with fat, with the blood of lambs and goats,"}
              , {class: "q2", text: "with the fat of the kidneys of rams;"}
              , {class: "q2", text: "for Yahweh has a sacrifice in Bozrah,"}
                , {class: "q2", text: "And a great slaughter in the land of Edom."}
                ]
        , 7:  [ {class: "q", text: "The wild oxen will come down with them,"}
              , {class: "q2", text: "and the young bulls with the mighty bulls;"}
              , {class: "q2", text: "and their land will be drunken with blood,"}
              , {class: "q2", text: "and their dust made greasy with fat."}
              ]
        , 8:  [ {class: "q", text: "For Yahweh has a day of vengeance,"}
              , {class: "q2", text: "a year of recompense for the cause of Zion."}
              ]
        , 9:  [ {class: "q", text: "Its streams will be turned into pitch,"}
              , {class: "q2", text: "its dust into sulfur,"}
              , {class: "q2", text: "And its land will become burning pitch."}
              ]
        , 10: [ {class: "q", text: "It won’t be quenched night nor day."}
              , {class: "q2", text: "Its smoke will go up forever."}
              , {class: "q2", text: "From generation to generation, it will lie waste."}
              , {class: "q2", text: "No one will pass through it forever and ever."}
              ]
        , 11: [ {class: "q", text: "But the pelican and the porcupine will possess it."}
              , {class: "q2", text: "The owl and the raven will dwell in it."}
              , {class: "q", text: "He will stretch the line of confusion over it,"}
              , {class: "q2", text: "and the plumb line of emptiness."}
              ]
        , 12: [ {class: "q", text: "They shall call its nobles to the kingdom, but none shall be there;"}
              , {class: "q2", text: "and all its princes shall be nothing."}
              ]
        , 13: [ {class: "q", text: "Thorns will come up in its palaces,"}
              , {class: "q2", text: "nettles and thistles in its fortresses;"}
              , {class: "q2", text: "and it will be a habitation of jackals,"}
              , {class: "q2", text: "a court for ostriches."}
              ]
        , 14: [ {class: "q", text: "The wild animals of the desert will meet with the wolves,"}
              , {class: "q2", text: "and the wild goat will cry to his fellow."}
              , {class: "q", text: "Yes, the night creature shall settle there,"}
              , {class: "q2", text: "and shall find herself a place of rest."}
              ]
        , 15: [ {class: "q", text: "The arrow snake will make her nest there,"}
              , {class: "q2", text: "and lay, hatch, and gather under her shade."}
              , {class: "q2", text: "Yes, the kites will be gathered there, every one with her mate."}
              ]
        , 16: [ {class: "q", text: "Search in the book of Yahweh, and read:"}
              , {class: "q2", text: "not one of these will be missing."}
              , {class: "q2", text: "None will lack her mate."}
              , {class: "q2", text: "For my mouth has commanded,"}
              , {class: "q2", text: "and his Spirit has gathered them."}
              ]
        , 17: [ {class: "q", text: "He has cast the lot for them,"}
              , {class: "q2", text: "and his hand has divided it to them with a measuring line."}
              , {class: "q2", text: "They shall possess it forever."}
              , {class: "q2", text: "From generation to generation they will dwell in it."}
              ]
        }
  , 35: { 1:  [ {class: "q", text: "The wilderness and the dry land will be glad."}
              , {class: "q2", text: "The desert will rejoice and blossom like a rose."}
              ]
        , 2:  [ {class: "q", text: "It will blossom abundantly,"}
              , {class: "q2", text: "and rejoice even with joy and singing."}
              , {class: "q2", text: "Lebanon’s glory will be given to it,"}
              , {class: "q2", text: "the excellence of Carmel and Sharon."}
              , {class: "q2", text: "They will see Yahweh’s glory,"}
              , {class: "q2", text: "the excellence of our God."}
              ]
        , 3:  [ {class: "q", text: "Strengthen the weak hands,"}
              , {class: "q2", text: "and make the feeble knees firm."}
              ]
        , 4:  [ {class: "q", text: "Tell those who have a fearful heart, “Be strong!"}
              , {class: "q2", text: "Don’t be afraid!"}
              , {class: "q", text: "Behold, your God will come with vengeance, God’s retribution."}
              , {class: "q2", text: "He will come and save you."}
              ]
        , 5:  [ {class: "q", text: "Then the eyes of the blind will be opened,"}
              , {class: "q2", text: "and the ears of the deaf will be unstopped."}
              ]
        , 6:  [ {class: "q", text: "Then the lame man will leap like a deer,"}
              , {class: "q2", text: "and the tongue of the mute will sing;"}
              , {class: "q2", text: "for waters will break out in the wilderness,"}
              , {class: "q2", text: "and streams in the desert."}
              ]
        , 7:  [ {class: "q", text: "The burning sand will become a pool,"}
              , {class: "q2", text: "and the thirsty ground springs of water."}
              , {class: "q2", text: "Grass with reeds and rushes will be in the habitation of jackals, where they lay."}
              ]
        , 8:  [ {class: "q", text: "A highway will be there, a road,"}
              , {class: "q2", text: "and it will be called “The Holy Way”."}
              , {class: "q", text: "The unclean shall not pass over it,"}
              , {class: "q2", text: "but it will be for those who walk in the Way."}
              , {class: "q2", text: "Wicked fools shall not go there."}
              ]
        , 9:  [ {class: "q", text: "No lion will be there,"}
              , {class: "q2", text: "nor will any ravenous animal go up on it."}
              , {class: "q2", text: "They will not be found there;"}
              , {class: "q2", text: "but the redeemed will walk there."}
              ]
        , 10: [ {class: "q", text: "Then Yahweh’s ransomed ones will return,"}
              , {class: "q2", text: "and come with singing to Zion;"}
              , {class: "q2", text: "and everlasting joy will be on their heads."}
              , {class: "q", text: "They will obtain gladness and joy,"}
              , {class: "q2", text: "and sorrow and sighing will flee away.”"}
              ]
        }
  , 36: { 1:  [ {class: "vs", text: "Now in the fourteenth year of king Hezekiah, Sennacherib king of Assyria attacked all of the fortified cities of Judah and captured them."} ]
        , 2:  [ {class: "vs", text: "The king of Assyria sent Rabshakeh from Lachish to Jerusalem to king Hezekiah with a large army. He stood by the aqueduct from the upper pool in the fuller’s field highway."} ]
        , 3:  [ {class: "vs", text: "Then Eliakim the son of Hilkiah, who was over the household, and Shebna the scribe, and Joah, the son of Asaph, the recorder came out to him."} ]
        , 4:  [ {class: "vs", text: "Rabshakeh said to them, “Now tell Hezekiah, ‘The great king, the king of Assyria, says, “What confidence is this in which you trust?"} ]
        , 5:  [ {class: "vs", text: "I say that your counsel and strength for the war are only vain words. Now in whom do you trust, that you have rebelled against me?"} ]
        , 6:  [ {class: "vs", text: "Behold, you trust in the staff of this bruised reed, even in Egypt, which if a man leans on it, it will go into his hand and pierce it. So is Pharaoh king of Egypt to all who trust in him."} ]
        , 7:  [ {class: "vs", text: "But if you tell me, ‘We trust in Yahweh our God,’ isn’t that he whose high places and whose altars Hezekiah has taken away, and has said to Judah and to Jerusalem, ‘You shall worship before this altar?’”"} ]
        , 8:  [ {class: "vs", text: "Now therefore, please make a pledge to my master the king of Assyria, and I will give you two thousand horses, if you are able on your part to set riders on them."} ]
        , 9:  [ {class: "vs", text: "How then can you turn away the face of one captain of the least of my master’s servants, and put your trust on Egypt for chariots and for horsemen?"} ]
        , 10: [ {class: "vs", text: "Have I come up now without Yahweh against this land to destroy it? Yahweh said to me, “Go up against this land, and destroy it.”’”"} ]
        , 11: [ {class: "vs", text: "Then Eliakim, Shebna and Joah said to Rabshakeh, “Please speak to your servants in Aramaic, for we understand it. Don’t speak to us in the Jews’ language in the hearing of the people who are on the wall.”"} ]
        , 12: [ {class: "vs", text: "But Rabshakeh said, “Has my master sent me only to your master and to you, to speak these words, and not to the men who sit on the wall, who will eat their own dung and drink their own urine with you?”"} ]
        , 13: [ {class: "vs", text: "Then Rabshakeh stood, and called out with a loud voice in the Jews’ language, and said, “Hear the words of the great king, the king of Assyria!"} ]
        , 14: [ {class: "vs", text: "The king says, ‘Don’t let Hezekiah deceive you; for he will not be able to deliver you."} ]
        , 15: [ {class: "vs", text: "Don’t let Hezekiah make you trust in Yahweh, saying, “Yahweh will surely deliver us. This city won’t be given into the hand of the king of Assyria.”’"} ]
        , 16: [ {class: "vs", text: "Don’t listen to Hezekiah, for the king of Assyria says, ‘Make your peace with me, and come out to me; and each of you eat from his vine, and each one from his fig tree, and each one of you drink the waters of his own cistern;"} ]
        , 17: [ {class: "vs", text: "until I come and take you away to a land like your own land, a land of grain and new wine, a land of bread and vineyards."} ]
        , 18: [ {class: "vs", text: "Beware lest Hezekiah persuade you, saying, “Yahweh will deliver us.” Have any of the gods of the nations delivered their lands from the hand of the king of Assyria?"} ]
        , 19: [ {class: "vs", text: "Where are the gods of Hamath and Arpad? Where are the gods of Sepharvaim? Have they delivered Samaria from my hand?"} ]
        , 20: [ {class: "vs", text: "Who are they among all the gods of these countries that have delivered their country out of my hand, that Yahweh should deliver Jerusalem out of my hand?’”"} ]
        , 21: [ {class: "vs", text: "But they remained silent, and said nothing in reply, for the king’s commandment was, “Don’t answer him.”"} ]
        , 22: [ {class: "vs", text: "Then Eliakim the son of Hilkiah, who was over the household, and Shebna the scribe, and Joah, the son of Asaph, the recorder, came to Hezekiah with their clothes torn, and told him the words of Rabshakeh."} ]
        }
  , 37: { 1:  [ {class: "vs", text: "When king Hezekiah heard it, he tore his clothes, covered himself with sackcloth, and went into Yahweh’s house."} ]
        , 2:  [ {class: "vs", text: "He sent Eliakim, who was over the household, and Shebna the scribe, and the elders of the priests, covered with sackcloth, to Isaiah the prophet, the son of Amoz."} ]
        , 3:  [ {class: "vs", text: "They said to him, “Hezekiah says, ‘Today is a day of trouble, and of rebuke, and of rejection; for the children have come to the birth, and there is no strength to give birth."} ]
        , 4:  [ {class: "vs", text: "It may be Yahweh your God will hear the words of Rabshakeh, whom the king of Assyria his master has sent to defy the living God, and will rebuke the words which Yahweh your God has heard. Therefore lift up your prayer for the remnant that is left.’”"} ]
        , 5:  [ {class: "vs", text: "So the servants of king Hezekiah came to Isaiah."} ]
        , 6:  [ {class: "vs", text: "Isaiah said to them, “Tell your master, ‘Yahweh says, “Don’t be afraid of the words that you have heard, with which the servants of the king of Assyria have blasphemed me."} ]
        , 7:  [ {class: "vs", text: "Behold, I will put a spirit in him and he will hear news, and will return to his own land. I will cause him to fall by the sword in his own land.”’”"} ]
        , 8:  [ {class: "vs", text: "So Rabshakeh returned, and found the king of Assyria warring against Libnah, for he had heard that he was departed from Lachish."} ]
        , 9:  [ {class: "vs", text: "He heard news concerning Tirhakah king of Ethiopia, “He has come out to fight against you.” When he heard it, he sent messengers to Hezekiah, saying,"} ]
        , 10: [ {class: "vs", text: "“Thus you shall speak to Hezekiah king of Judah, saying, ‘Don’t let your God in whom you trust deceive you, saying, “Jerusalem won’t be given into the hand of the king of Assyria.”"} ]
        , 11: [ {class: "vs", text: "Behold, you have heard what the kings of Assyria have done to all lands, by destroying them utterly. Shall you be delivered?"} ]
        , 12: [ {class: "vs", text: "Have the gods of the nations delivered them, which my fathers have destroyed, Gozan, Haran, Rezeph, and the children of Eden who were in Telassar?"} ]
        , 13: [ {class: "vs", text: "Where is the king of Hamath, and the king of Arpad, and the king of the city of Sepharvaim, of Hena, and Ivvah?’”"} ]
        , 14: [ {class: "vs", text: "Hezekiah received the letter from the hand of the messengers and read it. Then Hezekiah went up to Yahweh’s house, and spread it before Yahweh."} ]
        , 15: [ {class: "vs", text: "Hezekiah prayed to Yahweh, saying,"} ]
        , 16: [ {class: "vs", text: "“Yahweh of Armies, the God of Israel, who is enthroned among the cherubim, you are the God, even you alone, of all the kingdoms of the earth. You have made heaven and earth."} ]
        , 17: [ {class: "vs", text: "Turn your ear, Yahweh, and hear. Open your eyes, Yahweh, and behold. Hear all of the words of Sennacherib, who has sent to defy the living God."} ]
        , 18: [ {class: "vs", text: "Truly, Yahweh, the kings of Assyria have destroyed all the countries and their land,"} ]
        , 19: [ {class: "vs", text: "and have cast their gods into the fire; for they were no gods, but the work of men’s hands, wood and stone; therefore they have destroyed them."} ]
        , 20: [ {class: "vs", text: "Now therefore, Yahweh our God, save us from his hand, that all the kingdoms of the earth may know that you are Yahweh, even you only.”"} ]
        , 21: [ {class: "vs", text: "Then Isaiah the son of Amoz sent to Hezekiah, saying, “Yahweh, the God of Israel says, ‘Because you have prayed to me against Sennacherib king of Assyria,"} ]
        , 22: [ {class: "vs", text: "this is the word which Yahweh has spoken concerning him. The virgin daughter of Zion has despised you and ridiculed you. The daughter of Jerusalem has shaken her head at you."} ]
        , 23: [ {class: "vs", text: "Whom have you defied and blasphemed? Against whom have you exalted your voice and lifted up your eyes on high? Against the Holy One of Israel."} ]
        , 24: [ {class: "vs", text: "By your servants, you have defied the Lord, and have said, “With the multitude of my chariots I have come up to the height of the mountains, to the innermost parts of Lebanon. I will cut down its tall cedars and its choice cypress trees. I will enter into its farthest height, the forest of its fruitful field."} ]
        , 25: [ {class: "vs", text: "I have dug and drunk water, and with the sole of my feet I will dry up all the rivers of Egypt.”"} ]
        , 26: [ {class: "vs", text: "“‘Have you not heard how I have done it long ago, and formed it in ancient times? Now I have brought it to pass, that it should be yours to destroy fortified cities, turning them into ruinous heaps."} ]
        , 27: [ {class: "vs", text: "Therefore their inhabitants had little power. They were dismayed and confounded. They were like the grass of the field, and like the green herb, like the grass on the housetops, and like a field before its crop has grown."} ]
        , 28: [ {class: "vs", text: "But I know your sitting down, your going out, your coming in, and your raging against me."} ]
        , 29: [ {class: "vs", text: "Because of your raging against me, and because your arrogance has come up into my ears, therefore I will put my hook in your nose and my bridle in your lips, and I will turn you back by the way by which you came."} ]
        , 30: [ {class: "vs", text: "“‘This shall be the sign to you. You will eat this year that which grows of itself, and in the second year that which springs from it; and in the third year sow and reap and plant vineyards, and eat their fruit."} ]
        , 31: [ {class: "vs", text: "The remnant that is escaped of the house of Judah will again take root downward, and bear fruit upward."} ]
        , 32: [ {class: "vs", text: "For out of Jerusalem a remnant will go out, and survivors will escape from Mount Zion. The zeal of Yahweh of Armies will perform this.’"} ]
        , 33: [ {class: "vs", text: "“Therefore Yahweh says concerning the king of Assyria, ‘He will not come to this city, nor shoot an arrow there, neither will he come before it with shield, nor cast up a mound against it."} ]
        , 34: [ {class: "vs", text: "He will return the way that he came, and he won’t come to this city,’ says Yahweh."} ]
        , 35: [ {class: "vs", text: "‘For I will defend this city to save it, for my own sake, and for my servant David’s sake.’”"} ]
        , 36: [ {class: "vs", text: "Then Yahweh’s angel went out and struck one hundred and eighty-five thousand men in the camp of the Assyrians. When men arose early in the morning, behold, these were all dead bodies."} ]
        , 37: [ {class: "vs", text: "So Sennacherib king of Assyria departed, went away, returned to Nineveh, and stayed there."} ]
        , 38: [ {class: "vs", text: "As he was worshiping in the house of Nisroch his god, Adrammelech and Sharezer his sons struck him with the sword; and they escaped into the land of Ararat. Esar Haddon his son reigned in his place."} ]
        }
  , 38: { 1:  [ {class: "vs", text: "In those days Hezekiah was sick and near death. Isaiah the prophet, the son of Amoz, came to him, and said to him, “Yahweh says, ‘Set your house in order, for you will die, and not live.’”"} ]
        , 2:  [ {class: "vs", text: "Then Hezekiah turned his face to the wall and prayed to Yahweh,"} ]
        , 3:  [ {class: "vs", text: "and said, “Remember now, Yahweh, I beg you, how I have walked before you in truth and with a perfect heart, and have done that which is good in your sight.” Then Hezekiah wept bitterly."} ]
        , 4:  [ {class: "vs", text: "Then Yahweh’s word came to Isaiah, saying,"} ]
        , 5:  [ {class: "vs", text: "“Go, and tell Hezekiah, ‘Yahweh, the God of David your father, says, “I have heard your prayer. I have seen your tears. Behold, I will add fifteen years to your life."} ]
        , 6:  [ {class: "vs", text: "I will deliver you and this city out of the hand of the king of Assyria, and I will defend this city."} ]
        , 7:  [ {class: "vs", text: "This shall be the sign to you from Yahweh, that Yahweh will do this thing that he has spoken."} ]
        , 8:  [ {class: "vs", text: "Behold, I will cause the shadow on the sundial, which has gone down on the sundial of Ahaz with the sun, to return backward ten steps.”’” So the sun returned ten steps on the sundial on which it had gone down."} ]
        , 9:  [ {class: "vs", text: "The writing of Hezekiah king of Judah, when he had been sick, and had recovered of his sickness."} ]
        , 10: [ {class: "q", text: "I said, “In the middle of my life I go into the gates of Sheol."}
              , {class: "q2", text: "I am deprived of the residue of my years.”"}
              ]
        , 11: [ {class: "q", text: "I said, “I won’t see Yah,"}
              , {class: "q2", text: "Yah in the land of the living."}
              , {class: "q2", text: "I will see man no more with the inhabitants of the world."}
              ]
        , 12: [ {class: "q", text: "My dwelling is removed,"}
              , {class: "q2", text: "and is carried away from me like a shepherd’s tent."}
              , {class: "q", text: "I have rolled up my life like a weaver."}
              , {class: "q2", text: "He will cut me off from the loom."}
              , {class: "q2", text: "From day even to night you will make an end of me."}
              ]
        , 13: [ {class: "q", text: "I waited patiently until morning."}
              , {class: "q2", text: "He breaks all my bones like a lion."}
              , {class: "q2", text: "From day even to night you will make an end of me."}
              ]
        , 14: [ {class: "q", text: "I chattered like a swallow or a crane."}
              , {class: "q2", text: "I moaned like a dove."}
              , {class: "q2", text: "My eyes weaken looking upward."}
              , {class: "q2", text: "Lord, I am oppressed."}
              , {class: "q2", text: "Be my security.”"}
              ]
        , 15: [ {class: "q", text: "What will I say?"}
              , {class: "q2", text: "He has both spoken to me, and himself has done it."}
              , {class: "q2", text: "I will walk carefully all my years because of the anguish of my soul."}
              ]
        , 16: [ {class: "q", text: "Lord, men live by these things;"}
              , {class: "q2", text: "and my spirit finds life in all of them:"}
              , {class: "q2", text: "you restore me, and cause me to live."}
              ]
        , 17: [ {class: "q", text: "Behold, for peace I had great anguish,"}
              , {class: "q2", text: "but you have in love for my soul delivered it from the pit of corruption;"}
              , {class: "q2", text: "for you have cast all my sins behind your back."}
              ]
        , 18: [ {class: "q", text: "For Sheol can’t praise you."}
              , {class: "q2", text: "Death can’t celebrate you."}
              , {class: "q", text: "Those who go down into the pit can’t hope for your truth."}
              ]
        , 19: [ {class: "q", text: "The living, the living, he shall praise you, as I do today."}
              , {class: "q2", text: "The father shall make known your truth to the children."}
              ]
        , 20: [ {class: "q", text: "Yahweh will save me."}
              , {class: "q2", text: "Therefore we will sing my songs with stringed instruments all the days of our life in Yahweh’s house."}
              ]
        , 21: [ {class: "vs", text: "Now Isaiah had said, “Let them take a cake of figs, and lay it for a poultice on the boil, and he shall recover.”"} ]
        , 22: [ {class: "vs", text: "Hezekiah also had said, “What is the sign that I will go up to Yahweh’s house?”"} ]
        }
  , 39: { 1:  [ {class: "vs", text: "At that time, Merodach Baladan the son of Baladan, king of Babylon, sent letters and a present to Hezekiah; for he heard that he had been sick, and had recovered."} ]
        , 2:  [ {class: "vs", text: "Hezekiah was pleased with them, and showed them the house of his precious things, the silver, the gold, the spices, and the precious oil, and all the house of his armor, and all that was found in his treasures. There was nothing in his house, nor in all his dominion, that Hezekiah didn’t show them."} ]
        , 3:  [ {class: "vs", text: "Then Isaiah the prophet came to king Hezekiah, and asked him, “What did these men say? From where did they come to you?”</p><p> Hezekiah said, “They have come from a country far from me, even from Babylon.”"} ]
        , 4:  [ {class: "vs", text: "Then he asked, “What have they seen in your house?”</p><p> Hezekiah answered, “They have seen all that is in my house. There is nothing among my treasures that I have not shown them.”"} ]
        , 5:  [ {class: "vs", text: "Then Isaiah said to Hezekiah, “Hear the word of Yahweh of Armies:"} ]
        , 6:  [ {class: "vs", text: "‘Behold, the days are coming when all that is in your house, and that which your fathers have stored up until today, will be carried to Babylon. Nothing will be left,’ says Yahweh."} ]
        , 7:  [ {class: "vs", text: "‘They will take away your sons who will issue from you, whom you shall father, and they will be eunuchs in the king of Babylon’s palace.’”"} ]
        , 8:  [ {class: "vs", text: "Then Hezekiah said to Isaiah, “Yahweh’s word which you have spoken is good.” He said moreover, “For there will be peace and truth in my days.”"} ]
        }
  , 4:  { 1:  [ {class: "vs", text: "Seven women shall take hold of one man in that day, saying, “We will eat our own bread, and wear our own clothing. Just let us be called by your name. Take away our reproach.”"} ]
        , 2:  [ {class: "vs", text: "In that day, Yahweh’s branch will be beautiful and glorious, and the fruit of the land will be the beauty and glory of the survivors of Israel."} ]
        , 3:  [ {class: "vs", text: "It will happen that he who is left in Zion and he who remains in Jerusalem shall be called holy, even everyone who is written among the living in Jerusalem,"} ]
        , 4:  [ {class: "vs", text: "when the Lord shall have washed away the filth of the daughters of Zion, and shall have purged the blood of Jerusalem from within it, by the spirit of justice and by the spirit of burning."} ]
        , 5:  [ {class: "vs", text: "Yahweh will create over the whole habitation of Mount Zion and over her assemblies, a cloud and smoke by day, and the shining of a flaming fire by night, for over all the glory will be a canopy."} ]
        , 6:  [ {class: "vs", text: "There will be a pavilion for a shade in the daytime from the heat, and for a refuge and for a shelter from storm and from rain."} ]
        }
  , 40: { 1:  [ {class: "vs", text: "“Comfort, comfort my people,” says your God."} ]
        , 2:  [ {class: "vs", text: "“Speak comfortably to Jerusalem; and call out to her that her warfare is accomplished, that her iniquity is pardoned, that she has received of Yahweh’s hand double for all her sins.”"} ]
        , 3:  [ {class: "q", text: "The voice of one who calls out,"}
              , {class: "q2", text: "“Prepare the way of Yahweh in the wilderness!"}
              , {class: "q2", text: "Make a level highway in the desert for our God."}
              ]
        , 4:  [ {class: "q", text: "Every valley shall be exalted,"}
              , {class: "q2", text: "and every mountain and hill shall be made low."}
              , {class: "q2", text: "The uneven shall be made level,"}
              , {class: "q2", text: "and the rough places a plain."}
              ]
        , 5:  [ {class: "q", text: "Yahweh’s glory shall be revealed,"}
              , {class: "q2", text: "and all flesh shall see it together;"}
              , {class: "q2", text: "for the mouth of Yahweh has spoken it.”"}
              ]
        , 6:  [ {class: "q", text: "The voice of one saying, “Cry!”"}
              , {class: "q2", text: "One said, “What shall I cry?”"}
              , {class: "q", text: "“All flesh is like grass,"}
              , {class: "q2", text: "and all its glory is like the flower of the field."}
              ]
        , 7:  [ {class: "q", text: "The grass withers,"}
              , {class: "q2", text: "the flower fades,"}
              , {class: "q2", text: "because Yahweh’s breath blows on it."}
              , {class: "q2", text: "Surely the people are like grass."}
              ]
        , 8:  [ {class: "q", text: "The grass withers,"}
              , {class: "q2", text: "the flower fades;"}
              , {class: "q2", text: "but the word of our God stands forever.”"}
              ]
        , 9:  [ {class: "q", text: "You who tell good news to Zion, go up on a high mountain."}
              , {class: "q2", text: "You who tell good news to Jerusalem, lift up your voice with strength!"}
              , {class: "q2", text: "Lift it up! Don’t be afraid!"}
              , {class: "q2", text: "Say to the cities of Judah, “Behold, your God!”"}
              ]
        , 10: [ {class: "q", text: "Behold, the Lord Yahweh will come as a mighty one,"}
              , {class: "q2", text: "and his arm will rule for him."}
              , {class: "q2", text: "Behold, his reward is with him,"}
              , {class: "q2", text: "and his recompense before him."}
              ]
        , 11: [ {class: "q", text: "He will feed his flock like a shepherd."}
              , {class: "q2", text: "He will gather the lambs in his arm,"}
              , {class: "q2", text: "and carry them in his bosom."}
              , {class: "q2", text: "He will gently lead those who have their young."}
              ]
        , 12: [ {class: "q", text: "Who has measured the waters in the hollow of his hand,"}
              , {class: "q2", text: "and marked off the sky with his span,"}
              , {class: "q2", text: "and calculated the dust of the earth in a measuring basket,"}
              , {class: "q2", text: "and weighed the mountains in scales,"}
              , {class: "q2", text: "and the hills in a balance?"}
              ]
        , 13: [ {class: "q", text: "Who has directed Yahweh’s Spirit,"}
              , {class: "q2", text: "or has taught him as his counselor?"}
              ]
        , 14: [ {class: "q", text: "Who did he take counsel with,"}
              , {class: "q2", text: "and who instructed him,"}
              , {class: "q2", text: "and taught him in the path of justice,"}
              , {class: "q2", text: "and taught him knowledge,"}
              , {class: "q2", text: "and showed him the way of understanding?"}
              ]
        , 15: [ {class: "q", text: "Behold, the nations are like a drop in a bucket,"}
              , {class: "q2", text: "and are regarded as a speck of dust on a balance."}
              , {class: "q2", text: "Behold, he lifts up the islands like a very little thing."}
              ]
        , 16: [ {class: "q", text: "Lebanon is not sufficient to burn,"}
              , {class: "q2", text: "nor its animals sufficient for a burnt offering."}
              ]
        , 17: [ {class: "q", text: "All the nations are like nothing before him."}
              , {class: "q2", text: "They are regarded by him as less than nothing, and vanity."}
              ]
        , 18: [ {class: "q", text: "To whom then will you liken God?"}
              , {class: "q2", text: "Or what likeness will you compare to him?"}
              ]
        , 19: [ {class: "q", text: "A workman has cast an image,"}
              , {class: "q2", text: "and the goldsmith overlays it with gold,"}
              , {class: "q2", text: "and casts silver chains for it."}
              ]
        , 20: [ {class: "q", text: "He who is too impoverished for such an offering chooses a tree that will not rot."}
              , {class: "q2", text: "He seeks a skillful workman to set up a carved image for him that will not be moved."}
              ]
        , 21: [ {class: "q", text: "Haven’t you known?"}
              , {class: "q2", text: "Haven’t you heard?"}
              , {class: "q2", text: "Haven’t you been told from the beginning?"}
              , {class: "q2", text: "Haven’t you understood from the foundations of the earth?"}
              ]
        , 22: [ {class: "q", text: "It is he who sits above the circle of the earth,"}
              , {class: "q2", text: "and its inhabitants are like grasshoppers;"}
              , {class: "q2", text: "who stretches out the heavens like a curtain,"}
              , {class: "q2", text: "and spreads them out like a tent to dwell in,"}
              ]
        , 23: [ {class: "q2", text: "who brings princes to nothing,"}
              , {class: "q2", text: "who makes the judges of the earth meaningless."}
              ]
        , 24: [ {class: "q", text: "They are planted scarcely."}
              , {class: "q2", text: "They are sown scarcely."}
              , {class: "q2", text: "Their stock has scarcely taken root in the ground."}
              , {class: "q2", text: "He merely blows on them, and they wither,"}
              , {class: "q2", text: "and the whirlwind takes them away as stubble."}
              ]
        , 25: [ {class: "q", text: "“To whom then will you liken me?"}
              , {class: "q2", text: "Who is my equal?” says the Holy One."}
              ]
        , 26: [ {class: "q", text: "Lift up your eyes on high,"}
              , {class: "q2", text: "and see who has created these,"}
              , {class: "q2", text: "who brings out their army by number."}
              , {class: "q2", text: "He calls them all by name."}
              , {class: "q2", text: "by the greatness of his might,"}
              , {class: "q2", text: "and because he is strong in power,"}
              , {class: "q2", text: "not one is lacking."}
              ]
        , 27: [ {class: "q", text: "Why do you say, Jacob,"}
              , {class: "q2", text: "and speak, Israel,"}
              , {class: "q2", text: "“My way is hidden from Yahweh,"}
              , {class: "q2", text: "and the justice due me is disregarded by my God?”"}
              ]
        , 28: [ {class: "q", text: "Haven’t you known?"}
              , {class: "q2", text: "Haven’t you heard?"}
              , {class: "q2", text: "The everlasting God, Yahweh,"}
              , {class: "q2", text: "the Creator of the ends of the earth, doesn’t faint."}
              , {class: "q2", text: "He isn’t weary."}
              , {class: "q2", text: "His understanding is unsearchable."}
              ]
        , 29: [ {class: "q", text: "He gives power to the weak."}
              , {class: "q2", text: "He increases the strength of him who has no might."}
              ]
        , 30: [ {class: "q", text: "Even the youths faint and get weary,"}
              , {class: "q2", text: "and the young men utterly fall;"}
              ]
        , 31: [ {class: "q2", text: "but those who wait for Yahweh will renew their strength."}
              , {class: "q2", text: "They will mount up with wings like eagles."}
              , {class: "q2", text: "They will run, and not be weary."}
              , {class: "q2", text: "They will walk, and not faint."}
              ]
          }
  , 41: { 1:  [ {class: "q", text: "“Keep silent before me, islands,"}
              , {class: "q2", text: "and let the peoples renew their strength."}
              , {class: "q", text: "Let them come near,"}
              , {class: "q2", text: "then let them speak."}
              , {class: "q2", text: "Let’s meet together for judgment."}
              ]
        , 2:  [ {class: "q", text: "Who has raised up one from the east?"}
              , {class: "q2", text: "Who called him to his foot in righteousness?"}
              , {class: "q2", text: "He hands over nations to him"}
              , {class: "q2", text: "and makes him rule over kings."}
              , {class: "q2", text: "He gives them like the dust to his sword,"}
              , {class: "q2", text: "like the driven stubble to his bow."}
              ]
        , 3:  [ {class: "q", text: "He pursues them"}
              , {class: "q2", text: "and passes by safely,"}
              , {class: "q2", text: "even by a way that he had not gone with his feet."}
              ]
        , 4:  [ {class: "q", text: "Who has worked and done it,"}
              , {class: "q2", text: "calling the generations from the beginning?"}
              , {class: "q2", text: "I, Yahweh, the first, and with the last, I am he.”"}
              ]
        , 5:  [ {class: "q", text: "The islands have seen, and fear."}
              , {class: "q2", text: "The ends of the earth tremble."}
              , {class: "q2", text: "They approach, and come."}
              ]
        , 6:  [ {class: "q", text: "Everyone helps his neighbor."}
              , {class: "q2", text: "They say to their brothers, “Be strong!”"}
              ]
        , 7:  [ {class: "q", text: "So the carpenter encourages the goldsmith."}
              , {class: "q2", text: "He who smoothes with the hammer encourages him who strikes the anvil,"}
              , {class: "q2", text: "saying of the soldering, “It is good;”"}
              , {class: "q2", text: "and he fastens it with nails, that it might not totter."}
              ]
        , 8:  [ {class: "q", text: "“But you, Israel, my servant,"}
              , {class: "q2", text: "Jacob whom I have chosen,"}
              , {class: "q2", text: "the offspring of Abraham my friend,"}
              ]
        , 9:  [ {class: "q2", text: "You whom I have taken hold of from the ends of the earth,"}
              , {class: "q2", text: "and called from its corners,"}
              , {class: "q2", text: "and said to you, ‘You are my servant, I have chosen you and have not cast you away.’"}
              ]
        , 10: [ {class: "q", text: "Don’t you be afraid, for I am with you."}
              , {class: "q2", text: "Don’t be dismayed, for I am your God."}
              , {class: "q2", text: "I will strengthen you."}
              , {class: "q2", text: "Yes, I will help you."}
              , {class: "q2", text: "Yes, I will uphold you with the right hand of my righteousness."}
              ]
        , 11: [ {class: "q", text: "Behold, all those who are incensed against you will be disappointed and confounded."}
              , {class: "q2", text: "Those who strive with you will be like nothing, and shall perish."}
              ]
        , 12: [ {class: "q", text: "You will seek them, and won’t find them,"}
              , {class: "q2", text: "even those who contend with you."}
              , {class: "q2", text: "Those who war against you will be as nothing,"}
              , {class: "q2", text: "as a non-existent thing."}
              ]
        , 13: [ {class: "q", text: "For I, Yahweh your God, will hold your right hand,"}
              , {class: "q2", text: "saying to you, ‘Don’t be afraid."}
              , {class: "q2", text: "I will help you.’"}
              ]
        , 14: [ {class: "q", text: "Don’t be afraid, you worm Jacob,"}
              , {class: "q2", text: "and you men of Israel."}
              , {class: "q2", text: "I will help you,” says Yahweh."}
              , {class: "q2", text: "“Your Redeemer is the Holy One of Israel."}
              ]
        , 15: [ {class: "q", text: "Behold, I have made you into a new sharp threshing instrument with teeth."}
              , {class: "q2", text: "You will thresh the mountains,"}
              , {class: "q2", text: "and beat them small,"}
              , {class: "q2", text: "and will make the hills like chaff."}
              ]
        , 16: [ {class: "q", text: "You will winnow them,"}
              , {class: "q2", text: "and the wind will carry them away,"}
              , {class: "q2", text: "and the whirlwind will scatter them."}
              , {class: "q", text: "You will rejoice in Yahweh."}
              , {class: "q2", text: "You will glory in the Holy One of Israel."}
              ]
        , 17: [ {class: "q", text: "The poor and needy seek water, and there is none."}
              , {class: "q2", text: "Their tongue fails for thirst."}
              , {class: "q", text: "I, Yahweh, will answer them."}
              , {class: "q2", text: "I, the God of Israel, will not forsake them."}
              ]
        , 18: [ {class: "q", text: "I will open rivers on the bare heights,"}
              , {class: "q2", text: "and springs in the middle of the valleys."}
              , {class: "q2", text: "I will make the wilderness a pool of water,"}
              , {class: "q2", text: "and the dry land springs of water."}
              ]
        , 19: [ {class: "q", text: "I will put cedar, acacia, myrtle, and oil trees in the wilderness."}
              , {class: "q2", text: "I will set cypress trees, pine, and box trees together in the desert;"}
              ]
        , 20: [ {class: "q2", text: "that they may see, know, consider, and understand together,"}
              , {class: "q2", text: "that Yahweh’s hand has done this,"}
              , {class: "q2", text: "and the Holy One of Israel has created it."}
              ]
        , 21: [ {class: "q", text: "Produce your cause,” says Yahweh."}
              , {class: "q2", text: "“Bring out your strong reasons!” says the King of Jacob."}
              ]
        , 22: [ {class: "q", text: "“Let them announce and declare to us what will happen!"}
              , {class: "q2", text: "Declare the former things, what they are,"}
              , {class: "q2", text: "that we may consider them, and know the latter end of them;"}
              , {class: "q2", text: "or show us things to come."}
              ]
        , 23: [ {class: "q", text: "Declare the things that are to come hereafter,"}
              , {class: "q2", text: "that we may know that you are gods."}
              , {class: "q", text: "Yes, do good, or do evil,"}
              , {class: "q2", text: "that we may be dismayed,"}
              , {class: "q2", text: "and see it together."}
              ]
        , 24: [ {class: "q", text: "Behold, you are nothing,"}
              , {class: "q2", text: "and your work is nothing."}
              , {class: "q2", text: "He who chooses you is an abomination."}
              ]
        , 25: [ {class: "q", text: "“I have raised up one from the north, and he has come,"}
              , {class: "q2", text: "from the rising of the sun, one who calls on my name,"}
              , {class: "q2", text: "and he shall come on rulers as on mortar,"}
              , {class: "q2", text: "and as the potter treads clay."}
              ]
        , 26: [ {class: "q", text: "Who has declared it from the beginning, that we may know?"}
              , {class: "q2", text: "and before, that we may say, ‘He is right?’"}
              , {class: "q", text: "Surely, there is no one who declares."}
              , {class: "q2", text: "Surely, there is no one who shows."}
              , {class: "q2", text: "Surely, there is no one who hears your words."}
              ]
        , 27: [ {class: "q", text: "I am the first to say to Zion, ‘Behold, look at them;’"}
              , {class: "q2", text: "and I will give one who brings good news to Jerusalem."}
              ]
        , 28: [ {class: "q", text: "When I look, there is no man,"}
              , {class: "q2", text: "even among them there is no counselor who, when I ask of them, can answer a word."}
              ]
        , 29: [ {class: "q", text: "Behold, all of their deeds are vanity and nothing."}
              , {class: "q2", text: "Their molten images are wind and confusion."}
              ]
        }
  , 42: { 1:  [ {class: "q", text: "“Behold, my servant, whom I uphold,"}
              , {class: "q2", text: "my chosen, in whom my soul delights:"}
              , {class: "q2", text: "I have put my Spirit on him."}
              , {class: "q2", text: "He will bring justice to the nations."}
              ]
        , 2:  [ {class: "q", text: "He will not shout,"}
              , {class: "q2", text: "nor raise his voice,"}
              , {class: "q2", text: "nor cause it to be heard in the street."}
              ]
        , 3:  [ {class: "q", text: "He won’t break a bruised reed."}
              , {class: "q2", text: "He won’t quench a dimly burning wick."}
              , {class: "q2", text: "He will faithfully bring justice."}
              ]
        , 4:  [ {class: "q", text: "He will not fail nor be discouraged,"}
              , {class: "q2", text: "until he has set justice in the earth,"}
              , {class: "q2", text: "and the islands wait for his law.”"}
              ]
        , 5:  [ {class: "q", text: "God Yahweh,"}
              , {class: "q2", text: "he who created the heavens and stretched them out,"}
              , {class: "q2", text: "he who spread out the earth and that which comes out of it,"}
              , {class: "q2", text: "he who gives breath to its people and spirit to those who walk in it, says:"}
              ]
        , 6:  [ {class: "q", text: "“I, Yahweh, have called you in righteousness."}
              , {class: "q2", text: "I will hold your hand."}
              , {class: "q2", text: "I will keep you,"}
              , {class: "q2", text: "and make you a covenant for the people,"}
              , {class: "q2", text: "as a light for the nations,"}
              ]
        , 7:  [ {class: "q2", text: "to open the blind eyes,"}
              , {class: "q2", text: "to bring the prisoners out of the dungeon,"}
              , {class: "q2", text: "and those who sit in darkness out of the prison."}
              ]
        , 8:  [ {class: "q", text: "“I am Yahweh."}
              , {class: "q2", text: "That is my name."}
              , {class: "q2", text: "I will not give my glory to another,"}
              , {class: "q2", text: "nor my praise to engraved images."}
              ]
        , 9:  [ {class: "q", text: "Behold, the former things have happened"}
              , {class: "q2", text: "and I declare new things."}
              , {class: "q2", text: "I tell you about them before they come up.”"}
              ]
        , 10: [ {class: "q", text: "Sing to Yahweh a new song,"}
              , {class: "q2", text: "and his praise from the end of the earth,"}
              , {class: "q2", text: "you who go down to the sea,"}
              , {class: "q2", text: "and all that is therein,"}
              , {class: "q2", text: "the islands and their inhabitants."}
              ]
        , 11: [ {class: "q", text: "Let the wilderness and its cities raise their voices,"}
              , {class: "q2", text: "with the villages that Kedar inhabits."}
              , {class: "q2", text: "Let the inhabitants of Sela sing."}
              , {class: "q2", text: "Let them shout from the top of the mountains!"}
              ]
        , 12: [ {class: "q", text: "Let them give glory to Yahweh,"}
              , {class: "q2", text: "and declare his praise in the islands."}
              ]
        , 13: [ {class: "q", text: "Yahweh will go out like a mighty man."}
              , {class: "q2", text: "He will stir up zeal like a man of war."}
              , {class: "q2", text: "He will raise a war cry."}
              , {class: "q2", text: "Yes, he will shout aloud."}
              , {class: "q2", text: "He will triumph over his enemies."}
              ]
        , 14: [ {class: "q", text: "“I have been silent a long time."}
              , {class: "q2", text: "I have been quiet and restrained myself."}
              , {class: "q2", text: "Now I will cry out like a travailing woman. I will both gasp and pant."}
              ]
        , 15: [ {class: "q", text: "I will destroy mountains and hills,"}
              , {class: "q2", text: "and dry up all their herbs."}
              , {class: "q2", text: "I will make the rivers islands,"}
              , {class: "q2", text: "and will dry up the pools."}
              ]
        , 16: [ {class: "q", text: "I will bring the blind by a way that they don’t know."}
              , {class: "q2", text: "I will lead them in paths that they don’t know."}
              , {class: "q2", text: "I will make darkness light before them,"}
              , {class: "q2", text: "and crooked places straight."}
              , {class: "q2", text: "I will do these things,"}
              , {class: "q2", text: "and I will not forsake them."}
              ]
        , 17: [ {class: "q", text: "“Those who trust in engraved images,"}
              , {class: "q2", text: "who tell molten images,"}
              , {class: "q2", text: "‘You are our gods,’"}
              , {class: "q2", text: "will be turned back."}
              , {class: "q2", text: "They will be utterly disappointed."}
              ]
        , 18: [ {class: "q", text: "“Hear, you deaf,"}
              , {class: "q2", text: "and look, you blind,"}
              , {class: "q2", text: "that you may see."}
              ]
        , 19: [ {class: "q", text: "Who is blind, but my servant?"}
              , {class: "q2", text: "Or who is as deaf as my messenger whom I send?"}
              , {class: "q", text: "Who is as blind as he who is at peace,"}
              , {class: "q2", text: "and as blind as Yahweh’s servant?"}
              ]
        , 20: [ {class: "q", text: "You see many things, but don’t observe."}
              , {class: "q2", text: "His ears are open, but he doesn’t listen."}
              ]
        , 21: [ {class: "q", text: "It pleased Yahweh, for his righteousness’ sake, to magnify the law"}
              , {class: "q2", text: "and make it honorable."}
              ]
        , 22: [ {class: "q", text: "But this is a robbed and plundered people."}
              , {class: "q2", text: "All of them are snared in holes,"}
              , {class: "q2", text: "and they are hidden in prisons."}
              , {class: "q", text: "They have become captives, and no one delivers,"}
              , {class: "q2", text: "and a plunder, and no one says, ‘Restore them!’"}
              ]
        , 23: [ {class: "q", text: "Who is there among you who will give ear to this?"}
              , {class: "q2", text: "Who will listen and hear for the time to come?"}
              ]
        , 24: [ {class: "q", text: "Who gave Jacob as plunder,"}
              , {class: "q2", text: "and Israel to the robbers?"}
              , {class: "q2", text: "Didn’t Yahweh, he against whom we have sinned?"}
              , {class: "q2", text: "For they would not walk in his ways,"}
              , {class: "q2", text: "and they disobeyed his law."}
              ]
        , 25: [ {class: "q", text: "Therefore he poured the fierceness of his anger on him,"}
              , {class: "q2", text: "and the strength of battle."}
              , {class: "q", text: "It set him on fire all around, but he didn’t know."}
              , {class: "q2", text: "It burned him, but he didn’t take it to heart.”"}
              ]
        }
  , 43: { 1:  [ {class: "q", text: "But now Yahweh who created you, Jacob,"}
              , {class: "q2", text: "and he who formed you, Israel, says:"}
              , {class: "q", text: "“Don’t be afraid, for I have redeemed you."}
              , {class: "q2", text: "I have called you by your name."}
              , {class: "q2", text: "You are mine."}
              ]
        , 2:  [ {class: "q", text: "When you pass through the waters, I will be with you,"}
              , {class: "q2", text: "and through the rivers, they will not overflow you."}
              , {class: "q", text: "When you walk through the fire, you will not be burned,"}
              , {class: "q2", text: "and flame will not scorch you."}
              ]
        , 3:  [ {class: "q", text: "For I am Yahweh your God,"}
              , {class: "q2", text: "the Holy One of Israel,"}
              , {class: "q2", text: "your Savior."}
              , {class: "q", text: "I have given Egypt as your ransom,"}
              , {class: "q2", text: "Ethiopia and Seba in your place."}
              ]
        , 4:  [ {class: "q", text: "Since you have been precious and honored in my sight,"}
              , {class: "q2", text: "and I have loved you,"}
              , {class: "q2", text: "therefore I will give people in your place,"}
              , {class: "q2", text: "and nations instead of your life."}
              ]
        , 5:  [ {class: "q", text: "Don’t be afraid, for I am with you."}
              , {class: "q2", text: "I will bring your offspring from the east,"}
              , {class: "q2", text: "and gather you from the west."}
              ]
        , 6:  [ {class: "q", text: "I will tell the north, ‘Give them up!’"}
              , {class: "q2", text: "and tell the south, ‘Don’t hold them back!"}
              , {class: "q2", text: "Bring my sons from far away,"}
              , {class: "q2", text: "and my daughters from the ends of the earth—"}
              ]
        , 7:  [ {class: "q", text: "everyone who is called by my name,"}
              , {class: "q2", text: "and whom I have created for my glory,"}
              , {class: "q2", text: "whom I have formed,"}
              , {class: "q2", text: "yes, whom I have made.’”"}
              ]
        , 8:  [ {class: "q", text: "Bring out the blind people who have eyes,"}
              , {class: "q2", text: "and the deaf who have ears."}
              ]
        , 9:  [ {class: "q", text: "Let all the nations be gathered together,"}
              , {class: "q2", text: "and let the peoples be assembled."}
              , {class: "q", text: "Who among them can declare this,"}
              , {class: "q2", text: "and show us former things?"}
              , {class: "q", text: "Let them bring their witnesses, that they may be justified,"}
              , {class: "q2", text: "or let them hear, and say, “That is true.”"}
              ]
        , 10: [ {class: "q", text: "“You are my witnesses,” says Yahweh,"}
              , {class: "q2", text: "“With my servant whom I have chosen;"}
              , {class: "q2", text: "that you may know and believe me,"}
              , {class: "q2", text: "and understand that I am he."}
              , {class: "q", text: "Before me there was no God formed,"}
              , {class: "q2", text: "neither will there be after me."}
              ]
        , 11: [ {class: "q", text: "I myself am Yahweh."}
              , {class: "q2", text: "Besides me, there is no savior."}
              ]
        , 12: [ {class: "q", text: "I have declared, I have saved, and I have shown,"}
              , {class: "q2", text: "and there was no strange god among you."}
              , {class: "q", text: "Therefore you are my witnesses”,"}
              , {class: "q2", text: "says Yahweh, “and I am God."}
              ]
        , 13: [ {class: "q", text: "Yes, since the day was, I am he."}
              , {class: "q2", text: "There is no one who can deliver out of my hand."}
              , {class: "q2", text: "I will work, and who can hinder it?”"}
              ]
        , 14: [ {class: "vs", text: "Yahweh, your Redeemer, the Holy One of Israel says: “For your sake, I have sent to Babylon, and I will bring all of them down as fugitives, even the Chaldeans, in the ships of their rejoicing."} ]
        , 15: [ {class: "vs", text: "I am Yahweh, your Holy One, the Creator of Israel, your King.”"} ]
        , 16: [ {class: "endpara", text: "Yahweh, who makes a way in the sea,"}
              , {class: "q2", text: "and a path in the mighty waters,"}
              ]
        , 17: [ {class: "q", text: "who brings out the chariot and horse,"}
              , {class: "q2", text: "the army and the mighty man"}
              , {class: "q2", text: "(they lie down together, they shall not rise;"}
              , {class: "q2", text: "they are extinct, they are quenched like a wick) says:"}
              ]
        , 18: [ {class: "q", text: "“Don’t remember the former things,"}
              , {class: "q2", text: "and don’t consider the things of old."}
              ]
        , 19: [ {class: "q", text: "Behold, I will do a new thing."}
              , {class: "q2", text: "It springs out now."}
              , {class: "q2", text: "Don’t you know it?"}
              , {class: "q", text: "I will even make a way in the wilderness,"}
              , {class: "q2", text: "and rivers in the desert."}
              ]
        , 20: [ {class: "q", text: "The animals of the field, the jackals and the ostriches, shall honor me,"}
              , {class: "q2", text: "because I give water in the wilderness and rivers in the desert,"}
              , {class: "q2", text: "to give drink to my people, my chosen,"}
              ]
        , 21: [ {class: "q2", text: "the people which I formed for myself,"}
              , {class: "q2", text: "that they might declare my praise."}
              ]
        , 22: [ {class: "q", text: "Yet you have not called on me, Jacob;"}
              , {class: "q2", text: "but you have been weary of me, Israel."}
              ]
        , 23: [ {class: "q", text: "You have not brought me any of your sheep for burnt offerings,"}
              , {class: "q2", text: "neither have you honored me with your sacrifices."}
              , {class: "q", text: "I have not burdened you with offerings,"}
              , {class: "q2", text: "nor wearied you with frankincense."}
              ]
        , 24: [ {class: "q", text: "You have bought me no sweet cane with money,"}
              , {class: "q2", text: "nor have you filled me with the fat of your sacrifices,"}
              , {class: "q", text: "but you have burdened me with your sins."}
              , {class: "q2", text: "You have wearied me with your iniquities."}
              ]
        , 25: [ {class: "q", text: "I, even I, am he who blots out your transgressions for my own sake;"}
              , {class: "q2", text: "and I will not remember your sins."}
              ]
        , 26: [ {class: "q", text: "Put me in remembrance."}
              , {class: "q2", text: "Let us plead together."}
              , {class: "q", text: "Declare your case,"}
              , {class: "q2", text: "that you may be justified."}
              ]
        , 27: [ {class: "q", text: "Your first father sinned,"}
              , {class: "q2", text: "and your teachers have transgressed against me."}
              ]
        , 28: [ {class: "q", text: "Therefore I will profane the princes of the sanctuary;"}
              , {class: "q2", text: "and I will make Jacob a curse,"}
              , {class: "q2", text: "and Israel an insult.”"}
              ]
        }
  , 44: { 1:  [ {class: "q", text: "Yet listen now, Jacob my servant,"}
              , {class: "q2", text: "and Israel, whom I have chosen."}
              ]
        , 2:  [ {class: "q", text: "This is what Yahweh who made you,"}
              , {class: "q2", text: "and formed you from the womb,"}
              , {class: "q2", text: "who will help you says:"}
              , {class: "q", text: "“Don’t be afraid, Jacob my servant;"}
              , {class: "q2", text: "and you, Jeshurun, whom I have chosen."}
              ]
        , 3:  [ {class: "q", text: "For I will pour water on him who is thirsty,"}
              , {class: "q2", text: "and streams on the dry ground."}
              , {class: "q", text: "I will pour my Spirit on your descendants,"}
              , {class: "q2", text: "and my blessing on your offspring:"}
              ]
        , 4:  [ {class: "q", text: "and they will spring up among the grass,"}
              , {class: "q2", text: "as willows by the watercourses."}
              ]
        , 5:  [ {class: "q", text: "One will say, ‘I am Yahweh’s;’"}
              , {class: "q2", text: "and another will be called by the name of Jacob;"}
              , {class: "q2", text: "and another will write with his hand ‘to Yahweh,’"}
              , {class: "q2", text: "and honor the name of Israel.”"}
              ]
        , 6:  [ {class: "q", text: "This is what Yahweh, the King of Israel,"}
              , {class: "q2", text: "and his Redeemer, Yahweh of Armies, says:"}
              , {class: "q", text: "“I am the first, and I am the last;"}
              , {class: "q2", text: "and besides me there is no God."}
              ]
        , 7:  [ {class: "q", text: "Who is like me?"}
              , {class: "q2", text: "Who will call,"}
              , {class: "q2", text: "and will declare it,"}
              , {class: "q2", text: "and set it in order for me,"}
              , {class: "q2", text: "since I established the ancient people?"}
              , {class: "q", text: "Let them declare the things that are coming,"}
              , {class: "q2", text: "and that will happen."}
              ]
        , 8:  [ {class: "q", text: "Don’t fear,"}
              , {class: "q2", text: "neither be afraid."}
              , {class: "q", text: "Haven’t I declared it to you long ago,"}
              , {class: "q2", text: "and shown it?"}
              , {class: "q", text: "You are my witnesses."}
              , {class: "q2", text: "Is there a God besides me?"}
              , {class: "q", text: "Indeed, there is not."}
              , {class: "q2", text: "I don’t know any other Rock.”"}
              ]
        , 9:  [ {class: "q", text: "Everyone who makes a carved image is vain."}
              , {class: "q2", text: "The things that they delight in will not profit."}
              , {class: "q2", text: "Their own witnesses don’t see, nor know, that they may be disappointed."}
              ]
        , 10: [ {class: "q", text: "Who has fashioned a god,"}
              , {class: "q2", text: "or molds an image that is profitable for nothing?"}
              ]
        , 11: [ {class: "q", text: "Behold, all his fellows will be disappointed;"}
              , {class: "q2", text: "and the workmen are mere men."}
              , {class: "q", text: "Let them all be gathered together."}
              , {class: "q2", text: "Let them stand up."}
              , {class: "q2", text: "They will fear."}
              , {class: "q2", text: "They will be put to shame together."}
              ]
        , 12: [ {class: "q", text: "The blacksmith takes an ax,"}
              , {class: "q2", text: "works in the coals,"}
              , {class: "q2", text: "fashions it with hammers,"}
              , {class: "q2", text: "and works it with his strong arm."}
              , {class: "q", text: "He is hungry,"}
              , {class: "q2", text: "and his strength fails;"}
              , {class: "q", text: "he drinks no water,"}
              , {class: "q2", text: "and is faint."}
              ]
        , 13: [ {class: "q", text: "The carpenter stretches out a line."}
              , {class: "q2", text: "He marks it out with a pencil."}
              , {class: "q2", text: "He shapes it with planes."}
              , {class: "q2", text: "He marks it out with compasses,"}
              , {class: "q2", text: "and shapes it like the figure of a man,"}
              , {class: "q2", text: "with the beauty of a man,"}
              , {class: "q2", text: "to reside in a house."}
              ]
        , 14: [ {class: "q", text: "He cuts down cedars for himself,"}
              , {class: "q2", text: "and takes the cypress and the oak,"}
              , {class: "q2", text: "and strengthens for himself one among the trees of the forest."}
              , {class: "q", text: "He plants a cypress tree,"}
              , {class: "q2", text: "and the rain nourishes it."}
              ]
        , 15: [ {class: "q", text: "Then it will be for a man to burn;"}
              , {class: "q2", text: "and he takes some of it, and warms himself."}
              , {class: "q2", text: "Yes, he burns it, and bakes bread."}
              , {class: "q", text: "Yes, he makes a god, and worships it;"}
              , {class: "q2", text: "he makes it a carved image, and falls down to it."}
              ]
        , 16: [ {class: "q", text: "He burns part of it in the fire."}
              , {class: "q2", text: "With part of it, he eats meat."}
              , {class: "q2", text: "He roasts a roast, and is satisfied."}
              , {class: "q", text: "Yes, he warms himself,"}
              , {class: "q2", text: "and says, “Aha! I am warm. I have seen the fire.”"}
              ]
        , 17: [ {class: "q", text: "The rest of it he makes into a god,"}
              , {class: "q2", text: "even his engraved image."}
              , {class: "q", text: "He bows down to it and worships,"}
              , {class: "q2", text: "and prays to it, and says, “Deliver me; for you are my god!”"}
              ]
        , 18: [ {class: "q", text: "They don’t know, neither do they consider:"}
              , {class: "q2", text: "for he has shut their eyes, that they can’t see;"}
                , {class: "q2", text: "and their hearts, that they can’t understand."}
                ]
        , 19: [ {class: "q", text: "No one thinks,"}
              , {class: "q2", text: "neither is there knowledge nor understanding to say,"}
              , {class: "q2", text: "“I have burned part of it in the fire."}
              , {class: "q2", text: "Yes, I have also baked bread on its coals."}
              , {class: "q2", text: "I have roasted meat and eaten it."}
              , {class: "q2", text: "Shall I make the rest of it into an abomination?"}
              , {class: "q2", text: "Shall I bow down to a tree trunk?”"}
              ]
        , 20: [ {class: "q", text: "He feeds on ashes."}
              , {class: "q2", text: "A deceived heart has turned him aside;"}
              , {class: "q2", text: "and he can’t deliver his soul,"}
              , {class: "q2", text: "nor say, “Isn’t there a lie in my right hand?”"}
              ]
        , 21: [ {class: "q", text: "Remember these things, Jacob and Israel;"}
              , {class: "q2", text: "for you are my servant."}
              , {class: "q2", text: "I have formed you."}
              ,  {class: "q2", text: "You are my servant."}
              , {class: "q2", text: "Israel, you will not be forgotten by me."}
              ]
        , 22: [ {class: "q", text: "I have blotted out, as a thick cloud, your transgressions,"}
              , {class: "q2", text: "and, as a cloud, your sins."}
              , {class: "q2", text: "Return to me, for I have redeemed you."}
              ]
        , 23: [ {class: "q", text: "Sing, you heavens, for Yahweh has done it!"}
              , {class: "q2", text: "Shout, you lower parts of the earth!"}
              , {class: "q2", text: "Break out into singing, you mountains, O forest, all of your trees,"}
              , {class: "q2", text: "for Yahweh has redeemed Jacob,"}
              , {class: "q2", text: "and will glorify himself in Israel."}
              ]
        , 24: [ {class: "q", text: "Yahweh, your Redeemer,"}
              , {class: "q2", text: "and he who formed you from the womb says:"}
              , {class: "q", text: "“I am Yahweh, who makes all things;"}
              , {class: "q2", text: "who alone stretches out the heavens;"}
              , {class: "q2", text: "who spreads out the earth by myself;"}
              ]
        , 25: [ {class: "q", text: "who frustrates the signs of the liars,"}
              , {class: "q2", text: "and makes diviners mad;"}
              , {class: "q", text: "who turns wise men backward,"}
              , {class: "q2", text: "and makes their knowledge foolish;"}
              ]
        , 26: [ {class: "q", text: "who confirms the word of his servant,"}
              , {class: "q2", text: "and performs the counsel of his messengers;"}
              , {class: "q", text: "who says of Jerusalem, ‘She will be inhabited;’"}
              , {class: "q2", text: "and of the cities of Judah, ‘They will be built,’"}
              , {class: "q2", text: "and ‘I will raise up its waste places;’"}
              ]
        , 27: [ {class: "q", text: "who says to the deep, ‘Be dry,’"}
              , {class: "q2", text: "and ‘I will dry up your rivers;’"}
              ]
        , 28: [ {class: "q", text: "Who says of Cyrus, ‘He is my shepherd, and shall perform all my pleasure,’"}
              , {class: "q2", text: "even saying of Jerusalem, ‘She will be built;’"}
              , {class: "q2", text: "and of the temple, ‘Your foundation will be laid.’”"}
              ]
        }
  , 45: { 1:  [ {class: "vs", text: "Yahweh says to his anointed, to Cyrus, whose right hand I have held, to subdue nations before him, and strip kings of their armor; to open the doors before him, and the gates shall not be shut:"} ]
        , 2:  [ {class: "q", text: "“I will go before you"}
              , {class: "q2", text: "and make the rough places smooth."}
              , {class: "q", text: "I will break the doors of bronze in pieces"}
              , {class: "q2", text: "and cut apart the bars of iron."}
              ]
        , 3:  [ {class: "q", text: "I will give you the treasures of darkness"}
              , {class: "q2", text: "and hidden riches of secret places,"}
              , {class: "q", text: "that you may know that it is I, Yahweh, who call you by your name,"}
              , {class: "q2", text: "even the God of Israel."}
              ]
        , 4:  [ {class: "q", text: "For Jacob my servant’s sake,"}
              , {class: "q2", text: "and Israel my chosen,"}
              , {class: "q", text: "I have called you by your name."}
              , {class: "q2", text: "I have given you a title,"}
              , {class: "q2", text: "though you have not known me."}
              ]
        , 5:  [ {class: "q", text: "I am Yahweh, and there is no one else."}
              , {class: "q2", text: "Besides me, there is no God."}
              , {class: "q", text: "I will strengthen you,"}
              , {class: "q2", text: "though you have not known me,"}
              ]
        , 6:  [ {class: "q", text: "that they may know from the rising of the sun,"}
              , {class: "q2", text: "and from the west,"}
              , {class: "q", text: "that there is no one besides me."}
              , {class: "q2", text: "I am Yahweh, and there is no one else."}
              ]
        , 7:  [ {class: "q", text: "I form the light"}
              , {class: "q2", text: "and create darkness."}
              , {class: "q", text: "I make peace"}
              , {class: "q2", text: "and create calamity."}
              , {class: "q", text: "I am Yahweh,"}
              , {class: "q2", text: "who does all these things."}
              ]
        , 8:  [ {class: "q", text: "Rain, you heavens, from above,"}
              , {class: "q2", text: "and let the skies pour down righteousness."}
              , {class: "q", text: "Let the earth open, that it may produce salvation,"}
              , {class: "q2", text: "and let it cause righteousness to spring up with it."}
              , {class: "q", text: "I, Yahweh, have created it."}
              ]
        , 9:  [ {class: "q", text: "Woe to him who strives with his Maker—"}
              , {class: "q2", text: "a clay pot among the clay pots of the earth!"}
              , {class: "q", text: "Shall the clay ask him who fashions it, ‘What are you making?’"}
              , {class: "q2", text: "or your work, ‘He has no hands?’"}
              ]
        , 10: [ {class: "q", text: "Woe to him who says to a father, ‘What have you become the father of?’"}
              , {class: "q2", text: "or to a mother, ‘What have you given birth to?’”"}
              ]
        , 11: [ {class: "q", text: "Yahweh, the Holy One of Israel"}
              , {class: "q2", text: "and his Maker says:"}
              , {class: "q", text: "“You ask me about the things that are to come, concerning my sons,"}
              , {class: "q2", text: "and you command me concerning the work of my hands!"}
              ]
        , 12: [ {class: "q", text: "I have made the earth, and created man on it."}
              , {class: "q2", text: "I, even my hands, have stretched out the heavens."}
              , {class: "q2", text: "I have commanded all their army."}
              ]
        , 13: [ {class: "q", text: "I have raised him up in righteousness,"}
              , {class: "q2", text: "and I will make all his ways straight."}
              , {class: "q", text: "He shall build my city,"}
              , {class: "q2", text: "and he shall let my exiles go free,"}
              , {class: "q2", text: "not for price nor reward,” says Yahweh of Armies."}
              ]
        , 14: [ {class: "q", text: "Yahweh says: “The labor of Egypt,"}
              , {class: "q2", text: "and the merchandise of Ethiopia,"}
              , {class: "q2", text: "and the Sabeans, men of stature, will come over to you,"}
              , {class: "q2", text: "and they will be yours."}
              , {class: "q", text: "They will go after you."}
              , {class: "q2", text: "They shall come over in chains."}
              , {class: "q2", text: "They will bow down to you."}
              , {class: "q", text: "They will make supplication to you:"}
              , {class: "q2", text: "‘Surely God is in you; and there is no one else."}
              , {class: "q2", text: "There is no other god."}
              ]
        , 15: [ {class: "q", text: "Most certainly you are a God who has hidden yourself,"}
              , {class: "q2", text: "God of Israel, the Savior.’”"}
              ]
        , 16: [ {class: "q", text: "They will be disappointed,"}
              , {class: "q2", text: "yes, confounded, all of them."}
              , {class: "q2", text: "Those who are makers of idols will go into confusion together."}
              ]
        , 17: [ {class: "q", text: "Israel will be saved by Yahweh with an everlasting salvation."}
              , {class: "q2", text: "You will not be disappointed nor confounded to ages everlasting."}
              ]
        , 18: [ {class: "q", text: "For Yahweh who created the heavens,"}
              , {class: "q2", text: "the God who formed the earth and made it,"}
              , {class: "q2", text: "who established it and didn’t create it a waste,"}
              , {class: "q2", text: "who formed it to be inhabited says:"}
              , {class: "q", text: "“I am Yahweh."}
              , {class: "q2", text: "There is no other."}
              ]
        , 19: [ {class: "q", text: "I have not spoken in secret,"}
              , {class: "q2", text: "in a place of the land of darkness."}
              , {class: "q", text: "I didn’t say to the offspring of Jacob, ‘Seek me in vain.’"}
              , {class: "q2", text: "I, Yahweh, speak righteousness."}
              , {class: "q2", text: "I declare things that are right."}
              ]
        , 20: [ {class: "q", text: "“Assemble yourselves and come."}
              , {class: "q2", text: "Draw near together, you who have escaped from the nations."}
              , {class: "q", text: "Those have no knowledge who carry the wood of their engraved image,"}
              , {class: "q2", text: "and pray to a god that can’t save."}
              ]
        , 21: [ {class: "q", text: "Declare and present it."}
              , {class: "q2", text: "Yes, let them take counsel together."}
              , {class: "q", text: "Who has shown this from ancient time?"}
              , {class: "q2", text: "Who has declared it of old?"}
              , {class: "q2", text: "Haven’t I, Yahweh?"}
              , {class: "q", text: "There is no other God besides me, a just God and a Savior."}
              , {class: "q2", text: "There is no one besides me."}
              ]
        , 22: [ {class: "q", text: "“Look to me, and be saved, all the ends of the earth;"}
              , {class: "q2", text: "for I am God, and there is no other."}
              ]
        , 23: [ {class: "q", text: "I have sworn by myself."}
              ,  {class: "q2", text: "The word has gone out of my mouth in righteousness, and will not be revoked,"}
              , {class: "q", text: "that to me every knee shall bow,"}
              , {class: "q2", text: "every tongue shall take an oath."}
              ]
        , 24: [ {class: "q", text: "They will say of me,"}
              , {class: "q2", text: "‘There is righteousness and strength only in Yahweh.’”"}
              , {class: "q", text: "Even to him will men come."}
              , {class: "q2", text: "All those who raged against him will be disappointed."}
              ]
        , 25: [ {class: "q", text: "All the offspring of Israel will be justified in Yahweh,"}
              , {class: "q2", text: "and will rejoice!"}
              ]
        }
  , 46: { 1:  [ {class: "q", text: "Bel bows down."}
              , {class: "q2", text: "Nebo stoops."}
              , {class: "q", text: "Their idols are carried by animals,"}
              , {class: "q2", text: "and on the livestock."}
              , {class: "q", text: "The things that you carried around are heavy loads,"}
              , {class: "q2", text: "a burden for the weary."}
              ]
        , 2:  [ {class: "q", text: "They stoop and they bow down together."}
              , {class: "q2", text: "They could not deliver the burden,"}
              , {class: "q2", text: "but they have gone into captivity."}
              ]
        , 3:  [ {class: "q", text: "“Listen to me, house of Jacob,"}
              , {class: "q2", text: "and all the remnant of the house of Israel,"}
              , {class: "q2", text: "that have been carried from their birth,"}
              , {class: "q2", text: "that have been carried from the womb."}
              ]
        , 4:  [ {class: "q", text: "Even to old age I am he,"}
              , {class: "q2", text: "and even to gray hairs I will carry you."}
              , {class: "q", text: "I have made, and I will bear."}
              , {class: "q2", text: "Yes, I will carry, and will deliver."}
              ]
        , 5:  [ {class: "q", text: "“To whom will you compare me, and consider my equal,"}
              , {class: "q2", text: "and compare me, as if we were the same?"}
              ]
        , 6:  [ {class: "q", text: "Some pour out gold from the bag,"}
              , {class: "q2", text: "and weigh silver in the balance."}
              , {class: "q", text: "They hire a goldsmith,"}
              , {class: "q2", text: "and he makes it a god."}
              , {class: "q", text: "They fall down—"}
              , {class: "q2", text: "yes, they worship."}
              ]
        , 7:  [ {class: "q", text: "They bear it on their shoulder."}
              , {class: "q2", text: "They carry it, and set it in its place, and it stands there."}
              , {class: "q2", text: "It cannot move from its place."}
              , {class: "q", text: "Yes, one may cry to it, yet it can not answer."}
              , {class: "q2", text: "It cannot save him out of his trouble."}
              ]
        , 8:  [ {class: "q", text: "“Remember this, and show yourselves men."}
              , {class: "q2", text: "Bring it to mind again, you transgressors."}
              ]
        , 9:  [ {class: "q", text: "Remember the former things of old:"}
              , {class: "q2", text: "for I am God, and there is no other."}
          , {class: "q2", text: "I am God, and there is none like me."}
          ]
        , 10: [ {class: "q", text: "I declare the end from the beginning,"}
              , {class: "q2", text: "and from ancient times things that are not yet done."}
              , {class: "q", text: "I say: My counsel will stand,"}
              , {class: "q2", text: "and I will do all that I please."}
              ]
        , 11: [ {class: "q", text: "I call a ravenous bird from the east,"}
              , {class: "q2", text: "the man of my counsel from a far country."}
              , {class: "q", text: "Yes, I have spoken."}
              , {class: "q2", text: "I will also bring it to pass."}
              , {class: "q", text: "I have planned."}
              , {class: "q2", text: "I will also do it."}
              ]
        , 12: [ {class: "q", text: "Listen to me, you stubborn-hearted,"}
              , {class: "q2", text: "who are far from righteousness!"}
              ]
        , 13: [ {class: "q", text: "I bring my righteousness near."}
              , {class: "q2", text: "It is not far off,"}
              , {class: "q2", text: "and my salvation will not wait."}
              , {class: "q", text: "I will grant salvation to Zion,"}
              , {class: "q2", text: "my glory to Israel."}
              ]
        }
  , 47: { 1:  [ {class: "endpara", text: "“Come down and sit in the dust, virgin daughter of Babylon."}
              , {class: "q2", text: "Sit on the ground without a throne, daughter of the Chaldeans."}
              , {class: "q2", text: "For you will no longer be called tender and delicate."}
              ]
        , 2:  [ {class: "q", text: "Take the millstones and grind flour."}
              , {class: "q2", text: "Remove your veil, lift up your skirt, uncover your legs,"}
              , {class: "q2", text: "and wade through the rivers."}
              ]
        , 3:  [ {class: "q", text: "Your nakedness will be uncovered."}
              , {class: "q2", text: "Yes, your shame will be seen."}
              , {class: "q", text: "I will take vengeance,"}
              , {class: "q2", text: "and will spare no one.”"}
              ]
        , 4:  [ {class: "q", text: "Our Redeemer, Yahweh of Armies is his name,"}
              , {class: "q2", text: "is the Holy One of Israel."}
              ]
        , 5:  [ {class: "q", text: "“Sit in silence, and go into darkness,"}
              , {class: "q2", text: "daughter of the Chaldeans."}
              , {class: "q", text: "For you shall no longer be called"}
              , {class: "q2", text: "the mistress of kingdoms."}
              ]
        , 6:  [ {class: "q", text: "I was angry with my people."}
              , {class: "q2", text: "I profaned my inheritance"}
              , {class: "q2", text: "and gave them into your hand."}
              , {class: "q", text: "You showed them no mercy."}
              , {class: "q2", text: "You laid a very heavy yoke on the aged."}
              ]
        , 7:  [ {class: "q", text: "You said, ‘I will be a princess forever,’"}
              , {class: "q2", text: "so that you didn’t lay these things to your heart,"}
              , {class: "q2", text: "nor did you remember the results."}
              ]
        , 8:  [ {class: "q", text: "“Now therefore hear this, you who are given to pleasures,"}
              , {class: "q2", text: "who sit securely,"}
              , {class: "q", text: "who say in your heart,"}
              , {class: "q2", text: "‘I am, and there is no one else besides me."}
              , {class: "q", text: "I won’t sit as a widow,"}
              , {class: "q2", text: "neither will I know the loss of children.’"}
              ]
        , 9:  [ {class: "q", text: "But these two things will come to you in a moment in one day,"}
              , {class: "q2", text: "the loss of children and widowhood."}
              , {class: "q", text: "They will come on you in their full measure,"}
              , {class: "q2", text: "in the multitude of your sorceries,"}
              , {class: "q2", text: "and the great abundance of your enchantments."}
              ]
        , 10: [ {class: "q", text: "For you have trusted in your wickedness."}
              , {class: "q2", text: "You have said, ‘No one sees me.’"}
              , {class: "q", text: "Your wisdom and your knowledge has perverted you."}
              , {class: "q2", text: "You have said in your heart, ‘I am, and there is no one else besides me.’"}
              ]
        , 11: [ {class: "q", text: "Therefore disaster will come on you."}
              , {class: "q2", text: "You won’t know when it dawns."}
              , {class: "q", text: "Mischief will fall on you."}
              , {class: "q2", text: "You won’t be able to put it away."}
              , {class: "q", text: "Desolation will come on you suddenly,"}
              , {class: "q2", text: "which you don’t understand."}
              ]
        , 12: [ {class: "q", text: "“Stand now with your enchantments"}
              , {class: "q2", text: "and with the multitude of your sorceries,"}
              , {class: "q2", text: "in which you have labored from your youth,"}
              , {class: "q", text: "as if you might profit,"}
              , {class: "q2", text: "as if you might prevail."}
              ]
        , 13: [ {class: "q", text: "You are wearied in the multitude of your counsels."}
              , {class: "q2", text: "Now let the astrologers, the stargazers, and the monthly prognosticators, stand up and save you from the things that will happen to you."}
              ]
        , 14: [ {class: "q", text: "Behold, they are like stubble."}
              , {class: "q2", text: "The fire will burn them."}
              , {class: "q2", text: "They won’t deliver themselves from the power of the flame."}
              , {class: "q", text: "It won’t be a coal to warm at"}
              , {class: "q2", text: "or a fire to sit by."}
              ]
        , 15: [ {class: "q", text: "The things that you labored in will be like this:"}
              , {class: "q2", text: "those who have trafficked with you from your youth will each wander in his own way."}
              , {class: "q2", text: "There will be no one to save you."}
              ]
        }
  , 48: { 1:  [ {class: "q", text: "“Hear this, house of Jacob,"}
              , {class: "q2", text: "you who are called by the name of Israel,"}
              , {class: "q2", text: "and have come out of the waters of Judah."}
              , {class: "q", text: "You swear by Yahweh’s name,"}
              , {class: "q2", text: "and make mention of the God of Israel,"}
              , {class: "q2", text: "but not in truth, nor in righteousness—"}
              ]
        , 2:  [ {class: "q", text: "for they call themselves citizens of the holy city,"}
              , {class: "q2", text: "and rely on the God of Israel;"}
              , {class: "q2", text: "Yahweh of Armies is his name."}
              ]
        , 3:  [ {class: "q", text: "I have declared the former things from of old."}
              , {class: "q2", text: "Yes, they went out of my mouth, and I revealed them."}
              , {class: "q2", text: "I did them suddenly, and they happened."}
              ]
        , 4:  [ {class: "q", text: "Because I knew that you are obstinate,"}
              , {class: "q2", text: "and your neck is an iron sinew,"}
              , {class: "q2", text: "and your brow bronze;"}
              ]
        , 5:  [ {class: "q", text: "therefore I have declared it to you from of old;"}
              , {class: "q2", text: "before it came to pass I showed it to you;"}
              , {class: "q2", text: "lest you should say, ‘My idol has done them."}
              , {class: "q2", text: "My engraved image and my molten image has commanded them.’"}
              ]
        , 6:  [ {class: "q", text: "You have heard it."}
              , {class: "q2", text: "Now see all this."}
              , {class: "q2", text: "And you, won’t you declare it?</div><br /><div class=\"“I have shown you new things from this time,"}
              , {class: "q2", text: "even hidden things, which you have not known."}
              ]
        , 7:  [ {class: "q", text: "They are created now, and not from of old."}
              , {class: "q2", text: "Before today, you didn’t hear them,"}
              , {class: "q2", text: "lest you should say, ‘Behold, I knew them.’"}
              ]
        , 8:  [ {class: "q", text: "Yes, you didn’t hear."}
              , {class: "q2", text: "Yes, you didn’t know."}
              , {class: "q2", text: "Yes, from of old your ear was not opened,"}
              , {class: "q", text: "for I knew that you dealt very treacherously,"}
              , {class: "q2", text: "and were called a transgressor from the womb."}
              ]
        , 9:  [ {class: "q", text: "For my name’s sake, I will defer my anger,"}
              , {class: "q2", text: "and for my praise, I hold it back for you"}
              , {class: "q2", text: "so that I don’t cut you off."}
              ]
        , 10: [ {class: "q", text: "Behold, I have refined you,"}
              , {class: "q2", text: "but not as silver."}
              , {class: "q2", text: "I have chosen you in the furnace of affliction."}
              ]
        , 11: [ {class: "q", text: "For my own sake,"}
              , {class: "q2", text: "for my own sake, I will do it;"}
              , {class: "q", text: "for how would my name be profaned?"}
              , {class: "q2", text: "I will not give my glory to another."}
              ]
        , 12: [ {class: "q", text: "“Listen to me, O Jacob,"}
              , {class: "q2", text: "and Israel my called:"}
              , {class: "q", text: "I am he."}
              , {class: "q2", text: "I am the first."}
              , {class: "q2", text: "I am also the last."}
              ]
        , 13: [ {class: "q", text: "Yes, my hand has laid the foundation of the earth,"}
              , {class: "q2", text: "and my right hand has spread out the heavens."}
              , {class: "q2", text: "when I call to them, they stand up together."}
              ]
        , 14: [ {class: "q", text: "“Assemble yourselves, all of you, and hear!"}
              , {class: "q2", text: "Who among them has declared these things?"}
              , {class: "q", text: "He whom Yahweh loves will do what he likes to Babylon,"}
              , {class: "q2", text: "and his arm will be against the Chaldeans."}
              ]
        , 15: [ {class: "q", text: "I, even I, have spoken."}
              , {class: "q2", text: "Yes, I have called him."}
              , {class: "q", text: "I have brought him"}
              , {class: "q2", text: "and he shall make his way prosperous."}
              ]
        , 16: [ {class: "endpara", text: "“Come near to me and hear this:"}
              , {class: "q", text: "“From the beginning I have not spoken in secret;"}
              , {class: "q2", text: "from the time that it happened, I was there.”</div><br /><div class=\"Now the Lord Yahweh has sent me"}
              , {class: "q2", text: "with his Spirit."}
              ]
        , 17: [ {class: "q", text: "Yahweh,"}
              , {class: "q2", text: "your Redeemer,"}
              , {class: "q2", text: "the Holy One of Israel says:"}
              , {class: "q", text: "“I am Yahweh your God,"}
              , {class: "q2", text: "who teaches you to profit,"}
              , {class: "q2", text: "who leads you by the way that you should go."}
              ]
        , 18: [ {class: "q", text: "Oh that you had listened to my commandments!"}
              , {class: "q2", text: "Then your peace would have been like a river"}
              , {class: "q2", text: "and your righteousness like the waves of the sea."}
              ]
        , 19: [ {class: "q", text: "Your offspring also would have been as the sand"}
              , {class: "q2", text: "and the descendants of your body like its grains."}
              , {class: "q2", text: "His name would not be cut off nor destroyed from before me.”"}
              ]
        , 20: [ {class: "q", text: "Leave Babylon!"}
              , {class: "q2", text: "Flee from the Chaldeans!"}
              , {class: "q", text: "With a voice of singing announce this,"}
              , {class: "q2", text: "tell it even to the end of the earth:"}
              , {class: "q2", text: "say, “Yahweh has redeemed his servant Jacob!”"}
              ]
        , 21: [ {class: "q", text: "They didn’t thirst when he led them through the deserts."}
              , {class: "q2", text: "He caused the waters to flow out of the rock for them."}
              , {class: "q2", text: "He also split the rock and the waters gushed out."}
              ]
        , 22: [ {class: "q", text: "“There is no peace”, says Yahweh, “for the wicked.”"} ]
        }
  , 49: { 1:  [ {class: "q", text: "Listen, islands, to me."}
              , {class: "q2", text: "Listen, you peoples, from afar:"}
              , {class: "q", text: "Yahweh has called me from the womb;"}
              , {class: "q2", text: "from the inside of my mother, he has mentioned my name."}
              ]
        , 2:  [ {class: "q", text: "He has made my mouth like a sharp sword."}
              , {class: "q2", text: "He has hidden me in the shadow of his hand."}
              , {class: "q", text: "He has made me a polished shaft."}
              , {class: "q2", text: "He has kept me close in his quiver."}
              ]
        , 3:  [ {class: "q", text: "He said to me, “You are my servant,"}
              , {class: "q2", text: "Israel, in whom I will be glorified.”"}
              ]
        , 4:  [ {class: "q", text: "But I said, “I have labored in vain."}
              , {class: "q2", text: "I have spent my strength in vain for nothing;"}
              , {class: "q", text: "yet surely the justice due to me is with Yahweh,"}
              , {class: "q2", text: "and my reward with my God.”"}
              ]
        , 5:  [ {class: "q", text: "Now Yahweh, he who formed me from the womb to be his servant,"}
              , {class: "q2", text: "says to bring Jacob again to him,"}
              , {class: "q2", text: "and to gather Israel to him,"}
              , {class: "q2", text: "for I am honorable in Yahweh’s eyes,"}
              , {class: "q2", text: "and my God has become my strength."}
              ]
        , 6:  [ {class: "q", text: "Indeed, he says, “It is too light a thing that you should be my servant to raise up the tribes of Jacob,"}
              , {class: "q2", text: "and to restore the preserved of Israel."}
              , {class: "q", text: "I will also give you as a light to the nations,"}
              , {class: "q2", text: "that you may be my salvation to the end of the earth.”"}
              ]
        , 7:  [ {class: "q", text: "Yahweh, the Redeemer of Israel, and his Holy One,"}
              , {class: "q2", text: "says to him whom man despises, to him whom the nation abhors, to a servant of rulers:"}
              , {class: "q", text: "“Kings shall see and rise up,"}
              , {class: "q2", text: "princes, and they shall worship,"}
              , {class: "q2", text: "because of Yahweh who is faithful, even the Holy One of Israel, who has chosen you.”"}
              ]
        , 8:  [ {class: "q", text: "Yahweh says, “I have answered you in an acceptable time."}
              , {class: "q2", text: "I have helped you in a day of salvation."}
              , {class: "q", text: "I will preserve you and give you for a covenant of the people,"}
              , {class: "q2", text: "to raise up the land, to make them inherit the desolate heritage,"}
              ]
        , 9:  [ {class: "q", text: "saying to those who are bound, ‘Come out!’;"}
              , {class: "q2", text: "to those who are in darkness, ‘Show yourselves!’"}
              , {class: "q", text: "“They shall feed along the paths,"}
              , {class: "q2", text: "and their pasture shall be on all treeless heights."}
              ]
        , 10: [ {class: "q", text: "They shall not hunger nor thirst;"}
              , {class: "q2", text: "neither shall the heat nor sun strike them:"}
              , {class: "q2", text: "for he who has mercy on them will lead them."}
              , {class: "q2", text: "He will guide them by springs of water."}
              ]
        , 11: [ {class: "q", text: "I will make all my mountains a road,"}
              , {class: "q2", text: "and my highways shall be exalted."}
              ]
        , 12: [ {class: "q", text: "Behold, these shall come from afar,"}
              , {class: "q2", text: "and behold, these from the north and from the west;"}
              , {class: "q2", text: "and these from the land of Sinim.”"}
              ]
        , 13: [ {class: "q", text: "Sing, heavens, and be joyful, earth!"}
              , {class: "q2", text: "Break out into singing, mountains,"}
              , {class: "q", text: "for Yahweh has comforted his people,"}
              , {class: "q2", text: "and will have compassion on his afflicted."}
              ]
        , 14: [ {class: "q", text: "But Zion said, “Yahweh has forsaken me,"}
              , {class: "q2", text: "and the Lord has forgotten me.”"}
              ]
        , 15: [ {class: "q", text: "“Can a woman forget her nursing child,"}
              , {class: "q2", text: "that she should not have compassion on the son of her womb?"}
              , {class: "q", text: "Yes, these may forget,"}
              , {class: "q2", text: "yet I will not forget you!"}
              ]
        , 16: [ {class: "q", text: "Behold, I have engraved you on the palms of my hands."}
              , {class: "q2", text: "Your walls are continually before me."}
              ]
        , 17: [ {class: "q", text: "Your children hurry."}
              , {class: "q2", text: "Your destroyers and those who devastated you will leave you."}
              ]
        , 18: [ {class: "q", text: "Lift up your eyes all around, and see:"}
              , {class: "q2", text: "all these gather themselves together, and come to you."}
              , {class: "q", text: "As I live,” says Yahweh, “you shall surely clothe yourself with them all as with an ornament,"}
              , {class: "q2", text: "and dress yourself with them, like a bride."}
              ]
        , 19: [ {class: "q", text: "“For, as for your waste and your desolate places,"}
              , {class: "q2", text: "and your land that has been destroyed,"}
              , {class: "q", text: "surely now that land will be too small for the inhabitants,"}
              , {class: "q2", text: "and those who swallowed you up will be far away."}
              ]
        , 20: [ {class: "q", text: "The children of your bereavement will say in your ears,"}
              , {class: "q2", text: "‘This place is too small for me."}
              , {class: "q2", text: "Give me a place to live in.’"}
              ]
        , 21: [ {class: "q", text: "Then you will say in your heart, ‘Who has conceived these for me, since I have been bereaved of my children"}
              , {class: "q2", text: "and am alone, an exile, and wandering back and forth?"}
              , {class: "q", text: "Who has brought these up?"}
              , {class: "q2", text: "Behold, I was left alone. Where were these?’”"}
              ]
        , 22: [ {class: "q", text: "The Lord Yahweh says, “Behold, I will lift up my hand to the nations,"}
              , {class: "q2", text: "and lift up my banner to the peoples."}
              , {class: "q", text: "They shall bring your sons in their bosom,"}
              , {class: "q2", text: "and your daughters shall be carried on their shoulders."}
              ]
        , 23: [ {class: "q", text: "Kings shall be your foster fathers,"}
              , {class: "q2", text: "and their queens your nursing mothers."}
              , {class: "q", text: "They will bow down to you with their faces to the earth,"}
              , {class: "q2", text: "and lick the dust of your feet."}
              , {class: "q", text: "Then you will know that I am Yahweh;"}
              , {class: "q2", text: "and those who wait for me shall not be disappointed.”"}
              ]
        , 24: [ {class: "q", text: "Shall the plunder be taken from the mighty,"}
              , {class: "q2", text: "or the lawful captives be delivered?"}
              ]
        , 25: [ {class: "q", text: "But Yahweh says, “Even the captives of the mighty shall be taken away,"}
              , {class: "q2", text: "and the plunder retrieved from the fierce,"}
              , {class: "q", text: "for I will contend with him who contends with you"}
              , {class: "q2", text: "and I will save your children."}
              ]
        , 26: [ {class: "q", text: "I will feed those who oppress you with their own flesh;"}
              , {class: "q2", text: "and they will be drunk on their own blood, as with sweet wine."}
              , {class: "q", text: "Then all flesh shall know that I, Yahweh, am your Savior"}
              , {class: "q2", text: "and your Redeemer, the Mighty One of Jacob.”"}
              ]
        }
  , 5:  { 1:  [ {class: "q", text: "Let me sing for my well beloved a song of my beloved about his vineyard."}
              , {class: "q2", text: "My beloved had a vineyard on a very fruitful hill."}
              ]
        , 2:  [ {class: "q", text: "He dug it up,"}
              , {class: "q2", text: "gathered out its stones,"}
              , {class: "q2", text: "planted it with the choicest vine,"}
              , {class: "q2", text: "built a tower in the middle of it,"}
              , {class: "q2", text: "and also cut out a wine press in it."}
              , {class: "q", text: "He looked for it to yield grapes,"}
              , {class: "q2", text: "but it yielded wild grapes."}
              ]
        , 3:  [ {class: "q", text: "“Now, inhabitants of Jerusalem and men of Judah,"}
              , {class: "q2", text: "please judge between me and my vineyard."}
              ]
        , 4:  [ {class: "q", text: "What could have been done more to my vineyard, that I have not done in it?"}
              , {class: "q2", text: "Why, when I looked for it to yield grapes, did it yield wild grapes?"}
              ]
        , 5:  [ {class: "q", text: "Now I will tell you what I will do to my vineyard."}
              , {class: "q2", text: "I will take away its hedge, and it will be eaten up."}
              , {class: "q2", text: "I will break down its wall, and it will be trampled down."}
              ]
        , 6:  [ {class: "q", text: "I will lay it a wasteland."}
              , {class: "q2", text: "It won’t be pruned or hoed,"}
              , {class: "q2", text: "but it will grow briers and thorns."}
              , {class: "q2", text: "I will also command the clouds that they rain no rain on it.”"}
              ]
        , 7:  [ {class: "q", text: "For the vineyard of Yahweh of Armies is the house of Israel,"}
              , {class: "q2", text: "and the men of Judah his pleasant plant:"}
              , {class: "q2", text: "and he looked for justice, but, behold, oppression;"}
              , {class: "q2", text: "for righteousness, but, behold, a cry of distress."}
              ]
        , 8:  [ {class: "q", text: "Woe to those who join house to house,"}
              ,  {class: "q2", text: "who lay field to field, until there is no room,"}
              , {class: "q2", text: "and you are made to dwell alone in the middle of the land!"}
              ]
        , 9:  [ {class: "q", text: "In my ears, Yahweh of Armies says: “Surely many houses will be desolate,"}
              , {class: "q2", text: "even great and beautiful, unoccupied."}
              ]
        , 10: [ {class: "q", text: "For ten acres of vineyard shall yield one bath,"}
              , {class: "q2", text: "and a homer of seed shall yield an ephah.”"}
              ]
        , 11: [ {class: "q", text: "Woe to those who rise up early in the morning, that they may follow strong drink,"}
              , {class: "q2", text: "who stay late into the night, until wine inflames them!"}
              ]
        , 12: [ {class: "q", text: "The harp, lyre, tambourine, and flute, with wine, are at their feasts;"}
              , {class: "q2", text: "but they don’t respect the work of Yahweh,"}
              , {class: "q2", text: "neither have they considered the operation of his hands."}
              ]
        , 13: [ {class: "q", text: "Therefore my people go into captivity for lack of knowledge."}
              , {class: "q2", text: "Their honorable men are famished,"}
              , {class: "q2", text: "and their multitudes are parched with thirst."}
              ]
        , 14: [ {class: "q", text: "Therefore Sheol has enlarged its desire,"}
              , {class: "q2", text: "and opened its mouth without measure;"}
              , {class: "q2", text: "and their glory, their multitude, their pomp, and he who rejoices among them, descend into it."}
              ]
        , 15: [ {class: "q", text: "So man is brought low,"}
              , {class: "q2", text: "mankind is humbled,"}
              , {class: "q2", text: "and the eyes of the arrogant ones are humbled;"}
              ]
        , 16: [ {class: "q", text: "but Yahweh of Armies is exalted in justice,"}
              , {class: "q2", text: "and God the Holy One is sanctified in righteousness."}
              ]
        , 17: [ {class: "q", text: "Then the lambs will graze as in their pasture,"}
              , {class: "q2", text: "and strangers will eat the ruins of the rich."}
              ]
        , 18: [ {class: "q", text: "Woe to those who draw iniquity with cords of falsehood,"}
              , {class: "q2", text: "and wickedness as with cart rope,"}
              ]
        , 19: [ {class: "q", text: "who say, “Let him make haste, let him hasten his work, that we may see it;"}
              , {class: "q2", text: "let the counsel of the Holy One of Israel draw near and come,"}
              , {class: "q2", text: "that we may know it!”"}
              ]
        , 20: [ {class: "q", text: "Woe to those who call evil good, and good evil;"}
              , {class: "q2", text: "who put darkness for light,"}
              , {class: "q2", text: "and light for darkness;"}
              , {class: "q", text: "who put bitter for sweet,"}
              , {class: "q2", text: "and sweet for bitter!"}
              ]
        , 21: [ {class: "q", text: "Woe to those who are wise in their own eyes,"}
              , {class: "q2", text: "and prudent in their own sight!"}
              ]
        , 22: [ {class: "q", text: "Woe to those who are mighty to drink wine,"}
              , {class: "q2", text: "and champions at mixing strong drink;"}
              ]
        , 23: [ {class: "q", text: "who acquit the guilty for a bribe,"}
              , {class: "q2", text: "but deny justice for the innocent!"}
              ]
        , 24: [ {class: "q", text: "Therefore as the tongue of fire devours the stubble,"}
              , {class: "q2", text: "and as the dry grass sinks down in the flame,"}
              , {class: "q2", text: "so their root shall be as rottenness,"}
              , {class: "q2", text: "and their blossom shall go up as dust,"}
              , {class: "q", text: "because they have rejected the law of Yahweh of Armies,"}
              , {class: "q2", text: "and despised the word of the Holy One of Israel."}
              ]
        , 25: [ {class: "q", text: "Therefore Yahweh’s anger burns against his people,"}
              , {class: "q2", text: "and he has stretched out his hand against them and has struck them."}
              , {class: "q", text: "The mountains tremble,"}
              , {class: "q2", text: "and their dead bodies are as refuse in the middle of the streets."}
              , {class: "q", text: "For all this, his anger is not turned away,"}
              , {class: "q2", text: "but his hand is still stretched out."}
              ]
        , 26: [ {class: "q", text: "He will lift up a banner to the nations from far away,"}
              , {class: "q2", text: "and he will whistle for them from the end of the earth."}
              , {class: "q2", text: "Behold, they will come speedily and swiftly."}
              ]
        , 27: [ {class: "q", text: "No one shall be weary nor stumble among them;"}
              , {class: "q2", text: "no one shall slumber nor sleep,"}
              , {class: "q2", text: "neither shall the belt of their waist be untied,"}
              , {class: "q2", text: "nor the strap of their sandals be broken,"}
              ]
        , 28: [ {class: "q", text: "whose arrows are sharp,"}
              , {class: "q2", text: "and all their bows bent."}
              , {class: "q", text: "Their horses’ hoofs will be like flint,"}
              , {class: "q2", text: "and their wheels like a whirlwind."}
              ]
        , 29: [ {class: "q", text: "Their roaring will be like a lioness."}
              , {class: "q2", text: "They will roar like young lions."}
              , {class: "q", text: "Yes, they shall roar,"}
              , {class: "q2", text: "and seize their prey and carry it off,"}
              , {class: "q2", text: "and there will be no one to deliver."}
              ]
        , 30: [ {class: "q", text: "They will roar against them in that day like the roaring of the sea."}
              , {class: "q2", text: "If one looks to the land, behold, darkness and distress."}
              , {class: "q2", text: "The light is darkened in its clouds."}
              ]
        }
  , 50: { 1:  [ {class: "q", text: "Yahweh says, “Where is the bill of your mother’s divorce, with which I have put her away?"}
              , {class: "q2", text: "Or to which of my creditors have I sold you?"}
              , {class: "q", text: "Behold, you were sold for your iniquities,"}
              , {class: "q2", text: "and your mother was put away for your transgressions."}
              ]
        , 2:  [ {class: "q", text: "Why, when I came, was there no one?"}
              , {class: "q2", text: "When I called, why was there no one to answer?"}
              , {class: "q", text: "Is my hand shortened at all, that it can’t redeem?"}
              , {class: "q2", text: "Or have I no power to deliver?"}
              , {class: "q", text: "Behold, at my rebuke I dry up the sea."}
              , {class: "q2", text: "I make the rivers a wilderness."}
              , {class: "q2", text: "Their fish stink because there is no water, and die of thirst."}
              ]
        , 3:  [ {class: "q", text: "I clothe the heavens with blackness."}
              , {class: "q2", text: "I make sackcloth their covering.”"}
              ]
        , 4:  [ {class: "q", text: "The Lord Yahweh has given me the tongue of those who are taught,"}
              , {class: "q2", text: "that I may know how to sustain with words him who is weary."}
              , {class: "q", text: "He awakens morning by morning,"}
              , {class: "q2", text: "he awakens my ear to hear as those who are taught."}
              ]
        , 5:  [ {class: "q", text: "The Lord Yahweh has opened my ear."}
              , {class: "q2", text: "I was not rebellious."}
              , {class: "q2", text: "I have not turned back."}
              ]
        , 6:  [ {class: "q", text: "I gave my back to those who beat me,"}
              , {class: "q2", text: "and my cheeks to those who plucked off the hair."}
              , {class: "q2", text: "I didn’t hide my face from shame and spitting."}
              ]
        , 7:  [ {class: "q", text: "For the Lord Yahweh will help me."}
              , {class: "q2", text: "Therefore I have not been confounded."}
              , {class: "q", text: "Therefore I have set my face like a flint,"}
              , {class: "q2", text: "and I know that I won’t be disappointed."}
              ]
        , 8:  [ {class: "q", text: "He who justifies me is near."}
              , {class: "q2", text: "Who will bring charges against me?"}
              , {class: "q", text: "Let us stand up together."}
              , {class: "q2", text: "Who is my adversary?"}
              , {class: "q2", text: "Let him come near to me."}
              ]
        , 9:  [ {class: "q", text: "Behold, the Lord Yahweh will help me!"}
              , {class: "q2", text: "Who is he who will condemn me?"}
              , {class: "q", text: "Behold, they will all grow old like a garment."}
              , {class: "q2", text: "The moths will eat them up."}
              ]
        , 10: [ {class: "q", text: "Who among you fears Yahweh"}
              , {class: "q2", text: "and obeys the voice of his servant?"}
              , {class: "q", text: "He who walks in darkness"}
              , {class: "q2", text: "and has no light,"}
              , {class: "q", text: "let him trust in Yahweh’s name,"}
              , {class: "q2", text: "and rely on his God."}
              ]
        , 11: [ {class: "q", text: "Behold, all you who kindle a fire,"}
              , {class: "q2", text: "who adorn yourselves with torches around yourselves,"}
              , {class: "q", text: "walk in the flame of your fire,"}
              , {class: "q2", text: "and among the torches that you have kindled."}
              , {class: "q", text: "You will have this from my hand:"}
              , {class: "q2", text: "you will lie down in sorrow."}
              ]
        }
  , 51: { 1:  [ {class: "q", text: "“Listen to me, you who follow after righteousness,"}
              , {class: "q2", text: "you who seek Yahweh."}
              , {class: "q", text: "Look to the rock you were cut from,"}
              , {class: "q2", text: "and to the quarry you were dug from."}
              ]
        , 2:  [ {class: "q", text: "Look to Abraham your father,"}
              , {class: "q2", text: "and to Sarah who bore you;"}
              , {class: "q", text: "for when he was but one I called him,"}
              , {class: "q2", text: "I blessed him,"}
              , {class: "q2", text: "and made him many."}
              ]
        , 3:  [ {class: "q", text: "For Yahweh has comforted Zion."}
              , {class: "q2", text: "He has comforted all her waste places,"}
              , {class: "q2", text: "and has made her wilderness like Eden,"}
              , {class: "q2", text: "and her desert like the garden of Yahweh."}
              , {class: "q", text: "Joy and gladness will be found in them,"}
              , {class: "q2", text: "thanksgiving, and the voice of melody."}
              ]
        , 4:  [ {class: "q", text: "“Listen to me, my people;"}
              , {class: "q2", text: "and hear me, my nation,"}
              , {class: "q", text: "for a law will go out from me,"}
              , {class: "q2", text: "and I will establish my justice for a light to the peoples."}
              ]
        , 5:  [ {class: "q", text: "My righteousness is near."}
              , {class: "q2", text: "My salvation has gone out,"}
              , {class: "q2", text: "and my arms will judge the peoples."}
              , {class: "q", text: "The islands will wait for me,"}
              , {class: "q2", text: "and they will trust my arm."}
              ]
        , 6:  [ {class: "q", text: "Lift up your eyes to the heavens,"}
              , {class: "q2", text: "and look at the earth beneath;"}
              , {class: "q", text: "for the heavens will vanish away like smoke,"}
              , {class: "q2", text: "and the earth will wear out like a garment."}
              , {class: "q", text: "Its inhabitants will die in the same way,"}
              , {class: "q2", text: "but my salvation will be forever,"}
              , {class: "q2", text: "and my righteousness will not be abolished."}
              ]
        , 7:  [ {class: "q", text: "“Listen to me, you who know righteousness,"}
              , {class: "q2", text: "the people in whose heart is my law."}
              , {class: "q", text: "Don’t fear the reproach of men,"}
              , {class: "q2", text: "and don’t be dismayed at their insults."}
              ]
        , 8:  [ {class: "q", text: "For the moth will eat them up like a garment,"}
              , {class: "q2", text: "and the worm will eat them like wool;"}
              , {class: "q", text: "but my righteousness will be forever,"}
              , {class: "q2", text: "and my salvation to all generations.”"}
              ]
        , 9:  [ {class: "q", text: "Awake, awake, put on strength, arm of Yahweh!"}
              , {class: "q2", text: "Awake, as in the days of old,"}
              , {class: "q2", text: "the generations of ancient times."}
              , {class: "q", text: "Isn’t it you who cut Rahab in pieces,"}
              , {class: "q2", text: "who pierced the monster?"}
              ]
        , 10: [ {class: "q", text: "Isn’t it you who dried up the sea,"}
              , {class: "q2", text: "the waters of the great deep;"}
              , {class: "q2", text: "who made the depths of the sea a way for the redeemed to pass over?"}
              ]
        , 11: [ {class: "q", text: "Those ransomed by Yahweh will return,"}
              , {class: "q2", text: "and come with singing to Zion."}
              , {class: "q2", text: "Everlasting joy shall be on their heads."}
              , {class: "q", text: "They will obtain gladness and joy."}
              , {class: "q2", text: "Sorrow and sighing shall flee away."}
              ]
        , 12: [ {class: "q", text: "“I, even I, am he who comforts you."}
              , {class: "q2", text: "Who are you, that you are afraid of man who shall die,"}
              , {class: "q2", text: "and of the son of man who will be made as grass?"}
              ]
        , 13: [ {class: "q", text: "Have you forgotten Yahweh your Maker,"}
              , {class: "q2", text: "who stretched out the heavens,"}
              , {class: "q2", text: "and laid the foundations of the earth?"}
              , {class: "q", text: "Do you live in fear continually all day because of the fury of the oppressor,"}
              , {class: "q2", text: "when he prepares to destroy?"}
              , {class: "q2", text: "Where is the fury of the oppressor?"}
              ]
        , 14: [ {class: "q", text: "The captive exile will speedily be freed."}
              , {class: "q2", text: "He will not die and go down into the pit."}
              , {class: "q2", text: "His bread won’t fail."}
              ]
        , 15: [ {class: "q", text: "For I am Yahweh your God, who stirs up the sea"}
              , {class: "q2", text: "so that its waves roar."}
              , {class: "q2", text: "Yahweh of Armies is his name."}
              ]
        , 16: [ {class: "q", text: "I have put my words in your mouth"}
              , {class: "q2", text: "and have covered you in the shadow of my hand,"}
              , {class: "q", text: "that I may plant the heavens,"}
              , {class: "q2", text: "and lay the foundations of the earth,"}
              , {class: "q2", text: "and tell Zion, ‘You are my people.’”"}
              ]
        , 17: [ {class: "q", text: "Awake, awake!"}
              , {class: "q2", text: "Stand up, Jerusalem,"}
              , {class: "q2", text: "you who have drunk from Yahweh’s hand the cup of his wrath."}
              , {class: "q", text: "You have drunken the bowl of the cup of staggering,"}
              , {class: "q2", text: "and drained it."}
              ]
        , 18: [ {class: "q", text: "There is no one to guide her among all the sons to whom she has given birth;"}
              , {class: "q2", text: "and there is no one who takes her by the hand among all the sons who she has brought up."}
              ]
        , 19: [ {class: "q", text: "These two things have happened to you—"}
              , {class: "q2", text: "who will grieve with you?—"}
              , {class: "q", text: "desolation and destruction,"}
              , {class: "q2", text: "and famine and the sword."}
              , {class: "q2", text: "How can I comfort you?"}
              ]
        , 20: [ {class: "q", text: "Your sons have fainted."}
              , {class: "q2", text: "They lie at the head of all the streets,"}
              , {class: "q2", text: "like an antelope in a net."}
              , {class: "q", text: "They are full of Yahweh’s wrath,"}
              , {class: "q2", text: "the rebuke of your God."}
              ]
        , 21: [ {class: "q", text: "Therefore now hear this, you afflicted,"}
              , {class: "q2", text: "and drunken, but not with wine:"}
              ]
        , 22: [ {class: "q", text: "Your Lord Yahweh,"}
              , {class: "q2", text: "your God who pleads the cause of his people, says,"}
              , {class: "q", text: "“Behold, I have taken out of your hand the cup of staggering,"}
              , {class: "q2", text: "even the bowl of the cup of my wrath."}
              , {class: "q2", text: "You will not drink it any more."}
              ]
        , 23: [ {class: "q", text: "I will put it into the hand of those who afflict you,"}
              , {class: "q2", text: "who have said to your soul, ‘Bow down, that we may walk over you;’"}
              , {class: "q2", text: "and you have laid your back as the ground,"}
              , {class: "q2", text: "like a street to those who walk over.”"}
              ]
        }
  , 52: { 1:  [ {class: "q", text: "Awake, awake! Put on your strength, Zion."}
              , {class: "q2", text: "Put on your beautiful garments, Jerusalem, the holy city:"}
              , {class: "q2", text: "for from now on the uncircumcised and the unclean will no more come into you."}
              ]
        , 2:  [ {class: "q", text: "Shake yourself from the dust!"}
              , {class: "q2", text: "Arise, sit up, Jerusalem!"}
              , {class: "q2", text: "Release yourself from the bonds of your neck, captive daughter of Zion!"}
              ]
        , 3:  [ {class: "q", text: "For Yahweh says, “You were sold for nothing;"}
              , {class: "q2", text: "and you will be redeemed without money.”"}
              ]
        , 4:  [ {class: "endpara", text: "For the Lord Yahweh says:"}
              , {class: "q", text: "“My people went down at the first into Egypt to live there:"}
              , {class: "q2", text: "and the Assyrian has oppressed them without cause."}
              ]
        , 5:  [ {class: "q", text: "“Now therefore, what do I do here,” says Yahweh,"}
              , {class: "q2", text: "“seeing that my people are taken away for nothing?"}
              , {class: "q", text: "Those who rule over them mock,” says Yahweh,"}
              , {class: "q2", text: "“and my name is blasphemed continually all day long."}
              ]
        , 6:  [ {class: "q", text: "Therefore my people shall know my name."}
              , {class: "q2", text: "Therefore they shall know in that day that I am he who speaks."}
              , {class: "q2", text: "Behold, it is I.”"}
              ]
        , 7:  [ {class: "q", text: "How beautiful on the mountains are the feet of him who brings good news,"}
              , {class: "q2", text: "who publishes peace,"}
              , {class: "q2", text: "who brings good news,"}
              , {class: "q2", text: "who proclaims salvation,"}
              , {class: "q2", text: "who says to Zion, “Your God reigns!”"}
              ]
        , 8:  [ {class: "q", text: "Your watchmen lift up their voice."}
              , {class: "q2", text: "Together they sing;"}
              , {class: "q2", text: "for they shall see eye to eye when Yahweh returns to Zion."}
              ]
        , 9:  [ {class: "q", text: "Break out into joy!"}
              , {class: "q2", text: "Sing together, you waste places of Jerusalem;"}
              , {class: "q2", text: "for Yahweh has comforted his people."}
              , {class: "q2", text: "He has redeemed Jerusalem."}
              ]
        , 10: [ {class: "q", text: "Yahweh has made his holy arm bare in the eyes of all the nations."}
              , {class: "q2", text: "All the ends of the earth have seen the salvation of our God."}
              ]
        , 11: [ {class: "q", text: "Depart! Depart! Go out from there! Touch no unclean thing!"}
              , {class: "q2", text: "Go out from among her!"}
              , {class: "q2", text: "Cleanse yourselves, you who carry Yahweh’s vessels."}
              ]
        , 12: [ {class: "q", text: "For you shall not go out in haste,"}
              , {class: "q2", text: "neither shall you go by flight:"}
              , {class: "q", text: "for Yahweh will go before you;"}
              , {class: "q2", text: "and the God of Israel will be your rear guard."}
              ]
        , 13: [ {class: "q", text: "Behold, my servant will deal wisely."}
              , {class: "q2", text: "He will be exalted and lifted up,"}
              , {class: "q2", text: "and will be very high."}
              ]
        , 14: [ {class: "q", text: "Just as many were astonished at you—"}
              , {class: "q2", text: "his appearance was marred more than any man, and his form more than the sons of men—"}
              ]
        , 15: [ {class: "q", text: "so he will cleanse many nations."}
              , {class: "q2", text: "Kings will shut their mouths at him;"}
              , {class: "q2", text: "for they will see that which had not been told them,"}
              , {class: "q2", text: "and they will understand that which they had not heard."}
              ]
        }
  , 53: { 1:  [ {class: "q", text: "Who has believed our message?"}
              , {class: "q2", text: "To whom has Yahweh’s arm been revealed?"}
              ]
        , 2:  [ {class: "q", text: "For he grew up before him as a tender plant,"}
              , {class: "q2", text: "and as a root out of dry ground."}
              , {class: "q", text: "He has no good looks or majesty."}
              , {class: "q2", text: "When we see him, there is no beauty that we should desire him."}
              ]
        , 3:  [ {class: "q", text: "He was despised"}
              , {class: "q2", text: "and rejected by men,"}
              , {class: "q", text: "a man of suffering"}
              , {class: "q2", text: "and acquainted with disease."}
              , {class: "q", text: "He was despised as one from whom men hide their face;"}
              , {class: "q2", text: "and we didn’t respect him."}
              ]
        , 4:  [ {class: "q", text: "Surely he has borne our sickness"}
              , {class: "q2", text: "and carried our suffering;"}
              , {class: "q", text: "yet we considered him plagued,"}
              , {class: "q2", text: "struck by God, and afflicted."}
              ]
        , 5:  [ {class: "q", text: "But he was pierced for our transgressions."}
              , {class: "q2", text: "He was crushed for our iniquities."}
              , {class: "q", text: "The punishment that brought our peace was on him;"}
              , {class: "q2", text: "and by his wounds we are healed."}
              ]
        , 6:  [ {class: "q", text: "All we like sheep have gone astray."}
              , {class: "q2", text: "Everyone has turned to his own way;"}
              , {class: "q2", text: "and Yahweh has laid on him the iniquity of us all."}
              ]
        , 7:  [ {class: "q", text: "He was oppressed,"}
              , {class: "q2", text: "yet when he was afflicted he didn’t open his mouth."}
              , {class: "q", text: "As a lamb that is led to the slaughter,"}
              , {class: "q2", text: "and as a sheep that before its shearers is silent,"}
              , {class: "q2", text: "so he didn’t open his mouth."}
              ]
        , 8:  [ {class: "q", text: "He was taken away by oppression and judgment."}
              , {class: "q2", text: "As for his generation,"}
              , {class: "q2", text: "who considered that he was cut off out of the land of the living"}
              , {class: "q2", text: "and stricken for the disobedience of my people?"}
              ]
        , 9:  [ {class: "q", text: "They made his grave with the wicked,"}
              , {class: "q2", text: "and with a rich man in his death,"}
              , {class: "q", text: "although he had done no violence,"}
              , {class: "q2", text: "nor was any deceit in his mouth."}
              ]
        , 10: [ {class: "q", text: "Yet it pleased Yahweh to bruise him."}
              , {class: "q2", text: "He has caused him to suffer."}
              , {class: "q", text: "When you make his soul an offering for sin,"}
              , {class: "q2", text: "he will see his offspring."}
              , {class: "q", text: "He will prolong his days"}
              , {class: "q2", text: "and Yahweh’s pleasure will prosper in his hand."}
              ]
        , 11: [ {class: "q", text: "After the suffering of his soul,"}
              , {class: "q2", text: "he will see the light and be satisfied."}
              , {class: "q", text: "My righteous servant will justify many by the knowledge of himself;"}
              , {class: "q2", text: "and he will bear their iniquities."}
              ]
        , 12: [ {class: "q", text: "Therefore I will give him a portion with the great."}
              , {class: "q2", text: "He will divide the plunder with the strong;"}
              , {class: "q", text: "because he poured out his soul to death"}
              , {class: "q2", text: "and was counted with the transgressors;"}
              , {class: "q", text: "yet he bore the sins of many"}
              , {class: "q2", text: "and made intercession for the transgressors."}
              ]
        }
  , 54: { 1:  [ {class: "q", text: "“Sing, barren, you who didn’t give birth;"}
              , {class: "q2", text: "break out into singing, and cry aloud, you who didn’t travail with child:"}
              , {class: "q2", text: "for more are the children of the desolate than the children of the married wife,” says Yahweh."}
              ]
        , 2:  [ {class: "q", text: "“Enlarge the place of your tent,"}
              ,  {class: "q2", text: "and let them stretch out the curtains of your habitations;"}
              , {class: "q2", text: "don’t spare: lengthen your cords, and strengthen your stakes."}
              ]
        , 3:  [ {class: "q", text: "For you will spread out on the right hand and on the left;"}
              , {class: "q2", text: "and your offspring will possess the nations"}
              , {class: "q2", text: "and settle in desolate cities."}
              ]
        , 4:  [ {class: "q", text: "“Don’t be afraid, for you will not be ashamed."}
              , {class: "q2", text: "Don’t be confounded, for you will not be disappointed."}
              , {class: "q", text: "For you will forget the shame of your youth."}
              , {class: "q2", text: "You will remember the reproach of your widowhood no more."}
              ]
        , 5:  [ {class: "q", text: "For your Maker is your husband; Yahweh of Armies is his name."}
              , {class: "q2", text: "The Holy One of Israel is your Redeemer."}
              , {class: "q2", text: "He will be called the God of the whole earth."}
              ]
        , 6:  [ {class: "q", text: "For Yahweh has called you as a wife forsaken and grieved in spirit,"}
              , {class: "q2", text: "even a wife of youth, when she is cast off,” says your God."}
              ]
        , 7:  [ {class: "q", text: "“For a small moment I have forsaken you,"}
              , {class: "q2", text: "but I will gather you with great mercies."}
              ]
        , 8:  [ {class: "q", text: "In overflowing wrath I hid my face from you for a moment,"}
              , {class: "q2", text: "but with everlasting loving kindness I will have mercy on you,” says Yahweh your Redeemer."}
              ]
        , 9:  [ {class: "q", text: "“For this is like the waters of Noah to me;"}
              , {class: "q2", text: "for as I have sworn that the waters of Noah will no more go over the earth,"}
              , {class: "q2", text: "so I have sworn that I will not be angry with you, nor rebuke you."}
              ]
        , 10: [ {class: "q", text: "For the mountains may depart,"}
              , {class: "q2", text: "and the hills be removed;"}
              , {class: "q", text: "but my loving kindness will not depart from you,"}
              , {class: "q2", text: "and my covenant of peace will not be removed,”"}
              , {class: "q2", text: "says Yahweh who has mercy on you."}
              ]
        , 11: [ {class: "q", text: "“You afflicted, tossed with storms, and not comforted,"}
              , {class: "q2", text: "behold, I will set your stones in beautiful colors,"}
              , {class: "q2", text: "and lay your foundations with sapphires."}
              ]
        , 12: [ {class: "q", text: "I will make your pinnacles of rubies,"}
              , {class: "q2", text: "your gates of sparkling jewels,"}
              , {class: "q2", text: "and all your walls of precious stones."}
              ]
        , 13: [ {class: "q", text: "All your children will be taught by Yahweh;"}
              , {class: "q2", text: "and your children’s peace will be great."}
              ]
        , 14: [ {class: "q", text: "You will be established in righteousness."}
              , {class: "q2", text: "You will be far from oppression,"}
              , {class: "q2", text: "for you will not be afraid,"}
              , {class: "q2", text: "and far from terror,"}
              ,  {class: "q2", text: "for it shall not come near you."}
              ]
        , 15: [ {class: "q", text: "Behold, they may gather together, but not by me."}
              ,  {class: "q2", text: "Whoever gathers together against you will fall because of you."}
              ]
        , 16: [ {class: "q", text: "“Behold, I have created the blacksmith who fans the coals into flame,"}
              , {class: "q2", text: "and forges a weapon for his work;"}
              , {class: "q2", text: "and I have created the destroyer to destroy."}
              ]
        , 17: [ {class: "q", text: "No weapon that is formed against you will prevail;"}
              , {class: "q2", text: "and you will condemn every tongue that rises against you in judgment."}
              , {class: "q", text: "This is the heritage of Yahweh’s servants,"}
              , {class: "q2", text: "and their righteousness is of me,” says Yahweh."}
              ]
        }
  , 55: { 1:  [ {class: "q", text: "“Hey! Come, everyone who thirsts, to the waters!"}
              , {class: "q2", text: "Come, he who has no money, buy, and eat!"}
              , {class: "q2", text: "Yes, come, buy wine and milk without money and without price."}
              ]
        , 2:  [ {class: "q", text: "Why do you spend money for that which is not bread,"}
              , {class: "q2", text: "and your labor for that which doesn’t satisfy?"}
              , {class: "q", text: "Listen diligently to me, and eat that which is good,"}
              , {class: "q2", text: "and let your soul delight itself in richness."}
              ]
        , 3:  [ {class: "q", text: "Turn your ear, and come to me."}
              , {class: "q2", text: "Hear, and your soul will live."}
              , {class: "q2", text: "I will make an everlasting covenant with you, even the sure mercies of David."}
              ]
        , 4:  [ {class: "q", text: "Behold, I have given him for a witness to the peoples,"}
              , {class: "q2", text: "a leader and commander to the peoples."}
              ]
        , 5:  [ {class: "q", text: "Behold, you shall call a nation that you don’t know;"}
              , {class: "q2", text: "and a nation that didn’t know you shall run to you,"}
              , {class: "q2", text: "because of Yahweh your God,"}
              , {class: "q2", text: "and for the Holy One of Israel;"}
              , {class: "q2", text: "for he has glorified you.”"}
              ]
        , 6:  [ {class: "q", text: "Seek Yahweh while he may be found."}
              ,  {class: "q2", text: "Call on him while he is near."}
              ]
        , 7:  [ {class: "q", text: "Let the wicked forsake his way,"}
              , {class: "q2", text: "and the unrighteous man his thoughts."}
              , {class: "q", text: "Let him return to Yahweh, and he will have mercy on him,"}
              , {class: "q2", text: "to our God, for he will freely pardon."}
              ]
        , 8:  [ {class: "q", text: "“For my thoughts are not your thoughts,"}
              , {class: "q2", text: "and your ways are not my ways,” says Yahweh."}
              ]
        , 9:  [ {class: "q", text: "“For as the heavens are higher than the earth,"}
              , {class: "q2", text: "so are my ways higher than your ways,"}
              , {class: "q2", text: "and my thoughts than your thoughts."}
              ]
        , 10: [ {class: "q", text: "For as the rain comes down and the snow from the sky,"}
              , {class: "q2", text: "and doesn’t return there, but waters the earth,"}
              , {class: "q2", text: "and makes it grow and bud,"}
              , {class: "q2", text: "and gives seed to the sower and bread to the eater;"}
              ]
        , 11: [ {class: "q", text: "so is my word that goes out of my mouth:"}
              , {class: "q2", text: "it will not return to me void,"}
              , {class: "q2", text: "but it will accomplish that which I please,"}
              , {class: "q2", text: "and it will prosper in the thing I sent it to do."}
              ]
        , 12: [ {class: "q", text: "For you shall go out with joy,"}
              , {class: "q2", text: "and be led out with peace."}
              , {class: "q", text: "The mountains and the hills will break out before you into singing;"}
              , {class: "q2", text: "and all the trees of the fields will clap their hands."}
              ]
        , 13: [ {class: "q", text: "Instead of the thorn the cypress tree will come up;"}
              , {class: "q2", text: "and instead of the brier the myrtle tree will come up."}
              , {class: "q", text: "It will make a name for Yahweh,"}
              , {class: "q2", text: "for an everlasting sign that will not be cut off.”"}
              ]
        }
  , 56: { 1:  [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“Maintain justice"}
              , {class: "q2", text: "and do what is right,"}
              , {class: "q", text: "for my salvation is near"}
              , {class: "q2", text: "and my righteousness will soon be revealed."}
              ]
        , 2:  [ {class: "q", text: "Blessed is the man who does this,"}
              , {class: "q2", text: "and the son of man who holds it fast;"}
              , {class: "q", text: "who keeps the Sabbath without profaning it"}
              , {class: "q2", text: "and keeps his hand from doing any evil.”"}
              ]
        , 3:  [ {class: "q", text: "Let no foreigner who has joined himself to Yahweh speak, saying,"}
              , {class: "q2", text: "“Yahweh will surely separate me from his people.”"}
              , {class: "q2", text: "Do not let the eunuch say, “Behold, I am a dry tree.”"}
              ]
        , 4:  [ {class: "q", text: "For Yahweh says, “To the eunuchs who keep my Sabbaths,"}
              , {class: "q2", text: "choose the things that please me,"}
              , {class: "q2", text: "and hold fast to my covenant,"}
              ]
        , 5:  [ {class: "q", text: "I will give them in my house and within my walls a memorial and a name better than of sons and of daughters."}
              , {class: "q2", text: "I will give them an everlasting name that will not be cut off."}
              ]
        , 6:  [ {class: "q", text: "Also the foreigners who join themselves to Yahweh"}
              , {class: "q2", text: "to serve him,"}
              , {class: "q", text: "and to love Yahweh’s name,"}
              , {class: "q2", text: "to be his servants,"}
              , {class: "q", text: "everyone who keeps the Sabbath from profaning it,"}
              , {class: "q2", text: "and holds fast my covenant,"}
              ]
        , 7:  [ {class: "q", text: "I will bring these to my holy mountain,"}
              , {class: "q2", text: "and make them joyful in my house of prayer."}
              , {class: "q", text: "Their burnt offerings and their sacrifices will be accepted on my altar;"}
              , {class: "q2", text: "for my house will be called a house of prayer for all peoples.”"}
              ]
        , 8:  [ {class: "q", text: "The Lord Yahweh, who gathers the outcasts of Israel, says,"}
              ,  {class: "q2", text: "“I will yet gather others to him,"}
              , {class: "q2", text: "in addition to his own who are gathered.”"}
              ]
        , 9:  [ {class: "q", text: "All you animals of the field,"}
              , {class: "q2", text: "come to devour,"}
              , {class: "q2", text: "all you animals in the forest."}
              ]
        , 10: [ {class: "q", text: "His watchmen are blind."}
              , {class: "q2", text: "They are all without knowledge."}
              , {class: "q", text: "They are all mute dogs."}
              , {class: "q2", text: "They can’t bark—"}
              , {class: "q2", text: "dreaming, lying down, loving to slumber."}
              ]
        , 11: [ {class: "q", text: "Yes, the dogs are greedy."}
              , {class: "q2", text: "They can never have enough."}
              , {class: "q", text: "They are shepherds who can’t understand."}
              , {class: "q2", text: "They have all turned to their own way,"}
              , {class: "q2", text: "each one to his gain, from every quarter."}
              ]
        , 12: [ {class: "q", text: "“Come,” say they, “I will get wine,"}
              , {class: "q2", text: "and we will fill ourselves with strong drink;"}
              , {class: "q2", text: "and tomorrow will be as today,"}
              , {class: "q2", text: "great beyond measure.”"}
              ]
        }
  , 57: { 1:  [ {class: "q", text: "The righteous perish,"}
              , {class: "q2", text: "and no one lays it to heart."}
              , {class: "q", text: "Merciful men are taken away,"}
              , {class: "q2", text: "and no one considers that the righteous is taken away from the evil."}
              ]
        , 2:  [ {class: "q", text: "He enters into peace."}
              , {class: "q2", text: "They rest in their beds,"}
              , {class: "q2", text: "each one who walks in his uprightness."}
              ]
        , 3:  [ {class: "q", text: "“But draw near here, you sons of a sorceress,"}
              , {class: "q2", text: "you offspring of adulterers and prostitutes."}
              ]
        , 4:  [ {class: "q", text: "Whom do you mock?"}
              , {class: "q2", text: "Against whom do you make a wide mouth"}
              , {class: "q2", text: "and stick out your tongue?"}
              , {class: "q", text: "Aren’t you children of disobedience"}
              , {class: "q2", text: "and offspring of falsehood,"}
              ]
        , 5:  [ {class: "q", text: "you who inflame yourselves among the oaks,"}
              , {class: "q2", text: "under every green tree;"}
              , {class: "q", text: "who kill the children in the valleys,"}
              , {class: "q2", text: "under the clefts of the rocks?"}
              ]
        , 6:  [ {class: "q", text: "Among the smooth stones of the valley is your portion."}
              , {class: "q2", text: "They, they are your lot."}
              , {class: "q", text: "You have even poured a drink offering to them."}
              , {class: "q2", text: "You have offered an offering."}
              , {class: "q2", text: "Shall I be appeased for these things?"}
              ]
        , 7:  [ {class: "q", text: "On a high and lofty mountain you have set your bed."}
              , {class: "q2", text: "You also went up there to offer sacrifice."}
              ]
        , 8:  [ {class: "q", text: "You have set up your memorial behind the doors and the posts,"}
              , {class: "q2", text: "for you have exposed yourself to someone besides me,"}
              , {class: "q2", text: "and have gone up."}
              , {class: "q", text: "You have enlarged your bed"}
              ,  {class: "q2", text: "and made you a covenant with them."}
              , {class: "q2", text: "You loved what you saw on their bed."}
              ]
        , 9:  [ {class: "q", text: "You went to the king with oil,"}
              , {class: "q2", text: "increased your perfumes,"}
              , {class: "q2", text: "sent your ambassadors far off,"}
              , {class: "q2", text: "and degraded yourself even to Sheol."}
              ]
        , 10: [ {class: "q", text: "You were wearied with the length of your ways;"}
              , {class: "q2", text: "yet you didn’t say, ‘It is in vain.’"}
              , {class: "q", text: "You found a reviving of your strength;"}
              , {class: "q2", text: "therefore you weren’t faint."}
              ]
        , 11: [ {class: "q", text: "“Whom have you dreaded and feared,"}
              , {class: "q2", text: "so that you lie,"}
              , {class: "q2", text: "and have not remembered me, nor laid it to your heart?"}
              , {class: "q", text: "Haven’t I held my peace for a long time,"}
              , {class: "q2", text: "and you don’t fear me?"}
              ]
        , 12: [ {class: "q", text: "I will declare your righteousness;"}
              , {class: "q2", text: "and as for your works, they will not benefit you."}
              ]
        , 13: [ {class: "q", text: "When you cry,"}
              , {class: "q2", text: "let those whom you have gathered deliver you;"}
              , {class: "q", text: "but the wind will take them."}
              , {class: "q2", text: "a breath will carry them all away:"}
              , {class: "q", text: "but he who takes refuge in me will possess the land,"}
              , {class: "q2", text: "and will inherit my holy mountain.”"}
              ]
        , 14: [ {class: "q", text: "He will say, “Build up, build up, prepare the way!"}
              , {class: "q2", text: "Remove the stumbling-block out of the way of my people.”"}
              ]
        , 15: [ {class: "q", text: "For the high and lofty One who inhabits eternity,"}
              , {class: "q2", text: "whose name is Holy, says:"}
              , {class: "q", text: "“I dwell in the high and holy place, with him also who is of a contrite and humble spirit,"}
              , {class: "q2", text: "to revive the spirit of the humble,"}
              , {class: "q2", text: "and to revive the heart of the contrite."}
              ]
        , 16: [ {class: "q", text: "For I will not contend forever, neither will I always be angry;"}
              , {class: "q2", text: "for the spirit would faint before me,"}
              , {class: "q2", text: "and the souls whom I have made."}
              ]
        , 17: [ {class: "q", text: "I was angry because of the iniquity of his covetousness and struck him."}
              , {class: "q2", text: "I hid myself and was angry;"}
              , {class: "q2", text: "and he went on backsliding in the way of his heart."}
              ]
        , 18: [ {class: "q", text: "I have seen his ways, and will heal him."}
              , {class: "q2", text: "I will lead him also,"}
              , {class: "q2", text: "and restore comforts to him and to his mourners."}
              ]
        , 19: [ {class: "q", text: "I create the fruit of the lips:"}
              , {class: "q2", text: "Peace, peace, to him who is far off and to him who is near,”"}
              , {class: "q2", text: "says Yahweh; “and I will heal them.”"}
              ]
        , 20: [ {class: "q", text: "But the wicked are like the troubled sea;"}
              , {class: "q2", text: "for it can’t rest and its waters cast up mire and mud."}
              ]
        , 21: [ {class: "q", text: "“There is no peace”, says my God,"}
              ,  {class: "q2", text: "“for the wicked.”"}
              ]
        }
  , 58: { 1:  [ {class: "q", text: "“Cry aloud! Don’t spare!"}
              , {class: "q2", text: "Lift up your voice like a trumpet!"}
              , {class: "q", text: "Declare to my people their disobedience,"}
              , {class: "q2", text: "and to the house of Jacob their sins."}
              ]
        , 2:  [ {class: "q", text: "Yet they seek me daily,"}
              , {class: "q2", text: "and delight to know my ways."}
              , {class: "q", text: "As a nation that did righteousness,"}
              , {class: "q2", text: "and didn’t forsake the ordinance of their God,"}
              , {class: "q", text: "they ask of me righteous judgments."}
              , {class: "q2", text: "They delight to draw near to God."}
              ]
        , 3:  [ {class: "q", text: "‘Why have we fasted,’ they say, ‘and you don’t see?"}
              , {class: "q2", text: "Why have we afflicted our soul, and you don’t notice?’"}
              , {class: "q", text: "“Behold, in the day of your fast you find pleasure,"}
              , {class: "q2", text: "and oppress all your laborers."}
              ]
        , 4:  [ {class: "q", text: "Behold, you fast for strife and contention,"}
              , {class: "q2", text: "and to strike with the fist of wickedness."}
              , {class: "q2", text: "You don’t fast today so as to make your voice to be heard on high."}
              ]
        , 5:  [ {class: "q", text: "Is this the fast that I have chosen?"}
              , {class: "q2", text: "A day for a man to humble his soul?"}
              , {class: "q", text: "Is it to bow down his head like a reed,"}
              , {class: "q2", text: "and to spread sackcloth and ashes under himself?"}
              , {class: "q", text: "Will you call this a fast,"}
              , {class: "q2", text: "and an acceptable day to Yahweh?"}
              ]
        , 6:  [ {class: "q", text: "“Isn’t this the fast that I have chosen:"}
              , {class: "q2", text: "to release the bonds of wickedness,"}
              , {class: "q2", text: "to undo the straps of the yoke,"}
              , {class: "q2", text: "to let the oppressed go free,"}
              , {class: "q2", text: "and that you break every yoke?"}
              ]
        , 7:  [ {class: "q", text: "Isn’t it to distribute your bread to the hungry,"}
              , {class: "q2", text: "and that you bring the poor who are cast out to your house?"}
              , {class: "q", text: "When you see the naked,"}
              , {class: "q2", text: "that you cover him;"}
              , {class: "q2", text: "and that you not hide yourself from your own flesh?"}
              ]
        , 8:  [ {class: "q", text: "Then your light will break out as the morning,"}
              , {class: "q2", text: "and your healing will appear quickly;"}
              , {class: "q", text: "then your righteousness shall go before you,"}
              , {class: "q2", text: "and Yahweh’s glory will be your rear guard."}
              ]
        , 9:  [ {class: "q", text: "Then you will call, and Yahweh will answer."}
              , {class: "q2", text: "You will cry for help, and he will say, ‘Here I am.’"}
              , {class: "q", text: "“If you take away from among you the yoke,"}
              , {class: "q2", text: "finger pointing,"}
              , {class: "q2", text: "and speaking wickedly;"}
              ]
        , 10: [ {class: "q", text: "and if you pour out your soul to the hungry,"}
              , {class: "q2", text: "and satisfy the afflicted soul,"}
              , {class: "q", text: "then your light will rise in darkness,"}
              , {class: "q2", text: "and your obscurity will be as the noonday;"}
              ]
        , 11: [ {class: "q", text: "and Yahweh will guide you continually,"}
              , {class: "q2", text: "satisfy your soul in dry places,"}
              , {class: "q2", text: "and make your bones strong."}
              , {class: "q", text: "You will be like a watered garden,"}
              , {class: "q2", text: "and like a spring of water"}
              , {class: "q2", text: "whose waters don’t fail."}
              ]
        , 12: [ {class: "q", text: "Those who will be of you will build the old waste places."}
              , {class: "q2", text: "You will raise up the foundations of many generations."}
              , {class: "q", text: "You will be called Repairer of the Breach,"}
              , {class: "q2", text: "Restorer of Paths with Dwellings."}
              ]
        , 13: [ {class: "q", text: "“If you turn away your foot from the Sabbath,"}
              , {class: "q2", text: "from doing your pleasure on my holy day;"}
              , {class: "q", text: "and call the Sabbath a delight,"}
              , {class: "q2", text: "and the holy of Yahweh honorable;"}
              , {class: "q2", text: "and honor it,"}
              , {class: "q2", text: "not doing your own ways,"}
              , {class: "q2", text: "nor finding your own pleasure,"}
              , {class: "q2", text: "nor speaking your own words,"}
              ]
        , 14: [ {class: "q", text: "then you will delight yourself in Yahweh,"}
              , {class: "q2", text: "and I will make you to ride on the high places of the earth,"}
              , {class: "q2", text: "and I will feed you with the heritage of Jacob your father;”"}
              , {class: "q2", text: "for Yahweh’s mouth has spoken it."}
              ]
        }
  , 59: { 1:  [ {class: "q", text: "Behold, Yahweh’s hand is not shortened, that it can’t save;"}
              , {class: "q2", text: "nor his ear dull, that it can’t hear."}
              ]
        , 2:  [ {class: "q", text: "But your iniquities have separated you and your God,"}
              , {class: "q2", text: "and your sins have hidden his face from you,"}
              , {class: "q2", text: "so that he will not hear."}
              ]
        , 3:  [ {class: "q", text: "For your hands are defiled with blood,"}
              , {class: "q2", text: "and your fingers with iniquity."}
              , {class: "q", text: "Your lips have spoken lies."}
              , {class: "q2", text: "Your tongue mutters wickedness."}
              ]
        , 4:  [ {class: "q", text: "No one sues in righteousness,"}
              , {class: "q2", text: "and no one pleads in truth."}
              , {class: "q", text: "They trust in vanity,"}
              , {class: "q2", text: "and speak lies."}
              , {class: "q", text: "They conceive mischief,"}
              , {class: "q2", text: "and give birth to iniquity."}
              ]
        , 5:  [ {class: "q", text: "They hatch adders’ eggs,"}
              , {class: "q2", text: "and weave the spider’s web."}
              , {class: "q", text: "He who eats of their eggs dies;"}
              , {class: "q2", text: "and that which is crushed breaks out into a viper."}
              ]
        , 6:  [ {class: "q", text: "Their webs won’t become garments."}
              , {class: "q2", text: "They won’t cover themselves with their works."}
              , {class: "q", text: "Their works are works of iniquity,"}
              , {class: "q2", text: "and acts of violence are in their hands."}
              ]
        , 7:  [ {class: "q", text: "Their feet run to evil,"}
              , {class: "q2", text: "and they hurry to shed innocent blood."}
              , {class: "q", text: "Their thoughts are thoughts of iniquity."}
              , {class: "q2", text: "Desolation and destruction are in their paths."}
              ]
        , 8:  [ {class: "q", text: "They don’t know the way of peace;"}
              , {class: "q2", text: "and there is no justice in their ways."}
              , {class: "q", text: "They have made crooked paths for themselves;"}
              , {class: "q2", text: "whoever goes in them doesn’t know peace."}
              ]
        , 9:  [ {class: "q", text: "Therefore justice is far from us,"}
              , {class: "q2", text: "and righteousness doesn’t overtake us."}
              , {class: "q", text: "We look for light, but see darkness;"}
              , {class: "q2", text: "for brightness, but we walk in obscurity."}
              ]
        , 10: [ {class: "q", text: "We grope for the wall like the blind."}
              ,  {class: "q2", text: "Yes, we grope as those who have no eyes."}
              , {class: "q", text: "We stumble at noon as if it were twilight."}
              , {class: "q2", text: "Among those who are strong, we are like dead men."}
              ]
        , 11: [ {class: "q", text: "We all roar like bears"}
              , {class: "q2", text: "and moan bitterly like doves."}
              , {class: "q", text: "We look for justice, but there is none,"}
              , {class: "q2", text: "for salvation, but it is far off from us."}
              ]
        , 12: [ {class: "q", text: "For our transgressions are multiplied before you,"}
              ,  {class: "q2", text: "and our sins testify against us;"}
              , {class: "q", text: "for our transgressions are with us,"}
              , {class: "q2", text: "and as for our iniquities, we know them:"}
              ]
        , 13: [ {class: "q", text: "transgressing and denying Yahweh,"}
              , {class: "q2", text: "and turning away from following our God,"}
              , {class: "q2", text: "speaking oppression and revolt,"}
              , {class: "q2", text: "conceiving and uttering from the heart words of falsehood."}
              ]
        , 14: [ {class: "q", text: "Justice is turned away backward,"}
              , {class: "q2", text: "and righteousness stands far away;"}
              , {class: "q", text: "for truth has fallen in the street,"}
              , {class: "q2", text: "and uprightness can’t enter."}
              ]
        , 15: [ {class: "q", text: "Yes, truth is lacking;"}
              , {class: "q2", text: "and he who departs from evil makes himself a prey.</div><br /><div class=\"Yahweh saw it,"}
              , {class: "q2", text: "and it displeased him that there was no justice."}
              ]
        , 16: [ {class: "q", text: "He saw that there was no man,"}
              , {class: "q2", text: "and wondered that there was no intercessor."}
              , {class: "q", text: "Therefore his own arm brought salvation to him;"}
              , {class: "q2", text: "and his righteousness sustained him."}
              ]
        , 17: [ {class: "q", text: "He put on righteousness as a breastplate,"}
              , {class: "q2", text: "and a helmet of salvation on his head."}
              , {class: "q", text: "He put on garments of vengeance for clothing,"}
              , {class: "q2", text: "and was clad with zeal as a mantle."}
              ]
        , 18: [ {class: "q", text: "According to their deeds,"}
              , {class: "q2", text: "he will repay as appropriate,"}
              , {class: "q2", text: "wrath to his adversaries,"}
              , {class: "q2", text: "recompense to his enemies;"}
              , {class: "q2", text: "he will repay the islands their due."}
              ]
        , 19: [ {class: "q", text: "So they will fear Yahweh’s name from the west,"}
              , {class: "q2", text: "and his glory from the rising of the sun;"}
              , {class: "q", text: "for he will come as a rushing stream,"}
              , {class: "q2", text: "which Yahweh’s breath drives."}
              ]
        , 20: [ {class: "q", text: "“A Redeemer will come to Zion,"}
              , {class: "q2", text: "and to those who turn from disobedience in Jacob,” says Yahweh."}
              ]
        , 21: [ {class: "vs", text: "“As for me, this is my covenant with them,” says Yahweh. “My Spirit who is on you, and my words which I have put in your mouth shall not depart out of your mouth, nor out of the mouth of your offspring, nor out of the mouth of your offspring’s offspring,” says Yahweh, “from now on and forever.”"} ]
        }
  , 6:  { 1:  [ {class: "vs", text: "In the year that king Uzziah died, I saw the Lord sitting on a throne, high and lifted up; and his train filled the temple."} ]
        , 2:  [ {class: "vs", text: "Above him stood the seraphim. Each one had six wings. With two he covered his face. With two he covered his feet. With two he flew."} ]
        , 3:  [ {class: "endpara", text: "One called to another, and said,"}
              , {class: "q", text: "“Holy, holy, holy, is Yahweh of Armies!"}
              , {class: "q2", text: "The whole earth is full of his glory!”"}
              ]
        , 4:  [ {class: "vs", text: "The foundations of the thresholds shook at the voice of him who called, and the house was filled with smoke."} ]
        , 5:  [ {class: "vs", text: "Then I said, “Woe is me! For I am undone, because I am a man of unclean lips, and I dwell among a people of unclean lips: for my eyes have seen the King, Yahweh of Armies!”"} ]
        , 6:  [ {class: "vs", text: "Then one of the seraphim flew to me, having a live coal in his hand, which he had taken with the tongs from off the altar."} ]
        , 7:  [ {class: "vs", text: "He touched my mouth with it, and said, “Behold, this has touched your lips; and your iniquity is taken away, and your sin forgiven.”"} ]
        , 8:  [ {class: "endpara", text: "I heard the Lord’s voice, saying, “Whom shall I send, and who will go for us?”"}
              , {class: "para", text: "Then I said, “Here I am. Send me!”"}
              ]
        , 9:  [ {class: "endpara", text: "He said, “Go, and tell this people,"}
              , {class: "q", text: "‘You hear indeed,"}
              , {class: "q2", text: "but don’t understand."}
              , {class: "q", text: "You see indeed,"}
              , {class: "q2", text: "but don’t perceive.’"}
              ]
        , 10: [ {class: "q", text: "Make the heart of this people fat."}
              , {class: "q2", text: "Make their ears heavy, and shut their eyes;"}
              , {class: "q", text: "lest they see with their eyes,"}
              , {class: "q2", text: "hear with their ears,"}
              , {class: "q2", text: "understand with their heart,"}
              , {class: "q2", text: "and turn again, and be healed.”"}
              ]
        , 11: [ {class: "", text: "Then I said, “Lord, how long?”</p><p> He answered,"}
              , {class: "q", text: "“Until cities are waste without inhabitant,"}
              , {class: "q2", text: "houses without man,"}
              , {class: "q2", text: "the land becomes utterly waste,"}
              ]
        , 12: [ {class: "q2", text: "and Yahweh has removed men far away,"}
              , {class: "q2", text: "and the forsaken places are many within the land."}
              ]
        , 13: [ {class: "q", text: "If there is a tenth left in it,"}
              , {class: "q2", text: "that also will in turn be consumed,"}
              , {class: "q", text: "as a terebinth, and as an oak, whose stump remains when they are cut down;"}
              , {class: "q2", text: "so the holy seed is its stock.”"}
              ]
        }
  , 60: { 1:  [ {class: "q", text: "“Arise, shine; for your light has come,"}
              , {class: "q2", text: "and Yahweh’s glory has risen on you."}
              ]
        , 2:  [ {class: "q", text: "For, behold, darkness will cover the earth,"}
              , {class: "q2", text: "and thick darkness the peoples;"}
              , {class: "q", text: "but Yahweh will arise on you,"}
              , {class: "q2", text: "and his glory shall be seen on you."}
              ]
        , 3:  [ {class: "q", text: "Nations will come to your light,"}
              , {class: "q2", text: "and kings to the brightness of your rising."}
              ]
        , 4:  [ {class: "q", text: "“Lift up your eyes all around, and see:"}
              , {class: "q2", text: "they all gather themselves together."}
              , {class: "q2", text: "They come to you."}
              , {class: "q", text: "Your sons will come from far away,"}
              , {class: "q2", text: "and your daughters will be carried in arms."}
              ]
        , 5:  [ {class: "q", text: "Then you shall see and be radiant,"}
              , {class: "q2", text: "and your heart will thrill and be enlarged;"}
              , {class: "q", text: "because the abundance of the sea will be turned to you."}
              , {class: "q2", text: "The wealth of the nations will come to you."}
              ]
        , 6:  [ {class: "q", text: "A multitude of camels will cover you,"}
              , {class: "q2", text: "the dromedaries of Midian and Ephah."}
              , {class: "q", text: "All from Sheba will come."}
              , {class: "q2", text: "They will bring gold and frankincense,"}
              , {class: "q2", text: "and will proclaim the praises of Yahweh."}
              ]
        , 7:  [ {class: "q", text: "All the flocks of Kedar will be gathered together to you."}
              , {class: "q2", text: "The rams of Nebaioth will serve you."}
              , {class: "q", text: "They will be accepted as offerings on my altar;"}
              , {class: "q2", text: "and I will beautify my glorious house."}
              ]
        , 8:  [ {class: "q", text: "“Who are these who fly as a cloud,"}
              , {class: "q2", text: "and as the doves to their windows?"}
              ]
        , 9:  [ {class: "q", text: "Surely the islands will wait for me,"}
              , {class: "q2", text: "and the ships of Tarshish first,"}
              , {class: "q", text: "to bring your sons from far,"}
              , {class: "q2", text: "their silver and their gold with them,"}
              , {class: "q", text: "for the name of Yahweh your God,"}
              , {class: "q2", text: "and for the Holy One of Israel,"}
              , {class: "q2", text: "because he has glorified you."}
              ]
        , 10: [ {class: "q", text: "“Foreigners will build up your walls,"}
              , {class: "q2", text: "and their kings will serve you:"}
              , {class: "q", text: "for in my wrath I struck you,"}
              , {class: "q2", text: "but in my favor I have had mercy on you."}
              ]
        , 11: [ {class: "q", text: "Your gates also shall be open continually; they shall not be shut day nor night, that men may bring to you the wealth of the nations, and their kings led captive."} ]
        , 12: [ {class: "q", text: "For that nation and kingdom that will not serve you shall perish; yes, those nations shall be utterly wasted."} ]
        , 13: [ {class: "q", text: "“The glory of Lebanon shall come to you, the cypress tree, the pine, and the box tree together, to beautify the place of my sanctuary; and I will make the place of my feet glorious."} ]
        , 14: [ {class: "q", text: "The sons of those who afflicted you will come bowing to you;"}
              , {class: "q2", text: "and all those who despised you will bow themselves down at the soles of your feet."}
              , {class: "q", text: "They will call you Yahweh’s City,"}
              , {class: "q2", text: "the Zion of the Holy One of Israel."}
              ]
        , 15: [ {class: "q", text: "“Whereas you have been forsaken and hated,"}
              , {class: "q2", text: "so that no one passed through you,"}
              , {class: "q", text: "I will make you an eternal excellency,"}
              , {class: "q2", text: "a joy of many generations."}
              ]
        , 16: [ {class: "q", text: "You will also drink the milk of the nations,"}
              , {class: "q2", text: "and will nurse from royal breasts."}
              , {class: "q", text: "Then you will know that I, Yahweh, am your Savior,"}
              , {class: "q2", text: "your Redeemer,"}
              , {class: "q2", text: "the Mighty One of Jacob."}
              ]
        , 17: [ {class: "q", text: "For bronze I will bring gold;"}
              , {class: "q2", text: "for iron I will bring silver;"}
              , {class: "q2", text: "for wood, bronze,"}
              , {class: "q2", text: "and for stones, iron."}
              , {class: "q", text: "I will also make peace your governor,"}
              ,  {class: "q2", text: "and righteousness your ruler."}
              ]
        , 18: [ {class: "q", text: "Violence shall no more be heard in your land,"}
              , {class: "q2", text: "nor desolation or destruction within your borders;"}
              , {class: "q", text: "but you will call your walls Salvation,"}
              , {class: "q2", text: "and your gates Praise."}
              ]
        , 19: [ {class: "q", text: "The sun will be no more your light by day;"}
              , {class: "q2", text: "nor will the brightness of the moon give light to you,"}
              , {class: "q", text: "but Yahweh will be your everlasting light,"}
              , {class: "q2", text: "and your God will be your glory."}
              ]
        , 20: [ {class: "q", text: "Your sun will not go down any more,"}
              , {class: "q2", text: "nor will your moon withdraw itself;"}
              , {class: "q", text: "for Yahweh will be your everlasting light,"}
              , {class: "q2", text: "and the days of your mourning will end."}
              ]
        , 21: [ {class: "q", text: "Then your people will all be righteous."}
              , {class: "q2", text: "They will inherit the land forever,"}
              , {class: "q2", text: "the branch of my planting,"}
              , {class: "q2", text: "the work of my hands,"}
              , {class: "q2", text: "that I may be glorified."}
              ]
        , 22: [ {class: "q", text: "The little one will become a thousand,"}
              , {class: "q2", text: "and the small one a strong nation."}
              , {class: "q2", text: "I, Yahweh, will do this quickly in its time.”"}
              ]
        }
  , 61: { 1:  [ {class: "q", text: "The Lord Yahweh’s Spirit is on me,"}
              , {class: "q2", text: "because Yahweh has anointed me to preach good news to the humble."}
              , {class: "q", text: "He has sent me to bind up the broken hearted,"}
              , {class: "q2", text: "to proclaim liberty to the captives"}
              , {class: "q2", text: "and release to those who are bound,"}
              ]
        , 2:  [ {class: "q", text: "to proclaim the year of Yahweh’s favor"}
              , {class: "q2", text: "and the day of vengeance of our God,"}
              , {class: "q2", text: "to comfort all who mourn,"}
              ]
        , 3:  [ {class: "q", text: "to provide for those who mourn in Zion,"}
              , {class: "q2", text: "to give to them a garland for ashes,"}
              , {class: "q2", text: "the oil of joy for mourning,"}
              , {class: "q2", text: "the garment of praise for the spirit of heaviness,"}
              , {class: "q", text: "that they may be called trees of righteousness,"}
              , {class: "q2", text: "the planting of Yahweh,"}
              , {class: "q2", text: "that he may be glorified."}
              ]
        , 4:  [ {class: "q", text: "They will rebuild the old ruins."}
              , {class: "q2", text: "They will raise up the former devastated places."}
              , {class: "q", text: "They will repair the ruined cities"}
              , {class: "q2", text: "that have been devastated for many generations."}
              ]
        , 5:  [ {class: "q", text: "Strangers will stand and feed your flocks."}
              , {class: "q2", text: "Foreigners will work your fields and your vineyards."}
              ]
        , 6:  [ {class: "q", text: "But you will be called Yahweh’s priests."}
              , {class: "q2", text: "Men will call you the servants of our God."}
              , {class: "q", text: "You will eat the wealth of the nations."}
              , {class: "q2", text: "You will boast in their glory."}
              ]
        , 7:  [ {class: "q", text: "Instead of your shame you will have double."}
              , {class: "q2", text: "Instead of dishonor, they will rejoice in their portion."}
              , {class: "q", text: "Therefore in their land they will possess double."}
              , {class: "q2", text: "Everlasting joy will be to them."}
              ]
        , 8:  [ {class: "q", text: "“For I, Yahweh, love justice."}
              , {class: "q2", text: "I hate robbery and iniquity."}
              , {class: "q", text: "I will give them their reward in truth"}
              , {class: "q2", text: "and I will make an everlasting covenant with them."}
              ]
        , 9:  [ {class: "q", text: "Their offspring will be known among the nations,"}
              , {class: "q2", text: "and their offspring among the peoples."}
              , {class: "q", text: "All who see them will acknowledge them,"}
              , {class: "q2", text: "that they are the offspring which Yahweh has blessed.”"}
              ]
        , 10: [ {class: "q", text: "I will greatly rejoice in Yahweh!"}
              , {class: "q2", text: "My soul will be joyful in my God,"}
              , {class: "q", text: "for he has clothed me with the garments of salvation."}
              , {class: "q2", text: "He has covered me with the robe of righteousness,"}
              , {class: "q2", text: "as a bridegroom decks himself with a garland"}
              , {class: "q2", text: "and as a bride adorns herself with her jewels."}
              ]
        , 11: [ {class: "q", text: "For as the earth produces its bud,"}
              , {class: "q2", text: "and as the garden causes the things that are sown in it to spring up,"}
              , {class: "q2", text: "so the Lord Yahweh will cause righteousness and praise to spring up before all the nations."}
              ]
        }
  , 62: { 1:  [ {class: "q", text: "For Zion’s sake I will not hold my peace,"}
              , {class: "q2", text: "and for Jerusalem’s sake I will not rest,"}
              , {class: "q", text: "until her righteousness shines out like the dawn,"}
              , {class: "q2", text: "and her salvation like a burning lamp."}
              ]
        , 2:  [ {class: "q", text: "The nations will see your righteousness,"}
              , {class: "q2", text: "and all kings your glory."}
              , {class: "q", text: "You will be called by a new name,"}
              , {class: "q2", text: "which Yahweh’s mouth will name."}
              ]
        , 3:  [ {class: "q", text: "You will also be a crown of beauty in Yahweh‘s hand,"}
              , {class: "q2", text: "and a royal diadem in your God’s hand."}
              ]
        , 4:  [ {class: "q", text: "You will not be called Forsaken any more,"}
              , {class: "q2", text: "nor will your land be called Desolate any more;"}
              , {class: "q", text: "but you will be called Hephzibah,"}
              , {class: "q2", text: "and your land Beulah;"}
              , {class: "q", text: "for Yahweh delights in you,"}
              , {class: "q2", text: "and your land will be married."}
              ]
        , 5:  [ {class: "q", text: "For as a young man marries a virgin,"}
              , {class: "q2", text: "so your sons will marry you."}
              , {class: "q", text: "As a bridegroom rejoices over his bride,"}
              , {class: "q2", text: "so your God will rejoice over you."}
              ]
        , 6:  [ {class: "q", text: "I have set watchmen on your walls, Jerusalem."}
              , {class: "q2", text: "They will never be silent day nor night."}
              , {class: "q", text: "You who call on Yahweh, take no rest,"}
              ]
        , 7:  [ {class: "q2", text: "and give him no rest, until he establishes,"}
              , {class: "q2", text: "and until he makes Jerusalem a praise in the earth."}
              ]
        , 8:  [ {class: "q", text: "Yahweh has sworn by his right hand,"}
              , {class: "q2", text: "and by the arm of his strength,"}
              , {class: "q", text: "“Surely I will no more give your grain to be food for your enemies,"}
              , {class: "q2", text: "and foreigners will not drink your new wine, for which you have labored,"}
              ]
        , 9:  [ {class: "q", text: "but those who have harvested it will eat it, and praise Yahweh."}
              , {class: "q2", text: "Those who have gathered it will drink it in the courts of my sanctuary.”"}
              ]
        , 10: [ {class: "q", text: "Go through, go through the gates!"}
              , {class: "q2", text: "Prepare the way of the people!"}
              , {class: "q", text: "Build up, build up the highway!"}
              , {class: "q2", text: "Gather out the stones!"}
              , {class: "q2", text: "Lift up a banner for the peoples."}
              ]
        , 11: [ {class: "q", text: "Behold, Yahweh has proclaimed to the end of the earth,"}
              , {class: "q2", text: "“Say to the daughter of Zion,"}
              , {class: "q2", text: "‘Behold, your salvation comes!"}
              , {class: "q", text: "Behold, his reward is with him,"}
              , {class: "q2", text: "and his recompense before him!’”"}
              ]
        , 12: [ {class: "q", text: "They will call them “The Holy People,"}
              , {class: "q2", text: "Yahweh’s Redeemed”."}
              , {class: "q", text: "You will be called “Sought Out,"}
              , {class: "q2", text: "A City Not Forsaken”."}
              ]
        }
  , 63: { 1:  [ {class: "q", text: "Who is this who comes from Edom,"}
              , {class: "q2", text: "with dyed garments from Bozrah?"}
              , {class: "q", text: "Who is this who is glorious in his clothing,"}
              , {class: "q2", text: "marching in the greatness of his strength?"}
              , {class: "q", text: "“It is I who speak in righteousness,"}
              , {class: "q2", text: "mighty to save.”"}
              ]
        , 2:  [ {class: "q", text: "Why is your clothing red,"}
              , {class: "q2", text: "and your garments like him who treads in the wine vat?"}
              ]
        , 3:  [ {class: "q", text: "“I have trodden the wine press alone."}
              , {class: "q2", text: "Of the peoples, no one was with me."}
              , {class: "q", text: "Yes, I trod them in my anger"}
              , {class: "q2", text: "and trampled them in my wrath."}
              , {class: "q", text: "Their lifeblood is sprinkled on my garments,"}
              , {class: "q2", text: "and I have stained all my clothing."}
              ]
        , 4:  [ {class: "q", text: "For the day of vengeance was in my heart,"}
              , {class: "q2", text: "and the year of my redeemed has come."}
              ]
        , 5:  [ {class: "q", text: "I looked, and there was no one to help;"}
              , {class: "q2", text: "and I wondered that there was no one to uphold."}
              , {class: "q", text: "Therefore my own arm brought salvation to me."}
              , {class: "q2", text: "My own wrath upheld me."}
              ]
        , 6:  [ {class: "q", text: "I trod down the peoples in my anger"}
              , {class: "q2", text: "and made them drunk in my wrath."}
              , {class: "q2", text: "I poured their lifeblood out on the earth.”"}
              ]
        , 7:  [ {class: "q", text: "I will tell of the loving kindnesses of Yahweh"}
              , {class: "q2", text: "and the praises of Yahweh,"}
              , {class: "q2", text: "according to all that Yahweh has given to us,"}
              , {class: "q", text: "and the great goodness toward the house of Israel,"}
              , {class: "q2", text: "which he has given to them according to his mercies,"}
              , {class: "q2", text: "and according to the multitude of his loving kindnesses."}
              ]
        , 8:  [ {class: "q", text: "For he said, “Surely, they are my people,"}
              , {class: "q2", text: "children who will not deal falsely;”"}
              , {class: "q2", text: "so he became their Savior."}
              ]
        , 9:  [ {class: "q", text: "In all their affliction he was afflicted,"}
              , {class: "q2", text: "and the angel of his presence saved them."}
              , {class: "q", text: "In his love and in his pity he redeemed them."}
              , {class: "q2", text: "He bore them,"}
              , {class: "q2", text: "and carried them all the days of old."}
              ]
        , 10: [ {class: "q", text: "But they rebelled"}
              , {class: "q2", text: "and grieved his Holy Spirit."}
              , {class: "q", text: "Therefore he turned and became their enemy,"}
              , {class: "q2", text: "and he himself fought against them."}
              ]
        , 11: [ {class: "q", text: "Then he remembered the days of old,"}
              , {class: "q2", text: "Moses and his people, saying,"}
              , {class: "q", text: "“Where is he who brought them up out of the sea with the shepherds of his flock?"}
              , {class: "q2", text: "Where is he who put his Holy Spirit among them?”"}
              ]
        , 12: [ {class: "q", text: "Who caused his glorious arm to be at Moses’ right hand?"}
              , {class: "q2", text: "Who divided the waters before them, to make himself an everlasting name?"}
              ]
        , 13: [ {class: "q", text: "Who led them through the depths,"}
              , {class: "q2", text: "like a horse in the wilderness,"}
              , {class: "q2", text: "so that they didn’t stumble?"}
              ]
        , 14: [ {class: "q", text: "As the livestock that go down into the valley,"}
              , {class: "q2", text: "Yahweh’s Spirit caused them to rest."}
              , {class: "q2", text: "So you led your people to make yourself a glorious name."}
              ]
        , 15: [ {class: "q", text: "Look down from heaven,"}
              , {class: "q2", text: "and see from the habitation of your holiness and of your glory."}
              , {class: "q", text: "Where are your zeal and your mighty acts?"}
              , {class: "q2", text: "The yearning of your heart and your compassion is restrained toward me."}
              ]
        , 16: [ {class: "q", text: "For you are our Father,"}
              , {class: "q2", text: "though Abraham doesn’t know us,"}
              , {class: "q2", text: "and Israel does not acknowledge us."}
              , {class: "q", text: "You, Yahweh, are our Father."}
              , {class: "q2", text: "Our Redeemer from everlasting is your name."}
              ]
        , 17: [ {class: "q", text: "O Yahweh, why do you make us wander from your ways,"}
              , {class: "q2", text: "and harden our heart from your fear?"}
              , {class: "q", text: "Return for your servants’ sake,"}
              , {class: "q2", text: "the tribes of your inheritance."}
              ]
        , 18: [ {class: "q", text: "Your holy people possessed it but a little while."}
              , {class: "q2", text: "Our adversaries have trodden down your sanctuary."}
              ]
        , 19: [ {class: "q", text: "We have become like those over whom you never ruled,"}
              , {class: "q2", text: "like those who were not called by your name."}
              ]
        }
  , 64: { 1:  [ {class: "q", text: "Oh that you would tear the heavens,"}
              , {class: "q2", text: "that you would come down,"}
              , {class: "q2", text: "that the mountains might quake at your presence."}
              ]
        , 2:  [ {class: "q", text: "As when fire kindles the brushwood,"}
              , {class: "q2", text: "and the fire causes the water to boil;"}
              , {class: "q", text: "Make your name known to your adversaries,"}
              , {class: "q2", text: "that the nations may tremble at your presence!"}
              ]
        , 3:  [ {class: "q", text: "When you did awesome things which we didn’t look for,"}
              , {class: "q2", text: "you came down, and the mountains quaked at your presence."}
              ]
        , 4:  [ {class: "q", text: "For from of old men have not heard,"}
              , {class: "q2", text: "nor perceived by the ear,"}
              , {class: "q2", text: "nor has the eye seen a God besides you,"}
              , {class: "q2", text: "who works for him who waits for him."}
              ]
        , 5:  [ {class: "q", text: "You meet him who rejoices and does righteousness,"}
              , {class: "q2", text: "those who remember you in your ways."}
              , {class: "q", text: "Behold, you were angry, and we sinned."}
              , {class: "q2", text: "We have been in sin for a long time."}
              , {class: "q2", text: "Shall we be saved?"}
              ]
        , 6:  [ {class: "q", text: "For we have all become like one who is unclean,"}
              , {class: "q2", text: "and all our righteousness is like a polluted garment."}
              , {class: "q", text: "We all fade like a leaf;"}
              , {class: "q2", text: "and our iniquities, like the wind, take us away."}
              ]
        , 7:  [ {class: "q", text: "There is no one who calls on your name,"}
              , {class: "q2", text: "who stirs himself up to take hold of you;"}
              , {class: "q", text: "for you have hidden your face from us,"}
              , {class: "q2", text: "and have consumed us by means of our iniquities."}
              ]
        , 8:  [ {class: "q", text: "But now, Yahweh, you are our Father."}
              , {class: "q2", text: "We are the clay and you our potter."}
              , {class: "q2", text: "We all are the work of your hand."}
              ]
        , 9:  [ {class: "q", text: "Don’t be furious, Yahweh."}
              , {class: "q2", text: "Don’t remember iniquity forever."}
              , {class: "q", text: "Look and see, we beg you,"}
              , {class: "q2", text: "we are all your people."}
              ]
        , 10: [ {class: "q", text: "Your holy cities have become a wilderness."}
              , {class: "q2", text: "Zion has become a wilderness,"}
              , {class: "q2", text: "Jerusalem a desolation."}
              ]
        , 11: [ {class: "q", text: "Our holy and our beautiful house where our fathers praised you"}
              , {class: "q2", text: "is burned with fire."}
              , {class: "q2", text: "All our pleasant places are laid waste."}
              ]
        , 12: [ {class: "q", text: "Will you hold yourself back for these things, Yahweh?"}
              , {class: "q2", text: "Will you keep silent and punish us very severely?"}
              ]
        }
  , 65: { 1:  [ {class: "q", text: "“I am inquired of by those who didn’t ask."}
              , {class: "q2", text: "I am found by those who didn’t seek me."}
              , {class: "q2", text: "I said, ‘See me, see me,’ to a nation that was not called by my name."}
              ]
        , 2:  [ {class: "q", text: "I have spread out my hands all day to a rebellious people,"}
              , {class: "q2", text: "who walk in a way that is not good,"}
              , {class: "q2", text: "after their own thoughts;"}
              ]
        , 3:  [ {class: "q", text: "a people who provoke me to my face continually,"}
              , {class: "q2", text: "sacrificing in gardens,"}
              , {class: "q2", text: "and burning incense on bricks;"}
              ]
        , 4:  [ {class: "q", text: "who sit among the graves,"}
              , {class: "q2", text: "and spend nights in secret places;"}
              , {class: "q", text: "who eat pig’s meat,"}
              , {class: "q2", text: "and broth of abominable things is in their vessels;"}
              ]
        , 5:  [ {class: "q", text: "who say, ‘Stay by yourself,"}
              , {class: "q2", text: "don’t come near to me,"}
              , {class: "q2", text: "for I am holier than you.’"}
              , {class: "q", text: "These are smoke in my nose,"}
              , {class: "q2", text: "a fire that burns all day."}
              ]
        , 6:  [ {class: "q", text: "“Behold, it is written before me:"}
              , {class: "q2", text: "I will not keep silence,"}
              , {class: "q2", text: "but will repay,"}
              , {class: "q2", text: "yes, I will repay into their bosom,"}
              ]
        , 7:  [ {class: "q", text: "your own iniquities, and the iniquities of your fathers together”, says Yahweh,"}
              , {class: "q2", text: "“who have burned incense on the mountains,"}
              , {class: "q2", text: "and blasphemed me on the hills."}
              , {class: "q2", text: "Therefore I will first measure their work into their bosom.”"}
              ]
        , 8:  [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“As the new wine is found in the cluster,"}
              , {class: "q2", text: "and one says, ‘Don’t destroy it, for a blessing is in it:’"}
              , {class: "q", text: "so I will do for my servants’ sake,"}
              , {class: "q2", text: "that I may not destroy them all."}
              ]
        , 9:  [ {class: "q", text: "I will bring offspring out of Jacob,"}
              , {class: "q2", text: "and out of Judah an inheritor of my mountains."}
              , {class: "q", text: "My chosen will inherit it,"}
              , {class: "q2", text: "and my servants will dwell there."}
              ]
        , 10: [ {class: "q", text: "Sharon will be a fold of flocks,"}
              , {class: "q2", text: "and the valley of Achor a place for herds to lie down in,"}
              , {class: "q2", text: "for my people who have sought me."}
              ]
        , 11: [ {class: "q", text: "“But you who forsake Yahweh,"}
              ,  {class: "q2", text: "who forget my holy mountain,"}
              , {class: "q2", text: "who prepare a table for Fortune,"}
              , {class: "q2", text: "and who fill up mixed wine to Destiny;"}
              ]
        , 12: [ {class: "q", text: "I will destine you to the sword,"}
              , {class: "q2", text: "and you will all bow down to the slaughter;"}
              , {class: "q", text: "because when I called, you didn’t answer."}
              , {class: "q2", text: "When I spoke, you didn’t listen;"}
              , {class: "q", text: "but you did that which was evil in my eyes,"}
              , {class: "q2", text: "and chose that in which I didn’t delight.”"}
              ]
        , 13: [ {class: "endpara", text: "Therefore the Lord Yahweh says,"}
              , {class: "q", text: "“Behold, my servants will eat,"}
              , {class: "q2", text: "but you will be hungry;"}
              , {class: "q", text: "behold, my servants will drink,"}
              , {class: "q2", text: "but you will be thirsty."}
              , {class: "q", text: "Behold, my servants will rejoice,"}
              , {class: "q2", text: "but you will be disappointed;"}
              ]
        , 14: [ {class: "q", text: "Behold, my servants will sing for joy of heart,"}
              , {class: "q2", text: "but you will cry for sorrow of heart,"}
              , {class: "q2", text: "and will wail for anguish of spirit."}
              ]
        , 15: [ {class: "q", text: "You will leave your name for a curse to my chosen;"}
              , {class: "q2", text: "and the Lord Yahweh will kill you."}
              , {class: "q", text: "He will call his servants by another name,"}
              ]
        , 16: [ {class: "q2", text: "so that he who blesses himself in the earth will bless himself in the God of truth;"}
              , {class: "q2", text: "and he who swears in the earth will swear by the God of truth;"}
              , {class: "q", text: "because the former troubles are forgotten,"}
              , {class: "q2", text: "and because they are hidden from my eyes."}
              ]
        , 17: [ {class: "q", text: "“For, behold, I create new heavens and a new earth;"}
              , {class: "q2", text: "and the former things will not be remembered,"}
              , {class: "q2", text: "nor come into mind."}
              ]
        , 18: [ {class: "q", text: "But be glad and rejoice forever in that which I create;"}
              , {class: "q2", text: "for, behold, I create Jerusalem to be a delight,"}
              , {class: "q2", text: "and her people a joy."}
              ]
        , 19: [ {class: "q", text: "I will rejoice in Jerusalem,"}
              , {class: "q2", text: "and delight in my people;"}
              , {class: "q", text: "and the voice of weeping and the voice of crying"}
              , {class: "q2", text: "will be heard in her no more."}
              ]
        , 20: [ {class: "q", text: "“No more will there be an infant who only lives a few days,"}
              , {class: "q2", text: "nor an old man who has not filled his days;"}
              , {class: "q", text: "for the child will die one hundred years old,"}
              , {class: "q2", text: "and the sinner being one hundred years old will be accursed."}
              ]
        , 21: [ {class: "q", text: "They will build houses and inhabit them."}
              , {class: "q2", text: "They will plant vineyards and eat their fruit."}
              ]
        , 22: [ {class: "q", text: "They will not build and another inhabit."}
              , {class: "q2", text: "They will not plant and another eat:"}
              , {class: "q", text: "for the days of my people will be like the days of a tree,"}
              , {class: "q2", text: "and my chosen will long enjoy the work of their hands."}
              ]
        , 23: [ {class: "q", text: "They will not labor in vain"}
              , {class: "q2", text: "nor give birth for calamity;"}
              , {class: "q", text: "for they are the offspring of Yahweh’s blessed"}
              , {class: "q2", text: "and their descendants with them."}
              ]
        , 24: [ {class: "q", text: "It will happen that before they call, I will answer;"}
              , {class: "q2", text: "and while they are yet speaking, I will hear."}
              ]
        , 25: [ {class: "q", text: "The wolf and the lamb will feed together."}
              , {class: "q2", text: "The lion will eat straw like the ox."}
              , {class: "q2", text: "Dust will be the serpent’s food."}
              , {class: "q", text: "They will not hurt nor destroy in all my holy mountain,”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        }
  , 66: { 1:  [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“Heaven is my throne, and the earth is my footstool."}
              , {class: "q2", text: "What kind of house will you build to me?"}
              , {class: "q2", text: "Where will I rest?"}
              ]
        , 2:  [ {class: "q", text: "For my hand has made all these things,"}
              , {class: "q2", text: "and so all these things came to be,” says Yahweh:"}
              , {class: "q", text: "“but I will look to this man,"}
              , {class: "q2", text: "even to he who is poor and of a contrite spirit,"}
              , {class: "q2", text: "and who trembles at my word."}
              ]
        , 3:  [ {class: "q", text: "He who kills an ox is as he who kills a man;"}
              , {class: "q2", text: "he who sacrifices a lamb, as he who breaks a dog’s neck;"}
              , {class: "q2", text: "he who offers an offering, as he who offers pig’s blood;"}
              , {class: "q2", text: "he who burns frankincense, as he who blesses an idol."}
              , {class: "q", text: "Yes, they have chosen their own ways,"}
              , {class: "q2", text: "and their soul delights in their abominations:"}
              ]
        , 4:  [ {class: "q", text: "I also will choose their delusions,"}
              , {class: "q2", text: "and will bring their fears on them;"}
              , {class: "q", text: "because when I called, no one answered;"}
              , {class: "q2", text: "when I spoke, they didn’t listen;"}
              , {class: "q", text: "but they did that which was evil in my eyes,"}
              , {class: "q2", text: "and chose that in which I didn’t delight.”"}
              ]
        , 5:  [ {class: "q", text: "Hear Yahweh’s word,"}
              , {class: "q2", text: "you who tremble at his word:"}
              , {class: "q", text: "“Your brothers who hate you,"}
              , {class: "q2", text: "who cast you out for my name’s sake, have said,"}
              , {class: "q", text: "‘Let Yahweh be glorified,"}
              , {class: "q2", text: "that we may see your joy;’"}
              , {class: "q2", text: "but it is those who shall be disappointed."}
              ]
        , 6:  [ {class: "q", text: "A voice of tumult from the city,"}
              , {class: "q2", text: "a voice from the temple,"}
              , {class: "q2", text: "a voice of Yahweh that repays his enemies what they deserve."}
              ]
        , 7:  [ {class: "q", text: "“Before she travailed, she gave birth."}
              , {class: "q2", text: "Before her pain came, she delivered a son."}
              ]
        , 8:  [ {class: "q", text: "Who has heard of such a thing?"}
              , {class: "q2", text: "Who has seen such things?"}
              , {class: "q", text: "Shall a land be born in one day?"}
              , {class: "q2", text: "Shall a nation be born at once?"}
              , {class: "q", text: "For as soon as Zion travailed,"}
              , {class: "q2", text: "she gave birth to her children."}
              ]
        , 9:  [ {class: "q", text: "Shall I bring to the birth, and not cause to be delivered?” says Yahweh."}
              , {class: "q2", text: "“Shall I who cause to give birth shut the womb?” says your God."}
              ]
        , 10: [ {class: "q", text: "“Rejoice with Jerusalem, and be glad for her, all you who love her."}
              , {class: "q2", text: "Rejoice for joy with her, all you who mourn over her;"}
              ]
        , 11: [ {class: "q", text: "that you may nurse and be satisfied at the comforting breasts;"}
              , {class: "q2", text: "that you may drink deeply,"}
              , {class: "q2", text: "and be delighted with the abundance of her glory.”"}
              ]
        , 12: [ {class: "q", text: "For Yahweh says, “Behold, I will extend peace to her like a river,"}
              , {class: "q2", text: "and the glory of the nations like an overflowing stream;"}
              , {class: "q2", text: "and you will nurse."}
              , {class: "q", text: "You will be carried on her side,"}
              , {class: "q2", text: "and will be dandled on her knees."}
              ]
        , 13: [ {class: "q", text: "As one whom his mother comforts,"}
              , {class: "q2", text: "so I will comfort you."}
              , {class: "q2", text: "You will be comforted in Jerusalem.”"}
              ]
        , 14: [ {class: "q", text: "You will see it, and your heart shall rejoice,"}
              , {class: "q2", text: "and your bones will flourish like the tender grass."}
              , {class: "q", text: "Yahweh’s hand will be known among his servants;"}
              , {class: "q2", text: "and he will have indignation against his enemies."}
              ]
        , 15: [ {class: "q", text: "For, behold, Yahweh will come with fire,"}
              , {class: "q2", text: "and his chariots will be like the whirlwind;"}
              , {class: "q", text: "to render his anger with fierceness,"}
              , {class: "q2", text: "and his rebuke with flames of fire."}
              ]
        , 16: [ {class: "q", text: "For Yahweh will execute judgment by fire and by his sword on all flesh;"}
              , {class: "q2", text: "and those slain by Yahweh will be many."}
              ]
        , 17: [ {class: "vs", text: "“Those who sanctify themselves and purify themselves to go to the gardens, behind one in the middle, eating pig’s meat, abominable things, and the mouse, they shall come to an end together,” says Yahweh."} ]
        , 18: [ {class: "vs", text: "“For I know their works and their thoughts. The time comes that I will gather all nations and languages, and they will come, and will see my glory."} ]
        , 19: [ {class: "vs", text: "“I will set a sign among them, and I will send those who escape of them to the nations, to Tarshish, Pul, and Lud, who draw the bow, to Tubal and Javan, to far-away islands, who have not heard my fame, nor have seen my glory; and they shall declare my glory among the nations."} ]
        , 20: [ {class: "vs", text: "They shall bring all your brothers out of all the nations for an offering to Yahweh, on horses, in chariots, in litters, on mules, and on camels, to my holy mountain Jerusalem, says Yahweh, as the children of Israel bring their offering in a clean vessel into Yahweh’s house."} ]
        , 21: [ {class: "vs", text: "Of them I will also select priests and Levites,” says Yahweh."} ]
        , 22: [ {class: "vs", text: "“For as the new heavens and the new earth, which I will make, shall remain before me,” says Yahweh, “so your offspring and your name shall remain."} ]
        , 23: [ {class: "vs", text: "It shall happen that from one new moon to another, and from one Sabbath to another, all flesh will come to worship before me,” says Yahweh."} ]
        , 24: [ {class: "vs", text: "“They will go out, and look at the dead bodies of the men who have transgressed against me; for their worm will not die, nor will their fire be quenched, and they will be loathsome to all mankind.”"} ]
        }
  , 7:  { 1:  [ {class: "vs", text: "In the days of Ahaz the son of Jotham, the son of Uzziah, king of Judah, Rezin the king of Syria, and Pekah the son of Remaliah, king of Israel, went up to Jerusalem to war against it, but could not prevail against it."} ]
        , 2:  [ {class: "vs", text: "David’s house was told, “Syria is allied with Ephraim.” His heart trembled, and the heart of his people, as the trees of the forest tremble with the wind."} ]
        , 3:  [ {class: "vs", text: "Then Yahweh said to Isaiah, “Go out now to meet Ahaz, you, and Shearjashub your son, at the end of the conduit of the upper pool, on the highway of the fuller’s field."} ]
        , 4:  [ {class: "vs", text: "Tell him, ‘Be careful, and keep calm. Don’t be afraid, neither let your heart be faint because of these two tails of smoking torches, for the fierce anger of Rezin and Syria, and of the son of Remaliah."} ]
        , 5:  [ {class: "vs", text: "Because Syria, Ephraim, and the son of Remaliah, have plotted evil against you, saying,"} ]
        , 6:  [ {class: "vs", text: "“Let’s go up against Judah, and tear it apart, and let’s divide it among ourselves, and set up a king within it, even the son of Tabeel.”"} ]
        , 7:  [ {class: "vs", text: "This is what the Lord Yahweh says: “It shall not stand, neither shall it happen.”"} ]
        , 8:  [ {class: "vs", text: "For the head of Syria is Damascus, and the head of Damascus is Rezin. Within sixty-five years Ephraim shall be broken in pieces, so that it shall not be a people."} ]
        , 9:  [ {class: "vs", text: "The head of Ephraim is Samaria, and the head of Samaria is Remaliah’s son. If you will not believe, surely you shall not be established.’”"} ]
        , 10: [ {class: "vs", text: "Yahweh spoke again to Ahaz, saying,"} ]
        , 11: [ {class: "vs", text: "“Ask a sign of Yahweh your God; ask it either in the depth, or in the height above.”"} ]
        , 12: [ {class: "vs", text: "But Ahaz said, “I won’t ask. I won’t tempt Yahweh.”"} ]
        , 13: [ {class: "vs", text: "He said, “Listen now, house of David. Is it not enough for you to try the patience of men, that you will try the patience of my God also?"} ]
        , 14: [ {class: "vs", text: "Therefore the Lord himself will give you a sign. Behold, the virgin will conceive, and bear a son, and shall call his name Immanuel."} ]
        , 15: [ {class: "vs", text: "He shall eat butter and honey when he knows to refuse the evil and choose the good."} ]
        , 16: [ {class: "vs", text: "For before the child knows to refuse the evil and choose the good, the land whose two kings you abhor shall be forsaken."} ]
        , 17: [ {class: "vs", text: "Yahweh will bring on you, on your people, and on your father’s house, days that have not come, from the day that Ephraim departed from Judah, even the king of Assyria."} ]
        , 18: [ {class: "vs", text: "It will happen in that day that Yahweh will whistle for the fly that is in the uttermost part of the rivers of Egypt, and for the bee that is in the land of Assyria."} ]
        , 19: [ {class: "vs", text: "They shall come, and shall all rest in the desolate valleys, in the clefts of the rocks, on all thorn hedges, and on all pastures."} ]
        , 20: [ {class: "vs", text: "In that day the Lord will shave with a razor that is hired in the parts beyond the River, even with the king of Assyria, the head and the hair of the feet; and it shall also consume the beard."} ]
        , 21: [ {class: "vs", text: "It shall happen in that day that a man shall keep alive a young cow, and two sheep."} ]
        , 22: [ {class: "vs", text: "It shall happen, that because of the abundance of milk which they shall give he shall eat butter; for everyone will eat butter and honey that is left within the land."} ]
        , 23: [ {class: "vs", text: "It will happen in that day that every place where there were a thousand vines at a thousand silver shekels, shall be for briers and thorns."} ]
        , 24: [ {class: "vs", text: "People will go there with arrows and with bow, because all the land will be briers and thorns."} ]
        , 25: [ {class: "vs", text: "All the hills that were cultivated with the hoe, you shall not come there for fear of briers and thorns; but it shall be for the sending out of oxen, and for sheep to tread on.”"} ]
        }
  , 8:  { 1:  [ {class: "vs", text: "Yahweh said to me, “Take a large tablet, and write on it with a man’s pen, ‘For Maher Shalal Hash Baz’;"} ]
        , 2:  [ {class: "vs", text: "and I will take for myself faithful witnesses to testify: Uriah the priest, and Zechariah the son of Jeberechiah.”"} ]
        , 3:  [ {class: "vs", text: "I went to the prophetess, and she conceived, and bore a son. Then Yahweh said to me, “Call his name ‘Maher Shalal Hash Baz.’"} ]
        , 4:  [ {class: "vs", text: "For before the child knows how to say, ‘My father,’ and, ‘My mother,’ the riches of Damascus and the plunder of Samaria will be carried away by the king of Assyria.”"} ]
        , 5:  [ {class: "vs", text: "Yahweh spoke to me yet again, saying,"} ]
        , 6:  [ {class: "vs", text: "“Because this people has refused the waters of Shiloah that go softly, and rejoice in Rezin and Remaliah’s son;"} ]
        , 7:  [ {class: "vs", text: "now therefore, behold, the Lord brings upon them the mighty flood waters of the River: the king of Assyria and all his glory. It will come up over all its channels, and go over all its banks."} ]
        , 8:  [ {class: "vs", text: "It will sweep onward into Judah. It will overflow and pass through. It will reach even to the neck. The stretching out of its wings will fill the width of your land, Immanuel."} ]
        , 9:  [ {class: "vs", text: "Make an uproar, you peoples, and be broken in pieces! Listen, all you from far countries: dress for battle, and be shattered! Dress for battle, and be shattered!"} ]
        , 10: [ {class: "vs", text: "Take counsel together, and it will be brought to nothing; speak the word, and it will not stand, for God is with us.”"} ]
        , 11: [ {class: "vs", text: "For Yahweh spoke this to me with a strong hand, and instructed me not to walk in the way of this people, saying,"} ]
        , 12: [ {class: "vs", text: "“Don’t say, ‘A conspiracy!’ concerning all about which this people say, ‘A conspiracy!’ neither fear their threats, nor be terrorized."} ]
        , 13: [ {class: "vs", text: "Yahweh of Armies is who you must respect as holy. He is the one you must fear. He is the one you must dread."} ]
        , 14: [ {class: "vs", text: "He will be a sanctuary, but for both houses of Israel, he will be a stumbling stone and a rock that makes them fall. For the people of Jerusalem, he will be a trap and a snare."} ]
        , 15: [ {class: "vs", text: "Many will stumble over it, fall, be broken, be snared, and be captured.”"} ]
        , 16: [ {class: "vs", text: "Wrap up the covenant. Seal the law among my disciples."} ]
        , 17: [ {class: "vs", text: "I will wait for Yahweh, who hides his face from the house of Jacob, and I will look for him."} ]
        , 18: [ {class: "vs", text: "Behold, I and the children whom Yahweh has given me are for signs and for wonders in Israel from Yahweh of Armies, who dwells in Mount Zion."} ]
        , 19: [ {class: "vs", text: "When they tell you, “Consult with those who have familiar spirits and with the wizards, who chirp and who mutter,” shouldn’t a people consult with their God? Should they consult the dead on behalf of the living?"} ]
        , 20: [ {class: "vs", text: "Turn to the law and to the covenant! If they don’t speak according to this word, surely there is no morning for them."} ]
        , 21: [ {class: "vs", text: "They will pass through it, very distressed and hungry. It will happen that when they are hungry, they will worry, and curse by their king and by their God. They will turn their faces upward,"} ]
        , 22: [ {class: "vs", text: "and look to the earth, and see distress, darkness, and the gloom of anguish. They will be driven into thick darkness."} ]
        }
  , 9:  { 1:  [ {class: "vs", text: "But there shall be no more gloom for her who was in anguish. In the former time, he brought into contempt the land of Zebulun and the land of Naphtali; but in the latter time he has made it glorious, by the way of the sea, beyond the Jordan, Galilee of the nations."} ]
        , 2:  [ {class: "endpara", text: "The people who walked in darkness have seen a great light."}
              , {class: "q2", text: "The light has shined on those who lived in the land of the shadow of death."}
              ]
        , 3:  [ {class: "q", text: "You have multiplied the nation."}
              , {class: "q2", text: "You have increased their joy.</div><p> They rejoice before you according to the joy in harvest, as men rejoice when they divide the plunder."}
              ]
        , 4:  [ {class: "vs", text: "For the yoke of his burden, and the staff of his shoulder, the rod of his oppressor, you have broken as in the day of Midian."} ]
        , 5:  [ {class: "vs", text: "For all the armor of the armed man in the noisy battle, and the garments rolled in blood, will be for burning, fuel for the fire."} ]
        , 6:  [ {class: "vs", text: "For a child is born to us. A son is given to us; and the government will be on his shoulders. His name will be called Wonderful, Counselor, Mighty God, Everlasting Father, Prince of Peace."} ]
        , 7:  [ {class: "vs", text: "Of the increase of his government and of peace there shall be no end, on David’s throne, and on his kingdom, to establish it, and to uphold it with justice and with righteousness from that time on, even forever. The zeal of Yahweh of Armies will perform this."} ]
        , 8:  [ {class: "endpara", text: "The Lord sent a word into Jacob,"}
              , {class: "q2", text: "and it falls on Israel."}
              ]
        , 9:  [ {class: "q", text: "All the people will know,"}
              , {class: "q2", text: "including Ephraim and the inhabitants of Samaria, who say in pride and in arrogance of heart,"}
              ]
        , 10: [ {class: "q", text: "“The bricks have fallen,"}
              , {class: "q2", text: "but we will build with cut stone."}
              , {class: "q", text: "The sycamore fig trees have been cut down,"}
              , {class: "q2", text: "but we will put cedars in their place.”"}
              ]
        , 11: [ {class: "q", text: "Therefore Yahweh will set up on high against him the adversaries of Rezin,"}
              , {class: "q2", text: "and will stir up his enemies,"}
              ]
        , 12: [ {class: "q2", text: "The Syrians in front,"}
              , {class: "q2", text: "and the Philistines behind;"}
              , {class: "q2", text: "and they will devour Israel with open mouth."}
              , {class: "q", text: "For all this, his anger is not turned away,"}
              , {class: "q2", text: "but his hand is stretched out still."}
              ]
        , 13: [ {class: "q", text: "Yet the people have not turned to him who struck them,"}
              , {class: "q2", text: "neither have they sought Yahweh of Armies."}
              ]
        , 14: [ {class: "q", text: "Therefore Yahweh will cut off from Israel head and tail,"}
              , {class: "q2", text: "palm branch and reed, in one day."}
              ]
        , 15: [ {class: "q", text: "The elder and the honorable man is the head,"}
              , {class: "q2", text: "and the prophet who teaches lies is the tail."}
              ]
        , 16: [ {class: "q", text: "For those who lead this people lead them astray;"}
              , {class: "q2", text: "and those who are led by them are destroyed."}
              ]
        , 17: [ {class: "q", text: "Therefore the Lord will not rejoice over their young men,"}
              , {class: "q2", text: "neither will he have compassion on their fatherless and widows;"}
              , {class: "q", text: "for everyone is profane and an evildoer,"}
              , {class: "q2", text: "and every mouth speaks folly."}
              , {class: "q", text: "For all this his anger is not turned away,"}
              , {class: "q2", text: "but his hand is stretched out still."}
              ]
        , 18: [ {class: "q", text: "For wickedness burns like a fire."}
              , {class: "q2", text: "It devours the briers and thorns;"}
              , {class: "q2", text: "yes, it kindles in the thickets of the forest,"}
              , {class: "q2", text: "and they roll upward in a column of smoke."}
              ]
        , 19: [ {class: "q", text: "Through Yahweh of Armies’ wrath, the land is burned up;"}
              , {class: "q2", text: "and the people are the fuel for the fire."}
              , {class: "q2", text: "No one spares his brother."}
              ]
        , 20: [ {class: "q", text: "One will devour on the right hand, and be hungry;"}
              , {class: "q2", text: "and he will eat on the left hand, and they will not be satisfied."}
              , {class: "q", text: "Everyone will eat the flesh of his own arm:"}
              ]
        , 21: [ {class: "q2", text: "Manasseh, Ephraim; and Ephraim, Manasseh; and they together shall be against Judah."}
              , {class: "q", text: "For all this his anger is not turned away,"}
              , {class: "q2", text: "but his hand is stretched out still."}
              ]
        }
  }