exports.jeremiah =
  { 1:  { 1:  [ {class: "vs", text: "The words of Jeremiah the son of Hilkiah, one of the priests who were in Anathoth in the land of Benjamin."} ]
        , 2:  [ {class: "vs", text: "Yahweh’s word came to him in the days of Josiah the son of Amon, king of Judah, in the thirteenth year of his reign."} ]
        , 3:  [ {class: "vs", text: "It came also in the days of Jehoiakim the son of Josiah, king of Judah, to the end of the eleventh year of Zedekiah, the son of Josiah, king of Judah, to the carrying away of Jerusalem captive in the fifth month."} ]
        , 4:  [ {class: "vs", text: "Now Yahweh’s word came to me, saying,"} ]
        , 5:  [ {class: "q", text: "“Before I formed you in the womb, I knew you."}
              , {class: "q2", text: "Before you were born, I sanctified you."}
              , {class: "q2", text: "I have appointed you a prophet to the nations.”"}
              ]
        , 6:  [ {class: "vs", text: "Then I said, “Ah, Lord Yahweh! Behold, I don’t know how to speak; for I am a child.”"} ]
        , 7:  [ {class: "vs", text: "But Yahweh said to me, “Don’t say, ‘I am a child;’ for you must go to whomever I send you, and you must say whatever I command you."} ]
        , 8:  [ {class: "vs", text: "Don’t be afraid because of them, for I am with you to rescue you,” says Yahweh."} ]
        , 9:  [ {class: "vs", text: "Then Yahweh stretched out his hand, and touched my mouth. Then Yahweh said to me, “Behold, I have put my words in your mouth."} ]
        , 10: [ {class: "vs", text: "Behold, I have today set you over the nations and over the kingdoms, to uproot and to tear down, to destroy and to overthrow, to build and to plant.”"} ]
        , 11: [ {class: "vs", text: "Moreover Yahweh’s word came to me, saying, “Jeremiah, what do you see?”</p><p> I said, “I see a branch of an almond tree.”"} ]
        , 12: [ {class: "vs", text: "Then Yahweh said to me, “You have seen well; for I watch over my word to perform it.”"} ]
        , 13: [ {class: "vs", text: "Yahweh’s word came to me the second time, saying, “What do you see?”</p><p> I said, “I see a boiling cauldron; and it is tipping away from the north.”"} ]
        , 14: [ {class: "vs", text: "Then Yahweh said to me, “Out of the north, evil will break out on all the inhabitants of the land."} ]
        , 15: [ {class: "endpara", text: "For, behold, I will call all the families of the kingdoms of the north,” says Yahweh."}
              , {class: "q", text: "“They will come, and they will each set his throne at the entrance of the gates of Jerusalem,"}
              , {class: "q2", text: "and against all its walls all around, and against all the cities of Judah."}
              ]
        , 16: [ {class: "q", text: "I will utter my judgments against them concerning all their wickedness,"}
              , {class: "q2", text: "in that they have forsaken me,"}
              , {class: "q2", text: "and have burned incense to other gods,"}
              , {class: "q2", text: "and worshiped the works of their own hands."}
              ]
        , 17: [ {class: "vs", text: "“You therefore put your belt on your waist, arise, and say to them all that I command you. Don’t be dismayed at them, lest I dismay you before them."} ]
        , 18: [ {class: "vs", text: "For, behold, I have made you today a fortified city, an iron pillar, and bronze walls against the whole land, against the kings of Judah, against its princes, against its priests, and against the people of the land."} ]
        , 19: [ {class: "vs", text: "They will fight against you, but they will not prevail against you; for I am with you”, says Yahweh, “to rescue you.”"} ]
        }
  , 10: { 1:  [ {class: "vs", text: "Hear the word which Yahweh speaks to you, house of Israel!"} ]
        , 2:  [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“Don’t learn the way of the nations,"}
              , {class: "q2", text: "and don’t be dismayed at the signs of the sky;"}
              , {class: "q2", text: "for the nations are dismayed at them."}
              ]
        , 3:  [ {class: "q", text: "For the customs of the peoples are vanity;"}
              ,  {class: "q2", text: "for one cuts a tree out of the forest,"}
              , {class: "q2", text: "the work of the hands of the workman with the ax."}
              ]
        , 4:  [ {class: "q", text: "They deck it with silver and with gold."}
              , {class: "q2", text: "They fasten it with nails and with hammers,"}
              , {class: "q2", text: "so that it can’t move."}
              ]
        , 5:  [ {class: "q", text: "They are like a palm tree, of turned work,"}
              , {class: "q2", text: "and don’t speak."}
              , {class: "q", text: "They must be carried,"}
              , {class: "q2", text: "because they can’t move."}
              , {class: "q", text: "Don’t be afraid of them;"}
              , {class: "q2", text: "for they can’t do evil,"}
              , {class: "q2", text: "neither is it in them to do good.”"}
              ]
        , 6:  [ {class: "q", text: "There is no one like you, Yahweh."}
              , {class: "q2", text: "You are great,"}
              , {class: "q2", text: "and your name is great in might."}
              ]
        , 7:  [ {class: "q", text: "Who shouldn’t fear you,"}
              , {class: "q2", text: "King of the nations?"}
              , {class: "q2", text: "For it belongs to you."}
              , {class: "q", text: "Because among all the wise men of the nations,"}
              , {class: "q2", text: "and in all their royal estate,"}
              , {class: "q2", text: "there is no one like you."}
              ]
        , 8:  [ {class: "q", text: "But they are together brutish and foolish,"}
              , {class: "q2", text: "instructed by idols!"}
              , {class: "q2", text: "It is just wood."}
              ]
        , 9:  [ {class: "q", text: "There is silver beaten into plates, which is brought from Tarshish,"}
              , {class: "q2", text: "and gold from Uphaz,"}
              , {class: "q2", text: "the work of the engraver and of the hands of the goldsmith."}
              , {class: "q", text: "Their clothing is blue and purple."}
              , {class: "q2", text: "They are all the work of skillful men."}
              ]
        , 10: [ {class: "q", text: "But Yahweh is the true God."}
              , {class: "q2", text: "He is the living God,"}
              , {class: "q2", text: "and an everlasting King."}
              , {class: "q", text: "At his wrath, the earth trembles."}
              , {class: "q2", text: "The nations aren’t able to withstand his indignation."}
              ]
        , 11: [ {class: "vs", text: "“You shall say this to them: ‘The gods that have not made the heavens and the earth will perish from the earth, and from under the heavens.’”"} ]
        , 12: [ {class: "q", text: "God has made the earth by his power."}
              , {class: "q2", text: "He has established the world by his wisdom,"}
              , {class: "q2", text: "and by his understanding has he stretched out the heavens."}
              ]
        , 13: [ {class: "q", text: "When he utters his voice,"}
              , {class: "q2", text: "the waters in the heavens roar,"}
              , {class: "q2", text: "and he causes the vapors to ascend from the ends of the earth."}
              , {class: "q", text: "He makes lightnings for the rain,"}
              , {class: "q2", text: "and brings the wind out of his treasuries."}
              ]
        , 14: [ {class: "q", text: "Every man has become brutish and without knowledge."}
              , {class: "q2", text: "Every goldsmith is disappointed by his engraved image;"}
              , {class: "q", text: "for his molten image is falsehood,"}
              , {class: "q2", text: "and there is no breath in them."}
              ]
        , 15: [ {class: "q", text: "They are vanity, a work of delusion."}
              , {class: "q2", text: "In the time of their visitation they will perish."}
              ]
        , 16: [ {class: "q", text: "The portion of Jacob is not like these;"}
              , {class: "q2", text: "for he is the maker of all things;"}
              , {class: "q", text: "and Israel is the tribe of his inheritance:"}
              , {class: "q2", text: "Yahweh of Armies is his name."}
              ]
        , 17: [ {class: "q", text: "Gather up your wares out of the land,"}
              , {class: "q2", text: "you who live under siege."}
              ]
        , 18: [ {class: "q", text: "For Yahweh says,"}
              , {class: "q2", text: "“Behold, I will sling out the inhabitants of the land at this time,"}
              , {class: "q2", text: "and will distress them, that they may feel it.”"}
              ]
        , 19: [ {class: "q", text: "Woe is me because of my injury!"}
              , {class: "q2", text: "My wound is serious;"}
              , {class: "q", text: "but I said,"}
              , {class: "q2", text: "“Truly this is my grief, and I must bear it.”"}
              ]
        , 20: [ {class: "q", text: "My tent has been destroyed,"}
              , {class: "q2", text: "and all my cords are broken."}
              , {class: "q", text: "My children have gone away from me, and they are no more."}
              , {class: "q2", text: "There is no one to spread my tent any more,"}
              , {class: "q2", text: "to set up my curtains."}
              ]
        , 21: [ {class: "q", text: "For the shepherds have become brutish,"}
              , {class: "q2", text: "and have not inquired of Yahweh."}
              , {class: "q", text: "Therefore they have not prospered,"}
              , {class: "q2", text: "and all their flocks have scattered."}
              ]
        , 22: [ {class: "q", text: "The voice of news, behold, it comes,"}
              , {class: "q2", text: "and a great commotion out of the north country,"}
              , {class: "q", text: "to make the cities of Judah a desolation,"}
              , {class: "q2", text: "a dwelling place of jackals."}
              ]
        , 23: [ {class: "q", text: "Yahweh, I know that the way of man is not in himself."}
              , {class: "q2", text: "It is not in man who walks to direct his steps."}
              ]
        , 24: [ {class: "q", text: "Yahweh, correct me, but gently;"}
              , {class: "q2", text: "not in your anger,"}
              , {class: "q2", text: "lest you reduce me to nothing."}
              ]
        , 25: [ {class: "q", text: "Pour out your wrath on the nations that don’t know you,"}
              , {class: "q2", text: "and on the families that don’t call on your name;"}
              , {class: "q", text: "for they have devoured Jacob."}
              , {class: "q2", text: "Yes, they have devoured him, consumed him,"}
              , {class: "q2", text: "and have laid waste his habitation."}
              ]
        }
  , 11: { 1:  [ {class: "vs", text: "The word that came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "“Hear the words of this covenant, and speak to the men of Judah, and to the inhabitants of Jerusalem;"} ]
        , 3:  [ {class: "vs", text: "and say to them, Yahweh, the God of Israel says: ‘Cursed is the man who doesn’t hear the words of this covenant,"} ]
        , 4:  [ {class: "vs", text: "which I commanded your fathers in the day that I brought them out of the land of Egypt, out of the iron furnace,’ saying, ‘Obey my voice, and do them, according to all which I command you; so you shall be my people, and I will be your God;"} ]
        , 5:  [ {class: "vs", text: "that I may establish the oath which I swore to your fathers, to give them a land flowing with milk and honey,’ as it is today.”</p><p> Then answered I, and said, “Amen, Yahweh.”"} ]
        , 6:  [ {class: "vs", text: "Yahweh said to me, “Proclaim all these words in the cities of Judah, and in the streets of Jerusalem, saying, ‘Hear the words of this covenant, and do them."} ]
        , 7:  [ {class: "vs", text: "For I earnestly protested to your fathers in the day that I brought them up out of the land of Egypt, even to this day, rising early and protesting, saying, “Obey my voice.”"} ]
        , 8:  [ {class: "vs", text: "Yet they didn’t obey, nor turn their ear, but everyone walked in the stubbornness of their evil heart. Therefore I brought on them all the words of this covenant, which I commanded them to do, but they didn’t do them.’”"} ]
        , 9:  [ {class: "vs", text: "Yahweh said to me, “A conspiracy is found among the men of Judah, and among the inhabitants of Jerusalem."} ]
        , 10: [ {class: "vs", text: "They have turned back to the iniquities of their forefathers, who refused to hear my words. They have gone after other gods to serve them. The house of Israel and the house of Judah have broken my covenant which I made with their fathers."} ]
        , 11: [ {class: "vs", text: "Therefore Yahweh says, ‘Behold, I will bring evil on them, which they will not be able to escape; and they will cry to me, but I will not listen to them."} ]
        , 12: [ {class: "vs", text: "Then the cities of Judah and the inhabitants of Jerusalem will go and cry to the gods to which they offer incense, but they will not save them at all in the time of their trouble."} ]
        , 13: [ {class: "vs", text: "For according to the number of your cities are your gods, Judah; and according to the number of the streets of Jerusalem have you set up altars to the shameful thing, even altars to burn incense to Baal.’"} ]
        , 14: [ {class: "vs", text: "“Therefore don’t pray for this people. Don’t lift up cry or prayer for them; for I will not hear them in the time that they cry to me because of their trouble."} ]
        , 15: [ {class: "q", text: "What has my beloved to do in my house,"}
              , {class: "q2", text: "since she has behaved lewdly with many,"}
              , {class: "q2", text: "and the holy flesh has passed from you?"}
              , {class: "q", text: "When you do evil,"}
              , {class: "q2", text: "then you rejoice.”"}
              ]
        , 16: [ {class: "q", text: "Yahweh called your name, “A green olive tree,"}
              , {class: "q2", text: "beautiful with goodly fruit.”"}
              , {class: "q", text: "With the noise of a great roar he has kindled fire on it,"}
              , {class: "q2", text: "and its branches are broken."}
              ]
        , 17: [ {class: "vs", text: "For Yahweh of Armies, who planted you, has pronounced evil against you, because of the evil of the house of Israel and of the house of Judah, which they have done to themselves in provoking me to anger by offering incense to Baal."} ]
        , 18: [ {class: "vs", text: "Yahweh gave me knowledge of it, and I knew it. Then you showed me their doings."} ]
        , 19: [ {class: "endpara", text: "But I was like a gentle lamb that is led to the slaughter. I didn’t know that they had devised plans against me, saying,"}
              , {class: "q", text: "“Let’s destroy the tree with its fruit,"}
              , {class: "q2", text: "and let’s cut him off from the land of the living,"}
              , {class: "q2", text: "that his name may be no more remembered.”"}
              ]
        , 20: [ {class: "q", text: "But, Yahweh of Armies, who judges righteously,"}
              , {class: "q2", text: "who tests the heart and the mind,"}
              , {class: "q", text: "I will see your vengeance on them;"}
              , {class: "q2", text: "for to you I have revealed my cause."}
              ]
        , 21: [ {class: "vs", text: "“Therefore Yahweh says concerning the men of Anathoth, who seek your life, saying, ‘You shall not prophesy in Yahweh’s name, that you not die by our hand;’"} ]
        , 22: [ {class: "vs", text: "therefore Yahweh of Armies says, ‘Behold, I will punish them. The young men will die by the sword. Their sons and their daughters will die by famine."} ]
        , 23: [ {class: "vs", text: "There will be no remnant to them, for I will bring evil on the men of Anathoth, even the year of their visitation.’”"} ]
        }
  , 12: { 1:  [ {class: "q", text: "You are righteous, Yahweh,"}
              , {class: "q2", text: "when I contend with you;"}
              , {class: "q", text: "yet I would like to reason the cause with you."}
              , {class: "q2", text: "Why does the way of the wicked prosper?"}
              , {class: "q2", text: "Why are they all at ease who deal very treacherously?"}
              ]
        , 2:  [ {class: "q", text: "You have planted them. Yes, they have taken root."}
              , {class: "q2", text: "They grow. Yes, they produce fruit."}
              , {class: "q", text: "You are near in their mouth,"}
              , {class: "q2", text: "and far from their heart."}
              ]
        , 3:  [ {class: "q", text: "But you, Yahweh, know me."}
              , {class: "q2", text: "You see me, and test my heart toward you."}
              , {class: "q", text: "Pull them out like sheep for the slaughter,"}
              , {class: "q2", text: "and prepare them for the day of slaughter."}
              ]
        , 4:  [ {class: "q", text: "How long will the land mourn,"}
              , {class: "q2", text: "and the herbs of the whole country wither?"}
              , {class: "q", text: "Because of the wickedness of those who dwell therein,"}
              , {class: "q2", text: "the animals and birds are consumed;"}
              , {class: "q", text: "because they said,"}
              , {class: "q2", text: "“He won’t see our latter end.”"}
              ]
        , 5:  [ {class: "q", text: "“If you have run with the footmen,"}
              , {class: "q2", text: "and they have wearied you,"}
              , {class: "q2", text: "then how can you contend with horses?"}
              , {class: "q", text: "Though in a land of peace you are secure,"}
              , {class: "q2", text: "yet how will you do in the pride of the Jordan?"}
              ]
        , 6:  [ {class: "q", text: "For even your brothers, and the house of your father,"}
              , {class: "q2", text: "even they have dealt treacherously with you!"}
              , {class: "q2", text: "Even they have cried aloud after you!"}
              , {class: "q", text: "Don’t believe them,"}
              , {class: "q2", text: "though they speak beautiful words to you."}
              ]
        , 7:  [ {class: "q", text: "“I have forsaken my house."}
              , {class: "q2", text: "I have cast off my heritage."}
              , {class: "q2", text: "I have given the dearly beloved of my soul into the hand of her enemies."}
              ]
        , 8:  [ {class: "q", text: "My heritage has become to me as a lion in the forest."}
              , {class: "q2", text: "She has uttered her voice against me."}
              , {class: "q2", text: "Therefore I have hated her."}
              ]
        , 9:  [ {class: "q", text: "Is my heritage to me as a speckled bird of prey?"}
              , {class: "q2", text: "Are the birds of prey against her all around?"}
              , {class: "q", text: "Go, assemble all the animals of the field."}
              , {class: "q2", text: "Bring them to devour."}
              ]
        , 10: [ {class: "q", text: "Many shepherds have destroyed my vineyard."}
              , {class: "q2", text: "They have trodden my portion under foot."}
              , {class: "q2", text: "They have made my pleasant portion a desolate wilderness."}
              ]
        , 11: [ {class: "q", text: "They have made it a desolation."}
              , {class: "q2", text: "It mourns to me, being desolate."}
              , {class: "q", text: "The whole land is made desolate,"}
              , {class: "q2", text: "because no one cares."}
              ]
        , 12: [ {class: "q", text: "Destroyers have come on all the bare heights in the wilderness;"}
              , {class: "q2", text: "for the sword of Yahweh devours from the one end of the land even to the other end of the land."}
              , {class: "q2", text: "No flesh has peace."}
              ]
        , 13: [ {class: "q", text: "They have sown wheat,"}
              , {class: "q2", text: "and have reaped thorns."}
              , {class: "q", text: "They have exhausted themselves,"}
              , {class: "q2", text: "and profit nothing."}
              , {class: "q", text: "You will be ashamed of your fruits,"}
              , {class: "q2", text: "because of Yahweh’s fierce anger.”"}
              ]
        , 14: [ {class: "vs", text: "Yahweh says, “Concerning all my evil neighbors, who touch the inheritance which I have caused my people Israel to inherit: Behold, I will pluck them up from off their land, and will pluck up the house of Judah from among them."} ]
        , 15: [ {class: "vs", text: "It will happen, that after I have plucked them up, I will return and have compassion on them. I will bring them again, every man to his heritage, and every man to his land."} ]
        , 16: [ {class: "vs", text: "It will happen, if they will diligently learn the ways of my people, to swear by my name, ‘As Yahweh lives;’ even as they taught my people to swear by Baal; then they will be built up in the middle of my people."} ]
        , 17: [ {class: "vs", text: "But if they will not hear, then I will pluck up that nation, plucking up and destroying it,” says Yahweh."} ]
        }
  , 13: { 1:  [ {class: "vs", text: "Yahweh said to me, “Go, and buy yourself a linen belt, and put it on your waist, and don’t put it in water.”"} ]
        , 2:  [ {class: "vs", text: "So I bought a belt according to Yahweh’s word, and put it on my waist."} ]
        , 3:  [ {class: "vs", text: "Yahweh’s word came to me the second time, saying,"} ]
        , 4:  [ {class: "vs", text: "“Take the belt that you have bought, which is on your waist, and arise, go to the Euphrates, and hide it there in a cleft of the rock.”"} ]
        , 5:  [ {class: "vs", text: "So I went, and hid it by the Euphrates, as Yahweh commanded me."} ]
        , 6:  [ {class: "vs", text: "After many days, Yahweh said to me, “Arise, go to the Euphrates, and take the belt from there, which I commanded you to hide there.”"} ]
        , 7:  [ {class: "vs", text: "Then I went to the Euphrates, and dug, and took the belt from the place where I had hidden it; and behold, the belt was ruined. It was profitable for nothing."} ]
        , 8:  [ {class: "vs", text: "Then Yahweh’s word came to me, saying,"} ]
        , 9:  [ {class: "vs", text: "“Yahweh says, ‘In this way I, will ruin the pride of Judah, and the great pride of Jerusalem."} ]
        , 10: [ {class: "vs", text: "This evil people, who refuse to hear my words, who walk in the stubbornness of their heart, and have gone after other gods to serve them, and to worship them, will even be as this belt, which is profitable for nothing."} ]
        , 11: [ {class: "vs", text: "For as the belt clings to the waist of a man, so I have caused the whole house of Israel and the whole house of Judah to cling to me,’ says Yahweh; ‘that they may be to me for a people, for a name, for praise, and for glory; but they would not hear.’"} ]
        , 12: [ {class: "vs", text: "“Therefore you shall speak to them this word: ‘Yahweh, the God of Israel says, “Every container should be filled with wine.”’ They will tell you, ‘Do we not certainly know that every container should be filled with wine?’"} ]
        , 13: [ {class: "vs", text: "Then tell them, ‘Yahweh says, “Behold, I will fill all the inhabitants of this land, even the kings who sit on David’s throne, the priests, the prophets, and all the inhabitants of Jerusalem, with drunkenness."} ]
        , 14: [ {class: "vs", text: "I will dash them one against another, even the fathers and the sons together,” says Yahweh: “I will not pity, spare, or have compassion, that I should not destroy them.”’”"} ]
        , 15: [ {class: "q", text: "Hear, and give ear."}
              , {class: "q2", text: "Don’t be proud,"}
              , {class: "q2", text: "for Yahweh has spoken."}
              ]
        , 16: [ {class: "q", text: "Give glory to Yahweh your God,"}
              , {class: "q2", text: "before he causes darkness,"}
              , {class: "q2", text: "and before your feet stumble on the dark mountains,"}
              , {class: "q", text: "and, while you look for light,"}
              , {class: "q2", text: "he turns it into the shadow of death,"}
              , {class: "q2", text: "and makes it deep darkness."}
              ]
        , 17: [ {class: "q", text: "But if you will not hear it,"}
              , {class: "q2", text: "my soul will weep in secret for your pride."}
              , {class: "q", text: "My eye will weep bitterly,"}
              , {class: "q2", text: "and run down with tears,"}
              , {class: "q2", text: "because Yahweh’s flock has been taken captive."}
              ]
        , 18: [ {class: "q", text: "Say to the king and to the queen mother,"}
              , {class: "q2", text: "“Humble yourselves."}
              , {class: "q", text: "Sit down, for your crowns have come down,"}
              , {class: "q2", text: "even the crown of your glory."}
              ]
        , 19: [ {class: "q", text: "The cities of the South is shut up,"}
              , {class: "q2", text: "and there is no one to open them."}
              , {class: "q", text: "Judah is carried away captive: all of it."}
              , {class: "q2", text: "It is wholly carried away captive."}
              ]
        , 20: [ {class: "q", text: "Lift up your eyes,"}
              , {class: "q2", text: "and see those who come from the north."}
              , {class: "q", text: "Where is the flock that was given to you,"}
              , {class: "q2", text: "your beautiful flock?"}
              ]
        , 21: [ {class: "q", text: "What will you say, when he sets over you as head those whom you have yourself taught to be friends to you?"}
              , {class: "q2", text: "Won’t sorrows take hold of you, as of a woman in travail?"}
              ]
        , 22: [ {class: "q", text: "If you say in your heart,"}
              , {class: "q2", text: "“Why have these things come on me?”"}
              , {class: "q", text: "Your skirts are uncovered because of the greatness of your iniquity,"}
              , {class: "q2", text: "and your heels suffer violence."}
              ]
        , 23: [ {class: "q", text: "Can the Ethiopian change his skin,"}
              , {class: "q2", text: "or the leopard his spots?"}
              , {class: "q", text: "Then may you also do good,"}
              , {class: "q2", text: "who are accustomed to do evil."}
              ]
        , 24: [ {class: "q", text: "“Therefore I will scatter them,"}
              , {class: "q2", text: "as the stubble that passes away,"}
              , {class: "q2", text: "by the wind of the wilderness."}
              ]
        , 25: [ {class: "q", text: "This is your lot,"}
              , {class: "q2", text: "the portion measured to you from me,” says Yahweh,"}
              , {class: "q", text: "“because you have forgotten me,"}
              , {class: "q2", text: "and trusted in falsehood.”"}
              ]
        , 26: [ {class: "q", text: "Therefore I will also uncover your skirts on your face,"}
              , {class: "q2", text: "and your shame will appear."}
              ]
        , 27: [ {class: "q", text: "I have seen your abominations, even your adulteries,"}
              , {class: "q2", text: "and your neighing, the lewdness of your prostitution,"}
              , {class: "q2", text: "on the hills in the field."}
              , {class: "q", text: "Woe to you, Jerusalem!"}
              , {class: "q2", text: "You will not be made clean."}
              , {class: "q2", text: "How long will it yet be?”"}
              ]
        }
  , 14: { 1:  [ {class: "vs", text: "This is Yahweh’s word that came to Jeremiah concerning the drought."} ]
        , 2:  [ {class: "q", text: "“Judah mourns,"}
              , {class: "q2", text: "and its gates languish."}
              , {class: "q", text: "They sit in black on the ground."}
              , {class: "q2", text: "The cry of Jerusalem goes up."}
              ]
        , 3:  [ {class: "q", text: "Their nobles send their little ones to the waters."}
              , {class: "q2", text: "They come to the cisterns,"}
              , {class: "q2", text: "and find no water."}
              , {class: "q", text: "They return with their vessels empty."}
              , {class: "q2", text: "They are disappointed and confounded,"}
              , {class: "q2", text: "and cover their heads."}
              ]
        , 4:  [ {class: "q", text: "Because of the ground which is cracked,"}
              , {class: "q2", text: "because no rain has been in the land,"}
              , {class: "q", text: "the plowmen are disappointed."}
              , {class: "q2", text: "They cover their heads."}
              ]
        , 5:  [ {class: "q", text: "Yes, the doe in the field also calves and forsakes her young,"}
              , {class: "q2", text: "because there is no grass."}
              ]
        , 6:  [ {class: "q", text: "The wild donkeys stand on the bare heights."}
              , {class: "q2", text: "They pant for air like jackals."}
              , {class: "q", text: "Their eyes fail,"}
              , {class: "q2", text: "because there is no vegetation."}
              ]
        , 7:  [ {class: "q", text: "Though our iniquities testify against us,"}
              , {class: "q2", text: "work for your name’s sake, Yahweh;"}
              , {class: "q", text: "for our rebellions are many."}
              , {class: "q2", text: "We have sinned against you."}
              ]
        , 8:  [ {class: "q", text: "You hope of Israel,"}
              , {class: "q2", text: "its Savior in the time of trouble,"}
              , {class: "q", text: "why should you be as a foreigner in the land,"}
              , {class: "q2", text: "and as a wayfaring man who turns aside to stay for a night?"}
              ]
        , 9:  [ {class: "q", text: "Why should you be like a scared man,"}
              , {class: "q2", text: "as a mighty man who can’t save?"}
              , {class: "q", text: "Yet you, Yahweh, are in the middle of us,"}
              , {class: "q2", text: "and we are called by your name."}
              , {class: "q2", text: "Don’t leave us."}
              ]
        , 10: [ {class: "endpara", text: "Yahweh says to this people,"}
              , {class: "q", text: "“Even so they have loved to wander."}
              , {class: "q2", text: "They have not restrained their feet."}
              , {class: "q", text: "Therefore Yahweh does not accept them."}
              , {class: "q2", text: "Now he will remember their iniquity,"}
              , {class: "q2", text: "and punish them for their sins.”"}
              ]
        , 11: [ {class: "vs", text: "Yahweh said to me, “Don’t pray for this people for their good."} ]
        , 12: [ {class: "vs", text: "When they fast, I will not hear their cry; and when they offer burnt offering and meal offering, I will not accept them; but I will consume them by the sword, by famine, and by pestilence.”"} ]
        , 13: [ {class: "vs", text: "Then I said, “Ah, Lord Yahweh! Behold, the prophets tell them, ‘You will not see the sword, neither will you have famine; but I will give you assured peace in this place.’”"} ]
        , 14: [ {class: "vs", text: "Then Yahweh said to me, “The prophets prophesy lies in my name. I didn’t send them. I didn’t command them. I didn’t speak to them. They prophesy to you a lying vision, divination, and a thing of nothing, and the deceit of their own heart."} ]
        , 15: [ {class: "vs", text: "Therefore Yahweh says concerning the prophets who prophesy in my name, but I didn’t send them, yet they say, ‘Sword and famine will not be in this land.’ Those prophets will be consumed by sword and famine."} ]
        , 16: [ {class: "vs", text: "The people to whom they prophesy will be cast out in the streets of Jerusalem because of the famine and the sword. They will have no one to bury them—them, their wives, their sons, or their daughters, for I will pour their wickedness on them."} ]
        , 17: [ {class: "endpara", text: "“You shall say this word to them:"}
              , {class: "“‘Let my eyes run down with tears night and day,"}
              , {class: "q2", text: "and let them not cease;"}
              , {class: "q", text: "for the virgin daughter of my people is broken with a great breach,"}
              , {class: "q2", text: "with a very grievous wound."}
              ]
        , 18: [ {class: "q", text: "If I go out into the field,"}
              , {class: "q2", text: "then, behold, the slain with the sword!"}
              , {class: "q", text: "If I enter into the city,"}
              , {class: "q2", text: "then, behold, those who are sick with famine!"}
              , {class: "q", text: "For both the prophet and the priest go about in the land,"}
              , {class: "q2", text: "and have no knowledge.’”"}
              ]
        , 19: [ {class: "q", text: "Have you utterly rejected Judah?"}
              , {class: "q2", text: "Has your soul loathed Zion?"}
              , {class: "q", text: "Why have you struck us, and there is no healing for us?"}
              , {class: "q2", text: "We looked for peace, but no good came;"}
              , {class: "q2", text: "and for a time of healing, and behold, dismay!"}
              ]
        , 20: [ {class: "q", text: "We acknowledge, Yahweh, our wickedness,"}
              , {class: "q2", text: "and the iniquity of our fathers;"}
              , {class: "q2", text: "for we have sinned against you."}
              ]
        , 21: [ {class: "q", text: "Do not abhor us, for your name’s sake."}
              ,  {class: "q2", text: "Do not disgrace the throne of your glory."}
              , {class: "q2", text: "Remember, and don’t break your covenant with us."}
              ]
        , 22: [ {class: "q", text: "Are there any among the vanities of the nations that can cause rain?"}
              , {class: "q2", text: "Or can the sky give showers?"}
              , {class: "q2", text: "Aren’t you he, Yahweh our God?"}
              , {class: "q", text: "Therefore we will wait for you;"}
              , {class: "q2", text: "for you have made all these things."}
              ]
        }
  , 15: { 1:  [ {class: "vs", text: "Then Yahweh said to me, “Though Moses and Samuel stood before me, yet my mind would not turn toward this people. Cast them out of my sight, and let them go out!"} ]
        , 2:  [ {class: "endpara", text: "It will happen, when they ask you, ‘Where shall we go out?’ then you shall tell them, ‘Yahweh says:"}
              , {class: "q", text: "“Such as are for death, to death;"}
              , {class: "q", text: "such as are for the sword, to the sword;"}
              , {class: "q", text: "such as are for the famine, to the famine;"}
              , {class: "q", text: "and such as are for captivity, to captivity.”’"}
              ]
        , 3:  [ {class: "vs", text: "“I will appoint over them four kinds,” says Yahweh: “the sword to kill, the dogs to tear, the birds of the sky, and the animals of the earth, to devour and to destroy."} ]
        , 4:  [ {class: "vs", text: "I will cause them to be tossed back and forth among all the kingdoms of the earth, because of Manasseh, the son of Hezekiah, king of Judah, for that which he did in Jerusalem."} ]
        , 5:  [ {class: "q", text: "For who will have pity on you, Jerusalem?"}
              , {class: "q2", text: "Who will mourn you?"}
              , {class: "q2", text: "Who will come to ask of your welfare?"}
              ]
        , 6:  [ {class: "q", text: "You have rejected me,” says Yahweh."}
              , {class: "q2", text: "“You have gone backward."}
              , {class: "q", text: "Therefore I have stretched out my hand against you"}
              , {class: "q2", text: "and destroyed you."}
              , {class: "q2", text: "I am weary of showing compassion."}
              ]
        , 7:  [ {class: "q", text: "I have winnowed them with a fan in the gates of the land."}
              , {class: "q2", text: "I have bereaved them of children."}
              , {class: "q", text: "I have destroyed my people."}
              , {class: "q2", text: "They didn’t return from their ways."}
              ]
        , 8:  [ {class: "q", text: "Their widows are increased more than the sand of the seas."}
              , {class: "q2", text: "I have brought on them against the mother of the young men a destroyer at noonday."}
              , {class: "q2", text: "I have caused anguish and terrors to fall on her suddenly."}
              ]
        , 9:  [ {class: "q", text: "She who has borne seven languishes."}
              , {class: "q2", text: "She has given up the spirit."}
              , {class: "q", text: "Her sun has gone down while it was yet day."}
              , {class: "q2", text: "She has been disappointed and confounded."}
              , {class: "q2", text: "I will deliver their residue to the sword before their enemies,” says Yahweh."}
              ]
        , 10: [ {class: "q", text: "Woe is me, my mother, that you have borne me, a man of strife,"}
              , {class: "q2", text: "and a man of contention to the whole earth!"}
              , {class: "q", text: "I have not lent, neither have men lent to me;"}
              , {class: "q2", text: "yet every one of them curses me."}
              ]
        , 11: [ {class: "endpara", text: "Yahweh said,"}
              , {class: "q", text: "“Most certainly I will strengthen you for good."}
              , {class: "q2", text: "Most certainly I will cause the enemy to make supplication to you in the time of evil"}
              , {class: "q2", text: "and in the time of affliction."}
              ]
        , 12: [ {class: "q", text: "Can one break iron,"}
              , {class: "q2", text: "even iron from the north, and bronze?"}
              ]
        , 13: [ {class: "q", text: "I will give your substance and your treasures for a plunder without price,"}
              , {class: "q2", text: "and that for all your sins,"}
              , {class: "q2", text: "even in all your borders."}
              ]
        , 14: [ {class: "q", text: "I will make them to pass with your enemies into a land which you don’t know;"}
              , {class: "q2", text: "for a fire is kindled in my anger,"}
              , {class: "q2", text: "which will burn on you.”"}
              ]
        , 15: [ {class: "q", text: "Yahweh, you know."}
              , {class: "q2", text: "Remember me, visit me,"}
              , {class: "q2", text: "and avenge me of my persecutors."}
              , {class: "q", text: "You are patient, so don’t take me away."}
              , {class: "q2", text: "Know that for your sake I have suffered reproach."}
              ]
        , 16: [ {class: "q", text: "Your words were found,"}
              , {class: "q2", text: "and I ate them."}
              , {class: "q", text: "Your words were to me a joy and the rejoicing of my heart,"}
              , {class: "q2", text: "for I am called by your name, Yahweh, God of Armies."}
              ]
        , 17: [ {class: "q", text: "I didn’t sit in the assembly of those who make merry and rejoice."}
              ,  {class: "q2", text: "I sat alone because of your hand,"}
              , {class: "q2", text: "for you have filled me with indignation."}
              ]
        , 18: [ {class: "q", text: "Why is my pain perpetual,"}
              ,  {class: "q2", text: "and my wound incurable,"}
              , {class: "q2", text: "which refuses to be healed?"}
              , {class: "q", text: "Will you indeed be to me as a deceitful brook,"}
              , {class: "q2", text: "like waters that fail?"}
              ]
        , 19: [ {class: "endpara", text: "Therefore Yahweh says,"}
              , {class: "q", text: "“If you return, then I will bring you again,"}
              , {class: "q2", text: "that you may stand before me;"}
              , {class: "q", text: "and if you take out the precious from the vile,"}
              , {class: "q2", text: "you will be as my mouth."}
              , {class: "q", text: "They will return to you,"}
              , {class: "q2", text: "but you will not return to them."}
              ]
        , 20: [ {class: "q", text: "I will make you to this people a fortified bronze wall."}
              , {class: "q2", text: "They will fight against you,"}
              , {class: "q2", text: "but they will not prevail against you;"}
              , {class: "q", text: "for I am with you to save you"}
              , {class: "q2", text: "and to deliver you,” says Yahweh."}
              ]
        , 21: [ {class: "q", text: "“I will deliver you out of the hand of the wicked,"}
              , {class: "q2", text: "and I will redeem you out of the hand of the terrible.”"}
              ]
        }
  , 16: { 1:  [ {class: "vs", text: "Yahweh’s word came also to me, saying,"} ]
        , 2:  [ {class: "vs", text: "“You shall not take a wife, neither shall you have sons or daughters, in this place.”"} ]
        , 3:  [ {class: "vs", text: "For Yahweh says concerning the sons and concerning the daughters who are born in this place, and concerning their mothers who bore them, and concerning their fathers who became their father in this land:"} ]
        , 4:  [ {class: "vs", text: "“They will die grievous deaths. They will not be lamented, neither will they be buried. They will be as dung on the surface of the ground. They will be consumed by the sword and by famine. Their dead bodies will be food for the birds of the sky and for the animals of the earth.”"} ]
        , 5:  [ {class: "vs", text: "For Yahweh says, “Don’t enter into the house of mourning. Don’t go to lament. Don’t bemoan them, for I have taken away my peace from this people,” says Yahweh, “even loving kindness and tender mercies."} ]
        , 6:  [ {class: "vs", text: "Both great and small will die in this land. They will not be buried. Men won’t lament for them, cut themselves, or make themselves bald for them."} ]
        , 7:  [ {class: "vs", text: "Men won’t break bread for them in mourning, to comfort them for the dead. Men won’t give them the cup of consolation to drink for their father or for their mother."} ]
        , 8:  [ {class: "vs", text: "“You shall not go into the house of feasting to sit with them, to eat and to drink.”"} ]
        , 9:  [ {class: "vs", text: "For Yahweh of Armies, the God of Israel says: “Behold, I will cause to cease out of this place, before your eyes and in your days, the voice of mirth and the voice of gladness, the voice of the bridegroom and the voice of the bride."} ]
        , 10: [ {class: "vs", text: "It will happen, when you tell this people all these words, and they ask you, ‘Why has Yahweh pronounced all this great evil against us?’ or ‘What is our iniquity? or ‘What is our sin that we have committed against Yahweh our God?’"} ]
        , 11: [ {class: "vs", text: "then you shall tell them, ‘Because your fathers have forsaken me,’ says Yahweh, ‘and have walked after other gods, have served them, have worshiped them, have forsaken me, and have not kept my law."} ]
        , 12: [ {class: "vs", text: "You have done evil more than your fathers, for behold, you each walk after the stubbornness of his evil heart, so that you don’t listen to me."} ]
        , 13: [ {class: "vs", text: "Therefore I will cast you out of this land into the land that you have not known, neither you nor your fathers. There you will serve other gods day and night, for I will show you no favor.’"} ]
        , 14: [ {class: "vs", text: "“Therefore behold, the days come,” says Yahweh, “that it will no more be said, ‘As Yahweh lives, who brought up the children of Israel out of the land of Egypt;’"} ]
        , 15: [ {class: "vs", text: "but, ‘As Yahweh lives, who brought up the children of Israel from the land of the north, and from all the countries where he had driven them.’ I will bring them again into their land that I gave to their fathers."} ]
        , 16: [ {class: "vs", text: "“Behold, I will send for many fishermen,” says Yahweh, “and they will fish them up. Afterward I will send for many hunters, and they will hunt them from every mountain, from every hill, and out of the clefts of the rocks."} ]
        , 17: [ {class: "vs", text: "For my eyes are on all their ways. They are not hidden from my face. Their iniquity isn’t concealed from my eyes."} ]
        , 18: [ {class: "vs", text: "First I will recompense their iniquity and their sin double, because they have polluted my land with the carcasses of their detestable things, and have filled my inheritance with their abominations.”"} ]
        , 19: [ {class: "q", text: "Yahweh, my strength, and my stronghold,"}
              , {class: "q2", text: "and my refuge in the day of affliction,"}
              , {class: "q", text: "the nations will come to you from the ends of the earth,"}
              , {class: "q2", text: "and will say,"}
              , {class: "q", text: "“Our fathers have inherited nothing but lies,"}
              , {class: "q2", text: "vanity and things in which there is no profit."}
              ]
        , 20: [ {class: "q", text: "Should a man make to himself gods"}
              , {class: "q2", text: "which yet are no gods?”"}
              ]
        , 21: [ {class: "q", text: "“Therefore behold, I will cause them to know,"}
              , {class: "q2", text: "this once I will cause them to know my hand and my might."}
              , {class: "q2", text: "Then they will know that my name is Yahweh.”"}
              ]
          }
        , 17: { 1:  [ {class: "q", text: "“The sin of Judah is written with a pen of iron,"}
              , {class: "q2", text: "and with the point of a diamond."}
              , {class: "q", text: "It is engraved on the tablet of their heart,"}
              , {class: "q2", text: "and on the horns of your altars."}
              ]
        , 2:  [ {class: "q", text: "Even their children remember their altars"}
              , {class: "q2", text: "and their Asherah poles by the green trees on the high hills."}
              ]
        , 3:  [ {class: "q", text: "My mountain in the field,"}
              , {class: "q2", text: "I will give your substance and all your treasures for a plunder,"}
              , {class: "q2", text: "and your high places, because of sin, throughout all your borders."}
              ]
        , 4:  [ {class: "q", text: "You, even of yourself, will discontinue from your heritage that I gave you."}
              , {class: "q2", text: "I will cause you to serve your enemies in the land which you don’t know,"}
              , {class: "q2", text: "for you have kindled a fire in my anger which will burn forever.”"}
              ]
        , 5:  [ {class: "endpara", text: "Yahweh says:</p><div class=\"“Cursed is the man who trusts in man,"}
              ,  {class: "q2", text: "relies on strength of flesh,"}
              , {class: "q2", text: "and whose heart departs from Yahweh."}
              ]
        , 6:  [ {class: "q", text: "For he will be like a bush in the desert,"}
              , {class: "q2", text: "and will not see when good comes,"}
              , {class: "q", text: "but will inhabit the parched places in the wilderness,"}
              , {class: "q2", text: "an uninhabited salt land."}
              ]
        , 7:  [ {class: "q", text: "“Blessed is the man who trusts in Yahweh,"}
              , {class: "q2", text: "and whose confidence is in Yahweh."}
              ]
        , 8:  [ {class: "q", text: "For he will be as a tree planted by the waters,"}
              , {class: "q2", text: "who spreads out its roots by the river,"}
              , {class: "q", text: "and will not fear when heat comes,"}
              , {class: "q2", text: "but its leaf will be green,"}
              , {class: "q", text: "and will not be concerned in the year of drought."}
              , {class: "q2", text: "It won’t cease from yielding fruit."}
              ]
        , 9:  [ {class: "q", text: "The heart is deceitful above all things"}
              , {class: "q2", text: "and it is exceedingly corrupt."}
              , {class: "q2", text: "Who can know it?"}
              ]
        , 10: [ {class: "q", text: "“I, Yahweh, search the mind."}
              , {class: "q2", text: "I try the heart,"}
              , {class: "q", text: "even to give every man according to his ways,"}
              , {class: "q2", text: "according to the fruit of his doings.”"}
              ]
        , 11: [ {class: "q", text: "As the partridge that sits on eggs which she has not laid,"}
              , {class: "q2", text: "so is he who gets riches, and not by right."}
              , {class: "q", text: "In the middle of his days, they will leave him."}
              , {class: "q2", text: "At his end, he will be a fool."}
              ]
        , 12: [ {class: "q", text: "A glorious throne, set on high from the beginning,"}
              , {class: "q2", text: "is the place of our sanctuary."}
              ]
        , 13: [ {class: "q", text: "Yahweh, the hope of Israel,"}
              , {class: "q2", text: "all who forsake you will be disappointed."}
              , {class: "q", text: "Those who depart from me will be written in the earth,"}
              , {class: "q2", text: "because they have forsaken Yahweh,"}
              , {class: "q2", text: "the spring of living waters."}
              ]
        , 14: [ {class: "q", text: "Heal me, O Yahweh, and I will be healed."}
              , {class: "q2", text: "Save me, and I will be saved;"}
              , {class: "q2", text: "for you are my praise."}
              ]
        , 15: [ {class: "q", text: "Behold, they ask me,"}
              ,  {class: "q2", text: "“Where is Yahweh’s word?"}
              , {class: "q2", text: "Let it be fulfilled now.”"}
              ]
        , 16: [ {class: "q", text: "As for me, I have not hurried from being a shepherd after you."}
              , {class: "q2", text: "I haven’t desired the woeful day. You know."}
              , {class: "q2", text: "That which came out of my lips was before your face."}
              ]
        , 17: [ {class: "q", text: "Don’t be a terror to me."}
              , {class: "q2", text: "You are my refuge in the day of evil."}
              ]
        , 18: [ {class: "q", text: "Let them be disappointed who persecute me,"}
              , {class: "q2", text: "but let not me be disappointed."}
              , {class: "q", text: "Let them be dismayed,"}
              , {class: "q2", text: "but don’t let me be dismayed."}
              , {class: "q", text: "Bring on them the day of evil,"}
              , {class: "q2", text: "and destroy them with double destruction."}
              ]
        , 19: [ {class: "vs", text: "Yahweh said this to me: “Go and stand in the gate of the children of the people, through which the kings of Judah come in and by which they go out, and in all the gates of Jerusalem."} ]
        , 20: [ {class: "vs", text: "Tell them, ‘Hear Yahweh’s word, you kings of Judah, all Judah, and all the inhabitants of Jerusalem, that enter in by these gates:"} ]
        , 21: [ {class: "vs", text: "Yahweh says, “Be careful, and bear no burden on the Sabbath day, nor bring it in by the gates of Jerusalem."} ]
        , 22: [ {class: "vs", text: "Don’t carry a burden out of your houses on the Sabbath day. Don’t do any work, but make the Sabbath day holy, as I commanded your fathers."} ]
        , 23: [ {class: "vs", text: "But they didn’t listen. They didn’t turn their ear, but made their neck stiff, that they might not hear, and might not receive instruction."} ]
        , 24: [ {class: "vs", text: "It will happen, if you diligently listen to me,” says Yahweh, “to bring in no burden through the gates of this city on the Sabbath day, but to make the Sabbath day holy, to do no work therein;"} ]
        , 25: [ {class: "vs", text: "then there will enter in by the gates of this city kings and princes sitting on David’s throne, riding in chariots and on horses, they, and their princes, the men of Judah, and the inhabitants of Jerusalem; and this city will remain forever."} ]
        , 26: [ {class: "vs", text: "They will come from the cities of Judah, and from the places around Jerusalem, from the land of Benjamin, from the lowland, from the hill country, and from the South, bringing burnt offerings, sacrifices, meal offerings, and frankincense, and bringing sacrifices of thanksgiving, to Yahweh’s house."} ]
        , 27: [ {class: "vs", text: "But if you will not listen to me to make the Sabbath day holy, and not to bear a burden and enter in at the gates of Jerusalem on the Sabbath day, then I will kindle a fire in its gates, and it will devour the palaces of Jerusalem. It will not be quenched.”’”"} ]
        }
  , 18: { 1:  [ {class: "vs", text: "The word which came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "“Arise, and go down to the potter’s house, and there I will cause you to hear my words.”"} ]
        , 3:  [ {class: "vs", text: "Then I went down to the potter’s house, and behold, he was making something on the wheels."} ]
        , 4:  [ {class: "vs", text: "When the vessel that he made of the clay was marred in the hand of the potter, he made it again another vessel, as seemed good to the potter to make it."} ]
        , 5:  [ {class: "vs", text: "Then Yahweh’s word came to me, saying,"} ]
        , 6:  [ {class: "vs", text: "“House of Israel, can’t I do with you as this potter?” says Yahweh. “Behold, as the clay in the potter’s hand, so are you in my hand, house of Israel."} ]
        , 7:  [ {class: "vs", text: "At the instant I speak concerning a nation, and concerning a kingdom, to pluck up and to break down and to destroy it;"} ]
        , 8:  [ {class: "vs", text: "if that nation, concerning which I have spoken, turns from their evil, I will repent of the evil that I thought to do to them."} ]
        , 9:  [ {class: "vs", text: "At the instant I speak concerning a nation, and concerning a kingdom, to build and to plant it;"} ]
        , 10: [ {class: "vs", text: "if they do that which is evil in my sight, that they not obey my voice, then I will repent of the good with which I said I would benefit them."} ]
        , 11: [ {class: "vs", text: "“Now therefore, speak to the men of Judah, and to the inhabitants of Jerusalem, saying, ‘Yahweh says: “Behold, I frame evil against you, and devise a plan against you. Everyone return from his evil way now, and amend your ways and your doings.”’"} ]
        , 12: [ {class: "vs", text: "But they say, ‘It is in vain; for we will walk after our own plans, and we will each follow the stubbornness of his evil heart.’”"} ]
        , 13: [ {class: "endpara", text: "Therefore Yahweh says:"}
              , {class: "q", text: "“Ask now among the nations,"}
              , {class: "q2", text: "‘Who has heard such things?’"}
              , {class: "q2", text: "The virgin of Israel has done a very horrible thing."}
              ]
        , 14: [ {class: "q", text: "Will the snow of Lebanon fail from the rock of the field?"}
              , {class: "q2", text: "Will the cold waters that flow down from afar be dried up?"}
              ]
        , 15: [ {class: "q", text: "For my people have forgotten me."}
              , {class: "q2", text: "They have burned incense to false gods."}
              , {class: "q", text: "They have been made to stumble in their ways,"}
              , {class: "q2", text: "in the ancient paths,"}
              , {class: "q2", text: "to walk in byways, in a way not built up,"}
              ]
        , 16: [ {class: "q", text: "to make their land an astonishment,"}
              , {class: "q2", text: "and a perpetual hissing."}
              , {class: "q", text: "Everyone who passes by it will be astonished,"}
              , {class: "q2", text: "and shake his head."}
              ]
        , 17: [ {class: "q", text: "I will scatter them as with an east wind before the enemy."}
              , {class: "q2", text: "I will show them the back, and not the face,"}
              , {class: "q2", text: "in the day of their calamity."}
              ]
        , 18: [ {class: "vs", text: "Then they said, “Come! Let’s devise plans against Jeremiah; for the law won’t perish from the priest, nor counsel from the wise, nor the word from the prophet. Come, and let’s strike him with the tongue, and let’s not give heed to any of his words.”"} ]
        , 19: [ {class: "q", text: "Give heed to me, Yahweh,"}
              , {class: "q2", text: "and listen to the voice of those who contend with me."}
              ]
        , 20: [ {class: "q", text: "Should evil be recompensed for good?"}
              , {class: "q2", text: "For they have dug a pit for my soul."}
              , {class: "q", text: "Remember how I stood before you to speak good for them,"}
              , {class: "q2", text: "to turn away your wrath from them."}
              ]
        , 21: [ {class: "q", text: "Therefore deliver up their children to the famine,"}
              , {class: "q2", text: "and give them over to the power of the sword."}
              , {class: "q", text: "Let their wives become childless and widows."}
              , {class: "q2", text: "Let their men be killed"}
              , {class: "q2", text: "and their young men struck by the sword in battle."}
              ]
        , 22: [ {class: "q", text: "Let a cry be heard from their houses"}
              , {class: "q2", text: "when you bring a troop suddenly on them;"}
              , {class: "q", text: "for they have dug a pit to take me"}
              , {class: "q2", text: "and hidden snares for my feet."}
              ]
        , 23: [ {class: "q", text: "Yet, Yahweh, you know all their counsel against me to kill me."}
              , {class: "q2", text: "Don’t forgive their iniquity."}
              , {class: "q2", text: "Don’t blot out their sin from your sight,"}
              , {class: "q", text: "Let them be overthrown before you."}
              , {class: "q2", text: "Deal with them in the time of your anger."}
              ]
        }
  , 19: { 1:  [ {class: "vs", text: "Thus said Yahweh, “Go, and buy a potter’s earthen container, and take some of the elders of the people, and of the elders of the priests;"} ]
        , 2:  [ {class: "vs", text: "and go out to the valley of the son of Hinnom, which is by the entry of the gate Harsith, and proclaim there the words that I will tell you."} ]
        , 3:  [ {class: "vs", text: "Say, ‘Hear Yahweh’s word, kings of Judah, and inhabitants of Jerusalem: Yahweh of Armies, the God of Israel says, “Behold, I will bring evil on this place, which whoever hears, his ears will tingle."} ]
        , 4:  [ {class: "vs", text: "Because they have forsaken me, and have defiled this place, and have burned incense in it to other gods that they didn’t know, they, their fathers, and the kings of Judah, and have filled this place with the blood of innocents,"} ]
        , 5:  [ {class: "vs", text: "and have built the high places of Baal, to burn their children in the fire for burnt offerings to Baal, which I didn’t command, nor speak, which didn’t even enter into my mind."} ]
        , 6:  [ {class: "vs", text: "Therefore, behold, the days come,” says Yahweh, “that this place will no more be called ‘Topheth’, nor ‘The Valley of the son of Hinnom’, but ‘The valley of Slaughter’."} ]
        , 7:  [ {class: "vs", text: "“‘“I will make the counsel of Judah and Jerusalem void in this place. I will cause them to fall by the sword before their enemies, and by the hand of those who seek their life. I will give their dead bodies to be food for the birds of the sky and for the animals of the earth."} ]
        , 8:  [ {class: "vs", text: "I will make this city an astonishment and a hissing. Everyone who passes by it will be astonished and hiss because of all its plagues."} ]
        , 9:  [ {class: "vs", text: "I will cause them to eat the flesh of their sons and the flesh of their daughters. They will each eat the flesh of his friend in the siege and in the distress, with which their enemies, and those who seek their life, will distress them.”’"} ]
        , 10: [ {class: "vs", text: "“Then you shall break the container in the sight of the men who go with you,"} ]
        , 11: [ {class: "vs", text: "and shall tell them, ‘Yahweh of Armies says: “Even so I will break this people and this city, as one breaks a potter’s vessel, that can’t be made whole again. They will bury in Topheth, until there is no place to bury."} ]
        , 12: [ {class: "vs", text: "This is what I will do to this place,” says Yahweh, “and to its inhabitants, even making this city as Topheth."} ]
        , 13: [ {class: "vs", text: "The houses of Jerusalem, and the houses of the kings of Judah, which are defiled, will be as the place of Topheth, even all the houses on whose roofs they have burned incense to all the army of the sky and have poured out drink offerings to other gods.”’”"} ]
        , 14: [ {class: "vs", text: "Then Jeremiah came from Topheth, where Yahweh had sent him to prophesy, and he stood in the court of Yahweh’s house, and said to all the people:"} ]
        , 15: [ {class: "vs", text: "“Yahweh of Armies, the God of Israel says, ‘Behold, I will bring on this city and on all its towns all the evil that I have pronounced against it, because they have made their neck stiff, that they may not hear my words.’”"} ]
        }
  , 2:  { 1:  [ {class: "vs", text: "Yahweh’s word came to me, saying,"} ]
        , 2:  [ {class: "endpara", text: "“Go and proclaim in the ears of Jerusalem, saying, ‘Yahweh says,"}
              , {class: "q", text: "“I remember for you the kindness of your youth,"}
              , {class: "q2", text: "the love of your weddings;"}
              , {class: "q", text: "how you went after me in the wilderness,"}
              , {class: "q2", text: "in a land that was not sown."}
              ]
        , 3:  [ {class: "q", text: "Israel was holiness to Yahweh,"}
              , {class: "q2", text: "the first fruits of his increase."}
              , {class: "q", text: "All who devour him will be held guilty."}
              , {class: "q2", text: "Evil will come on them,”’ says Yahweh.”"}
              ]
        , 4:  [ {class: "vs", text: "Hear Yahweh’s word, O house of Jacob, and all the families of the house of Israel!"} ]
        , 5:  [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“What unrighteousness have your fathers found in me,"}
              , {class: "q2", text: "that they have gone far from me,"}
              , {class: "q", text: "and have walked after worthless vanity,"}
              , {class: "q2", text: "and have become worthless?"}
              ]
        , 6:  [ {class: "q", text: "They didn’t say, ‘Where is Yahweh who brought us up out of the land of Egypt,"}
              , {class: "q2", text: "who led us through the wilderness,"}
              , {class: "q2", text: "through a land of deserts and of pits,"}
              , {class: "q2", text: "through a land of drought and of the shadow of death,"}
              , {class: "q2", text: "through a land that no one passed through,"}
              , {class: "q2", text: "and where no man lived?’"}
              ]
        , 7:  [ {class: "q", text: "I brought you into a plentiful land"}
              , {class: "q2", text: "to eat its fruit and its goodness;"}
              , {class: "q", text: "but when you entered, you defiled my land,"}
              , {class: "q2", text: "and made my heritage an abomination."}
              ]
        , 8:  [ {class: "q", text: "The priests didn’t say, ‘Where is Yahweh?’"}
              , {class: "q2", text: "and those who handle the law didn’t know me."}
              , {class: "q", text: "The rulers also transgressed against me,"}
              , {class: "q2", text: "and the prophets prophesied by Baal"}
              , {class: "q2", text: "and followed things that do not profit."}
              ]
        , 9:  [ {class: "q", text: "“Therefore I will yet contend with you,” says Yahweh,"}
              , {class: "q2", text: "“and I will contend with your children’s children."}
              ]
        , 10: [ {class: "q", text: "For pass over to the islands of Kittim, and see."}
              , {class: "q2", text: "Send to Kedar, and consider diligently,"}
              , {class: "q2", text: "and see if there has been such a thing."}
              ]
        , 11: [ {class: "q", text: "Has a nation changed its gods,"}
              , {class: "q2", text: "which really are no gods?"}
              , {class: "q2", text: "But my people have changed their glory for that which doesn’t profit."}
              ]
        , 12: [ {class: "q", text: "“Be astonished, you heavens, at this"}
              , {class: "q2", text: "and be horribly afraid."}
              , {class: "q2", text: "Be very desolate,” says Yahweh."}
              ]
        , 13: [ {class: "q", text: "“For my people have committed two evils:"}
              , {class: "q2", text: "they have forsaken me, the spring of living waters,"}
              , {class: "q2", text: "and cut out cisterns for themselves: broken cisterns that can’t hold water."}
              ]
        , 14: [ {class: "q", text: "Is Israel a slave?"}
              , {class: "q2", text: "Is he born into slavery?"}
              , {class: "q2", text: "Why has he become a captive?"}
              ]
        , 15: [ {class: "q", text: "The young lions have roared at him and yelled."}
              , {class: "q2", text: "They have made his land waste."}
              , {class: "q2", text: "His cities are burned up, without inhabitant."}
              ]
        , 16: [ {class: "q2", text: "</span> The children also of Memphis and Tahpanhes have broken the crown of your head."} ]
        , 17: [ {class: "q", text: "“Haven’t you brought this on yourself,"}
              , {class: "q2", text: "in that you have forsaken Yahweh your God, when he led you by the way?"}
              ]
        , 18: [ {class: "q", text: "Now what do you gain by going to Egypt, to drink the waters of the Shihor?"}
              , {class: "q2", text: "Or why do you to go on the way to Assyria, to drink the waters of the River?"}
              ]
        , 19: [ {class: "q", text: "“Your own wickedness will correct you,"}
              , {class: "q2", text: "and your backsliding will rebuke you."}
              , {class: "q", text: "Know therefore and see that it is an evil and bitter thing,"}
              , {class: "q2", text: "that you have forsaken Yahweh your God,"}
              , {class: "q2", text: "and that my fear is not in you,” says the Lord, Yahweh of Armies."}
              ]
        , 20: [ {class: "q", text: "“For long ago I broke off your yoke,"}
              , {class: "q2", text: "and burst your bonds."}
              , {class: "q", text: "You said, ‘I will not serve;’"}
              , {class: "q2", text: "for on every high hill and under every green tree you bowed yourself,"}
              , {class: "q2", text: "playing the prostitute."}
              ]
        , 21: [ {class: "q", text: "Yet I had planted you a noble vine,"}
              , {class: "q2", text: "a pure and faithful seed."}
              , {class: "q2", text: "How then have you turned into the degenerate branches of a foreign vine to me?"}
              ]
        , 22: [ {class: "q", text: "For though you wash yourself with lye,"}
              , {class: "q2", text: "and use much soap,"}
              , {class: "q2", text: "yet your iniquity is marked before me,” says the Lord Yahweh."}
              ]
        , 23: [ {class: "q", text: "“How can you say, ‘I am not defiled."}
              , {class: "q2", text: "I have not gone after the Baals’?"}
              , {class: "q", text: "See your way in the valley."}
              , {class: "q2", text: "Know what you have done."}
              , {class: "q", text: "You are a swift dromedary traversing her ways,"}
              ]
        , 24: [ {class: "endpara", text: "a wild donkey used to the wilderness, that sniffs the wind in her craving."}
              , {class: "q2", text: "When she is in heat, who can turn her away?"}
              , {class: "q2", text: "All those who seek her will not weary themselves. In her month, they will find her."}
              ]
        , 25: [ {class: "q", text: "“Keep your feet from being bare,"}
              , {class: "q2", text: "and your throat from thirst."}
              , {class: "q", text: "But you said, ‘It is in vain."}
              , {class: "q2", text: "No, for I have loved strangers,"}
              , {class: "q2", text: "and I will go after them.’"}
              ]
        , 26: [ {class: "q", text: "As the thief is ashamed when he is found,"}
              , {class: "q2", text: "so the house of Israel is ashamed:"}
              , {class: "q2", text: "they, their kings, their princes, their priests, and their prophets,"}
              ]
        , 27: [ {class: "q", text: "who tell wood, ‘You are my father,’"}
              , {class: "q2", text: "and a stone, ‘You have given birth to me,’"}
              , {class: "q", text: "for they have turned their back to me,"}
              , {class: "q2", text: "and not their face;"}
              , {class: "q2", text: "but in the time of their trouble they will say, ‘Arise, and save us!’"}
              ]
        , 28: [ {class: "q", text: "“But where are your gods that you have made for yourselves?"}
              , {class: "q2", text: "Let them arise, if they can save you in the time of your trouble;"}
              , {class: "q2", text: "for you have as many gods as you have towns, O Judah."}
              ]
        , 29: [ {class: "q", text: "“Why will you contend with me?"}
              ,  {class: "q2", text: "You all have transgressed against me,” says Yahweh."}
              ]
        , 30: [ {class: "q", text: "“I have struck your children in vain."}
              , {class: "q2", text: "They received no correction."}
              , {class: "q", text: "Your own sword has devoured your prophets,"}
              , {class: "q2", text: "like a destroying lion."}
              ]
        , 31: [ {class: "q", text: "Generation, consider Yahweh’s word."}
              , {class: "q2", text: "Have I been a wilderness to Israel?"}
              , {class: "q2", text: "Or a land of thick darkness?"}
              , {class: "q", text: "Why do my people say, ‘We have broken loose."}
              , {class: "q2", text: "We will come to you no more?’"}
              ]
        , 32: [ {class: "q", text: "“Can a virgin forget her ornaments,"}
              , {class: "q2", text: "or a bride her attire?"}
              , {class: "q2", text: "Yet my people have forgotten me for days without number."}
              ]
        , 33: [ {class: "q", text: "How well you prepare your way to seek love!"}
              , {class: "q2", text: "Therefore you have even taught the wicked women your ways."}
              ]
        , 34: [ {class: "q", text: "Also the blood of the souls of the innocent poor is found in your skirts."}
              , {class: "q2", text: "You didn’t find them breaking in,"}
              , {class: "q2", text: "but it is because of all these things."}
              ]
        , 35: [ {class: "q", text: "“Yet you said, ‘I am innocent."}
              , {class: "q2", text: "Surely his anger has turned away from me.’"}
              , {class: "q", text: "“Behold, I will judge you,"}
              , {class: "q2", text: "because you say, ‘I have not sinned.’"}
              ]
        , 36: [ {class: "q", text: "Why do you go about so much to change your ways?"}
              , {class: "q2", text: "You will be ashamed of Egypt also,"}
              , {class: "q2", text: "as you were ashamed of Assyria."}
              ]
        , 37: [ {class: "q", text: "You will also leave that place with your hands on your head;"}
              , {class: "q2", text: "for Yahweh has rejected those in whom you trust,"}
              , {class: "q2", text: "and you won’t prosper with them."}
              ]
        }
  , 20: { 1:  [ {class: "vs", text: "Now Pashhur, the son of Immer the priest, who was chief officer in Yahweh’s house, heard Jeremiah prophesying these things."} ]
        , 2:  [ {class: "vs", text: "Then Pashhur struck Jeremiah the prophet and put him in the stocks that were in the upper gate of Benjamin, which was in Yahweh’s house."} ]
        , 3:  [ {class: "vs", text: "On the next day, Pashhur released Jeremiah out of the stocks. Then Jeremiah said to him, “Yahweh has not called your name Pashhur, but Magormissabib."} ]
        , 4:  [ {class: "vs", text: "For Yahweh says, ‘Behold, I will make you a terror to yourself and to all your friends. They will fall by the sword of their enemies, and your eyes will see it. I will give all Judah into the hand of the king of Babylon, and he will carry them captive to Babylon, and will kill them with the sword."} ]
        , 5:  [ {class: "vs", text: "Moreover I will give all the riches of this city, and all its gains, and all its precious things, yes, I will give all the treasures of the kings of Judah into the hand of their enemies. They will make them captives, take them, and carry them to Babylon."} ]
        , 6:  [ {class: "vs", text: "You, Pashhur, and all who dwell in your house will go into captivity. You will come to Babylon, and there you will die, and there you will be buried, you, and all your friends, to whom you have prophesied falsely.’”"} ]
        , 7:  [ {class: "q", text: "Yahweh, you have persuaded me, and I was persuaded."}
              , {class: "q2", text: "You are stronger than I, and have prevailed."}
              , {class: "q", text: "I have become a laughingstock all day."}
              , {class: "q2", text: "Everyone mocks me."}
              ]
        , 8:  [ {class: "q", text: "For as often as I speak, I cry out;"}
              , {class: "q2", text: "I cry, “Violence and destruction!”"}
              , {class: "q", text: "because Yahweh’s word has been made a reproach to me,"}
              , {class: "q2", text: "and a derision, all day."}
              ]
        , 9:  [ {class: "q", text: "If I say, I will not make mention of him,"}
              , {class: "q2", text: "or speak any more in his name,"}
              , {class: "q", text: "then there is in my heart as it were a burning fire shut up in my bones."}
              , {class: "q2", text: "I am weary with holding it in."}
              , {class: "q2", text: "I can’t."}
              ]
        , 10: [ {class: "q", text: "For I have heard the defaming of many,"}
              , {class: "q2", text: "“Terror on every side!"}
              , {class: "q2", text: "Denounce, and we will denounce him!”"}
              , {class: "q", text: "say all my familiar friends,"}
              , {class: "q2", text: "those who watch for my fall."}
              , {class: "q", text: "“Perhaps he will be persuaded,"}
              , {class: "q2", text: "and we will prevail against him,"}
              , {class: "q2", text: "and we will take our revenge on him.”"}
              ]
        , 11: [ {class: "q", text: "But Yahweh is with me as an awesome mighty one."}
              , {class: "q2", text: "Therefore my persecutors will stumble,"}
              , {class: "q2", text: "and they won’t prevail."}
              , {class: "q", text: "They will be utterly disappointed,"}
              , {class: "q2", text: "because they have not dealt wisely,"}
              , {class: "q2", text: "even with an everlasting dishonor which will never be forgotten."}
              ]
        , 12: [ {class: "q", text: "But Yahweh of Armies, who tests the righteous,"}
              , {class: "q2", text: "who sees the heart and the mind,"}
              , {class: "q", text: "let me see your vengeance on them,"}
              , {class: "q2", text: "for I have revealed my cause to you."}
              ]
        , 13: [ {class: "q", text: "Sing to Yahweh!"}
              , {class: "q2", text: "Praise Yahweh,"}
              , {class: "q2", text: "for he has delivered the soul of the needy from the hand of evildoers."}
              ]
        , 14: [ {class: "q", text: "Cursed is the day in which I was born."}
              , {class: "q2", text: "Don’t let the day in which my mother bore me be blessed."}
              ]
        , 15: [ {class: "q", text: "Cursed is the man who brought news to my father, saying,"}
              , {class: "q2", text: "“A boy is born to you,” making him very glad."}
              ]
        , 16: [ {class: "q", text: "Let that man be as the cities which Yahweh overthrew,"}
              , {class: "q2", text: "and didn’t repent."}
              , {class: "q", text: "Let him hear a cry in the morning,"}
              , {class: "q2", text: "and shouting at noontime;"}
              ]
        , 17: [ {class: "q", text: "because he didn’t kill me from the womb."}
              , {class: "q2", text: "So my mother would have been my grave,"}
              , {class: "q2", text: "and her womb always great."}
              ]
        , 18: [ {class: "q", text: "Why did I come out of the womb to see labor and sorrow,"}
              , {class: "q2", text: "that my days should be consumed with shame?"}
              ]
        }
  , 21: { 1:  [ {class: "vs", text: "The word which came to Jeremiah from Yahweh, when king Zedekiah sent to him Pashhur the son of Malchijah, and Zephaniah the son of Maaseiah, the priest, saying,"} ]
        , 2:  [ {class: "vs", text: "“Please inquire of Yahweh for us; for Nebuchadnezzar king of Babylon makes war against us. Perhaps Yahweh will deal with us according to all his wondrous works, that he may withdraw from us.”"} ]
        , 3:  [ {class: "vs", text: "Then Jeremiah said to them, “Tell Zedekiah:"} ]
        , 4:  [ {class: "vs", text: "‘Yahweh, the God of Israel says, “Behold, I will turn back the weapons of war that are in your hands, with which you fight against the king of Babylon, and against the Chaldeans who besiege you outside the walls; and I will gather them into the middle of this city."} ]
        , 5:  [ {class: "vs", text: "I myself will fight against you with an outstretched hand and with a strong arm, even in anger, in wrath, and in great indignation."} ]
        , 6:  [ {class: "vs", text: "I will strike the inhabitants of this city, both man and animal. They will die of a great pestilence."} ]
        , 7:  [ {class: "vs", text: "Afterward,” says Yahweh, “I will deliver Zedekiah king of Judah, his servants, and the people, even those who are left in this city from the pestilence, from the sword, and from the famine, into the hand of Nebuchadnezzar king of Babylon, and into the hand of their enemies, and into the hand of those who seek their life. He will strike them with the edge of the sword. He will not spare them, have pity, or have mercy.”’"} ]
        , 8:  [ {class: "vs", text: "“You shall say to this people, ‘Yahweh says: “Behold, I set before you the way of life and the way of death."} ]
        , 9:  [ {class: "vs", text: "He who remains in this city will die by the sword, by the famine, and by the pestilence; but he who goes out and passes over to the Chaldeans who besiege you, he will live, and he will escape with his life."} ]
        , 10: [ {class: "vs", text: "For I have set my face on this city for evil, and not for good,” says Yahweh. “It will be given into the hand of the king of Babylon, and he will burn it with fire.”’"} ]
        , 11: [ {class: "vs", text: "“Concerning the house of the king of Judah, hear Yahweh’s word:"} ]
        , 12: [ {class: "", text: "House of David, Yahweh says,"}
              , {class: "q", text: "‘Execute justice in the morning,"}
              , {class: "q2", text: "and deliver him who is robbed out of the hand of the oppressor,"}
              , {class: "q", text: "lest my wrath go out like fire,"}
              , {class: "q2", text: "and burn so that no one can quench it,"}
              , {class: "q2", text: "because of the evil of your doings."}
              ]
        , 13: [ {class: "q", text: "Behold, I am against you, O inhabitant of the valley,"}
              , {class: "q2", text: "and of the rock of the plain,’ says Yahweh."}
              , {class: "q", text: "‘You that say, “Who would come down against us?”"}
              , {class: "q2", text: "or “Who would enter into our homes?”"}
              ]
        , 14: [ {class: "q", text: "I will punish you according to the fruit of your doings, says Yahweh;"}
              , {class: "q2", text: "and I will kindle a fire in her forest,"}
              , {class: "q2", text: "and it will devour all that is around her.’”"}
              ]
        }
  , 22: { 1:  [ {class: "vs", text: "Yahweh said, “Go down to the house of the king of Judah, and speak this word, there:"} ]
        , 2:  [ {class: "vs", text: "‘Hear Yahweh’s word, king of Judah, who sits on David’s throne, you, your servants, and your people who enter in by these gates."} ]
        , 3:  [ {class: "vs", text: "Yahweh says: “Execute justice and righteousness, and deliver him who is robbed out of the hand of the oppressor. Do no wrong. Do no violence to the foreigner, the fatherless, or the widow. Don’t shed innocent blood in this place."} ]
        , 4:  [ {class: "vs", text: "For if you do this thing indeed, then kings sitting on David’s throne will enter in by the gates of this house, riding in chariots and on horses, he, his servants, and his people."} ]
        , 5:  [ {class: "vs", text: "But if you will not hear these words, I swear by myself,” says Yahweh, “that this house will become a desolation.”’”"} ]
        , 6:  [ {class: "", text: "For Yahweh says concerning the house of the king of Judah:</p><div class=\"“You are Gilead to me,"}
              , {class: "q2", text: "the head of Lebanon."}
              , {class: "q", text: "Yet surely I will make you a wilderness,"}
              , {class: "q2", text: "cities which are not inhabited."}
              ]
        , 7:  [ {class: "q", text: "I will prepare destroyers against you,"}
              , {class: "q2", text: "everyone with his weapons,"}
              , {class: "q", text: "and they will cut down your choice cedars,"}
              , {class: "q2", text: "and cast them into the fire."}
              ]
        , 8:  [ {class: "vs", text: "“Many nations will pass by this city, and they will each ask his neighbor, ‘Why has Yahweh done this to this great city?’"} ]
        , 9:  [ {class: "vs", text: "Then they will answer, ‘Because they abandoned the covenant of Yahweh their God, worshiped other gods, and served them.’”"} ]
        , 10: [ {class: "q", text: "Don’t weep for the dead."}
              , {class: "q2", text: "Don’t bemoan him;"}
              , {class: "q", text: "but weep bitterly for him who goes away,"}
              , {class: "q2", text: "for he will return no more,"}
              , {class: "q2", text: "and not see his native country."}
              ]
        , 11: [ {class: "vs", text: "For Yahweh says touching Shallum the son of Josiah, king of Judah, who reigned instead of Josiah his father, and who went out of this place: “He won’t return there any more."} ]
        , 12: [ {class: "vs", text: "But he will die in the place where they have led him captive. He will see this land no more.”"} ]
        , 13: [ {class: "q", text: "“Woe to him who builds his house by unrighteousness,"}
              , {class: "q2", text: "and his rooms by injustice;"}
              , {class: "q", text: "who uses his neighbor’s service without wages,"}
              , {class: "q2", text: "and doesn’t give him his hire;"}
              ]
        , 14: [ {class: "q", text: "who says, ‘I will build myself a wide house and spacious rooms,’"}
              , {class: "q2", text: "and cuts out windows for himself;"}
              , {class: "q", text: "with a cedar ceiling,"}
              , {class: "q2", text: "and painted with red."}
              ]
        , 15: [ {class: "q", text: "“Should you reign, because you strive to excel in cedar?"}
              , {class: "q2", text: "Didn’t your father eat and drink,"}
              , {class: "q2", text: "and do justice and righteousness?"}
              , {class: "q2", text: "Then it was well with him."}
              ]
        , 16: [ {class: "q", text: "He judged the cause of the poor and needy;"}
              , {class: "q2", text: "so it was well, then."}
              , {class: "q", text: "Wasn’t this to know me?”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        , 17: [ {class: "q", text: "But your eyes and your heart are only for your covetousness,"}
              , {class: "q2", text: "for shedding innocent blood,"}
              , {class: "q2", text: "for oppression, and for doing violence.”"}
              ]
        , 18: [ {class: "", text: "Therefore Yahweh says concerning Jehoiakim the son of Josiah, king of Judah:</p><div class=\"“They won’t lament for him,"}
              , {class: "q2", text: "saying, ‘Ah my brother!’ or, ‘Ah sister!’"}
              , {class: "q", text: "They won’t lament for him,"}
              , {class: "q2", text: "saying ‘Ah lord!’ or, ‘Ah his glory!’"}
              ]
        , 19: [ {class: "q", text: "He will be buried with the burial of a donkey,"}
              , {class: "q2", text: "drawn and cast out beyond the gates of Jerusalem.”"}
              ]
        , 20: [ {class: "q", text: "“Go up to Lebanon, and cry."}
              , {class: "q2", text: "Lift up your voice in Bashan,"}
              , {class: "q", text: "and cry from Abarim;"}
              , {class: "q2", text: "for all your lovers have been destroyed."}
              ]
        , 21: [ {class: "q", text: "I spoke to you in your prosperity;"}
              , {class: "q2", text: "but you said, ‘I will not listen.’"}
              , {class: "q", text: "This has been your way from your youth,"}
              , {class: "q2", text: "that you didn’t obey my voice."}
              ]
        , 22: [ {class: "q", text: "The wind will feed all your shepherds,"}
              , {class: "q2", text: "and your lovers will go into captivity."}
              , {class: "q", text: "Surely then you will be ashamed"}
              , {class: "q2", text: "and confounded for all your wickedness."}
              ]
        , 23: [ {class: "q", text: "Inhabitant of Lebanon,"}
              , {class: "q2", text: "who makes your nest in the cedars,"}
              , {class: "q", text: "how greatly to be pitied you will be when pangs come on you,"}
              , {class: "q2", text: "the pain as of a woman in travail!"}
              ]
        , 24: [ {class: "vs", text: "“As I live,” says Yahweh, “though Coniah the son of Jehoiakim king of Judah were the signet on my right hand, I would still pluck you from there."} ]
        , 25: [ {class: "vs", text: "I would give you into the hand of those who seek your life, and into the hand of them of whom you are afraid, even into the hand of Nebuchadnezzar king of Babylon, and into the hand of the Chaldeans."} ]
        , 26: [ {class: "vs", text: "I will cast you out with your mother who bore you into another country, where you were not born; and there you will die."} ]
        , 27: [ {class: "vs", text: "But to the land to which their soul longs to return, there they will not return.”"} ]
        , 28: [ {class: "q", text: "Is this man Coniah a despised broken vessel?"}
              , {class: "q2", text: "Is he a vessel in which no one delights?"}
              , {class: "q", text: "Why are they cast out, he and his offspring,"}
              , {class: "q2", text: "and cast into a land which they don’t know?"}
              ]
        , 29: [ {class: "q", text: "O earth, earth, earth,"}
              , {class: "q2", text: "hear Yahweh’s word!"}
              ]
        , 30: [ {class: "q", text: "Yahweh says,"}
              , {class: "q2", text: "“Record this man as childless,"}
              , {class: "q2", text: "a man who will not prosper in his days;"}
              , {class: "q", text: "for no more will a man of his offspring prosper,"}
              , {class: "q2", text: "sitting on David’s throne,"}
              , {class: "q2", text: "and ruling in Judah.”"}
              ]
        }
  , 23: { 1:  [ {class: "vs", text: "“Woe to the shepherds who destroy and scatter the sheep of my pasture!” says Yahweh."} ]
        , 2:  [ {class: "vs", text: "Therefore Yahweh, the God of Israel, says against the shepherds who feed my people: “You have scattered my flock, driven them away, and have not visited them. Behold, I will visit on you the evil of your doings,” says Yahweh."} ]
        , 3:  [ {class: "vs", text: "“I will gather the remnant of my flock out of all the countries where I have driven them, and will bring them again to their folds; and they will be fruitful and multiply."} ]
        , 4:  [ {class: "vs", text: "I will set up shepherds over them, who will feed them. They will no longer be afraid or dismayed, neither will any be lacking,” says Yahweh."} ]
        , 5:  [ {class: "q", text: "“Behold, the days come,” says Yahweh,"}
              , {class: "q2", text: "“that I will raise to David a righteous Branch,"}
              , {class: "q", text: "and he will reign as king and deal wisely,"}
              , {class: "q2", text: "and will execute justice and righteousness in the land."}
              ]
        , 6:  [ {class: "q", text: "In his days Judah will be saved,"}
              , {class: "q2", text: "and Israel will dwell safely."}
              , {class: "q", text: "This is his name by which he will be called:"}
              , {class: "q2", text: "Yahweh our righteousness."}
              ]
        , 7:  [ {class: "vs", text: "“Therefore behold, the days come,” says Yahweh, “that they will no more say, ‘As Yahweh lives, who brought up the children of Israel out of the land of Egypt;’"} ]
        , 8:  [ {class: "vs", text: "but, ‘As Yahweh lives, who brought up and who led the offspring of the house of Israel out of the north country, and from all the countries where I had driven them.’ Then they will dwell in their own land.”"} ]
        , 9:  [ {class: "", text: "Concerning the prophets:</p><div class=\"My heart within me is broken."}
              , {class: "q2", text: "All my bones shake."}
              , {class: "q", text: "I am like a drunken man,"}
              , {class: "q2", text: "and like a man whom wine has overcome,"}
              , {class: "q", text: "because of Yahweh,"}
              , {class: "q2", text: "and because of his holy words."}
              ]
        , 10: [ {class: "q", text: "For the land is full of adulterers;"}
              , {class: "q2", text: "for because of the curse the land mourns."}
              , {class: "q", text: "The pastures of the wilderness have dried up."}
              , {class: "q2", text: "Their course is evil,"}
              , {class: "q2", text: "and their might is not right;"}
              ]
        , 11: [ {class: "q", text: "for both prophet and priest are profane."}
              , {class: "q2", text: "Yes, in my house I have found their wickedness,” says Yahweh."}
              ]
        , 12: [ {class: "q", text: "Therefore their way will be to them as slippery places in the darkness."}
              , {class: "q2", text: "They will be driven on,"}
              , {class: "q2", text: "and fall therein;"}
              , {class: "q", text: "for I will bring evil on them,"}
              , {class: "q2", text: "even the year of their visitation,” says Yahweh."}
              ]
        , 13: [ {class: "q", text: "“I have seen folly in the prophets of Samaria."}
              , {class: "q2", text: "They prophesied by Baal,"}
              , {class: "q2", text: "and caused my people Israel to err."}
              ]
        , 14: [ {class: "q", text: "In the prophets of Jerusalem I have also seen a horrible thing:"}
              , {class: "q2", text: "they commit adultery and walk in lies."}
              , {class: "q", text: "They strengthen the hands of evildoers,"}
              , {class: "q2", text: "so that no one returns from his wickedness."}
              , {class: "q", text: "They have all become to me as Sodom,"}
              , {class: "q2", text: "and its inhabitants as Gomorrah.”"}
              ]
        , 15: [ {class: "endpara", text: "Therefore Yahweh of Armies says concerning the prophets:"}
              , {class: "q", text: "“Behold, I will feed them with wormwood,"}
              , {class: "q2", text: "and make them drink poisoned water;"}
              , {class: "q2", text: "for from the prophets of Jerusalem ungodliness has gone out into all the land.”"}
              ]
        , 16: [ {class: "", text: "Yahweh of Armies says,"}
              , {class: "q", text: "“Don’t listen to the words of the prophets who prophesy to you."}
              , {class: "q2", text: "They teach you vanity."}
              , {class: "q2", text: "They speak a vision of their own heart,"}
              , {class: "q2", text: "and not out of the mouth of Yahweh."}
              ]
        , 17: [ {class: "q", text: "They say continually to those who despise me,"}
              , {class: "q2", text: "‘Yahweh has said, “You will have peace;”’"}
              , {class: "q", text: "and to everyone who walks in the stubbornness of his own heart they say,"}
              , {class: "q2", text: "‘No evil will come on you.’"}
              ]
        , 18: [ {class: "q", text: "For who has stood in the council of Yahweh,"}
              , {class: "q2", text: "that he should perceive and hear his word?"}
              , {class: "q2", text: "Who has listened to my word, and heard it?"}
              ]
        , 19: [ {class: "q", text: "Behold, Yahweh’s storm, his wrath, has gone out."}
              , {class: "q2", text: "Yes, a whirling storm:"}
              , {class: "q2", text: "It will burst on the head of the wicked."}
              ]
        , 20: [ {class: "q", text: "Yahweh’s anger will not return until he has executed,"}
              , {class: "q2", text: "and until he has performed the intents of his heart."}
              , {class: "q2", text: "In the latter days, you will understand it perfectly."}
              ]
        , 21: [ {class: "q", text: "I didn’t send these prophets, yet they ran."}
              , {class: "q2", text: "I didn’t speak to them, yet they prophesied."}
              ]
        , 22: [ {class: "q", text: "But if they had stood in my council,"}
              , {class: "q2", text: "then they would have caused my people to hear my words,"}
              , {class: "q", text: "and would have turned them from their evil way,"}
              , {class: "q2", text: "and from the evil of their doings."}
              ]
        , 23: [ {class: "q", text: "“Am I a God at hand,” says Yahweh,"}
              , {class: "q2", text: "“and not a God afar off?"}
              ]
        , 24: [ {class: "q", text: "Can anyone hide himself in secret places"}
              , {class: "q2", text: "so that I can’t see him?” says Yahweh."}
              , {class: "q2", text: "“Don’t I fill heaven and earth?” says Yahweh."}
              ]
        , 25: [ {class: "vs", text: "“I have heard what the prophets have said, who prophesy lies in my name, saying, ‘I had a dream! I had a dream!’"} ]
        , 26: [ {class: "vs", text: "How long will this be in the heart of the prophets who prophesy lies, even the prophets of the deceit of their own heart?"} ]
        , 27: [ {class: "vs", text: "They intend to cause my people to forget my name by their dreams which they each tell his neighbor, as their fathers forgot my name because of Baal."} ]
        , 28: [ {class: "vs", text: "The prophet who has a dream, let him tell a dream; and he who has my word, let him speak my word faithfully. What is the straw to the wheat?” says Yahweh."} ]
        , 29: [ {class: "vs", text: "“Isn’t my word like fire?” says Yahweh; “and like a hammer that breaks the rock in pieces?"} ]
        , 30: [ {class: "vs", text: "“Therefore behold, I am against the prophets,” says Yahweh, “who each steal my words from his neighbor."} ]
        , 31: [ {class: "vs", text: "Behold, I am against the prophets,” says Yahweh, “who use their tongues, and say, ‘He says.’"} ]
        , 32: [ {class: "vs", text: "Behold, I am against those who prophesy lying dreams,” says Yahweh, “who tell them, and cause my people to err by their lies, and by their vain boasting; yet I didn’t send them or command them. They don’t profit this people at all,” says Yahweh."} ]
        , 33: [ {class: "vs", text: "“When this people, or the prophet, or a priest, asks you, saying, ‘What is the message from Yahweh?’ Then you shall tell them, ‘“What message? I will cast you off,” says Yahweh.’"} ]
        , 34: [ {class: "vs", text: "As for the prophet, the priest, and the people, who say, ‘The message from Yahweh,’ I will even punish that man and his household."} ]
        , 35: [ {class: "vs", text: "You will say everyone to his neighbor, and everyone to his brother, ‘What has Yahweh answered?’ and, ‘What has Yahweh said?’"} ]
        , 36: [ {class: "vs", text: "You will mention the message from Yahweh no more: for every man’s own word has become his message; for you have perverted the words of the living God, of Yahweh of Armies, our God."} ]
        , 37: [ {class: "vs", text: "You will say to the prophet, ‘What has Yahweh answered you?’ and, ‘What has Yahweh spoken?’"} ]
        , 38: [ {class: "vs", text: "Although you say, ‘The message from Yahweh;’ therefore Yahweh says: ‘Because you say this word, “The message from Yahweh,” and I have sent to you, telling you not to say, “The message from Yahweh;”"} ]
        , 39: [ {class: "vs", text: "therefore, behold, I will utterly forget you, and I will cast you off, and the city that I gave to you and to your fathers, away from my presence."} ]
        , 40: [ {class: "vs", text: "I will bring an everlasting reproach on you, and a perpetual shame, which will not be forgotten.’”"} ]
        }
  , 24: { 1:  [ {class: "vs", text: "Yahweh showed me, and behold, two baskets of figs were set before Yahweh’s temple, after Nebuchadnezzar king of Babylon had carried away captive Jeconiah the son of Jehoiakim, king of Judah, and the princes of Judah, with the craftsmen and smiths, from Jerusalem, and had brought them to Babylon."} ]
        , 2:  [ {class: "vs", text: "One basket had very good figs, like the figs that are first-ripe; and the other basket had very bad figs, which could not be eaten, they were so bad."} ]
        , 3:  [ {class: "vs", text: "Then Yahweh asked me, “What do you see, Jeremiah?”</p><p> I said, “Figs. The good figs are very good, and the bad are very bad, so bad that can’t be eaten.”"} ]
        , 4:  [ {class: "vs", text: "Yahweh’s word came to me, saying,"} ]
        , 5:  [ {class: "vs", text: "“Yahweh, the God of Israel says: ‘Like these good figs, so I will regard the captives of Judah, whom I have sent out of this place into the land of the Chaldeans, for good."} ]
        , 6:  [ {class: "vs", text: "For I will set my eyes on them for good, and I will bring them again to this land. I will build them, and not pull them down. I will plant them, and not pluck them up."} ]
        , 7:  [ {class: "vs", text: "I will give them a heart to know me, that I am Yahweh. They will be my people, and I will be their God; for they will return to me with their whole heart."} ]
        , 8:  [ {class: "vs", text: "“‘As the bad figs, which can’t be eaten, they are so bad,’ surely Yahweh says, ‘So I will give up Zedekiah the king of Judah, and his princes, and the remnant of Jerusalem, who remain in this land, and those who dwell in the land of Egypt."} ]
        , 9:  [ {class: "vs", text: "I will even give them up to be tossed back and forth among all the kingdoms of the earth for evil; to be a reproach and a proverb, a taunt and a curse, in all places where I will drive them."} ]
        , 10: [ {class: "vs", text: "I will send the sword, the famine, and the pestilence, among them, until they are consumed from off the land that I gave to them and to their fathers.’”"} ]
        }
  , 25: { 1:  [ {class: "vs", text: "The word that came to Jeremiah concerning all the people of Judah, in the fourth year of Jehoiakim the son of Josiah, king of Judah (this was the first year of Nebuchadnezzar king of Babylon),"} ]
        , 2:  [ {class: "vs", text: "which Jeremiah the prophet spoke to all the people of Judah, and to all the inhabitants of Jerusalem:"} ]
        , 3:  [ {class: "vs", text: "From the thirteenth year of Josiah the son of Amon, king of Judah, even to this day, these twenty-three years, Yahweh’s word has come to me, and I have spoken to you, rising up early and speaking; but you have not listened."} ]
        , 4:  [ {class: "vs", text: "Yahweh has sent to you all his servants the prophets, rising up early and sending them (but you have not listened or inclined your ear to hear),"} ]
        , 5:  [ {class: "vs", text: "saying, “Return now everyone from his evil way, and from the evil of your doings, and dwell in the land that Yahweh has given to you and to your fathers, from of old and even forever more."} ]
        , 6:  [ {class: "vs", text: "Don’t go after other gods to serve them or worship them, and don’t provoke me to anger with the work of your hands; then I will do you no harm.”"} ]
        , 7:  [ {class: "vs", text: "“Yet you have not listened to me,” says Yahweh; “that you may provoke me to anger with the work of your hands to your own hurt.”"} ]
        , 8:  [ {class: "vs", text: "Therefore Yahweh of Armies says: “Because you have not heard my words,"} ]
        , 9:  [ {class: "vs", text: "behold, I will send and take all the families of the north,” says Yahweh, “and I will send to Nebuchadnezzar the king of Babylon, my servant, and will bring them against this land, and against its inhabitants, and against all these nations around. I will utterly destroy them, and make them an astonishment, and a hissing, and perpetual desolations."} ]
        , 10: [ {class: "vs", text: "Moreover I will take from them the voice of mirth and the voice of gladness, the voice of the bridegroom and the voice of the bride, the sound of the millstones, and the light of the lamp."} ]
        , 11: [ {class: "vs", text: "This whole land will be a desolation, and an astonishment; and these nations will serve the king of Babylon seventy years."} ]
        , 12: [ {class: "vs", text: "“It will happen, when seventy years are accomplished, that I will punish the king of Babylon and that nation,” says Yahweh, “for their iniquity. I will make the land of the Chaldeans desolate forever."} ]
        , 13: [ {class: "vs", text: "I will bring on that land all my words which I have pronounced against it, even all that is written in this book, which Jeremiah has prophesied against all the nations."} ]
        , 14: [ {class: "vs", text: "For many nations and great kings will make bondservants of them, even of them. I will recompense them according to their deeds, and according to the work of their hands.”"} ]
        , 15: [ {class: "vs", text: "For Yahweh, the God of Israel, says to me: “Take this cup of the wine of wrath from my hand, and cause all the nations to whom I send you to drink it."} ]
        , 16: [ {class: "vs", text: "They will drink, and reel back and forth, and be insane, because of the sword that I will send among them.”"} ]
        , 17: [ {class: "vs", text: "Then I took the cup at Yahweh’s hand, and made all the nations to drink, to whom Yahweh had sent me:"} ]
        , 18: [ {class: "vs", text: "Jerusalem, and the cities of Judah, with its kings and its princes, to make them a desolation, an astonishment, a hissing, and a curse, as it is today;"} ]
        , 19: [ {class: "vs", text: "Pharaoh king of Egypt, with his servants, his princes, and all his people;"} ]
        , 20: [ {class: "vs", text: "and all the mixed people, and all the kings of the land of Uz, all the kings of the Philistines, Ashkelon, Gaza, Ekron, and the remnant of Ashdod;"} ]
        , 21: [ {class: "vs", text: "Edom, Moab, and the children of Ammon;"} ]
        , 22: [ {class: "vs", text: "and all the kings of Tyre, all the kings of Sidon, and the kings of the isle which is beyond the sea;"} ]
        , 23: [ {class: "vs", text: "Dedan, Tema, Buz, and all who have the corners of their beard cut off;"} ]
        , 24: [ {class: "vs", text: "and all the kings of Arabia, all the kings of the mixed people who dwell in the wilderness;"} ]
        , 25: [ {class: "vs", text: "and all the kings of Zimri, all the kings of Elam, and all the kings of the Medes;"} ]
        , 26: [ {class: "vs", text: "and all the kings of the north, far and near, one with another; and all the kingdoms of the world, which are on the surface of the earth. The king of Sheshach will drink after them."} ]
        , 27: [ {class: "vs", text: "“You shall tell them, ‘Yahweh of Armies, the God of Israel says: “Drink, and be drunk, vomit, fall, and rise no more, because of the sword which I will send among you.”’"} ]
        , 28: [ {class: "vs", text: "It shall be, if they refuse to take the cup at your hand to drink, then you shall tell them, ‘Yahweh of Armies says: “You shall surely drink."} ]
        , 29: [ {class: "vs", text: "For, behold, I begin to work evil at the city which is called by my name; and should you be utterly unpunished? You will not be unpunished; for I will call for a sword on all the inhabitants of the earth, says Yahweh of Armies.”’"} ]
        , 30: [ {class: "endpara", text: "“Therefore prophesy against them all these words, and tell them,"}
              , {class: "q", text: "“‘Yahweh will roar from on high,"}
              , {class: "q2", text: "and utter his voice from his holy habitation."}
              , {class: "q2", text: "He will mightily roar against his fold."}
              , {class: "q", text: "He will give a shout, as those who tread grapes,"}
              , {class: "q2", text: "against all the inhabitants of the earth."}
              ]
        , 31: [ {class: "q", text: "A noise will come even to the end of the earth;"}
              , {class: "q2", text: "for Yahweh has a controversy with the nations."}
              , {class: "q", text: "He will enter into judgment with all flesh."}
              , {class: "q2", text: "As for the wicked, he will give them to the sword,”’ says Yahweh.”"}
              ]
        , 32: [ {class: "", text: "Yahweh of Armies says,"}
              , {class: "q", text: "“Behold, evil will go out from nation to nation,"}
              , {class: "q2", text: "and a great storm will be raised up from the uttermost parts of the earth.”"}
              ]
        , 33: [ {class: "vs", text: "The slain of Yahweh will be at that day from one end of the earth even to the other end of the earth. They won’t be lamented. They won’t be gathered or buried. They will be dung on the surface of the ground."} ]
        , 34: [ {class: "q", text: "Wail, you shepherds, and cry."}
              , {class: "q2", text: "Wallow in dust, you leader of the flock;"}
              , {class: "q", text: "for the days of your slaughter and of your dispersions have fully come,"}
              , {class: "q2", text: "and you will fall like fine pottery."}
              ]
        , 35: [ {class: "q", text: "The shepherds will have no way to flee."}
              , {class: "q2", text: "The leader of the flock will have no escape."}
              ]
        , 36: [ {class: "q", text: "A voice of the cry of the shepherds,"}
              , {class: "q2", text: "and the wailing of the leader of the flock,"}
              , {class: "q2", text: "for Yahweh destroys their pasture."}
              ]
        , 37: [ {class: "q", text: "The peaceful folds are brought to silence"}
              , {class: "q2", text: "because of the fierce anger of Yahweh."}
              ]
        , 38: [ {class: "q", text: "He has left his covert, as the lion;"}
              , {class: "q2", text: "for their land has become an astonishment because of the fierceness of the oppression,"}
              , {class: "q2", text: "and because of his fierce anger."}
              ]
        }
  , 26: { 1:  [ {class: "vs", text: "In the beginning of the reign of Jehoiakim the son of Josiah, king of Judah, this word came from Yahweh:"} ]
        , 2:  [ {class: "vs", text: "“Yahweh says: ‘Stand in the court of Yahweh’s house, and speak to all the cities of Judah, which come to worship in Yahweh’s house, all the words that I command you to speak to them. Don’t omit a word."} ]
        , 3:  [ {class: "vs", text: "It may be they will listen, and every man turn from his evil way; that I may relent from the evil which I intend to do to them because of the evil of their doings.’"} ]
        , 4:  [ {class: "vs", text: "You shall tell them, “Yahweh says: ‘If you will not listen to me, to walk in my law, which I have set before you,"} ]
        , 5:  [ {class: "vs", text: "to listen to the words of my servants the prophets, whom I send to you, even rising up early and sending them, but you have not listened;"} ]
        , 6:  [ {class: "vs", text: "then I will make this house like Shiloh, and will make this city a curse to all the nations of the earth.”’”"} ]
        , 7:  [ {class: "vs", text: "The priests and the prophets and all the people heard Jeremiah speaking these words in Yahweh’s house."} ]
        , 8:  [ {class: "vs", text: "When Jeremiah had finished speaking all that Yahweh had commanded him to speak to all the people, the priests and the prophets and all the people seized him, saying, “You shall surely die!"} ]
        , 9:  [ {class: "vs", text: "Why have you prophesied in Yahweh’s name, saying, ‘This house will be like Shiloh, and this city will be desolate, without inhabitant?’” All the people were crowded around Jeremiah in Yahweh’s house."} ]
        , 10: [ {class: "vs", text: "When the princes of Judah heard these things, they came up from the king’s house to Yahweh’s house; and they sat in the entry of the new gate of Yahweh’s house."} ]
        , 11: [ {class: "vs", text: "Then the priests and the prophets spoke to the princes and to all the people, saying, “This man is worthy of death; for he has prophesied against this city, as you have heard with your ears.”"} ]
        , 12: [ {class: "vs", text: "Then Jeremiah spoke to all the princes and to all the people, saying, “Yahweh sent me to prophesy against this house and against this city all the words that you have heard."} ]
        , 13: [ {class: "vs", text: "Now therefore amend your ways and your doings, and obey Yahweh your God’s voice; then Yahweh will relent from the evil that he has pronounced against you."} ]
        , 14: [ {class: "vs", text: "But as for me, behold, I am in your hand. Do with me what is good and right in your eyes."} ]
        , 15: [ {class: "vs", text: "Only know for certain that, if you put me to death, you will bring innocent blood on yourselves, on this city, and on its inhabitants; for in truth Yahweh has sent me to you to speak all these words in your ears.”"} ]
        , 16: [ {class: "vs", text: "Then the princes and all the people said to the priests and to the prophets: “This man is not worthy of death; for he has spoken to us in the name of Yahweh our God.”"} ]
        , 17: [ {class: "vs", text: "Then certain of the elders of the land rose up, and spoke to all the assembly of the people, saying,"} ]
        , 18: [ {class: "endpara", text: "“Micah the Morashtite prophesied in the days of Hezekiah king of Judah; and he spoke to all the people of Judah, saying, ‘Yahweh of Armies says:"}
              , {class: "q", text: "“‘Zion will be plowed as a field,"}
              , {class: "q2", text: "and Jerusalem will become heaps,"}
              , {class: "q2", text: "and the mountain of the house as the high places of a forest.’"}
              ]
        , 19: [ {class: "vs", text: "Did Hezekiah king of Judah and all Judah put him to death? Didn’t he fear Yahweh, and entreat the favor of Yahweh, and Yahweh relented of the disaster which he had pronounced against them? We would commit great evil against our own souls that way!”"} ]
        , 20: [ {class: "vs", text: "There was also a man who prophesied in Yahweh’s name, Uriah the son of Shemaiah of Kiriath Jearim; and he prophesied against this city and against this land according to all the words of Jeremiah."} ]
        , 21: [ {class: "vs", text: "When Jehoiakim the king, with all his mighty men and all the princes heard his words, the king sought to put him to death; but when Uriah heard it, he was afraid, and fled, and went into Egypt."} ]
        , 22: [ {class: "vs", text: "Then Jehoiakim the king sent men into Egypt, Elnathan the son of Achbor, and certain men with him, into Egypt;"} ]
        , 23: [ {class: "vs", text: "and they fetched Uriah out of Egypt, and brought him to Jehoiakim the king, who killed him with the sword, and cast his dead body into the graves of the common people."} ]
        , 24: [ {class: "vs", text: "But the hand of Ahikam the son of Shaphan was with Jeremiah, so that they didn’t give him into the hand of the people to put him to death."} ]
        }
  , 27: { 1:  [ {class: "vs", text: "In the beginning of the reign of Jehoiakim the son of Josiah, king of Judah, this word came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "Yahweh says to me: “Make bonds and bars, and put them on your neck."} ]
        , 3:  [ {class: "vs", text: "Then send them to the king of Edom, to the king of Moab, to the king of the children of Ammon, to the king of Tyre, and to the king of Sidon, by the hand of the messengers who come to Jerusalem to Zedekiah king of Judah."} ]
        , 4:  [ {class: "vs", text: "Give them a command to their masters, saying, ‘Yahweh of Armies, the God of Israel says, “You shall tell your masters:"} ]
        , 5:  [ {class: "vs", text: "‘I have made the earth, the men, and the animals that are on the surface of the earth by my great power and by my outstretched arm. I give it to whom it seems right to me."} ]
        , 6:  [ {class: "vs", text: "Now I have given all these lands into the hand of Nebuchadnezzar the king of Babylon, my servant. I have also given the animals of the field to him to serve him."} ]
        , 7:  [ {class: "vs", text: "All the nations will serve him, his son, and his son’s son, until the time of his own land comes. Then many nations and great kings will make him their bondservant."} ]
        , 8:  [ {class: "vs", text: "“‘“‘It will happen that I will punish the nation and the kingdom which will not serve the same Nebuchadnezzar king of Babylon, and that will not put their neck under the yoke of the king of Babylon,’ says Yahweh, ‘with the sword, with famine, and with pestilence, until I have consumed them by his hand."} ]
        , 9:  [ {class: "vs", text: "But as for you, don’t listen to your prophets, to your diviners, to your dreams, to your soothsayers, or to your sorcerers, who speak to you, saying, “You shall not serve the king of Babylon;”"} ]
        , 10: [ {class: "vs", text: "for they prophesy a lie to you, to remove you far from your land, so that I would drive you out, and you would perish."} ]
        , 11: [ {class: "vs", text: "But the nation that brings their neck under the yoke of the king of Babylon and serves him, that nation I will let remain in their own land,’ says Yahweh; ‘and they will till it and dwell in it.’”’”"} ]
        , 12: [ {class: "vs", text: "I spoke to Zedekiah king of Judah according to all these words, saying, “Bring your necks under the yoke of the king of Babylon, and serve him and his people, and live."} ]
        , 13: [ {class: "vs", text: "Why will you die, you and your people, by the sword, by the famine, and by the pestilence, as Yahweh has spoken concerning the nation that will not serve the king of Babylon?"} ]
        , 14: [ {class: "vs", text: "Don’t listen to the words of the prophets who speak to you, saying, ‘You shall not serve the king of Babylon;’ for they prophesy a lie to you."} ]
        , 15: [ {class: "vs", text: "For I have not sent them,” says Yahweh, “but they prophesy falsely in my name; that I may drive you out, and that you may perish, you, and the prophets who prophesy to you.”"} ]
        , 16: [ {class: "vs", text: "Also I spoke to the priests and to all this people, saying, Yahweh says, “Don’t listen to the words of your prophets who prophesy to you, saying, ‘Behold, the vessels of Yahweh’s house will now shortly be brought again from Babylon;’ for they prophesy a lie to you."} ]
        , 17: [ {class: "vs", text: "Don’t listen to them. Serve the king of Babylon, and live. Why should this city become a desolation?"} ]
        , 18: [ {class: "vs", text: "But if they are prophets, and if Yahweh’s word is with them, let them now make intercession to Yahweh of Armies, that the vessels which are left in Yahweh’s house, in the house of the king of Judah, and at Jerusalem, don’t go to Babylon."} ]
        , 19: [ {class: "vs", text: "For Yahweh of Armies says concerning the pillars, concerning the sea, concerning the bases, and concerning the rest of the vessels that are left in this city,"} ]
        , 20: [ {class: "vs", text: "which Nebuchadnezzar king of Babylon didn’t take when he carried away captive Jeconiah the son of Jehoiakim, king of Judah, from Jerusalem to Babylon, and all the nobles of Judah and Jerusalem;"} ]
        , 21: [ {class: "vs", text: "yes, Yahweh of Armies, the God of Israel, says concerning the vessels that are left in Yahweh’s house, and in the house of the king of Judah, and at Jerusalem:"} ]
        , 22: [ {class: "vs", text: "‘They will be carried to Babylon, and there they will be, until the day that I visit them,’ says Yahweh; ‘then I will bring them up, and restore them to this place.’”"} ]
        }
  , 28: { 1:  [ {class: "vs", text: "That same year, in the beginning of the reign of Zedekiah king of Judah, in the fourth year, in the fifth month, Hananiah the son of Azzur, the prophet, who was of Gibeon, spoke to me in Yahweh’s house, in the presence of the priests and of all the people, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh of Armies, the God of Israel, says, ‘I have broken the yoke of the king of Babylon."} ]
        , 3:  [ {class: "vs", text: "Within two full years I will bring again into this place all the vessels of Yahweh’s house, that Nebuchadnezzar king of Babylon took away from this place, and carried to Babylon."} ]
        , 4:  [ {class: "vs", text: "I will bring again to this place Jeconiah the son of Jehoiakim, king of Judah, with all the captives of Judah, who went to Babylon,’ says Yahweh; ‘for I will break the yoke of the king of Babylon.’”"} ]
        , 5:  [ {class: "vs", text: "Then the prophet Jeremiah said to the prophet Hananiah in the presence of the priests, and in the presence of all the people who stood in Yahweh’s house,"} ]
        , 6:  [ {class: "vs", text: "even the prophet Jeremiah said, “Amen! May Yahweh do so. May Yahweh perform your words which you have prophesied, to bring again the vessels of Yahweh’s house, and all those who are captives, from Babylon to this place."} ]
        , 7:  [ {class: "vs", text: "Nevertheless listen now to this word that I speak in your ears, and in the ears of all the people:"} ]
        , 8:  [ {class: "vs", text: "The prophets who have been before me and before you of old prophesied against many countries, and against great kingdoms, of war, of evil, and of pestilence."} ]
        , 9:  [ {class: "vs", text: "The prophet who prophesies of peace, when the word of the prophet happens, then the prophet will be known, that Yahweh has truly sent him.”"} ]
        , 10: [ {class: "vs", text: "Then Hananiah the prophet took the bar from off the prophet Jeremiah’s neck, and broke it."} ]
        , 11: [ {class: "vs", text: "Hananiah spoke in the presence of all the people, saying, “Yahweh says: ‘Even so I will break the yoke of Nebuchadnezzar king of Babylon from off the neck of all the nations within two full years.’” Then the prophet Jeremiah went his way."} ]
        , 12: [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah, after Hananiah the prophet had broken the bar from off the neck of the prophet Jeremiah, saying,"} ]
        , 13: [ {class: "vs", text: "“Go, and tell Hananiah, saying, ‘Yahweh says, “You have broken the bars of wood, but you have made in their place bars of iron.”"} ]
        , 14: [ {class: "vs", text: "For Yahweh of Armies, the God of Israel says, “I have put a yoke of iron on the neck of all these nations, that they may serve Nebuchadnezzar king of Babylon; and they will serve him. I have also given him the animals of the field.”’”"} ]
        , 15: [ {class: "vs", text: "Then the prophet Jeremiah said to Hananiah the prophet, “Listen, Hananiah! Yahweh has not sent you, but you make this people trust in a lie."} ]
        , 16: [ {class: "vs", text: "Therefore Yahweh says, ‘Behold, I will send you away from off the surface of the earth. This year you will die, because you have spoken rebellion against Yahweh.’”"} ]
        , 17: [ {class: "vs", text: "So Hananiah the prophet died the same year in the seventh month."} ]
        }
  , 29: { 1:  [ {class: "vs", text: "Now these are the words of the letter that Jeremiah the prophet sent from Jerusalem to the residue of the elders of the captivity, and to the priests, to the prophets, and to all the people whom Nebuchadnezzar had carried away captive from Jerusalem to Babylon,"} ]
        , 2:  [ {class: "vs", text: "(after Jeconiah the king, the queen mother, the eunuchs, the princes of Judah and Jerusalem, the craftsmen, and the smiths, had departed from Jerusalem),"} ]
        , 3:  [ {class: "vs", text: "by the hand of Elasah the son of Shaphan, and Gemariah the son of Hilkiah, (whom Zedekiah king of Judah sent to Babylon to Nebuchadnezzar king of Babylon). It said:"} ]
        , 4:  [ {class: "vs", text: "Yahweh of Armies, the God of Israel, says to all the captives whom I have caused to be carried away captive from Jerusalem to Babylon:"} ]
        , 5:  [ {class: "vs", text: "“Build houses and dwell in them. Plant gardens and eat their fruit."} ]
        , 6:  [ {class: "vs", text: "Take wives and father sons and daughters. Take wives for your sons, and give your daughters to husbands, that they may bear sons and daughters. Multiply there, and don’t be diminished."} ]
        , 7:  [ {class: "vs", text: "Seek the peace of the city where I have caused you to be carried away captive, and pray to Yahweh for it; for in its peace you will have peace.”"} ]
        , 8:  [ {class: "vs", text: "For Yahweh of Armies, the God of Israel says: “Don’t let your prophets who are among you and your diviners deceive you. Don’t listen to your dreams which you cause to be dreamed."} ]
        , 9:  [ {class: "vs", text: "For they prophesy falsely to you in my name. I have not sent them,” says Yahweh."} ]
        , 10: [ {class: "vs", text: "For Yahweh says, “After seventy years are accomplished for Babylon, I will visit you and perform my good word toward you, in causing you to return to this place."} ]
        , 11: [ {class: "vs", text: "For I know the thoughts that I think toward you,” says Yahweh, “thoughts of peace, and not of evil, to give you hope and a future."} ]
        , 12: [ {class: "vs", text: "You shall call on me, and you shall go and pray to me, and I will listen to you."} ]
        , 13: [ {class: "vs", text: "You shall seek me, and find me, when you search for me with all your heart."} ]
        , 14: [ {class: "vs", text: "I will be found by you,” says Yahweh, “and I will turn again your captivity, and I will gather you from all the nations, and from all the places where I have driven you, says Yahweh. I will bring you again to the place from where I caused you to be carried away captive.”"} ]
        , 15: [ {class: "vs", text: "Because you have said, “Yahweh has raised us up prophets in Babylon;”"} ]
        , 16: [ {class: "vs", text: "Yahweh says concerning the king who sits on David’s throne, and concerning all the people who dwell in this city, your brothers who haven’t gone with you into captivity;"} ]
        , 17: [ {class: "vs", text: "Yahweh of Armies says: “Behold, I will send on them the sword, the famine, and the pestilence, and will make them like rotten figs that can’t be eaten, they are so bad."} ]
        , 18: [ {class: "vs", text: "I will pursue after them with the sword, with the famine, and with the pestilence, and will deliver them to be tossed back and forth among all the kingdoms of the earth, to be an object of horror, an astonishment, a hissing, and a reproach among all the nations where I have driven them,"} ]
        , 19: [ {class: "vs", text: "because they have not listened to my words,” says Yahweh, “with which I sent to them my servants the prophets, rising up early and sending them; but you would not hear,” says Yahweh."} ]
        , 20: [ {class: "vs", text: "Hear therefore Yahweh’s word, all you captives, whom I have sent away from Jerusalem to Babylon."} ]
        , 21: [ {class: "vs", text: "Yahweh of Armies, the God of Israel, says concerning Ahab the son of Kolaiah, and concerning Zedekiah the son of Maaseiah, who prophesy a lie to you in my name: “Behold, I will deliver them into the hand of Nebuchadnezzar king of Babylon; and he will kill them before your eyes."} ]
        , 22: [ {class: "vs", text: "A curse will be taken up about them by all the captives of Judah who are in Babylon, saying, ‘Yahweh make you like Zedekiah and like Ahab, whom the king of Babylon roasted in the fire;’"} ]
        , 23: [ {class: "vs", text: "because they have done foolish things in Israel, and have committed adultery with their neighbors’ wives, and have spoken words in my name falsely, which I didn’t command them. I am he who knows, and am witness,” says Yahweh."} ]
        , 24: [ {class: "vs", text: "Concerning Shemaiah the Nehelamite you shall speak, saying,"} ]
        , 25: [ {class: "vs", text: "“Yahweh of Armies, the God of Israel, says, ‘Because you have sent letters in your own name to all the people who are at Jerusalem, and to Zephaniah the son of Maaseiah, the priest, and to all the priests, saying,"} ]
        , 26: [ {class: "vs", text: "“Yahweh has made you priest in the place of Jehoiada the priest, that there may be officers in Yahweh’s house, for every man who is crazy, and makes himself a prophet, that you should put him in the stocks and in shackles."} ]
        , 27: [ {class: "vs", text: "Now therefore, why have you not rebuked Jeremiah of Anathoth, who makes himself a prophet to you,"} ]
        , 28: [ {class: "vs", text: "because he has sent to us in Babylon, saying, The captivity is long. Build houses, and dwell in them. Plant gardens, and eat their fruit?”’”"} ]
        , 29: [ {class: "vs", text: "Zephaniah the priest read this letter in the hearing of Jeremiah the prophet."} ]
        , 30: [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah, saying,"} ]
        , 31: [ {class: "vs", text: "“Send to all of the captives, saying, ‘Yahweh says concerning Shemaiah the Nehelamite: “Because Shemaiah has prophesied to you, and I didn’t send him, and he has caused you to trust in a lie;”"} ]
        , 32: [ {class: "vs", text: "therefore Yahweh says, “Behold, I will punish Shemaiah the Nehelamite and his offspring. He will not have a man to dwell among this people. He won’t see the good that I will do to my people,” says Yahweh, “because he has spoken rebellion against Yahweh.”’”"} ]
        }
  , 3:  { 1:  [ {class: "vs", text: "“They say, ‘If a man puts away his wife, and she goes from him, and becomes another man’s, should he return to her again?’ Wouldn’t that land be greatly polluted? But you have played the prostitute with many lovers; yet return again to me,” says Yahweh."} ]
        , 2:  [ {class: "vs", text: "“Lift up your eyes to the bare heights, and see! Where have you not been lain with? You have sat waiting for them by the road, as an Arabian in the wilderness. You have polluted the land with your prostitution and with your wickedness."} ]
        , 3:  [ {class: "vs", text: "Therefore the showers have been withheld and there has been no latter rain; yet you have had a prostitute’s forehead and you refused to be ashamed."} ]
        , 4:  [ {class: "vs", text: "Will you not from this time cry to me, ‘My Father, you are the guide of my youth?’"} ]
        , 5:  [ {class: "vs", text: "“‘Will he retain his anger forever? Will he keep it to the end?’ Behold, you have spoken and have done evil things, and have had your way.”"} ]
        , 6:  [ {class: "vs", text: "Moreover, Yahweh said to me in the days of Josiah the king, “Have you seen that which backsliding Israel has done? She has gone up on every high mountain and under every green tree, and has played the prostitute there."} ]
        , 7:  [ {class: "vs", text: "I said after she had done all these things, ‘She will return to me;’ but she didn’t return, and her treacherous sister Judah saw it."} ]
        , 8:  [ {class: "vs", text: "I saw when, for this very cause, that backsliding Israel had committed adultery, I had put her away and given her a certificate of divorce, yet treacherous Judah, her sister, had no fear; but she also went and played the prostitute."} ]
        , 9:  [ {class: "vs", text: "Because she took her prostitution lightly, the land was polluted, and she committed adultery with stones and with wood."} ]
        , 10: [ {class: "vs", text: "Yet for all this her treacherous sister, Judah, has not returned to me with her whole heart, but only in pretense,” says Yahweh."} ]
        , 11: [ {class: "vs", text: "Yahweh said to me, “Backsliding Israel has shown herself more righteous than treacherous Judah."} ]
        , 12: [ {class: "vs", text: "Go, and proclaim these words toward the north, and say, ‘Return, you backsliding Israel,’ says Yahweh; ‘I will not look in anger on you; for I am merciful,’ says Yahweh. ‘I will not keep anger forever."} ]
        , 13: [ {class: "vs", text: "Only acknowledge your iniquity, that you have transgressed against Yahweh your God, and have scattered your ways to the strangers under every green tree, and you have not obeyed my voice,’ says Yahweh.”"} ]
        , 14: [ {class: "vs", text: "“Return, backsliding children,” says Yahweh; “for I am a husband to you. I will take one of you from a city, and two from a family, and I will bring you to Zion."} ]
        , 15: [ {class: "vs", text: "I will give you shepherds according to my heart, who will feed you with knowledge and understanding."} ]
        , 16: [ {class: "vs", text: "It will come to pass, when you are multiplied and increased in the land, in those days,” says Yahweh, “they will no longer say, ‘the ark of Yahweh’s covenant!’ It will not come to mind. They won’t remember it. They won’t miss it, nor will another be made."} ]
        , 17: [ {class: "vs", text: "At that time they will call Jerusalem ‘Yahweh’s Throne;’ and all the nations will be gathered to it, to Yahweh’s name, to Jerusalem. They will no longer walk after the stubbornness of their evil heart."} ]
        , 18: [ {class: "vs", text: "In those days the house of Judah will walk with the house of Israel, and they will come together out of the land of the north to the land that I gave for an inheritance to your fathers."} ]
        , 19: [ {class: "vs", text: "“But I said, ‘How I desire to put you among the children, and give you a pleasant land, a goodly heritage of the armies of the nations!’ and I said, ‘You shall call me “My Father”, and shall not turn away from following me.’"} ]
        , 20: [ {class: "vs", text: "“Surely as a wife treacherously departs from her husband, so you have dealt treacherously with me, house of Israel,” says Yahweh."} ]
        , 21: [ {class: "vs", text: "A voice is heard on the bare heights, the weeping and the petitions of the children of Israel; because they have perverted their way, they have forgotten Yahweh their God."} ]
        , 22: [ {class: "vs", text: "Return, you backsliding children, and I will heal your backsliding.</p><p> “Behold, we have come to you; for you are Yahweh our God."} ]
        , 23: [ {class: "vs", text: "Truly help from the hills, the tumult on the mountains, is in vain. Truly the salvation of Israel is in Yahweh our God."} ]
        , 24: [ {class: "vs", text: "But the shameful thing has devoured the labor of our fathers from our youth, their flocks and their herds, their sons and their daughters."} ]
        , 25: [ {class: "vs", text: "Let us lie down in our shame, and let our confusion cover us; for we have sinned against Yahweh our God, we and our fathers, from our youth even to this day. We have not obeyed Yahweh our God’s voice.”"} ]
        }
  , 30: { 1:  [ {class: "vs", text: "The word that came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh, the God of Israel, says, ‘Write all the words that I have spoken to you in a book."} ]
        , 3:  [ {class: "vs", text: "For, behold, the days come,’ says Yahweh, ‘that I will reverse the captivity of my people Israel and Judah,’ says Yahweh. ‘I will cause them to return to the land that I gave to their fathers, and they will possess it.’”"} ]
        , 4:  [ {class: "vs", text: "These are the words that Yahweh spoke concerning Israel and concerning Judah."} ]
        , 5:  [ {class: "endpara", text: "For Yahweh says:"}
              , {class: "q", text: "“We have heard a voice of trembling;"}
              , {class: "q2", text: "a voice of fear, and not of peace."}
              ]
        , 6:  [ {class: "q", text: "Ask now, and see whether a man travails with child."}
              , {class: "q2", text: "Why do I see every man with his hands on his waist, as a woman in travail,"}
              , {class: "q2", text: "and all faces are turned pale?"}
              ]
        , 7:  [ {class: "q", text: "Alas! for that day is great, so that none is like it."}
              , {class: "q2", text: "It is even the time of Jacob’s trouble;"}
              , {class: "q2", text: "but he will be saved out of it."}
              ]
        , 8:  [ {class: "q", text: "It will come to pass in that day, says Yahweh of Armies, that I will break his yoke from off your neck,"}
              , {class: "q2", text: "and will burst your bonds."}
              , {class: "q2", text: "Strangers will no more make them their bondservants;"}
              ]
        , 9:  [ {class: "q", text: "but they will serve Yahweh their God,"}
              , {class: "q2", text: "and David their king,"}
              , {class: "q2", text: "whom I will raise up to them."}
              ]
        , 10: [ {class: "q", text: "Therefore don’t be afraid, O Jacob my servant, says Yahweh."}
              , {class: "q2", text: "Don’t be dismayed, Israel."}
              , {class: "q", text: "For, behold, I will save you from afar,"}
              , {class: "q2", text: "and save your offspring from the land of their captivity."}
              , {class: "q", text: "Jacob will return,"}
              , {class: "q2", text: "and will be quiet and at ease."}
              , {class: "q2", text: "No one will make him afraid."}
              ]
        , 11: [ {class: "q", text: "For I am with you, says Yahweh, to save you;"}
              , {class: "q2", text: "for I will make a full end of all the nations where I have scattered you,"}
              , {class: "q2", text: "but I will not make a full end of you;"}
              , {class: "q", text: "but I will correct you in measure,"}
              , {class: "q2", text: "and will in no way leave you unpunished.”"}
              ]
        , 12: [ {class: "", text: "For Yahweh says,"}
              , {class: "q", text: "“Your hurt is incurable."}
              , {class: "q2", text: "Your wound is grievous."}
              ]
        , 13: [ {class: "q", text: "There is no one to plead your cause,"}
              , {class: "q2", text: "that you may be bound up."}
              , {class: "q2", text: "You have no healing medicines."}
              ]
        , 14: [ {class: "q", text: "All your lovers have forgotten you."}
              , {class: "q2", text: "They don’t seek you."}
              , {class: "q", text: "For I have wounded you with the wound of an enemy,"}
              , {class: "q2", text: "with the chastisement of a cruel one,"}
              , {class: "q", text: "for the greatness of your iniquity,"}
              , {class: "q2", text: "because your sins were increased."}
              ]
        , 15: [ {class: "q", text: "Why do you cry over your injury?"}
              , {class: "q2", text: "Your pain is incurable."}
              , {class: "q", text: "For the greatness of your iniquity,"}
              , {class: "q2", text: "because your sins have increased,"}
              , {class: "q2", text: "I have done these things to you."}
              ]
        , 16: [ {class: "q", text: "Therefore all those who devour you will be devoured."}
              , {class: "q2", text: "All your adversaries, everyone of them, will go into captivity."}
              , {class: "q", text: "Those who plunder you will be plunder."}
              , {class: "q2", text: "I will make all who prey on you become prey."}
              ]
        , 17: [ {class: "q", text: "For I will restore health to you,"}
              , {class: "q2", text: "and I will heal you of your wounds,” says Yahweh;"}
              , {class: "q", text: "“because they have called you an outcast,"}
              , {class: "q2", text: "saying, ‘It is Zion, whom no man seeks after.’”"}
              ]
        , 18: [ {class: "", text: "Yahweh says:</p><div class=\"“Behold, I will reverse the captivity of Jacob’s tents,"}
              , {class: "q2", text: "and have compassion on his dwelling places."}
              , {class: "q", text: "The city will be built on its own hill,"}
              , {class: "q2", text: "and the palace will be inhabited in its own place."}
              ]
        , 19: [ {class: "q", text: "Thanksgiving will proceed out of them"}
              , {class: "q2", text: "with the voice of those who make merry."}
              , {class: "q", text: "I will multiply them,"}
              , {class: "q2", text: "and they will not be few;"}
              , {class: "q", text: "I will also glorify them,"}
              , {class: "q2", text: "and they will not be small."}
              ]
        , 20: [ {class: "q", text: "Their children also will be as before,"}
              , {class: "q2", text: "and their congregation will be established before me."}
              , {class: "q2", text: "I will punish all who oppress them."}
              ]
        , 21: [ {class: "q", text: "Their prince will be one of them,"}
              , {class: "q2", text: "and their ruler will proceed from among them."}
              , {class: "q", text: "I will cause him to draw near,"}
              , {class: "q2", text: "and he will approach me;"}
              , {class: "q2", text: "for who is he who has had boldness to approach me?” says Yahweh."}
              ]
        , 22: [ {class: "q", text: "“You shall be my people,"}
              , {class: "q2", text: "and I will be your God."}
              ]
        , 23: [ {class: "q", text: "Behold, Yahweh’s storm, his wrath, has gone out,"}
              , {class: "q2", text: "a sweeping storm:"}
              , {class: "q2", text: "it will burst on the head of the wicked."}
              ]
        , 24: [ {class: "q", text: "The fierce anger of Yahweh will not return until he has accomplished,"}
              , {class: "q2", text: "and until he has performed the intentions of his heart."}
              , {class: "q2", text: "In the latter days you will understand it.”"}
              ]
        }
  , 31: { 1:  [ {class: "vs", text: "“At that time,” says Yahweh, “I will be the God of all the families of Israel, and they will be my people.”"} ]
        , 2:  [ {class: "vs", text: "Yahweh says, “The people who survive the sword found favor in the wilderness; even Israel, when I went to cause him to rest.”"} ]
        , 3:  [ {class: "", text: "Yahweh appeared of old to me, saying,"}
              , {class: "q", text: "“Yes, I have loved you with an everlasting love."}
              , {class: "q2", text: "Therefore I have drawn you with loving kindness."}
              ]
        , 4:  [ {class: "q", text: "I will build you again,"}
              , {class: "q2", text: "and you will be built, O virgin of Israel."}
              , {class: "q", text: "You will again be adorned with your tambourines,"}
              , {class: "q2", text: "and will go out in the dances of those who make merry."}
              ]
        , 5:  [ {class: "q", text: "Again you will plant vineyards on the mountains of Samaria."}
              , {class: "q2", text: "The planters will plant,"}
              , {class: "q2", text: "and will enjoy its fruit."}
              ]
        , 6:  [ {class: "q", text: "For there will be a day that the watchmen on the hills of Ephraim cry,"}
              , {class: "q2", text: "‘Arise! Let’s go up to Zion to Yahweh our God.’”"}
              ]
        , 7:  [ {class: "endpara", text: "For Yahweh says,"}
              , {class: "q", text: "“Sing with gladness for Jacob,"}
              , {class: "q2", text: "and shout for the chief of the nations."}
              , {class: "q", text: "Publish, praise, and say,"}
              , {class: "q2", text: "‘Yahweh, save your people,"}
              , {class: "q2", text: "the remnant of Israel!’"}
              ]
        , 8:  [ {class: "q", text: "Behold, I will bring them from the north country,"}
              , {class: "q2", text: "and gather them from the uttermost parts of the earth,"}
              , {class: "q", text: "along with the blind and the lame,"}
              , {class: "q2", text: "the woman with child and her who travails with child together."}
              , {class: "q2", text: "They will return as a great company."}
              ]
        , 9:  [ {class: "q", text: "They will come with weeping."}
              , {class: "q2", text: "I will lead them with petitions."}
              , {class: "q", text: "I will cause them to walk by rivers of waters,"}
              , {class: "q2", text: "in a straight way in which they won’t stumble;"}
              , {class: "q", text: "for I am a father to Israel."}
              , {class: "q2", text: "Ephraim is my firstborn."}
              ]
        , 10: [ {class: "q", text: "“Hear Yahweh’s word, you nations,"}
              , {class: "q2", text: "and declare it in the distant islands. Say,"}
              , {class: "q", text: "‘He who scattered Israel will gather him,"}
              , {class: "q2", text: "and keep him, as a shepherd does his flock.’"}
              ]
        , 11: [ {class: "q", text: "For Yahweh has ransomed Jacob,"}
              , {class: "q2", text: "and redeemed him from the hand of him who was stronger than he."}
              ]
        , 12: [ {class: "q", text: "They will come and sing in the height of Zion,"}
              , {class: "q2", text: "and will flow to the goodness of Yahweh,"}
              , {class: "q", text: "to the grain, to the new wine, to the oil,"}
              , {class: "q2", text: "and to the young of the flock and of the herd."}
              , {class: "q", text: "Their soul will be as a watered garden."}
              , {class: "q2", text: "They will not sorrow any more at all."}
              ]
        , 13: [ {class: "q", text: "Then the virgin will rejoice in the dance;"}
              , {class: "q2", text: "the young men and the old together;"}
              , {class: "q", text: "for I will turn their mourning into joy,"}
              , {class: "q2", text: "and will comfort them, and make them rejoice from their sorrow."}
              ]
        , 14: [ {class: "q", text: "I will satiate the soul of the priests with fatness,"}
              , {class: "q2", text: "and my people will be satisfied with my goodness,” says Yahweh."}
              ]
        , 15: [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“A voice is heard in Ramah,"}
              , {class: "q2", text: "lamentation and bitter weeping,"}
              , {class: "q", text: "Rachel weeping for her children."}
              , {class: "q2", text: "She refuses to be comforted for her children,"}
              , {class: "q2", text: "because they are no more.”"}
              ]
        , 16: [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“Refrain your voice from weeping,"}
              , {class: "q2", text: "and your eyes from tears;"}
              , {class: "q2", text: "for your work will be rewarded,” says Yahweh."}
              , {class: "q2", text: "“They will come again from the land of the enemy."}
              ]
        , 17: [ {class: "q", text: "There is hope for your latter end,” says Yahweh."}
              , {class: "q2", text: "“Your children will come again to their own territory."}
              ]
        , 18: [ {class: "q", text: "“I have surely heard Ephraim grieving thus,"}
              , {class: "q2", text: "‘You have chastised me,"}
              , {class: "q2", text: "and I was chastised, as an untrained calf."}
              , {class: "q", text: "Turn me, and I will be turned;"}
              , {class: "q2", text: "for you are Yahweh my God."}
              ]
        , 19: [ {class: "q", text: "Surely after that I was turned."}
              , {class: "q2", text: "I repented."}
              , {class: "q", text: "After that I was instructed."}
              , {class: "q2", text: "I struck my thigh."}
              , {class: "q", text: "I was ashamed, yes, even confounded,"}
              , {class: "q2", text: "because I bore the reproach of my youth.’"}
              ]
        , 20: [ {class: "q", text: "Is Ephraim my dear son?"}
              , {class: "q2", text: "Is he a darling child?"}
              , {class: "q", text: "For as often as I speak against him,"}
              , {class: "q2", text: "I still earnestly remember him."}
              , {class: "q", text: "therefore my heart yearns for him."}
              , {class: "q2", text: "I will surely have mercy on him,” says Yahweh."}
              ]
        , 21: [ {class: "q", text: "“Set up road signs."}
              , {class: "q2", text: "Make guideposts."}
              , {class: "q", text: "Set your heart toward the highway,"}
              , {class: "q2", text: "even the way by which you went."}
              , {class: "q", text: "Turn again, virgin of Israel."}
              , {class: "q2", text: "Turn again to these your cities."}
              ]
        , 22: [ {class: "q", text: "How long will you go here and there,"}
              , {class: "q2", text: "you backsliding daughter?"}
              , {class: "q", text: "For Yahweh has created a new thing in the earth:"}
              , {class: "q2", text: "a woman will encompass a man.”"}
              ]
        , 23: [ {class: "vs", text: "Yahweh of Armies, the God of Israel, says: “Yet again they will use this speech in the land of Judah and in its cities, when I reverse their captivity: ‘Yahweh bless you, habitation of righteousness, mountain of holiness.’"} ]
        , 24: [ {class: "vs", text: "Judah and all its cities will dwell therein together, the farmers, and those who go about with flocks."} ]
        , 25: [ {class: "vs", text: "For I have satiated the weary soul, and I have replenished every sorrowful soul.”"} ]
        , 26: [ {class: "vs", text: "On this I awakened, and saw; and my sleep was sweet to me."} ]
        , 27: [ {class: "vs", text: "“Behold, the days come,” says Yahweh, “that I will sow the house of Israel and the house of Judah with the seed of man and with the seed of animal."} ]
        , 28: [ {class: "vs", text: "It will happen that, like as I have watched over them to pluck up and to break down and to overthrow and to destroy and to afflict, so I will watch over them to build and to plant,” says Yahweh."} ]
        , 29: [ {class: "endpara", text: "“In those days they will say no more,"}
              , {class: "q", text: "“‘The fathers have eaten sour grapes,"}
              , {class: "q2", text: "and the children’s teeth are set on edge.’"}
              ]
        , 30: [ {class: "vs", text: "But everyone will die for his own iniquity. Every man who eats the sour grapes, his teeth will be set on edge."} ]
        , 31: [ {class: "q", text: "“Behold, the days come,” says Yahweh,"}
              , {class: "q2", text: "“that I will make a new covenant with the house of Israel,"}
              , {class: "q2", text: "and with the house of Judah:"}
              ]
        , 32: [ {class: "q", text: "not according to the covenant that I made with their fathers in the day that I took them by the hand to bring them out of the land of Egypt;"}
              , {class: "q2", text: "which covenant of mine they broke,"}
              , {class: "q2", text: "although I was a husband to them,” says Yahweh."}
              ]
        , 33: [ {class: "q", text: "“But this is the covenant that I will make with the house of Israel after those days,”"}
              , {class: "q2", text: "says Yahweh:"}
              , {class: "q", text: "“I will put my law in their inward parts,"}
              , {class: "q2", text: "and I will write it in their heart."}
              , {class: "q", text: "I will be their God,"}
              , {class: "q2", text: "and they shall be my people."}
              ]
        , 34: [ {class: "q", text: "They will no longer each teach his neighbor,"}
              , {class: "q2", text: "and every man teach his brother, saying, ‘Know Yahweh;’"}
              , {class: "q", text: "for they will all know me,"}
              , {class: "q2", text: "from their least to their greatest,” says Yahweh:"}
              , {class: "q", text: "“for I will forgive their iniquity,"}
              , {class: "q2", text: "and I will remember their sin no more.”"}
              ]
        , 35: [ {class: "q", text: "Yahweh, who gives the sun for a light by day,"}
              , {class: "q2", text: "and the ordinances of the moon and of the stars for a light by night,"}
              , {class: "q", text: "who stirs up the sea, so that its waves roar;"}
              , {class: "q2", text: "Yahweh of Armies is his name, says:"}
              ]
        , 36: [ {class: "q", text: "“If these ordinances depart from before me,” says Yahweh,"}
              , {class: "q2", text: "“then the offspring of Israel also will cease from being a nation before me forever.”"}
              ]
        , 37: [ {class: "q", text: "Yahweh says: “If heaven above can be measured,"}
              , {class: "q2", text: "and the foundations of the earth searched out beneath,"}
              , {class: "q2", text: "then I will also cast off all the offspring of Israel for all that they have done,” says Yahweh."}
              ]
        , 38: [ {class: "vs", text: "“Behold, the days come,” says Yahweh, “that the city will be built to Yahweh from the tower of Hananel to the gate of the corner."} ]
        , 39: [ {class: "vs", text: "The measuring line will go out further straight onward to the hill Gareb, and will turn toward Goah."} ]
        , 40: [ {class: "vs", text: "The whole valley of the dead bodies and of the ashes, and all the fields to the brook Kidron, to the corner of the horse gate toward the east, will be holy to Yahweh. It will not be plucked up or thrown down any more forever.”"} ]
        }
  , 32: { 1:  [ {class: "vs", text: "The word that came to Jeremiah from Yahweh in the tenth year of Zedekiah king of Judah, which was the eighteenth year of Nebuchadnezzar."} ]
        , 2:  [ {class: "vs", text: "Now at that time the king of Babylon’s army was besieging Jerusalem. Jeremiah the prophet was shut up in the court of the guard, which was in the king of Judah’s house."} ]
        , 3:  [ {class: "vs", text: "For Zedekiah king of Judah had shut him up, saying, “Why do you prophesy, and say, ‘Yahweh says, “Behold, I will give this city into the hand of the king of Babylon, and he will take it;"} ]
        , 4:  [ {class: "vs", text: "and Zedekiah king of Judah won’t escape out of the hand of the Chaldeans, but will surely be delivered into the hand of the king of Babylon, and will speak with him mouth to mouth, and his eyes will see his eyes;"} ]
        , 5:  [ {class: "vs", text: "and he will bring Zedekiah to Babylon, and he will be there until I visit him,” says Yahweh: “though you fight with the Chaldeans, you will not prosper?”’”"} ]
        , 6:  [ {class: "vs", text: "Jeremiah said, “Yahweh’s word came to me, saying,"} ]
        , 7:  [ {class: "vs", text: "‘Behold, Hanamel the son of Shallum your uncle will come to you, saying, “Buy my field that is in Anathoth; for the right of redemption is yours to buy it.”’”"} ]
        , 8:  [ {class: "vs", text: "“So Hanamel my uncle’s son came to me in the court of the guard according to Yahweh’s word, and said to me, ‘Please buy my field that is in Anathoth, which is in the land of Benjamin; for the right of inheritance is yours, and the redemption is yours. Buy it for yourself.’</p><p> “Then I knew that this was Yahweh’s word."} ]
        , 9:  [ {class: "vs", text: "I bought the field that was in Anathoth of Hanamel my uncle’s son, and weighed him the money, even seventeen shekels of silver."} ]
        , 10: [ {class: "vs", text: "I signed the deed, sealed it, called witnesses, and weighed the money in the balances to him."} ]
        , 11: [ {class: "vs", text: "So I took the deed of the purchase, both that which was sealed, containing the terms and conditions, and that which was open;"} ]
        , 12: [ {class: "vs", text: "and I delivered the deed of the purchase to Baruch the son of Neriah, the son of Mahseiah, in the presence of Hanamel my uncle’s son, and in the presence of the witnesses who signed the deed of the purchase, before all the Jews who sat in the court of the guard."} ]
        , 13: [ {class: "vs", text: "“I commanded Baruch before them, saying,"} ]
        , 14: [ {class: "vs", text: "Yahweh of Armies, the God of Israel says: ‘Take these deeds, this deed of the purchase which is sealed, and this deed which is open, and put them in an earthen vessel, that they may last many days.’"} ]
        , 15: [ {class: "vs", text: "For Yahweh of Armies, the God of Israel says: ‘Houses and fields and vineyards will yet again be bought in this land.’"} ]
        , 16: [ {class: "vs", text: "Now after I had delivered the deed of the purchase to Baruch the son of Neriah, I prayed to Yahweh, saying,"} ]
        , 17: [ {class: "vs", text: "“Ah Lord Yahweh! Behold, you have made the heavens and the earth by your great power and by your outstretched arm. There is nothing too hard for you."} ]
        , 18: [ {class: "vs", text: "You show loving kindness to thousands, and repay the iniquity of the fathers into the bosom of their children after them. The great, the mighty God, Yahweh of Armies is your name:"} ]
        , 19: [ {class: "vs", text: "great in counsel, and mighty in work; whose eyes are open to all the ways of the children of men, to give everyone according to his ways, and according to the fruit of his doings;"} ]
        , 20: [ {class: "vs", text: "who performed signs and wonders in the land of Egypt, even to this day, both in Israel and among other men; and made yourself a name, as it is today;"} ]
        , 21: [ {class: "vs", text: "and brought your people Israel out of the land of Egypt with signs, with wonders, with a strong hand, with an outstretched arm, and with great terror;"} ]
        , 22: [ {class: "vs", text: "and gave them this land, which you swore to their fathers to give them, a land flowing with milk and honey."} ]
        , 23: [ {class: "vs", text: "They came in and possessed it, but they didn’t obey your voice and didn’t walk in your law. They have done nothing of all that you commanded them to do. Therefore you have caused all this evil to come upon them."} ]
        , 24: [ {class: "vs", text: "“Behold, siege ramps have come to the city to take it. The city is given into the hand of the Chaldeans who fight against it, because of the sword, of the famine, and of the pestilence. What you have spoken has happened. Behold, you see it."} ]
        , 25: [ {class: "vs", text: "You have said to me, Lord Yahweh, ‘Buy the field for money, and call witnesses;’ whereas the city is given into the hand of the Chaldeans.”"} ]
        , 26: [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah, saying,"} ]
        , 27: [ {class: "vs", text: "“Behold, I am Yahweh, the God of all flesh. Is there anything too hard for me?"} ]
        , 28: [ {class: "vs", text: "Therefore Yahweh says: Behold, I will give this city into the hand of the Chaldeans, and into the hand of Nebuchadnezzar king of Babylon, and he will take it."} ]
        , 29: [ {class: "vs", text: "The Chaldeans, who fight against this city, will come and set this city on fire, and burn it with the houses on whose roofs they have offered incense to Baal, and poured out drink offerings to other gods, to provoke me to anger."} ]
        , 30: [ {class: "vs", text: "“For the children of Israel and the children of Judah have done only that which was evil in my sight from their youth; for the children of Israel have only provoked me to anger with the work of their hands, says Yahweh."} ]
        , 31: [ {class: "vs", text: "For this city has been to me a provocation of my anger and of my wrath from the day that they built it even to this day, so that I should remove it from before my face,"} ]
        , 32: [ {class: "vs", text: "because of all the evil of the children of Israel and of the children of Judah, which they have done to provoke me to anger: they, their kings, their princes, their priests, their prophets, the men of Judah, and the inhabitants of Jerusalem."} ]
        , 33: [ {class: "vs", text: "They have turned their backs to me, and not their faces. Although I taught them, rising up early and teaching them, yet they have not listened to receive instruction."} ]
        , 34: [ {class: "vs", text: "But they set their abominations in the house which is called by my name, to defile it."} ]
        , 35: [ {class: "vs", text: "They built the high places of Baal, which are in the valley of the son of Hinnom, to cause their sons and their daughters to pass through fire to Molech, which I didn’t command them. It didn’t even come into my mind, that they should do this abomination, to cause Judah to sin.”"} ]
        , 36: [ {class: "vs", text: "Now therefore Yahweh, the God of Israel, says concerning this city, about which you say, “It is given into the hand of the king of Babylon by the sword, by the famine, and by the pestilence:”"} ]
        , 37: [ {class: "vs", text: "“Behold, I will gather them out of all the countries where I have driven them in my anger, and in my wrath, and in great indignation; and I will bring them again to this place. I will cause them to dwell safely."} ]
        , 38: [ {class: "vs", text: "Then they will be my people, and I will be their God."} ]
        , 39: [ {class: "vs", text: "I will give them one heart and one way, that they may fear me forever, for their good, and the good of their children after them."} ]
        , 40: [ {class: "vs", text: "I will make an everlasting covenant with them, that I will not turn away from following them, to do them good. I will put my fear in their hearts, that they may not depart from me."} ]
        , 41: [ {class: "vs", text: "Yes, I will rejoice over them to do them good, and I will plant them in this land assuredly with my whole heart and with my whole soul.”"} ]
        , 42: [ {class: "vs", text: "For Yahweh says: “Just as I have brought all this great evil on this people, so I will bring on them all the good that I have promised them."} ]
        , 43: [ {class: "vs", text: "Fields will be bought in this land, about which you say, ‘It is desolate, without man or animal. It is given into the hand of the Chaldeans.’"} ]
        , 44: [ {class: "vs", text: "Men will buy fields for money, sign the deeds, seal them, and call witnesses, in the land of Benjamin, and in the places around Jerusalem, in the cities of Judah, in the cities of the hill country, in the cities of the lowland, and in the cities of the South; for I will cause their captivity to be reversed,” says Yahweh."} ]
        }
  , 33: { 1:  [ {class: "vs", text: "Moreover Yahweh’s word came to Jeremiah the second time, while he was still locked up in the court of the guard, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh who does it, Yahweh who forms it to establish it—Yahweh is his name, says:"} ]
        , 3:  [ {class: "vs", text: "‘Call to me, and I will answer you, and will show you great and difficult things, which you don’t know.’"} ]
        , 4:  [ {class: "vs", text: "For Yahweh, the God of Israel, says concerning the houses of this city and concerning the houses of the kings of Judah, which are broken down to make a defense against the mounds and against the sword;"} ]
        , 5:  [ {class: "vs", text: "while men come to fight with the Chaldeans, and to fill them with the dead bodies of men, whom I have killed in my anger and in my wrath, and for all whose wickedness I have hidden my face from this city:"} ]
        , 6:  [ {class: "vs", text: "‘Behold, I will bring it health and cure, and I will cure them; and I will reveal to them abundance of peace and truth."} ]
        , 7:  [ {class: "vs", text: "I will cause the captivity of Judah and the captivity of Israel to return, and will build them, as at the first."} ]
        , 8:  [ {class: "vs", text: "I will cleanse them from all their iniquity by which they have sinned against me. I will pardon all their iniquities by which they have sinned against me and by which they have transgressed against me."} ]
        , 9:  [ {class: "vs", text: "This city will be to me for a name of joy, for praise, and for glory, before all the nations of the earth, which will hear all the good that I do to them, and will fear and tremble for all the good and for all the peace that I provide to it.’”"} ]
        , 10: [ {class: "vs", text: "Yahweh says: “Yet again there will be heard in this place, about which you say, ‘It is waste, without man and without animal, even in the cities of Judah, and in the streets of Jerusalem, that are desolate, without man and without inhabitant and without animal,’"} ]
        , 11: [ {class: "vs", text: "the voice of joy and the voice of gladness, the voice of the bridegroom and the voice of the bride, the voice of those who say, ‘Give thanks to Yahweh of Armies, for Yahweh is good, for his loving kindness endures forever;’ who bring thanksgiving into Yahweh’s house. For I will cause the captivity of the land to be reversed as at the first,” says Yahweh."} ]
        , 12: [ {class: "vs", text: "Yahweh of Armies says: “Yet again there will be in this place, which is waste, without man and without animal, and in all its cities, a habitation of shepherds causing their flocks to lie down."} ]
        , 13: [ {class: "vs", text: "In the cities of the hill country, in the cities of the lowland, in the cities of the South, in the land of Benjamin, in the places around Jerusalem, and in the cities of Judah, the flocks will again pass under the hands of him who counts them,” says Yahweh."} ]
        , 14: [ {class: "vs", text: "“Behold, the days come,” says Yahweh, “that I will perform that good word which I have spoken concerning the house of Israel and concerning the house of Judah."} ]
        , 15: [ {class: "q", text: "“In those days and at that time,"}
              , {class: "q2", text: "I will cause a Branch of righteousness to grow up to David."}
              , {class: "q2", text: "He will execute justice and righteousness in the land."}
              ]
        , 16: [ {class: "q", text: "In those days Judah will be saved,"}
              , {class: "q2", text: "and Jerusalem will dwell safely."}
              , {class: "q", text: "This is the name by which she will be called:"}
              , {class: "q2", text: "Yahweh our righteousness.”"}
              ]
        , 17: [ {class: "vs", text: "For Yahweh says: “David will never lack a man to sit on the throne of the house of Israel."} ]
        , 18: [ {class: "vs", text: "The Levitical priests won’t lack a man before me to offer burnt offerings, to burn meal offerings, and to do sacrifice continually.”"} ]
        , 19: [ {class: "vs", text: "Yahweh’s word came to Jeremiah, saying,"} ]
        , 20: [ {class: "vs", text: "“Yahweh says: ‘If you can break my covenant of the day and my covenant of the night, so that there will not be day and night in their time;"} ]
        , 21: [ {class: "vs", text: "then may my covenant also be broken with David my servant, that he won’t have a son to reign on his throne; and with the Levites the priests, my ministers."} ]
        , 22: [ {class: "vs", text: "As the army of the sky can’t be counted, and the sand of the sea can’t be measured; so I will multiply the offspring of David my servant and the Levites who minister to me.’”"} ]
        , 23: [ {class: "vs", text: "Yahweh’s word came to Jeremiah, saying,"} ]
        , 24: [ {class: "vs", text: "“Don’t consider what this people has spoken, saying, ‘Has Yahweh cast off the two families which he chose?’ Thus they despise my people, that they should be no more a nation before them.”"} ]
        , 25: [ {class: "vs", text: "Yahweh says: “If my covenant of day and night fails, if I have not appointed the ordinances of heaven and earth;"} ]
        , 26: [ {class: "vs", text: "then I will also cast away the offspring of Jacob, and of David my servant, so that I will not take of his offspring to be rulers over the offspring of Abraham, Isaac, and Jacob: for I will cause their captivity to be reversed and will have mercy on them.”"} ]
        }
  , 34: { 1:  [ {class: "vs", text: "The word which came to Jeremiah from Yahweh, when Nebuchadnezzar king of Babylon, with all his army, all the kingdoms of the earth that were under his dominion, and all the peoples, were fighting against Jerusalem and against all its cities, saying:"} ]
        , 2:  [ {class: "vs", text: "“Yahweh, the God of Israel, says, ‘Go, and speak to Zedekiah king of Judah, and tell him, Yahweh says, “Behold, I will give this city into the hand of the king of Babylon and he will burn it with fire."} ]
        , 3:  [ {class: "vs", text: "You won’t escape out of his hand, but will surely be taken and delivered into his hand. Your eyes will see the eyes of the king of Babylon, and he will speak with you mouth to mouth. You will go to Babylon.”’"} ]
        , 4:  [ {class: "vs", text: "“Yet hear Yahweh’s word, O Zedekiah king of Judah: Yahweh says concerning you, ‘You won’t die by the sword."} ]
        , 5:  [ {class: "vs", text: "You will die in peace; and with the burnings of your fathers, the former kings who were before you, so they will make a burning for you. They will lament you, saying, “Ah Lord!” for I have spoken the word,’ says Yahweh.”"} ]
        , 6:  [ {class: "vs", text: "Then Jeremiah the prophet spoke all these words to Zedekiah king of Judah in Jerusalem,"} ]
        , 7:  [ {class: "vs", text: "when the king of Babylon’s army was fighting against Jerusalem, and against all the cities of Judah that were left, against Lachish and against Azekah; for these alone remained of the cities of Judah as fortified cities."} ]
        , 8:  [ {class: "vs", text: "The word came to Jeremiah from Yahweh, after king Zedekiah had made a covenant with all the people who were at Jerusalem, to proclaim liberty to them;"} ]
        , 9:  [ {class: "vs", text: "that every man should let his male servant, and every man his female servant, who is a Hebrew or a Hebrewess, go free; that no one should make bondservants of them, of a Jew his brother."} ]
        , 10: [ {class: "vs", text: "All the princes and all the people obeyed who had entered into the covenant, that everyone should let his male servant, and everyone his female servant go free, that no one should make bondservants of them any more. They obeyed and let them go;"} ]
        , 11: [ {class: "vs", text: "but afterwards they turned, and caused the servants and the handmaids, whom they had let go free, to return, and brought them into subjection for servants and for handmaids."} ]
        , 12: [ {class: "vs", text: "Therefore Yahweh’s word came to Jeremiah from Yahweh, saying,"} ]
        , 13: [ {class: "vs", text: "“Yahweh, the God of Israel, says: ‘I made a covenant with your fathers in the day that I brought them out of the land of Egypt, out of the house of bondage, saying,"} ]
        , 14: [ {class: "vs", text: "At the end of seven years, every man of you shall release his brother who is a Hebrew, who has been sold to you, and has served you six years. You shall let him go free from you; but your fathers didn’t listen to me, and didn’t incline their ear."} ]
        , 15: [ {class: "vs", text: "You had now turned, and had done that which is right in my eyes, in every man proclaiming liberty to his neighbor. You had made a covenant before me in the house which is called by my name;"} ]
        , 16: [ {class: "vs", text: "but you turned and profaned my name, and every man caused his servant, and every man his handmaid, whom you had let go free at their pleasure, to return. You brought them into subjection, to be to you for servants and for handmaids.’”"} ]
        , 17: [ {class: "vs", text: "Therefore Yahweh says: “You have not listened to me, to proclaim liberty, every man to his brother, and every man to his neighbor. Behold, I proclaim to you a liberty,” says Yahweh, “to the sword, to the pestilence, and to the famine. I will make you be tossed back and forth among all the kingdoms of the earth."} ]
        , 18: [ {class: "vs", text: "I will give the men who have transgressed my covenant, who have not performed the words of the covenant which they made before me, when they cut the calf in two and passed between its parts:"} ]
        , 19: [ {class: "vs", text: "the princes of Judah, the princes of Jerusalem, the eunuchs, the priests, and all the people of the land, who passed between the parts of the calf;"} ]
        , 20: [ {class: "vs", text: "I will even give them into the hand of their enemies, and into the hand of those who seek their life. Their dead bodies will be food for the birds of the sky and for the animals of the earth."} ]
        , 21: [ {class: "vs", text: "“I will give Zedekiah king of Judah and his princes into the hands of their enemies, into the hands of those who seek their life and into the hands of the king of Babylon’s army, who has gone away from you."} ]
        , 22: [ {class: "vs", text: "Behold, I will command,” says Yahweh, “and cause them to return to this city. They will fight against it, take it, and burn it with fire. I will make the cities of Judah a desolation, without inhabitant.”"} ]
        }
  , 35: { 1:  [ {class: "vs", text: "The word which came to Jeremiah from Yahweh in the days of Jehoiakim the son of Josiah, king of Judah, saying,"} ]
        , 2:  [ {class: "vs", text: "“Go to the house of the Rechabites, and speak to them, and bring them into Yahweh’s house, into one of the rooms, and give them wine to drink.”"} ]
        , 3:  [ {class: "vs", text: "Then I took Jaazaniah the son of Jeremiah, the son of Habazziniah, with his brothers, all his sons, and the whole house of the Rechabites;"} ]
        , 4:  [ {class: "vs", text: "and I brought them into Yahweh’s house, into the room of the sons of Hanan the son of Igdaliah, the man of God, which was by the room of the princes, which was above the room of Maaseiah the son of Shallum, the keeper of the threshold."} ]
        , 5:  [ {class: "vs", text: "I set before the sons of the house of the Rechabites bowls full of wine, and cups; and I said to them, “Drink wine!”"} ]
        , 6:  [ {class: "vs", text: "But they said, “We will drink no wine; for Jonadab the son of Rechab, our father, commanded us, saying, ‘You shall drink no wine, neither you, nor your sons, forever."} ]
        , 7:  [ {class: "vs", text: "You shall not build a house, sow seed, plant a vineyard, or have any; but all your days you shall dwell in tents, that you may live many days in the land in which you live as nomads.’"} ]
        , 8:  [ {class: "vs", text: "We have obeyed the voice of Jonadab the son of Rechab, our father, in all that he commanded us, to drink no wine all our days, we, our wives, our sons, or our daughters;"} ]
        , 9:  [ {class: "vs", text: "and not to build houses for ourselves to dwell in. We have no vineyard, field, or seed;"} ]
        , 10: [ {class: "vs", text: "but we have lived in tents, and have obeyed, and done according to all that Jonadab our father commanded us."} ]
        , 11: [ {class: "vs", text: "But when Nebuchadnezzar king of Babylon came up into the land, we said, ‘Come! Let’s go to Jerusalem for fear of the army of the Chaldeans, and for fear of the army of the Syrians; so we will dwell at Jerusalem.’”"} ]
        , 12: [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah, saying,"} ]
        , 13: [ {class: "vs", text: "“Yahweh of Armies, the God of Israel, says: ‘Go and tell the men of Judah and the inhabitants of Jerusalem, “Will you not receive instruction to listen to my words?” says Yahweh."} ]
        , 14: [ {class: "vs", text: "“The words of Jonadab the son of Rechab that he commanded his sons, not to drink wine, are performed; and to this day they drink none, for they obey their father’s commandment; but I have spoken to you, rising up early and speaking, and you have not listened to me."} ]
        , 15: [ {class: "vs", text: "I have sent also to you all my servants the prophets, rising up early and sending them, saying, ‘Every one of you must return now from his evil way, amend your doings, and don’t go after other gods to serve them, then you will dwell in the land which I have given to you and to your fathers:’ but you have not inclined your ear, nor listened to me."} ]
        , 16: [ {class: "vs", text: "The sons of Jonadab the son of Rechab have performed the commandment of their father which he commanded them, but this people has not listened to me.”’"} ]
        , 17: [ {class: "vs", text: "“Therefore Yahweh, the God of Armies, the God of Israel, says: ‘Behold, I will bring on Judah and on all the inhabitants of Jerusalem all the evil that I have pronounced against them; because I have spoken to them, but they have not heard; and I have called to them, but they have not answered.’”"} ]
        , 18: [ {class: "vs", text: "Jeremiah said to the house of the Rechabites, “Yahweh of Armies, the God of Israel, says: ‘Because you have obeyed the commandment of Jonadab your father, and kept all his precepts, and done according to all that he commanded you;’"} ]
        , 19: [ {class: "vs", text: "therefore Yahweh of Armies, the God of Israel, says: ‘Jonadab the son of Rechab will not lack a man to stand before me forever.’”"} ]
        }
  , 36: { 1:  [ {class: "vs", text: "In the fourth year of Jehoiakim the son of Josiah, king of Judah, this word came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "“Take a scroll of a book, and write in it all the words that I have spoken to you against Israel, and against Judah, and against all the nations, from the day I spoke to you, from the days of Josiah, even to this day."} ]
        , 3:  [ {class: "vs", text: "It may be that the house of Judah will hear all the evil which I intend to do to them; that they may each return from his evil way; that I may forgive their iniquity and their sin.”"} ]
        , 4:  [ {class: "vs", text: "Then Jeremiah called Baruch the son of Neriah; and Baruch wrote from the mouth of Jeremiah all Yahweh’s words, which he had spoken to him, on a scroll of a book."} ]
        , 5:  [ {class: "vs", text: "Jeremiah commanded Baruch, saying, “I am restricted. I can’t go into Yahweh’s house."} ]
        , 6:  [ {class: "vs", text: "Therefore you go, and read from the scroll which you have written from my mouth, Yahweh’s words, in the ears of the people in Yahweh’s house on the fast day. Also you shall read them in the ears of all Judah who come out of their cities."} ]
        , 7:  [ {class: "vs", text: "It may be they will present their supplication before Yahweh, and will each return from his evil way; for Yahweh has pronounced great anger and wrath against this people.”"} ]
        , 8:  [ {class: "vs", text: "Baruch the son of Neriah did according to all that Jeremiah the prophet commanded him, reading in the book Yahweh’s words in Yahweh’s house."} ]
        , 9:  [ {class: "vs", text: "Now in the fifth year of Jehoiakim the son of Josiah, king of Judah, in the ninth month, all the people in Jerusalem, and all the people who came from the cities of Judah to Jerusalem, proclaimed a fast before Yahweh."} ]
        , 10: [ {class: "vs", text: "Then Baruch read the words of Jeremiah from the book in Yahweh’s house, in the room of Gemariah the son of Shaphan, the scribe, in the upper court, at the entry of the new gate of Yahweh’s house, in the ears of all the people."} ]
        , 11: [ {class: "vs", text: "When Micaiah the son of Gemariah, the son of Shaphan, had heard out of the book all Yahweh’s words,"} ]
        , 12: [ {class: "vs", text: "he went down into the king’s house, into the scribe’s room: and behold, all the princes were sitting there, Elishama the scribe, Delaiah the son of Shemaiah, Elnathan the son of Achbor, Gemariah the son of Shaphan, Zedekiah the son of Hananiah, and all the princes."} ]
        , 13: [ {class: "vs", text: "Then Micaiah declared to them all the words that he had heard, when Baruch read the book in the ears of the people."} ]
        , 14: [ {class: "vs", text: "Therefore all the princes sent Jehudi the son of Nethaniah, the son of Shelemiah, the son of Cushi, to Baruch, saying, “Take in your hand the scroll in which you have read in the ears of the people, and come.”</p><p> So Baruch the son of Neriah took the scroll in his hand, and came to them."} ]
        , 15: [ {class: "vs", text: "They said to him, “Sit down now, and read it in our hearing.”</p><p> So Baruch read it in their hearing."} ]
        , 16: [ {class: "vs", text: "Now when they had heard all the words, they turned in fear one toward another, and said to Baruch, “We will surely tell the king of all these words.”"} ]
        , 17: [ {class: "vs", text: "They asked Baruch, saying, “Tell us now, how did you write all these words at his mouth?”"} ]
        , 18: [ {class: "vs", text: "Then Baruch answered them, “He dictated all these words to me with his mouth, and I wrote them with ink in the book.”"} ]
        , 19: [ {class: "vs", text: "Then the princes said to Baruch, “You and Jeremiah go hide. Don’t let anyone know where you are.”"} ]
        , 20: [ {class: "vs", text: "They went in to the king into the court; but they had laid up the scroll in the room of Elishama the scribe. Then they told all the words in the hearing of the king."} ]
        , 21: [ {class: "vs", text: "So the king sent Jehudi to get the scroll; and he took it out of the room of Elishama the scribe. Jehudi read it in the hearing of the king, and in the hearing of all the princes who stood beside the king."} ]
        , 22: [ {class: "vs", text: "Now the king was sitting in the winter house in the ninth month; and there was a fire in the brazier burning before him."} ]
        , 23: [ {class: "vs", text: "When Jehudi had read three or four leaves, the king cut it with the penknife, and cast it into the fire that was in the brazier, until all the scroll was consumed in the fire that was in the brazier."} ]
        , 24: [ {class: "vs", text: "The king and his servants who heard all these words were not afraid, and didn’t tear their garments."} ]
        , 25: [ {class: "vs", text: "Moreover Elnathan and Delaiah and Gemariah had made intercession to the king that he would not burn the scroll; but he would not listen to them."} ]
        , 26: [ {class: "vs", text: "The king commanded Jerahmeel the king’s son, and Seraiah the son of Azriel, and Shelemiah the son of Abdeel, to arrest Baruch the scribe and Jeremiah the prophet; but Yahweh hid them."} ]
        , 27: [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah, after the king had burned the scroll, and the words which Baruch wrote at the mouth of Jeremiah, saying,"} ]
        , 28: [ {class: "vs", text: "“Take again another scroll, and write in it all the former words that were in the first scroll, which Jehoiakim the king of Judah has burned."} ]
        , 29: [ {class: "vs", text: "Concerning Jehoiakim king of Judah you shall say, ‘Yahweh says: “You have burned this scroll, saying, “Why have you written therein, saying, ‘The king of Babylon will certainly come and destroy this land, and will cause to cease from there man and animal?’”’"} ]
        , 30: [ {class: "vs", text: "Therefore Yahweh says concerning Jehoiakim king of Judah: “He will have no one to sit on David’s throne. His dead body will be cast out in the day to the heat, and in the night to the frost."} ]
        , 31: [ {class: "vs", text: "I will punish him, his offspring, and his servants for their iniquity. I will bring on them, on the inhabitants of Jerusalem, and on the men of Judah, all the evil that I have pronounced against them, but they didn’t listen.”’”"} ]
        , 32: [ {class: "vs", text: "Then took Jeremiah another scroll, and gave it to Baruch the scribe, the son of Neriah, who wrote therein from the mouth of Jeremiah all the words of the book which Jehoiakim king of Judah had burned in the fire; and many similar words were added to them."} ]
        }
  , 37: { 1:  [ {class: "vs", text: "Zedekiah the son of Josiah reigned as king, instead of Coniah the son of Jehoiakim, whom Nebuchadnezzar king of Babylon made king in the land of Judah."} ]
        , 2:  [ {class: "vs", text: "But neither he, nor his servants, nor the people of the land, listened to Yahweh’s words, which he spoke by the prophet Jeremiah."} ]
        , 3:  [ {class: "vs", text: "Zedekiah the king sent Jehucal the son of Shelemiah, and Zephaniah the son of Maaseiah, the priest, to the prophet Jeremiah, saying, “Pray now to Yahweh our God for us.”"} ]
        , 4:  [ {class: "vs", text: "Now Jeremiah came in and went out among the people; for they had not put him into prison."} ]
        , 5:  [ {class: "vs", text: "Pharaoh’s army had come out of Egypt; and when the Chaldeans who were besieging Jerusalem heard news of them, they broke up from Jerusalem."} ]
        , 6:  [ {class: "vs", text: "Then Yahweh’s word came to the prophet Jeremiah, saying,"} ]
        , 7:  [ {class: "vs", text: "“Yahweh, the God of Israel, says, ‘You shall tell the king of Judah, who sent you to me to inquire of me: “Behold, Pharaoh’s army, which has come out to help you, will return to Egypt into their own land."} ]
        , 8:  [ {class: "vs", text: "The Chaldeans will come again, and fight against this city. They will take it and burn it with fire.”’"} ]
        , 9:  [ {class: "vs", text: "“Yahweh says, ‘Don’t deceive yourselves, saying, “The Chaldeans will surely depart from us;” for they will not depart."} ]
        , 10: [ {class: "vs", text: "For though you had struck the whole army of the Chaldeans who fight against you, and only wounded men remained among them, they would each rise up in his tent and burn this city with fire.’”"} ]
        , 11: [ {class: "vs", text: "When the army of the Chaldeans had broken up from Jerusalem for fear of Pharaoh’s army,"} ]
        , 12: [ {class: "vs", text: "then Jeremiah went out of Jerusalem to go into the land of Benjamin, to receive his portion there, in the middle of the people."} ]
        , 13: [ {class: "vs", text: "When he was in Benjamin’s gate, a captain of the guard was there, whose name was Irijah, the son of Shelemiah, the son of Hananiah; and he seized Jeremiah the prophet, saying, “You are defecting to the Chaldeans!”"} ]
        , 14: [ {class: "vs", text: "Then Jeremiah said, “That is false! I am not defecting to the Chaldeans.”</p><p> But he didn’t listen to him; so Irijah seized Jeremiah, and brought him to the princes."} ]
        , 15: [ {class: "vs", text: "The princes were angry with Jeremiah, and struck him, and put him in prison in the house of Jonathan the scribe; for they had made that the prison."} ]
        , 16: [ {class: "vs", text: "When Jeremiah had come into the dungeon house, and into the cells, and Jeremiah had remained there many days,"} ]
        , 17: [ {class: "vs", text: "then Zedekiah the king sent, and had him brought out. The king asked him secretly in his house, “Is there any word from Yahweh?”</p><p> Jeremiah said, “There is.” He also said, “You will be delivered into the hand of the king of Babylon.”"} ]
        , 18: [ {class: "vs", text: "Moreover Jeremiah said to king Zedekiah, “How have I sinned against you, against your servants, or against this people, that you have put me in prison?"} ]
        , 19: [ {class: "vs", text: "Now where are your prophets who prophesied to you, saying, ‘The king of Babylon will not come against you, nor against this land?’"} ]
        , 20: [ {class: "vs", text: "Now please hear, my lord the king: please let my supplication be presented before you, that you not cause me to return to the house of Jonathan the scribe, lest I die there.”"} ]
        , 21: [ {class: "vs", text: "Then Zedekiah the king commanded, and they committed Jeremiah into the court of the guard. They gave him daily a loaf of bread out of the bakers’ street, until all the bread in the city was gone. Thus Jeremiah remained in the court of the guard."} ]
        }
  , 38: { 1:  [ {class: "vs", text: "Shephatiah the son of Mattan, and Gedaliah the son of Pashhur, and Jucal the son of Shelemiah, and Pashhur the son of Malchijah, heard the words that Jeremiah spoke to all the people, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh says, ‘He who remains in this city will die by the sword, by the famine, and by the pestilence; but he who goes out to the Chaldeans will live, and he will escape with his life, and he will live.’"} ]
        , 3:  [ {class: "vs", text: "Yahweh says, ‘This city will surely be given into the hand of the army of the king of Babylon, and he will take it.’”"} ]
        , 4:  [ {class: "vs", text: "Then the princes said to the king, “Please let this man be put to death; because he weakens the hands of the men of war who remain in this city, and the hands of all the people, in speaking such words to them: for this man doesn’t seek the welfare of this people, but harm.”"} ]
        , 5:  [ {class: "vs", text: "Zedekiah the king said, “Behold, he is in your hand; for the king can’t do anything to oppose you.”"} ]
        , 6:  [ {class: "vs", text: "Then they took Jeremiah and threw him into the dungeon of Malchijah the king’s son, that was in the court of the guard. They let down Jeremiah with cords. In the dungeon there was no water, but mire; and Jeremiah sank in the mire."} ]
        , 7:  [ {class: "vs", text: "Now when Ebedmelech the Ethiopian, a eunuch, who was in the king’s house, heard that they had put Jeremiah in the dungeon (the king was then sitting in Benjamin’s gate),"} ]
        , 8:  [ {class: "vs", text: "Ebedmelech went out of the king’s house, and spoke to the king, saying,"} ]
        , 9:  [ {class: "vs", text: "“My lord the king, these men have done evil in all that they have done to Jeremiah the prophet, whom they have cast into the dungeon. He is likely to die in the place where he is, because of the famine; for there is no more bread in the city.”"} ]
        , 10: [ {class: "vs", text: "Then the king commanded Ebedmelech the Ethiopian, saying, “Take from here thirty men with you, and take up Jeremiah the prophet out of the dungeon, before he dies.”"} ]
        , 11: [ {class: "vs", text: "So Ebedmelech took the men with him, and went into the house of the king under the treasury, and took from there rags and worn-out garments, and let them down by cords into the dungeon to Jeremiah."} ]
        , 12: [ {class: "vs", text: "Ebedmelech the Ethiopian said to Jeremiah, “Now put these rags and worn-out garments under your armpits under the cords.”</p><p> Jeremiah did so."} ]
        , 13: [ {class: "vs", text: "So they drew up Jeremiah with the cords, and took him up out of the dungeon; and Jeremiah remained in the court of the guard."} ]
        , 14: [ {class: "vs", text: "Then Zedekiah the king sent and took Jeremiah the prophet to himself into the third entry that is in Yahweh’s house. Then the king said to Jeremiah, “I will ask you something. Hide nothing from me.”"} ]
        , 15: [ {class: "vs", text: "Then Jeremiah said to Zedekiah, “If I declare it to you, will you not surely put me to death? If I give you counsel, you will not listen to me.”"} ]
        , 16: [ {class: "vs", text: "So Zedekiah the king swore secretly to Jeremiah, saying, “As Yahweh lives, who made us this soul, I will not put you to death, neither will I give you into the hand of these men who seek your life.”"} ]
        , 17: [ {class: "vs", text: "Then Jeremiah said to Zedekiah, “Yahweh, the God of Armies, the God of Israel, says: ‘If you will go out to the king of Babylon’s princes, then your soul will live, and this city will not be burned with fire. You will live, along with your house."} ]
        , 18: [ {class: "vs", text: "But if you will not go out to the king of Babylon’s princes, then this city will be given into the hand of the Chaldeans, and they will burn it with fire, and you won’t escape out of their hand.’”"} ]
        , 19: [ {class: "vs", text: "Zedekiah the king said to Jeremiah, “I am afraid of the Jews who have defected to the Chaldeans, lest they deliver me into their hand, and they mock me.”"} ]
        , 20: [ {class: "vs", text: "But Jeremiah said, “They won’t deliver you. Obey, I beg you, Yahweh’s voice, in that which I speak to you; so it will be well with you, and your soul will live."} ]
        , 21: [ {class: "vs", text: "But if you refuse to go out, this is the word that Yahweh has shown me:"} ]
        , 22: [ {class: "endpara", text: "‘Behold, all the women who are left in the king of Judah’s house will be brought out to the king of Babylon’s princes, and those women will say,"}
              , {class: "q", text: "“Your familiar friends have turned on you,"}
              , {class: "q2", text: "and have prevailed over you."}
              , {class: "q", text: "Your feet are sunk in the mire,"}
              , {class: "q2", text: "they have turned away from you.”"}
              ]
        , 23: [ {class: "vs", text: "They will bring out all your wives and your children to the Chaldeans. You won’t escape out of their hand, but will be taken by the hand of the king of Babylon. You will cause this city to be burned with fire.’”"} ]
        , 24: [ {class: "vs", text: "Then Zedekiah said to Jeremiah, “Let no man know of these words, and you won’t die."} ]
        , 25: [ {class: "vs", text: "But if the princes hear that I have talked with you, and they come to you, and tell you, ‘Declare to us now what you have said to the king; don’t hide it from us, and we will not put you to death; also tell us what the king said to you;’"} ]
        , 26: [ {class: "vs", text: "then you shall tell them, ‘I presented my supplication before the king, that he would not cause me to return to Jonathan’s house, to die there.’”"} ]
        , 27: [ {class: "vs", text: "Then all the princes came to Jeremiah, and asked him; and he told them according to all these words that the king had commanded. So they stopped speaking with him; for the matter was not perceived."} ]
        , 28: [ {class: "vs", text: "So Jeremiah stayed in the court of the guard until the day that Jerusalem was taken."} ]
        }
  , 39: { 1:  [ {class: "vs", text: "In the ninth year of Zedekiah king of Judah, in the tenth month, Nebuchadnezzar king of Babylon and all his army came against Jerusalem, and besieged it."} ]
        , 2:  [ {class: "vs", text: "In the eleventh year of Zedekiah, in the fourth month, the ninth day of the month, a breach was made in the city."} ]
        , 3:  [ {class: "vs", text: "All the princes of the king of Babylon came in, and sat in the middle gate, Nergal Sharezer, Samgarnebo, Sarsechim, Rabsaris, Nergal Sharezer, Rabmag, with all the rest of the princes of the king of Babylon."} ]
        , 4:  [ {class: "vs", text: "When Zedekiah the king of Judah and all the men of war saw them, then they fled, and went out of the city by night, by the way of the king’s garden, through the gate between the two walls; and he went out toward the Arabah."} ]
        , 5:  [ {class: "vs", text: "But the army of the Chaldeans pursued them, and overtook Zedekiah in the plains of Jericho. When they had taken him, they brought him up to Nebuchadnezzar king of Babylon to Riblah in the land of Hamath; and he pronounced judgment on him."} ]
        , 6:  [ {class: "vs", text: "Then the king of Babylon killed Zedekiah’s sons in Riblah before his eyes. The king of Babylon also killed all the nobles of Judah."} ]
        , 7:  [ {class: "vs", text: "Moreover he put out Zedekiah’s eyes and bound him in fetters, to carry him to Babylon."} ]
        , 8:  [ {class: "vs", text: "The Chaldeans burned the king’s house, and the houses of the people, with fire, and broke down the walls of Jerusalem."} ]
        , 9:  [ {class: "vs", text: "Then Nebuzaradan the captain of the guard carried away captive into Babylon the residue of the people who remained in the city, the deserters also who fell away to him, and the residue of the people who remained."} ]
        , 10: [ {class: "vs", text: "But Nebuzaradan the captain of the guard left of the poor of the people, who had nothing, in the land of Judah, and gave them vineyards and fields at the same time."} ]
        , 11: [ {class: "vs", text: "Now Nebuchadnezzar king of Babylon commanded Nebuzaradan the captain of the guard concerning Jeremiah, saying,"} ]
        , 12: [ {class: "vs", text: "“Take him, and take care of him. Do him no harm; but do to him even as he tells you.”"} ]
        , 13: [ {class: "vs", text: "So Nebuzaradan the captain of the guard sent, with Nebushazban, Rabsaris, and Nergal Sharezer, Rabmag, and all the chief officers of the king of Babylon;"} ]
        , 14: [ {class: "vs", text: "they sent, and took Jeremiah out of the court of the guard, and committed him to Gedaliah the son of Ahikam, the son of Shaphan, that he should carry him home. So he lived among the people."} ]
        , 15: [ {class: "vs", text: "Now Yahweh’s word came to Jeremiah, while he was shut up in the court of the guard, saying,"} ]
        , 16: [ {class: "vs", text: "“Go, and speak to Ebedmelech the Ethiopian, saying, ‘Yahweh of Armies, the God of Israel, says: “Behold, I will bring my words on this city for evil, and not for good; and they will be accomplished before you in that day."} ]
        , 17: [ {class: "vs", text: "But I will deliver you in that day,” says Yahweh; “and you will not be given into the hand of the men of whom you are afraid."} ]
        , 18: [ {class: "vs", text: "For I will surely save you, and you won’t fall by the sword, but you will escape with your life; because you have put your trust in me,” says Yahweh.’”"} ]
        }
  , 4:  { 1:  [ {class: "vs", text: "“If you will return, Israel,” says Yahweh, “if you will return to me, and if you will put away your abominations out of my sight; then you will not be removed;"} ]
        , 2:  [ {class: "vs", text: "and you will swear, ‘As Yahweh lives,’ in truth, in justice, and in righteousness. The nations will bless themselves in him, and they will glory in him.”"} ]
        , 3:  [ {class: "vs", text: "For Yahweh says to the men of Judah and to Jerusalem, “Break up your fallow ground, and don’t sow among thorns."} ]
        , 4:  [ {class: "vs", text: "Circumcise yourselves to Yahweh, and take away the foreskins of your heart, you men of Judah and inhabitants of Jerusalem; lest my wrath go out like fire, and burn so that no one can quench it, because of the evil of your doings."} ]
        , 5:  [ {class: "vs", text: "Declare in Judah, and publish in Jerusalem; and say, ‘Blow the trumpet in the land!’ Cry aloud and say, ‘Assemble yourselves! Let’s go into the fortified cities!’"} ]
        , 6:  [ {class: "vs", text: "Set up a standard toward Zion. Flee for safety! Don’t wait; for I will bring evil from the north, and a great destruction.”"} ]
        , 7:  [ {class: "vs", text: "A lion has gone up from his thicket, and a destroyer of nations. He is on his way. He has gone out from his place, to make your land desolate, that your cities be laid waste, without inhabitant."} ]
        , 8:  [ {class: "vs", text: "For this, clothe yourself with sackcloth, lament and wail; for the fierce anger of Yahweh hasn’t turned back from us."} ]
        , 9:  [ {class: "vs", text: "“It will happen at that day,” says Yahweh, “that the heart of the king will perish, along with the heart of the princes. The priests will be astonished, and the prophets will wonder.”"} ]
        , 10: [ {class: "vs", text: "Then I said, “Ah, Lord Yahweh! Surely you have greatly deceived this people and Jerusalem, saying, ‘You will have peace;’ whereas the sword reaches to the heart.”"} ]
        , 11: [ {class: "vs", text: "At that time it will be said to this people and to Jerusalem, “A hot wind from the bare heights in the wilderness toward the daughter of my people, not to winnow, nor to cleanse;"} ]
        , 12: [ {class: "vs", text: "a full wind from these will come for me. Now I will also utter judgments against them.”"} ]
        , 13: [ {class: "vs", text: "Behold, he will come up as clouds, and his chariots will be as the whirlwind. His horses are swifter than eagles. Woe to us! For we are ruined."} ]
        , 14: [ {class: "vs", text: "Jerusalem, wash your heart from wickedness, that you may be saved. How long will your evil thoughts lodge within you?"} ]
        , 15: [ {class: "vs", text: "For a voice declares from Dan, and publishes evil from the hills of Ephraim:"} ]
        , 16: [ {class: "vs", text: "“Tell the nations, behold, publish against Jerusalem, ‘Watchers come from a far country, and raise their voice against the cities of Judah."} ]
        , 17: [ {class: "vs", text: "As keepers of a field, they are against her all around, because she has been rebellious against me,’” says Yahweh."} ]
        , 18: [ {class: "vs", text: "“Your way and your doings have brought these things to you. This is your wickedness; for it is bitter, for it reaches to your heart.”"} ]
        , 19: [ {class: "vs", text: "My anguish, my anguish! I am pained at my very heart! My heart trembles within me. I can’t hold my peace, because you have heard, O my soul, the sound of the trumpet, the alarm of war."} ]
        , 20: [ {class: "vs", text: "Destruction on destruction is decreed, for the whole land is laid waste. Suddenly my tents are destroyed, and my curtains gone in a moment."} ]
        , 21: [ {class: "vs", text: "How long will I see the standard and hear the sound of the trumpet?"} ]
        , 22: [ {class: "vs", text: "“For my people are foolish. They don’t know me. They are foolish children, and they have no understanding. They are skillful in doing evil, but they don’t know how to do good.”"} ]
        , 23: [ {class: "vs", text: "I saw the earth, and, behold, it was waste and void, and the heavens, and they had no light."} ]
        , 24: [ {class: "vs", text: "I saw the mountains, and behold, they trembled, and all the hills moved back and forth."} ]
        , 25: [ {class: "vs", text: "I saw, and behold, there was no man, and all the birds of the sky had fled."} ]
        , 26: [ {class: "vs", text: "I saw, and behold, the fruitful field was a wilderness, and all its cities were broken down at the presence of Yahweh, before his fierce anger."} ]
        , 27: [ {class: "vs", text: "For Yahweh says, “The whole land will be a desolation; yet I will not make a full end."} ]
        , 28: [ {class: "vs", text: "For this the earth will mourn, and the heavens above be black, because I have spoken it. I have planned it, and I have not repented, neither will I turn back from it.”"} ]
        , 29: [ {class: "vs", text: "Every city flees for the noise of the horsemen and archers. They go into the thickets, and climb up on the rocks. Every city is forsaken, and not a man dwells therein."} ]
        , 30: [ {class: "vs", text: "You, when you are made desolate, what will you do? Though you clothe yourself with scarlet, though you deck yourself with ornaments of gold, though you enlarge your eyes with makeup, you make yourself beautiful in vain. Your lovers despise you. They seek your life."} ]
        , 31: [ {class: "vs", text: "For I have heard a voice as of a woman in travail, the anguish as of her who gives birth to her first child, the voice of the daughter of Zion, who gasps for breath, who spreads her hands, saying, “Woe is me now! For my soul faints before the murderers.”"} ]
        }
  , 40: { 1:  [ {class: "vs", text: "The word which came to Jeremiah from Yahweh, after Nebuzaradan the captain of the guard had let him go from Ramah, when he had taken him being bound in chains among all the captives of Jerusalem and Judah, who were carried away captive to Babylon."} ]
        , 2:  [ {class: "vs", text: "The captain of the guard took Jeremiah, and said to him, “Yahweh your God pronounced this evil on this place;"} ]
        , 3:  [ {class: "vs", text: "and Yahweh has brought it, and done according as he spoke. Because you have sinned against Yahweh, and have not obeyed his voice, therefore this thing has come on you."} ]
        , 4:  [ {class: "vs", text: "Now, behold, I release you today from the chains which are on your hand. If it seems good to you to come with me into Babylon, come, and I will take care of you; but if it seems bad to you to come with me into Babylon, don’t. Behold, all the land is before you. Where it seems good and right to you to go, there go.”"} ]
        , 5:  [ {class: "vs", text: "Now while he had not yet gone back, “Go back then,” he said, “to Gedaliah the son of Ahikam, the son of Shaphan, whom the king of Babylon has made governor over the cities of Judah, and dwell with him among the people; or go wherever it seems right to you to go.”</p><p> So the captain of the guard gave him food and a present, and let him go."} ]
        , 6:  [ {class: "vs", text: "Then Jeremiah went to Gedaliah the son of Ahikam to Mizpah, and lived with him among the people who were left in the land."} ]
        , 7:  [ {class: "vs", text: "Now when all the captains of the forces who were in the fields, even they and their men, heard that the king of Babylon had made Gedaliah the son of Ahikam governor in the land, and had committed to him men, women, children, and of the poorest of the land, of those who were not carried away captive to Babylon;"} ]
        , 8:  [ {class: "vs", text: "then Ishmael the son of Nethaniah, and Johanan and Jonathan the sons of Kareah, and Seraiah the son of Tanhumeth, and the sons of Ephai the Netophathite, and Jezaniah the son of the Maacathite, they and their men came to Gedaliah to Mizpah."} ]
        , 9:  [ {class: "vs", text: "Gedaliah the son of Ahikam the son of Shaphan swore to them and to their men, saying, “Don’t be afraid to serve the Chaldeans. Dwell in the land, and serve the king of Babylon, and it will be well with you."} ]
        , 10: [ {class: "vs", text: "As for me, behold, I will dwell at Mizpah, to stand before the Chaldeans who will come to us; but you, gather wine and summer fruits and oil, and put them in your vessels, and dwell in your cities that you have taken.”"} ]
        , 11: [ {class: "vs", text: "Likewise when all the Jews who were in Moab, and among the children of Ammon, and in Edom, and who were in all the countries, heard that the king of Babylon had left a remnant of Judah, and that he had set over them Gedaliah the son of Ahikam, the son of Shaphan;"} ]
        , 12: [ {class: "vs", text: "then all the Jews returned out of all places where they were driven, and came to the land of Judah, to Gedaliah, to Mizpah, and gathered very much wine and summer fruits."} ]
        , 13: [ {class: "vs", text: "Moreover Johanan the son of Kareah, and all the captains of the forces who were in the fields, came to Gedaliah to Mizpah,"} ]
        , 14: [ {class: "vs", text: "and said to him, “Do you know that Baalis the king of the children of Ammon has sent Ishmael the son of Nethaniah to take your life?”</p><p> But Gedaliah the son of Ahikam didn’t believe them."} ]
        , 15: [ {class: "vs", text: "Then Johanan the son of Kareah spoke to Gedaliah in Mizpah secretly, saying, “Please let me go, and I will kill Ishmael the son of Nethaniah, and no man will know it. Why should he take your life, that all the Jews who are gathered to you should be scattered, and the remnant of Judah perish?”"} ]
        , 16: [ {class: "vs", text: "But Gedaliah the son of Ahikam said to Johanan the son of Kareah, “You shall not do this thing; for you speak falsely of Ishmael.”"} ]
        }
  , 41: { 1:  [ {class: "vs", text: "Now in the seventh month, Ishmael the son of Nethaniah, the son of Elishama, of the royal offspring and one of the chief officers of the king, and ten men with him, came to Gedaliah the son of Ahikam to Mizpah; and there they ate bread together in Mizpah."} ]
        , 2:  [ {class: "vs", text: "Then Ishmael the son of Nethaniah arose, and the ten men who were with him, and struck Gedaliah the son of Ahikam the son of Shaphan with the sword and killed him, whom the king of Babylon had made governor over the land."} ]
        , 3:  [ {class: "vs", text: "Ishmael also killed all the Jews who were with him, with Gedaliah, at Mizpah, and the Chaldeans who were found there, the men of war."} ]
        , 4:  [ {class: "vs", text: "The second day after he had killed Gedaliah, and no man knew it,"} ]
        , 5:  [ {class: "vs", text: "men came from Shechem, from Shiloh, and from Samaria, even eighty men, having their beards shaved and their clothes torn, and having cut themselves, with meal offerings and frankincense in their hand, to bring them to Yahweh’s house."} ]
        , 6:  [ {class: "vs", text: "Ishmael the son of Nethaniah went out from Mizpah to meet them, weeping all along as he went: and as he met them, he said to them, “Come to Gedaliah the son of Ahikam.”"} ]
        , 7:  [ {class: "vs", text: "It was so, when they came into the middle of the city, that Ishmael the son of Nethaniah killed them, and cast them into the middle of the pit, he, and the men who were with him."} ]
        , 8:  [ {class: "vs", text: "But ten men were found among those who said to Ishmael, “Don’t kill us; for we have stores hidden in the field, of wheat, and of barley, and of oil, and of honey.”</p><p> So he stopped, and didn’t kill them among their brothers."} ]
        , 9:  [ {class: "vs", text: "Now the pit in which Ishmael cast all the dead bodies of the men whom he had killed, by the side of Gedaliah (this was that which Asa the king had made for fear of Baasha king of Israel), Ishmael the son of Nethaniah filled it with those who were killed."} ]
        , 10: [ {class: "vs", text: "Then Ishmael carried away captive all of the people who were left in Mizpah, even the king’s daughters, and all the people who remained in Mizpah, whom Nebuzaradan the captain of the guard had committed to Gedaliah the son of Ahikam. Ishmael the son of Nethaniah carried them away captive, and departed to go over to the children of Ammon."} ]
        , 11: [ {class: "vs", text: "But when Johanan the son of Kareah, and all the captains of the forces who were with him, heard of all the evil that Ishmael the son of Nethaniah had done,"} ]
        , 12: [ {class: "vs", text: "then they took all the men, and went to fight with Ishmael the son of Nethaniah, and found him by the great waters that are in Gibeon."} ]
        , 13: [ {class: "vs", text: "Now when all the people who were with Ishmael saw Johanan the son of Kareah, and all the captains of the forces who were with him, then they were glad."} ]
        , 14: [ {class: "vs", text: "So all the people who Ishmael had carried away captive from Mizpah turned about and came back, and went to Johanan the son of Kareah."} ]
        , 15: [ {class: "vs", text: "But Ishmael the son of Nethaniah escaped from Johanan with eight men, and went to the children of Ammon."} ]
        , 16: [ {class: "vs", text: "Then Johanan the son of Kareah and all the captains of the forces who were with him took all the remnant of the people whom he had recovered from Ishmael the son of Nethaniah, from Mizpah, after he had killed Gedaliah the son of Ahikam, the men of war, with the women, the children, and the eunuchs, whom he had brought back from Gibeon."} ]
        , 17: [ {class: "vs", text: "They departed and lived in Geruth Chimham, which is by Bethlehem, to go to enter into Egypt"} ]
        , 18: [ {class: "vs", text: "because of the Chaldeans; for they were afraid of them, because Ishmael the son of Nethaniah had killed Gedaliah the son of Ahikam, whom the king of Babylon made governor over the land."} ]
        }
  , 42: { 1:  [ {class: "vs", text: "Then all the captains of the forces, and Johanan the son of Kareah, and Jezaniah the son of Hoshaiah, and all the people from the least even to the greatest, came near,"} ]
        , 2:  [ {class: "vs", text: "and said to Jeremiah the prophet, “Please let our supplication be presented before you, and pray for us to Yahweh your God, even for all this remnant; for we are left but a few of many, as your eyes see us;"} ]
        , 3:  [ {class: "vs", text: "that Yahweh your God may show us the way in which we should walk, and the things that we should do.”"} ]
        , 4:  [ {class: "vs", text: "Then Jeremiah the prophet said to them, “I have heard you. Behold, I will pray to Yahweh your God according to your words; and it will happen that whatever thing Yahweh answers you, I will declare it to you. I will keep nothing back from you.”"} ]
        , 5:  [ {class: "vs", text: "Then they said to Jeremiah, “May Yahweh be a true and faithful witness among us, if we don’t do according to all the word with which Yahweh your God sends you to tell us."} ]
        , 6:  [ {class: "vs", text: "Whether it is good, or whether it is bad, we will obey the voice of Yahweh our God, to whom we send you; that it may be well with us, when we obey the voice of Yahweh our God.”"} ]
        , 7:  [ {class: "vs", text: "After ten days, Yahweh’s word came to Jeremiah."} ]
        , 8:  [ {class: "vs", text: "Then he called Johanan the son of Kareah, and all the captains of the forces who were with him, and all the people from the least even to the greatest,"} ]
        , 9:  [ {class: "vs", text: "and said to them, “Yahweh, the God of Israel, to whom you sent me to present your supplication before him, says:"} ]
        , 10: [ {class: "vs", text: "‘If you will still live in this land, then I will build you, and not pull you down, and I will plant you, and not pluck you up; for I grieve over the distress that I have brought on you."} ]
        , 11: [ {class: "vs", text: "Don’t be afraid of the king of Babylon, of whom you are afraid. Don’t be afraid of him,’ says Yahweh: ‘for I am with you to save you, and to deliver you from his hand."} ]
        , 12: [ {class: "vs", text: "I will grant you mercy, that he may have mercy on you, and cause you to return to your own land."} ]
        , 13: [ {class: "vs", text: "“‘But if you say, “We will not dwell in this land;” so that you don’t obey Yahweh your God’s voice,"} ]
        , 14: [ {class: "vs", text: "saying, “No; but we will go into the land of Egypt, where we will see no war, nor hear the sound of the trumpet, nor have hunger of bread; and there will we dwell:”’"} ]
        , 15: [ {class: "vs", text: "now therefore hear Yahweh’s word, O remnant of Judah: Yahweh of Armies, the God of Israel, says, ‘If you indeed set your faces to enter into Egypt, and go to live there;"} ]
        , 16: [ {class: "vs", text: "then it will happen that the sword, which you fear, will overtake you there in the land of Egypt; and the famine, about which you are afraid, will follow close behind you there in Egypt; and you will die there."} ]
        , 17: [ {class: "vs", text: "So will it be with all the men who set their faces to go into Egypt to live there. They will die by the sword, by the famine, and by the pestilence. None of them will remain or escape from the evil that I will bring on them.’"} ]
        , 18: [ {class: "vs", text: "For Yahweh of Armies, the God of Israel, says: ‘As my anger and my wrath has been poured out on the inhabitants of Jerusalem, so my wrath will be poured out on you, when you enter into Egypt; and you will be an object of horror, an astonishment, a curse, and a reproach; and you will see this place no more.’"} ]
        , 19: [ {class: "vs", text: "“Yahweh has spoken concerning you, remnant of Judah, ‘Don’t go into Egypt!’ Know certainly that I have testified to you today."} ]
        , 20: [ {class: "vs", text: "For you have dealt deceitfully against your own souls; for you sent me to Yahweh your God, saying, ‘Pray for us to Yahweh our God; and according to all that Yahweh our God says, so declare to us, and we will do it.’"} ]
        , 21: [ {class: "vs", text: "I have declared it to you today; but you have not obeyed Yahweh your God’s voice in anything for which he has sent me to you."} ]
        , 22: [ {class: "vs", text: "Now therefore know certainly that you will die by the sword, by the famine, and by the pestilence in the place where you desire to go to live there.”"} ]
        }
  , 43: { 1:  [ {class: "vs", text: "When Jeremiah had finished speaking to all the people all the words of Yahweh their God, with which Yahweh their God had sent him to them, even all these words,"} ]
        , 2:  [ {class: "vs", text: "then Azariah the son of Hoshaiah, Johanan the son of Kareah, and all the proud men spoke, saying to Jeremiah, “You speak falsely. Yahweh our God has not sent you to say, ‘You shall not go into Egypt to live there;’"} ]
        , 3:  [ {class: "vs", text: "but Baruch the son of Neriah has turned you against us, to deliver us into the hand of the Chaldeans, that they may put us to death, and carry us away captive to Babylon.”"} ]
        , 4:  [ {class: "vs", text: "So Johanan the son of Kareah, and all the captains of the forces, and all the people, didn’t obey Yahweh’s voice, to dwell in the land of Judah."} ]
        , 5:  [ {class: "vs", text: "But Johanan the son of Kareah, and all the captains of the forces, took all the remnant of Judah, who had returned from all the nations where they had been driven, to live in the land of Judah;"} ]
        , 6:  [ {class: "vs", text: "the men, and the women, and the children, and the king’s daughters, and every person who Nebuzaradan the captain of the guard had left with Gedaliah the son of Ahikam, the son of Shaphan; and Jeremiah the prophet, and Baruch the son of Neriah;"} ]
        , 7:  [ {class: "vs", text: "and they came into the land of Egypt; for they didn’t obey Yahweh’s voice: and they came to Tahpanhes."} ]
        , 8:  [ {class: "vs", text: "Then Yahweh’s word came to Jeremiah in Tahpanhes, saying,"} ]
        , 9:  [ {class: "vs", text: "“Take great stones in your hand, and hide them in mortar in the brick work, which is at the entry of Pharaoh’s house in Tahpanhes, in the sight of the men of Judah;"} ]
        , 10: [ {class: "vs", text: "and tell them, Yahweh of Armies, the God of Israel, says: ‘Behold, I will send and take Nebuchadnezzar the king of Babylon, my servant, and will set his throne on these stones that I have hidden; and he will spread his royal pavilion over them."} ]
        , 11: [ {class: "vs", text: "He will come, and will strike the land of Egypt; such as are for death will be put to death, and such as are for captivity to captivity, and such as are for the sword to the sword."} ]
        , 12: [ {class: "vs", text: "I will kindle a fire in the houses of the gods of Egypt. He will burn them, and carry them away captive. He will array himself with the land of Egypt, as a shepherd puts on his garment; and he will go out from there in peace."} ]
        , 13: [ {class: "vs", text: "He will also break the pillars of Beth Shemesh, that is in the land of Egypt; and he will burn the houses of the gods of Egypt with fire.’”"} ]
        }
  , 44: { 1:  [ {class: "vs", text: "The word that came to Jeremiah concerning all the Jews who lived in the land of Egypt, who lived at Migdol, and at Tahpanhes, and at Memphis, and in the country of Pathros, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh of Armies, the God of Israel, says: ‘You have seen all the evil that I have brought on Jerusalem, and on all the cities of Judah. Behold, today they are a desolation, and no man dwells in them,"} ]
        , 3:  [ {class: "vs", text: "because of their wickedness which they have committed to provoke me to anger, in that they went to burn incense, to serve other gods that they didn’t know, neither they, nor you, nor your fathers."} ]
        , 4:  [ {class: "vs", text: "However I sent to you all my servants the prophets, rising up early and sending them, saying, “Oh, don’t do this abominable thing that I hate.”"} ]
        , 5:  [ {class: "vs", text: "But they didn’t listen and didn’t incline their ear. They didn’t turn from their wickedness, to stop burning incense to other gods."} ]
        , 6:  [ {class: "vs", text: "Therefore my wrath and my anger was poured out, and was kindled in the cities of Judah and in the streets of Jerusalem; and they are wasted and desolate, as it is today.’"} ]
        , 7:  [ {class: "vs", text: "“Therefore now Yahweh, the God of Armies, the God of Israel, says: ‘Why do you commit great evil against your own souls, to cut off from yourselves man and woman, infant and nursing child out of the middle of Judah, to leave yourselves no one remaining;"} ]
        , 8:  [ {class: "vs", text: "in that you provoke me to anger with the works of your hands, burning incense to other gods in the land of Egypt, where you have gone to live; that you may be cut off, and that you may be a curse and a reproach among all the nations of the earth?"} ]
        , 9:  [ {class: "vs", text: "Have you forgotten the wickedness of your fathers, and the wickedness of the kings of Judah, and the wickedness of their wives, and your own wickedness, and the wickedness of your wives, which they committed in the land of Judah and in the streets of Jerusalem?"} ]
        , 10: [ {class: "vs", text: "They are not humbled even to this day, neither have they feared, nor walked in my law, nor in my statutes, that I set before you and before your fathers.’"} ]
        , 11: [ {class: "vs", text: "“Therefore Yahweh of Armies, the God of Israel, says: ‘Behold, I will set my face against you for evil, even to cut off all Judah."} ]
        , 12: [ {class: "vs", text: "I will take the remnant of Judah, that have set their faces to go into the land of Egypt to live there, and they will all be consumed. They will fall in the land of Egypt. They will be consumed by the sword and by the famine. They will die, from the least even to the greatest, by the sword and by the famine. They will be an object of horror, an astonishment, and a curse, and a reproach."} ]
        , 13: [ {class: "vs", text: "For I will punish those who dwell in the land of Egypt, as I have punished Jerusalem, by the sword, by the famine, and by the pestilence;"} ]
        , 14: [ {class: "vs", text: "so that none of the remnant of Judah, who have gone into the land of Egypt to live there, will escape or be left to return into the land of Judah, to which they have a desire to return to dwell there; for no one will return except those who will escape.’”"} ]
        , 15: [ {class: "vs", text: "Then all the men who knew that their wives burned incense to other gods, and all the women who stood by, a great assembly, even all the people who lived in the land of Egypt, in Pathros, answered Jeremiah, saying,"} ]
        , 16: [ {class: "vs", text: "“As for the word that you have spoken to us in Yahweh’s name, we will not listen to you."} ]
        , 17: [ {class: "vs", text: "But we will certainly perform every word that has gone out of our mouth, to burn incense to the queen of the sky, and to pour out drink offerings to her, as we have done, we and our fathers, our kings and our princes, in the cities of Judah, and in the streets of Jerusalem; for then had we plenty of food, and were well, and saw no evil."} ]
        , 18: [ {class: "vs", text: "But since we stopped burning incense to the queen of the sky, and pouring out drink offerings to her, we have lacked all things, and have been consumed by the sword and by the famine."} ]
        , 19: [ {class: "vs", text: "“When we burned incense to the queen of the sky, and poured out drink offerings to her, did we make her cakes to worship her, and pour out drink offerings to her, without our husbands?”"} ]
        , 20: [ {class: "vs", text: "Then Jeremiah said to all the people, to the men, and to the women, even to all the people who had given him an answer, saying,"} ]
        , 21: [ {class: "vs", text: "“The incense that you burned in the cities of Judah, and in the streets of Jerusalem, you and your fathers, your kings and your princes, and the people of the land, didn’t Yahweh remember them, and didn’t it come into his mind?"} ]
        , 22: [ {class: "vs", text: "Thus Yahweh could no longer bear it, because of the evil of your doings, and because of the abominations which you have committed. Therefore your land has become a desolation, and an astonishment, and a curse, without inhabitant, as it is today."} ]
        , 23: [ {class: "vs", text: "Because you have burned incense, and because you have sinned against Yahweh, and have not obeyed Yahweh’s voice, nor walked in his law, nor in his statutes, nor in his testimonies; therefore this evil has happened to you, as it is today.”"} ]
        , 24: [ {class: "vs", text: "Moreover Jeremiah said to all the people, including all the women, “Hear Yahweh’s word, all Judah who are in the land of Egypt!"} ]
        , 25: [ {class: "vs", text: "Yahweh of Armies, the God of Israel, says, ‘You and your wives have both spoken with your mouths, and with your hands have fulfilled it, saying, “We will surely perform our vows that we have vowed, to burn incense to the queen of the sky, and to pour out drink offerings to her.”</p><p> “‘Establish then your vows, and perform your vows.’"} ]
        , 26: [ {class: "vs", text: "“Therefore hear Yahweh’s word, all Judah who dwell in the land of Egypt: ‘Behold, I have sworn by my great name,’ says Yahweh, ‘that my name will no more be named in the mouth of any man of Judah in all the land of Egypt, saying, “As the Lord Yahweh lives.”"} ]
        , 27: [ {class: "vs", text: "Behold, I watch over them for evil, and not for good; and all the men of Judah who are in the land of Egypt will be consumed by the sword and by the famine, until they are all gone."} ]
        , 28: [ {class: "vs", text: "Those who escape the sword will return out of the land of Egypt into the land of Judah few in number. All the remnant of Judah, who have gone into the land of Egypt to live there, will know whose word will stand, mine or theirs."} ]
        , 29: [ {class: "vs", text: "“‘This will be the sign to you,’ says Yahweh, ‘that I will punish you in this place, that you may know that my words will surely stand against you for evil.’"} ]
        , 30: [ {class: "vs", text: "Yahweh says, ‘Behold, I will give Pharaoh Hophra king of Egypt into the hand of his enemies, and into the hand of those who seek his life; as I gave Zedekiah king of Judah into the hand of Nebuchadnezzar king of Babylon, who was his enemy, and sought his life.’”"} ]
        }
  , 45: { 1:  [ {class: "vs", text: "The message that Jeremiah the prophet spoke to Baruch the son of Neriah, when he wrote these words in a book at the mouth of Jeremiah, in the fourth year of Jehoiakim the son of Josiah, king of Judah, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh, the God of Israel, says to you, Baruch:"} ]
        , 3:  [ {class: "vs", text: "‘You said, “Woe is me now! For Yahweh has added sorrow to my pain! I am weary with my groaning, and I find no rest.”’"} ]
        , 4:  [ {class: "vs", text: "“You shall tell him, Yahweh says: ‘Behold, that which I have built, I will break down, and that which I have planted I will pluck up; and this in the whole land."} ]
        , 5:  [ {class: "vs", text: "Do you seek great things for yourself? Don’t seek them; for, behold, I will bring evil on all flesh,’ says Yahweh; ‘but I will let you escape with your life wherever you go.’”"} ]
        }
  , 46: { 1:  [ {class: "vs", text: "Yahweh’s word which came to Jeremiah the prophet concerning the nations."} ]
        , 2:  [ {class: "vs", text: "Of Egypt: concerning the army of Pharaoh Necoh king of Egypt, which was by the river Euphrates in Carchemish, which Nebuchadnezzar king of Babylon struck in the fourth year of Jehoiakim the son of Josiah, king of Judah."} ]
        , 3:  [ {class: "q", text: "“Prepare the buckler and shield,"}
              , {class: "q2", text: "and draw near to battle!"}
              ]
        , 4:  [ {class: "q", text: "Harness the horses, and get up, you horsemen,"}
              , {class: "q2", text: "and stand up with your helmets."}
              , {class: "q", text: "Polish the spears,"}
              , {class: "q2", text: "put on the coats of mail."}
              ]
        , 5:  [ {class: "q", text: "Why have I seen it?"}
              , {class: "q2", text: "They are dismayed and are turned backward."}
              , {class: "q", text: "Their mighty ones are beaten down,"}
              , {class: "q2", text: "have fled in haste,"}
              , {class: "q2", text: "and don’t look back."}
              , {class: "q", text: "Terror is on every side,”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        , 6:  [ {class: "q", text: "“Don’t let the swift flee away,"}
              , {class: "q2", text: "nor the mighty man escape."}
              , {class: "q", text: "In the north by the river Euphrates"}
              , {class: "q2", text: "they have stumbled and fallen."}
              ]
        , 7:  [ {class: "q", text: "“Who is this who rises up like the Nile,"}
              , {class: "q2", text: "whose waters toss themselves like the rivers?"}
              ]
        , 8:  [ {class: "q", text: "Egypt rises up like the Nile,"}
              , {class: "q2", text: "and his waters toss themselves like the rivers."}
              , {class: "q", text: "He says, ‘I will rise up. I will cover the earth."}
              , {class: "q2", text: "I will destroy cities and its inhabitants.’"}
              ]
        , 9:  [ {class: "q", text: "Go up, you horses!"}
              , {class: "q2", text: "Rage, you chariots!"}
              , {class: "q", text: "Let the mighty men go out:"}
              , {class: "q2", text: "Cush and Put, who handle the shield;"}
              , {class: "q2", text: "and the Ludim, who handle and bend the bow."}
              ]
        , 10: [ {class: "q", text: "For that day is of the Lord, Yahweh of Armies,"}
              , {class: "q2", text: "a day of vengeance,"}
              , {class: "q2", text: "that he may avenge himself of his adversaries."}
              , {class: "q", text: "The sword will devour and be satiated,"}
              , {class: "q2", text: "and will drink its fill of their blood;"}
              , {class: "q2", text: "for the Lord, Yahweh of Armies, has a sacrifice in the north country by the river Euphrates."}
              ]
        , 11: [ {class: "q", text: "Go up into Gilead, and take balm, virgin daughter of Egypt."}
              , {class: "q2", text: "You use many medicines in vain."}
              , {class: "q2", text: "There is no healing for you."}
              ]
        , 12: [ {class: "q", text: "The nations have heard of your shame,"}
              , {class: "q2", text: "and the earth is full of your cry;"}
              , {class: "q2", text: "for the mighty man has stumbled against the mighty,"}
              , {class: "q2", text: "they both fall together.”"}
              ]
        , 13: [ {class: "vs", text: "The word that Yahweh spoke to Jeremiah the prophet, how that Nebuchadnezzar king of Babylon should come and strike the land of Egypt."} ]
        , 14: [ {class: "q", text: "“Declare in Egypt,"}
              , {class: "q2", text: "publish in Migdol,"}
              , {class: "q2", text: "and publish in Memphis and in Tahpanhes:"}
              , {class: "q", text: "say, ‘Stand up, and prepare;"}
              , {class: "q2", text: "for the sword has devoured around you.’"}
              ]
        , 15: [ {class: "q", text: "Why are your strong ones swept away?"}
              , {class: "q2", text: "They didn’t stand, because Yahweh pushed them."}
              ]
        , 16: [ {class: "q", text: "He made many to stumble."}
              , {class: "q2", text: "Yes, they fell on one another."}
              , {class: "q", text: "They said, ‘Arise! Let’s go again to our own people,"}
              , {class: "q2", text: "and to the land of our birth,"}
              , {class: "q2", text: "from the oppressing sword.’"}
              ]
        , 17: [ {class: "q", text: "They cried there, ‘Pharaoh king of Egypt is but a noise;"}
              , {class: "q2", text: "he has let the appointed time pass by.’"}
              ]
        , 18: [ {class: "q", text: "“As I live,” says the King,"}
              , {class: "q2", text: "whose name is Yahweh of Armies,"}
              , {class: "q", text: "“surely like Tabor among the mountains,"}
              , {class: "q2", text: "and like Carmel by the sea,"}
              , {class: "q2", text: "so he will come."}
              ]
        , 19: [ {class: "q", text: "You daughter who dwells in Egypt,"}
              , {class: "q2", text: "furnish yourself to go into captivity;"}
              , {class: "q", text: "for Memphis will become a desolation,"}
              , {class: "q2", text: "and will be burned up,"}
              , {class: "q2", text: "without inhabitant."}
              ]
        , 20: [ {class: "q", text: "“Egypt is a very beautiful heifer;"}
              , {class: "q2", text: "but destruction out of the north has come."}
              , {class: "q2", text: "It has come."}
              ]
        , 21: [ {class: "q", text: "Also her hired men in the middle of her are like calves of the stall;"}
              , {class: "q2", text: "for they also are turned back."}
              , {class: "q2", text: "They have fled away together."}
              , {class: "q", text: "They didn’t stand,"}
              , {class: "q2", text: "for the day of their calamity has come on them,"}
              , {class: "q2", text: "the time of their visitation."}
              ]
        , 22: [ {class: "q", text: "Its sound will go like the serpent;"}
              , {class: "q2", text: "for they will march with an army,"}
              , {class: "q2", text: "and come against her with axes, as wood cutters."}
              ]
        , 23: [ {class: "q", text: "They will cut down her forest,” says Yahweh,"}
              , {class: "q2", text: "“though it can’t be searched;"}
              , {class: "q", text: "because they are more than the locusts,"}
              , {class: "q2", text: "and are innumerable."}
              ]
        , 24: [ {class: "q", text: "The daughter of Egypt will be disappointed;"}
              , {class: "q2", text: "she will be delivered into the hand of the people of the north.”"}
              ]
        , 25: [ {class: "vs", text: "Yahweh of Armies, the God of Israel, says: “Behold, I will punish Amon of No, and Pharaoh, and Egypt, with her gods, and her kings; even Pharaoh, and those who trust in him."} ]
        , 26: [ {class: "vs", text: "I will deliver them into the hand of those who seek their lives, and into the hand of Nebuchadnezzar king of Babylon, and into the hand of his servants. Afterwards it will be inhabited, as in the days of old,” says Yahweh."} ]
        , 27: [ {class: "q", text: "“But don’t you be afraid, Jacob my servant."}
              , {class: "q2", text: "Don’t be dismayed, Israel;"}
              , {class: "q", text: "for, behold, I will save you from afar,"}
              , {class: "q2", text: "and your offspring from the land of their captivity."}
              , {class: "q", text: "Jacob will return,"}
              , {class: "q2", text: "and will be quiet and at ease."}
              , {class: "q2", text: "No one will make him afraid."}
              ]
        , 28: [ {class: "q", text: "Don’t be afraid, O Jacob my servant,” says Yahweh;"}
              , {class: "q2", text: "“for I am with you;"}
              , {class: "q2", text: "for I will make a full end of all the nations where I have driven you;"}
              , {class: "q", text: "but I will not make a full end of you,"}
              , {class: "q2", text: "but I will correct you in measure,"}
              , {class: "q2", text: "and will in no way leave you unpunished.”"}
              ]
        }
  , 47: { 1:  [ {class: "vs", text: "Yahweh’s word that came to Jeremiah the prophet concerning the Philistines, before Pharaoh struck Gaza."} ]
        , 2:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“Behold, waters rise up out of the north,"}
              , {class: "q2", text: "and will become an overflowing stream,"}
              , {class: "q", text: "and will overflow the land and all that is therein,"}
              , {class: "q2", text: "the city and those who dwell therein."}
              , {class: "q", text: "The men will cry,"}
              , {class: "q2", text: "and all the inhabitants of the land will wail."}
              ]
        , 3:  [ {class: "q", text: "At the noise of the stamping of the hoofs of his strong ones,"}
              , {class: "q2", text: "at the rushing of his chariots,"}
              , {class: "q2", text: "at the rumbling of his wheels,"}
              , {class: "q", text: "the fathers don’t look back to their children"}
              , {class: "q2", text: "for feebleness of hands;"}
              ]
        , 4:  [ {class: "q", text: "because of the day that comes to destroy all the Philistines,"}
              , {class: "q2", text: "to cut off from Tyre and Sidon every helper who remains;"}
              , {class: "q", text: "for Yahweh will destroy the Philistines,"}
              , {class: "q2", text: "the remnant of the isle of Caphtor."}
              ]
        , 5:  [ {class: "q", text: "Baldness has come on Gaza;"}
              , {class: "q2", text: "Ashkelon is brought to nothing,"}
              , {class: "q", text: "the remnant of their valley:"}
              , {class: "q2", text: "how long will you cut yourself?"}
              ]
        , 6:  [ {class: "q", text: "“‘You sword of Yahweh, how long will it be before you are quiet?"}
              , {class: "q2", text: "Put yourself back into your scabbard;"}
              , {class: "q2", text: "rest, and be still.’"}
              ]
        , 7:  [ {class: "q", text: "“How can you be quiet,"}
              , {class: "q2", text: "since Yahweh has given you a command?"}
              , {class: "q", text: "Against Ashkelon, and against the seashore,"}
              , {class: "q2", text: "there has he appointed it.”"}
              ]
        }
  , 48: { 1:  [ {class: "endpara", text: "Of Moab. Yahweh of Armies, the God of Israel, says:"}
              , {class: "q", text: "“Woe to Nebo!"}
              , {class: "q2", text: "For it is laid waste."}
              , {class: "q", text: "Kiriathaim is disappointed."}
              , {class: "q2", text: "It is taken."}
              , {class: "q", text: "Misgab is put to shame"}
              , {class: "q2", text: "and broken down."}
              ]
        , 2:  [ {class: "q", text: "The praise of Moab is no more."}
              , {class: "q2", text: "In Heshbon they have devised evil against her:"}
              , {class: "q2", text: "‘Come! Let’s cut her off from being a nation.’"}
              , {class: "q", text: "You also, Madmen, will be brought to silence."}
              , {class: "q2", text: "The sword will pursue you."}
              ]
        , 3:  [ {class: "q", text: "The sound of a cry from Horonaim,"}
              , {class: "q2", text: "desolation and great destruction!"}
              ]
        , 4:  [ {class: "q", text: "Moab is destroyed."}
              , {class: "q2", text: "Her little ones have caused a cry to be heard."}
              ]
        , 5:  [ {class: "q", text: "For they will go up by the ascent of Luhith with continual weeping."}
              , {class: "q2", text: "For at the descent of Horonaim they have heard the distress of the cry of destruction."}
              ]
        , 6:  [ {class: "q", text: "Flee! Save your lives!"}
              , {class: "q2", text: "Be like the juniper bush in the wilderness."}
              ]
        , 7:  [ {class: "q", text: "For, because you have trusted in your works and in your treasures,"}
              , {class: "q2", text: "you also will be taken."}
              , {class: "q", text: "Chemosh will go out into captivity,"}
              , {class: "q2", text: "his priests and his princes together."}
              ]
        , 8:  [ {class: "q", text: "The destroyer will come on every city,"}
              , {class: "q2", text: "and no city will escape;"}
              , {class: "q", text: "the valley also will perish,"}
              , {class: "q2", text: "and the plain will be destroyed; as Yahweh has spoken."}
              ]
        , 9:  [ {class: "q", text: "Give wings to Moab,"}
              , {class: "q2", text: "that she may fly and get herself away:"}
              , {class: "q", text: "and her cities will become a desolation,"}
              , {class: "q2", text: "without anyone to dwell in them."}
              ]
        , 10: [ {class: "q", text: "“Cursed is he who does the work of Yahweh negligently;"}
              , {class: "q2", text: "and cursed is he who keeps back his sword from blood."}
              ]
        , 11: [ {class: "q", text: "“Moab has been at ease from his youth,"}
              , {class: "q2", text: "and he has settled on his lees,"}
              , {class: "q", text: "and has not been emptied from vessel to vessel,"}
              , {class: "q2", text: "neither has he gone into captivity:"}
              , {class: "q", text: "therefore his taste remains in him,"}
              , {class: "q2", text: "and his scent is not changed."}
              ]
        , 12: [ {class: "q", text: "Therefore behold, the days come,” says Yahweh,"}
              , {class: "q2", text: "“that I will send to him those who pour off,"}
              , {class: "q", text: "and they will pour him off;"}
              , {class: "q2", text: "and they will empty his vessels,"}
              , {class: "q2", text: "and break their containers in pieces."}
              ]
        , 13: [ {class: "q", text: "Moab will be ashamed of Chemosh,"}
              , {class: "q2", text: "as the house of Israel was ashamed of Bethel, their confidence."}
              ]
        , 14: [ {class: "q", text: "“How do you say, ‘We are mighty men,"}
              , {class: "q2", text: "and valiant men for the war?’"}
              ]
        , 15: [ {class: "q", text: "Moab is laid waste,"}
              , {class: "q2", text: "and they have gone up into his cities,"}
              , {class: "q2", text: "and his chosen young men have gone down to the slaughter,”"}
              , {class: "q2", text: "says the King, whose name is Yahweh of Armies."}
              ]
        , 16: [ {class: "q", text: "“The calamity of Moab is near to come,"}
              , {class: "q2", text: "and his affliction hurries fast."}
              ]
        , 17: [ {class: "q", text: "All you who are around him, bemoan him,"}
              , {class: "q2", text: "and all you who know his name; say,"}
              , {class: "q", text: "‘How the strong staff is broken,"}
              , {class: "q2", text: "the beautiful rod!’"}
              ]
        , 18: [ {class: "q", text: "“You daughter who dwells in Dibon,"}
              , {class: "q2", text: "come down from your glory,"}
              , {class: "q2", text: "and sit in thirst;"}
              , {class: "q", text: "for the destroyer of Moab has come up against you."}
              , {class: "q2", text: "He has destroyed your strongholds."}
              ]
        , 19: [ {class: "q", text: "Inhabitant of Aroer, stand by the way and watch."}
              , {class: "q2", text: "Ask him who flees, and her who escapes;"}
              , {class: "q2", text: "say, ‘What has been done?’"}
              ]
        , 20: [ {class: "q", text: "Moab is disappointed;"}
              , {class: "q2", text: "for it is broken down."}
              , {class: "q", text: "Wail and cry!"}
              , {class: "q2", text: "Tell it by the Arnon, that Moab is laid waste."}
              ]
        , 21: [ {class: "q", text: "Judgment has come on the plain country,"}
              , {class: "q2", text: "on Holon, on Jahzah, on Mephaath,"}
              ]
        , 22: [ {class: "q2", text: "on Dibon, on Nebo, on Beth Diblathaim,"} ]
        , 23: [ {class: "q2", text: "on Kiriathaim, on Beth Gamul, on Beth Meon,"} ]
        , 24: [ {class: "q2", text: "on Kerioth, on Bozrah,"}
              , {class: "q2", text: "and on all the cities of the land of Moab, far or near."}
              ]
        , 25: [ {class: "q", text: "The horn of Moab is cut off,"}
              , {class: "q2", text: "and his arm is broken,” says Yahweh."}
              ]
        , 26: [ {class: "q", text: "“Make him drunken;"}
              , {class: "q2", text: "for he magnified himself against Yahweh."}
              , {class: "q", text: "Moab will wallow in his vomit,"}
              , {class: "q2", text: "and he also will be in derision."}
              ]
        , 27: [ {class: "q", text: "For wasn’t Israel a derision to you?"}
              , {class: "q2", text: "Was he found among thieves?"}
              , {class: "q", text: "For as often as you speak of him,"}
              , {class: "q2", text: "you shake your head."}
              ]
        , 28: [ {class: "q", text: "You inhabitants of Moab, leave the cities, and dwell in the rock."}
              , {class: "q2", text: "Be like the dove that makes her nest over the mouth of the abyss."}
              ]
        , 29: [ {class: "q", text: "“We have heard of the pride of Moab."}
              , {class: "q2", text: "He is very proud in his loftiness, his pride,"}
              , {class: "q2", text: "his arrogance, and the arrogance of his heart."}
              ]
        , 30: [ {class: "q", text: "I know his wrath,” says Yahweh, “that it is nothing;"}
              , {class: "q2", text: "his boastings have done nothing."}
              ]
        , 31: [ {class: "q", text: "Therefore I will wail for Moab."}
              , {class: "q2", text: "Yes, I will cry out for all Moab."}
              , {class: "q2", text: "They will mourn for the men of Kir Heres."}
              ]
        , 32: [ {class: "q", text: "With more than the weeping of Jazer"}
              , {class: "q2", text: "I will weep for you, vine of Sibmah."}
              , {class: "q", text: "Your branches passed over the sea."}
              , {class: "q2", text: "They reached even to the sea of Jazer."}
              , {class: "q", text: "The destroyer has fallen on your summer fruits"}
              , {class: "q2", text: "and on your vintage."}
              ]
        , 33: [ {class: "q", text: "Gladness and joy is taken away from the fruitful field"}
              , {class: "q2", text: "and from the land of Moab."}
              , {class: "q", text: "I have caused wine to cease from the wine presses."}
              , {class: "q2", text: "No one will tread with shouting."}
              , {class: "q2", text: "The shouting will be no shouting."}
              ]
        , 34: [ {class: "q", text: "From the cry of Heshbon even to Elealeh,"}
              , {class: "q2", text: "even to Jahaz they have uttered their voice,"}
              , {class: "q2", text: "from Zoar even to Horonaim, to Eglath Shelishiyah;"}
              , {class: "q2", text: "for the waters of Nimrim will also become desolate."}
              ]
        , 35: [ {class: "q", text: "Moreover I will cause to cease in Moab,” says Yahweh,"}
              , {class: "q2", text: "“him who offers in the high place,"}
              , {class: "q2", text: "and him who burns incense to his gods."}
              ]
        , 36: [ {class: "q", text: "Therefore my heart sounds for Moab like pipes,"}
              , {class: "q2", text: "and my heart sounds like pipes for the men of Kir Heres."}
              , {class: "q2", text: "Therefore the abundance that he has gotten has perished."}
              ]
        , 37: [ {class: "q", text: "For every head is bald,"}
              , {class: "q2", text: "and every beard clipped."}
              , {class: "q", text: "There are cuttings on all the hands,"}
              , {class: "q2", text: "and sackcloth on the waist."}
              ]
        , 38: [ {class: "q", text: "On all the housetops of Moab,"}
              , {class: "q2", text: "and in its streets, there is lamentation everywhere;"}
              , {class: "q2", text: "for I have broken Moab like a vessel in which no one delights,” says Yahweh."}
              ]
        , 39: [ {class: "q", text: "“How it is broken down!"}
              , {class: "q2", text: "How they wail!"}
              , {class: "q", text: "How Moab has turned the back with shame!"}
              , {class: "q2", text: "So will Moab become a derision"}
              , {class: "q2", text: "and a terror to all who are around him.”"}
              ]
        , 40: [ {class: "q", text: "For Yahweh says: “Behold, he will fly as an eagle,"}
              , {class: "q2", text: "and will spread out his wings against Moab."}
              ]
        , 41: [ {class: "q", text: "Kerioth is taken,"}
              , {class: "q2", text: "and the strongholds are seized."}
              , {class: "q", text: "The heart of the mighty men of Moab at that day"}
              , {class: "q2", text: "will be as the heart of a woman in her pangs."}
              ]
        , 42: [ {class: "q", text: "Moab will be destroyed from being a people,"}
              , {class: "q2", text: "because he has magnified himself against Yahweh."}
              ]
        , 43: [ {class: "q", text: "Terror, the pit, and the snare are on you,"}
              , {class: "q2", text: "inhabitant of Moab,” says Yahweh."}
              ]
        , 44: [ {class: "q", text: "“He who flees from the terror will fall into the pit;"}
              , {class: "q2", text: "and he who gets up out of the pit will be taken in the snare:"}
              , {class: "q", text: "for I will bring on him, even on Moab,"}
              , {class: "q2", text: "the year of their visitation,” says Yahweh."}
              ]
        , 45: [ {class: "q", text: "“Those who fled stand without strength under the shadow of Heshbon;"}
              , {class: "q2", text: "for a fire has gone out of Heshbon,"}
              , {class: "q2", text: "and a flame from the middle of Sihon,"}
              , {class: "q", text: "and has devoured the corner of Moab,"}
              , {class: "q2", text: "and the crown of the head of the tumultuous ones."}
              ]
        , 46: [ {class: "q", text: "Woe to you, O Moab!"}
              , {class: "q2", text: "The people of Chemosh are undone;"}
              , {class: "q", text: "for your sons are taken away captive,"}
              , {class: "q2", text: "and your daughters into captivity."}
              ]
        , 47: [ {class: "q", text: "“Yet I will reverse the captivity of Moab in the latter days,”"}
              , {class: "q2", text: "says Yahweh."}
              , {class: "para", text: "Thus far is the judgment of Moab."}
              ]
        }
  , 49: { 1:  [ {class: "vs", text: "Of the children of Ammon. Yahweh says:</p><div class=\"“Has Israel no sons?"} 
              , {class: "q2", text: "Has he no heir?"}
              , {class: "q", text: "Why then does Malcam possess Gad,"}
              , {class: "q2", text: "and his people dwell in its cities?"}
              ]
        , 2:  [ {class: "q", text: "Therefore behold, the days come,”"}
              , {class: "q2", text: "says Yahweh,"}
              , {class: "q", text: "“that I will cause an alarm of war to be heard against Rabbah of the children of Ammon;"}
              , {class: "q2", text: "and it will become a desolate heap,"}
              , {class: "q2", text: "and her daughters will be burned with fire:"}
              , {class: "q", text: "then Israel will possess those who possessed him,”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        , 3:  [ {class: "q", text: "“Wail, Heshbon, for Ai is laid waste!"}
              , {class: "q2", text: "Cry, you daughters of Rabbah!"}
              , {class: "q", text: "Clothe yourself in sackcloth."}
              , {class: "q2", text: "Lament, and run back and forth among the fences;"}
              , {class: "q", text: "for Malcam will go into captivity,"}
              , {class: "q2", text: "his priests and his princes together."}
              ]
        , 4:  [ {class: "q", text: "Why do you boast in the valleys,"}
              , {class: "q2", text: "your flowing valley, backsliding daughter?"}
              , {class: "q", text: "You trusted in her treasures,"}
              , {class: "q2", text: "saying, ‘Who will come to me?’"}
              ]
        , 5:  [ {class: "q", text: "Behold, I will bring a terror on you,”"}
              , {class: "q2", text: "says the Lord, Yahweh of Armies,"}
              , {class: "q", text: "“from all who are around you."}
              , {class: "q2", text: "All of you will be driven completely out,"}
              , {class: "q2", text: "and there will be no one to gather together the fugitives."}
              ]
        , 6:  [ {class: "q", text: "“But afterward I will reverse the captivity of the children of Ammon,”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        , 7:  [ {class: "endpara", text: "Of Edom, Yahweh of Armies says:</p><div class=\"“Is wisdom no more in Teman?"}
              , {class: "q2", text: "Has counsel perished from the prudent?"}
              , {class: "q2", text: "Has their wisdom vanished?"}
              ]
        , 8:  [ {class: "q", text: "Flee! Turn back!"}
              , {class: "q2", text: "Dwell in the depths, inhabitants of Dedan;"}
              , {class: "q2", text: "for I will bring the calamity of Esau on him when I visit him."}
              ]
        , 9:  [ {class: "q", text: "If grape gatherers came to you,"}
              ,  {class: "q2", text: "would they not leave some gleaning grapes?"}
              , {class: "q", text: "If thieves came by night,"}
              , {class: "q2", text: "wouldn’t they steal until they had enough?"}
              ]
        , 10: [ {class: "q", text: "But I have made Esau bare,"}
              , {class: "q2", text: "I have uncovered his secret places,"}
              , {class: "q2", text: "and he will not be able to hide himself."}
              , {class: "q", text: "His offspring is destroyed,"}
              , {class: "q2", text: "with his brothers and his neighbors;"}
              , {class: "q2", text: "and he is no more."}
              ]
        , 11: [ {class: "q", text: "Leave your fatherless children."}
              , {class: "q2", text: "I will preserve them alive."}
              , {class: "q2", text: "Let your widows trust in me.”"}
              ]
        , 12: [ {class: "vs", text: "For Yahweh says: “Behold, they to whom it didn’t pertain to drink of the cup will certainly drink; and are you he who will altogether go unpunished? You won’t go unpunished, but you will surely drink."} ]
        , 13: [ {class: "vs", text: "For I have sworn by myself,” says Yahweh, “that Bozrah will become an astonishment, a reproach, a waste, and a curse. All its cities will be perpetual wastes.”"} ]
        , 14: [ {class: "q", text: "I have heard news from Yahweh,"}
              , {class: "q2", text: "and an ambassador is sent among the nations,"}
              , {class: "q", text: "saying, “Gather yourselves together!"}
              , {class: "q2", text: "Come against her!"}
              , {class: "q2", text: "Rise up to the battle!”"}
              ]
        , 15: [ {class: "q", text: "“For, behold, I have made you small among the nations,"}
              , {class: "q2", text: "and despised among men."}
              ]
        , 16: [ {class: "q", text: "As for your terror,"}
              , {class: "q2", text: "the pride of your heart has deceived you,"}
              , {class: "q", text: "O you who dwell in the clefts of the rock,"}
              , {class: "q2", text: "who hold the height of the hill,"}
              , {class: "q", text: "though you should make your nest as high as the eagle,"}
              , {class: "q2", text: "I will bring you down from there,” says Yahweh."}
              ]
        , 17: [ {class: "q", text: "“Edom will become an astonishment."}
              , {class: "q2", text: "Everyone who passes by it will be astonished,"}
              , {class: "q2", text: "and will hiss at all its plagues."}
              ]
        , 18: [ {class: "q", text: "As in the overthrow of Sodom and Gomorrah and its neighbor cities,” says Yahweh,"}
              , {class: "q2", text: "“no man will dwell there,"}
              , {class: "q2", text: "neither will any son of man live therein."}
              ]
        , 19: [ {class: "q", text: "“Behold, he will come up like a lion from the pride of the Jordan against the strong habitation:"}
              , {class: "q2", text: "for I will suddenly make them run away from it;"}
              , {class: "q", text: "and whoever is chosen,"}
              , {class: "q2", text: "I will appoint him over it."}
              , {class: "q", text: "For who is like me?"}
              ,  {class: "q2", text: "Who will appoint me a time?"}
              , {class: "q2", text: "Who is the shepherd who will stand before me?”"}
              ]
        , 20: [ {class: "q", text: "Therefore hear the counsel of Yahweh, that he has taken against Edom;"}
              , {class: "q2", text: "and his purposes, that he has purposed against the inhabitants of Teman:"}
              , {class: "q", text: "Surely they will drag them away,"}
              , {class: "q2", text: "the little ones of the flock."}
              , {class: "q2", text: "Surely he will make their habitation desolate over them."}
              ]
        , 21: [ {class: "q", text: "The earth trembles at the noise of their fall;"}
              , {class: "q2", text: "there is a cry, the noise which is heard in the Red Sea."}
              ]
        , 22: [ {class: "q", text: "Behold, he will come up and fly as the eagle,"}
              , {class: "q2", text: "and spread out his wings against Bozrah."}
              , {class: "q2", text: "The heart of the mighty men of Edom at that day will be as the heart of a woman in her pangs."}
              ]
        , 23: [ {class: "endpara", text: "Of Damascus:</p><div class=\"“Hamath is confounded, and Arpad;"}
              , {class: "q2", text: "for they have heard evil news."}
              , {class: "q2", text: "They have melted away."}
              , {class: "q", text: "There is sorrow on the sea."}
              ,  {class: "q2", text: "It can’t be quiet."}
              ]
        , 24: [ {class: "q", text: "Damascus has grown feeble,"}
              , {class: "q2", text: "she turns herself to flee,"}
              , {class: "q2", text: "and trembling has seized her."}
              , {class: "q", text: "Anguish and sorrows have taken hold of her,"}
              , {class: "q2", text: "as of a woman in travail."}
              ]
        , 25: [ {class: "q", text: "How is the city of praise not forsaken,"}
              , {class: "q2", text: "the city of my joy?"}
              ]
        , 26: [ {class: "q", text: "Therefore her young men will fall in her streets,"}
              , {class: "q2", text: "and all the men of war will be brought to silence in that day,”"}
              , {class: "q2", text: "says Yahweh of Armies."}
              ]
        , 27: [ {class: "q", text: "“I will kindle a fire in the wall of Damascus,"}
              , {class: "q2", text: "and it will devour the palaces of Ben Hadad.”"}
              ]
        , 28: [ {class: "q", text: "Of Kedar, and of the kingdoms of Hazor, which Nebuchadnezzar king of Babylon struck. Yahweh says:"}
              , {class: "q", text: "“Arise, go up to Kedar,"}
              , {class: "q2", text: "and destroy the children of the east."}
              ]
        , 29: [ {class: "q", text: "They will take their tents and their flocks."}
              , {class: "q2", text: "they will carry away for themselves their curtains,"}
              , {class: "q2", text: "all their vessels, and their camels;"}
              , {class: "q2", text: "and they will cry to them, ‘Terror on every side!’"}
              ]
        , 30: [ {class: "q", text: "Flee!"}
              , {class: "q2", text: "Wander far off!"}
              , {class: "q", text: "Dwell in the depths, you inhabitants of Hazor,” says Yahweh;"}
              , {class: "q2", text: "“for Nebuchadnezzar king of Babylon has taken counsel against you,"}
              , {class: "q2", text: "and has conceived a purpose against you."}
              ]
        , 31: [ {class: "q", text: "Arise! Go up to a nation that is at ease,"}
              , {class: "q2", text: "that dwells without care,” says Yahweh;"}
              , {class: "q2", text: "“that has neither gates nor bars,"}
              , {class: "q2", text: "that dwells alone."}
              ]
        , 32: [ {class: "q", text: "Their camels will be a booty,"}
              , {class: "q2", text: "and the multitude of their livestock a plunder."}
              , {class: "q", text: "I will scatter to all winds those who have the corners of their beards cut off;"}
              , {class: "q2", text: "and I will bring their calamity from every side of them,”"}
              , {class: "q2", text: "says Yahweh."}
              ]
        , 33: [ {class: "q", text: "Hazor will be a dwelling place of jackals,"}
              , {class: "q2", text: "a desolation forever."}
              , {class: "q", text: "No man will dwell there,"}
              , {class: "q2", text: "neither will any son of man live therein.”"}
              ]
        , 34: [ {class: "vs", text: "Yahweh’s word that came to Jeremiah the prophet concerning Elam, in the beginning of the reign of Zedekiah king of Judah, saying,"} ]
        , 35: [ {class: "endpara", text: "“Yahweh of Armies says:"}
              , {class: "q", text: "‘Behold, I will break the bow of Elam,"}
              , {class: "q2", text: "the chief of their might."}
              ]
        , 36: [ {class: "q", text: "I will bring on Elam the four winds from the four quarters of the sky,"}
              , {class: "q2", text: "and will scatter them toward all those winds."}
              , {class: "q2", text: "There will be no nation where the outcasts of Elam will not come."}
              ]
        , 37: [ {class: "q", text: "I will cause Elam to be dismayed before their enemies,"}
              , {class: "q2", text: "and before those who seek their life."}
              , {class: "q", text: "I will bring evil on them, even my fierce anger,’ says Yahweh;"}
              , {class: "q2", text: "‘and I will send the sword after them,"}
              , {class: "q2", text: "until I have consumed them."}
              ]
        , 38: [ {class: "q", text: "I will set my throne in Elam,"}
              , {class: "q2", text: "and will destroy from there king and princes,’ says Yahweh."}
              ]
        , 39: [ {class: "q", text: "‘But it will happen in the latter days"}
              , {class: "q2", text: "that I will reverse the captivity of Elam,’ says Yahweh.”"}
              ]
        }
  , 5:  { 1:  [ {class: "vs", text: "“Run back and forth through the streets of Jerusalem, and see now, and know, and seek in its wide places, if you can find a man, if there is anyone who does justly, who seeks truth, then I will pardon her."} ]
        , 2:  [ {class: "vs", text: "Though they say, ‘As Yahweh lives,’ surely they swear falsely.”"} ]
        , 3:  [ {class: "vs", text: "O Yahweh, don’t your eyes look on truth? You have stricken them, but they were not grieved. You have consumed them, but they have refused to receive correction. They have made their faces harder than a rock. They have refused to return."} ]
        , 4:  [ {class: "vs", text: "Then I said, “Surely these are poor. They are foolish; for they don’t know the Yahweh’s way, nor the law of their God."} ]
        , 5:  [ {class: "vs", text: "I will go to the great men and will speak to them, for they know the way of Yahweh, and the law of their God.” But these with one accord have broken the yoke, and burst the bonds."} ]
        , 6:  [ {class: "vs", text: "Therefore a lion out of the forest will kill them. A wolf of the evenings will destroy them. A leopard will watch against their cities. Everyone who goes out there will be torn in pieces, because their transgressions are many and their backsliding has increased."} ]
        , 7:  [ {class: "vs", text: "“How can I pardon you? Your children have forsaken me, and sworn by what are no gods. When I had fed them to the full, they committed adultery, and assembled themselves in troops at the prostitutes’ houses."} ]
        , 8:  [ {class: "vs", text: "They were as fed horses roaming at large. Everyone neighed after his neighbor’s wife."} ]
        , 9:  [ {class: "vs", text: "Shouldn’t I punish them for these things?” says Yahweh. “Shouldn’t my soul be avenged on such a nation as this?"} ]
        , 10: [ {class: "vs", text: "“Go up on her walls, and destroy; but don’t make a full end. Take away her branches, for they are not Yahweh’s."} ]
        , 11: [ {class: "vs", text: "For the house of Israel and the house of Judah have dealt very treacherously against me,” says Yahweh."} ]
        , 12: [ {class: "vs", text: "They have denied Yahweh, and said, “It is not he. Evil will won’t come on us. We won’t see sword or famine."} ]
        , 13: [ {class: "vs", text: "The prophets will become wind, and the word is not in them. Thus it will be done to them.”"} ]
        , 14: [ {class: "vs", text: "Therefore Yahweh, the God of Armies says, “Because you speak this word, behold, I will make my words in your mouth fire, and this people wood, and it will devour them."} ]
        , 15: [ {class: "vs", text: "Behold, I will bring a nation on you from far away, house of Israel,” says Yahweh. “It is a mighty nation. It is an ancient nation, a nation whose language you don’t know and don’t understand what they say."} ]
        , 16: [ {class: "vs", text: "Their quiver is an open tomb. They are all mighty men."} ]
        , 17: [ {class: "vs", text: "They will eat up your harvest and your bread, which your sons and your daughters should eat. They will eat up your flocks and your herds. They will eat up your vines and your fig trees. They will beat down your fortified cities in which you trust with the sword."} ]
        , 18: [ {class: "vs", text: "“But even in those days,” says Yahweh, “I will not make a full end of you."} ]
        , 19: [ {class: "vs", text: "It will happen when you say, ‘Why has Yahweh our God done all these things to us?’ Then you shall say to them, ‘Just as you have forsaken me and served foreign gods in your land, so you will serve strangers in a land that is not yours.’"} ]
        , 20: [ {class: "vs", text: "“Declare this in the house of Jacob, and publish it in Judah, saying,"} ]
        , 21: [ {class: "vs", text: "‘Hear this now, foolish people without understanding, who have eyes, and don’t see, who have ears, and don’t hear:"} ]
        , 22: [ {class: "vs", text: "Don’t you fear me?’ says Yahweh ‘Won’t you tremble at my presence, who have placed the sand for the bound of the sea, by a perpetual decree, that it can’t pass it? Though its waves toss themselves, yet they can’t prevail. Though they roar, they still can’t pass over it.’"} ]
        , 23: [ {class: "vs", text: "“But this people has a revolting and a rebellious heart. They have revolted and gone."} ]
        , 24: [ {class: "vs", text: "They don’t say in their heart, ‘Let’s now fear Yahweh our God, who gives rain, both the former and the latter, in its season, who preserves to us the appointed weeks of the harvest.’"} ]
        , 25: [ {class: "vs", text: "“Your iniquities have turned away these things, and your sins have withheld good from you."} ]
        , 26: [ {class: "vs", text: "For wicked men are found among my people. They watch, as fowlers lie in wait. They set a trap. They catch men."} ]
        , 27: [ {class: "vs", text: "As a cage is full of birds, so are their houses full of deceit. Therefore they have become great, and grew rich."} ]
        , 28: [ {class: "vs", text: "They have grown fat. They shine; yes, they excel in deeds of wickedness. They don’t plead the cause, the cause of the fatherless, that they may prosper; and they don’t defend the rights of the needy."} ]
        , 29: [ {class: "vs", text: "“Shouldn’t I punish for these things?” says Yahweh. “Shouldn’t my soul be avenged on such a nation as this?"} ]
        , 30: [ {class: "vs", text: "“An astonishing and horrible thing has happened in the land."} ]
        , 31: [ {class: "vs", text: "The prophets prophesy falsely, and the priests rule by their own authority; and my people love to have it so. What will you do in the end of it?"} ]
        }
  , 50:  { 1:  [ {class: "vs", text: "The word that Yahweh spoke concerning Babylon, concerning the land of the Chaldeans, by Jeremiah the prophet."} ]
         , 2:  [ {class: "q", text: "“Declare among the nations and publish,"}
              , {class: "q2", text: "and set up a standard;"}
              , {class: "q2", text: "publish, and don’t conceal:"}
              , {class: "q", text: "say, ‘Babylon has been taken,"}
              , {class: "q2", text: "Bel is disappointed,"}
              , {class: "q2", text: "Merodach is dismayed!"}
              , {class: "q", text: "Her images are disappointed."}
              , {class: "q2", text: "Her idols are dismayed.’"}
              ]
        , 3:  [ {class: "q", text: "For a nation comes up out of the north against her,"}
              , {class: "q2", text: "which will make her land desolate,"}
              , {class: "q2", text: "and no one will dwell in it."}
              , {class: "q", text: "They have fled."}
              , {class: "q2", text: "They are gone,"}
              , {class: "q2", text: "both man and animal."}
              ]
        , 4:  [ {class: "q", text: "“In those days, and in that time,” says Yahweh,"}
              , {class: "q2", text: "“the children of Israel will come,"}
              , {class: "q2", text: "they and the children of Judah together;"}
              , {class: "q", text: "they will go on their way weeping,"}
              , {class: "q2", text: "and will seek Yahweh their God."}
              ]
        , 5:  [ {class: "q", text: "They will inquire concerning Zion with their faces turned toward it,"}
              , {class: "q2", text: "saying, ‘Come, and join yourselves to Yahweh in an everlasting covenant"}
              , {class: "q2", text: "that will not be forgotten.’"}
              ]
        , 6:  [ {class: "q", text: "My people have been lost sheep."}
              , {class: "q2", text: "Their shepherds have caused them to go astray."}
              , {class: "q", text: "They have turned them away on the mountains."}
              , {class: "q2", text: "They have gone from mountain to hill."}
              , {class: "q2", text: "They have forgotten their resting place."}
              ]
        , 7:  [ {class: "q", text: "All who found them have devoured them."}
              , {class: "q2", text: "Their adversaries said, ‘We are not guilty,"}
              , {class: "q", text: "because they have sinned against Yahweh,"}
              , {class: "q2", text: "the habitation of righteousness,"}
              , {class: "q2", text: "even Yahweh, the hope of their fathers.’"}
              ]
        , 8:  [ {class: "q", text: "“Flee out of the middle of Babylon!"}
              , {class: "q2", text: "Go out of the land of the Chaldeans,"}
              , {class: "q2", text: "and be as the male goats before the flocks."}
              ]
        , 9:  [ {class: "q", text: "For, behold, I will stir up"}
              , {class: "q2", text: "and cause to come up against Babylon a company of great nations from the north country;"}
              , {class: "q2", text: "and they will set themselves in array against her."}
              , {class: "q", text: "She will be taken from there."}
              , {class: "q2", text: "Their arrows will be as of an expert mighty man."}
              , {class: "q2", text: "None of them will return in vain."}
              ]
        , 10: [ {class: "q", text: "Chaldea will be a prey."}
              , {class: "q2", text: "All who prey on her will be satisfied,” says Yahweh."}
              ]
        , 11: [ {class: "q", text: "“Because you are glad,"}
              , {class: "q2", text: "because you rejoice,"}
              , {class: "q", text: "O you who plunder my heritage,"}
              , {class: "q2", text: "because you are wanton as a heifer that treads out the grain,"}
              , {class: "q2", text: "and neigh as strong horses;"}
              ]
        , 12: [ {class: "q", text: "your mother will be utterly disappointed."}
              , {class: "q2", text: "She who bore you will be confounded."}
              , {class: "q", text: "Behold, she will be the least of the nations,"}
              , {class: "q2", text: "a wilderness, a dry land, and a desert."}
              ]
        , 13: [ {class: "q", text: "Because of Yahweh’s wrath she won’t be inhabited,"}
              , {class: "q2", text: "but she will be wholly desolate."}
              , {class: "q", text: "Everyone who goes by Babylon will be astonished,"}
              , {class: "q2", text: "and hiss at all her plagues."}
              ]
        , 14: [ {class: "q", text: "Set yourselves in array against Babylon all around,"}
              , {class: "q2", text: "all you who bend the bow;"}
              , {class: "q2", text: "shoot at her."}
              , {class: "q", text: "Spare no arrows;"}
              , {class: "q2", text: "for she has sinned against Yahweh."}
              ]
        , 15: [ {class: "q", text: "Shout against her all around."}
              , {class: "q2", text: "She has submitted herself."}
              , {class: "q2", text: "Her bulwarks have fallen."}
              , {class: "q", text: "Her walls have been thrown down,"}
              , {class: "q2", text: "for it is the vengeance of Yahweh."}
              , {class: "q", text: "Take vengeance on her."}
              , {class: "q2", text: "As she has done, do to her."}
              ]
        , 16: [ {class: "q", text: "Cut off the sower from Babylon,"}
              , {class: "q2", text: "and him who handles the sickle in the time of harvest."}
              , {class: "q", text: "For fear of the oppressing sword,"}
              , {class: "q2", text: "they will each return to their own people,"}
              , {class: "q2", text: "and they will each flee to their own land."}
              ]
        , 17: [ {class: "q", text: "“Israel is a hunted sheep."}
              , {class: "q2", text: "The lions have driven him away."}
              , {class: "q", text: "First, the king of Assyria devoured him,"}
              , {class: "q2", text: "and now at last Nebuchadnezzar king of Babylon has broken his bones.”"}
              ]
        , 18: [ {class: "endpara", text: "Therefore Yahweh of Armies, the God of Israel, says:"}
              , {clall: "q", text: "“Behold, I will punish the king of Babylon and his land,"}
              , {class: "q2", text: "as I have punished the king of Assyria."}
              ]
        , 19: [ {class: "q", text: "I will bring Israel again to his pasture,"}
              , {class: "q2", text: "and he will feed on Carmel and Bashan."}
              , {class: "q2", text: "His soul will be satisfied on the hills of Ephraim and in Gilead."}
              ]
        , 20: [ {class: "q", text: "In those days, and in that time,” says Yahweh,"}
              , {class: "q2", text: "“the iniquity of Israel will be sought for,"}
              , {class: "q2", text: "and there will be none;"}
              , {class: "q", text: "also the sins of Judah,"}
              , {class: "q2", text: "and they won’t be found;"}
              , {class: "q2", text: "for I will pardon them whom I leave as a remnant."}
              ]
        , 21: [ {class: "q", text: "“Go up against the land of Merathaim,"}
              ,  {class: "q2", text: "even against it, and against the inhabitants of Pekod."}
              , {class: "q", text: "Kill and utterly destroy after them,” says Yahweh,"}
              , {class: "q2", text: "“and do according to all that I have commanded you."}
              ]
        , 22: [ {class: "q", text: "A sound of battle is in the land,"}
              , {class: "q2", text: "and of great destruction."}
              ]
        , 23: [ {class: "q", text: "How the hammer of the whole earth is cut apart and broken!"}
              , {class: "q2", text: "How Babylon has become a desolation among the nations!"}
              ]
        , 24: [ {class: "q", text: "I have laid a snare for you,"}
              , {class: "q2", text: "and you are also taken, Babylon,"}
              , {class: "q2", text: "and you weren’t aware."}
              , {class: "q", text: "You are found,"}
              , {class: "q2", text: "and also caught,"}
              , {class: "q2", text: "because you have fought against Yahweh."}
              ]
        , 25: [ {class: "q", text: "Yahweh has opened his armory,"}
              , {class: "q2", text: "and has brought out the weapons of his indignation;"}
              , {class: "q2", text: "for the Lord, Yahweh of Armies, has a work to do in the land of the Chaldeans."}
              ]
        , 26: [ {class: "q", text: "Come against her from the farthest border."}
              ,  {class: "q2", text: "Open her storehouses."}
              , {class: "q2", text: "Cast her up as heaps."}
              , {class: "q", text: "Destroy her utterly."}
              , {class: "q2", text: "Let nothing of her be left."}
              ]
        , 27: [ {class: "q", text: "Kill all her bulls."}
              , {class: "q2", text: "Let them go down to the slaughter."}
              , {class: "q", text: "Woe to them! For their day has come,"}
              , {class: "q2", text: "the time of their visitation."}
              ]
        , 28: [ {class: "q", text: "Listen to those who flee and escape out of the land of Babylon,"}
              , {class: "q2", text: "to declare in Zion the vengeance of Yahweh our God,"}
              , {class: "q2", text: "the vengeance of his temple."}
              ]
        , 29: [ {class: "q", text: "“Call together the archers against Babylon,"}
              , {class: "q2", text: "all those who bend the bow."}
              , {class: "q", text: "Encamp against her all around."}
              , {class: "q2", text: "Let none of it escape."}
              , {class: "q", text: "Pay her back according to her work."}
              , {class: "q2", text: "According to all that she has done, do to her;"}
              , {class: "q", text: "for she has been proud against Yahweh,"}
              , {class: "q2", text: "against the Holy One of Israel."}
              ]
        , 30: [ {class: "q", text: "Therefore her young men will fall in her streets."}
              , {class: "q2", text: "All her men of war will be brought to silence in that day,” says Yahweh."}
              ]
        , 31: [ {class: "q", text: "“Behold, I am against you, you proud one,” says the Lord, Yahweh of Armies;"}
              , {class: "q2", text: "“for your day has come,"}
              , {class: "q2", text: "the time that I will visit you."}
              ]
        , 32: [ {class: "q", text: "The proud one will stumble and fall,"}
              , {class: "q2", text: "and no one will raise him up."}
              , {class: "q", text: "I will kindle a fire in his cities,"}
              , {class: "q2", text: "and it will devour all who are around him.”"}
              ]
        , 33: [ {class: "q", text: "Yahweh of Armies says: “The children of Israel and the children of Judah are oppressed together."}
              , {class: "q2", text: "All who took them captive hold them fast."}
              , {class: "q2", text: "They refuse to let them go."}
              ]
        , 34: [ {class: "q", text: "Their Redeemer is strong:"}
              , {class: "q2", text: "Yahweh of Armies is his name."}
              , {class: "q", text: "He will thoroughly plead their cause,"}
              , {class: "q2", text: "that he may give rest to the earth,"}
              , {class: "q2", text: "and disquiet the inhabitants of Babylon."}
              ]
        , 35: [ {class: "q", text: "“A sword is on the Chaldeans,” says Yahweh,"}
              , {class: "q2", text: "“and on the inhabitants of Babylon,"}
              , {class: "q2", text: "on her princes,"}
              , {class: "q2", text: "and on her wise men."}
              ]
        , 36: [ {class: "q", text: "A sword is on the boasters,"}
              , {class: "q2", text: "and they will become fools."}
              , {class: "q", text: "A sword is on her mighty men,"}
              , {class: "q2", text: "and they will be dismayed."}
              ]
        , 37: [ {class: "q", text: "A sword is on their horses,"}
              , {class: "q2", text: "on their chariots,"}
              , {class: "q2", text: "and on all the mixed people who are in the middle of her;"}
              , {class: "q2", text: "and they will become as women."}
              , {class: "q", text: "A sword is on her treasures,"}
              , {class: "q2", text: "and they will be robbed."}
              ]
        , 38: [ {class: "q", text: "A drought is on her waters,"}
              , {class: "q2", text: "and they will be dried up;"}
              , {class: "q", text: "for it is a land of engraved images,"}
              , {class: "q2", text: "and they are mad over idols."}
              ]
        , 39: [ {class: "q", text: "Therefore the wild animals of the desert"}
              , {class: "q2", text: "with the wolves will dwell there."}
              , {class: "q", text: "The ostriches will dwell therein;"}
              , {class: "q2", text: "and it will be inhabited no more forever;"}
              , {class: "q2", text: "neither will it be lived in from generation to generation."}
              ]
        , 40: [ {class: "q", text: "As when God overthrew Sodom and Gomorrah and its neighbor cities,” says Yahweh,"}
              , {class: "q2", text: "“so no man will dwell there,"}
              , {class: "q2", text: "neither will any son of man live therein."}
              ]
        , 41: [ {class: "q", text: "“Behold, a people comes from the north;"}
              , {class: "q2", text: "and a great nation and many kings will be stirred up from the uttermost parts of the earth."}
              ]
        , 42: [ {class: "q", text: "They take up bow and spear."}
              , {class: "q2", text: "They are cruel, and have no mercy."}
              , {class: "q2", text: "Their voice roars like the sea."}
              , {class: "q", text: "They ride on horses,"}
              , {class: "q2", text: "everyone set in array,"}
              , {class: "q2", text: "as a man to the battle,"}
              , {class: "q2", text: "against you, daughter of Babylon."}
              ]
        , 43: [ {class: "q", text: "The king of Babylon has heard the news of them,"}
              , {class: "q2", text: "and his hands become feeble:"}
              , {class: "q", text: "anguish has taken hold of him,"}
              , {class: "q2", text: "pains as of a woman in labor."}
              ]
        , 44: [ {class: "q", text: "Behold, the enemy will come up like a lion"}
              , {class: "q2", text: "from the pride of the Jordan against the strong habitation;"}
              , {class: "q2", text: "for I will suddenly make them run away from it."}
              , {class: "q", text: "Whoever is chosen,"}
              , {class: "q2", text: "I will appoint him over it;"}
              ,  {class: "q2", text: "for who is like me?"}
              , {class: "q", text: "Who will appoint me a time?"}
              , {class: "q2", text: "Who is the shepherd who can stand before me?”"}
              ]
        , 45: [ {class: "q", text: "Therefore hear the counsel of Yahweh,"}
              , {class: "q2", text: "that he has taken against Babylon;"}
              , {class: "q", text: "and his purposes,"}
              , {class: "q2", text: "that he has purposed against the land of the Chaldeans:"}
              , {class: "q", text: "Surely they will drag them away,"}
              , {class: "q2", text: "even the little ones of the flock."}
              , {class: "q2", text: "Surely he will make their habitation desolate over them."}
              ]
        , 46: [ {class: "q", text: "At the noise of the taking of Babylon the earth trembles;"}
              , {class: "q2", text: "and the cry is heard among the nations."}
              ]
        }
  , 51: { 1:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“Behold, I will raise up against Babylon,"}
              , {class: "q2", text: "and against those who dwell in Lebkamai, a destroying wind."}
              ]
        , 2:  [ {class: "q", text: "I will send to Babylon strangers, who will winnow her."}
              , {class: "q2", text: "They will empty her land;"}
              , {class: "q2", text: "for in the day of trouble they will be against her all around."}
              ]
        , 3:  [ {class: "q", text: "Against him who bends, let the archer bend his bow,"}
              ,  {class: "q2", text: "also against him who lifts himself up in his coat of mail."}
              , {class: "q", text: "Don’t spare her young men!"}
              , {class: "q2", text: "Utterly destroy all her army!"}
              ]
        , 4:  [ {class: "q", text: "They will fall down slain in the land of the Chaldeans,"}
              , {class: "q2", text: "and thrust through in her streets."}
              ]
        , 5:  [ {class: "q", text: "For Israel is not forsaken, nor Judah, by his God,"}
              , {class: "q2", text: "by Yahweh of Armies;"}
              , {class: "q2", text: "though their land is full of guilt against the Holy One of Israel."}
              ]
        , 6:  [ {class: "q", text: "“Flee out of the middle of Babylon!"}
              , {class: "q2", text: "Everyone save his own life!"}
              , {class: "q", text: "Don’t be cut off in her iniquity;"}
              , {class: "q2", text: "for it is the time of Yahweh’s vengeance."}
              , {class: "q2", text: "He will render to her a recompense."}
              ]
        , 7:  [ {class: "q", text: "Babylon has been a golden cup in Yahweh’s hand,"}
              , {class: "q2", text: "who made all the earth drunk."}
              , {class: "q", text: "The nations have drunk of her wine;"}
              , {class: "q2", text: "therefore the nations have gone mad."}
              ]
        , 8:  [ {class: "q", text: "Babylon has suddenly fallen and been destroyed!"}
              , {class: "q2", text: "Wail for her!"}
              , {class: "q", text: "Take balm for her pain."}
              , {class: "q2", text: "Perhaps she may be healed."}
              ]
        , 9:  [ {class: "q", text: "“We would have healed Babylon,"}
              , {class: "q2", text: "but she is not healed."}
              , {class: "q", text: "Forsake her,"}
              , {class: "q2", text: "and let’s each go into his own country;"}
              , {class: "q", text: "for her judgment reaches to heaven,"}
              , {class: "q2", text: "and is lifted up even to the skies."}
              ]
        , 10: [ {class: "q", text: "‘Yahweh has produced our righteousness:"}
              , {class: "q2", text: "come, and let’s declare in Zion the work of Yahweh our God.’"}
              ]
        , 11: [ {class: "q", text: "“Make the arrows sharp!"}
              , {class: "q2", text: "Hold the shields firmly!"}
              , {class: "q", text: "Yahweh has stirred up the spirit of the kings of the Medes,"}
              , {class: "q2", text: "because his purpose is against Babylon, to destroy it;"}
              , {class: "q", text: "for it is the vengeance of Yahweh,"}
              , {class: "q2", text: "the vengeance of his temple."}
              ]
        , 12: [ {class: "q", text: "Set up a standard against the walls of Babylon!"}
              , {class: "q2", text: "Make the watch strong!"}
              , {class: "q", text: "Set the watchmen,"}
              , {class: "q2", text: "and prepare the ambushes;"}
              , {class: "q", text: "for Yahweh has both purposed and done"}
              , {class: "q2", text: "that which he spoke concerning the inhabitants of Babylon."}
              ]
        , 13: [ {class: "q", text: "You who dwell on many waters, abundant in treasures,"}
              , {class: "q2", text: "your end has come, the measure of your covetousness."}
              ]
        , 14: [ {class: "q", text: "Yahweh of Armies has sworn by himself, saying,"}
              , {class: "q2", text: "‘Surely I will fill you with men,"}
              , {class: "q2", text: "as with the canker worm;"}
              , {class: "q2", text: "and they will lift up a shout against you.’"}
              ]
        , 15: [ {class: "q", text: "“He has made the earth by his power."}
              , {class: "q2", text: "He has established the world by his wisdom."}
              , {class: "q2", text: "By his understanding he has stretched out the heavens."}
              ]
        , 16: [ {class: "q", text: "When he utters his voice,"}
              , {class: "q2", text: "there is a roar of waters in the heavens,"}
              , {class: "q2", text: "and he causes the vapors to ascend from the ends of the earth."}
              , {class: "q", text: "He makes lightning for the rain,"}
              , {class: "q2", text: "and brings the wind out of his treasuries."}
              ]
        , 17: [ {class: "q", text: "“Every man has become brutish without knowledge."}
              , {class: "q2", text: "Every goldsmith is disappointed by his image;"}
              , {class: "q", text: "for his molten image is falsehood,"}
              , {class: "q2", text: "and there is no breath in them."}
              ]
        , 18: [ {class: "q", text: "They are vanity,"}
              , {class: "q2", text: "a work of delusion."}
              , {class: "q2", text: "In the time of their visitation, they will perish."}
              ]
        , 19: [ {class: "q", text: "The portion of Jacob is not like these,"}
              , {class: "q2", text: "for he is the former of all things;"}
              , {class: "q2", text: "including the tribe of his inheritance:"}
              ,  {class: "q2", text: "Yahweh of Armies is his name."}
              ]
        , 20: [ {class: "q", text: "“You are my battle ax and weapons of war."}
              , {class: "q2", text: "With you I will break the nations into pieces."}
              , {class: "q2", text: "With you I will destroy kingdoms."}
              ]
        , 21: [ {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the horse and his rider."}
              ]
        , 22: [ {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the chariot and him who rides therein."}
              , {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "man and woman."}
              , {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the old man and the youth."}
              , {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the young man and the virgin."}
              ]
        , 23: [ {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the shepherd and his flock."}
              , {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "the farmer and his yoke."}
              , {class: "q", text: "With you I will break in pieces"}
              , {class: "q2", text: "governors and deputies."}
              ]
        , 24: [ {class: "vs", text: "“I will render to Babylon and to all the inhabitants of Chaldea all their evil that they have done in Zion in your sight,” says Yahweh."} ]
        , 25: [ {class: "q", text: "“Behold, I am against you, destroying mountain,” says Yahweh,"}
              , {class: "q2", text: "“which destroys all the earth."}
              , {class: "q", text: "I will stretch out my hand on you,"}
              , {class: "q2", text: "roll you down from the rocks,"}
              , {class: "q2", text: "and will make you a burned mountain."}
              ]
        , 26: [ {class: "q", text: "They won’t take a cornerstone from you,"}
              , {class: "q2", text: "nor a stone for foundations;"}
              , {class: "q2", text: "but you will be desolate forever,” says Yahweh."}
              ]
        , 27: [ {class: "q", text: "“Set up a standard in the land!"}
              , {class: "q2", text: "Blow the trumpet among the nations!"}
              , {class: "q", text: "Prepare the nations against her!"}
              , {class: "q2", text: "Call together against her the kingdoms of Ararat, Minni, and Ashkenaz!"}
              , {class: "q", text: "Appoint a marshal against her!"}
              , {class: "q2", text: "Cause the horses to come up as the rough canker worm!"}
              ]
        , 28: [ {class: "q", text: "Prepare against her the nations,"}
              , {class: "q2", text: "the kings of the Medes, its governors, and all its deputies, and all the land of their dominion!"}
              ]
        , 29: [ {class: "q", text: "The land trembles and is in pain;"}
              , {class: "q2", text: "for the purposes of Yahweh against Babylon stand,"}
              , {class: "q2", text: "to make the land of Babylon a desolation, without inhabitant."}
              ]
        , 30: [ {class: "q", text: "The mighty men of Babylon have stopped fighting,"}
              , {class: "q2", text: "they remain in their strongholds."}
              , {class: "q", text: "Their might has failed."}
              , {class: "q2", text: "They have become as women."}
              , {class: "q", text: "Her dwelling places are set on fire."}
              , {class: "q2", text: "Her bars are broken."}
              ]
        , 31: [ {class: "q", text: "One runner will run to meet another,"}
              , {class: "q2", text: "and one messenger to meet another,"}
              , {class: "q2", text: "to show the king of Babylon that his city is taken on every quarter."}
              ]
        , 32: [ {class: "q", text: "So the passages are seized."}
              , {class: "q2", text: "They have burned the reeds with fire."}
              , {class: "q2", text: "The men of war are frightened.”"}
              ]
        , 33: [ {class: "endpara", text: "For Yahweh of Armies, the God of Israel says:</p><div class=\"“The daughter of Babylon is like a threshing floor at the time when it is trodden."}
              , {class: "q2", text: "Yet a little while, and the time of harvest comes for her.”"}
              ]
        , 34: [ {class: "q", text: "“Nebuchadnezzar the king of Babylon has devoured me."}
              , {class: "q2", text: "He has crushed me."}
              , {class: "q2", text: "He has made me an empty vessel."}
              , {class: "q", text: "He has, like a monster, swallowed me up."}
              , {class: "q2", text: "He has filled his mouth with my delicacies."}
              , {class: "q2", text: "He has cast me out."}
              ]
        , 35: [ {class: "q", text: "May the violence done to me and to my flesh be on Babylon!”"}
              , {class: "q2", text: "the inhabitant of Zion will say; and,"}
              , {class: "q", text: "“May my blood be on the inhabitants of Chaldea!”"}
              , {class: "q2", text: "will Jerusalem say."}
              ]
        , 36: [ {class: "endpara", text: "Therefore Yahweh says:</p><div class=\"“Behold, I will plead your cause,"}
              , {class: "q2", text: "and take vengeance for you."}
              , {class: "q", text: "I will dry up her sea,"}
              , {class: "q2", text: "and make her fountain dry."}
              ]
        , 37: [ {class: "q", text: "Babylon will become heaps,"}
              , {class: "q2", text: "a dwelling place for jackals,"}
              , {class: "q2", text: "an astonishment, and a hissing,"}
              , {class: "q2", text: "without inhabitant."}
              ]
        , 38: [ {class: "q", text: "They will roar together like young lions."}
              , {class: "q2", text: "They will growl as lions’ cubs."}
              ]
        , 39: [ {class: "q", text: "When they are heated, I will make their feast,"}
              , {class: "q2", text: "and I will make them drunk,"}
              , {class: "q", text: "that they may rejoice,"}
              , {class: "q2", text: "and sleep a perpetual sleep,"}
              , {class: "q2", text: "and not wake up,” says Yahweh."}
              ]
        , 40: [ {class: "q", text: "“I will bring them down like lambs to the slaughter,"}
              , {class: "q2", text: "like rams with male goats."}
              ]
        , 41: [ {class: "q", text: "“How Sheshach is taken!"}
              , {class: "q2", text: "How the praise of the whole earth seized!"}
              , {class: "q2", text: "How Babylon has become a desolation among the nations!"}
              ]
        , 42: [ {class: "q", text: "The sea has come up on Babylon."}
              , {class: "q2", text: "She is covered with the multitude of its waves."}
              ]
        , 43: [ {class: "q", text: "Her cities have become a desolation,"}
              , {class: "q2", text: "a dry land, and a desert,"}
              , {class: "q2", text: "a land in which no man dwells."}
              , {class: "q2", text: "No son of man passes by it."}
              ]
        , 44: [ {class: "q", text: "I will execute judgment on Bel in Babylon,"}
              , {class: "q2", text: "and I will bring out of his mouth that which he has swallowed up."}
              , {class: "q", text: "The nations will not flow any more to him."}
              , {class: "q2", text: "Yes, the wall of Babylon will fall."}
              ]
        , 45: [ {class: "q", text: "“My people, go away from the middle of her,"}
              , {class: "q2", text: "and each of you save yourselves from Yahweh’s fierce anger."}
              ]
        , 46: [ {class: "q", text: "Don’t let your heart faint."}
              , {class: "q2", text: "Don’t fear for the news that will be heard in the land."}
              , {class: "q", text: "For news will come one year,"}
              , {class: "q2", text: "and after that in another year news will come,"}
              , {class: "q2", text: "and violence in the land,"}
              , {class: "q2", text: "ruler against ruler."}
              ]
        , 47: [ {class: "q", text: "Therefore behold, the days come that I will execute judgment on the engraved images of Babylon;"}
              , {class: "q2", text: "and her whole land will be confounded."}
              , {class: "q2", text: "All her slain will fall in the middle of her."}
              ]
        , 48: [ {class: "q", text: "Then the heavens and the earth,"}
              , {class: "q2", text: "and all that is therein,"}
              , {class: "q", text: "will sing for joy over Babylon;"}
              , {class: "q2", text: "for the destroyers will come to her from the north,” says Yahweh."}
              ]
        , 49: [ {class: "q", text: "“As Babylon has caused the slain of Israel to fall,"}
              ,  {class: "q2", text: "so the slain of all the land will fall at Babylon."}
              ]
        , 50: [ {class: "q", text: "You who have escaped the sword, go!"}
              , {class: "q2", text: "Don’t stand still!"}
              , {class: "q", text: "Remember Yahweh from afar,"}
              , {class: "q2", text: "and let Jerusalem come into your mind.”"}
              ]
        , 51: [ {class: "q", text: "“We are confounded,"}
              , {class: "q2", text: "because we have heard reproach."}
              , {class: "q", text: "Confusion has covered our faces,"}
              , {class: "q2", text: "for strangers have come into the sanctuaries of Yahweh’s house.”"}
              ]
        , 52: [ {class: "q", text: "“Therefore behold, the days come,” says Yahweh,"}
              ,  {class: "q2", text: "“that I will execute judgment on her engraved images;"}
              , {class: "q2", text: "and through all her land the wounded will groan."}
              ]
        , 53: [ {class: "q", text: "Though Babylon should mount up to the sky,"}
              , {class: "q2", text: "and though she should fortify the height of her strength,"}
              , {class: "q2", text: "yet destroyers will come to her from me,” says Yahweh."}
              ]
        , 54: [ {class: "q", text: "“The sound of a cry comes from Babylon,"}
              , {class: "q2", text: "and of great destruction from the land of the Chaldeans!"}
              ]
        , 55: [ {class: "q", text: "For Yahweh lays Babylon waste,"}
              , {class: "q2", text: "and destroys out of her the great voice!"}
              , {class: "q", text: "Their waves roar like many waters."}
              , {class: "q2", text: "The noise of their voice is uttered."}
              ]
        , 56: [ {class: "q", text: "For the destroyer has come on her,"}
              , {class: "q2", text: "even on Babylon."}
              , {class: "q", text: "Her mighty men are taken."}
              , {class: "q2", text: "Their bows are broken in pieces,"}
              , {class: "q", text: "for Yahweh is a God of retribution."}
              , {class: "q2", text: "He will surely repay."}
              ]
        , 57: [ {class: "q", text: "I will make her princes, her wise men,"}
              , {class: "q2", text: "her governors, her deputies, and her mighty men drunk."}
              , {class: "q", text: "They will sleep a perpetual sleep,"}
              , {class: "q2", text: "and not wake up,”"}
              , {class: "q2", text: "says the King, whose name is Yahweh of Armies."}
              ]
        , 58: [ {class: "endpara", text: "Yahweh of Armies says:</p><div class=\"“The wide walls of Babylon will be utterly overthrown."}
              , {class: "q2", text: "Her high gates will be burned with fire."}
              , {class: "q", text: "The peoples will labor for vanity,"}
              , {class: "q2", text: "and the nations for the fire;"}
              , {class: "q2", text: "and they will be weary.”"}
              ]
        , 59: [ {class: "vs", text: "The word which Jeremiah the prophet commanded Seraiah the son of Neriah, the son of Mahseiah, when he went with Zedekiah the king of Judah to Babylon in the fourth year of his reign. Now Seraiah was chief quartermaster."} ]
        , 60: [ {class: "vs", text: "Jeremiah wrote in a book all the evil that should come on Babylon, even all these words that are written concerning Babylon."} ]
        , 61: [ {class: "vs", text: "Jeremiah said to Seraiah, “When you come to Babylon, then see that you read all these words,"} ]
        , 62: [ {class: "vs", text: "and say, ‘Yahweh, you have spoken concerning this place, to cut it off, that no one will dwell in it, neither man nor animal, but that it will be desolate forever.’"} ]
        , 63: [ {class: "vs", text: "It will be, when you have finished reading this book, that you shall bind a stone to it, and cast it into the middle of the Euphrates."} ]
        , 64: [ {class: "vs", text: "Then you shall say, ‘Thus will Babylon sink, and will not rise again because of the evil that I will bring on her; and they will be weary.’”</p><p> Thus far are the words of Jeremiah."} ]
        }
  , 52: { 1:  [ {class: "vs", text: "Zedekiah was twenty-one years old when he began to reign. He reigned eleven years in Jerusalem: and his mother’s name was Hamutal the daughter of Jeremiah of Libnah."} ]
        , 2:  [ {class: "vs", text: "He did that which was evil in Yahweh’s sight, according to all that Jehoiakim had done."} ]
        , 3:  [ {class: "vs", text: "For through Yahweh’s anger this happened in Jerusalem and Judah, until he had cast them out from his presence.</p><p> Zedekiah rebelled against the king of Babylon."} ]
        , 4:  [ {class: "vs", text: "In the ninth year of his reign, in the tenth month, in the tenth day of the month, Nebuchadnezzar king of Babylon came, he and all his army, against Jerusalem, and encamped against it; and they built forts against it round about."} ]
        , 5:  [ {class: "vs", text: "So the city was besieged to the eleventh year of king Zedekiah."} ]
        , 6:  [ {class: "vs", text: "In the fourth month, in the ninth day of the month, the famine was severe in the city, so that there was no bread for the people of the land."} ]
        , 7:  [ {class: "vs", text: "Then a breach was made in the city, and all the men of war fled, and went out of the city by night by the way of the gate between the two walls, which was by the king’s garden. Now the Chaldeans were against the city all around. The men of war went toward the Arabah,"} ]
        , 8:  [ {class: "vs", text: "but the army of the Chaldeans pursued the king, and overtook Zedekiah in the plains of Jericho; and all his army was scattered from him."} ]
        , 9:  [ {class: "vs", text: "Then they took the king, and carried him up to the king of Babylon to Riblah in the land of Hamath; and he pronounced judgment on him."} ]
        , 10: [ {class: "vs", text: "The king of Babylon killed the sons of Zedekiah before his eyes. He also killed all the princes of Judah in Riblah."} ]
        , 11: [ {class: "vs", text: "He put out the eyes of Zedekiah; and the king of Babylon bound him in fetters, and carried him to Babylon, and put him in prison until the day of his death."} ]
        , 12: [ {class: "vs", text: "Now in the fifth month, in the tenth day of the month, which was the nineteenth year of king Nebuchadnezzar, king of Babylon, Nebuzaradan the captain of the guard, who stood before the king of Babylon, came into Jerusalem."} ]
        , 13: [ {class: "vs", text: "He burned Yahweh’s house, and the king’s house; and all the houses of Jerusalem, even every great house, he burned with fire."} ]
        , 14: [ {class: "vs", text: "All the army of the Chaldeans, who were with the captain of the guard, broke down all the walls of Jerusalem all around."} ]
        , 15: [ {class: "vs", text: "Then Nebuzaradan the captain of the guard carried away captive of the poorest of the people, and the residue of the people who were left in the city, and those who fell away, who fell to the king of Babylon, and the residue of the multitude."} ]
        , 16: [ {class: "vs", text: "But Nebuzaradan the captain of the guard left of the poorest of the land to be vineyard keepers and farmers."} ]
        , 17: [ {class: "vs", text: "The Chaldeans broke the pillars of bronze that were in Yahweh’s house, and the bases and the bronze sea that were in Yahweh’s house in pieces, and carried all of their bronze to Babylon."} ]
        , 18: [ {class: "vs", text: "They also took away the pots, the shovels, the snuffers, the basins, the spoons, and all the vessels of bronze with which they ministered."} ]
        , 19: [ {class: "vs", text: "The captain of the guard took away the cups, the fire pans, the basins, the pots, the lamp stands, the spoons, and the bowls; that which was of gold, in gold, and that which was of silver, in silver."} ]
        , 20: [ {class: "vs", text: "They took the two pillars, the one sea, and the twelve bronze bulls that were under the bases, which king Solomon had made for Yahweh’s house. The bronze of all these vessels was without weight."} ]
        , 21: [ {class: "vs", text: "As for the pillars, the height of the one pillar was eighteen cubits; and a line of twelve cubits encircled it; and its thickness was four fingers. It was hollow."} ]
        , 22: [ {class: "vs", text: "A capital of bronze was on it; and the height of the one capital was five cubits, with network and pomegranates on the capital all around, all of bronze: and the second pillar also had like these, and pomegranates."} ]
        , 23: [ {class: "vs", text: "There were ninety-six pomegranates on the sides; all the pomegranates were one hundred on the network all around."} ]
        , 24: [ {class: "vs", text: "The captain of the guard took Seraiah the chief priest, and Zephaniah the second priest, and the three keepers of the threshold:"} ]
        , 25: [ {class: "vs", text: "and out of the city he took an officer who was set over the men of war; and seven men of those who saw the king’s face, who were found in the city; and the scribe of the captain of the army, who mustered the people of the land; and sixty men of the people of the land, who were found in the middle of the city."} ]
        , 26: [ {class: "vs", text: "Nebuzaradan the captain of the guard took them, and brought them to the king of Babylon to Riblah."} ]
        , 27: [ {class: "vs", text: "The king of Babylon struck them, and put them to death at Riblah in the land of Hamath.</p><p> So Judah was carried away captive out of his land."} ]
        , 28: [ {class: "vs", text: "This is the number of the people whom Nebuchadnezzar carried away captive:</p><p class=\"m\"> in the seventh year, three thousand twenty-three Jews;"} ]
        , 29: [ {class: "vs", text: "in the eighteenth year of Nebuchadnezzar, he carried away captive from Jerusalem eight hundred thirty-two persons;"} ]
        , 30: [ {class: "vs", text: "in the twenty-third year of Nebuchadnezzar Nebuzaradan the captain of the guard carried away captive of the Jews seven hundred forty-five people:</p><p class=\"m\"> all the people were four thousand six hundred."} ]
        , 31: [ {class: "vs", text: "In the thirty-seventh year of the captivity of Jehoiachin king of Judah, in the twelfth month, in the twenty-fifth day of the month, Evilmerodach king of Babylon, in the first year of his reign, lifted up the head of Jehoiachin king of Judah, and released him from prison."} ]
        , 32: [ {class: "vs", text: "He spoke kindly to him, and set his throne above the throne of the kings who were with him in Babylon,"} ]
        , 33: [ {class: "vs", text: "and changed his prison garments. Jehoiachin ate bread before him continually all the days of his life."} ]
        , 34: [ {class: "vs", text: "For his allowance, there was a continual allowance given him by the king of Babylon, every day a portion until the day of his death, all the days of his life."} ]
          }
  , 6: { 1:  [ {class: "vs", text: "“Flee for safety, you children of Benjamin, out of the middle of Jerusalem! Blow the trumpet in Tekoa and raise up a signal on Beth Haccherem, for evil looks out from the north with a great destruction."} ]
        , 2:  [ {class: "vs", text: "I will cut off the beautiful and delicate one, the daughter of Zion."} ]
        , 3:  [ {class: "vs", text: "Shepherds with their flocks will come to her. They will pitch their tents against her all around. They will feed everyone in his place.”"} ]
        , 4:  [ {class: "vs", text: "“Prepare war against her! Arise! Let’s go up at noon. Woe to us! For the day declines, for the shadows of the evening are stretched out."} ]
        , 5:  [ {class: "vs", text: "Arise! Let’s go up by night, and let’s destroy her palaces.”"} ]
        , 6:  [ {class: "vs", text: "For Yahweh of Armies said, “Cut down trees, and cast up a mound against Jerusalem. This is the city to be visited. She is wholly oppression within herself."} ]
        , 7:  [ {class: "vs", text: "As a well produces its waters, so she produces her wickedness. Violence and destruction is heard in her. Sickness and wounds are continually before me."} ]
        , 8:  [ {class: "vs", text: "Be instructed, Jerusalem, lest my soul be alienated from you, lest I make you a desolation, an uninhabited land.”"} ]
        , 9:  [ {class: "vs", text: "Yahweh of Armies says, “They will thoroughly glean the remnant of Israel like a vine. Turn again your hand as a grape gatherer into the baskets.”"} ]
        , 10: [ {class: "vs", text: "To whom should I speak and testify, that they may hear? Behold, their ear is uncircumcised, and they can’t listen. Behold, Yahweh’s word has become a reproach to them. They have no delight in it."} ]
        , 11: [ {class: "", text: "Therefore I am full of Yahweh’s wrath. I am weary with holding it in.</p><div class=\"“Pour it out on the children in the street,"}
              , {class: "q2", text: "and on the assembly of young men together;"}
              , {class: "q", text: "for even the husband with the wife will be taken,"}
              , {class: "q2", text: "the aged with him who is full of days."}
              ]
        , 12: [ {class: "q", text: "Their houses will be turned to others,"}
              , {class: "q2", text: "their fields and their wives together;"}
              , {class: "q", text: "for I will stretch out my hand on the inhabitants of the land, says Yahweh.”"}
              ]
        , 13: [ {class: "q", text: "“For from their least even to their greatest, everyone is given to covetousness."}
              , {class: "q2", text: "From the prophet even to the priest, everyone deals falsely."}
              ]
        , 14: [ {class: "q", text: "They have healed also the hurt of my people superficially,"}
              , {class: "q2", text: "saying, ‘Peace, peace!’ when there is no peace."}
              ]
        , 15: [ {class: "q", text: "Were they ashamed when they had committed abomination?"}
              , {class: "q2", text: "No, they were not at all ashamed, neither could they blush."}
              , {class: "q", text: "Therefore they will fall among those who fall."}
              , {class: "q2", text: "When I visit them, they will be cast down,” says Yahweh."}
              ]
        , 16: [ {class: "vs", text: "Yahweh says, “Stand in the ways and see, and ask for the old paths, ‘Where is the good way?’ and walk in it, and you will find rest for your souls. But they said, ‘We will not walk in it.’"} ]
        , 17: [ {class: "vs", text: "I set watchmen over you, saying, ‘Listen to the sound of the trumpet!’ But they said, ‘We will not listen!’"} ]
        , 18: [ {class: "vs", text: "Therefore hear, you nations, and know, congregation, what is among them."} ]
        , 19: [ {class: "vs", text: "Hear, earth! Behold, I will bring evil on this people, even the fruit of their thoughts, because they have not listened to my words; and as for my law, they have rejected it."} ]
        , 20: [ {class: "vs", text: "To what purpose does frankincense from Sheba come to me, and the sweet cane from a far country? Your burnt offerings are not acceptable, and your sacrifices are not pleasing to me.”"} ]
        , 21: [ {class: "vs", text: "Therefore Yahweh says, “Behold, I will lay stumbling blocks before this people. The fathers and the sons together will stumble against them. The neighbor and his friend will perish.”"} ]
        , 22: [ {class: "vs", text: "Yahweh says, “Behold, a people comes from the north country. A great nation will be stirred up from the uttermost parts of the earth."} ]
        , 23: [ {class: "vs", text: "They take hold of bow and spear. They are cruel, and have no mercy. Their voice roars like the sea, and they ride on horses, everyone set in array, as a man to the battle, against you, daughter of Zion.”"} ]
        , 24: [ {class: "vs", text: "We have heard its report. Our hands become feeble. Anguish has taken hold of us, and pains as of a woman in labor."} ]
        , 25: [ {class: "vs", text: "Don’t go out into the field or walk by the way; for the sword of the enemy and terror are on every side."} ]
        , 26: [ {class: "vs", text: "Daughter of my people, clothe yourself with sackcloth, and wallow in ashes! Mourn, as for an only son, most bitter lamentation, for the destroyer will suddenly come on us."} ]
        , 27: [ {class: "vs", text: "“I have made you a tester of metals and a fortress among my people, that you may know and try their way."} ]
        , 28: [ {class: "vs", text: "They are all grievous rebels, going around to slander. They are bronze and iron. All of them deal corruptly."} ]
        , 29: [ {class: "vs", text: "The bellows blow fiercely. The lead is consumed in the fire. In vain they go on refining, for the wicked are not plucked away."} ]
        , 30: [ {class: "vs", text: "Men will call them rejected silver, because Yahweh has rejected them.”"} ]
        }
  , 7:  { 1:  [ {class: "vs", text: "The word that came to Jeremiah from Yahweh, saying,"} ]
        , 2:  [ {class: "vs", text: "“Stand in the gate of Yahweh’s house, and proclaim this word there, and say, ‘Hear Yahweh’s word, all you of Judah, who enter in at these gates to worship Yahweh.’”"} ]
        , 3:  [ {class: "vs", text: "Yahweh of Armies, the God of Israel says, “Amend your ways and your doings, and I will cause you to dwell in this place."} ]
        , 4:  [ {class: "vs", text: "Don’t trust in lying words, saying, ‘Yahweh’s temple, Yahweh’s temple, Yahweh’s temple, are these.’"} ]
        , 5:  [ {class: "vs", text: "For if you thoroughly amend your ways and your doings, if you thoroughly execute justice between a man and his neighbor;"} ]
        , 6:  [ {class: "vs", text: "if you don’t oppress the foreigner, the fatherless, and the widow, and don’t shed innocent blood in this place, and don’t walk after other gods to your own hurt;"} ]
        , 7:  [ {class: "vs", text: "then I will cause you to dwell in this place, in the land that I gave to your fathers, from of old even forever more."} ]
        , 8:  [ {class: "vs", text: "Behold, you trust in lying words that can’t profit."} ]
        , 9:  [ {class: "vs", text: "Will you steal, murder, commit adultery, swear falsely, burn incense to Baal, and walk after other gods that you have not known,"} ]
        , 10: [ {class: "vs", text: "then come and stand before me in this house, which is called by my name, and say, ‘We are delivered,’ that you may do all these abominations?"} ]
        , 11: [ {class: "vs", text: "Has this house, which is called by my name, become a den of robbers in your eyes? Behold, I myself have seen it,” says Yahweh."} ]
        , 12: [ {class: "vs", text: "“But go now to my place which was in Shiloh, where I caused my name to dwell at the first, and see what I did to it for the wickedness of my people Israel."} ]
        , 13: [ {class: "vs", text: "Now, because you have done all these works,” says Yahweh, “and I spoke to you, rising up early and speaking, but you didn’t hear; and I called you, but you didn’t answer;"} ]
        , 14: [ {class: "vs", text: "therefore I will do to the house which is called by my name, in which you trust, and to the place which I gave to you and to your fathers, as I did to Shiloh."} ]
        , 15: [ {class: "vs", text: "I will cast you out of my sight, as I have cast out all your brothers, even the whole offspring of Ephraim."} ]
        , 16: [ {class: "vs", text: "“Therefore don’t pray for this people. Don’t lift up a cry or prayer for them or make intercession to me; for I will not hear you."} ]
        , 17: [ {class: "vs", text: "Don’t you see what they do in the cities of Judah and in the streets of Jerusalem?"} ]
        , 18: [ {class: "vs", text: "The children gather wood, and the fathers kindle the fire, and the women knead the dough, to make cakes to the queen of the sky, and to pour out drink offerings to other gods, that they may provoke me to anger."} ]
        , 19: [ {class: "vs", text: "Do they provoke me to anger?” says Yahweh. “Don’t they provoke themselves, to the confusion of their own faces?”"} ]
        , 20: [ {class: "vs", text: "Therefore the Lord Yahweh says: “Behold, my anger and my wrath will be poured out on this place, on man, on animal, on the trees of the field, and on the fruit of the ground; and it will burn and will not be quenched.”"} ]
        , 21: [ {class: "vs", text: "Yahweh of Armies, the God of Israel says: “Add your burnt offerings to your sacrifices and eat meat."} ]
        , 22: [ {class: "vs", text: "For I didn’t speak to your fathers or command them in the day that I brought them out of the land of Egypt concerning burnt offerings or sacrifices;"} ]
        , 23: [ {class: "vs", text: "but this thing I commanded them, saying, ‘Listen to my voice, and I will be your God, and you shall be my people. Walk in all the way that I command you, that it may be well with you.’"} ]
        , 24: [ {class: "vs", text: "But they didn’t listen or turn their ear, but walked in their own counsels and in the stubbornness of their evil heart, and went backward, and not forward."} ]
        , 25: [ {class: "vs", text: "Since the day that your fathers came out of the land of Egypt to this day, I have sent to you all my servants the prophets, daily rising up early and sending them."} ]
        , 26: [ {class: "vs", text: "Yet they didn’t listen to me or incline their ear, but made their neck stiff. They did worse than their fathers."} ]
        , 27: [ {class: "vs", text: "“You shall speak all these words to them, but they will not listen to you. You shall also call to them, but they will not answer you."} ]
        , 28: [ {class: "vs", text: "You shall tell them, ‘This is the nation that has not listened to Yahweh their God’s voice, nor received instruction. Truth has perished, and is cut off from their mouth.’"} ]
        , 29: [ {class: "vs", text: "Cut off your hair, and throw it away, and take up a lamentation on the bare heights; for Yahweh has rejected and forsaken the generation of his wrath."} ]
        , 30: [ {class: "vs", text: "“For the children of Judah have done that which is evil in my sight,” says Yahweh. “They have set their abominations in the house which is called by my name, to defile it."} ]
        , 31: [ {class: "vs", text: "They have built the high places of Topheth, which is in the valley of the son of Hinnom, to burn their sons and their daughters in the fire; which I didn’t command, nor did it come into my mind."} ]
        , 32: [ {class: "vs", text: "Therefore behold, the days come”, says Yahweh, “that it will no more be called ‘Topheth’ or ‘The valley of the son of Hinnom’, but ‘The valley of Slaughter’; for they will bury in Topheth until there is no place to bury."} ]
        , 33: [ {class: "vs", text: "The dead bodies of this people will be food for the birds of the sky, and for the animals of the earth. No one will frighten them away."} ]
        , 34: [ {class: "vs", text: "Then I will cause to cease from the cities of Judah, and from the streets of Jerusalem, the voice of mirth and the voice of gladness, the voice of the bridegroom and the voice of the bride; for the land will become a waste.”"} ]
        }
  , 8:  { 1:  [ {class: "vs", text: "“At that time,” says Yahweh, “they will bring the bones of the kings of Judah, the bones of his princes, the bones of the priests, the bones of the prophets, and the bones of the inhabitants of Jerusalem, out of their graves."} ]
        , 2:  [ {class: "vs", text: "They will spread them before the sun, the moon, and all the army of the sky, which they have loved, which they have served, after which they have walked, which they have sought, and which they have worshiped. They will not be gathered or be buried. They will be like dung on the surface of the earth."} ]
        , 3:  [ {class: "vs", text: "Death will be chosen rather than life by all the residue that remain of this evil family, that remain in all the places where I have driven them,” says Yahweh of Armies."} ]
        , 4:  [ {class: "endpara", text: "“Moreover you shall tell them, ‘Yahweh says:"}
              , {class: "q", text: "“‘Do men fall, and not rise up again?"}
              , {class: "q2", text: "Does one turn away, and not return?"}
              ]
        , 5:  [ {class: "q", text: "Why then have the people of Jerusalem fallen back by a perpetual backsliding?"}
              , {class: "q2", text: "They cling to deceit."}
              , {class: "q2", text: "They refuse to return."}
              ]
        , 6:  [ {class: "q", text: "I listened and heard, but they didn’t say what is right."}
              , {class: "q2", text: "No one repents of his wickedness, saying, “What have I done?”"}
              , {class: "q", text: "Everyone turns to his course,"}
              , {class: "q2", text: "as a horse that rushes headlong in the battle."}
              ]
        , 7:  [ {class: "q", text: "Yes, the stork in the sky knows her appointed times."}
              , {class: "q2", text: "The turtledove, the swallow, and the crane observe the time of their coming;"}
              , {class: "q2", text: "but my people don’t know Yahweh’s law."}
              ]
        , 8:  [ {class: "q", text: "“‘How do you say, “We are wise, and Yahweh’s law is with us?”"}
              , {class: "q2", text: "But, behold, the false pen of the scribes has made that a lie."}
              ]
        , 9:  [ {class: "q", text: "The wise men are disappointed."}
              , {class: "q2", text: "They are dismayed and trapped."}
              , {class: "q", text: "Behold, they have rejected Yahweh’s word."}
              , {class: "q2", text: "What kind of wisdom is in them?"}
              ]
        , 10: [ {class: "q", text: "Therefore I will give their wives to others"}
              , {class: "q2", text: "and their fields to those who will possess them."}
              , {class: "q", text: "For everyone from the least even to the greatest is given to covetousness;"}
              , {class: "q2", text: "from the prophet even to the priest everyone deals falsely."}
              ]
        , 11: [ {class: "q", text: "They have healed the hurt of the daughter of my people slightly, saying,"}
              , {class: "q2", text: "“Peace, peace,” when there is no peace."}
              ]
        , 12: [ {class: "q", text: "Were they ashamed when they had committed abomination?"}
              , {class: "q2", text: "No, they were not at all ashamed."}
              , {class: "q2", text: "They couldn’t blush."}
              , {class: "q", text: "Therefore they will fall among those who fall."}
              , {class: "q2", text: "In the time of their visitation they will be cast down, says Yahweh."}
              ]
        , 13: [ {class: "q", text: "“‘I will utterly consume them, says Yahweh."}
              , {class: "q2", text: "No grapes will be on the vine,"}
              , {class: "q2", text: "no figs on the fig tree,"}
              , {class: "q2", text: "and the leaf will fade."}
              , {class: "q", text: "The things that I have given them"}
              , {class: "q2", text: "will pass away from them.’”"}
              ]
        , 14: [ {class: "q", text: "“Why do we sit still?"}
              , {class: "q2", text: "Assemble yourselves!"}
              , {class: "q2", text: "Let’s enter into the fortified cities,"}
              , {class: "q2", text: "and let’s be silent there;"}
              , {class: "q", text: "for Yahweh our God has put us to silence,"}
              , {class: "q2", text: "and given us poisoned water to drink,"}
              , {class: "q2", text: "because we have sinned against Yahweh."}
              ]
        , 15: [ {class: "q", text: "We looked for peace, but no good came;"}
              , {class: "q2", text: "and for a time of healing, and behold, dismay!"}
              ]
        , 16: [ {class: "q", text: "The snorting of his horses is heard from Dan."}
              , {class: "q2", text: "The whole land trembles at the sound of the neighing of his strong ones;"}
              , {class: "q", text: "for they have come, and have devoured the land and all that is in it,"}
              , {class: "q2", text: "the city and those who dwell therein.”"}
              ]
        , 17: [ {class: "q", text: "“For, behold, I will send serpents,"}
              , {class: "q2", text: "adders among you,"}
              , {class: "q2", text: "which will not be charmed;"}
              , {class: "q2", text: "and they will bite you,” says Yahweh."}
              ]
        , 18: [ {class: "q", text: "Oh that I could comfort myself against sorrow!"}
              , {class: "q2", text: "My heart is faint within me."}
              ]
        , 19: [ {class: "q", text: "Behold, the voice of the cry of the daughter of my people from a land that is very far off:"}
              , {class: "q2", text: "“Isn’t Yahweh in Zion?"}
              , {class: "q2", text: "Isn’t her King in her?”</div><br /><div class=\"“Why have they provoked me to anger with their engraved images,"}
              , {class: "q2", text: "and with foreign idols?”"}
              ]
        , 20: [ {class: "q", text: "“The harvest is past."}
              , {class: "q2", text: "The summer has ended,"}
              , {class: "q2", text: "and we are not saved.”"}
              ]
        , 21: [ {class: "q", text: "For the hurt of the daughter of my people, I am hurt."}
              , {class: "q2", text: "I mourn."}
              , {class: "q2", text: "Dismay has taken hold of me."}
              ]
        , 22: [ {class: "q", text: "Is there no balm in Gilead?"}
              , {class: "q2", text: "Is there no physician there?"}
              , {class: "q", text: "Why then isn’t the health of the daughter of my people recovered?"}
              ]
        }
  , 9:  { 1:  [ {class: "q", text: "Oh that my head were waters,"}
              , {class: "q2", text: "and my eyes a spring of tears,"}
              , {class: "q", text: "that I might weep day and night"}
              , {class: "q2", text: "for the slain of the daughter of my people!"}
              ]
        , 2:  [ {class: "q", text: "Oh that I had in the wilderness"}
              ,  {class: "q2", text: "a lodging place of wayfaring men;"}
              , {class: "q", text: "that I might leave my people,"}
              , {class: "q2", text: "and go from them!"}
              , {class: "q", text: "For they are all adulterers,"}
              , {class: "q2", text: "an assembly of treacherous men."}
              ]
        , 3:  [ {class: "q", text: "“They bend their tongue,"}
              , {class: "q2", text: "as their bow, for falsehood."}
              , {class: "q", text: "They have grown strong in the land,"}
              , {class: "q2", text: "but not for truth;"}
              , {class: "q", text: "for they proceed from evil to evil,"}
              , {class: "q2", text: "and they don’t know me,” says Yahweh."}
              ]
        , 4:  [ {class: "q", text: "“Everyone beware of his neighbor,"}
              , {class: "q2", text: "and don’t trust in any brother;"}
              , {class: "q", text: "for every brother will utterly supplant,"}
              , {class: "q2", text: "and every neighbor will go around like a slanderer."}
              ]
        , 5:  [ {class: "q", text: "Friends deceive each other,"}
              , {class: "q2", text: "and will not speak the truth."}
              , {class: "q", text: "They have taught their tongue to speak lies."}
              , {class: "q2", text: "They weary themselves commiting iniquity."}
              ]
        , 6:  [ {class: "q", text: "Your habitation is in the middle of deceit."}
              , {class: "q2", text: "Through deceit, they refuse to know me,” says Yahweh."}
              ]
        , 7:  [ {class: "endpara", text: "Therefore Yahweh of Armies says,"}
              , {class: "q", text: "“Behold, I will melt them and test them;"}
              , {class: "q2", text: "for how should I deal with the daughter of my people?"}
              ]
        , 8:  [ {class: "q", text: "Their tongue is a deadly arrow."}
              ,  {class: "q2", text: "It speaks deceit."}
              , {class: "q", text: "One speaks peaceably to his neighbor with his mouth,"}
              , {class: "q2", text: "but in his heart, he waits to ambush him."}
              ]
        , 9:  [ {class: "q", text: "Shouldn’t I punish them for these things?” says Yahweh."}
              , {class: "q2", text: "“Shouldn’t my soul be avenged on a nation such as this?"}
              ]
        , 10: [ {class: "q", text: "I will weep and wail for the mountains,"}
              , {class: "q2", text: "and lament for the pastures of the wilderness,"}
              , {class: "q", text: "because they are burned up, so that no one passes through;"}
              , {class: "q2", text: "Men can’t hear the voice of the livestock."}
              , {class: "q", text: "Both the birds of the sky and the animals have fled."}
              , {class: "q2", text: "They are gone."}
              ]
        , 11: [ {class: "q", text: "“I will make Jerusalem heaps,"}
              , {class: "q2", text: "a dwelling place of jackals."}
              , {class: "q", text: "I will make the cities of Judah a desolation,"}
              , {class: "q2", text: "without inhabitant.”"}
              ]
        , 12: [ {class: "vs", text: "Who is wise enough to understand this? Who is he to whom the mouth of Yahweh has spoken, that he may declare it? Why has the land perished and burned up like a wilderness, so that no one passes through?"} ]
        , 13: [ {class: "vs", text: "Yahweh says, “Because they have forsaken my law which I set before them, and have not obeyed my voice or walked in my ways,"} ]
        , 14: [ {class: "vs", text: "but have walked after the stubbornness of their own heart and after the Baals, which their fathers taught them.”"} ]
        , 15: [ {class: "vs", text: "Therefore Yahweh of Armies, the God of Israel, says, “Behold, I will feed them, even this people, with wormwood and give them poisoned water to drink."} ]
        , 16: [ {class: "vs", text: "I will scatter them also among the nations, whom neither they nor their fathers have known. I will send the sword after them, until I have consumed them.”"} ]
        , 17: [ {class: "endpara", text: "Yahweh of Armies says,"}
              , {class: "q", text: "“Consider, and call for the mourning women, that they may come."}
              , {class: "q2", text: "Send for the skillful women, that they may come."}
              ]
        , 18: [ {class: "q", text: "Let them make haste"}
              , {class: "q2", text: "and take up a wailing for us,"}
              , {class: "q", text: "that our eyes may run down with tears"}
              , {class: "q2", text: "and our eyelids gush out with waters."}
              ]
        , 19: [ {class: "q", text: "For a voice of wailing is heard out of Zion,"}
              , {class: "q2", text: "‘How we are ruined!"}
              , {class: "q", text: "We are greatly confounded"}
              , {class: "q2", text: "because we have forsaken the land,"}
              , {class: "q2", text: "because they have cast down our dwellings.’”"}
              ]
        , 20: [ {class: "q", text: "Yet hear Yahweh’s word, you women."}
              , {class: "q2", text: "Let your ear receive the word of his mouth."}
              , {class: "q", text: "Teach your daughters wailing."}
              , {class: "q2", text: "Everyone teach her neighbor a lamentation."}
              ]
        , 21: [ {class: "q", text: "For death has come up into our windows."}
              , {class: "q2", text: "It has entered into our palaces"}
              , {class: "q", text: "to cut off the children from outside,"}
              , {class: "q2", text: "and the young men from the streets."}
              ]
        , 22: [ {class: "endpara", text: "Speak, “Yahweh says,"}
              , {class: "q", text: "“‘The dead bodies of men will fall as dung on the open field,"}
              , {class: "q2", text: "and as the handful after the harvester."}
              , {class: "q2", text: "No one will gather them.’”"}
              ]
        , 23: [ {class: "endpara", text: "Yahweh says,"}
              , {class: "q", text: "“Don’t let the wise man glory in his wisdom."}
              , {class: "q2", text: "Don’t let the mighty man glory in his might."}
              , {class: "q2", text: "Don’t let the rich man glory in his riches."}
              ]
        , 24: [ {class: "q", text: "But let him who glories glory in this,"}
              , {class: "q2", text: "that he has understanding, and knows me,"}
              , {class: "q", text: "that I am Yahweh who exercises loving kindness, justice, and righteousness in the earth,"}
              , {class: "q2", text: "for I delight in these things,” says Yahweh."}
              ]
        , 25: [ {class: "vs", text: "“Behold, the days come,” says Yahweh, “that I will punish all those who are circumcised only in their flesh:"} ]
        , 26: [ {class: "vs", text: "Egypt, Judah, Edom, the children of Ammon, Moab, and all who have the corners of their hair cut off, who dwell in the wilderness, for all the nations are uncircumcised, and all the house of Israel are uncircumcised in heart.”"} ]
        }
  }