exports.psalms =
  { 1: { 1:   [ {class: "q", text: "Blessed is the man who doesn’t walk in the counsel of the wicked,"}
              , {class: "q2", text: "nor stand on the path of sinners,"}
              , {class: "q2", text: "nor sit in the seat of scoffers;"}
              ]
        , 2:  [ {class: "q", text: "but his delight is in Yahweh’s law."}
              , {class: "q2", text: "On his law he meditates day and night."}
              ]
        , 3:  [ {class: "q", text: "He will be like a tree planted by the streams of water,"}
              , {class: "q2", text: "that produces its fruit in its season,"}
              , {class: "q2", text: "whose leaf also does not wither."}
              , {class: "q2", text: "Whatever he does shall prosper."}
              ]
        , 4:  [ {class: "q", text: "The wicked are not so,"}
              , {class: "q2", text: "but are like the chaff which the wind drives away."}
              ]
        , 5:  [ {class: "q", text: "Therefore the wicked shall not stand in the judgment,"}
              , {class: "q2", text: "nor sinners in the congregation of the righteous."}
              ]
        , 6:  [ {class: "q", text: "For Yahweh knows the way of the righteous,"}
              , {class: "q2", text: "but the way of the wicked shall perish."}
              ]
        }
  , 10: { 1:  [ {class: "q", text: "Why do you stand far off, Yahweh?"}
              , {class: "q2", text: "Why do you hide yourself in times of trouble?"}
              ]
        , 2:  [ {class: "q", text: "In arrogance, the wicked hunt down the weak."}
              , {class: "q2", text: "They are caught in the schemes that they devise."}
              ]
        , 3:  [ {class: "q", text: "For the wicked boasts of his heart’s cravings."}
              , {class: "q2", text: "He blesses the greedy and condemns Yahweh."}
              ]
        , 4:  [ {class: "q", text: "The wicked, in the pride of his face,"}
              , {class: "q2", text: "has no room in his thoughts for God."}
              ]
        , 5:  [ {class: "q", text: "His ways are prosperous at all times."}
              , {class: "q2", text: "He is arrogant, and your laws are far from his sight."}
              , {class: "q", text: "As for all his adversaries, he sneers at them."}
              ]
        , 6:  [ {class: "q2", text: "/span> He says in his heart, “I shall not be shaken."}
              , {class: "q2", text: "For generations I shall have no trouble.”"}
              ]
        , 7:  [ {class: "q", text: "His mouth is full of cursing, deceit, and oppression."}
              , {class: "q2", text: "Under his tongue is mischief and iniquity."}
              ]
        , 8:  [ {class: "q", text: "He lies in wait near the villages."}
              , {class: "q2", text: "From ambushes, he murders the innocent."}
              , {class: "q", text: "His eyes are secretly set against the helpless."}
              ]
        , 9:  [ {class: "q", text: "He lurks in secret as a lion in his ambush."}
              , {class: "q2", text: "He lies in wait to catch the helpless."}
              , {class: "q2", text: "He catches the helpless when he draws him in his net."}
              ]
        , 10: [ {class: "q", text: "The helpless are crushed."}
              , {class: "q2", text: "They collapse."}
              , {class: "q2", text: "They fall under his strength."}
              ]
        , 11: [ {class: "q", text: "He says in his heart, “God has forgotten."}
              , {class: "q2", text: "He hides his face."}
              , {class: "q2", text: "He will never see it.”"}
              ]
        , 12: [ {class: "q", text: "Arise, Yahweh!"}
              , {class: "q2", text: "God, lift up your hand!"}
              , {class: "q2", text: "Don’t forget the helpless."}
              ]
        , 13: [ {class: "q", text: "Why does the wicked person condemn God,"}
              , {class: "q2", text: "and say in his heart, “God won’t call me into account?”"}
              ]
        , 14: [ {class: "q", text: "But you do see trouble and grief."}
              , {class: "q2", text: "You consider it to take it into your hand."}
              , {class: "q2", text: "You help the victim and the fatherless."}
              ]
        , 15: [ {class: "q", text: "Break the arm of the wicked."}
              , {class: "q2", text: "As for the evil man, seek out his wickedness until you find none."}
              ]
        , 16: [ {class: "q", text: "Yahweh is King forever and ever!"}
              , {class: "q2", text: "The nations will perish out of his land."}
              ]
        , 17: [ {class: "q", text: "Yahweh, you have heard the desire of the humble."}
              , {class: "q2", text: "You will prepare their heart."}
              , {class: "q2", text: "You will cause your ear to hear,"}
              ]
        , 18: [ {class: "q2", text: "</span> to judge the fatherless and the oppressed,"}
              , {class: "q2", text: "that man who is of the earth may terrify no more."}
              ]
        }
  , 100:{ 1:  [ {class: "q", text: "Shout for joy to Yahweh, all you lands!"} ]
        , 2:  [ {class: "q2", text: "/span> Serve Yahweh with gladness."}
              , {class: "q2", text: "Come before his presence with singing."}
              ]
        , 3:  [ {class: "q", text: "Know that Yahweh, he is God."}
              , {class: "q2", text: "It is he who has made us, and we are his."}
              , {class: "q2", text: "We are his people, and the sheep of his pasture."}
              ]
        , 4:  [ {class: "q", text: "Enter into his gates with thanksgiving,"}
              , {class: "q2", text: "and into his courts with praise."}
              , {class: "q2", text: "Give thanks to him, and bless his name."}
              ]
        , 5:  [ {class: "q", text: "For Yahweh is good."}
              , {class: "q2", text: "His loving kindness endures forever,"}
              , {class: "q2", text: "his faithfulness to all generations."}
              ]
        }
  , 101:{ 1:  [ {class: "q", text: "I will sing of loving kindness and justice."}
              , {class: "q2", text: "To you, Yahweh, I will sing praises."}
              ]
        , 2:  [ {class: "q", text: "I will be careful to live a blameless life."}
              , {class: "q2", text: "When will you come to me?"}
              , {class: "q2", text: "I will walk within my house with a blameless heart."}
              ]
        , 3:  [ {class: "q", text: "I will set no vile thing before my eyes."}
              , {class: "q2", text: "I hate the deeds of faithless men."}
              , {class: "q2", text: "They will not cling to me."}
              ]
        , 4:  [ {class: "q", text: "A perverse heart will be far from me."}
              , {class: "q2", text: "I will have nothing to do with evil."}
              ]
        , 5:  [ {class: "q", text: "I will silence whoever secretly slanders his neighbor."}
              , {class: "q2", text: "I won’t tolerate one who is arrogant and conceited."}
              ]
        , 6:  [ {class: "q", text: "My eyes will be on the faithful of the land,"}
              , {class: "q2", text: "that they may dwell with me."}
              , {class: "q", text: "He who walks in a perfect way,"}
              , {class: "q2", text: "he will serve me."}
              ]
        , 7:  [ {class: "q", text: "He who practices deceit won’t dwell within my house."}
              , {class: "q2", text: "He who speaks falsehood won’t be established before my eyes."}
              ]
        , 8:  [ {class: "q", text: "Morning by morning, I will destroy all the wicked of the land,"}
              , {class: "q2", text: "to cut off all the workers of iniquity from Yahweh’s city."}
              ]
        }
  , 102:{ 1:  [ {class: "q", text: "Hear my prayer, Yahweh!"}
              , {class: "q2", text: "Let my cry come to you."}
              ]
        , 2:  [ {class: "q", text: "Don’t hide your face from me in the day of my distress."}
              , {class: "q2", text: "Turn your ear to me."}
              , {class: "q2", text: "Answer me quickly in the day when I call."}
              ]
        , 3:  [ {class: "q", text: "For my days consume away like smoke."}
              , {class: "q2", text: "My bones are burned as a torch."}
              ]
        , 4:  [ {class: "q", text: "My heart is blighted like grass, and withered,"}
              , {class: "q2", text: "for I forget to eat my bread."}
              ]
        , 5:  [ {class: "q", text: "By reason of the voice of my groaning,"}
              ,  {class: "q2", text: "my bones stick to my skin."}
              ]
        , 6:  [ {class: "q", text: "I am like a pelican of the wilderness."}
              , {class: "q2", text: "I have become as an owl of the waste places."}
              ]
        , 7:  [ {class: "q2", text: "/span> I watch, and have become like a sparrow that is alone on the housetop."} ]
        , 8:  [ {class: "q", text: "My enemies reproach me all day."}
              , {class: "q2", text: "Those who are mad at me use my name as a curse."}
              ]
        , 9:  [ {class: "q", text: "For I have eaten ashes like bread,"}
              , {class: "q2", text: "and mixed my drink with tears,"}
              ]
        , 10: [ {class: "q2", text: "</span> because of your indignation and your wrath;"}
              , {class: "q2", text: "for you have taken me up and thrown me away."}
              ]
        , 11: [ {class: "q", text: "My days are like a long shadow."}
              ,  {class: "q2", text: "I have withered like grass."}
              ]
        , 12: [ {class: "q", text: "But you, Yahweh, will remain forever;"}
              , {class: "q2", text: "your renown endures to all generations."}
              ]
        , 13: [ {class: "q", text: "You will arise and have mercy on Zion;"}
              , {class: "q2", text: "for it is time to have pity on her."}
              , {class: "q2", text: "Yes, the set time has come."}
               ]
        , 14: [ {class: "q", text: "For your servants take pleasure in her stones,"}
              , {class: "q2", text: "and have pity on her dust."}
              ]
        , 15: [ {class: "q", text: "So the nations will fear Yahweh’s name,"}
              , {class: "q2", text: "all the kings of the earth your glory."}
              ]
        , 16: [ {class: "q", text: "For Yahweh has built up Zion."}
              , {class: "q2", text: "He has appeared in his glory."}
              ]
        , 17: [ {class: "q", text: "He has responded to the prayer of the destitute,"}
              , {class: "q2", text: "and has not despised their prayer."}
              ]
        , 18: [ {class: "q", text: "This will be written for the generation to come."}
              , {class: "q2", text: "A people which will be created will praise Yah."}
              ]
        , 19: [ {class: "q", text: "For he has looked down from the height of his sanctuary."}
              , {class: "q2", text: "From heaven, Yahweh saw the earth;"}
              ]
        , 20: [ {class: "q", text: "to hear the groans of the prisoner;"}
              , {class: "q2", text: "to free those who are condemned to death;"}
              ]
        , 21: [ {class: "q", text: "that men may declare Yahweh’s name in Zion,"}
              , {class: "q2", text: "and his praise in Jerusalem;"}
              ]
        , 22: [ {class: "q", text: "when the peoples are gathered together,"}
              , {class: "q2", text: "the kingdoms, to serve Yahweh."}
              ]
        , 23: [ {class: "q", text: "He weakened my strength along the course."}
              , {class: "q2", text: "He shortened my days."}
              ]
        , 24: [ {class: "q", text: "I said, “My God, don’t take me away in the middle of my days."}
              , {class: "q2", text: "Your years are throughout all generations."}
              ]
        , 25: [ {class: "q", text: "Of old, you laid the foundation of the earth."}
              , {class: "q2", text: "The heavens are the work of your hands."}
              ]
        , 26: [ {class: "q", text: "They will perish, but you will endure."}
              , {class: "q2", text: "Yes, all of them will wear out like a garment."}
              , {class: "q2", text: "You will change them like a cloak, and they will be changed."}
              ]
        , 27: [ {class: "q", text: "But you are the same."}
              , {class: "q2", text: "Your years will have no end."}
              ]
        , 28: [ {class: "q", text: "The children of your servants will continue."}
              , {class: "q2", text: "Their offspring will be established before you.”"}
              ]
        }
  , 103:{ 1:  [ {class: "q", text: "Praise Yahweh, my soul!"}
              , {class: "q2", text: "All that is within me, praise his holy name!"}
              ]
        , 2:  [ {class: "q", text: "Praise Yahweh, my soul,"}
              , {class: "q2", text: "and don’t forget all his benefits,"}
              ]
        , 3:  [ {class: "q", text: "who forgives all your sins,"}
              , {class: "q2", text: "who heals all your diseases,"}
              ]
        , 4:  [ {class: "q", text: "who redeems your life from destruction,"}
              , {class: "q2", text: "who crowns you with loving kindness and tender mercies,"}
              ]
        , 5:  [ {class: "q", text: "who satisfies your desire with good things,"}
              , {class: "q2", text: "so that your youth is renewed like the eagle’s."}
              ]
        , 6:  [ {class: "q", text: "Yahweh executes righteous acts,"}
              , {class: "q2", text: "and justice for all who are oppressed."}
              ]
        , 7:  [ {class: "q", text: "He made known his ways to Moses,"}
              , {class: "q2", text: "his deeds to the children of Israel."}
              ]
        , 8:  [ {class: "q", text: "Yahweh is merciful and gracious,"}
              , {class: "q2", text: "slow to anger, and abundant in loving kindness."}
              ]
        , 9:  [ {class: "q", text: "He will not always accuse;"}
              , {class: "q2", text: "neither will he stay angry forever."}
              ]
        , 10: [ {class: "q", text: "He has not dealt with us according to our sins,"}
              , {class: "q2", text: "nor repaid us for our iniquities."}
              ]
        , 11: [ {class: "q", text: "For as the heavens are high above the earth,"}
              , {class: "q2", text: "so great is his loving kindness toward those who fear him."}
              ]
        , 12: [ {class: "q", text: "As far as the east is from the west,"}
              , {class: "q2", text: "so far has he removed our transgressions from us."}
              ]
        , 13: [ {class: "q", text: "Like a father has compassion on his children,"}
              , {class: "q2", text: "so Yahweh has compassion on those who fear him."}
              ]
        , 14: [ {class: "q", text: "For he knows how we are made."}
              , {class: "q2", text: "He remembers that we are dust."}
              ]
        , 15: [ {class: "q", text: "As for man, his days are like grass."}
              , {class: "q2", text: "As a flower of the field, so he flourishes."}
              ]
        , 16: [ {class: "q", text: "For the wind passes over it, and it is gone."}
              , {class: "q2", text: "Its place remembers it no more."}
              ]
        , 17: [ {class: "q", text: "But Yahweh’s loving kindness is from everlasting to everlasting with those who fear him,"}
              , {class: "q2", text: "his righteousness to children’s children,"}
              ]
        , 18: [ {class: "q", text: "to those who keep his covenant,"}
              , {class: "q2", text: "to those who remember to obey his precepts."}
              ]
        , 19: [ {class: "q", text: "Yahweh has established his throne in the heavens."}
              , {class: "q2", text: "His kingdom rules over all."}
              ]
        , 20: [ {class: "q", text: "Praise Yahweh, you angels of his,"}
              , {class: "q2", text: "who are mighty in strength, who fulfill his word,"}
              , {class: "q2", text: "obeying the voice of his word."}
              ]
        , 21: [ {class: "q", text: "Praise Yahweh, all you armies of his,"}
              , {class: "q2", text: "you servants of his, who do his pleasure."}
              ]
        , 22: [ {class: "q", text: "Praise Yahweh, all you works of his,"}
              , {class: "q2", text: "in all places of his dominion."}
              , {class: "q2", text: "Praise Yahweh, my soul!"}
              ]
        }
  , 104:{ 1:  [ {class: "q", text: "Bless Yahweh, my soul."}
              , {class: "q2", text: "Yahweh, my God, you are very great."}
              , {class: "q2", text: "You are clothed with honor and majesty."}
              ]
        , 2:  [ {class: "q", text: "He covers himself with light as with a garment."}
              , {class: "q2", text: "He stretches out the heavens like a curtain."}
              ]
        , 3:  [ {class: "q", text: "He lays the beams of his rooms in the waters."}
              , {class: "q2", text: "He makes the clouds his chariot."}
              , {class: "q2", text: "He walks on the wings of the wind."}
              ]
        , 4:  [ {class: "q", text: "He makes his messengers winds,"}
              , {class: "q2", text: "and his servants flames of fire."}
              ]
        , 5:  [ {class: "q", text: "He laid the foundations of the earth,"}
              , {class: "q2", text: "that it should not be moved forever."}
              ]
        , 6:  [ {class: "q", text: "You covered it with the deep as with a cloak."}
              , {class: "q2", text: "The waters stood above the mountains."}
              ]
        , 7:  [ {class: "q", text: "At your rebuke they fled."}
              , {class: "q2", text: "At the voice of your thunder they hurried away."}
              ]
        , 8:  [ {class: "q", text: "The mountains rose,"}
              , {class: "q2", text: "the valleys sank down,"}
              , {class: "q2", text: "to the place which you had assigned to them."}
              ]
        , 9:  [ {class: "q", text: "You have set a boundary that they may not pass over,"}
              , {class: "q2", text: "that they don’t turn again to cover the earth."}
              ]
        , 10: [ {class: "q", text: "He sends springs into the valleys."}
              , {class: "q2", text: "They run among the mountains."}
              ]
        , 11: [ {class: "q", text: "They give drink to every animal of the field."}
              , {class: "q2", text: "The wild donkeys quench their thirst."}
              ]
        , 12: [ {class: "q", text: "The birds of the sky nest by them."}
              , {class: "q2", text: "They sing among the branches."}
              ]
        , 13: [ {class: "q", text: "He waters the mountains from his rooms."}
              , {class: "q2", text: "The earth is filled with the fruit of your works."}
              ]
        , 14: [ {class: "q", text: "He causes the grass to grow for the livestock,"}
              , {class: "q2", text: "and plants for man to cultivate,"}
              , {class: "q2", text: "that he may produce food out of the earth:"}
              ]
        , 15: [ {class: "q", text: "wine that makes the heart of man glad,"}
              , {class: "q2", text: "oil to make his face to shine,"}
              , {class: "q2", text: "and bread that strengthens man’s heart."}
              ]
        , 16: [ {class: "q", text: "Yahweh’s trees are well watered,"}
              , {class: "q2", text: "the cedars of Lebanon, which he has planted;"}
              ]
        , 17: [ {class: "q", text: "where the birds make their nests."}
              , {class: "q2", text: "The stork makes its home in the cypress trees."}
              ]
        , 18: [ {class: "q", text: "The high mountains are for the wild goats."}
              , {class: "q2", text: "The rocks are a refuge for the rock badgers."}
              ]
        , 19: [ {class: "q", text: "He appointed the moon for seasons."}
              , {class: "q2", text: "The sun knows when to set."}
              ]
        , 20: [ {class: "q", text: "You make darkness, and it is night,"}
              , {class: "q2", text: "in which all the animals of the forest prowl."}
              ]
        , 21: [ {class: "q", text: "The young lions roar after their prey,"}
              , {class: "q2", text: "and seek their food from God."}
              ]
        , 22: [ {class: "q", text: "The sun rises, and they steal away,"}
              , {class: "q2", text: "and lie down in their dens."}
              ]
        , 23: [ {class: "q", text: "Man goes out to his work,"}
              , {class: "q2", text: "to his labor until the evening."}
              ]
        , 24: [ {class: "q", text: "Yahweh, how many are your works!"}
              , {class: "q2", text: "In wisdom, you have made them all."}
              , {class: "q2", text: "The earth is full of your riches."}
              ]
        , 25: [ {class: "q", text: "There is the sea, great and wide,"}
              , {class: "q2", text: "in which are innumerable living things,"}
              , {class: "q2", text: "both small and large animals."}
              ]
        , 26: [ {class: "q", text: "There the ships go,"}
              , {class: "q2", text: "and leviathan, whom you formed to play there."}
              ]
        , 27: [ {class: "q", text: "These all wait for you,"}
              , {class: "q2", text: "that you may give them their food in due season."}
              ]
        , 28: [ {class: "q", text: "You give to them; they gather."}
              , {class: "q2", text: "You open your hand; they are satisfied with good."}
              ]
        , 29: [ {class: "q", text: "You hide your face; they are troubled."}
              , {class: "q2", text: "You take away their breath; they die and return to the dust."}
              ]
        , 30: [ {class: "q", text: "You send out your Spirit and they are created."}
              , {class: "q2", text: "You renew the face of the ground."}
              ]
        , 31: [ {class: "q", text: "Let Yahweh’s glory endure forever."}
              , {class: "q2", text: "Let Yahweh rejoice in his works."}
              ]
        , 32: [ {class: "q", text: "He looks at the earth, and it trembles."}
              , {class: "q2", text: "He touches the mountains, and they smoke."}
              ]
        , 33: [ {class: "q", text: "I will sing to Yahweh as long as I live."}
              , {class: "q2", text: "I will sing praise to my God while I have any being."}
              ]
        , 34: [ {class: "q", text: "Let my meditation be sweet to him."}
              , {class: "q2", text: "I will rejoice in Yahweh."}
              ]
        , 35: [ {class: "q", text: "Let sinners be consumed out of the earth."}
              , {class: "q2", text: "Let the wicked be no more."}
              , {class: "q2", text: "Bless Yahweh, my soul."}
              , {class: "q2", text: "Praise Yah!"}
              ]
        }
  , 105:{ 1:  [ {class: "q", text: "Give thanks to Yahweh! Call on his name!"}
              , {class: "q2", text: "Make his doings known among the peoples."}
              ]
        , 2:  [ {class: "q", text: "Sing to him, sing praises to him!"}
              , {class: "q2", text: "Tell of all his marvelous works."}
              ]
        , 3:  [ {class: "q", text: "Glory in his holy name."}
              , {class: "q2", text: "Let the heart of those who seek Yahweh rejoice."}
              ]
        , 4:  [ {class: "q", text: "Seek Yahweh and his strength."}
              , {class: "q2", text: "Seek his face forever more."}
              ]
        , 5:  [ {class: "q", text: "Remember his marvelous works that he has done:"}
              , {class: "q2", text: "his wonders, and the judgments of his mouth,"}
              ]
        , 6:  [ {class: "q", text: "you offspring of Abraham, his servant,"}
              , {class: "q2", text: "you children of Jacob, his chosen ones."}
              ]
        , 7:  [ {class: "q", text: "He is Yahweh, our God."}
              , {class: "q2", text: "His judgments are in all the earth."}
              ]
        , 8:  [ {class: "q", text: "He has remembered his covenant forever,"}
              , {class: "q2", text: "the word which he commanded to a thousand generations,"}
              ]
        , 9:  [ {class: "q", text: "the covenant which he made with Abraham,"}
              , {class: "q2", text: "his oath to Isaac,"}
              ]
        , 10: [ {class: "q", text: "and confirmed it to Jacob for a statute;"}
              , {class: "q2", text: "to Israel for an everlasting covenant,"}
              ]
        , 11: [ {class: "q", text: "saying, “To you I will give the land of Canaan,"}
              , {class: "q2", text: "the lot of your inheritance,”"}
              ]
        , 12: [ {class: "q", text: "when they were but a few men in number,"}
              , {class: "q2", text: "yes, very few, and foreigners in it."}
              ]
        , 13: [ {class: "q", text: "They went about from nation to nation,"}
              , {class: "q2", text: "from one kingdom to another people."}
              ]
        , 14: [ {class: "q", text: "He allowed no one to do them wrong."}
              , {class: "q2", text: "Yes, he reproved kings for their sakes,"}
              ]
        , 15: [ {class: "q", text: "“Don’t touch my anointed ones!"}
              , {class: "q2", text: "Do my prophets no harm!”"}
              ]
        , 16: [ {class: "q", text: "He called for a famine on the land."}
              , {class: "q2", text: "He destroyed the food supplies."}
              ]
        , 17: [ {class: "q", text: "He sent a man before them."}
              , {class: "q2", text: "Joseph was sold for a slave."}
              ]
        , 18: [ {class: "q", text: "They bruised his feet with shackles."}
              , {class: "q2", text: "His neck was locked in irons,"}
              ]
        , 19: [ {class: "q", text: "until the time that his word happened,"}
              , {class: "q2", text: "and Yahweh’s word proved him true."}
              ]
        , 20: [ {class: "q", text: "The king sent and freed him,"}
              , {class: "q2", text: "even the ruler of peoples, and let him go free."}
              ]
        , 21: [ {class: "q", text: "He made him lord of his house,"}
              , {class: "q2", text: "and ruler of all of his possessions,"}
              ]
        , 22: [ {class: "q", text: "to discipline his princes at his pleasure,"}
              , {class: "q2", text: "and to teach his elders wisdom."}
              ]
        , 23: [ {class: "q", text: "Israel also came into Egypt."}
              , {class: "q2", text: "Jacob lived in the land of Ham."}
              ]
        , 24: [ {class: "q", text: "He increased his people greatly,"}
              , {class: "q2", text: "and made them stronger than their adversaries."}
              ]
        , 25: [ {class: "q", text: "He turned their heart to hate his people,"}
              , {class: "q2", text: "to conspire against his servants."}
              ]
        , 26: [ {class: "q", text: "He sent Moses, his servant,"}
              , {class: "q2", text: "and Aaron, whom he had chosen."}
              ]
        , 27: [ {class: "q", text: "They performed miracles among them,"}
              , {class: "q2", text: "and wonders in the land of Ham."}
              ]
        , 28: [ {class: "q", text: "He sent darkness, and made it dark."}
              , {class: "q2", text: "They didn’t rebel against his words."}
              ]
        , 29: [ {class: "q", text: "He turned their waters into blood,"}
              , {class: "q2", text: "and killed their fish."}
              ]
        , 30: [ {class: "q", text: "Their land swarmed with frogs,"}
              , {class: "q2", text: "even in the rooms of their kings."}
              ]
        , 31: [ {class: "q", text: "He spoke, and swarms of flies came,"}
              , {class: "q2", text: "and lice in all their borders."}
              ]
        , 32: [ {class: "q", text: "He gave them hail for rain,"}
              , {class: "q2", text: "with lightning in their land."}
              ]
        , 33: [ {class: "q", text: "He struck their vines and also their fig trees,"}
              , {class: "q2", text: "and shattered the trees of their country."}
              ]
        , 34: [ {class: "q", text: "He spoke, and the locusts came"}
              , {class: "q2", text: "with the grasshoppers, without number,"}
              ]
        , 35: [ {class: "q", text: "ate up every plant in their land,"}
              , {class: "q2", text: "and ate up the fruit of their ground."}
              ]
        , 36: [ {class: "q", text: "He struck also all the firstborn in their land,"}
              , {class: "q2", text: "the first fruits of all their manhood."}
              ]
        , 37: [ {class: "q", text: "He brought them out with silver and gold."}
              , {class: "q2", text: "There was not one feeble person among his tribes."}
              ]
        , 38: [ {class: "q", text: "Egypt was glad when they departed,"}
              , {class: "q2", text: "for the fear of them had fallen on them."}
              ]
        , 39: [ {class: "q", text: "He spread a cloud for a covering,"}
              ,  {class: "q2", text: "fire to give light in the night."}
              ]
        , 40: [ {class: "q", text: "They asked, and he brought quails,"}
              , {class: "q2", text: "and satisfied them with the bread of the sky."}
              ]
        , 41: [ {class: "q", text: "He opened the rock, and waters gushed out."}
              , {class: "q2", text: "They ran as a river in the dry places."}
              ]
        , 42: [ {class: "q", text: "For he remembered his holy word,"}
              , {class: "q2", text: "and Abraham, his servant."}
              ]
        , 43: [ {class: "q", text: "He brought his people out with joy,"}
              , {class: "q2", text: "his chosen with singing."}
              ]
        , 44: [ {class: "q", text: "He gave them the lands of the nations."}
              , {class: "q2", text: "They took the labor of the peoples in possession,"}
              ]
        , 45: [ {class: "q", text: "that they might keep his statutes,"}
              , {class: "q2", text: "and observe his laws."}
              , {class: "q2", text: "Praise Yah!"}
              ]
        }
  , 106:{ 1:  [ {class: "q", text: "Praise Yahweh!"}
              , {class: "q2", text: "Give thanks to Yahweh, for he is good,"}
              , {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 2:  [ {class: "q", text: "Who can utter the mighty acts of Yahweh,"}
              ,  {class: "q2", text: "or fully declare all his praise?"}
              ]
        , 3:  [ {class: "q", text: "Blessed are those who keep justice."}
              , {class: "q2", text: "Blessed is one who does what is right at all times."}
              ]
        , 4:  [ {class: "q", text: "Remember me, Yahweh, with the favor that you show to your people."}
              , {class: "q2", text: "Visit me with your salvation,"}
              ]
        , 5:  [ {class: "q", text: "that I may see the prosperity of your chosen,"}
              , {class: "q2", text: "that I may rejoice in the gladness of your nation,"}
              , {class: "q2", text: "that I may glory with your inheritance."}
              ]
        , 6:  [ {class: "q", text: "We have sinned with our fathers."}
              , {class: "q2", text: "We have committed iniquity."}
              , {class: "q2", text: "We have done wickedly."}
              ]
        , 7:  [ {class: "q", text: "Our fathers didn’t understand your wonders in Egypt."}
              , {class: "q2", text: "They didn’t remember the multitude of your loving kindnesses,"}
              , {class: "q2", text: "but were rebellious at the sea, even at the Red Sea."}
              ]
        , 8:  [ {class: "q", text: "Nevertheless he saved them for his name’s sake,"}
              , {class: "q2", text: "that he might make his mighty power known."}
              ]
        , 9:  [ {class: "q", text: "He rebuked the Red Sea also, and it was dried up;"}
              , {class: "q2", text: "so he led them through the depths, as through a desert."}
              ]
        , 10: [ {class: "q", text: "He saved them from the hand of him who hated them,"}
              , {class: "q2", text: "and redeemed them from the hand of the enemy."}
              ]
        , 11: [ {class: "q", text: "The waters covered their adversaries."}
              , {class: "q2", text: "There was not one of them left."}
              ]
        , 12: [ {class: "q", text: "Then they believed his words."}
              , {class: "q2", text: "They sang his praise."}
              ]
        , 13: [ {class: "q", text: "They soon forgot his works."}
              , {class: "q2", text: "They didn’t wait for his counsel,"}
              ]
        , 14: [ {class: "q2", text: "</span> but gave in to craving in the desert,"}
              , {class: "q2", text: "and tested God in the wasteland."}
              ]
        , 15: [ {class: "q", text: "He gave them their request,"}
              , {class: "q2", text: "but sent leanness into their soul."}
              ]
        , 16: [ {class: "q", text: "They envied Moses also in the camp,"}
              , {class: "q2", text: "and Aaron, Yahweh’s saint."}
              ]
        , 17: [ {class: "q", text: "The earth opened and swallowed up Dathan,"}
              , {class: "q2", text: "and covered the company of Abiram."}
              ]
        , 18: [ {class: "q", text: "A fire was kindled in their company."}
              , {class: "q2", text: "The flame burned up the wicked."}
              ]
        , 19: [ {class: "q", text: "They made a calf in Horeb,"}
              , {class: "q2", text: "and worshiped a molten image."}
              ]
        , 20: [ {class: "q", text: "Thus they exchanged their glory"}
              , {class: "q2", text: "for an image of a bull that eats grass."}
              ]
        , 21: [ {class: "q", text: "They forgot God, their Savior,"}
              ,  {class: "q2", text: "who had done great things in Egypt,"}
              ]
        , 22: [ {class: "q2", text: "</span> wondrous works in the land of Ham,"}
              , {class: "q2", text: "and awesome things by the Red Sea."}
              ]
        , 23: [ {class: "q", text: "Therefore he said that he would destroy them,"}
              , {class: "q2", text: "had Moses, his chosen, not stood before him in the breach,"}
              , {class: "q2", text: "to turn away his wrath, so that he wouldn’t destroy them."}
              ]
        , 24: [ {class: "q", text: "Yes, they despised the pleasant land."}
              , {class: "q2", text: "They didn’t believe his word,"}
              ]
        , 25: [ {class: "q2", text: "</span> but murmured in their tents,"}
              , {class: "q2", text: "and didn’t listen to Yahweh’s voice."}
              ]
        , 26: [ {class: "q", text: "Therefore he swore to them"}
              , {class: "q2", text: "that he would overthrow them in the wilderness,"}
              ]
        , 27: [ {class: "q2", text: "</span> that he would overthrow their offspring among the nations,"}
              , {class: "q2", text: "and scatter them in the lands."}
              ]
        , 28: [ {class: "q", text: "They joined themselves also to Baal Peor,"}
              , {class: "q2", text: "and ate the sacrifices of the dead."}
              ]
        , 29: [ {class: "q", text: "Thus they provoked him to anger with their deeds."}
              , {class: "q2", text: "The plague broke in on them."}
              ]
        , 30: [ {class: "q", text: "Then Phinehas stood up and executed judgment,"}
              , {class: "q2", text: "so the plague was stopped."}
              ]
        , 31: [ {class: "q", text: "That was credited to him for righteousness,"}
              , {class: "q2", text: "for all generations to come."}
              ]
        , 32: [ {class: "q", text: "They angered him also at the waters of Meribah,"}
              ,  {class: "q2", text: "so that Moses was troubled for their sakes;"}
              ]
        , 33: [ {class: "q", text: "because they were rebellious against his spirit,"}
              , {class: "q2", text: "he spoke rashly with his lips."}
              ]
        , 34: [ {class: "q", text: "They didn’t destroy the peoples,"}
              , {class: "q2", text: "as Yahweh commanded them,"}
              ]
        , 35: [ {class: "q2", text: "</span> but mixed themselves with the nations,"}
              , {class: "q2", text: "and learned their works."}
              ]
        , 36: [ {class: "q", text: "They served their idols,"}
              , {class: "q2", text: "which became a snare to them."}
              ]
        , 37: [ {class: "q", text: "Yes, they sacrificed their sons and their daughters to demons."} ]
        , 38: [ {class: "q2", text: "</span> They shed innocent blood,"}
              , {class: "q2", text: "even the blood of their sons and of their daughters,"}
              , {class: "q2", text: "whom they sacrificed to the idols of Canaan."}
              , {class: "q2", text: "The land was polluted with blood."}
              ]
        , 39: [ {class: "q", text: "Thus they were defiled with their works,"}
              , {class: "q2", text: "and prostituted themselves in their deeds."}
              ]
        , 40: [ {class: "q", text: "Therefore Yahweh burned with anger against his people."}
              , {class: "q2", text: "He abhorred his inheritance."}
              ]
        , 41: [ {class: "q", text: "He gave them into the hand of the nations."}
              , {class: "q2", text: "Those who hated them ruled over them."}
              ]
        , 42: [ {class: "q", text: "Their enemies also oppressed them."}
              , {class: "q2", text: "They were brought into subjection under their hand."}
              ]
        , 43: [ {class: "q", text: "He rescued them many times,"}
              , {class: "q2", text: "but they were rebellious in their counsel,"}
              , {class: "q2", text: "and were brought low in their iniquity."}
              ]
        , 44: [ {class: "q", text: "Nevertheless he regarded their distress,"}
              , {class: "q2", text: "when he heard their cry."}
              ]
        , 45: [ {class: "q", text: "He remembered for them his covenant,"}
              , {class: "q2", text: "and repented according to the multitude of his loving kindnesses."}
              ]
        , 46: [ {class: "q", text: "He made them also to be pitied"}
              , {class: "q2", text: "by all those who carried them captive."}
              ]
        , 47: [ {class: "q", text: "Save us, Yahweh, our God,"}
              , {class: "q2", text: "gather us from among the nations,"}
              , {class: "q2", text: "to give thanks to your holy name,"}
              , {class: "q2", text: "to triumph in your praise!"}
              ]
        , 48: [ {class: "q", text: "Blessed be Yahweh, the God of Israel,"}
              , {class: "q2", text: "from everlasting even to everlasting!"}
              , {class: "q", text: "Let all the people say, “Amen.”"}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 107:{ 1:  [ {class: "q", text: "Give thanks to Yahweh, for he is good,"}
              , {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 2:  [ {class: "q", text: "Let the redeemed by Yahweh say so,"}
              ,  {class: "q2", text: "whom he has redeemed from the hand of the adversary,"}
              ]
        , 3:  [ {class: "q2", text: "/span> And gathered out of the lands,"}
              , {class: "q2", text: "from the east and from the west,"}
              , {class: "q2", text: "from the north and from the south."}
              ]
        , 4:  [ {class: "q", text: "They wandered in the wilderness in a desert way."}
              , {class: "q2", text: "They found no city to live in."}
              ]
        , 5:  [ {class: "q", text: "Hungry and thirsty,"}
              , {class: "q2", text: "their soul fainted in them."}
              ]
        , 6:  [ {class: "q", text: "Then they cried to Yahweh in their trouble,"}
              , {class: "q2", text: "and he delivered them out of their distresses,"}
              ]
        , 7:  [ {class: "q", text: "he led them also by a straight way,"}
              , {class: "q2", text: "that they might go to a city to live in."}
              ]
        , 8:  [ {class: "q", text: "Let them praise Yahweh for his loving kindness,"}
              , {class: "q2", text: "for his wonderful deeds to the children of men!"}
              ]
        , 9:  [ {class: "q", text: "For he satisfies the longing soul."}
              ,  {class: "q2", text: "He fills the hungry soul with good."}
              ]
        , 10: [ {class: "q", text: "Some sat in darkness and in the shadow of death,"}
              , {class: "q2", text: "being bound in affliction and iron,"}
              ]
        , 11: [ {class: "q2", text: "</span> because they rebelled against the words of God,"}
              , {class: "q2", text: "and condemned the counsel of the Most High."}
              ]
        , 12: [ {class: "q", text: "Therefore he brought down their heart with labor."}
              , {class: "q2", text: "They fell down, and there was no one to help."}
              ]
        , 13: [ {class: "q", text: "Then they cried to Yahweh in their trouble,"}
              , {class: "q2", text: "and he saved them out of their distresses."}
              ]
        , 14: [ {class: "q", text: "He brought them out of darkness and the shadow of death,"}
              , {class: "q2", text: "and broke away their chains."}
              ]
        , 15: [ {class: "q", text: "Let them praise Yahweh for his loving kindness,"}
              , {class: "q2", text: "for his wonderful deeds to the children of men!"}
              ]
        , 16: [ {class: "q", text: "For he has broken the gates of bronze,"}
              ,  {class: "q2", text: "and cut through bars of iron."}
              ]
        , 17: [ {class: "q", text: "Fools are afflicted because of their disobedience,"}
              , {class: "q2", text: "and because of their iniquities."}
              ]
        , 18: [ {class: "q", text: "Their soul abhors all kinds of food."}
              , {class: "q2", text: "They draw near to the gates of death."}
              ]
        , 19: [ {class: "q", text: "Then they cry to Yahweh in their trouble,"}
              , {class: "q2", text: "he saves them out of their distresses."}
              ]
        , 20: [ {class: "q", text: "He sends his word, and heals them,"}
              , {class: "q2", text: "and delivers them from their graves."}
              ]
        , 21: [ {class: "q", text: "Let them praise Yahweh for his loving kindness,"}
              , {class: "q2", text: "for his wonderful deeds to the children of men!"}
              ]
        , 22: [ {class: "q", text: "Let them offer the sacrifices of thanksgiving,"}
              ,  {class: "q2", text: "and declare his deeds with singing."}
              ]
        , 23: [ {class: "q", text: "Those who go down to the sea in ships,"}
              , {class: "q2", text: "who do business in great waters;"}
              ]
        , 24: [ {class: "q2", text: "</span> These see Yahweh’s deeds,"}
              , {class: "q2", text: "and his wonders in the deep."}
              ]
        , 25: [ {class: "q", text: "For he commands, and raises the stormy wind,"}
              , {class: "q2", text: "which lifts up its waves."}
              ]
        , 26: [ {class: "q", text: "They mount up to the sky; they go down again to the depths."}
              , {class: "q2", text: "Their soul melts away because of trouble."}
              ]
        , 27: [ {class: "q", text: "They reel back and forth, and stagger like a drunken man,"}
              , {class: "q2", text: "and are at their wits’ end."}
              ]
        , 28: [ {class: "q", text: "Then they cry to Yahweh in their trouble,"}
              , {class: "q2", text: "and he brings them out of their distress."}
              ]
        , 29: [ {class: "q", text: "He makes the storm a calm,"}
              , {class: "q2", text: "so that its waves are still."}
              ]
        , 30: [ {class: "q", text: "Then they are glad because it is calm,"}
              , {class: "q2", text: "so he brings them to their desired haven."}
              ]
        , 31: [ {class: "q", text: "Let them praise Yahweh for his loving kindness,"}
              , {class: "q2", text: "for his wonderful deeds for the children of men!"}
              ]
        , 32: [ {class: "q", text: "Let them exalt him also in the assembly of the people,"}
              ,  {class: "q2", text: "and praise him in the seat of the elders."}
              ]
        , 33: [ {class: "q", text: "He turns rivers into a desert,"}
              , {class: "q2", text: "water springs into a thirsty ground,"}
              ]
        , 34: [ {class: "q2", text: "</span> and a fruitful land into a salt waste,"}
              , {class: "q2", text: "for the wickedness of those who dwell in it."}
              ]
        , 35: [ {class: "q", text: "He turns a desert into a pool of water,"}
              ,  {class: "q2", text: "and a dry land into water springs."}
              ]
        , 36: [ {class: "q", text: "There he makes the hungry live,"}
              , {class: "q2", text: "that they may prepare a city to live in,"}
              ]
        , 37: [ {class: "q2", text: "</span> sow fields, plant vineyards,"}
              , {class: "q2", text: "and reap the fruits of increase."}
              ]
        , 38: [ {class: "q", text: "He blesses them also, so that they are multiplied greatly."}
              , {class: "q2", text: "He doesn’t allow their livestock to decrease."}
              ]
        , 39: [ {class: "q", text: "Again, they are diminished and bowed down"}
              , {class: "q2", text: "through oppression, trouble, and sorrow."}
              ]
        , 40: [ {class: "q", text: "He pours contempt on princes,"}
              , {class: "q2", text: "and causes them to wander in a trackless waste."}
              ]
        , 41: [ {class: "q", text: "Yet he lifts the needy out of their affliction,"}
              , {class: "q2", text: "and increases their families like a flock."}
              ]
        , 42: [ {class: "q", text: "The upright will see it, and be glad."}
              , {class: "q2", text: "All the wicked will shut their mouths."}
              ]
        , 43: [ {class: "q", text: "Whoever is wise will pay attention to these things."}
              , {class: "q2", text: "They will consider the loving kindnesses of Yahweh."}
              ]
        }
  , 108:{ 1:  [ {class: "q", text: "My heart is steadfast, God."}
              , {class: "q2", text: "I will sing and I will make music with my soul."}
              ]
        , 2:  [ {class: "q", text: "Wake up, harp and lyre!"}
              , {class: "q2", text: "I will wake up the dawn."}
              ]
        , 3:  [ {class: "q", text: "I will give thanks to you, Yahweh, among the nations."}
              , {class: "q2", text: "I will sing praises to you among the peoples."}
              ]
        , 4:  [ {class: "q", text: "For your loving kindness is great above the heavens."}
              , {class: "q2", text: "Your faithfulness reaches to the skies."}
              ]
        , 5:  [ {class: "q", text: "Be exalted, God, above the heavens!"}
              , {class: "q2", text: "Let your glory be over all the earth."}
              ]
        , 6:  [ {class: "q", text: "That your beloved may be delivered,"}
              , {class: "q2", text: "save with your right hand, and answer us."}
              ]
        , 7:  [ {class: "q", text: "God has spoken from his sanctuary: “In triumph,"}
              , {class: "q2", text: "I will divide Shechem, and measure out the valley of Succoth."}
              ]
        , 8:  [ {class: "q", text: "Gilead is mine. Manasseh is mine."}
              , {class: "q2", text: "Ephraim also is my helmet."}
              , {class: "q2", text: "Judah is my scepter."}
              ]
        , 9:  [ {class: "q", text: "Moab is my wash pot."}
              , {class: "q2", text: "I will toss my sandal on Edom."}
              , {class: "q2", text: "I will shout over Philistia.”"}
              ]
        , 10: [ {class: "q", text: "Who will bring me into the fortified city?"}
              , {class: "q2", text: "Who has led me to Edom?"}
              ]
        , 11: [ {class: "q", text: "Haven’t you rejected us, God?"}
              , {class: "q2", text: "You don’t go out, God, with our armies."}
              ]
        , 12: [ {class: "q", text: "Give us help against the enemy,"}
              , {class: "q2", text: "for the help of man is vain."}
              ]
        , 13: [ {class: "q", text: "Through God, we will do valiantly."}
              ,  {class: "q2", text: "For it is he who will tread down our enemies."}
              ]
        }
  , 109:{ 1:  [ {class: "q", text: "God of my praise, don’t remain silent,"} ]
        , 2:  [ {class: "q2", text: "/span> for they have opened the mouth of the wicked and the mouth of deceit against me."}
              , {class: "q2", text: "They have spoken to me with a lying tongue."}
              ]
        , 3:  [ {class: "q", text: "They have also surrounded me with words of hatred,"}
              , {class: "q2", text: "and fought against me without a cause."}
              ]
        , 4:  [ {class: "q", text: "In return for my love, they are my adversaries;"}
              , {class: "q2", text: "but I am in prayer."}
              ]
        , 5:  [ {class: "q", text: "They have rewarded me evil for good,"}
              , {class: "q2", text: "and hatred for my love."}
              ]
        , 6:  [ {class: "q", text: "Set a wicked man over him."}
              , {class: "q2", text: "Let an adversary stand at his right hand."}
              ]
        , 7:  [ {class: "q", text: "When he is judged, let him come out guilty."}
              , {class: "q2", text: "Let his prayer be turned into sin."}
              ]
        , 8:  [ {class: "q", text: "Let his days be few."}
              , {class: "q2", text: "Let another take his office."}
              ]
        , 9:  [ {class: "q", text: "Let his children be fatherless,"}
              , {class: "q2", text: "and his wife a widow."}
              ]
        , 10: [ {class: "q", text: "Let his children be wandering beggars."}
              , {class: "q2", text: "Let them be sought from their ruins."}
              ]
        , 11: [ {class: "q", text: "Let the creditor seize all that he has."}
              , {class: "q2", text: "Let strangers plunder the fruit of his labor."}
              ]
        , 12: [ {class: "q", text: "Let there be no one to extend kindness to him,"}
              , {class: "q2", text: "neither let there be anyone to have pity on his fatherless children."}
              ]
        , 13: [ {class: "q", text: "Let his posterity be cut off."}
              , {class: "q2", text: "In the generation following let their name be blotted out."}
              ]
        , 14: [ {class: "q", text: "Let the iniquity of his fathers be remembered by Yahweh."}
              , {class: "q2", text: "Don’t let the sin of his mother be blotted out."}
              ]
        , 15: [ {class: "q", text: "Let them be before Yahweh continually,"}
              , {class: "q2", text: "that he may cut off their memory from the earth;"}
              ]
        , 16: [ {class: "q", text: "because he didn’t remember to show kindness,"}
              , {class: "q2", text: "but persecuted the poor and needy man,"}
              , {class: "q2", text: "the broken in heart, to kill them."}
              ]
        , 17: [ {class: "q", text: "Yes, he loved cursing, and it came to him."}
              , {class: "q2", text: "He didn’t delight in blessing, and it was far from him."}
              ]
        , 18: [ {class: "q", text: "He clothed himself also with cursing as with his garment."}
              , {class: "q2", text: "It came into his inward parts like water,"}
              , {class: "q2", text: "like oil into his bones."}
              ]
        , 19: [ {class: "q", text: "Let it be to him as the clothing with which he covers himself,"}
              , {class: "q2", text: "for the belt that is always around him."}
              ]
        , 20: [ {class: "q", text: "This is the reward of my adversaries from Yahweh,"}
              ,  {class: "q2", text: "of those who speak evil against my soul."}
              ]
        , 21: [ {class: "q", text: "But deal with me, Yahweh the Lord, for your name’s sake,"}
              , {class: "q2", text: "because your loving kindness is good, deliver me;"}
              ]
        , 22: [ {class: "q2", text: "</span> for I am poor and needy."}
              , {class: "q2", text: "My heart is wounded within me."}
              ]
        , 23: [ {class: "q", text: "I fade away like an evening shadow."}
              , {class: "q2", text: "I am shaken off like a locust."}
              ]
        , 24: [ {class: "q", text: "My knees are weak through fasting."}
              , {class: "q2", text: "My body is thin and lacks fat."}
              ]
        , 25: [ {class: "q", text: "I have also become a reproach to them."}
              , {class: "q2", text: "When they see me, they shake their head."}
              ]
        , 26: [ {class: "q", text: "Help me, Yahweh, my God."}
              , {class: "q2", text: "Save me according to your loving kindness;"}
              ]
        , 27: [ {class: "q", text: "that they may know that this is your hand;"}
              , {class: "q2", text: "that you, Yahweh, have done it."}
              ]
        , 28: [ {class: "q", text: "They may curse, but you bless."}
              , {class: "q2", text: "When they arise, they will be shamed,"}
              , {class: "q2", text: "but your servant shall rejoice."}
              ]
        , 29: [ {class: "q", text: "Let my adversaries be clothed with dishonor."}
              , {class: "q2", text: "Let them cover themselves with their own shame as with a robe."}
              ]
        , 30: [ {class: "q", text: "I will give great thanks to Yahweh with my mouth."}
              , {class: "q2", text: "Yes, I will praise him among the multitude."}
              ]
        , 31: [ {class: "q", text: "For he will stand at the right hand of the needy,"}
              , {class: "q2", text: "to save him from those who judge his soul."}
              ]
        }
  , 11: { 1:  [ {class: "q", text: "In Yahweh, I take refuge."}
              , {class: "q2", text: "How can you say to my soul, “Flee as a bird to your mountain”?"}
              ]
        , 2:  [ {class: "q", text: "For, behold, the wicked bend their bows."}
              , {class: "q2", text: "They set their arrows on the strings,"}
              , {class: "q2", text: "that they may shoot in darkness at the upright in heart."}
              ]
        , 3:  [ {class: "q", text: "If the foundations are destroyed,"}
              , {class: "q2", text: "what can the righteous do?"}
              ]
        , 4:  [ {class: "q", text: "Yahweh is in his holy temple."}
              , {class: "q2", text: "Yahweh is on his throne in heaven."}
              , {class: "q", text: "His eyes observe."}
              , {class: "q2", text: "His eyes examine the children of men."}
              ]
        , 5:  [ {class: "q", text: "Yahweh examines the righteous,"}
              , {class: "q2", text: "but his soul hates the wicked and him who loves violence."}
              ]
        , 6:  [ {class: "q", text: "On the wicked he will rain blazing coals;"}
              , {class: "q2", text: "fire, sulfur, and scorching wind shall be the portion of their cup."}
              ]
        , 7:  [ {class: "q", text: "For Yahweh is righteous."}
              , {class: "q2", text: "He loves righteousness."}
              , {class: "q2", text: "The upright shall see his face."}
              ]
        }
  , 110:{ 1:  [ {class: "q", text: "Yahweh says to my Lord, “Sit at my right hand,"}
              , {class: "q2", text: "until I make your enemies your footstool for your feet.”"}
              ]
        , 2:  [ {class: "q", text: "Yahweh will send out the rod of your strength out of Zion."}
              , {class: "q2", text: "Rule among your enemies."}
              ]
        , 3:  [ {class: "q", text: "Your people offer themselves willingly in the day of your power, in holy array."}
              , {class: "q2", text: "Out of the womb of the morning, you have the dew of your youth."}
              ]
        , 4:  [ {class: "q", text: "Yahweh has sworn, and will not change his mind:"}
              , {class: "q2", text: "“You are a priest forever in the order of Melchizedek.”"}
              ]
        , 5:  [ {class: "q", text: "The Lord is at your right hand."}
              , {class: "q2", text: "He will crush kings in the day of his wrath."}
              ]
        , 6:  [ {class: "q", text: "He will judge among the nations."}
              , {class: "q2", text: "He will heap up dead bodies."}
              , {class: "q2", text: "He will crush the ruler of the whole earth."}
              ]
        , 7:  [ {class: "q", text: "He will drink of the brook on the way;"}
              , {class: "q2", text: "therefore he will lift up his head."}
              ]
        }
  , 111:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "I will give thanks to Yahweh with my whole heart,"}
              , {class: "q2", text: "in the council of the upright, and in the congregation."}
              ]
        , 2:  [ {class: "q", text: "Yahweh’s works are great,"}
              , {class: "q2", text: "pondered by all those who delight in them."}
              ]
        , 3:  [ {class: "q", text: "His work is honor and majesty."}
              , {class: "q2", text: "His righteousness endures forever."}
              ]
        , 4:  [ {class: "q", text: "He has caused his wonderful works to be remembered."}
              , {class: "q2", text: "Yahweh is gracious and merciful."}
              ]
        , 5:  [ {class: "q", text: "He has given food to those who fear him."}
              , {class: "q2", text: "He always remembers his covenant."}
              ]
        , 6:  [ {class: "q", text: "He has shown his people the power of his works,"}
              , {class: "q2", text: "in giving them the heritage of the nations."}
              ]
        , 7:  [ {class: "q", text: "The works of his hands are truth and justice."}
              , {class: "q2", text: "All his precepts are sure."}
              ]
        , 8:  [ {class: "q", text: "They are established forever and ever."}
              , {class: "q2", text: "They are done in truth and uprightness."}
              ]
        , 9:  [ {class: "q", text: "He has sent redemption to his people."}
              , {class: "q2", text: "He has ordained his covenant forever."}
              , {class: "q2", text: "His name is holy and awesome!"}
              ]
        , 10: [ {class: "q", text: "The fear of Yahweh is the beginning of wisdom."}
              , {class: "q2", text: "All those who do his work have a good understanding."}
              , {class: "q", text: "His praise endures forever!"}
              ]
        }
  , 112:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Blessed is the man who fears Yahweh,"}
              , {class: "q2", text: "who delights greatly in his commandments."}
              ]
        , 2:  [ {class: "q", text: "His offspring will be mighty in the land."}
              , {class: "q2", text: "The generation of the upright will be blessed."}
              ]
        , 3:  [ {class: "q", text: "Wealth and riches are in his house."}
              , {class: "q2", text: "His righteousness endures forever."}
              ]
        , 4:  [ {class: "q", text: "Light dawns in the darkness for the upright,"}
              , {class: "q2", text: "gracious, merciful, and righteous."}
              ]
        , 5:  [ {class: "q", text: "It is well with the man who deals graciously and lends."}
              , {class: "q2", text: "He will maintain his cause in judgment."}
              ]
        , 6:  [ {class: "q", text: "For he will never be shaken."}
              , {class: "q2", text: "The righteous will be remembered forever."}
              ]
        , 7:  [ {class: "q", text: "He will not be afraid of evil news."}
              , {class: "q2", text: "His heart is steadfast, trusting in Yahweh."}
              ]
        , 8:  [ {class: "q", text: "His heart is established."}
              , {class: "q2", text: "He will not be afraid in the end when he sees his adversaries."}
              ]
        , 9:  [ {class: "q", text: "He has dispersed, he has given to the poor."}
              , {class: "q2", text: "His righteousness endures forever."}
              , {class: "q2", text: "His horn will be exalted with honor."}
              ]
        , 10: [ {class: "q", text: "The wicked will see it, and be grieved."}
              , {class: "q2", text: "He shall gnash with his teeth, and melt away."}
              , {class: "q2", text: "The desire of the wicked will perish."}
              ]
        }
  , 113:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Praise, you servants of Yahweh,"}
              , {class: "q2", text: "praise Yahweh’s name."}
              ]
        , 2:  [ {class: "q", text: "Blessed be Yahweh’s name,"}
              , {class: "q2", text: "from this time forward and forever more."}
              ]
        , 3:  [ {class: "q", text: "From the rising of the sun to its going down,"}
              , {class: "q2", text: "Yahweh’s name is to be praised."}
              ]
        , 4:  [ {class: "q", text: "Yahweh is high above all nations,"}
              , {class: "q2", text: "his glory above the heavens."}
              ]
        , 5:  [ {class: "q", text: "Who is like Yahweh, our God,"}
              , {class: "q2", text: "who has his seat on high,"}
              ]
        , 6:  [ {class: "q2", text: "/span> Who stoops down to see in heaven and in the earth?"} ]
        , 7:  [ {class: "q", text: "He raises up the poor out of the dust."}
              , {class: "q2", text: "Lifts up the needy from the ash heap,"}
              ]
        , 8:  [ {class: "q", text: "that he may set him with princes,"}
              , {class: "q2", text: "even with the princes of his people."}
              ]
        , 9:  [ {class: "q", text: "He settles the barren woman in her home"}
              , {class: "q2", text: "as a joyful mother of children."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 114:{ 1:  [ {class: "q", text: "When Israel went out of Egypt,"}
              , {class: "q2", text: "the house of Jacob from a people of foreign language,"}
              ]
        , 2:  [ {class: "q", text: "Judah became his sanctuary,"}
              , {class: "q2", text: "Israel his dominion."}
              ]
        , 3:  [ {class: "q", text: "The sea saw it, and fled."}
              , {class: "q2", text: "The Jordan was driven back."}
              ]
        , 4:  [ {class: "q", text: "The mountains skipped like rams,"}
              , {class: "q2", text: "the little hills like lambs."}
              ]
        , 5:  [ {class: "q", text: "What was it, you sea, that you fled?"}
              , {class: "q2", text: "You Jordan, that you turned back?"}
              ]
        , 6:  [ {class: "q", text: "You mountains, that you skipped like rams;"}
              , {class: "q2", text: "you little hills, like lambs?"}
              ]
        , 7:  [ {class: "q", text: "Tremble, you earth, at the presence of the Lord,"}
              , {class: "q2", text: "at the presence of the God of Jacob,"}
              ]
        , 8:  [ {class: "q", text: "who turned the rock into a pool of water,"}
              , {class: "q2", text: "the flint into a spring of waters."}
              ]
        }
  , 115:{ 1:  [ {class: "q", text: "Not to us, Yahweh, not to us,"}
              , {class: "q2", text: "but to your name give glory,"}
              , {class: "q2", text: "for your loving kindness, and for your truth’s sake."}
              ]
        , 2:  [ {class: "q", text: "Why should the nations say,"}
              ,  {class: "q2", text: "“Where is their God, now?”"}
              ]
        , 3:  [ {class: "q", text: "But our God is in the heavens."}
              , {class: "q2", text: "He does whatever he pleases."}
              ]
        , 4:  [ {class: "q", text: "Their idols are silver and gold,"}
              , {class: "q2", text: "the work of men’s hands."}
              ]
        , 5:  [ {class: "q", text: "They have mouths, but they don’t speak."}
              , {class: "q2", text: "They have eyes, but they don’t see."}
              ]
        , 6:  [ {class: "q", text: "They have ears, but they don’t hear."}
              , {class: "q2", text: "They have noses, but they don’t smell."}
              ]
        , 7:  [ {class: "q", text: "They have hands, but they don’t feel."}
              , {class: "q2", text: "They have feet, but they don’t walk,"}
              , {class: "q2", text: "neither do they speak through their throat."}
              ]
        , 8:  [ {class: "q", text: "Those who make them will be like them;"}
              , {class: "q2", text: "yes, everyone who trusts in them."}
              ]
        , 9:  [ {class: "q", text: "Israel, trust in Yahweh!"}
              , {class: "q2", text: "He is their help and their shield."}
              ]
        , 10: [ {class: "q", text: "House of Aaron, trust in Yahweh!"}
              , {class: "q2", text: "He is their help and their shield."}
              ]
        , 11: [ {class: "q", text: "You who fear Yahweh, trust in Yahweh!"}
              , {class: "q2", text: "He is their help and their shield."}
              ]
        , 12: [ {class: "q", text: "Yahweh remembers us. He will bless us."}
              , {class: "q2", text: "He will bless the house of Israel."}
              , {class: "q2", text: "He will bless the house of Aaron."}
              ]
        , 13: [ {class: "q", text: "He will bless those who fear Yahweh,"}
              , {class: "q2", text: "both small and great."}
              ]
        , 14: [ {class: "q", text: "May Yahweh increase you more and more,"}
              , {class: "q2", text: "you and your children."}
              ]
        , 15: [ {class: "q", text: "Blessed are you by Yahweh,"}
              , {class: "q2", text: "who made heaven and earth."}
              ]
        , 16: [ {class: "q", text: "The heavens are Yahweh’s heavens,"}
              , {class: "q2", text: "but he has given the earth to the children of men."}
              ]
        , 17: [ {class: "q", text: "The dead don’t praise Yah,"}
              , {class: "q2", text: "neither any who go down into silence;"}
              ]
        , 18: [ {class: "q", text: "but we will bless Yah,"}
              , {class: "q2", text: "from this time forward and forever more."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 116:{ 1:  [ {class: "q", text: "I love Yahweh, because he listens to my voice,"}
              , {class: "q2", text: "and my cries for mercy."}
              ]
        , 2:  [ {class: "q", text: "Because he has turned his ear to me,"}
              , {class: "q2", text: "therefore I will call on him as long as I live."}
              ]
        , 3:  [ {class: "q", text: "The cords of death surrounded me,"}
              , {class: "q2", text: "the pains of Sheol got a hold of me."}
              , {class: "q2", text: "I found trouble and sorrow."}
              ]
        , 4:  [ {class: "q", text: "Then I called on Yahweh’s name:"}
              , {class: "q2", text: "“Yahweh, I beg you, deliver my soul.”"}
              ]
        , 5:  [ {class: "q", text: "Yahweh is Gracious and righteous."}
              , {class: "q2", text: "Yes, our God is merciful."}
              ]
        , 6:  [ {class: "q", text: "Yahweh preserves the simple."}
              , {class: "q2", text: "I was brought low, and he saved me."}
              ]
        , 7:  [ {class: "q", text: "Return to your rest, my soul,"}
              , {class: "q2", text: "for Yahweh has dealt bountifully with you."}
              ]
        , 8:  [ {class: "q", text: "For you have delivered my soul from death,"}
              ,  {class: "q2", text: "my eyes from tears,"}
              , {class: "q2", text: "and my feet from falling."}
              ]
        , 9:  [ {class: "q", text: "I will walk before Yahweh in the land of the living."} ]
        , 10: [ {class: "q", text: "I believed, therefore I said,"}
              , {class: "q2", text: "“I was greatly afflicted.”"}
              ]
        , 11: [ {class: "q", text: "I said in my haste,"}
              , {class: "q2", text: "“All people are liars.”"}
              ]
        , 12: [ {class: "q", text: "What will I give to Yahweh for all his benefits toward me?"} ]
        , 13: [ {class: "q2", text: "</span> I will take the cup of salvation, and call on Yahweh’s name."} ]
        , 14: [ {class: "q", text: "I will pay my vows to Yahweh,"}
              , {class: "q2", text: "yes, in the presence of all his people."}
              ]
        , 15: [ {class: "q", text: "Precious in Yahweh’s sight is the death of his saints."} ]
        , 16: [ {class: "q", text: "Yahweh, truly I am your servant."}
              , {class: "q2", text: "I am your servant, the son of your servant girl."}
              , {class: "q2", text: "You have freed me from my chains."}
              ]
        , 17: [ {class: "q", text: "I will offer to you the sacrifice of thanksgiving,"}
              , {class: "q2", text: "and will call on Yahweh’s name."}
              ]
        , 18: [ {class: "q", text: "I will pay my vows to Yahweh,"}
              , {class: "q2", text: "yes, in the presence of all his people,"}
              ]
        , 19: [ {class: "q", text: "in the courts of Yahweh’s house,"}
              , {class: "q2", text: "in the middle of you, Jerusalem."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 117:{ 1:  [ {class: "q", text: "Praise Yahweh, all you nations!"}
              , {class: "q2", text: "Extol him, all you peoples!"}
              ]
        , 2:  [ {class: "q", text: "For his loving kindness is great toward us."}
              , {class: "q2", text: "Yahweh’s faithfulness endures forever."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 118:{ 1:  [ {class: "q", text: "Give thanks to Yahweh, for he is good,"}
              , {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 2:  [ {class: "q", text: "Let Israel now say"}
              ,  {class: "q2", text: "that his loving kindness endures forever."}
              ]
        , 3:  [ {class: "q", text: "Let the house of Aaron now say"}
              , {class: "q2", text: "that his loving kindness endures forever."}
              ]
        , 4:  [ {class: "q", text: "Now let those who fear Yahweh say"}
              , {class: "q2", text: "that his loving kindness endures forever."}
              ]
        , 5:  [ {class: "q", text: "Out of my distress, I called on Yah."}
              , {class: "q2", text: "Yah answered me with freedom."}
              ]
        , 6:  [ {class: "q", text: "Yahweh is on my side. I will not be afraid."}
              , {class: "q2", text: "What can man do to me?"}
              ]
        , 7:  [ {class: "q", text: "Yahweh is on my side among those who help me."}
              , {class: "q2", text: "Therefore I will look in triumph at those who hate me."}
              ]
        , 8:  [ {class: "q", text: "It is better to take refuge in Yahweh,"}
              , {class: "q2", text: "than to put confidence in man."}
              ]
        , 9:  [ {class: "q", text: "It is better to take refuge in Yahweh,"}
              , {class: "q2", text: "than to put confidence in princes."}
              ]
        , 10: [ {class: "q", text: "All the nations surrounded me,"}
              , {class: "q2", text: "but in Yahweh’s name, I cut them off."}
              ]
        , 11: [ {class: "q", text: "They surrounded me, yes, they surrounded me."}
              , {class: "q2", text: "In Yahweh’s name I indeed cut them off."}
              ]
        , 12: [ {class: "q", text: "They surrounded me like bees."}
              , {class: "q2", text: "They are quenched like the burning thorns."}
              , {class: "q2", text: "In Yahweh’s name I cut them off."}
              ]
        , 13: [ {class: "q", text: "You pushed me back hard, to make me fall,"}
              , {class: "q2", text: "but Yahweh helped me."}
              ]
        , 14: [ {class: "q", text: "Yah is my strength and song."}
              , {class: "q2", text: "He has become my salvation."}
              ]
        , 15: [ {class: "q", text: "The voice of rejoicing and salvation is in the tents of the righteous."}
              , {class: "q2", text: "“The right hand of Yahweh does valiantly."}
              ]
        , 16: [ {class: "q", text: "The right hand of Yahweh is exalted!"}
              , {class: "q2", text: "The right hand of Yahweh does valiantly!”"}
              ]
        , 17: [ {class: "q", text: "I will not die, but live,"}
              , {class: "q2", text: "and declare Yah’s works."}
              ]
        , 18: [ {class: "q", text: "Yah has punished me severely,"}
              , {class: "q2", text: "but he has not given me over to death."}
              ]
        , 19: [ {class: "q", text: "Open to me the gates of righteousness."}
              , {class: "q2", text: "I will enter into them."}
              , {class: "q2", text: "I will give thanks to Yah."}
              ]
        , 20: [ {class: "q", text: "This is the gate of Yahweh;"}
              , {class: "q2", text: "the righteous will enter into it."}
              ]
        , 21: [ {class: "q", text: "I will give thanks to you, for you have answered me,"}
              , {class: "q2", text: "and have become my salvation."}
              ]
        , 22: [ {class: "q", text: "The stone which the builders rejected"}
              , {class: "q2", text: "has become the cornerstone."}
              ]
        , 23: [ {class: "q", text: "This is Yahweh’s doing."}
              , {class: "q2", text: "It is marvelous in our eyes."}
              ]
        , 24: [ {class: "q", text: "This is the day that Yahweh has made."}
              , {class: "q2", text: "We will rejoice and be glad in it!"}
              ]
        , 25: [ {class: "q", text: "Save us now, we beg you, Yahweh!"}
              , {class: "q2", text: "Yahweh, we beg you, send prosperity now."}
              ]
        , 26: [ {class: "q", text: "Blessed is he who comes in Yahweh’s name!"}
              , {class: "q2", text: "We have blessed you out of Yahweh’s house."}
              ]
        , 27: [ {class: "q", text: "Yahweh is God, and he has given us light."}
              , {class: "q2", text: "Bind the sacrifice with cords, even to the horns of the altar."}
              ]
        , 28: [ {class: "q", text: "You are my God, and I will give thanks to you."}
              , {class: "q2", text: "You are my God, I will exalt you."}
              ]
        , 29: [ {class: "q", text: "Oh give thanks to Yahweh, for he is good,"}
              , {class: "q2", text: "for his loving kindness endures forever."}
              ]
        }
  , 119:{ 1:  [ {class: "q", text: "Blessed are those whose ways are blameless,"}
              , {class: "q2", text: "who walk according to Yahweh’s law."}
              ]
        , 2:  [ {class: "q", text: "Blessed are those who keep his statutes,"}
              , {class: "q2", text: "who seek him with their whole heart."}
              ]
        , 3:  [ {class: "q", text: "Yes, they do nothing wrong."}
              , {class: "q2", text: "They walk in his ways."}
              ]
        , 4:  [ {class: "q", text: "You have commanded your precepts,"}
              , {class: "q2", text: "that we should fully obey them."}
              ]
        , 5:  [ {class: "q", text: "Oh that my ways were steadfast"}
              , {class: "q2", text: "to obey your statutes!"}
              ]
        , 6:  [ {class: "q", text: "Then I wouldn’t be disappointed,"}
              , {class: "q2", text: "when I consider all of your commandments."}
              ]
        , 7:  [ {class: "q", text: "I will give thanks to you with uprightness of heart,"}
              , {class: "q2", text: "when I learn your righteous judgments."}
              ]
        , 8:  [ {class: "q", text: "I will observe your statutes."}
              , {class: "q2", text: "Don’t utterly forsake me."}
              ]
        , 9:  [ {class: "q", text: "How can a young man keep his way pure?"}
              , {class: "q2", text: "By living according to your word."}
              ]
        , 10: [ {class: "q", text: "With my whole heart, I have sought you."}
              , {class: "q2", text: "Don’t let me wander from your commandments."}
              ]
        , 11: [ {class: "q", text: "I have hidden your word in my heart,"}
              , {class: "q2", text: "that I might not sin against you."}
              ]
        , 12: [ {class: "q", text: "Blessed are you, Yahweh."}
              , {class: "q2", text: "Teach me your statutes."}
              ]
        , 13: [ {class: "q", text: "With my lips,"}
              , {class: "q2", text: "I have declared all the ordinances of your mouth."}
              ]
        , 14: [ {class: "q", text: "I have rejoiced in the way of your testimonies,"}
              , {class: "q2", text: "as much as in all riches."}
              ]
        , 15: [ {class: "q", text: "I will meditate on your precepts,"}
              , {class: "q2", text: "and consider your ways."}
              ]
        , 16: [ {class: "q", text: "I will delight myself in your statutes."}
              , {class: "q2", text: "I will not forget your word."}
              ]
        , 17: [ {class: "q", text: "Do good to your servant."}
              , {class: "q2", text: "I will live and I will obey your word."}
              ]
        , 18: [ {class: "q", text: "Open my eyes,"}
              , {class: "q2", text: "that I may see wondrous things out of your law."}
              ]
        , 19: [ {class: "q", text: "I am a stranger on the earth."}
              , {class: "q2", text: "Don’t hide your commandments from me."}
              ]
        , 20: [ {class: "q", text: "My soul is consumed with longing for your ordinances at all times."} ]
        , 21: [ {class: "q", text: "You have rebuked the proud who are cursed,"}
              , {class: "q2", text: "who wander from your commandments."}
              ]
        , 22: [ {class: "q", text: "Take reproach and contempt away from me,"}
              , {class: "q2", text: "for I have kept your statutes."}
              ]
        , 23: [ {class: "q", text: "Though princes sit and slander me,"}
              ,  {class: "q2", text: "your servant will meditate on your statutes."}
              ]
        , 24: [ {class: "q", text: "Indeed your statutes are my delight,"}
              , {class: "q2", text: "and my counselors."}
              ]
        , 25: [ {class: "q", text: "My soul is laid low in the dust."}
              , {class: "q2", text: "Revive me according to your word!"}
              ]
        , 26: [ {class: "q", text: "I declared my ways, and you answered me."}
              , {class: "q2", text: "Teach me your statutes."}
              ]
        , 27: [ {class: "q", text: "Let me understand the teaching of your precepts!"}
              , {class: "q2", text: "Then I will meditate on your wondrous works."}
              ]
        , 28: [ {class: "q", text: "My soul is weary with sorrow:"}
              , {class: "q2", text: "strengthen me according to your word."}
              ]
        , 29: [ {class: "q", text: "Keep me from the way of deceit."}
              , {class: "q2", text: "Grant me your law graciously!"}
              ]
        , 30: [ {class: "q", text: "I have chosen the way of truth."}
              , {class: "q2", text: "I have set your ordinances before me."}
              ]
        , 31: [ {class: "q", text: "I cling to your statutes, Yahweh."}
              , {class: "q2", text: "Don’t let me be disappointed."}
              ]
        , 32: [ {class: "q", text: "I run in the path of your commandments,"}
              , {class: "q2", text: "for you have set my heart free."}
              ]
        , 33: [ {class: "q", text: "Teach me, Yahweh, the way of your statutes."}
              ,  {class: "q2", text: "I will keep them to the end."}
              ]
        , 34: [ {class: "q", text: "Give me understanding, and I will keep your law."}
              , {class: "q2", text: "Yes, I will obey it with my whole heart."}
              ]
        , 35: [ {class: "q", text: "Direct me in the path of your commandments,"}
              , {class: "q2", text: "for I delight in them."}
              ]
        , 36: [ {class: "q", text: "Turn my heart toward your statutes,"}
              ,  {class: "q2", text: "not toward selfish gain."}
              ]
        , 37: [ {class: "q", text: "Turn my eyes away from looking at worthless things."}
              , {class: "q2", text: "Revive me in your ways."}
              ]
        , 38: [ {class: "q", text: "Fulfill your promise to your servant,"}
              , {class: "q2", text: "that you may be feared."}
              ]
        , 39: [ {class: "q", text: "Take away my disgrace that I dread,"}
              , {class: "q2", text: "for your ordinances are good."}
              ]
        , 40: [ {class: "q", text: "Behold, I long for your precepts!"}
              ,  {class: "q2", text: "Revive me in your righteousness."}
              ]
        , 41: [ {class: "q", text: "Let your loving kindness also come to me, Yahweh,"}
              , {class: "q2", text: "your salvation, according to your word."}
              ]
        , 42: [ {class: "q", text: "So I will have an answer for him who reproaches me,"}
              , {class: "q2", text: "for I trust in your word."}
              ]
        , 43: [ {class: "q", text: "Don’t snatch the word of truth out of my mouth,"}
              ,  {class: "q2", text: "for I put my hope in your ordinances."}
              ]
        , 44: [ {class: "q", text: "So I will obey your law continually,"}
              ,  {class: "q2", text: "forever and ever."}
              ]
        , 45: [ {class: "q", text: "I will walk in liberty,"}
              , {class: "q2", text: "for I have sought your precepts."}
              ]
        , 46: [ {class: "q", text: "I will also speak of your statutes before kings,"}
              ,  {class: "q2", text: "and will not be disappointed."}
              ]
        , 47: [ {class: "q", text: "I will delight myself in your commandments,"}
              , {class: "q2", text: "because I love them."}
              ]
        , 48: [ {class: "q", text: "I reach out my hands for your commandments, which I love."}
              , {class: "q2", text: "I will meditate on your statutes."}
              ]
        , 49: [ {class: "q", text: "Remember your word to your servant,"}
              , {class: "q2", text: "because you gave me hope."}
              ]
        , 50: [ {class: "q", text: "This is my comfort in my affliction,"}
              , {class: "q2", text: "for your word has revived me."}
              ]
        , 51: [ {class: "q", text: "The arrogant mock me excessively,"}
              ,  {class: "q2", text: "but I don’t swerve from your law."}
              ]
        , 52: [ {class: "q", text: "I remember your ordinances of old, Yahweh,"}
              , {class: "q2", text: "and have comforted myself."}
              ]
        , 53: [ {class: "q", text: "Indignation has taken hold on me,"}
              , {class: "q2", text: "because of the wicked who forsake your law."}
              ]
        , 54: [ {class: "q", text: "Your statutes have been my songs"}
              , {class: "q2", text: "in the house where I live."}
              ]
        , 55: [ {class: "q", text: "I have remembered your name, Yahweh, in the night,"}
              , {class: "q2", text: "and I obey your law."}
              ]
        , 56: [ {class: "q", text: "This is my way,"}
              , {class: "q2", text: "that I keep your precepts."}
              ]
        , 57: [ {class: "q", text: "Yahweh is my portion."}
              , {class: "q2", text: "I promised to obey your words."}
              ]
        , 58: [ {class: "q", text: "I sought your favor with my whole heart."}
              , {class: "q2", text: "Be merciful to me according to your word."}
              ]
        , 59: [ {class: "q", text: "I considered my ways,"}
              , {class: "q2", text: "and turned my steps to your statutes."}
              ]
        , 60: [ {class: "q", text: "I will hurry, and not delay,"}
              , {class: "q2", text: "to obey your commandments."}
              ]
        , 61: [ {class: "q", text: "The ropes of the wicked bind me,"}
              , {class: "q2", text: "but I won’t forget your law."}
              ]
        , 62: [ {class: "q", text: "At midnight I will rise to give thanks to you,"}
              , {class: "q2", text: "because of your righteous ordinances."}
              ]
        , 63: [ {class: "q", text: "I am a friend of all those who fear you,"}
              , {class: "q2", text: "of those who observe your precepts."}
              ]
        , 64: [ {class: "q", text: "The earth is full of your loving kindness, Yahweh."}
              , {class: "q2", text: "Teach me your statutes."}
              ]
        , 65: [ {class: "q", text: "Do good to your servant,"}
              , {class: "q2", text: "according to your word, Yahweh."}
              ]
        , 66: [ {class: "q", text: "Teach me good judgment and knowledge,"}
              , {class: "q2", text: "for I believe in your commandments."}
              ]
        , 67: [ {class: "q", text: "Before I was afflicted, I went astray;"}
              ,  {class: "q2", text: "but now I observe your word."}
              ]
        , 68: [ {class: "q", text: "You are good, and do good."}
              , {class: "q2", text: "Teach me your statutes."}
              ]
        , 69: [ {class: "q", text: "The proud have smeared a lie upon me."}
              , {class: "q2", text: "With my whole heart, I will keep your precepts."}
              ]
        , 70: [ {class: "q", text: "Their heart is as callous as the fat,"}
              , {class: "q2", text: "but I delight in your law."}
              ]
        , 71: [ {class: "q", text: "It is good for me that I have been afflicted,"}
              , {class: "q2", text: "that I may learn your statutes."}
              ]
        , 72: [ {class: "q", text: "The law of your mouth is better to me than thousands of pieces of gold and silver."} ]
        , 73: [ {class: "q", text: "Your hands have made me and formed me."}
              , {class: "q2", text: "Give me understanding, that I may learn your commandments."}
              ]
        , 74: [ {class: "q", text: "Those who fear you will see me and be glad,"}
              , {class: "q2", text: "because I have put my hope in your word."}
              ]
        , 75: [ {class: "q", text: "Yahweh, I know that your judgments are righteous,"}
              , {class: "q2", text: "that in faithfulness you have afflicted me."}
              ]
        , 76: [ {class: "q", text: "Please let your loving kindness be for my comfort,"}
              , {class: "q2", text: "according to your word to your servant."}
              ]
        , 77: [ {class: "q", text: "Let your tender mercies come to me, that I may live;"}
              , {class: "q2", text: "for your law is my delight."}
              ]
        , 78: [ {class: "q", text: "Let the proud be disappointed, for they have overthrown me wrongfully."}
              ,  {class: "q2", text: "I will meditate on your precepts."}
              ]
        , 79: [ {class: "q", text: "Let those who fear you turn to me."}
              , {class: "q2", text: "They will know your statutes."}
              ]
        , 80: [ {class: "q", text: "Let my heart be blameless toward your decrees,"}
              , {class: "q2", text: "that I may not be disappointed."}
              ]
        , 81: [ {class: "q", text: "My soul faints for your salvation."}
              , {class: "q2", text: "I hope in your word."}
              ]
        , 82: [ {class: "q", text: "My eyes fail for your word."}
              , {class: "q2", text: "I say, “When will you comfort me?”"}
              ]
        , 83: [ {class: "q", text: "For I have become like a wineskin in the smoke."}
              , {class: "q2", text: "I don’t forget your statutes."}
              ]
        , 84: [ {class: "q", text: "How many are the days of your servant?"}
              , {class: "q2", text: "When will you execute judgment on those who persecute me?"}
              ]
        , 85: [ {class: "q", text: "The proud have dug pits for me,"}
              , {class: "q2", text: "contrary to your law."}
              ]
        , 86: [ {class: "q", text: "All of your commandments are faithful."}
              , {class: "q2", text: "They persecute me wrongfully."}
              , {class: "q2", text: "Help me!"}
              ]
        , 87: [ {class: "q", text: "They had almost wiped me from the earth,"}
              , {class: "q2", text: "but I didn’t forsake your precepts."}
              ]
        , 88: [ {class: "q", text: "Preserve my life according to your loving kindness,"}
              , {class: "q2", text: "so I will obey the statutes of your mouth."}
              ]
        , 89: [ {class: "q", text: "Yahweh, your word is settled in heaven forever."} ]
        , 90: [ {class: "q", text: "Your faithfulness is to all generations."}
              , {class: "q2", text: "You have established the earth, and it remains."}
              ]
        , 91: [ {class: "q", text: "Your laws remain to this day,"}
              , {class: "q2", text: "for all things serve you."}
              ]
        , 92: [ {class: "q", text: "Unless your law had been my delight,"}
              ,  {class: "q2", text: "I would have perished in my affliction."}
              ]
        , 93: [ {class: "q", text: "I will never forget your precepts,"}
              , {class: "q2", text: "for with them, you have revived me."}
              ]
        , 94: [ {class: "q", text: "I am yours."}
              ,  {class: "q2", text: "Save me, for I have sought your precepts."}
              ]
        , 95: [ {class: "q", text: "The wicked have waited for me, to destroy me."}
              , {class: "q2", text: "I will consider your statutes."}
              ]
        , 96: [ {class: "q", text: "I have seen a limit to all perfection,"}
              , {class: "q2", text: "but your commands are boundless."}
              ]
        , 97: [ {class: "q", text: "How I love your law!"}
              , {class: "q2", text: "It is my meditation all day."}
              ]
        , 98: [ {class: "q", text: "Your commandments make me wiser than my enemies,"}
              , {class: "q2", text: "for your commandments are always with me."}
              ]
        , 99: [ {class: "q", text: "I have more understanding than all my teachers,"}
              ,  {class: "q2", text: "for your testimonies are my meditation."}
              ]
        , 100:[{class: "q", text: "I understand more than the aged,"}
              ,  {class: "q2", text: "because I have kept your precepts."}
              ]
        , 101:[{class: "q", text: "I have kept my feet from every evil way,"}
              , {class: "q2", text: "that I might observe your word."}
              ]
        , 102:[{class: "q", text: "I have not turned away from your ordinances,"}
              , {class: "q2", text: "for you have taught me."}
              ]
        , 103:[{class: "q", text: "How sweet are your promises to my taste,"}
              ,  {class: "q2", text: "more than honey to my mouth!"}
              ]
        , 104:[{class: "q", text: "Through your precepts, I get understanding;"}
              , {class: "q2", text: "therefore I hate every false way."}
              ]
        , 105:[{class: "q", text: "Your word is a lamp to my feet,"}
              , {class: "q2", text: "and a light for my path."}
              ]
        , 106:[{class: "q", text: "I have sworn, and have confirmed it,"}
              , {class: "q2", text: "that I will obey your righteous ordinances."}
              ]
        , 107:[{class: "q", text: "I am afflicted very much."}
              , {class: "q2", text: "Revive me, Yahweh, according to your word."}
              ]
        , 108:[{class: "q", text: "Accept, I beg you, the willing offerings of my mouth."}
              , {class: "q2", text: "Yahweh, teach me your ordinances."}
              ]
        , 109:[{class: "q", text: "My soul is continually in my hand,"}
              , {class: "q2", text: "yet I won’t forget your law."}
              ]
        , 110:[{class: "q", text: "The wicked have laid a snare for me,"}
              , {class: "q2", text: "yet I haven’t gone astray from your precepts."}
              ]
        , 111:[{class: "q", text: "I have taken your testimonies as a heritage forever,"}
              , {class: "q2", text: "for they are the joy of my heart."}
              ]
        , 112:[{class: "q", text: "I have set my heart to perform your statutes forever,"}
              ,  {class: "q2", text: "even to the end."}
              ]
        , 113:[{class: "q", text: "I hate double-minded men,"}
              , {class: "q2", text: "but I love your law."}
              ]
        , 114:[{class: "q", text: "You are my hiding place and my shield."}
              , {class: "q2", text: "I hope in your word."}
              ]
        , 115:[{class: "q", text: "Depart from me, you evildoers,"}
              , {class: "q2", text: "that I may keep the commandments of my God."}
              ]
        , 116:[{class: "q", text: "Uphold me according to your word, that I may live."}
              , {class: "q2", text: "Let me not be ashamed of my hope."}
              ]
        , 117:[{class: "q", text: "Hold me up, and I will be safe,"}
              , {class: "q2", text: "and will have respect for your statutes continually."}
              ]
        , 118:[{class: "q", text: "You reject all those who stray from your statutes,"}
              , {class: "q2", text: "for their deceit is in vain."}
              ]
        , 119:[{class: "q", text: "You put away all the wicked of the earth like dross."}
              ,  {class: "q2", text: "Therefore I love your testimonies."}
              ]
        , 120:[{class: "q", text: "My flesh trembles for fear of you."}
              , {class: "q2", text: "I am afraid of your judgments."}
              ]
        , 121:[{class: "q", text: "I have done what is just and righteous."}
              , {class: "q2", text: "Don’t leave me to my oppressors."}
              ]
        , 122:[{class: "q", text: "Ensure your servant’s well-being."}
              , {class: "q2", text: "Don’t let the proud oppress me."}
              ]
        , 123:[{class: "q", text: "My eyes fail looking for your salvation,"}
              , {class: "q2", text: "for your righteous word."}
              ]
        , 124:[{class: "q", text: "Deal with your servant according to your loving kindness."}
              ,  {class: "q2", text: "Teach me your statutes."}
              ]
        , 125:[{class: "q", text: "I am your servant. Give me understanding,"}
              , {class: "q2", text: "that I may know your testimonies."}
              ]
        , 126:[{class: "q", text: "It is time to act, Yahweh,"}
              , {class: "q2", text: "for they break your law."}
              ]
        , 127:[{class: "q", text: "Therefore I love your commandments more than gold,"}
              ,  {class: "q2", text: "yes, more than pure gold."}
              ]
        , 128:[{class: "q", text: "Therefore I consider all of your precepts to be right."}
              , {class: "q2", text: "I hate every false way."}
              ]
        , 129:[{class: "q", text: "Your testimonies are wonderful,"}
              , {class: "q2", text: "therefore my soul keeps them."}
              ]
        , 130:[{class: "q", text: "The entrance of your words gives light."}
              , {class: "q2", text: "It gives understanding to the simple."}
              ]
        , 131:[{class: "q", text: "I opened my mouth wide and panted,"}
              , {class: "q2", text: "for I longed for your commandments."}
              ]
        , 132:[{class: "q", text: "Turn to me, and have mercy on me,"}
              ,  {class: "q2", text: "as you always do to those who love your name."}
              ]
        , 133:[{class: "q", text: "Establish my footsteps in your word."}
              , {class: "q2", text: "Don’t let any iniquity have dominion over me."}
              ]
        , 134:[{class: "q", text: "Redeem me from the oppression of man,"}
              , {class: "q2", text: "so I will observe your precepts."}
              ]
        , 135:[{class: "q", text: "Make your face shine on your servant."}
              , {class: "q2", text: "Teach me your statutes."}
              ]
        , 136:[{class: "q", text: "Streams of tears run down my eyes,"}
              , {class: "q2", text: "because they don’t observe your law."}
              ]
        , 137:[{class: "q", text: "You are righteous, Yahweh."}
              , {class: "q2", text: "Your judgments are upright."}
              ]
        , 138:[{class: "q", text: "You have commanded your statutes in righteousness."}
              , {class: "q2", text: "They are fully trustworthy."}
              ]
        , 139:[{class: "q", text: "My zeal wears me out,"}
              , {class: "q2", text: "because my enemies ignore your words."}
              ]
        , 140:[{class: "q", text: "Your promises have been thoroughly tested,"}
              , {class: "q2", text: "and your servant loves them."}
              ]
        , 141:[{class: "q", text: "I am small and despised."}
              , {class: "q2", text: "I don’t forget your precepts."}
              ]
        , 142:[{class: "q", text: "Your righteousness is an everlasting righteousness."}
              , {class: "q2", text: "Your law is truth."}
              ]
        , 143:[{class: "q", text: "Trouble and anguish have taken hold of me."}
              , {class: "q2", text: "Your commandments are my delight."}
              ]
        , 144:[{class: "q", text: "Your testimonies are righteous forever."}
              , {class: "q2", text: "Give me understanding, that I may live."}
              ]
        , 145:[{class: "q", text: "I have called with my whole heart."}
              , {class: "q2", text: "Answer me, Yahweh!"}
              , {class: "q2", text: "I will keep your statutes."}
              ]
        , 146:[{class: "q", text: "I have called to you. Save me!"}
              , {class: "q2", text: "I will obey your statutes."}
              ]
        , 147:[{class: "q", text: "I rise before dawn and cry for help."}
              , {class: "q2", text: "I put my hope in your words."}
              ]
        , 148:[{class: "q", text: "My eyes stay open through the night watches,"}
              , {class: "q2", text: "that I might meditate on your word."}
              ]
        , 149:[{class: "q", text: "Hear my voice according to your loving kindness."}
              , {class: "q2", text: "Revive me, Yahweh, according to your ordinances."}
              ]
        , 150:[{class: "q", text: "They draw near who follow after wickedness."}
              , {class: "q2", text: "They are far from your law."}
              ]
        , 151:[{class: "q", text: "You are near, Yahweh."}
              , {class: "q2", text: "All your commandments are truth."}
              ]
        , 152:[{class: "q", text: "Of old I have known from your testimonies,"}
              , {class: "q2", text: "that you have founded them forever."}
              ]
        , 153:[{class: "q", text: "Consider my affliction, and deliver me,"}
              , {class: "q2", text: "for I don’t forget your law."}
              ]
        , 154:[{class: "q", text: "Plead my cause, and redeem me!"}
              ,  {class: "q2", text: "Revive me according to your promise."}
              ]
        , 155:[{class: "q", text: "Salvation is far from the wicked,"}
              , {class: "q2", text: "for they don’t seek your statutes."}
              ]
        , 156:[{class: "q", text: "Great are your tender mercies, Yahweh."}
              ,  {class: "q2", text: "Revive me according to your ordinances."}
              ]
        , 157:[{class: "q", text: "Many are my persecutors and my adversaries."}
              , {class: "q2", text: "I haven’t swerved from your testimonies."}
              ]
        , 158:[{class: "q", text: "I look at the faithless with loathing,"}
              , {class: "q2", text: "because they don’t observe your word."}
              ]
        , 159:[{class: "q", text: "Consider how I love your precepts."}
              , {class: "q2", text: "Revive me, Yahweh, according to your loving kindness."}
              ]
        , 160:[{class: "q", text: "All of your words are truth."}
              , {class: "q2", text: "Every one of your righteous ordinances endures forever."}
              ]
        , 161:[{class: "q", text: "Princes have persecuted me without a cause,"}
              , {class: "q2", text: "but my heart stands in awe of your words."}
              ]
        , 162:[{class: "q", text: "I rejoice at your word,"}
              , {class: "q2", text: "as one who finds great plunder."}
              ]
        , 163:[{class: "q", text: "I hate and abhor falsehood."}
              , {class: "q2", text: "I love your law."}
              ]
        , 164:[{class: "q", text: "Seven times a day, I praise you,"}
              , {class: "q2", text: "because of your righteous ordinances."}
              ]
        , 165:[{class: "q", text: "Those who love your law have great peace."}
              , {class: "q2", text: "Nothing causes them to stumble."}
              ]
        , 166:[{class: "q", text: "I have hoped for your salvation, Yahweh."}
              , {class: "q2", text: "I have done your commandments."}
              ]
        , 167:[{class: "q", text: "My soul has observed your testimonies."}
              , {class: "q2", text: "I love them exceedingly."}
              ]
        , 168:[{class: "q", text: "I have obeyed your precepts and your testimonies,"}
              , {class: "q2", text: "for all my ways are before you."}
              ]
        , 169:[{class: "q", text: "Let my cry come before you, Yahweh."}
              ,  {class: "q2", text: "Give me understanding according to your word."}
              ]
        , 170:[{class: "q", text: "Let my supplication come before you."}
              , {class: "q2", text: "Deliver me according to your word."}
              ]
        , 171:[{class: "q", text: "Let my lips utter praise,"}
              , {class: "q2", text: "for you teach me your statutes."}
              ]
        , 172:[{class: "q", text: "Let my tongue sing of your word,"}
              ,  {class: "q2", text: "for all your commandments are righteousness."}
              ]
        , 173:[{class: "q", text: "Let your hand be ready to help me,"}
              ,  {class: "q2", text: "for I have chosen your precepts."}
              ]
        , 174:[{class: "q", text: "I have longed for your salvation, Yahweh."}
              ,  {class: "q2", text: "Your law is my delight."}
              ]
        , 175:[{class: "q", text: "Let my soul live, that I may praise you."}
              , {class: "q2", text: "Let your ordinances help me."}
              ]
        , 176:[{class: "q", text: "I have gone astray like a lost sheep."}
              , {class: "q2", text: "Seek your servant, for I don’t forget your commandments."}
              ]
        }
  , 12: { 1:  [ {class: "q", text: "Help, Yahweh; for the godly man ceases."}
              , {class: "q2", text: "For the faithful fail from among the children of men."}
              ]
        , 2:  [ {class: "q", text: "Everyone lies to his neighbor."}
              , {class: "q2", text: "They speak with flattering lips, and with a double heart."}
              ]
        , 3:  [ {class: "q", text: "May Yahweh cut off all flattering lips,"}
              , {class: "q2", text: "and the tongue that boasts,"}
              ]
        , 4:  [ {class: "q", text: "who have said, “With our tongue we will prevail."}
              , {class: "q2", text: "Our lips are our own."}
              , {class: "q2", text: "Who is lord over us?”"}
              ]
        , 5:  [ {class: "q", text: "“Because of the oppression of the weak and because of the groaning of the needy,"}
              , {class: "q2", text: "I will now arise,” says Yahweh;"}
              , {class: "q", text: "“I will set him in safety from those who malign him.”"}
              ]
        , 6:  [ {class: "q", text: "Yahweh’s words are flawless words,"}
              , {class: "q2", text: "as silver refined in a clay furnace, purified seven times."}
              ]
        , 7:  [ {class: "q", text: "You will keep them, Yahweh."}
              , {class: "q2", text: "You will preserve them from this generation forever."}
              ]
        , 8:  [ {class: "q", text: "The wicked walk on every side,"}
              , {class: "q2", text: "when what is vile is exalted among the sons of men."}
              ]
        }
  , 120:{ 1:  [ {class: "q", text: "In my distress, I cried to Yahweh."}
              , {class: "q2", text: "He answered me."}
              ]
        , 2:  [ {class: "q", text: "Deliver my soul, Yahweh, from lying lips,"}
              , {class: "q2", text: "from a deceitful tongue."}
              ]
        , 3:  [ {class: "q", text: "What will be given to you, and what will be done more to you,"}
              , {class: "q2", text: "you deceitful tongue?"}
              ]
        , 4:  [ {class: "q", text: "Sharp arrows of the mighty,"}
              , {class: "q2", text: "with coals of juniper."}
              ]
        , 5:  [ {class: "q", text: "Woe is me, that I live in Meshech,"}
              , {class: "q2", text: "that I dwell among the tents of Kedar!"}
              ]
        , 6:  [ {class: "q", text: "My soul has had her dwelling too long"}
              , {class: "q2", text: "with him who hates peace."}
              ]
        , 7:  [ {class: "q", text: "I am for peace,"}
              , {class: "q2", text: "but when I speak, they are for war."}
              ]
        }
  , 121:{ 1:  [ {class: "q", text: "I will lift up my eyes to the hills."}
              , {class: "q2", text: "Where does my help come from?"}
              ]
        , 2:  [ {class: "q", text: "My help comes from Yahweh,"}
              , {class: "q2", text: "who made heaven and earth."}
              ]
        , 3:  [ {class: "q", text: "He will not allow your foot to be moved."}
              , {class: "q2", text: "He who keeps you will not slumber."}
              ]
        , 4:  [ {class: "q", text: "Behold, he who keeps Israel"}
              , {class: "q2", text: "will neither slumber nor sleep."}
              ]
        , 5:  [ {class: "q", text: "Yahweh is your keeper."}
              , {class: "q2", text: "Yahweh is your shade on your right hand."}
              ]
        , 6:  [ {class: "q", text: "The sun will not harm you by day,"}
              , {class: "q2", text: "nor the moon by night."}
              ]
        , 7:  [ {class: "q", text: "Yahweh will keep you from all evil."}
              , {class: "q2", text: "He will keep your soul."}
              ]
        , 8:  [ {class: "q", text: "Yahweh will keep your going out and your coming in,"}
              , {class: "q2", text: "from this time forward, and forever more."}
              ]
        }
  , 122:{ 1:  [ {class: "q", text: "I was glad when they said to me,"}
              , {class: "q2", text: "“Let’s go to Yahweh’s house!”"}
              ]
        , 2:  [ {class: "q", text: "Our feet are standing within your gates, Jerusalem,"} ]     
        , 3:  [ {class: "q2", text: "/span> Jerusalem, that is built as a city that is compact together,"} ]
        , 4:  [ {class: "q", text: "where the tribes go up, even Yah’s tribes,"}
              , {class: "q2", text: "according to an ordinance for Israel,"}
              , {class: "q2", text: "to give thanks to Yahweh’s name."}
              ]
        , 5:  [ {class: "q", text: "For there are set thrones for judgment,"}
              , {class: "q2", text: "the thrones of David’s house."}
              ]
        , 6:  [ {class: "q", text: "Pray for the peace of Jerusalem."}
              , {class: "q2", text: "Those who love you will prosper."}
              ]
        , 7:  [ {class: "q", text: "Peace be within your walls,"}
              , {class: "q2", text: "and prosperity within your palaces."}
              ]
        , 8:  [ {class: "q", text: "For my brothers’ and companions’ sakes,"}
              , {class: "q2", text: "I will now say, “Peace be within you.”"}
              ]
        , 9:  [ {class: "q", text: "For the sake of the house of Yahweh our God,"}
              , {class: "q2", text: "I will seek your good."}
              ]
        }
        , 123:{ 1:  [ {class: "q", text: "I lift up my eyes to you,"}
              , {class: "q2", text: "you who sit in the heavens."}
              ]
        , 2:  [ {class: "q", text: "Behold, as the eyes of servants look to the hand of their master,"}
              , {class: "q2", text: "as the eyes of a maid to the hand of her mistress;"}
              , {class: "q2", text: "so our eyes look to Yahweh, our God,"}
              , {class: "q2", text: "until he has mercy on us."}
              ]
        , 3:  [ {class: "q", text: "Have mercy on us, Yahweh, have mercy on us,"}
              , {class: "q2", text: "for we have endured much contempt."}
              ]
        , 4:  [ {class: "q", text: "Our soul is exceedingly filled with the scoffing of those who are at ease,"}
              ,  {class: "q2", text: "with the contempt of the proud."}
              ]
        }
  , 124:{ 1:  [ {class: "q", text: "If it had not been Yahweh who was on our side,"}
              , {class: "q2", text: "let Israel now say,"}
              ]
        , 2:  [ {class: "q", text: "if it had not been Yahweh who was on our side,"}
              , {class: "q2", text: "when men rose up against us;"}
              ]
        , 3:  [ {class: "q", text: "then they would have swallowed us up alive,"}
              , {class: "q2", text: "when their wrath was kindled against us;"}
              ]
        , 4:  [ {class: "q", text: "then the waters would have overwhelmed us,"}
              , {class: "q2", text: "the stream would have gone over our soul;"}
              ]
        , 5:  [ {class: "q", text: "then the proud waters would have gone over our soul."} ]
        , 6:  [ {class: "q", text: "Blessed be Yahweh,"}
              , {class: "q2", text: "who has not given us as a prey to their teeth."}
              ]
        , 7:  [ {class: "q", text: "Our soul has escaped like a bird out of the fowler’s snare."}
              , {class: "q2", text: "The snare is broken, and we have escaped."}
              ]
        , 8:  [ {class: "q", text: "Our help is in Yahweh’s name,"}
              , {class: "q2", text: "who made heaven and earth."}
              ]
        }
  , 125:{ 1:  [ {class: "q", text: "Those who trust in Yahweh are as Mount Zion,"}
              , {class: "q2", text: "which can’t be moved, but remains forever."}
              ]
        , 2:  [ {class: "q", text: "As the mountains surround Jerusalem,"}
              , {class: "q2", text: "so Yahweh surrounds his people from this time forward and forever more."}
              ]
        , 3:  [ {class: "q", text: "For the scepter of wickedness won’t remain over the allotment of the righteous;"}
              , {class: "q2", text: "so that the righteous won’t use their hands to do evil."}
              ]
        , 4:  [ {class: "q", text: "Do good, Yahweh, to those who are good,"}
              , {class: "q2", text: "to those who are upright in their hearts."}
              ]
        , 5:  [ {class: "q", text: "But as for those who turn away to their crooked ways,"}
              , {class: "q2", text: "Yahweh will lead them away with the workers of iniquity."}
              , {class: "q", text: "Peace be on Israel."}
              ]
        }
  , 126:{ 1:  [ {class: "q", text: "When Yahweh brought back those who returned to Zion,"}
              , {class: "q2", text: "we were like those who dream."}
              ]
        , 2:  [ {class: "q", text: "Then our mouth was filled with laughter,"}
              , {class: "q2", text: "and our tongue with singing."}
              , {class: "q", text: "Then they said among the nations,"}
              , {class: "q2", text: "“Yahweh has done great things for them.”"}
              ]
        , 3:  [ {class: "q", text: "Yahweh has done great things for us,"}
              , {class: "q2", text: "and we are glad."}
              ]
        , 4:  [ {class: "q", text: "Restore our fortunes again, Yahweh,"}
              , {class: "q2", text: "like the streams in the Negev."}
              ]
        , 5:  [ {class: "q", text: "Those who sow in tears will reap in joy."} ]
        , 6:  [ {class: "q2", text: "/span> He who goes out weeping, carrying seed for sowing,"}
              , {class: "q2", text: "will certainly come again with joy, carrying his sheaves."}
              ]
        }
  , 127:{ 1:  [ {class: "q", text: "Unless Yahweh builds the house,"}
              , {class: "q2", text: "they who build it labor in vain."}
              , {class: "q", text: "Unless Yahweh watches over the city,"}
              , {class: "q2", text: "the watchman guards it in vain."}
              ]
        , 2:  [ {class: "q", text: "It is vain for you to rise up early,"}
              , {class: "q2", text: "to stay up late,"}
              , {class: "q2", text: "eating the bread of toil,"}
              , {class: "q2", text: "for he gives sleep to his loved ones."}
              ]
        , 3:  [ {class: "q", text: "Behold, children are a heritage of Yahweh."}
              ,  {class: "q2", text: "The fruit of the womb is his reward."}
              ]
        , 4:  [ {class: "q", text: "As arrows in the hand of a mighty man,"}
              , {class: "q2", text: "so are the children of youth."}
              ]
        , 5:  [ {class: "q", text: "Happy is the man who has his quiver full of them."}
              , {class: "q2", text: "They won’t be disappointed when they speak with their enemies in the gate."}
              ]
        }
  , 128:{ 1:  [ {class: "q", text: "Blessed is everyone who fears Yahweh,"}
              , {class: "q2", text: "who walks in his ways."}
              ]
        , 2:  [ {class: "q", text: "For you will eat the labor of your hands."}
              , {class: "q2", text: "You will be happy, and it will be well with you."}
              ]
        , 3:  [ {class: "q", text: "Your wife will be as a fruitful vine in the innermost parts of your house,"}
              , {class: "q2", text: "your children like olive plants around your table."}
              ]
        , 4:  [ {class: "q", text: "Behold, thus is the man blessed who fears Yahweh."} ]
        , 5:  [ {class: "q2", text: "/span> May Yahweh bless you out of Zion,"}
              , {class: "q2", text: "and may you see the good of Jerusalem all the days of your life."}
              ]
        , 6:  [ {class: "q", text: "Yes, may you see your children’s children."}
              , {class: "q2", text: "Peace be upon Israel."}
              ]
        }
  , 129:{ 1:  [ {class: "q", text: "Many times they have afflicted me from my youth up."}
              , {class: "q2", text: "Let Israel now say,"}
              ]
        , 2:  [ {class: "q", text: "many times they have afflicted me from my youth up,"}
              , {class: "q2", text: "yet they have not prevailed against me."}
              ]
        , 3:  [ {class: "q", text: "The plowers plowed on my back."}
              , {class: "q2", text: "They made their furrows long."}
              ]
        , 4:  [ {class: "q", text: "Yahweh is righteous."}
              , {class: "q2", text: "He has cut apart the cords of the wicked."}
              ]
        , 5:  [ {class: "q", text: "Let them be disappointed and turned backward,"}
              , {class: "q2", text: "all those who hate Zion."}
              ]
        , 6:  [ {class: "q", text: "Let them be as the grass on the housetops,"}
              , {class: "q2", text: "which withers before it grows up,"}
              ]
        , 7:  [ {class: "q", text: "with which the reaper doesn’t fill his hand,"}
              , {class: "q2", text: "nor he who binds sheaves, his bosom."}
              ]
        , 8:  [ {class: "q", text: "Neither do those who go by say,"}
              , {class: "q2", text: "“The blessing of Yahweh be on you."}
              , {class: "q2", text: "We bless you in Yahweh’s name.”"}
              ]
        }
  , 13: { 1:  [ {class: "q", text: "How long, Yahweh?"}
              , {class: "q2", text: "Will you forget me forever?"}
              , {class: "q2", text: "How long will you hide your face from me?"}
              ]
        , 2:  [ {class: "q", text: "How long shall I take counsel in my soul,"}
              , {class: "q2", text: "having sorrow in my heart every day?"}
              , {class: "q2", text: "How long shall my enemy triumph over me?"}
              ]
        , 3:  [ {class: "q", text: "Behold, and answer me, Yahweh, my God."}
              , {class: "q2", text: "Give light to my eyes, lest I sleep in death;"}
              ]
        , 4:  [ {class: "q2", text: "/span> lest my enemy say, “I have prevailed against him;”"}
              , {class: "q2", text: "lest my adversaries rejoice when I fall."}
              ]
        , 5:  [ {class: "q", text: "But I trust in your loving kindness."}
              , {class: "q2", text: "My heart rejoices in your salvation."}
              ]
        , 6:  [ {class: "q", text: "I will sing to Yahweh,"}
              , {class: "q2", text: "because he has been good to me."}
              ]
        }
  , 130:{ 1:  [ {class: "q", text: "Out of the depths I have cried to you, Yahweh."} ]
        , 2:  [ {class: "q", text: "Lord, hear my voice."}
              , {class: "q2", text: "Let your ears be attentive to the voice of my petitions."}
              ]
        , 3:  [ {class: "q", text: "If you, Yah, kept a record of sins,"}
              , {class: "q2", text: "Lord, who could stand?"}
              ]
        , 4:  [ {class: "q", text: "But there is forgiveness with you,"}
              , {class: "q2", text: "therefore you are feared."}
              ]
        , 5:  [ {class: "q", text: "I wait for Yahweh."}
              , {class: "q2", text: "My soul waits."}
              , {class: "q2", text: "I hope in his word."}
              ]
        , 6:  [ {class: "q", text: "My soul longs for the Lord more than watchmen long for the morning,"}
              , {class: "q2", text: "more than watchmen for the morning."}
              ]
        , 7:  [ {class: "q", text: "Israel, hope in Yahweh,"}
              , {class: "q2", text: "for there is loving kindness with Yahweh."}
              , {class: "q2", text: "Abundant redemption is with him."}
              ]
        , 8:  [ {class: "q", text: "He will redeem Israel from all their sins."} ]
        }
  , 131:{ 1:  [ {class: "q", text: "Yahweh, my heart isn’t arrogant, nor my eyes lofty;"}
              , {class: "q2", text: "nor do I concern myself with great matters,"}
              , {class: "q2", text: "or things too wonderful for me."}
              ]
        , 2:  [ {class: "q", text: "Surely I have stilled and quieted my soul,"}
              , {class: "q2", text: "like a weaned child with his mother,"}
              , {class: "q2", text: "like a weaned child is my soul within me."}
              ]
        , 3:  [ {class: "q", text: "Israel, hope in Yahweh,"}
              , {class: "q2", text: "from this time forward and forever more."}
              ]
        }
  , 132:{ 1:  [ {class: "q", text: "Yahweh, remember David and all his affliction,"} ]
        , 2:  [ {class: "q", text: "how he swore to Yahweh,"}
              , {class: "q2", text: "and vowed to the Mighty One of Jacob:"}
              ]
        , 3:  [ {class: "q", text: "“Surely I will not come into the structure of my house,"}
              , {class: "q2", text: "nor go up into my bed;"}
              ]
        , 4:  [ {class: "q", text: "I will not give sleep to my eyes,"}
              , {class: "q2", text: "or slumber to my eyelids;"}
              ]
        , 5:  [ {class: "q", text: "until I find out a place for Yahweh,"}
              , {class: "q2", text: "a dwelling for the Mighty One of Jacob.”"}
              ]
        , 6:  [ {class: "q", text: "Behold, we heard of it in Ephrathah."}
              , {class: "q2", text: "We found it in the field of Jaar:"}
              ]
        , 7:  [ {class: "q", text: "“We will go into his dwelling place."}
              , {class: "q2", text: "We will worship at his footstool."}
              ]
        , 8:  [ {class: "q", text: "Arise, Yahweh, into your resting place,"}
              , {class: "q2", text: "you, and the ark of your strength."}
              ]
        , 9:  [ {class: "q", text: "Let your priests be clothed with righteousness."}
              , {class: "q2", text: "Let your saints shout for joy!”"}
              ]
        , 10: [ {class: "q", text: "For your servant David’s sake,"}
              , {class: "q2", text: "don’t turn away the face of your anointed one."}
              ]
        , 11: [ {class: "q", text: "Yahweh has sworn to David in truth."}
              , {class: "q2", text: "He will not turn from it:"}
              , {class: "q2", text: "“I will set the fruit of your body on your throne."}
              ]
        , 12: [ {class: "q", text: "If your children will keep my covenant,"}
              , {class: "q2", text: "my testimony that I will teach them,"}
              , {class: "q2", text: "their children also will sit on your throne forever more.”"}
              ]
        , 13: [ {class: "q", text: "For Yahweh has chosen Zion."}
              , {class: "q2", text: "He has desired it for his habitation."}
              ]
        , 14: [ {class: "q", text: "“This is my resting place forever."}
              , {class: "q2", text: "I will live here, for I have desired it."}
              ]
        , 15: [ {class: "q", text: "I will abundantly bless her provision."}
              , {class: "q2", text: "I will satisfy her poor with bread."}
              ]
        , 16: [ {class: "q", text: "I will also clothe her priests with salvation."}
              , {class: "q2", text: "Her saints will shout aloud for joy."}
              ]
        , 17: [ {class: "q", text: "I will make the horn of David to bud there."}
              , {class: "q2", text: "I have ordained a lamp for my anointed."}
              ]
        , 18: [ {class: "q", text: "I will clothe his enemies with shame,"}
              , {class: "q2", text: "but on himself, his crown will shine.”"}
              ]
        }
  , 133:{ 1:  [ {class: "q", text: "See how good and how pleasant it is"}
              , {class: "q2", text: "for brothers to live together in unity!"}
              ]
        , 2:  [ {class: "q", text: "It is like the precious oil on the head,"}
              ,  {class: "q2", text: "that ran down on the beard,"}
              , {class: "q2", text: "even Aaron’s beard,"}
              , {class: "q2", text: "that came down on the edge of his robes,"}
              ]
        , 3:  [ {class: "q", text: "like the dew of Hermon,"}
              , {class: "q2", text: "that comes down on the hills of Zion;"}
              , {class: "q2", text: "for there Yahweh gives the blessing,"}
              , {class: "q2", text: "even life forever more."}
              ]
        }
  , 134:{ 1:  [ {class: "q", text: "Look! Praise Yahweh, all you servants of Yahweh,"}
              , {class: "q2", text: "who stand by night in Yahweh’s house!"}
              ]
        , 2:  [ {class: "q", text: "Lift up your hands in the sanctuary."}
              , {class: "q2", text: "Praise Yahweh!"}
              ]
        , 3:  [ {class: "q", text: "May Yahweh bless you from Zion,"}
              , {class: "q2", text: "even he who made heaven and earth."}
              ]
        }
  , 135:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Praise Yahweh’s name!"}
              , {class: "q2", text: "Praise him, you servants of Yahweh,"}
              ]
        , 2:  [ {class: "q", text: "you who stand in Yahweh’s house,"}
              , {class: "q2", text: "in the courts of our God’s house."}
              ]
        , 3:  [ {class: "q", text: "Praise Yah, for Yahweh is good."}
              , {class: "q2", text: "Sing praises to his name, for that is pleasant."}
              ]
        , 4:  [ {class: "q", text: "For Yah has chosen Jacob for himself,"}
              , {class: "q2", text: "Israel for his own possession."}
              ]
        , 5:  [ {class: "q", text: "For I know that Yahweh is great,"}
              , {class: "q2", text: "that our Lord is above all gods."}
              ]
        , 6:  [ {class: "q", text: "Whatever Yahweh pleased, that he has done,"}
              , {class: "q2", text: "in heaven and in earth, in the seas and in all deeps."}
              ]
        , 7:  [ {class: "q", text: "He causes the clouds to rise from the ends of the earth."}
              , {class: "q2", text: "He makes lightnings with the rain."}
              , {class: "q2", text: "He brings the wind out of his treasuries."}
              ]
        , 8:  [ {class: "q", text: "He struck the firstborn of Egypt,"}
              , {class: "q2", text: "both of man and animal."}
              ]
        , 9:  [ {class: "q", text: "He sent signs and wonders into the middle of you, Egypt,"}
              , {class: "q2", text: "on Pharaoh, and on all his servants."}
              ]
        , 10: [ {class: "q", text: "He struck many nations,"}
              , {class: "q2", text: "and killed mighty kings,"}
              ]
        , 11: [ {class: "q", text: "Sihon king of the Amorites,"}
              , {class: "q2", text: "Og king of Bashan,"}
              , {class: "q2", text: "and all the kingdoms of Canaan,"}
              ]
        , 12: [ {class: "q", text: "and gave their land for a heritage,"}
              , {class: "q2", text: "a heritage to Israel, his people."}
              ]
        , 13: [ {class: "q", text: "Your name, Yahweh, endures forever;"}
              , {class: "q2", text: "your renown, Yahweh, throughout all generations."}
              ]
        , 14: [ {class: "q", text: "For Yahweh will judge his people"}
              , {class: "q2", text: "and have compassion on his servants."}
              ]
        , 15: [ {class: "q", text: "The idols of the nations are silver and gold,"}
              , {class: "q2", text: "the work of men’s hands."}
              ]
        , 16: [ {class: "q", text: "They have mouths, but they can’t speak."}
              , {class: "q2", text: "They have eyes, but they can’t see."}
              ]
        , 17: [ {class: "q", text: "They have ears, but they can’t hear,"}
              , {class: "q2", text: "neither is there any breath in their mouths."}
              ]
        , 18: [ {class: "q", text: "Those who make them will be like them,"}
              , {class: "q2", text: "yes, everyone who trusts in them."}
              ]
        , 19: [ {class: "q", text: "House of Israel, praise Yahweh!"}
              , {class: "q2", text: "House of Aaron, praise Yahweh!"}
              ]
        , 20: [ {class: "q", text: "House of Levi, praise Yahweh!"}
              , {class: "q2", text: "You who fear Yahweh, praise Yahweh!"}
              ]
        , 21: [ {class: "q", text: "Blessed be Yahweh from Zion,"}
              , {class: "q2", text: "who dwells at Jerusalem."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 136:{ 1:  [ {class: "q", text: "Give thanks to Yahweh, for he is good;"}
              , {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 2:  [ {class: "q", text: "Give thanks to the God of gods;"}
              ,  {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 3:  [ {class: "q", text: "Give thanks to the Lord of lords;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 4:  [ {class: "q", text: "to him who alone does great wonders;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 5:  [ {class: "q", text: "to him who by understanding made the heavens;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 6:  [ {class: "q", text: "to him who spread out the earth above the waters;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 7:  [ {class: "q", text: "to him who made the great lights;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 8:  [ {class: "q", text: "the sun to rule by day;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 9:  [ {class: "q", text: "the moon and stars to rule by night;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 10: [ {class: "q", text: "to him who struck down the Egyptian firstborn;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 11: [ {class: "q", text: "and brought out Israel from among them;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 12: [ {class: "q", text: "with a strong hand, and with an outstretched arm;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 13: [ {class: "q", text: "to him who divided the Red Sea apart;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 14: [ {class: "q", text: "and made Israel to pass through the middle of it;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 15: [ {class: "q", text: "but overthrew Pharaoh and his army in the Red Sea;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 16: [ {class: "q", text: "to him who led his people through the wilderness;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 17: [ {class: "q", text: "to him who struck great kings;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 18: [ {class: "q", text: "and killed mighty kings;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 19: [ {class: "q", text: "Sihon king of the Amorites;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 20: [ {class: "q", text: "Og king of Bashan;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 21: [ {class: "q", text: "and gave their land as an inheritance;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 22: [ {class: "q", text: "even a heritage to Israel his servant;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 23: [ {class: "q", text: "who remembered us in our low estate;"}
              ,  {class: "q2", text: "for his loving kindness endures forever;"}
              ]
        , 24: [ {class: "q", text: "and has delivered us from our adversaries;"}
              ,  {class: "q2", text: "for his loving kindness endures forever:"}
              ]
        , 25: [ {class: "q", text: "who gives food to every creature;"}
              ,  {class: "q2", text: "for his loving kindness endures forever."}
              ]
        , 26: [ {class: "q", text: "Oh give thanks to the God of heaven;"}
              ,  {class: "q2", text: "for his loving kindness endures forever."}
              ]
        }
  , 137:{ 1:  [ {class: "q", text: "By the rivers of Babylon, there we sat down."}
              , {class: "q2", text: "Yes, we wept, when we remembered Zion."}
              ]
        , 2:  [ {class: "q", text: "On the willows in that land,"}
              , {class: "q2", text: "we hung up our harps."}
              ]
        , 3:  [ {class: "q", text: "For there, those who led us captive asked us for songs."}
              , {class: "q2", text: "Those who tormented us demanded songs of joy:"}
              , {class: "q2", text: "“Sing us one of the songs of Zion!”"}
              ]
        , 4:  [ {class: "q", text: "How can we sing Yahweh’s song in a foreign land?"} ]
        , 5:  [ {class: "q", text: "If I forget you, Jerusalem,"}
              , {class: "q2", text: "let my right hand forget its skill."}
              ]
        , 6:  [ {class: "q", text: "Let my tongue stick to the roof of my mouth if I don’t remember you,"}
              , {class: "q2", text: "if I don’t prefer Jerusalem above my chief joy."}
              ]
        , 7:  [ {class: "q", text: "Remember, Yahweh, against the children of Edom in the day of Jerusalem,"}
              ,  {class: "q2", text: "who said, “Raze it!"}
              , {class: "q2", text: "Raze it even to its foundation!”"}
              ]
        , 8:  [ {class: "q", text: "Daughter of Babylon, doomed to destruction,"}
              , {class: "q2", text: "he will be happy who repays you,"}
              , {class: "q2", text: "as you have done to us."}
              ]
        , 9:  [ {class: "q", text: "Happy shall he be,"}
              , {class: "q2", text: "who takes and dashes your little ones against the rock."}
              ]
        }
  , 138:{ 1:  [ {class: "q", text: "I will give you thanks with my whole heart."}
              , {class: "q2", text: "Before the gods, I will sing praises to you."}
              ]
        , 2:  [ {class: "q", text: "I will bow down toward your holy temple,"}
              , {class: "q2", text: "and give thanks to your Name for your loving kindness and for your truth;"}
              , {class: "q2", text: "for you have exalted your Name and your Word above all."}
              ]
        , 3:  [ {class: "q", text: "In the day that I called, you answered me."}
              ,  {class: "q2", text: "You encouraged me with strength in my soul."}
              ]
        , 4:  [ {class: "q", text: "All the kings of the earth will give you thanks, Yahweh,"}
              , {class: "q2", text: "for they have heard the words of your mouth."}
              ]
        , 5:  [ {class: "q", text: "Yes, they will sing of the ways of Yahweh,"}
              ,  {class: "q2", text: "for Yahweh’s glory is great!"}
              ]
        , 6:  [ {class: "q", text: "For though Yahweh is high, yet he looks after the lowly;"}
              ,  {class: "q2", text: "but he knows the proud from afar."}
              ]
        , 7:  [ {class: "q", text: "Though I walk in the middle of trouble, you will revive me."}
              , {class: "q2", text: "You will stretch out your hand against the wrath of my enemies."}
              , {class: "q2", text: "Your right hand will save me."}
              ]
        , 8:  [ {class: "q", text: "Yahweh will fulfill that which concerns me."}
              , {class: "q2", text: "Your loving kindness, Yahweh, endures forever."}
              , {class: "q2", text: "Don’t forsake the works of your own hands."}
              ]
        }
  , 139:{ 1:  [ {class: "q", text: "Yahweh, you have searched me,"}
              , {class: "q2", text: "and you know me."}
              ]
        , 2:  [ {class: "q", text: "You know my sitting down and my rising up."}
              , {class: "q2", text: "You perceive my thoughts from afar."}
              ]
        , 3:  [ {class: "q", text: "You search out my path and my lying down,"}
              , {class: "q2", text: "and are acquainted with all my ways."}
              ]
        , 4:  [ {class: "q", text: "For there is not a word on my tongue,"}
              , {class: "q2", text: "but, behold, Yahweh, you know it altogether."}
              ]
        , 5:  [ {class: "q", text: "You hem me in behind and before."}
              , {class: "q2", text: "You laid your hand on me."}
              ]
        , 6:  [ {class: "q", text: "This knowledge is beyond me."}
              , {class: "q2", text: "It’s lofty."}
              , {class: "q2", text: "I can’t attain it."}
              ]
        , 7:  [ {class: "q", text: "Where could I go from your Spirit?"}
              , {class: "q2", text: "Or where could I flee from your presence?"}
              ]
        , 8:  [ {class: "q", text: "If I ascend up into heaven, you are there."}
              , {class: "q2", text: "If I make my bed in Sheol, behold, you are there!"}
              ]
        , 9:  [ {class: "q", text: "If I take the wings of the dawn,"}
              , {class: "q2", text: "and settle in the uttermost parts of the sea,"}
              ]
        , 10: [ {class: "q", text: "even there your hand will lead me,"}
              , {class: "q2", text: "and your right hand will hold me."}
              ]
        , 11: [ {class: "q", text: "If I say, “Surely the darkness will overwhelm me."}
              , {class: "q2", text: "The light around me will be night,”"}
              ]
        , 12: [ {class: "q", text: "even the darkness doesn’t hide from you,"}
              , {class: "q2", text: "but the night shines as the day."}
              , {class: "q2", text: "The darkness is like light to you."}
              ]
        , 13: [ {class: "q", text: "For you formed my inmost being."}
              , {class: "q2", text: "You knit me together in my mother’s womb."}
              ]
        , 14: [ {class: "q", text: "I will give thanks to you,"}
              , {class: "q2", text: "for I am fearfully and wonderfully made."}
              , {class: "q", text: "Your works are wonderful."}
              , {class: "q2", text: "My soul knows that very well."}
              ]
        , 15: [ {class: "q", text: "My frame wasn’t hidden from you,"}
              , {class: "q2", text: "when I was made in secret,"}
              , {class: "q2", text: "woven together in the depths of the earth."}
              ]
        , 16: [ {class: "q", text: "Your eyes saw my body."}
              , {class: "q2", text: "In your book they were all written,"}
              , {class: "q2", text: "the days that were ordained for me,"}
              , {class: "q2", text: "when as yet there were none of them."}
              ]
        , 17: [ {class: "q", text: "How precious to me are your thoughts, God!"}
              , {class: "q2", text: "How vast is their sum!"}
              ]
        , 18: [ {class: "q", text: "If I would count them, they are more in number than the sand."}
              , {class: "q2", text: "When I wake up, I am still with you."}
              ]
        , 19: [ {class: "q", text: "If only you, God, would kill the wicked."}
              , {class: "q2", text: "Get away from me, you bloodthirsty men!"}
              ]
        , 20: [ {class: "q", text: "For they speak against you wickedly."}
              , {class: "q2", text: "Your enemies take your name in vain."}
              ]
        , 21: [ {class: "q", text: "Yahweh, don’t I hate those who hate you?"}
              , {class: "q2", text: "Am I not grieved with those who rise up against you?"}
              ]
        , 22: [ {class: "q", text: "I hate them with perfect hatred."}
              , {class: "q2", text: "They have become my enemies."}
              ]
        , 23: [ {class: "q", text: "Search me, God, and know my heart."}
              , {class: "q2", text: "Try me, and know my thoughts."}
              ]
        , 24: [ {class: "q", text: "See if there is any wicked way in me,"}
              , {class: "q2", text: "and lead me in the everlasting way."}
              ]
        }
  , 14: { 1:  [ {class: "q", text: "The fool has said in his heart, “There is no God.”"}
              , {class: "q2", text: "They are corrupt."}
              , {class: "q2", text: "They have done abominable deeds."}
              , {class: "q2", text: "There is no one who does good."}
              ]
        , 2:  [ {class: "q", text: "Yahweh looked down from heaven on the children of men,"}
              , {class: "q2", text: "to see if there were any who understood,"}
              , {class: "q2", text: "who sought after God."}
              ]
        , 3:  [ {class: "q", text: "They have all gone aside."}
              , {class: "q2", text: "They have together become corrupt."}
              , {class: "q2", text: "There is no one who does good, no, not one."}
              ]
        , 4:  [ {class: "q", text: "Have all the workers of iniquity no knowledge,"}
              , {class: "q2", text: "who eat up my people as they eat bread,"}
              , {class: "q2", text: "and don’t call on Yahweh?"}
              ]
        , 5:  [ {class: "q", text: "There they were in great fear,"}
              , {class: "q2", text: "for God is in the generation of the righteous."}
              ]
        , 6:  [ {class: "q", text: "You frustrate the plan of the poor,"}
              ,  {class: "q2", text: "because Yahweh is his refuge."}
              ]
        , 7:  [ {class: "q", text: "Oh that the salvation of Israel would come out of Zion!"}
              , {class: "q2", text: "When Yahweh restores the fortunes of his people,"}
              , {class: "q2", text: "then Jacob shall rejoice, and Israel shall be glad."}
              ]
        }
  , 140:{ 1:  [ {class: "q", text: "Deliver me, Yahweh, from the evil man."}
              , {class: "q2", text: "Preserve me from the violent man:"}
              ]
        , 2:  [ {class: "q", text: "those who devise mischief in their hearts."}
              , {class: "q2", text: "They continually gather themselves together for war."}
              ]
        , 3:  [ {class: "q", text: "They have sharpened their tongues like a serpent."}
              , {class: "q2", text: "Viper’s poison is under their lips."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "Yahweh, keep me from the hands of the wicked."}
              , {class: "q2", text: "Preserve me from the violent men who have determined to trip my feet."}
              ]
        , 5:  [ {class: "q", text: "The proud have hidden a snare for me,"}
              , {class: "q2", text: "they have spread the cords of a net by the path."}
              , {class: "q2", text: "They have set traps for me."}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "I said to Yahweh, “You are my God.”"}
              , {class: "q2", text: "Listen to the cry of my petitions, Yahweh."}
              ]
        , 7:  [ {class: "q", text: "Yahweh, the Lord, the strength of my salvation,"}
              , {class: "q2", text: "you have covered my head in the day of battle."}
              ]
        , 8:  [ {class: "q", text: "Yahweh, don’t grant the desires of the wicked."}
              , {class: "q2", text: "Don’t let their evil plans succeed, or they will become proud."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "As for the head of those who surround me,"}
              , {class: "q2", text: "let the mischief of their own lips cover them."}
              ]
        , 10: [ {class: "q", text: "Let burning coals fall on them."}
              , {class: "q2", text: "Let them be thrown into the fire,"}
              , {class: "q2", text: "into miry pits, from where they never rise."}
              ]
        , 11: [ {class: "q", text: "An evil speaker won’t be established in the earth."}
              , {class: "q2", text: "Evil will hunt the violent man to overthrow him."}
              ]
        , 12: [ {class: "q", text: "I know that Yahweh will maintain the cause of the afflicted,"}
              , {class: "q2", text: "and justice for the needy."}
              ]
        , 13: [ {class: "q", text: "Surely the righteous will give thanks to your name."}
              , {class: "q2", text: "The upright will dwell in your presence."}
              ]
        }
  , 141:{ 1:  [ {class: "q", text: "Yahweh, I have called on you."}
              , {class: "q2", text: "Come to me quickly!"}
              , {class: "q2", text: "Listen to my voice when I call to you."}
              ]
        , 2:  [ {class: "q", text: "Let my prayer be set before you like incense;"}
              , {class: "q2", text: "the lifting up of my hands like the evening sacrifice."}
              ]
        , 3:  [ {class: "q", text: "Set a watch, Yahweh, before my mouth."}
              , {class: "q2", text: "Keep the door of my lips."}
              ]
        , 4:  [ {class: "q", text: "Don’t incline my heart to any evil thing,"}
              , {class: "q2", text: "to practice deeds of wickedness with men who work iniquity."}
              , {class: "q2", text: "Don’t let me eat of their delicacies."}
              ]
        , 5:  [ {class: "q", text: "Let the righteous strike me, it is kindness;"}
              , {class: "q2", text: "let him reprove me, it is like oil on the head;"}
              , {class: "q2", text: "don’t let my head refuse it;"}
              , {class: "q2", text: "Yet my prayer is always against evil deeds."}
              ]
        , 6:  [ {class: "q", text: "Their judges are thrown down by the sides of the rock."}
              , {class: "q2", text: "They will hear my words, for they are well spoken."}
              ]
        , 7:  [ {class: "q", text: "“As when one plows and breaks up the earth,"}
              , {class: "q2", text: "our bones are scattered at the mouth of Sheol.”"}
              ]
        , 8:  [ {class: "q", text: "For my eyes are on you, Yahweh, the Lord."}
              , {class: "q2", text: "In you, I take refuge."}
              , {class: "q2", text: "Don’t leave my soul destitute."}
              ]
        , 9:  [ {class: "q", text: "Keep me from the snare which they have laid for me,"}
              , {class: "q2", text: "from the traps of the workers of iniquity."}
              ]
        , 10: [ {class: "q", text: "Let the wicked fall together into their own nets"}
              , {class: "q2", text: "while I pass by."}
              ]
        }
  , 142:{ 1:  [ {class: "q", text: "I cry with my voice to Yahweh."}
              , {class: "q2", text: "With my voice, I ask Yahweh for mercy."}
              ]
        , 2:  [ {class: "q", text: "I pour out my complaint before him."}
              , {class: "q2", text: "I tell him my troubles."}
              ]
        , 3:  [ {class: "q", text: "When my spirit was overwhelmed within me,"}
              , {class: "q2", text: "you knew my route."}
              , {class: "q", text: "On the path in which I walk,"}
              , {class: "q2", text: "they have hidden a snare for me."}
              ]
        , 4:  [ {class: "q", text: "Look on my right, and see;"}
              , {class: "q2", text: "for there is no one who is concerned for me."}
              , {class: "q2", text: "Refuge has fled from me."}
              ,  {class: "q2", text: "No one cares for my soul."}
              ]
        , 5:  [ {class: "q", text: "I cried to you, Yahweh."}
              , {class: "q2", text: "I said, “You are my refuge,"}
              , {class: "q2", text: "my portion in the land of the living.”"}
              ]
        , 6:  [ {class: "q", text: "Listen to my cry,"}
              , {class: "q2", text: "for I am in desperate need."}
              , {class: "q", text: "Deliver me from my persecutors,"}
              , {class: "q2", text: "for they are too strong for me."}
              ]
        , 7:  [ {class: "q", text: "Bring my soul out of prison,"}
              ,  {class: "q2", text: "that I may give thanks to your name."}
              , {class: "q", text: "The righteous will surround me,"}
              , {class: "q2", text: "for you will be good to me."}
              ]
        }
  , 143:{ 1:  [ {class: "q", text: "Hear my prayer, Yahweh."}
              , {class: "q2", text: "Listen to my petitions."}
              , {class: "q2", text: "In your faithfulness and righteousness, relieve me."}
              ]
        , 2:  [ {class: "q", text: "Don’t enter into judgment with your servant,"}
              , {class: "q2", text: "for in your sight no man living is righteous."}
              ]
        , 3:  [ {class: "q", text: "For the enemy pursues my soul."}
              ,  {class: "q2", text: "He has struck my life down to the ground."}
              , {class: "q2", text: "He has made me live in dark places, as those who have been long dead."}
              ]
        , 4:  [ {class: "q", text: "Therefore my spirit is overwhelmed within me."}
              , {class: "q2", text: "My heart within me is desolate."}
              ]
        , 5:  [ {class: "q", text: "I remember the days of old."}
              , {class: "q2", text: "I meditate on all your doings."}
              , {class: "q2", text: "I contemplate the work of your hands."}
              ]
        , 6:  [ {class: "q", text: "I spread out my hands to you."}
              , {class: "q2", text: "My soul thirsts for you, like a parched land."}
              , {class: "qs", text: "Selah"}
              ]
        , 7:  [ {class: "q", text: "Hurry to answer me, Yahweh."}
              , {class: "q2", text: "My spirit fails."}
              , {class: "q", text: "Don’t hide your face from me,"}
              , {class: "q2", text: "so that I don’t become like those who go down into the pit."}
              ]
        , 8:  [ {class: "q", text: "Cause me to hear your loving kindness in the morning,"}
              , {class: "q2", text: "for I trust in you."}
              , {class: "q", text: "Cause me to know the way in which I should walk,"}
              , {class: "q2", text: "for I lift up my soul to you."}
              ]
        , 9:  [ {class: "q", text: "Deliver me, Yahweh, from my enemies."}
              ,  {class: "q2", text: "I flee to you to hide me."}
              ]
        , 10: [ {class: "q", text: "Teach me to do your will,"}
              , {class: "q2", text: "for you are my God."}
              , {class: "q", text: "Your Spirit is good."}
              , {class: "q2", text: "Lead me in the land of uprightness."}
              ]
        , 11: [ {class: "q", text: "Revive me, Yahweh, for your name’s sake."}
              , {class: "q2", text: "In your righteousness, bring my soul out of trouble."}
              ]
        , 12: [ {class: "q", text: "In your loving kindness, cut off my enemies,"}
              , {class: "q2", text: "and destroy all those who afflict my soul,"}
              , {class: "q2", text: "For I am your servant."}
              ]
        }
  , 144:{ 1:  [ {class: "q", text: "Blessed be Yahweh, my rock,"}
              , {class: "q2", text: "who teaches my hands to war,"}
              , {class: "q2", text: "and my fingers to battle:"}
              ]
        , 2:  [ {class: "q", text: "my loving kindness, my fortress,"}
              , {class: "q2", text: "my high tower, my deliverer,"}
              , {class: "q2", text: "my shield, and he in whom I take refuge,"}
              , {class: "q2", text: "who subdues my people under me."}
              ]
        , 3:  [ {class: "q", text: "Yahweh, what is man, that you care for him?"}
              , {class: "q2", text: "Or the son of man, that you think of him?"}
              ]
        , 4:  [ {class: "q", text: "Man is like a breath."}
              , {class: "q2", text: "His days are like a shadow that passes away."}
              ]
        , 5:  [ {class: "q", text: "Part your heavens, Yahweh, and come down."}
              , {class: "q2", text: "Touch the mountains, and they will smoke."}
              ]
        , 6:  [ {class: "q", text: "Throw out lightning, and scatter them."}
              , {class: "q2", text: "Send out your arrows, and rout them."}
              ]
        , 7:  [ {class: "q", text: "Stretch out your hand from above,"}
              , {class: "q2", text: "rescue me, and deliver me out of great waters,"}
              , {class: "q2", text: "out of the hands of foreigners,"}
              ]
        , 8:  [ {class: "q2", text: "/span> whose mouths speak deceit,"}
              , {class: "q2", text: "whose right hand is a right hand of falsehood."}
              ]
        , 9:  [ {class: "q", text: "I will sing a new song to you, God."}
              , {class: "q2", text: "On a ten-stringed lyre, I will sing praises to you."}
              ]
        , 10: [ {class: "q", text: "You are he who gives salvation to kings,"}
              , {class: "q2", text: "who rescues David, his servant, from the deadly sword."}
              ]
        , 11: [ {class: "q", text: "Rescue me, and deliver me out of the hands of foreigners,"}
              , {class: "q2", text: "whose mouths speak deceit,"}
              , {class: "q2", text: "whose right hand is a right hand of falsehood."}
              ]
        , 12: [ {class: "q", text: "Then our sons will be like well-nurtured plants,"}
              , {class: "q2", text: "our daughters like pillars carved to adorn a palace."}
              ]
        , 13: [ {class: "q", text: "Our barns are full, filled with all kinds of provision."}
              , {class: "q2", text: "Our sheep produce thousands and ten thousands in our fields."}
              ]
        , 14: [ {class: "q", text: "Our oxen will pull heavy loads."}
              , {class: "q2", text: "There is no breaking in, and no going away,"}
              , {class: "q2", text: "and no outcry in our streets."}
              ]
        , 15: [ {class: "q", text: "Happy are the people who are in such a situation."}
              , {class: "q2", text: "Happy are the people whose God is Yahweh."}
              ]
        }
  , 145:{ 1:  [ {class: "q", text: "I will exalt you, my God, the King."}
              , {class: "q2", text: "I will praise your name forever and ever."}
              ]
        , 2:  [ {class: "q", text: "Every day I will praise you."}
              , {class: "q2", text: "I will extol your name forever and ever."}
              ]
        , 3:  [ {class: "q", text: "Great is Yahweh, and greatly to be praised!"}
              , {class: "q2", text: "His greatness is unsearchable."}
              ]
        , 4:  [ {class: "q", text: "One generation will commend your works to another,"}
              , {class: "q2", text: "and will declare your mighty acts."}
              ]
        , 5:  [ {class: "q", text: "I will meditate on the glorious majesty of your honor,"}
              , {class: "q2", text: "on your wondrous works."}
              ]
        , 6:  [ {class: "q", text: "Men will speak of the might of your awesome acts."}
              , {class: "q2", text: "I will declare your greatness."}
              ]
        , 7:  [ {class: "q", text: "They will utter the memory of your great goodness,"}
              , {class: "q2", text: "and will sing of your righteousness."}
              ]
        , 8:  [ {class: "q", text: "Yahweh is gracious, merciful,"}
              , {class: "q2", text: "slow to anger, and of great loving kindness."}
              ]
        , 9:  [ {class: "q", text: "Yahweh is good to all."}
              , {class: "q2", text: "His tender mercies are over all his works."}
              ]
        , 10: [ {class: "q", text: "All your works will give thanks to you, Yahweh."}
              , {class: "q2", text: "Your saints will extol you."}
              ]
        , 11: [ {class: "q", text: "They will speak of the glory of your kingdom,"}
              , {class: "q2", text: "and talk about your power,"}
              ]
        , 12: [ {class: "q", text: "to make known to the sons of men his mighty acts,"}
              , {class: "q2", text: "the glory of the majesty of his kingdom."}
              ]
        , 13: [ {class: "q", text: "Your kingdom is an everlasting kingdom."}
              , {class: "q2", text: "Your dominion endures throughout all generations."}
              , {class: "q", text: "Yahweh is faithful in all his words,"}
              , {class: "q2", text: "and loving in all his deeds."}
              ]
        , 14: [ {class: "q", text: "Yahweh upholds all who fall,"}
              , {class: "q2", text: "and raises up all those who are bowed down."}
              ]
        , 15: [ {class: "q", text: "The eyes of all wait for you."}
              , {class: "q2", text: "You give them their food in due season."}
              ]
        , 16: [ {class: "q", text: "You open your hand,"}
              , {class: "q2", text: "and satisfy the desire of every living thing."}
              ]
        , 17: [ {class: "q", text: "Yahweh is righteous in all his ways,"}
              , {class: "q2", text: "and gracious in all his works."}
              ]
        , 18: [ {class: "q", text: "Yahweh is near to all those who call on him,"}
              , {class: "q2", text: "to all who call on him in truth."}
              ]
        , 19: [ {class: "q", text: "He will fulfill the desire of those who fear him."}
              , {class: "q2", text: "He also will hear their cry, and will save them."}
              ]
        , 20: [ {class: "q", text: "Yahweh preserves all those who love him,"}
              , {class: "q2", text: "but he will destroy all the wicked."}
              ]
        , 21: [ {class: "q", text: "My mouth will speak the praise of Yahweh."}
              , {class: "q2", text: "Let all flesh bless his holy name forever and ever."}
              ]
        }
  , 146:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Praise Yahweh, my soul."}
              ]
        , 2:  [ {class: "q", text: "While I live, I will praise Yahweh."}
              , {class: "q2", text: "I will sing praises to my God as long as I exist."}
              ]
        , 3:  [ {class: "q", text: "Don’t put your trust in princes,"}
              , {class: "q2", text: "each a son of man in whom there is no help."}
              ]
        , 4:  [ {class: "q", text: "His spirit departs, and he returns to the earth."}
              , {class: "q2", text: "In that very day, his thoughts perish."}
              ]
        , 5:  [ {class: "q", text: "Happy is he who has the God of Jacob for his help,"}
              , {class: "q2", text: "whose hope is in Yahweh, his God:"}
              ]
        , 6:  [ {class: "q", text: "who made heaven and earth,"}
              , {class: "q2", text: "the sea, and all that is in them;"}
              , {class: "q2", text: "who keeps truth forever;"}
              ]
        , 7:  [ {class: "q", text: "who executes justice for the oppressed;"}
              , {class: "q2", text: "who gives food to the hungry."}
              , {class: "q", text: "Yahweh frees the prisoners."}
              ]
        , 8:  [ {class: "q2", text: "/span> Yahweh opens the eyes of the blind."}
              , {class: "q2", text: "Yahweh raises up those who are bowed down."}
              , {class: "q2", text: "Yahweh loves the righteous."}
              ]
        , 9:  [ {class: "q", text: "Yahweh preserves the foreigners."}
              , {class: "q2", text: "He upholds the fatherless and widow,"}
              , {class: "q2", text: "but he turns the way of the wicked upside down."}
              ]
        , 10: [ {class: "q", text: "Yahweh will reign forever;"}
              , {class: "q2", text: "your God, O Zion, to all generations."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 147:{ 1:  [ {class: "q", text: "Praise Yah,"}
              , {class: "q2", text: "for it is good to sing praises to our God;"}
              , {class: "q2", text: "for it is pleasant and fitting to praise him."}
              ]
        , 2:  [ {class: "q", text: "Yahweh builds up Jerusalem."}
              ,  {class: "q2", text: "He gathers together the outcasts of Israel."}
              ]
        , 3:  [ {class: "q", text: "He heals the broken in heart,"}
              , {class: "q2", text: "and binds up their wounds."}
              ]
        , 4:  [ {class: "q", text: "He counts the number of the stars."}
              , {class: "q2", text: "He calls them all by their names."}
              ]
        , 5:  [ {class: "q", text: "Great is our Lord, and mighty in power."}
              , {class: "q2", text: "His understanding is infinite."}
              ]
        , 6:  [ {class: "q", text: "Yahweh upholds the humble."}
              , {class: "q2", text: "He brings the wicked down to the ground."}
              ]
        , 7:  [ {class: "q", text: "Sing to Yahweh with thanksgiving."}
              , {class: "q2", text: "Sing praises on the harp to our God,"}
              ]
        , 8:  [ {class: "q", text: "who covers the sky with clouds,"}
              , {class: "q2", text: "who prepares rain for the earth,"}
              , {class: "q2", text: "who makes grass grow on the mountains."}
              ]
        , 9:  [ {class: "q", text: "He provides food for the livestock,"}
              , {class: "q2", text: "and for the young ravens when they call."}
              ]
        , 10: [ {class: "q", text: "He doesn’t delight in the strength of the horse."}
              , {class: "q2", text: "He takes no pleasure in the legs of a man."}
              ]
        , 11: [ {class: "q", text: "Yahweh takes pleasure in those who fear him,"}
              , {class: "q2", text: "in those who hope in his loving kindness."}
              ]
        , 12: [ {class: "q", text: "Praise Yahweh, Jerusalem!"}
              , {class: "q2", text: "Praise your God, Zion!"}
              ]
        , 13: [ {class: "q", text: "For he has strengthened the bars of your gates."}
              , {class: "q2", text: "He has blessed your children within you."}
              ]
        , 14: [ {class: "q", text: "He makes peace in your borders."}
              , {class: "q2", text: "He fills you with the finest of the wheat."}
              ]
        , 15: [ {class: "q", text: "He sends out his commandment to the earth."}
              , {class: "q2", text: "His word runs very swiftly."}
              ]
        , 16: [ {class: "q", text: "He gives snow like wool,"}
              , {class: "q2", text: "and scatters frost like ashes."}
              ]
        , 17: [ {class: "q", text: "He hurls down his hail like pebbles."}
              , {class: "q2", text: "Who can stand before his cold?"}
              ]
        , 18: [ {class: "q", text: "He sends out his word, and melts them."}
              , {class: "q2", text: "He causes his wind to blow, and the waters flow."}
              ]
        , 19: [ {class: "q", text: "He shows his word to Jacob,"}
              , {class: "q2", text: "his statutes and his ordinances to Israel."}
              ]
        , 20: [ {class: "q", text: "He has not done this for just any nation."}
              , {class: "q2", text: "They don’t know his ordinances."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 148:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Praise Yahweh from the heavens!"}
              , {class: "q2", text: "Praise him in the heights!"}
              ]
        , 2:  [ {class: "q", text: "Praise him, all his angels!"}
              , {class: "q2", text: "Praise him, all his army!"}
              ]
        , 3:  [ {class: "q", text: "Praise him, sun and moon!"}
              , {class: "q2", text: "Praise him, all you shining stars!"}
              ]
        , 4:  [ {class: "q", text: "Praise him, you heavens of heavens,"}
              , {class: "q2", text: "you waters that are above the heavens."}
              ]
        , 5:  [ {class: "q", text: "Let them praise Yahweh’s name,"}
              , {class: "q2", text: "for he commanded, and they were created."}
              ]
        , 6:  [ {class: "q", text: "He has also established them forever and ever."}
              ,  {class: "q2", text: "He has made a decree which will not pass away."}
              ]
        , 7:  [ {class: "q", text: "Praise Yahweh from the earth,"}
              , {class: "q2", text: "you great sea creatures, and all depths;"}
              ]
        , 8:  [ {class: "q", text: "lightning and hail, snow and clouds;"}
              , {class: "q2", text: "stormy wind, fulfilling his word;"}
              ]
        , 9:  [ {class: "q", text: "mountains and all hills;"}
              , {class: "q2", text: "fruit trees and all cedars;"}
              ]
        , 10: [ {class: "q", text: "wild animals and all livestock;"}
              , {class: "q2", text: "small creatures and flying birds;"}
              ]
        , 11: [ {class: "q", text: "kings of the earth and all peoples;"}
              , {class: "q2", text: "princes and all judges of the earth;"}
              ]
        , 12: [ {class: "q", text: "both young men and maidens;"}
              , {class: "q2", text: "old men and children:"}
              ]
        , 13: [ {class: "q", text: "let them praise Yahweh’s name,"}
              , {class: "q2", text: "for his name alone is exalted."}
              , {class: "q2", text: "His glory is above the earth and the heavens."}
              ]
        , 14: [ {class: "q", text: "He has lifted up the horn of his people,"}
              , {class: "q2", text: "the praise of all his saints,"}
              , {class: "q2", text: "even of the children of Israel, a people near to him."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 149:{ 1:  [ {class: "q", text: "Praise Yahweh!"}
              , {class: "q2", text: "Sing to Yahweh a new song,"}
              , {class: "q2", text: "his praise in the assembly of the saints."}
              ]
        , 2:  [ {class: "q", text: "Let Israel rejoice in him who made them."}
              , {class: "q2", text: "Let the children of Zion be joyful in their King."}
              ]
        , 3:  [ {class: "q", text: "Let them praise his name in the dance!"}
              , {class: "q2", text: "Let them sing praises to him with tambourine and harp!"}
              ]
        , 4:  [ {class: "q", text: "For Yahweh takes pleasure in his people."}
              , {class: "q2", text: "He crowns the humble with salvation."}
              ]
        , 5:  [ {class: "q", text: "Let the saints rejoice in honor."}
              , {class: "q2", text: "Let them sing for joy on their beds."}
              ]
        , 6:  [ {class: "q", text: "May the high praises of God be in their mouths,"}
              , {class: "q2", text: "and a two-edged sword in their hand,"}
              ]
        , 7:  [ {class: "q", text: "to execute vengeance on the nations,"}
              , {class: "q2", text: "and punishments on the peoples;"}
              ]
        , 8:  [ {class: "q", text: "to bind their kings with chains,"}
              , {class: "q2", text: "and their nobles with fetters of iron;"}
              ]
        , 9:  [ {class: "q", text: "to execute on them the written judgment."}
              , {class: "q2", text: "All his saints have this honor."}
              , {class: "q", text: "Praise Yah!"}
              ]
        }
  , 15: { 1:  [ {class: "q", text: "Yahweh, who shall dwell in your sanctuary?"}
              , {class: "q2", text: "Who shall live on your holy hill?"}
              ]
        , 2:  [ {class: "q", text: "He who walks blamelessly and does what is right,"}
              , {class: "q2", text: "and speaks truth in his heart;"}
              ]
        , 3:  [ {class: "q", text: "he who doesn’t slander with his tongue,"}
              , {class: "q2", text: "nor does evil to his friend,"}
              , {class: "q2", text: "nor casts slurs against his fellow man;"}
              ]
        , 4:  [ {class: "q", text: "in whose eyes a vile man is despised,"}
              , {class: "q2", text: "but who honors those who fear Yahweh;"}
              , {class: "q2", text: "he who keeps an oath even when it hurts, and doesn’t change;"}
              ]
        , 5:  [ {class: "q", text: "he who doesn’t lend out his money for usury,"}
              , {class: "q2", text: "nor take a bribe against the innocent."}
              , {class: "q2", text: "He who does these things shall never be shaken."}
              ]
        }
  , 150:{ 1:  [ {class: "q", text: "Praise Yah!"}
              , {class: "q2", text: "Praise God in his sanctuary!"}
              , {class: "q2", text: "Praise him in his heavens for his acts of power!"}
              ]
        , 2:  [ {class: "q", text: "Praise him for his mighty acts!"}
              , {class: "q2", text: "Praise him according to his excellent greatness!"}
              ]
        , 3:  [ {class: "q", text: "Praise him with the sounding of the trumpet!"}
              , {class: "q2", text: "Praise him with harp and lyre!"}
              ]
        , 4:  [ {class: "q", text: "Praise him with tambourine and dancing!"}
              , {class: "q2", text: "Praise him with stringed instruments and flute!"}
              ]
        , 5:  [ {class: "q", text: "Praise him with loud cymbals!"}
              , {class: "q2", text: "Praise him with resounding cymbals!"}
              ]
        , 6:  [ {class: "q", text: "Let everything that has breath praise Yah!"}
              , {class: "q2", text: "Praise Yah!"}
              ]
        }
  , 16: { 1:  [ {class: "q", text: "Preserve me, God, for I take refuge in you."} ]
        , 2:  [ {class: "q", text: "My soul, you have said to Yahweh, “You are my Lord."}
              , {class: "q2", text: "Apart from you I have no good thing.”"}
              ]
        , 3:  [ {class: "q", text: "As for the saints who are in the earth,"}
              , {class: "q2", text: "they are the excellent ones in whom is all my delight."}
              ]
        , 4:  [ {class: "q", text: "Their sorrows shall be multiplied who give gifts to another god."}
              , {class: "q2", text: "Their drink offerings of blood I will not offer,"}
              , {class: "q2", text: "nor take their names on my lips."}
              ]
        , 5:  [ {class: "q", text: "Yahweh assigned my portion and my cup."}
              , {class: "q2", text: "You made my lot secure."}
              ]
        , 6:  [ {class: "q", text: "The lines have fallen to me in pleasant places."}
              , {class: "q2", text: "Yes, I have a good inheritance."}
              ]
        , 7:  [ {class: "q", text: "I will bless Yahweh, who has given me counsel."}
              , {class: "q2", text: "Yes, my heart instructs me in the night seasons."}
              ]
        , 8:  [ {class: "q", text: "I have set Yahweh always before me."}
              , {class: "q2", text: "Because he is at my right hand, I shall not be moved."}
              ]
        , 9:  [ {class: "q", text: "Therefore my heart is glad, and my tongue rejoices."}
              , {class: "q2", text: "My body shall also dwell in safety."}
              ]
        , 10: [ {class: "q", text: "For you will not leave my soul in Sheol,"}
              , {class: "q2", text: "neither will you allow your holy one to see corruption."}
              ]
        , 11: [ {class: "q", text: "You will show me the path of life."}
              , {class: "q2", text: "In your presence is fullness of joy."}
              , {class: "q", text: "In your right hand there are pleasures forever more."}
              ]
        }
  , 17: { 1:  [ {class: "q", text: "Hear, Yahweh, my righteous plea;"}
              , {class: "q2", text: "Give ear to my prayer, that doesn’t go out of deceitful lips."}
              ]
        , 2:  [ {class: "q", text: "Let my sentence come out of your presence."}
              , {class: "q2", text: "Let your eyes look on equity."}
              ]
        , 3:  [ {class: "q", text: "You have proved my heart."}
              , {class: "q2", text: "You have visited me in the night."}
              , {class: "q2", text: "You have tried me, and found nothing."}
              , {class: "q2", text: "I have resolved that my mouth shall not disobey."}
              ]
        , 4:  [ {class: "q", text: "As for the deeds of men, by the word of your lips,"}
              , {class: "q2", text: "I have kept myself from the ways of the violent."}
              ]
        , 5:  [ {class: "q", text: "My steps have held fast to your paths."}
              , {class: "q2", text: "My feet have not slipped."}
              ]
        , 6:  [ {class: "q", text: "I have called on you, for you will answer me, God."}
              , {class: "q2", text: "Turn your ear to me."}
              , {class: "q2", text: "Hear my speech."}
              ]
        , 7:  [ {class: "q", text: "Show your marvelous loving kindness,"}
              , {class: "q2", text: "you who save those who take refuge by your right hand from their enemies."}
              ]
        , 8:  [ {class: "q", text: "Keep me as the apple of your eye."}
              , {class: "q2", text: "Hide me under the shadow of your wings,"}
              ]
        , 9:  [ {class: "q", text: "from the wicked who oppress me,"}
              , {class: "q2", text: "my deadly enemies, who surround me."}
              ]
        , 10: [ {class: "q", text: "They close up their callous hearts."}
              , {class: "q2", text: "With their mouth they speak proudly."}
              ]
        , 11: [ {class: "q", text: "They have now surrounded us in our steps."}
              , {class: "q2", text: "They set their eyes to cast us down to the earth."}
              ]
        , 12: [ {class: "q", text: "He is like a lion that is greedy of his prey,"}
              , {class: "q2", text: "as it were a young lion lurking in secret places."}
              ]
        , 13: [ {class: "q", text: "Arise, Yahweh, confront him."}
              , {class: "q2", text: "Cast him down."}
              , {class: "q", text: "Deliver my soul from the wicked by your sword;"}
              ]
        , 14: [ {class: "q2", text: "</span> from men by your hand, Yahweh,"}
              , {class: "q2", text: "from men of the world, whose portion is in this life."}
              , {class: "q", text: "You fill the belly of your cherished ones."}
              , {class: "q2", text: "Your sons have plenty,"}
              , {class: "q2", text: "and they store up wealth for their children."}
              ]
        , 15: [ {class: "q", text: "As for me, I shall see your face in righteousness."}
              , {class: "q2", text: "I shall be satisfied, when I awake, with seeing your form."}
              ]
        }
  , 18: { 1:  [ {class: "q", text: "I love you, Yahweh, my strength."} ]
        , 2:  [ {class: "q", text: "Yahweh is my rock, my fortress, and my deliverer;"}
              , {class: "q2", text: "my God, my rock, in whom I take refuge;"}
              , {class: "q2", text: "my shield, and the horn of my salvation, my high tower."}
              ]
        , 3:  [ {class: "q", text: "I call on Yahweh, who is worthy to be praised;"}
              , {class: "q2", text: "and I am saved from my enemies."}
              ]
        , 4:  [ {class: "q", text: "The cords of death surrounded me."}
              , {class: "q2", text: "The floods of ungodliness made me afraid."}
              ]
        , 5:  [ {class: "q", text: "The cords of Sheol were around me."}
              , {class: "q2", text: "The snares of death came on me."}
              ]
        , 6:  [ {class: "q", text: "In my distress I called on Yahweh,"}
              , {class: "q2", text: "and cried to my God."}
              , {class: "q", text: "He heard my voice out of his temple."}
              , {class: "q2", text: "My cry before him came into his ears."}
              ]
        , 7:  [ {class: "q", text: "Then the earth shook and trembled."}
              , {class: "q2", text: "The foundations also of the mountains quaked and were shaken,"}
              , {class: "q2", text: "because he was angry."}
              ]
        , 8:  [ {class: "q", text: "Smoke went out of his nostrils."}
              , {class: "q2", text: "Consuming fire came out of his mouth."}
              , {class: "q2", text: "Coals were kindled by it."}
              ]
        , 9:  [ {class: "q", text: "He bowed the heavens also, and came down."}
              , {class: "q2", text: "Thick darkness was under his feet."}
              ]
        , 10: [ {class: "q", text: "He rode on a cherub, and flew."}
              , {class: "q2", text: "Yes, he soared on the wings of the wind."}
              ]
        , 11: [ {class: "q", text: "He made darkness his hiding place, his pavilion around him,"}
              , {class: "q2", text: "darkness of waters, thick clouds of the skies."}
              ]
        , 12: [ {class: "q", text: "At the brightness before him his thick clouds passed,"}
              , {class: "q2", text: "hailstones and coals of fire."}
              ]
        , 13: [ {class: "q", text: "Yahweh also thundered in the sky."}
              , {class: "q2", text: "The Most High uttered his voice:"}
              , {class: "q2", text: "hailstones and coals of fire."}
              ]
        , 14: [ {class: "q", text: "He sent out his arrows, and scattered them;"}
              , {class: "q2", text: "Yes, great lightning bolts, and routed them."}
              ]
        , 15: [ {class: "q", text: "Then the channels of waters appeared."}
              , {class: "q2", text: "The foundations of the world were laid bare at your rebuke, Yahweh,"}
              , {class: "q2", text: "at the blast of the breath of your nostrils."}
              ]
        , 16: [ {class: "q", text: "He sent from on high."}
              , {class: "q2", text: "He took me."}
              , {class: "q2", text: "He drew me out of many waters."}
              ]
        , 17: [ {class: "q", text: "He delivered me from my strong enemy,"}
              , {class: "q2", text: "from those who hated me; for they were too mighty for me."}
              ]
        , 18: [ {class: "q", text: "They came on me in the day of my calamity,"}
              , {class: "q2", text: "but Yahweh was my support."}
              ]
        , 19: [ {class: "q", text: "He brought me out also into a large place."}
              , {class: "q2", text: "He delivered me, because he delighted in me."}
              ]
        , 20: [ {class: "q", text: "Yahweh has rewarded me according to my righteousness."}
              , {class: "q2", text: "According to the cleanness of my hands, he has recompensed me."}
              ]
        , 21: [ {class: "q", text: "For I have kept the ways of Yahweh,"}
              , {class: "q2", text: "and have not wickedly departed from my God."}
              ]
        , 22: [ {class: "q", text: "For all his ordinances were before me."}
              , {class: "q2", text: "I didn’t put away his statutes from me."}
              ]
        , 23: [ {class: "q", text: "I was also blameless with him."}
              , {class: "q2", text: "I kept myself from my iniquity."}
              ]
        , 24: [ {class: "q", text: "Therefore Yahweh has rewarded me according to my righteousness,"}
              , {class: "q2", text: "according to the cleanness of my hands in his eyesight."}
              ]
        , 25: [ {class: "q", text: "With the merciful you will show yourself merciful."}
              , {class: "q2", text: "With the perfect man, you will show yourself perfect."}
              ]
        , 26: [ {class: "q", text: "With the pure, you will show yourself pure."}
              , {class: "q2", text: "With the crooked you will show yourself shrewd."}
              ]
        , 27: [ {class: "q", text: "For you will save the afflicted people,"}
              , {class: "q2", text: "but the arrogant eyes you will bring down."}
              ]
        , 28: [ {class: "q", text: "For you will light my lamp, Yahweh."}
              , {class: "q2", text: "My God will light up my darkness."}
              ]
        , 29: [ {class: "q", text: "For by you, I advance through a troop."}
              , {class: "q2", text: "By my God, I leap over a wall."}
              ]
        , 30: [ {class: "q", text: "As for God, his way is perfect."}
              , {class: "q2", text: "Yahweh’s word is tried."}
              , {class: "q2", text: "He is a shield to all those who take refuge in him."}
              ]
        , 31: [ {class: "q", text: "For who is God, except Yahweh?"}
              , {class: "q2", text: "Who is a rock, besides our God,"}
              ]
        , 32: [ {class: "q2", text: "</span> the God who arms me with strength, and makes my way perfect?"} ]
        , 33: [ {class: "q", text: "He makes my feet like deer’s feet,"}
              , {class: "q2", text: "and sets me on my high places."}
              ]
        , 34: [ {class: "q", text: "He teaches my hands to war,"}
              , {class: "q2", text: "so that my arms bend a bow of bronze."}
              ]
        , 35: [ {class: "q", text: "You have also given me the shield of your salvation."}
              , {class: "q2", text: "Your right hand sustains me."}
              , {class: "q2", text: "Your gentleness has made me great."}
              ]
        , 36: [ {class: "q", text: "You have enlarged my steps under me,"}
              , {class: "q2", text: "My feet have not slipped."}
              ]
        , 37: [ {class: "q", text: "I will pursue my enemies, and overtake them."}
              , {class: "q2", text: "I won’t turn away until they are consumed."}
              ]
        , 38: [ {class: "q", text: "I will strike them through, so that they will not be able to rise."}
              , {class: "q2", text: "They shall fall under my feet."}
              ]
        , 39: [ {class: "q", text: "For you have armed me with strength to the battle."}
              , {class: "q2", text: "You have subdued under me those who rose up against me."}
              ]
        , 40: [ {class: "q", text: "You have also made my enemies turn their backs to me,"}
              , {class: "q2", text: "that I might cut off those who hate me."}
              ]
        , 41: [ {class: "q", text: "They cried, but there was no one to save;"}
              , {class: "q2", text: "even to Yahweh, but he didn’t answer them."}
              ]
        , 42: [ {class: "q", text: "Then I beat them small as the dust before the wind."}
              , {class: "q2", text: "I cast them out as the mire of the streets."}
              ]
        , 43: [ {class: "q", text: "You have delivered me from the strivings of the people."}
              , {class: "q2", text: "You have made me the head of the nations."}
              , {class: "q", text: "A people whom I have not known shall serve me."}
              ]
        , 44: [ {class: "q2", text: "</span> As soon as they hear of me they shall obey me."}
              , {class: "q2", text: "The foreigners shall submit themselves to me."}
              ]
        , 45: [ {class: "q", text: "The foreigners shall fade away,"}
              , {class: "q2", text: "and shall come trembling out of their strongholds."}
              ]
        , 46: [ {class: "q", text: "Yahweh lives! Blessed be my rock."}
              , {class: "q2", text: "Exalted be the God of my salvation,"}
              ]
        , 47: [ {class: "q", text: "even the God who executes vengeance for me,"}
              , {class: "q2", text: "and subdues peoples under me."}
              ]
        , 48: [ {class: "q", text: "He rescues me from my enemies."}
              , {class: "q2", text: "Yes, you lift me up above those who rise up against me."}
              , {class: "q2", text: "You deliver me from the violent man."}
              ]
        , 49: [ {class: "q", text: "Therefore I will give thanks to you, Yahweh, among the nations,"}
              , {class: "q2", text: "and will sing praises to your name."}
              ]
        , 50: [ {class: "q", text: "He gives great deliverance to his king,"}
              , {class: "q2", text: "and shows loving kindness to his anointed,"}
              , {class: "q2", text: "to David and to his offspring, forever more."}
              ]
        }
  , 19: { 1:  [ {class: "q", text: "The heavens declare the glory of God."}
              , {class: "q2", text: "The expanse shows his handiwork."}
              ]
        , 2:  [ {class: "q", text: "Day after day they pour out speech,"}
              , {class: "q2", text: "and night after night they display knowledge."}
              ]
        , 3:  [ {class: "q", text: "There is no speech nor language,"}
              , {class: "q2", text: "where their voice is not heard."}
              ]
        , 4:  [ {class: "q", text: "Their voice has gone out through all the earth,"}
              , {class: "q2", text: "their words to the end of the world."}
              , {class: "q", text: "In them he has set a tent for the sun,"}
              ]
        , 5:  [ {class: "q2", text: "/span> which is as a bridegroom coming out of his room,"}
              , {class: "q2", text: "like a strong man rejoicing to run his course."}
              ]
        , 6:  [ {class: "q", text: "His going out is from the end of the heavens,"}
              , {class: "q2", text: "his circuit to its ends."}
              , {class: "q2", text: "There is nothing hidden from its heat."}
              ]
        , 7:  [ {class: "q", text: "Yahweh’s law is perfect, restoring the soul."}
              , {class: "q2", text: "Yahweh’s covenant is sure, making wise the simple."}
              ]
        , 8:  [ {class: "q", text: "Yahweh’s precepts are right, rejoicing the heart."}
              , {class: "q2", text: "Yahweh’s commandment is pure, enlightening the eyes."}
              ]
        , 9:  [ {class: "q", text: "The fear of Yahweh is clean, enduring forever."}
              , {class: "q2", text: "Yahweh’s ordinances are true, and righteous altogether."}
              ]
        , 10: [ {class: "q", text: "They are more to be desired than gold, yes, than much fine gold,"}
              , {class: "q2", text: "sweeter also than honey and the extract of the honeycomb."}
              ]
        , 11: [ {class: "q", text: "Moreover your servant is warned by them."}
              , {class: "q2", text: "In keeping them there is great reward."}
              ]
        , 12: [ {class: "q", text: "Who can discern his errors?"}
              , {class: "q2", text: "Forgive me from hidden errors."}
              ]
        , 13: [ {class: "q", text: "Keep back your servant also from presumptuous sins."}
              , {class: "q2", text: "Let them not have dominion over me."}
              , {class: "q", text: "Then I will be upright."}
              , {class: "q2", text: "I will be blameless and innocent of great transgression."}
              ]
        , 14: [ {class: "q", text: "Let the words of my mouth and the meditation of my heart"}
              , {class: "q2", text: "be acceptable in your sight,"}
              , {class: "q2", text: "Yahweh, my rock, and my redeemer."}
              ]
        }
  , 2:  { 1:  [ {class: "q", text: "Why do the nations rage,"}
              , {class: "q2", text: "and the peoples plot a vain thing?"}
              ]
        , 2:  [ {class: "q", text: "The kings of the earth take a stand,"}
              , {class: "q2", text: "and the rulers take counsel together,"}
              , {class: "q2", text: "against Yahweh, and against his Anointed, saying,"}
              ]
        , 3:  [ {class: "q", text: "“Let’s break their bonds apart,"}
              , {class: "q2", text: "and cast their cords from us.”"}
              ]
        , 4:  [ {class: "q", text: "He who sits in the heavens will laugh."}
              , {class: "q2", text: "The Lord will have them in derision."}
              ]
        , 5:  [ {class: "q", text: "Then he will speak to them in his anger,"}
              , {class: "q2", text: "and terrify them in his wrath:"}
              ]
        , 6:  [ {class: "q", text: "“Yet I have set my King on my holy hill of Zion.”"} ]
        , 7:  [ {class: "q2", text: "I will tell of the decree:"}
              , {class: "q", text: "Yahweh said to me, “You are my son."}
              , {class: "q2", text: "Today I have become your father."}
              ]
        , 8:  [ {class: "q", text: "Ask of me, and I will give the nations for your inheritance,"}
              , {class: "q2", text: "the uttermost parts of the earth for your possession."}
              ]
        , 9:  [ {class: "q", text: "You shall break them with a rod of iron."}
              , {class: "q2", text: "You shall dash them in pieces like a potter’s vessel.”"}
              ]
        , 10: [ {class: "q", text: "Now therefore be wise, you kings."}
              , {class: "q2", text: "Be instructed, you judges of the earth."}
              ]
        , 11: [ {class: "q", text: "Serve Yahweh with fear,"}
              , {class: "q2", text: "and rejoice with trembling."}
              ]
        , 12: [ {class: "q", text: "Give sincere homage to the Son, lest he be angry, and you perish on the way,"}
              , {class: "q2", text: "for his wrath will soon be kindled."}
              , {class: "q2", text: "Blessed are all those who take refuge in him."}
              ]
        }
  , 20: { 1:  [ {class: "q", text: "May Yahweh answer you in the day of trouble."}
              , {class: "q2", text: "May the name of the God of Jacob set you up on high,"}
              ]
        , 2:  [ {class: "q2", text: "/span> send you help from the sanctuary,"}
              , {class: "q2", text: "grant you support from Zion,"}
              ]
        , 3:  [ {class: "q2", text: "/span> remember all your offerings,"}
              , {class: "q2", text: "and accept your burned sacrifice."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "May he grant you your heart’s desire,"}
              , {class: "q2", text: "and fulfill all your counsel."}
              ]
        , 5:  [ {class: "q", text: "We will triumph in your salvation."}
              , {class: "q2", text: "In the name of our God, we will set up our banners."}
              , {class: "q2", text: "May Yahweh grant all your requests."}
              ]
        , 6:  [ {class: "q", text: "Now I know that Yahweh saves his anointed."}
              , {class: "q2", text: "He will answer him from his holy heaven,"}
              , {class: "q2", text: "with the saving strength of his right hand."}
              ]
        , 7:  [ {class: "q", text: "Some trust in chariots, and some in horses,"}
              , {class: "q2", text: "but we trust in the name of Yahweh our God."}
              ]
        , 8:  [ {class: "q", text: "They are bowed down and fallen,"}
              , {class: "q2", text: "but we rise up, and stand upright."}
              ]
        , 9:  [ {class: "q", text: "Save, Yahweh!"}
              , {class: "q2", text: "Let the King answer us when we call!"}
              ]
        }
  , 21: { 1:  [ {class: "q", text: "The king rejoices in your strength, Yahweh!"}
              , {class: "q2", text: "How greatly he rejoices in your salvation!"}
              ]
        , 2:  [ {class: "q", text: "You have given him his heart’s desire,"}
              , {class: "q2", text: "and have not withheld the request of his lips."}
              , {class: "qs", text: "Selah"}
              ]
        , 3:  [ {class: "q", text: "For you meet him with the blessings of goodness."}
              , {class: "q2", text: "You set a crown of fine gold on his head."}
              ]
        , 4:  [ {class: "q", text: "He asked life of you and you gave it to him,"}
              , {class: "q2", text: "even length of days forever and ever."}
              ]
        , 5:  [ {class: "q", text: "His glory is great in your salvation."}
              , {class: "q2", text: "You lay honor and majesty on him."}
              ]
        , 6:  [ {class: "q", text: "For you make him most blessed forever."}
              , {class: "q2", text: "You make him glad with joy in your presence."}
              ]
        , 7:  [ {class: "q", text: "For the king trusts in Yahweh."}
              , {class: "q2", text: "Through the loving kindness of the Most High, he shall not be moved."}
              ]
        , 8:  [ {class: "q", text: "Your hand will find out all of your enemies."}
              , {class: "q2", text: "Your right hand will find out those who hate you."}
              ]
        , 9:  [ {class: "q", text: "You will make them as a fiery furnace in the time of your anger."}
              , {class: "q2", text: "Yahweh will swallow them up in his wrath."}
              , {class: "q2", text: "The fire shall devour them."}
              ]
        , 10: [ {class: "q", text: "You will destroy their descendants from the earth,"}
              , {class: "q2", text: "their posterity from among the children of men."}
              ]
        , 11: [ {class: "q", text: "For they intended evil against you."}
              , {class: "q2", text: "They plotted evil against you which cannot succeed."}
              ]
        , 12: [ {class: "q", text: "For you will make them turn their back,"}
              , {class: "q2", text: "when you aim drawn bows at their face."}
              ]
        , 13: [ {class: "q", text: "Be exalted, Yahweh, in your strength,"}
              , {class: "q2", text: "so we will sing and praise your power."}
              ]
        }
  , 22: { 1:  [ {class: "q", text: "My God, my God, why have you forsaken me?"}
              , {class: "q2", text: "Why are you so far from helping me, and from the words of my groaning?"}
              ]
        , 2:  [ {class: "q", text: "My God, I cry in the daytime, but you don’t answer;"}
              , {class: "q2", text: "in the night season, and am not silent."}
              ]
        , 3:  [ {class: "q", text: "But you are holy,"}
              , {class: "q2", text: "you who inhabit the praises of Israel."}
              ]
        , 4:  [ {class: "q", text: "Our fathers trusted in you."}
              , {class: "q2", text: "They trusted, and you delivered them."}
              ]
        , 5:  [ {class: "q", text: "They cried to you, and were delivered."}
              , {class: "q2", text: "They trusted in you, and were not disappointed."}
              ]
        , 6:  [ {class: "q", text: "But I am a worm, and no man;"}
              , {class: "q2", text: "a reproach of men, and despised by the people."}
              ]
        , 7:  [ {class: "q", text: "All those who see me mock me."}
              , {class: "q2", text: "They insult me with their lips. They shake their heads, saying,"}
              ]
        , 8:  [ {class: "q2", text: "/span> “He trusts in Yahweh."}
              , {class: "q2", text: "Let him deliver him."}
              , {class: "q2", text: "Let him rescue him, since he delights in him.”"}
              ]
        , 9:  [ {class: "q", text: "But you brought me out of the womb."}
              , {class: "q2", text: "You made me trust while at my mother’s breasts."}
              ]
        , 10: [ {class: "q", text: "I was thrown on you from my mother’s womb."}
              , {class: "q2", text: "You are my God since my mother bore me."}
              ]
        , 11: [ {class: "q", text: "Don’t be far from me, for trouble is near."}
              , {class: "q2", text: "For there is no one to help."}
              ]
        , 12: [ {class: "q", text: "Many bulls have surrounded me."}
              , {class: "q2", text: "Strong bulls of Bashan have encircled me."}
              ]
        , 13: [ {class: "q", text: "They open their mouths wide against me,"}
              , {class: "q2", text: "lions tearing prey and roaring."}
              ]
        , 14: [ {class: "q", text: "I am poured out like water."}
              , {class: "q2", text: "All my bones are out of joint."}
              , {class: "q", text: "My heart is like wax."}
              , {class: "q2", text: "It is melted within me."}
              ]
        , 15: [ {class: "q", text: "My strength is dried up like a potsherd."}
              , {class: "q2", text: "My tongue sticks to the roof of my mouth."}
              , {class: "q", text: "You have brought me into the dust of death."}
              ]
        , 16: [ {class: "q", text: "For dogs have surrounded me."}
              , {class: "q2", text: "A company of evildoers have enclosed me."}
              , {class: "q2", text: "They have pierced my hands and feet."}
              ]
        , 17: [ {class: "q", text: "I can count all of my bones."}
              , {class: "q", text: "They look and stare at me."}
              ]
        , 18: [ {class: "q", text: "They divide my garments among them."}
              , {class: "q2", text: "They cast lots for my clothing."}
              ]
        , 19: [ {class: "q", text: "But don’t be far off, Yahweh."}
              , {class: "q2", text: "You are my help. Hurry to help me!"}
              ]
        , 20: [ {class: "q", text: "Deliver my soul from the sword,"}
              , {class: "q2", text: "my precious life from the power of the dog."}
              ]
        , 21: [ {class: "q", text: "Save me from the lion’s mouth!"}
              , {class: "q2", text: "Yes, you have rescued me from the horns of the wild oxen."}
              ]
        , 22: [ {class: "q", text: "I will declare your name to my brothers."}
              , {class: "q2", text: "Among the assembly, I will praise you."}
              ]
        , 23: [ {class: "q", text: "You who fear Yahweh, praise him!"}
              , {class: "q2", text: "All you descendants of Jacob, glorify him!"}
              , {class: "q2", text: "Stand in awe of him, all you descendants of Israel!"}
              ]
        , 24: [ {class: "q", text: "For he has not despised nor abhorred the affliction of the afflicted,"}
              , {class: "q2", text: "Neither has he hidden his face from him;"}
              , {class: "q2", text: "but when he cried to him, he heard."}
              ]
        , 25: [ {class: "q", text: "My praise of you comes in the great assembly."}
              , {class: "q2", text: "I will pay my vows before those who fear him."}
              ]
        , 26: [ {class: "q", text: "The humble shall eat and be satisfied."}
              , {class: "q2", text: "They shall praise Yahweh who seek after him."}
              , {class: "q2", text: "Let your hearts live forever."}
              ]
        , 27: [ {class: "q", text: "All the ends of the earth shall remember and turn to Yahweh."}
              , {class: "q2", text: "All the relatives of the nations shall worship before you."}
              ]
        , 28: [ {class: "q", text: "For the kingdom is Yahweh’s."}
              , {class: "q2", text: "He is the ruler over the nations."}
              ]
        , 29: [ {class: "q", text: "All the rich ones of the earth shall eat and worship."}
              , {class: "q2", text: "All those who go down to the dust shall bow before him,"}
              , {class: "q2", text: "even he who can’t keep his soul alive."}
              ]
        , 30: [ {class: "q", text: "Posterity shall serve him."}
              , {class: "q2", text: "Future generations shall be told about the Lord."}
              ]
        , 31: [ {class: "q", text: "They shall come and shall declare his righteousness to a people that shall be born,"}
              , {class: "q2", text: "for he has done it."}
              ]
        }
  , 23: { 1:  [ {class: "q", text: "Yahweh is my shepherd:"}
              , {class: "q2", text: "I shall lack nothing."}
              ]
        , 2:  [ {class: "q", text: "He makes me lie down in green pastures."}
              , {class: "q2", text: "He leads me beside still waters."}
              ]
        , 3:  [ {class: "q", text: "He restores my soul."}
              , {class: "q2", text: "He guides me in the paths of righteousness for his name’s sake."}
              ]
        , 4:  [ {class: "q", text: "Even though I walk through the valley of the shadow of death,"}
              , {class: "q2", text: "I will fear no evil, for you are with me."}
              , {class: "q", text: "Your rod and your staff,"}
              , {class: "q2", text: "they comfort me."}
              ]
        , 5:  [ {class: "q", text: "You prepare a table before me"}
              , {class: "q2", text: "in the presence of my enemies."}
              , {class: "q", text: "You anoint my head with oil."}
              , {class: "q2", text: "My cup runs over."}
              ]
        , 6:  [ {class: "q", text: "Surely goodness and loving kindness shall follow me all the days of my life,"}
              , {class: "q2", text: "and I will dwell in Yahweh’s house forever."}
              ]
        }
  , 24: { 1:  [ {class: "q", text: "The earth is Yahweh’s, with its fullness;"}
              , {class: "q2", text: "the world, and those who dwell in it."}
              ]
        , 2:  [ {class: "q", text: "For he has founded it on the seas,"}
              , {class: "q2", text: "and established it on the floods."}
              ]
        , 3:  [ {class: "q", text: "Who may ascend to Yahweh’s hill?"}
              , {class: "q2", text: "Who may stand in his holy place?"}
              ]
        , 4:  [ {class: "q", text: "He who has clean hands and a pure heart;"}
              , {class: "q2", text: "who has not lifted up his soul to falsehood,"}
              , {class: "q2", text: "and has not sworn deceitfully."}
              ]
        , 5:  [ {class: "q", text: "He shall receive a blessing from Yahweh,"}
              , {class: "q2", text: "righteousness from the God of his salvation."}
              ]
        , 6:  [ {class: "q", text: "This is the generation of those who seek Him,"}
              , {class: "q2", text: "who seek your face—even Jacob."}
              , {class: "qs", text: "Selah"}
              ]
        , 7:  [ {class: "q", text: "Lift up your heads, you gates!"}
              , {class: "q2", text: "Be lifted up, you everlasting doors,"}
              , {class: "q2", text: "and the King of glory will come in."}
              ]
        , 8:  [ {class: "q", text: "Who is the King of glory?"}
              , {class: "q2", text: "Yahweh strong and mighty,"}
              , {class: "q2", text: "Yahweh mighty in battle."}
              ]
        , 9:  [ {class: "q", text: "Lift up your heads, you gates;"}
              , {class: "q2", text: "yes, lift them up, you everlasting doors,"}
              , {class: "q2", text: "and the King of glory will come in."}
              ]
        , 10: [ {class: "q", text: "Who is this King of glory?"}
              , {class: "q2", text: "Yahweh of Armies is the King of glory!"}
              , {class: "qs", text: "Selah"}
              ]
        }
  , 25: { 1:  [ {class: "q", text: "To you, Yahweh, I lift up my soul."} ]
        , 2:  [ {class: "q", text: "My God, I have trusted in you."}
              , {class: "q2", text: "Don’t let me be shamed."}
              , {class: "q2", text: "Don’t let my enemies triumph over me."}
              ]
        , 3:  [ {class: "q", text: "Yes, no one who waits for you will be shamed."}
              , {class: "q2", text: "They will be shamed who deal treacherously without cause."}
              ]
        , 4:  [ {class: "q", text: "Show me your ways, Yahweh."}
              , {class: "q2", text: "Teach me your paths."}
              ]
        , 5:  [ {class: "q", text: "Guide me in your truth, and teach me,"}
              , {class: "q2", text: "For you are the God of my salvation,"}
              , {class: "q2", text: "I wait for you all day long."}
              ]
        , 6:  [ {class: "q", text: "Yahweh, remember your tender mercies and your loving kindness,"}
              , {class: "q2", text: "for they are from old times."}
              ]
        , 7:  [ {class: "q", text: "Don’t remember the sins of my youth, nor my transgressions."}
              ,  {class: "q2", text: "Remember me according to your loving kindness,"}
              , {class: "q2", text: "for your goodness’ sake, Yahweh."}
              ]
        , 8:  [ {class: "q", text: "Good and upright is Yahweh,"}
              ,  {class: "q2", text: "therefore he will instruct sinners in the way."}
              ]
        , 9:  [ {class: "q", text: "He will guide the humble in justice."}
              , {class: "q2", text: "He will teach the humble his way."}
              ]
        , 10: [ {class: "q", text: "All the paths of Yahweh are loving kindness and truth"}
              , {class: "q2", text: "to such as keep his covenant and his testimonies."}
              ]
        , 11: [ {class: "q", text: "For your name’s sake, Yahweh,"}
              , {class: "q2", text: "pardon my iniquity, for it is great."}
              ]
        , 12: [ {class: "q", text: "What man is he who fears Yahweh?"}
              , {class: "q2", text: "He shall instruct him in the way that he shall choose."}
              ]
        , 13: [ {class: "q", text: "His soul will dwell at ease."}
              , {class: "q2", text: "His offspring will inherit the land."}
              ]
        , 14: [ {class: "q", text: "The friendship of Yahweh is with those who fear him."}
              , {class: "q2", text: "He will show them his covenant."}
              ]
        , 15: [ {class: "q", text: "My eyes are ever on Yahweh,"}
              , {class: "q2", text: "for he will pluck my feet out of the net."}
              ]
        , 16: [ {class: "q", text: "Turn to me, and have mercy on me,"}
              ,  {class: "q2", text: "for I am desolate and afflicted."}
              ]
        , 17: [ {class: "q", text: "The troubles of my heart are enlarged."}
              ,  {class: "q2", text: "Oh bring me out of my distresses."}
              ]
        , 18: [ {class: "q", text: "Consider my affliction and my travail."}
              , {class: "q2", text: "Forgive all my sins."}
              ]
        , 19: [ {class: "q", text: "Consider my enemies, for they are many."}
              , {class: "q2", text: "They hate me with cruel hatred."}
              ]
        , 20: [ {class: "q", text: "Oh keep my soul, and deliver me."}
              , {class: "q2", text: "Let me not be disappointed, for I take refuge in you."}
              ]
        , 21: [ {class: "q", text: "Let integrity and uprightness preserve me,"}
              , {class: "q2", text: "for I wait for you."}
              ]
        , 22: [ {class: "q", text: "Redeem Israel, God,"}
              ,  {class: "q2", text: "out of all his troubles."}
              ]
        }
  , 26: { 1:  [ {class: "q", text: "Judge me, Yahweh, for I have walked in my integrity."}
              , {class: "q2", text: "I have trusted also in Yahweh without wavering."}
              ]
        , 2:  [ {class: "q", text: "Examine me, Yahweh, and prove me."}
              , {class: "q2", text: "Try my heart and my mind."}
              ]
        , 3:  [ {class: "q", text: "For your loving kindness is before my eyes."}
              , {class: "q2", text: "I have walked in your truth."}
              ]
        , 4:  [ {class: "q", text: "I have not sat with deceitful men,"}
              , {class: "q2", text: "neither will I go in with hypocrites."}
              ]
        , 5:  [ {class: "q", text: "I hate the assembly of evildoers,"}
              , {class: "q2", text: "and will not sit with the wicked."}
              ]
        , 6:  [ {class: "q", text: "I will wash my hands in innocence,"}
              , {class: "q2", text: "so I will go about your altar, Yahweh,"}
              ]
        , 7:  [ {class: "q2", text: "/span> that I may make the voice of thanksgiving to be heard"}
              , {class: "q2", text: "and tell of all your wondrous deeds."}
              ]
        , 8:  [ {class: "q", text: "Yahweh, I love the habitation of your house,"}
              , {class: "q2", text: "the place where your glory dwells."}
              ]
        , 9:  [ {class: "q", text: "Don’t gather my soul with sinners,"}
              , {class: "q2", text: "nor my life with bloodthirsty men;"}
              ]
        , 10: [ {class: "q2", text: "</span> in whose hands is wickedness,"}
              , {class: "q2", text: "their right hand is full of bribes."}
              ]
        , 11: [ {class: "q", text: "But as for me, I will walk in my integrity."}
              , {class: "q2", text: "Redeem me, and be merciful to me."}
              ]
        , 12: [ {class: "q", text: "My foot stands in an even place."}
              , {class: "q2", text: "In the congregations I will bless Yahweh."}
              ]
        }
  , 27: { 1:  [ {class: "q", text: "Yahweh is my light and my salvation."}
              , {class: "q2", text: "Whom shall I fear?"}
              , {class: "q", text: "Yahweh is the strength of my life."}
              , {class: "q2", text: "Of whom shall I be afraid?"}
              ]
        , 2:  [ {class: "q", text: "When evildoers came at me to eat up my flesh,"}
              , {class: "q2", text: "even my adversaries and my foes, they stumbled and fell."}
              ]
        , 3:  [ {class: "q", text: "Though an army should encamp against me,"}
              , {class: "q2", text: "my heart shall not fear."}
              , {class: "q", text: "Though war should rise against me,"}
              , {class: "q2", text: "even then I will be confident."}
              ]
        , 4:  [ {class: "q", text: "One thing I have asked of Yahweh, that I will seek after:"}
              , {class: "q2", text: "that I may dwell in Yahweh’s house all the days of my life,"}
              , {class: "q2", text: "to see Yahweh’s beauty,"}
              , {class: "q2", text: "and to inquire in his temple."}
              ]
        , 5:  [ {class: "q", text: "For in the day of trouble, he will keep me secretly in his pavilion."}
              , {class: "q2", text: "In the secret place of his tabernacle, he will hide me."}
              , {class: "q2", text: "He will lift me up on a rock."}
              ]
        , 6:  [ {class: "q", text: "Now my head will be lifted up above my enemies around me."}
              , {class: "q", text: "I will offer sacrifices of joy in his tent."}
              , {class: "q2", text: "I will sing, yes, I will sing praises to Yahweh."}
              ]
        , 7:  [ {class: "q", text: "Hear, Yahweh, when I cry with my voice."}
              , {class: "q2", text: "Have mercy also on me, and answer me."}
              ]
        , 8:  [ {class: "q", text: "When you said, “Seek my face,”"}
              , {class: "q2", text: "my heart said to you, “I will seek your face, Yahweh.”"}
              ]
        , 9:  [ {class: "q", text: "Don’t hide your face from me."}
              , {class: "q2", text: "Don’t put your servant away in anger."}
              , {class: "q", text: "You have been my help."}
              , {class: "q2", text: "Don’t abandon me,"}
              , {class: "q2", text: "neither forsake me, God of my salvation."}
              ]
        , 10: [ {class: "q", text: "When my father and my mother forsake me,"}
              , {class: "q2", text: "then Yahweh will take me up."}
              ]
        , 11: [ {class: "q", text: "Teach me your way, Yahweh."}
              , {class: "q2", text: "Lead me in a straight path, because of my enemies."}
              ]
        , 12: [ {class: "q", text: "Don’t deliver me over to the desire of my adversaries,"}
              , {class: "q2", text: "for false witnesses have risen up against me,"}
              , {class: "q2", text: "such as breathe out cruelty."}
              ]
        , 13: [ {class: "q", text: "I am still confident of this:"}
              , {class: "q2", text: "I will see the goodness of Yahweh in the land of the living."}
              ]
        , 14: [ {class: "q", text: "Wait for Yahweh."}
              , {class: "q2", text: "Be strong, and let your heart take courage."}
              , {class: "q", text: "Yes, wait for Yahweh."}
              ]
        }
  , 28: { 1:  [ {class: "q", text: "To you, Yahweh, I call."}
              , {class: "q2", text: "My rock, don’t be deaf to me,"}
              , {class: "q2", text: "lest, if you are silent to me,"}
              , {class: "q2", text: "I would become like those who go down into the pit."}
              ]
        , 2:  [ {class: "q", text: "Hear the voice of my petitions, when I cry to you,"}
              , {class: "q2", text: "when I lift up my hands toward your Most Holy Place."}
              ]
        , 3:  [ {class: "q", text: "Don’t draw me away with the wicked,"}
              , {class: "q2", text: "with the workers of iniquity who speak peace with their neighbors,"}
              , {class: "q2", text: "but mischief is in their hearts."}
              ]
        , 4:  [ {class: "q", text: "Give them according to their work, and according to the wickedness of their doings."}
              , {class: "q2", text: "Give them according to the operation of their hands."}
              , {class: "q2", text: "Bring back on them what they deserve."}
              ]
        , 5:  [ {class: "q", text: "Because they don’t respect the works of Yahweh,"}
              , {class: "q2", text: "nor the operation of his hands,"}
              , {class: "q2", text: "he will break them down and not build them up."}
              ]
        , 6:  [ {class: "q", text: "Blessed be Yahweh,"}
              , {class: "q2", text: "because he has heard the voice of my petitions."}
              ]
        , 7:  [ {class: "q", text: "Yahweh is my strength and my shield."}
              , {class: "q2", text: "My heart has trusted in him, and I am helped."}
              , {class: "q", text: "Therefore my heart greatly rejoices."}
              , {class: "q2", text: "With my song I will thank him."}
              ]
        , 8:  [ {class: "q", text: "Yahweh is their strength."}
              , {class: "q2", text: "He is a stronghold of salvation to his anointed."}
              ]
        , 9:  [ {class: "q", text: "Save your people,"}
              , {class: "q2", text: "and bless your inheritance."}
              , {class: "q", text: "Be their shepherd also,"}
              , {class: "q2", text: "and bear them up forever."}
              ]
        }
  , 29: { 1:  [ {class: "q", text: "Ascribe to Yahweh, you sons of the mighty,"}
              , {class: "q2", text: "ascribe to Yahweh glory and strength."}
              ]
        , 2:  [ {class: "q", text: "Ascribe to Yahweh the glory due to his name."}
              , {class: "q2", text: "Worship Yahweh in holy array."}
              ]
        , 3:  [ {class: "q", text: "Yahweh’s voice is on the waters."}
              , {class: "q2", text: "The God of glory thunders, even Yahweh on many waters."}
              ]
        , 4:  [ {class: "q", text: "Yahweh’s voice is powerful."}
              , {class: "q2", text: "Yahweh’s voice is full of majesty."}
              ]
        , 5:  [ {class: "q", text: "Yahweh’s voice breaks the cedars."}
              , {class: "q2", text: "Yes, Yahweh breaks in pieces the cedars of Lebanon."}
              ]
        , 6:  [ {class: "q", text: "He makes them also to skip like a calf;"}
              , {class: "q2", text: "Lebanon and Sirion like a young, wild ox."}
              ]
        , 7:  [ {class: "q", text: "Yahweh’s voice strikes with flashes of lightning."} ]
        , 8:  [ {class: "q2", text: "/span> Yahweh’s voice shakes the wilderness."}
              , {class: "q2", text: "Yahweh shakes the wilderness of Kadesh."}
              ]
        , 9:  [ {class: "q", text: "Yahweh’s voice makes the deer calve,"}
              , {class: "q2", text: "and strips the forests bare."}
              , {class: "q2", text: "In his temple everything says, “Glory!”"}
              ]
        , 10: [ {class: "q", text: "Yahweh sat enthroned at the Flood."}
              , {class: "q2", text: "Yes, Yahweh sits as King forever."}
              ]
        , 11: [ {class: "q", text: "Yahweh will give strength to his people."}
              , {class: "q2", text: "Yahweh will bless his people with peace."}
              ]
        }
  , 3:  { 1:  [ {class: "q", text: "Yahweh, how my adversaries have increased!"}
              , {class: "q2", text: "Many are those who rise up against me."}
              ]
        , 2:  [ {class: "q", text: "Many there are who say of my soul,"}
              , {class: "q2", text: "“There is no help for him in God.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 3:  [ {class: "q", text: "But you, Yahweh, are a shield around me,"}
              , {class: "q2", text: "my glory, and the one who lifts up my head."}
              ]
        , 4:  [ {class: "q", text: "I cry to Yahweh with my voice,"}
              , {class: "q2", text: "and he answers me out of his holy hill."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "I laid myself down and slept."}
              , {class: "q2", text: "I awakened; for Yahweh sustains me."}
              ]
        , 6:  [ {class: "q", text: "I will not be afraid of tens of thousands of people"}
              , {class: "q2", text: "who have set themselves against me on every side."}
              ]
        , 7:  [ {class: "q", text: "Arise, Yahweh!"}
              , {class: "q2", text: "Save me, my God!"}
              , {class: "q", text: "For you have struck all of my enemies on the cheek bone."}
              , {class: "q2", text: "You have broken the teeth of the wicked."}
              ]
        , 8:  [ {class: "q", text: "Salvation belongs to Yahweh."}
              , {class: "q2", text: "May your blessing be on your people."}
              , {class: "qs", text: "Selah"}
              ]
        }
        , 30: { 1:  [ {class: "q", text: "I will extol you, Yahweh, for you have raised me up,"}
              , {class: "q2", text: "and have not made my foes to rejoice over me."}
              ]
        , 2:  [ {class: "q", text: "Yahweh my God, I cried to you,"}
              , {class: "q2", text: "and you have healed me."}
              ]
        , 3:  [ {class: "q", text: "Yahweh, you have brought up my soul from Sheol."}
              , {class: "q2", text: "You have kept me alive, that I should not go down to the pit."}
              ]
        , 4:  [ {class: "q", text: "Sing praise to Yahweh, you saints of his."}
              , {class: "q2", text: "Give thanks to his holy name."}
              ]
        , 5:  [ {class: "q", text: "For his anger is but for a moment."}
              , {class: "q2", text: "His favor is for a lifetime."}
              , {class: "q", text: "Weeping may stay for the night,"}
              , {class: "q2", text: "but joy comes in the morning."}
              ]
        , 6:  [ {class: "q", text: "As for me, I said in my prosperity,"}
              , {class: "q2", text: "“I shall never be moved.”"}
              ]
        , 7:  [ {class: "q", text: "You, Yahweh, when you favored me, made my mountain stand strong;"}
              , {class: "q2", text: "but when you hid your face, I was troubled."}
              ]
        , 8:  [ {class: "q", text: "I cried to you, Yahweh."}
              , {class: "q2", text: "I made supplication to the Lord:"}
              ]
        , 9:  [ {class: "q", text: "“What profit is there in my destruction, if I go down to the pit?"}
              , {class: "q2", text: "Shall the dust praise you?"}
              , {class: "q2", text: "Shall it declare your truth?"}
              ]
        , 10: [ {class: "q", text: "Hear, Yahweh, and have mercy on me."}
              , {class: "q2", text: "Yahweh, be my helper.”"}
              ]
        , 11: [ {class: "q", text: "You have turned my mourning into dancing for me."}
              , {class: "q2", text: "You have removed my sackcloth, and clothed me with gladness,"}
              ]
        , 12: [ {class: "q2", text: "</span> to the end that my heart may sing praise to you, and not be silent."}
              , {class: "q", text: "Yahweh my God, I will give thanks to you forever!"}
              ]
        }
  , 31: { 1:  [ {class: "q", text: "In you, Yahweh, I take refuge."}
              , {class: "q2", text: "Let me never be disappointed."}
              , {class: "q2", text: "Deliver me in your righteousness."}
              ]
        , 2:  [ {class: "q", text: "Bow down your ear to me."}
              , {class: "q2", text: "Deliver me speedily."}
              , {class: "q", text: "Be to me a strong rock,"}
              , {class: "q2", text: "a house of defense to save me."}
              ]
        , 3:  [ {class: "q", text: "For you are my rock and my fortress,"}
              , {class: "q2", text: "therefore for your name’s sake lead me and guide me."}
              ]
        , 4:  [ {class: "q", text: "Pluck me out of the net that they have laid secretly for me,"}
              , {class: "q2", text: "for you are my stronghold."}
              ]
        , 5:  [ {class: "q", text: "Into your hand I commend my spirit."}
              ,  {class: "q2", text: "You redeem me, Yahweh, God of truth."}
              ]
        , 6:  [ {class: "q", text: "I hate those who regard lying vanities,"}
              , {class: "q2", text: "but I trust in Yahweh."}
              ]
        , 7:  [ {class: "q", text: "I will be glad and rejoice in your loving kindness,"}
              , {class: "q2", text: "for you have seen my affliction."}
              , {class: "q2", text: "You have known my soul in adversities."}
              ]
        , 8:  [ {class: "q", text: "You have not shut me up into the hand of the enemy."}
              , {class: "q2", text: "You have set my feet in a large place."}
              ]
        , 9:  [ {class: "q", text: "Have mercy on me, Yahweh, for I am in distress."}
              , {class: "q2", text: "My eye, my soul, and my body waste away with grief."}
              ]
        , 10: [ {class: "q", text: "For my life is spent with sorrow,"}
              , {class: "q2", text: "my years with sighing."}
              , {class: "q", text: "My strength fails because of my iniquity."}
              , {class: "q2", text: "My bones are wasted away."}
              ]
        , 11: [ {class: "q", text: "Because of all my adversaries I have become utterly contemptible to my neighbors,"}
              , {class: "q2", text: "a horror to my acquaintances."}
              , {class: "q2", text: "Those who saw me on the street fled from me."}
              ]
        , 12: [ {class: "q", text: "I am forgotten from their hearts like a dead man."}
              , {class: "q2", text: "I am like broken pottery."}
              ]
        , 13: [ {class: "q", text: "For I have heard the slander of many, terror on every side,"}
              , {class: "q2", text: "while they conspire together against me,"}
              , {class: "q2", text: "they plot to take away my life."}
              ]
        , 14: [ {class: "q", text: "But I trust in you, Yahweh."}
              , {class: "q2", text: "I said, “You are my God.”"}
              ]
        , 15: [ {class: "q", text: "My times are in your hand."}
              , {class: "q2", text: "Deliver me from the hand of my enemies, and from those who persecute me."}
              ]
        , 16: [ {class: "q", text: "Make your face to shine on your servant."}
              , {class: "q2", text: "Save me in your loving kindness."}
              ]
        , 17: [ {class: "q", text: "Let me not be disappointed, Yahweh, for I have called on you."}
              , {class: "q2", text: "Let the wicked be disappointed."}
              , {class: "q2", text: "Let them be silent in Sheol."}
              ]
        , 18: [ {class: "q", text: "Let the lying lips be mute,"}
              , {class: "q2", text: "which speak against the righteous insolently, with pride and contempt."}
              ]
        , 19: [ {class: "q", text: "Oh how great is your goodness,"}
              , {class: "q2", text: "which you have laid up for those who fear you,"}
              , {class: "q2", text: "which you have worked for those who take refuge in you,"}
              , {class: "q2", text: "before the sons of men!"}
              ]
        , 20: [ {class: "q", text: "In the shelter of your presence you will hide them from the plotting of man."}
              , {class: "q2", text: "You will keep them secretly in a dwelling away from the strife of tongues."}
              ]
        , 21: [ {class: "q", text: "Praise be to Yahweh,"}
              , {class: "q2", text: "for he has shown me his marvelous loving kindness in a strong city."}
              ]
        , 22: [ {class: "q", text: "As for me, I said in my haste, “I am cut off from before your eyes.”"}
              ,  {class: "q2", text: "Nevertheless you heard the voice of my petitions when I cried to you."}
              ]
        , 23: [ {class: "q", text: "Oh love Yahweh, all you his saints!"}
              , {class: "q2", text: "Yahweh preserves the faithful,"}
              , {class: "q", text: "and fully recompenses him who behaves arrogantly."}
              ]
        , 24: [ {class: "q", text: "Be strong, and let your heart take courage,"}
              , {class: "q2", text: "all you who hope in Yahweh."}
              ]
        }
  , 32: { 1:  [ {class: "q", text: "Blessed is he whose disobedience is forgiven,"}
              , {class: "q2", text: "whose sin is covered."}
              ]
        , 2:  [ {class: "q", text: "Blessed is the man to whom Yahweh doesn’t impute iniquity,"}
              , {class: "q2", text: "in whose spirit there is no deceit."}
              ]
        , 3:  [ {class: "q", text: "When I kept silence, my bones wasted away through my groaning all day long."} ]
        , 4:  [ {class: "q", text: "For day and night your hand was heavy on me."}
              , {class: "q2", text: "My strength was sapped in the heat of summer."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "I acknowledged my sin to you."}
              , {class: "q2", text: "I didn’t hide my iniquity."}
              , {class: "q", text: "I said, I will confess my transgressions to Yahweh,"}
              , {class: "q2", text: "and you forgave the iniquity of my sin."}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "For this, let everyone who is godly pray to you in a time when you may be found."}
              , {class: "q2", text: "Surely when the great waters overflow, they shall not reach to him."}
              ]
        , 7:  [ {class: "q", text: "You are my hiding place."}
              , {class: "q2", text: "You will preserve me from trouble."}
              , {class: "q2", text: "You will surround me with songs of deliverance."}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "I will instruct you and teach you in the way which you shall go."}
              , {class: "q2", text: "I will counsel you with my eye on you."}
              ]
        , 9:  [ {class: "q", text: "Don’t be like the horse, or like the mule, which have no understanding,"}
              , {class: "q2", text: "who are controlled by bit and bridle, or else they will not come near to you."}
              ]
        , 10: [ {class: "q", text: "Many sorrows come to the wicked,"}
              , {class: "q2", text: "but loving kindness shall surround him who trusts in Yahweh."}
              ]
        , 11: [ {class: "q", text: "Be glad in Yahweh, and rejoice, you righteous!"}
              , {class: "q2", text: "Shout for joy, all you who are upright in heart!"}
              ]
        }
  , 33: { 1:  [ {class: "q", text: "Rejoice in Yahweh, you righteous!"}
              , {class: "q2", text: "Praise is fitting for the upright."}
              ]
        , 2:  [ {class: "q", text: "Give thanks to Yahweh with the lyre."}
              , {class: "q2", text: "Sing praises to him with the harp of ten strings."}
              ]
        , 3:  [ {class: "q", text: "Sing to him a new song."}
              , {class: "q2", text: "Play skillfully with a shout of joy!"}
              ]
        , 4:  [ {class: "q", text: "For Yahweh’s word is right."}
              , {class: "q2", text: "All his work is done in faithfulness."}
              ]
        , 5:  [ {class: "q", text: "He loves righteousness and justice."}
              , {class: "q2", text: "The earth is full of the loving kindness of Yahweh."}
              ]
        , 6:  [ {class: "q", text: "By Yahweh’s word, the heavens were made:"}
              , {class: "q2", text: "all their army by the breath of his mouth."}
              ]
        , 7:  [ {class: "q", text: "He gathers the waters of the sea together as a heap."}
              , {class: "q2", text: "He lays up the deeps in storehouses."}
              ]
        , 8:  [ {class: "q", text: "Let all the earth fear Yahweh."}
              , {class: "q2", text: "Let all the inhabitants of the world stand in awe of him."}
              ]
        , 9:  [ {class: "q", text: "For he spoke, and it was done."}
              , {class: "q2", text: "He commanded, and it stood firm."}
              ]
        , 10: [ {class: "q", text: "Yahweh brings the counsel of the nations to nothing."}
              , {class: "q2", text: "He makes the thoughts of the peoples to be of no effect."}
              ]
        , 11: [ {class: "q", text: "The counsel of Yahweh stands fast forever,"}
              , {class: "q2", text: "the thoughts of his heart to all generations."}
              ]
        , 12: [ {class: "q", text: "Blessed is the nation whose God is Yahweh,"}
              , {class: "q2", text: "the people whom he has chosen for his own inheritance."}
              ]
        , 13: [ {class: "q", text: "Yahweh looks from heaven."}
              , {class: "q2", text: "He sees all the sons of men."}
              ]
        , 14: [ {class: "q", text: "From the place of his habitation he looks out on all the inhabitants of the earth,"} ]
        , 15: [ {class: "q2", text: "</span> he who fashions all of their hearts;"}
              , {class: "q2", text: "and he considers all of their works."}
              ]
        , 16: [ {class: "q", text: "There is no king saved by the multitude of an army."}
              , {class: "q2", text: "A mighty man is not delivered by great strength."}
              ]
        , 17: [ {class: "q", text: "A horse is a vain thing for safety,"}
              , {class: "q2", text: "neither does he deliver any by his great power."}
              ]
        , 18: [ {class: "q", text: "Behold, Yahweh’s eye is on those who fear him,"}
              , {class: "q2", text: "on those who hope in his loving kindness,"}
              ]
        , 19: [ {class: "q2", text: "</span> to deliver their soul from death,"}
              , {class: "q2", text: "to keep them alive in famine."}
              ]
        , 20: [ {class: "q", text: "Our soul has waited for Yahweh."}
              , {class: "q2", text: "He is our help and our shield."}
              ]
        , 21: [ {class: "q", text: "For our heart rejoices in him,"}
              , {class: "q2", text: "because we have trusted in his holy name."}
              ]
        , 22: [ {class: "q", text: "Let your loving kindness be on us, Yahweh,"}
              , {class: "q2", text: "since we have hoped in you."}
              ]
        }
  , 34: { 1:  [ {class: "q", text: "I will bless Yahweh at all times."}
              , {class: "q2", text: "His praise will always be in my mouth."}
              ]
        , 2:  [ {class: "q", text: "My soul shall boast in Yahweh."}
              , {class: "q2", text: "The humble shall hear of it and be glad."}
              ]
        , 3:  [ {class: "q", text: "Oh magnify Yahweh with me."}
              , {class: "q2", text: "Let’s exalt his name together."}
              ]
        , 4:  [ {class: "q", text: "I sought Yahweh, and he answered me,"}
              , {class: "q2", text: "and delivered me from all my fears."}
              ]
        , 5:  [ {class: "q", text: "They looked to him, and were radiant."}
              , {class: "q2", text: "Their faces shall never be covered with shame."}
              ]
        , 6:  [ {class: "q", text: "This poor man cried, and Yahweh heard him,"}
              , {class: "q2", text: "and saved him out of all his troubles."}
              ]
        , 7:  [ {class: "q", text: "Yahweh’s angel encamps around those who fear him,"}
              , {class: "q2", text: "and delivers them."}
              ]
        , 8:  [ {class: "q", text: "Oh taste and see that Yahweh is good."}
              , {class: "q2", text: "Blessed is the man who takes refuge in him."}
              ]
        , 9:  [ {class: "q", text: "Oh fear Yahweh, you his saints,"}
              , {class: "q2", text: "for there is no lack with those who fear him."}
              ]
        , 10: [ {class: "q", text: "The young lions do lack, and suffer hunger,"}
              ,  {class: "q2", text: "but those who seek Yahweh shall not lack any good thing."}
              ]
        , 11: [ {class: "q", text: "Come, you children, listen to me."}
              , {class: "q2", text: "I will teach you the fear of Yahweh."}
              ]
        , 12: [ {class: "q", text: "Who is someone who desires life,"}
              , {class: "q2", text: "and loves many days, that he may see good?"}
              ]
        , 13: [ {class: "q", text: "Keep your tongue from evil,"}
              , {class: "q2", text: "and your lips from speaking lies."}
              ]
        , 14: [ {class: "q", text: "Depart from evil, and do good."}
              , {class: "q2", text: "Seek peace, and pursue it."}
              ]
        , 15: [ {class: "q", text: "Yahweh’s eyes are toward the righteous."}
              , {class: "q2", text: "His ears listen to their cry."}
              ]
        , 16: [ {class: "q", text: "Yahweh’s face is against those who do evil,"}
              , {class: "q2", text: "to cut off their memory from the earth."}
              ]
        , 17: [ {class: "q", text: "The righteous cry, and Yahweh hears,"}
              , {class: "q2", text: "and delivers them out of all their troubles."}
              ]
        , 18: [ {class: "q", text: "Yahweh is near to those who have a broken heart,"}
              , {class: "q2", text: "and saves those who have a crushed spirit."}
              ]
        , 19: [ {class: "q", text: "Many are the afflictions of the righteous,"}
              , {class: "q2", text: "but Yahweh delivers him out of them all."}
              ]
        , 20: [ {class: "q", text: "He protects all of his bones."}
              , {class: "q2", text: "Not one of them is broken."}
              ]
        , 21: [ {class: "q", text: "Evil shall kill the wicked."}
              , {class: "q2", text: "Those who hate the righteous shall be condemned."}
              ]
        , 22: [ {class: "q", text: "Yahweh redeems the soul of his servants."}
              , {class: "q2", text: "None of those who take refuge in him shall be condemned."}
              ]
        }
        , 35: { 1:  [ {class: "q", text: "Contend, Yahweh, with those who contend with me."}
              , {class: "q2", text: "Fight against those who fight against me."}
              ]
        , 2:  [ {class: "q", text: "Take hold of shield and buckler,"}
              , {class: "q2", text: "and stand up for my help."}
              ]
        , 3:  [ {class: "q", text: "Brandish the spear and block those who pursue me."}
              , {class: "q2", text: "Tell my soul, “I am your salvation.”"}
              ]
        , 4:  [ {class: "q", text: "Let those who seek after my soul be disappointed and brought to dishonor."}
              , {class: "q2", text: "Let those who plot my ruin be turned back and confounded."}
              ]
        , 5:  [ {class: "q", text: "Let them be as chaff before the wind,"}
              , {class: "q2", text: "Yahweh’s angel driving them on."}
              ]
        , 6:  [ {class: "q", text: "Let their way be dark and slippery,"}
              , {class: "q2", text: "Yahweh’s angel pursuing them."}
              ]
        , 7:  [ {class: "q", text: "For without cause they have hidden their net in a pit for me."}
              , {class: "q2", text: "Without cause they have dug a pit for my soul."}
              ]
        , 8:  [ {class: "q", text: "Let destruction come on him unawares."}
              , {class: "q2", text: "Let his net that he has hidden catch himself."}
              , {class: "q2", text: "Let him fall into that destruction."}
              ]
        , 9:  [ {class: "q", text: "My soul shall be joyful in Yahweh."}
              , {class: "q2", text: "It shall rejoice in his salvation."}
              ]
        , 10: [ {class: "q", text: "All my bones shall say, “Yahweh, who is like you,"}
              , {class: "q2", text: "who delivers the poor from him who is too strong for him;"}
              , {class: "q2", text: "yes, the poor and the needy from him who robs him?”"}
              ]
        , 11: [ {class: "q", text: "Unrighteous witnesses rise up."}
              , {class: "q2", text: "They ask me about things that I don’t know about."}
              ]
        , 12: [ {class: "q", text: "They reward me evil for good,"}
              , {class: "q2", text: "to the bereaving of my soul."}
              ]
        , 13: [ {class: "q", text: "But as for me, when they were sick, my clothing was sackcloth."}
              , {class: "q2", text: "I afflicted my soul with fasting."}
              , {class: "q2", text: "My prayer returned into my own bosom."}
              ]
        , 14: [ {class: "q", text: "I behaved myself as though it had been my friend or my brother."}
              , {class: "q2", text: "I bowed down mourning, as one who mourns his mother."}
              ]
        , 15: [ {class: "q", text: "But in my adversity, they rejoiced, and gathered themselves together."}
              , {class: "q2", text: "The attackers gathered themselves together against me, and I didn’t know it."}
              , {class: "q2", text: "They tore at me, and didn’t cease."}
              ]
        , 16: [ {class: "q", text: "Like the profane mockers in feasts,"}
              , {class: "q2", text: "they gnashed their teeth at me."}
              ]
        , 17: [ {class: "q", text: "Lord, how long will you look on?"}
              , {class: "q2", text: "Rescue my soul from their destruction,"}
              , {class: "q2", text: "my precious life from the lions."}
              ]
        , 18: [ {class: "q", text: "I will give you thanks in the great assembly."}
              , {class: "q2", text: "I will praise you among many people."}
              ]
        , 19: [ {class: "q", text: "Don’t let those who are my enemies wrongfully rejoice over me;"}
              , {class: "q2", text: "neither let those who hate me without a cause wink their eyes."}
              ]
        , 20: [ {class: "q", text: "For they don’t speak peace,"}
              , {class: "q2", text: "but they devise deceitful words against those who are quiet in the land."}
              ]
        , 21: [ {class: "q", text: "Yes, they opened their mouth wide against me."}
              , {class: "q2", text: "They said, “Aha! Aha! Our eye has seen it!”"}
              ]
        , 22: [ {class: "q", text: "You have seen it, Yahweh. Don’t keep silent."}
              , {class: "q2", text: "Lord, don’t be far from me."}
              ]
        , 23: [ {class: "q", text: "Wake up! Rise up to defend me, my God!"}
              , {class: "q2", text: "My Lord, contend for me!"}
              ]
        , 24: [ {class: "q", text: "Vindicate me, Yahweh my God, according to your righteousness."}
              , {class: "q2", text: "Don’t let them gloat over me."}
              ]
        , 25: [ {class: "q", text: "Don’t let them say in their heart, “Aha! That’s the way we want it!”"}
              , {class: "q2", text: "Don’t let them say, “We have swallowed him up!”"}
              ]
        , 26: [ {class: "q", text: "Let them be disappointed and confounded together who rejoice at my calamity."}
              , {class: "q2", text: "Let them be clothed with shame and dishonor who magnify themselves against me."}
              ]
        , 27: [ {class: "q", text: "Let those who favor my righteous cause shout for joy and be glad."}
              , {class: "q2", text: "Yes, let them say continually, “May Yahweh be magnified,"}
              , {class: "q2", text: "who has pleasure in the prosperity of his servant!”"}
              ]
        , 28: [ {class: "q", text: "My tongue shall talk about your righteousness and about your praise all day long."} ]
        }
  , 36: { 1:  [ {class: "q", text: "A revelation is within my heart about the disobedience of the wicked:"}
              , {class: "q2", text: "“There is no fear of God before his eyes.”"}
              ]
        , 2:  [ {class: "q", text: "For he flatters himself in his own eyes,"}
              , {class: "q2", text: "too much to detect and hate his sin."}
              ]
        , 3:  [ {class: "q", text: "The words of his mouth are iniquity and deceit."}
              , {class: "q2", text: "He has ceased to be wise and to do good."}
              ]
        , 4:  [ {class: "q", text: "He plots iniquity on his bed."}
              , {class: "q2", text: "He sets himself in a way that is not good."}
              , {class: "q2", text: "He doesn’t abhor evil."}
              ]
        , 5:  [ {class: "q", text: "Your loving kindness, Yahweh, is in the heavens."}
              , {class: "q2", text: "Your faithfulness reaches to the skies."}
              ]
        , 6:  [ {class: "q", text: "Your righteousness is like the mountains of God."}
              , {class: "q2", text: "Your judgments are like a great deep."}
              , {class: "q2", text: "Yahweh, you preserve man and animal."}
              ]
        , 7:  [ {class: "q", text: "How precious is your loving kindness, God!"}
              , {class: "q2", text: "The children of men take refuge under the shadow of your wings."}
              ]
        , 8:  [ {class: "q", text: "They shall be abundantly satisfied with the abundance of your house."}
              , {class: "q2", text: "You will make them drink of the river of your pleasures."}
              ]
        , 9:  [ {class: "q", text: "For with you is the spring of life."}
              , {class: "q2", text: "In your light we will see light."}
              ]
        , 10: [ {class: "q", text: "Oh continue your loving kindness to those who know you,"}
              , {class: "q2", text: "your righteousness to the upright in heart."}
              ]
        , 11: [ {class: "q", text: "Don’t let the foot of pride come against me."}
              , {class: "q2", text: "Don’t let the hand of the wicked drive me away."}
              ]
        , 12: [ {class: "q", text: "There the workers of iniquity are fallen."}
              , {class: "q2", text: "They are thrust down, and shall not be able to rise."}
              ]
        }
  , 37: { 1:  [ {class: "q", text: "Don’t fret because of evildoers,"}
              , {class: "q2", text: "neither be envious against those who work unrighteousness."}
              ]
        , 2:  [ {class: "q", text: "For they shall soon be cut down like the grass,"}
              , {class: "q2", text: "and wither like the green herb."}
              ]
        , 3:  [ {class: "q", text: "Trust in Yahweh, and do good."}
              , {class: "q2", text: "Dwell in the land, and enjoy safe pasture."}
              ]
        , 4:  [ {class: "q", text: "Also delight yourself in Yahweh,"}
              , {class: "q2", text: "and he will give you the desires of your heart."}
              ]
        , 5:  [ {class: "q", text: "Commit your way to Yahweh."}
              , {class: "q2", text: "Trust also in him, and he will do this:"}
              ]
        , 6:  [ {class: "q", text: "he will make your righteousness shine out like light,"}
              , {class: "q2", text: "and your justice as the noon day sun."}
              ]
        , 7:  [ {class: "q", text: "Rest in Yahweh, and wait patiently for him."}
              , {class: "q2", text: "Don’t fret because of him who prospers in his way,"}
              , {class: "q2", text: "because of the man who makes wicked plots happen."}
              ]
        , 8:  [ {class: "q", text: "Cease from anger, and forsake wrath."}
              , {class: "q2", text: "Don’t fret; it leads only to evildoing."}
              ]
        , 9:  [ {class: "q", text: "For evildoers shall be cut off,"}
              , {class: "q2", text: "but those who wait for Yahweh shall inherit the land."}
              ]
        , 10: [ {class: "q", text: "For yet a little while, and the wicked will be no more."}
              , {class: "q2", text: "Yes, though you look for his place, he isn’t there."}
              ]
        , 11: [ {class: "q", text: "But the humble shall inherit the land,"}
              , {class: "q2", text: "and shall delight themselves in the abundance of peace."}
              ]
        , 12: [ {class: "q", text: "The wicked plots against the just,"}
              , {class: "q2", text: "and gnashes at him with his teeth."}
              ]
        , 13: [ {class: "q", text: "The Lord will laugh at him,"}
              , {class: "q2", text: "for he sees that his day is coming."}
              ]
        , 14: [ {class: "q", text: "The wicked have drawn out the sword, and have bent their bow,"}
              ,  {class: "q2", text: "to cast down the poor and needy,"}
              , {class: "q2", text: "to kill those who are upright on the path."}
              ]
        , 15: [ {class: "q", text: "Their sword shall enter into their own heart."}
              , {class: "q2", text: "Their bows shall be broken."}
              ]
        , 16: [ {class: "q", text: "Better is a little that the righteous has,"}
              , {class: "q2", text: "than the abundance of many wicked."}
              ]
        , 17: [ {class: "q", text: "For the arms of the wicked shall be broken,"}
              , {class: "q2", text: "but Yahweh upholds the righteous."}
              ]
        , 18: [ {class: "q", text: "Yahweh knows the days of the perfect."}
              , {class: "q2", text: "Their inheritance shall be forever."}
              ]
        , 19: [ {class: "q", text: "They shall not be disappointed in the time of evil."}
              , {class: "q2", text: "In the days of famine they shall be satisfied."}
              ]
        , 20: [ {class: "q", text: "But the wicked shall perish."}
              , {class: "q2", text: "The enemies of Yahweh shall be like the beauty of the fields."}
              , {class: "q2", text: "They will vanish—"}
              , {class: "q2", text: "vanish like smoke."}
              ]
        , 21: [ {class: "q", text: "The wicked borrow, and don’t pay back,"}
              , {class: "q2", text: "but the righteous give generously."}
              ]
        , 22: [ {class: "q", text: "For such as are blessed by him shall inherit the land."}
              , {class: "q2", text: "Those who are cursed by him shall be cut off."}
              ]
        , 23: [ {class: "q", text: "A man’s steps are established by Yahweh."}
              , {class: "q2", text: "He delights in his way."}
              ]
        , 24: [ {class: "q", text: "Though he stumble, he shall not fall,"}
              , {class: "q2", text: "for Yahweh holds him up with his hand."}
              ]
        , 25: [ {class: "q", text: "I have been young, and now am old,"}
              ,  {class: "q2", text: "yet I have not seen the righteous forsaken,"}
              , {class: "q2", text: "nor his children begging for bread."}
              ]
        , 26: [ {class: "q", text: "All day long he deals graciously, and lends."}
              , {class: "q2", text: "His offspring is blessed."}
              ]
        , 27: [ {class: "q", text: "Depart from evil, and do good."}
              , {class: "q2", text: "Live securely forever."}
              ]
        , 28: [ {class: "q", text: "For Yahweh loves justice,"}
              , {class: "q2", text: "and doesn’t forsake his saints."}
              , {class: "q2", text: "They are preserved forever,"}
              , {class: "q2", text: "but the children of the wicked shall be cut off."}
              ]
        , 29: [ {class: "q", text: "The righteous shall inherit the land,"}
              , {class: "q2", text: "and live in it forever."}
              ]
        , 30: [ {class: "q", text: "The mouth of the righteous talks of wisdom."}
              , {class: "q2", text: "His tongue speaks justice."}
              ]
        , 31: [ {class: "q", text: "The law of his God is in his heart."}
              , {class: "q2", text: "None of his steps shall slide."}
              ]
        , 32: [ {class: "q", text: "The wicked watch the righteous,"}
              , {class: "q2", text: "and seek to kill him."}
              ]
        , 33: [ {class: "q", text: "Yahweh will not leave him in his hand,"}
              , {class: "q2", text: "nor condemn him when he is judged."}
              ]
        , 34: [ {class: "q", text: "Wait for Yahweh, and keep his way,"}
              , {class: "q2", text: "and he will exalt you to inherit the land."}
              , {class: "q2", text: "When the wicked are cut off, you shall see it."}
              ]
        , 35: [ {class: "q", text: "I have seen the wicked in great power,"}
              , {class: "q2", text: "spreading himself like a green tree in its native soil."}
              ]
        , 36: [ {class: "q", text: "But he passed away, and behold, he was not."}
              , {class: "q2", text: "Yes, I sought him, but he could not be found."}
              ]
        , 37: [ {class: "q", text: "Mark the perfect man, and see the upright,"}
              , {class: "q2", text: "for there is a future for the man of peace."}
              ]
        , 38: [ {class: "q", text: "As for transgressors, they shall be destroyed together."}
              ,  {class: "q2", text: "The future of the wicked shall be cut off."}
              ]
        , 39: [ {class: "q", text: "But the salvation of the righteous is from Yahweh."}
              , {class: "q2", text: "He is their stronghold in the time of trouble."}
              ]
        , 40: [ {class: "q", text: "Yahweh helps them and rescues them."}
              , {class: "q2", text: "He rescues them from the wicked and saves them,"}
              , {class: "q2", text: "because they have taken refuge in him."}
              ]
        }
  , 38: { 1:  [ {class: "q", text: "Yahweh, don’t rebuke me in your wrath,"}
              , {class: "q2", text: "neither chasten me in your hot displeasure."}
              ]
        , 2:  [ {class: "q", text: "For your arrows have pierced me,"}
              , {class: "q2", text: "your hand presses hard on me."}
              ]
        , 3:  [ {class: "q", text: "There is no soundness in my flesh because of your indignation,"}
              , {class: "q2", text: "neither is there any health in my bones because of my sin."}
              ]
        , 4:  [ {class: "q", text: "For my iniquities have gone over my head."}
              , {class: "q2", text: "As a heavy burden, they are too heavy for me."}
              ]
        , 5:  [ {class: "q", text: "My wounds are loathsome and corrupt"}
              , {class: "q2", text: "because of my foolishness."}
              ]
        , 6:  [ {class: "q", text: "I am in pain and bowed down greatly."}
              , {class: "q2", text: "I go mourning all day long."}
              ]
        , 7:  [ {class: "q", text: "For my waist is filled with burning."}
              , {class: "q2", text: "There is no soundness in my flesh."}
              ]
        , 8:  [ {class: "q", text: "I am faint and severely bruised."}
              , {class: "q2", text: "I have groaned by reason of the anguish of my heart."}
              ]
        , 9:  [ {class: "q", text: "Lord, all my desire is before you."}
              , {class: "q2", text: "My groaning is not hidden from you."}
              ]
        , 10: [ {class: "q", text: "My heart throbs."}
              , {class: "q2", text: "My strength fails me."}
              , {class: "q2", text: "As for the light of my eyes, it has also left me."}
              ]
        , 11: [ {class: "q", text: "My lovers and my friends stand aloof from my plague."}
              , {class: "q2", text: "My kinsmen stand far away."}
              ]
        , 12: [ {class: "q", text: "They also who seek after my life lay snares."}
              , {class: "q2", text: "Those who seek my hurt speak mischievous things,"}
              , {class: "q2", text: "and meditate deceits all day long."}
              ]
        , 13: [ {class: "q", text: "But I, as a deaf man, don’t hear."}
              , {class: "q2", text: "I am as a mute man who doesn’t open his mouth."}
              ]
        , 14: [ {class: "q", text: "Yes, I am as a man who doesn’t hear,"}
              , {class: "q2", text: "in whose mouth are no reproofs."}
              ]
        , 15: [ {class: "q", text: "For I hope in you, Yahweh."}
              , {class: "q2", text: "You will answer, Lord my God."}
              ]
        , 16: [ {class: "q", text: "For I said, “Don’t let them gloat over me,"}
              , {class: "q2", text: "or exalt themselves over me when my foot slips.”"}
              ]
        , 17: [ {class: "q", text: "For I am ready to fall."}
              , {class: "q2", text: "My pain is continually before me."}
              ]
        , 18: [ {class: "q", text: "For I will declare my iniquity."}
              , {class: "q2", text: "I will be sorry for my sin."}
              ]
        , 19: [ {class: "q", text: "But my enemies are vigorous and many."}
              , {class: "q2", text: "Those who hate me without reason are numerous."}
              ]
        , 20: [ {class: "q", text: "They who render evil for good are also adversaries to me,"}
              , {class: "q2", text: "because I follow what is good."}
              ]
        , 21: [ {class: "q", text: "Don’t forsake me, Yahweh."}
              , {class: "q2", text: "My God, don’t be far from me."}
              ]
        , 22: [ {class: "q", text: "Hurry to help me,"}
              , {class: "q2", text: "Lord, my salvation."}
              ]
        }
  , 39: { 1:  [ {class: "q", text: "I said, “I will watch my ways, so that I don’t sin with my tongue."}
              , {class: "q2", text: "I will keep my mouth with a bridle while the wicked is before me.”"}
              ]
        , 2:  [ {class: "q", text: "I was mute with silence."}
              , {class: "q2", text: "I held my peace, even from good."}
              , {class: "q2", text: "My sorrow was stirred."}
              ]
        , 3:  [ {class: "q", text: "My heart was hot within me."}
              , {class: "q2", text: "While I meditated, the fire burned."}
              , {class: "q", text: "I spoke with my tongue:"}
              ]
        , 4:  [ {class: "q2", text: "/span> “Yahweh, show me my end,"}
              , {class: "q2", text: "what is the measure of my days."}
              , {class: "q2", text: "Let me know how frail I am."}
              ]
        , 5:  [ {class: "q", text: "Behold, you have made my days hand widths."}
              , {class: "q2", text: "My lifetime is as nothing before you."}
              , {class: "q", text: "Surely every man stands as a breath.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "“Surely every man walks like a shadow."}
              , {class: "q2", text: "Surely they busy themselves in vain."}
              , {class: "q2", text: "He heaps up, and doesn’t know who shall gather."}
              ]
        , 7:  [ {class: "q", text: "Now, Lord, what do I wait for?"}
              , {class: "q2", text: "My hope is in you."}
              ]
        , 8:  [ {class: "q", text: "Deliver me from all my transgressions."}
              , {class: "q2", text: "Don’t make me the reproach of the foolish."}
              ]
        , 9:  [ {class: "q", text: "I was mute."}
              , {class: "q2", text: "I didn’t open my mouth,"}
              , {class: "q2", text: "because you did it."}
              ]
        , 10: [ {class: "q", text: "Remove your scourge away from me."}
              , {class: "q2", text: "I am overcome by the blow of your hand."}
              ]
        , 11: [ {class: "q", text: "When you rebuke and correct man for iniquity,"}
              , {class: "q2", text: "you consume his wealth like a moth."}
              , {class: "q", text: "Surely every man is but a breath.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 12: [ {class: "q", text: "“Hear my prayer, Yahweh, and give ear to my cry."}
              , {class: "q2", text: "Don’t be silent at my tears."}
              , {class: "q", text: "For I am a stranger with you,"}
              , {class: "q2", text: "a foreigner, as all my fathers were."}
              ]
        , 13: [ {class: "q", text: "Oh spare me, that I may recover strength,"}
              , {class: "q2", text: "before I go away and exist no more.”"}
              ]
        }
  , 4:  { 1:  [ {class: "q", text: "Answer me when I call, God of my righteousness."}
              , {class: "q2", text: "Give me relief from my distress."}
              , {class: "q2", text: "Have mercy on me, and hear my prayer."}
              ]
        , 2:  [ {class: "q", text: "You sons of men, how long shall my glory be turned into dishonor?"}
              , {class: "q2", text: "Will you love vanity and seek after falsehood?"}
              , {class: "qs", text: "Selah"}
              ]
        , 3:  [ {class: "q", text: "But know that Yahweh has set apart for himself him who is godly:"}
              , {class: "q2", text: "Yahweh will hear when I call to him."}
              ]
        , 4:  [ {class: "q", text: "Stand in awe, and don’t sin."}
              , {class: "q2", text: "Search your own heart on your bed, and be still."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "Offer the sacrifices of righteousness."}
              , {class: "q2", text: "Put your trust in Yahweh."}
              ]
        , 6:  [ {class: "q", text: "Many say, “Who will show us any good?”"}
              , {class: "q2", text: "Yahweh, let the light of your face shine on us."}
              ]
        , 7:  [ {class: "q", text: "You have put gladness in my heart,"}
              , {class: "q2", text: "more than when their grain and their new wine are increased."}
              ]
        , 8:  [ {class: "q", text: "In peace I will both lay myself down and sleep,"}
              , {class: "q2", text: "for you, Yahweh alone, make me live in safety."}
              ]
        }
  , 40: { 1:  [ {class: "q", text: "I waited patiently for Yahweh."}
              , {class: "q2", text: "He turned to me, and heard my cry."}
              ]
        , 2:  [ {class: "q", text: "He brought me up also out of a horrible pit,"}
              , {class: "q2", text: "out of the miry clay."}
              , {class: "q", text: "He set my feet on a rock,"}
              , {class: "q2", text: "and gave me a firm place to stand."}
              ]
        , 3:  [ {class: "q", text: "He has put a new song in my mouth, even praise to our God."}
              , {class: "q2", text: "Many shall see it, and fear, and shall trust in Yahweh."}
              ]
        , 4:  [ {class: "q", text: "Blessed is the man who makes Yahweh his trust,"}
              , {class: "q2", text: "and doesn’t respect the proud, nor such as turn away to lies."}
              ]
        , 5:  [ {class: "q", text: "Many, Yahweh, my God, are the wonderful works which you have done,"}
              , {class: "q2", text: "and your thoughts which are toward us."}
              , {class: "q", text: "They can’t be declared back to you."}
              , {class: "q2", text: "If I would declare and speak of them, they are more than can be counted."}
              ]
        , 6:  [ {class: "q", text: "Sacrifice and offering you didn’t desire."}
              , {class: "q2", text: "You have opened my ears."}
              , {class: "q2", text: "You have not required burnt offering and sin offering."}
              ]
        , 7:  [ {class: "q", text: "Then I said, “Behold, I have come."}
              , {class: "q2", text: "It is written about me in the book in the scroll."}
              ]
        , 8:  [ {class: "q", text: "I delight to do your will, my God."}
              , {class: "q2", text: "Yes, your law is within my heart.”"}
              ]
        , 9:  [ {class: "q", text: "I have proclaimed glad news of righteousness in the great assembly."}
              , {class: "q2", text: "Behold, I will not seal my lips, Yahweh, you know."}
              ]
        , 10: [ {class: "q", text: "I have not hidden your righteousness within my heart."}
              , {class: "q2", text: "I have declared your faithfulness and your salvation."}
              , {class: "q2", text: "I have not concealed your loving kindness and your truth from the great assembly."}
              ]
        , 11: [ {class: "q", text: "Don’t withhold your tender mercies from me, Yahweh."}
              , {class: "q2", text: "Let your loving kindness and your truth continually preserve me."}
              ]
        , 12: [ {class: "q", text: "For innumerable evils have surrounded me."}
              , {class: "q2", text: "My iniquities have overtaken me, so that I am not able to look up."}
              , {class: "q", text: "They are more than the hairs of my head."}
              , {class: "q2", text: "My heart has failed me."}
              ]
        , 13: [ {class: "q", text: "Be pleased, Yahweh, to deliver me."}
              , {class: "q2", text: "Hurry to help me, Yahweh."}
              ]
        , 14: [ {class: "q", text: "Let them be disappointed and confounded together who seek after my soul to destroy it."}
              , {class: "q2", text: "Let them be turned backward and brought to dishonor who delight in my hurt."}
              ]
        , 15: [ {class: "q", text: "Let them be desolate by reason of their shame that tell me, “Aha! Aha!”"} ]
        , 16: [ {class: "q", text: "Let all those who seek you rejoice and be glad in you."}
              , {class: "q2", text: "Let such as love your salvation say continually, “Let Yahweh be exalted!”"}
              ]
        , 17: [ {class: "q", text: "But I am poor and needy."}
              , {class: "q2", text: "May the Lord think about me."}
              , {class: "q", text: "You are my help and my deliverer."}
              , {class: "q2", text: "Don’t delay, my God."}
              ]
        }
  , 41: { 1:  [ {class: "q", text: "Blessed is he who considers the poor."}
              , {class: "q2", text: "Yahweh will deliver him in the day of evil."}
              ]
        , 2:  [ {class: "q", text: "Yahweh will preserve him, and keep him alive."}
              , {class: "q2", text: "He shall be blessed on the earth,"}
              , {class: "q2", text: "and he will not surrender him to the will of his enemies."}
              ]
        , 3:  [ {class: "q", text: "Yahweh will sustain him on his sickbed,"}
              , {class: "q2", text: "and restore him from his bed of illness."}
              ]
        , 4:  [ {class: "q", text: "I said, “Yahweh, have mercy on me!"}
              , {class: "q2", text: "Heal me, for I have sinned against you.”"}
              ]
        , 5:  [ {class: "q", text: "My enemies speak evil against me:"}
              , {class: "q2", text: "“When will he die, and his name perish?”"}
              ]
        , 6:  [ {class: "q", text: "If he comes to see me, he speaks falsehood."}
              , {class: "q2", text: "His heart gathers iniquity to itself."}
              , {class: "q2", text: "When he goes abroad, he tells it."}
              ]
        , 7:  [ {class: "q", text: "All who hate me whisper together against me."}
              , {class: "q2", text: "They imagine the worst for me."}
              ]
        , 8:  [ {class: "q", text: "“An evil disease”, they say, “has afflicted him."}
              , {class: "q2", text: "Now that he lies he shall rise up no more.”"}
              ]
        , 9:  [ {class: "q", text: "Yes, my own familiar friend, in whom I trusted,"}
              , {class: "q2", text: "who ate bread with me,"}
              , {class: "q2", text: "has lifted up his heel against me."}
              ]
        , 10: [ {class: "q", text: "But you, Yahweh, have mercy on me, and raise me up,"}
              , {class: "q2", text: "that I may repay them."}
              ]
        , 11: [ {class: "q", text: "By this I know that you delight in me,"}
              , {class: "q2", text: "because my enemy doesn’t triumph over me."}
              ]
        , 12: [ {class: "q", text: "As for me, you uphold me in my integrity,"}
              , {class: "q2", text: "and set me in your presence forever."}
              ]
        , 13: [ {class: "q", text: "Blessed be Yahweh, the God of Israel,"}
              , {class: "q2", text: "from everlasting and to everlasting!"}
              , {class: "q", text: "Amen and amen."}
              ]
        }
  , 42: { 1:  [ {class: "q", text: "As the deer pants for the water brooks,"}
              , {class: "q2", text: "so my soul pants after you, God."}
              ]
        , 2:  [ {class: "q", text: "My soul thirsts for God, for the living God."}
              , {class: "q2", text: "When shall I come and appear before God?"}
              ]
        , 3:  [ {class: "q", text: "My tears have been my food day and night,"}
              , {class: "q2", text: "while they continually ask me, “Where is your God?”"}
              ]
        , 4:  [ {class: "q", text: "These things I remember, and pour out my soul within me,"}
              ,  {class: "q2", text: "how I used to go with the crowd, and led them to God’s house,"}
              , {class: "q2", text: "with the voice of joy and praise, a multitude keeping a holy day."}
              ]
        , 5:  [ {class: "q", text: "Why are you in despair, my soul?"}
              , {class: "q2", text: "Why are you disturbed within me?"}
              , {class: "q", text: "Hope in God!"}
              , {class: "q2", text: "For I shall still praise him for the saving help of his presence."}
              ]
        , 6:  [ {class: "q", text: "My God, my soul is in despair within me."}
              , {class: "q2", text: "Therefore I remember you from the land of the Jordan,"}
              , {class: "q2", text: "the heights of Hermon, from the hill Mizar."}
              ]
        , 7:  [ {class: "q", text: "Deep calls to deep at the noise of your waterfalls."}
              , {class: "q2", text: "All your waves and your billows have swept over me."}
              ]
        , 8:  [ {class: "q", text: "Yahweh will command his loving kindness in the daytime."}
              , {class: "q2", text: "In the night his song shall be with me:"}
              , {class: "q2", text: "a prayer to the God of my life."}
              ]
        , 9:  [ {class: "q", text: "I will ask God, my rock, “Why have you forgotten me?"}
              , {class: "q2", text: "Why do I go mourning because of the oppression of the enemy?”"}
              ]
        , 10: [ {class: "q", text: "As with a sword in my bones, my adversaries reproach me,"}
              , {class: "q2", text: "while they continually ask me, “Where is your God?”"}
              ]
        , 11: [ {class: "q", text: "Why are you in despair, my soul?"}
              ,  {class: "q2", text: "Why are you disturbed within me?"}
              , {class: "q", text: "Hope in God! For I shall still praise him,"}
              , {class: "q2", text: "the saving help of my countenance, and my God."}
              ]
        }
  , 43: { 1:  [ {class: "q", text: "Vindicate me, God, and plead my cause against an ungodly nation."}
              , {class: "q2", text: "Oh, deliver me from deceitful and wicked men."}
              ]
        , 2:  [ {class: "q", text: "For you are the God of my strength. Why have you rejected me?"}
              , {class: "q2", text: "Why do I go mourning because of the oppression of the enemy?"}
              ]
        , 3:  [ {class: "q", text: "Oh, send out your light and your truth."}
              , {class: "q2", text: "Let them lead me."}
              , {class: "q2", text: "Let them bring me to your holy hill,"}
              , {class: "q2", text: "to your tents."}
              ]
        , 4:  [ {class: "q", text: "Then I will go to the altar of God,"}
              , {class: "q2", text: "to God, my exceeding joy."}
              , {class: "q", text: "I will praise you on the harp, God, my God."}
              ]
        , 5:  [ {class: "q", text: "Why are you in despair, my soul?"}
              , {class: "q2", text: "Why are you disturbed within me?"}
              , {class: "q", text: "Hope in God!"}
              , {class: "q2", text: "For I shall still praise him:"}
              , {class: "q2", text: "my Savior, my helper, and my God."}
              ]
        }
  , 44: { 1:  [ {class: "q", text: "We have heard with our ears, God;"}
              , {class: "q2", text: "our fathers have told us what work you did in their days,"}
              , {class: "q2", text: "in the days of old."}
              ]
        , 2:  [ {class: "q", text: "You drove out the nations with your hand,"}
              , {class: "q2", text: "but you planted them."}
              , {class: "q", text: "You afflicted the peoples,"}
              , {class: "q2", text: "but you spread them abroad."}
              ]
        , 3:  [ {class: "q", text: "For they didn’t get the land in possession by their own sword,"}
              , {class: "q2", text: "neither did their own arm save them;"}
              , {class: "q", text: "but your right hand, your arm, and the light of your face,"}
              , {class: "q2", text: "because you were favorable to them."}
              ]
        , 4:  [ {class: "q", text: "You are my King, God."}
              , {class: "q2", text: "Command victories for Jacob!"}
              ]
        , 5:  [ {class: "q", text: "Through you, will we push down our adversaries."}
              , {class: "q2", text: "Through your name, we will tread down those who rise up against us."}
              ]
        , 6:  [ {class: "q", text: "For I will not trust in my bow,"}
              , {class: "q2", text: "neither shall my sword save me."}
              ]
        , 7:  [ {class: "q", text: "But you have saved us from our adversaries,"}
              , {class: "q2", text: "and have shamed those who hate us."}
              ]
        , 8:  [ {class: "q", text: "In God we have made our boast all day long,"}
              , {class: "q2", text: "we will give thanks to your name forever."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "But now you rejected us, and brought us to dishonor,"}
              , {class: "q2", text: "and don’t go out with our armies."}
              ]
        , 10: [ {class: "q", text: "You make us turn back from the adversary."}
              , {class: "q2", text: "Those who hate us take plunder for themselves."}
              ]
        , 11: [ {class: "q", text: "You have made us like sheep for food,"}
              , {class: "q2", text: "and have scattered us among the nations."}
              ]
        , 12: [ {class: "q", text: "You sell your people for nothing,"}
              , {class: "q2", text: "and have gained nothing from their sale."}
              ]
        , 13: [ {class: "q", text: "You make us a reproach to our neighbors,"}
              , {class: "q2", text: "a scoffing and a derision to those who are around us."}
              ]
        , 14: [ {class: "q", text: "You make us a byword among the nations,"}
              , {class: "q2", text: "a shaking of the head among the peoples."}
              ]
        , 15: [ {class: "q", text: "All day long my dishonor is before me,"}
              , {class: "q2", text: "and shame covers my face,"}
              ]
        , 16: [ {class: "q2", text: "</span> at the taunt of one who reproaches and verbally abuses,"}
              , {class: "q2", text: "because of the enemy and the avenger."}
              ]
        , 17: [ {class: "q", text: "All this has come on us,"}
              , {class: "q2", text: "yet we haven’t forgotten you."}
              , {class: "q2", text: "We haven’t been false to your covenant."}
              ]
        , 18: [ {class: "q", text: "Our heart has not turned back,"}
              , {class: "q2", text: "neither have our steps strayed from your path,"}
              ]
        , 19: [ {class: "q2", text: "</span> though you have crushed us in the haunt of jackals,"}
              , {class: "q2", text: "and covered us with the shadow of death."}
              ]
        , 20: [ {class: "q", text: "If we have forgotten the name of our God,"}
              , {class: "q2", text: "or spread out our hands to a strange god,"}
              ]
        , 21: [ {class: "q2", text: "</span> won’t God search this out?"}
              , {class: "q2", text: "For he knows the secrets of the heart."}
              ]
        , 22: [ {class: "q", text: "Yes, for your sake we are killed all day long."}
              , {class: "q2", text: "We are regarded as sheep for the slaughter."}
              ]
        , 23: [ {class: "q", text: "Wake up!"}
              , {class: "q2", text: "Why do you sleep, Lord?"}
              , {class: "q", text: "Arise!"}
              , {class: "q2", text: "Don’t reject us forever."}
              ]
        , 24: [ {class: "q", text: "Why do you hide your face,"}
              , {class: "q2", text: "and forget our affliction and our oppression?"}
              ]
        , 25: [ {class: "q", text: "For our soul is bowed down to the dust."}
              , {class: "q2", text: "Our body clings to the earth."}
              ]
        , 26: [ {class: "q", text: "Rise up to help us."}
              , {class: "q2", text: "Redeem us for your loving kindness’ sake."}
              ]
        }
  , 45: { 1:  [ {class: "q", text: "My heart overflows with a noble theme."}
              , {class: "q2", text: "I recite my verses for the king."}
              , {class: "q2", text: "My tongue is like the pen of a skillful writer."}
              ]
        , 2:  [ {class: "q", text: "You are the most excellent of the sons of men."}
              , {class: "q2", text: "Grace has anointed your lips,"}
              , {class: "q2", text: "therefore God has blessed you forever."}
              ]
        , 3:  [ {class: "q", text: "Strap your sword on your thigh, mighty one:"}
              , {class: "q2", text: "your splendor and your majesty."}
              ]
        , 4:  [ {class: "q", text: "In your majesty ride on victoriously on behalf of truth, humility, and righteousness."}
              , {class: "q2", text: "Let your right hand display awesome deeds."}
              ]
        , 5:  [ {class: "q", text: "Your arrows are sharp."}
              , {class: "q2", text: "The nations fall under you, with arrows in the heart of the king’s enemies."}
              ]
        , 6:  [ {class: "q", text: "Your throne, God, is forever and ever."}
              , {class: "q2", text: "A scepter of equity is the scepter of your kingdom."}
              ]
        , 7:  [ {class: "q", text: "You have loved righteousness, and hated wickedness."}
              , {class: "q2", text: "Therefore God, your God, has anointed you with the oil of gladness above your fellows."}
              ]
        , 8:  [ {class: "q", text: "All your garments smell like myrrh, aloes, and cassia."}
              , {class: "q2", text: "Out of ivory palaces stringed instruments have made you glad."}
              ]
        , 9:  [ {class: "q", text: "Kings’ daughters are among your honorable women."}
              , {class: "q2", text: "At your right hand the queen stands in gold of Ophir."}
              ]
        , 10: [ {class: "q", text: "Listen, daughter, consider, and turn your ear."}
              , {class: "q2", text: "Forget your own people, and also your father’s house."}
              ]
        , 11: [ {class: "q2", text: "</span> So the king will desire your beauty,"}
              , {class: "q2", text: "honor him, for he is your lord."}
              ]
        , 12: [ {class: "q", text: "The daughter of Tyre comes with a gift."}
              , {class: "q2", text: "The rich among the people entreat your favor."}
              ]
        , 13: [ {class: "q", text: "The princess inside is all glorious."}
              , {class: "q2", text: "Her clothing is interwoven with gold."}
              ]
        , 14: [ {class: "q", text: "She shall be led to the king in embroidered work."}
              , {class: "q2", text: "The virgins, her companions who follow her, shall be brought to you."}
              ]
        , 15: [ {class: "q", text: "With gladness and rejoicing they shall be led."}
              , {class: "q2", text: "They shall enter into the king’s palace."}
              ]
        , 16: [ {class: "q", text: "Your sons will take the place of your fathers."}
              , {class: "q2", text: "You shall make them princes in all the earth."}
              ]
        , 17: [ {class: "q", text: "I will make your name to be remembered in all generations."}
              , {class: "q2", text: "Therefore the peoples shall give you thanks forever and ever."}
              ]
        }
  , 46: { 1:  [ {class: "q", text: "God is our refuge and strength,"}
              , {class: "q2", text: "a very present help in trouble."}
              ]
        , 2:  [ {class: "q", text: "Therefore we won’t be afraid, though the earth changes,"}
              , {class: "q2", text: "though the mountains are shaken into the heart of the seas;"}
              ]
        , 3:  [ {class: "q2", text: "/span> though its waters roar and are troubled,"}
              , {class: "q2", text: "though the mountains tremble with their swelling."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "There is a river, the streams of which make the city of God glad,"}
              , {class: "q2", text: "the holy place of the tents of the Most High."}
              ]
        , 5:  [ {class: "q", text: "God is within her. She shall not be moved."}
              , {class: "q2", text: "God will help her at dawn."}
              ]
        , 6:  [ {class: "q", text: "The nations raged. The kingdoms were moved."}
              , {class: "q2", text: "He lifted his voice and the earth melted."}
              ]
        , 7:  [ {class: "q", text: "Yahweh of Armies is with us."}
              , {class: "q2", text: "The God of Jacob is our refuge."}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "Come, see Yahweh’s works,"}
              , {class: "q2", text: "what desolations he has made in the earth."}
              ]
        , 9:  [ {class: "q", text: "He makes wars cease to the end of the earth."}
              , {class: "q2", text: "He breaks the bow, and shatters the spear."}
              , {class: "q2", text: "He burns the chariots in the fire."}
              ]
        , 10: [ {class: "q", text: "“Be still, and know that I am God."}
              , {class: "q2", text: "I will be exalted among the nations."}
              , {class: "q2", text: "I will be exalted in the earth.”"}
              ]
        , 11: [ {class: "q", text: "Yahweh of Armies is with us."}
              , {class: "q2", text: "The God of Jacob is our refuge."}
              , {class: "qs", text: "Selah"}
              ]
        }
  , 47: { 1:  [ {class: "q", text: "Oh clap your hands, all you nations."}
              , {class: "q2", text: "Shout to God with the voice of triumph!"}
              ]
        , 2:  [ {class: "q", text: "For Yahweh Most High is awesome."}
              , {class: "q2", text: "He is a great King over all the earth."}
              ]
        , 3:  [ {class: "q", text: "He subdues nations under us,"}
              , {class: "q2", text: "and peoples under our feet."}
              ]
        , 4:  [ {class: "q", text: "He chooses our inheritance for us,"}
              , {class: "q2", text: "the glory of Jacob whom he loved."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "God has gone up with a shout,"}
              , {class: "q2", text: "Yahweh with the sound of a trumpet."}
              ]
        , 6:  [ {class: "q", text: "Sing praises to God! Sing praises!"}
              , {class: "q2", text: "Sing praises to our King! Sing praises!"}
              ]
        , 7:  [ {class: "q", text: "For God is the King of all the earth."}
              , {class: "q2", text: "Sing praises with understanding."}
              ]
        , 8:  [ {class: "q", text: "God reigns over the nations."}
              , {class: "q2", text: "God sits on his holy throne."}
              ]
        , 9:  [ {class: "q", text: "The princes of the peoples are gathered together,"}
              , {class: "q", text: "the people of the God of Abraham."}
              , {class: "q2", text: "For the shields of the earth belong to God."}
              , {class: "q2", text: "He is greatly exalted!"}
              ]
        }
  , 48: { 1:  [ {class: "q", text: "Great is Yahweh, and greatly to be praised,"}
              , {class: "q2", text: "in the city of our God, in his holy mountain."}
              ]
        , 2:  [ {class: "q", text: "Beautiful in elevation, the joy of the whole earth,"}
              , {class: "q2", text: "is Mount Zion, on the north sides,"}
              , {class: "q2", text: "the city of the great King."}
              ]
        , 3:  [ {class: "q", text: "God has shown himself in her citadels as a refuge."} ]
        , 4:  [ {class: "q", text: "For, behold, the kings assembled themselves,"}
              , {class: "q2", text: "they passed by together."}
              ]
        , 5:  [ {class: "q", text: "They saw it, then they were amazed."}
              , {class: "q2", text: "They were dismayed."}
              , {class: "q2", text: "They hurried away."}
              ]
        , 6:  [ {class: "q", text: "Trembling took hold of them there,"}
              , {class: "q2", text: "pain, as of a woman in travail."}
              ]
        , 7:  [ {class: "q", text: "With the east wind, you break the ships of Tarshish."} ]
        , 8:  [ {class: "q", text: "As we have heard, so we have seen,"}
              , {class: "q2", text: "in the city of Yahweh of Armies, in the city of our God."}
              , {class: "q", text: "God will establish it forever."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "We have thought about your loving kindness, God,"}
              , {class: "q2", text: "in the middle of your temple."}
              ]
        , 10: [ {class: "q", text: "As is your name, God,"}
              , {class: "q2", text: "so is your praise to the ends of the earth."}
              , {class: "q2", text: "Your right hand is full of righteousness."}
              ]
        , 11: [ {class: "q", text: "Let Mount Zion be glad!"}
              , {class: "q2", text: "Let the daughters of Judah rejoice because of your judgments."}
              ]
        , 12: [ {class: "q", text: "Walk about Zion, and go around her."}
              , {class: "q2", text: "Number its towers."}
              ]
        , 13: [ {class: "q", text: "Notice her bulwarks."}
              , {class: "q2", text: "Consider her palaces,"}
              , {class: "q2", text: "that you may tell it to the next generation."}
              ]
        , 14: [ {class: "q", text: "For this God is our God forever and ever."}
              , {class: "q2", text: "He will be our guide even to death."}
              ]
        }
  , 49: { 1:  [ {class: "q", text: "Hear this, all you peoples."}
              , {class: "q2", text: "Listen, all you inhabitants of the world,"}
              ]
        , 2:  [ {class: "q2", text: "/span> both low and high,"}
              , {class: "q2", text: "rich and poor together."}
              ]
        , 3:  [ {class: "q", text: "My mouth will speak words of wisdom."}
              , {class: "q2", text: "My heart will utter understanding."}
              ]
        , 4:  [ {class: "q", text: "I will incline my ear to a proverb."}
              , {class: "q2", text: "I will solve my riddle on the harp."}
              ]
        , 5:  [ {class: "q", text: "Why should I fear in the days of evil,"}
              , {class: "q2", text: "when iniquity at my heels surrounds me?"}
              ]
        , 6:  [ {class: "q", text: "Those who trust in their wealth,"}
              , {class: "q2", text: "and boast in the multitude of their riches—"}
              ]
        , 7:  [ {class: "q2", text: "/span> none of them can by any means redeem his brother,"}
              , {class: "q2", text: "nor give God a ransom for him."}
              ]
        , 8:  [ {class: "q", text: "For the redemption of their life is costly,"}
              , {class: "q2", text: "no payment is ever enough,"}
              ]
        , 9:  [ {class: "q2", text: "/span> that he should live on forever,"}
              , {class: "q2", text: "that he should not see corruption."}
              ]
        , 10: [ {class: "q", text: "For he sees that wise men die;"}
              , {class: "q2", text: "likewise the fool and the senseless perish,"}
              , {class: "q2", text: "and leave their wealth to others."}
              ]
        , 11: [ {class: "q", text: "Their inward thought is that their houses will endure forever,"}
              , {class: "q2", text: "and their dwelling places to all generations."}
              , {class: "q2", text: "They name their lands after themselves."}
              ]
        , 12: [ {class: "q", text: "But man, despite his riches, doesn’t endure."}
              , {class: "q2", text: "He is like the animals that perish."}
              ]
        , 13: [ {class: "q", text: "This is the destiny of those who are foolish,"}
              , {class: "q2", text: "and of those who approve their sayings."}
              , {class: "qs", text: "Selah"}
              ]
        , 14: [ {class: "q", text: "They are appointed as a flock for Sheol."}
              , {class: "q2", text: "Death shall be their shepherd."}
              , {class: "q", text: "The upright shall have dominion over them in the morning."}
              , {class: "q2", text: "Their beauty shall decay in Sheol,"}
              , {class: "q2", text: "far from their mansion."}
              ]
        , 15: [ {class: "q", text: "But God will redeem my soul from the power of Sheol,"}
              , {class: "q2", text: "for he will receive me."}
              , {class: "qs", text: "Selah"}
              ]
        , 16: [ {class: "q", text: "Don’t be afraid when a man is made rich,"}
              ,  {class: "q2", text: "when the glory of his house is increased;"}
              ]
        , 17: [ {class: "q", text: "for when he dies he will carry nothing away."}
              , {class: "q2", text: "His glory won’t descend after him."}
              ]
        , 18: [ {class: "q", text: "Though while he lived he blessed his soul—"}
              , {class: "q2", text: "and men praise you when you do well for yourself—"}
              ]
        , 19: [ {class: "q2", text: "</span> he shall go to the generation of his fathers."}
              , {class: "q2", text: "They shall never see the light."}
              ]
        , 20: [ {class: "q", text: "A man who has riches without understanding,"}
              , {class: "q2", text: "is like the animals that perish."}
              ]
        }
  , 5:  { 1:  [ {class: "q", text: "Give ear to my words, Yahweh."}
              , {class: "q2", text: "Consider my meditation."}
              ]
        , 2:  [ {class: "q", text: "Listen to the voice of my cry, my King and my God;"}
              , {class: "q2", text: "for I pray to you."}
              ]
        , 3:  [ {class: "q", text: "Yahweh, in the morning you will hear my voice."}
              ,  {class: "q2", text: "In the morning I will lay my requests before you, and will watch expectantly."}
              ]
        , 4:  [ {class: "q", text: "For you are not a God who has pleasure in wickedness."}
              , {class: "q2", text: "Evil can’t live with you."}
              ]
        , 5:  [ {class: "q", text: "The arrogant will not stand in your sight."}
              , {class: "q2", text: "You hate all workers of iniquity."}
              ]
        , 6:  [ {class: "q", text: "You will destroy those who speak lies."}
              , {class: "q2", text: "Yahweh abhors the bloodthirsty and deceitful man."}
              ]
        , 7:  [ {class: "q", text: "But as for me, in the abundance of your loving kindness I will come into your house."}
              , {class: "q2", text: "I will bow toward your holy temple in reverence of you."}
              ]
        , 8:  [ {class: "q", text: "Lead me, Yahweh, in your righteousness because of my enemies."}
              , {class: "q2", text: "Make your way straight before my face."}
              ]
        , 9:  [ {class: "q", text: "For there is no faithfulness in their mouth."}
              , {class: "q2", text: "Their heart is destruction."}
              , {class: "q2", text: "Their throat is an open tomb."}
              , {class: "q2", text: "They flatter with their tongue."}
              ]
        , 10: [ {class: "q", text: "Hold them guilty, God."}
              , {class: "q2", text: "Let them fall by their own counsels."}
              , {class: "q", text: "Thrust them out in the multitude of their transgressions,"}
              , {class: "q2", text: "for they have rebelled against you."}
              ]
        , 11: [ {class: "q", text: "But let all those who take refuge in you rejoice."}
              ,  {class: "q2", text: "Let them always shout for joy, because you defend them."}
              , {class: "q", text: "Let them also who love your name be joyful in you."}
              ]
        , 12: [ {class: "q2", text: "</span> For you will bless the righteous."}
              , {class: "q", text: "Yahweh, you will surround him with favor as with a shield."}
              ]
        }
  , 50: { 1:  [ {class: "q", text: "The Mighty One, God, Yahweh, speaks,"}
              , {class: "q2", text: "and calls the earth from sunrise to sunset."}
              ]
        , 2:  [ {class: "q", text: "Out of Zion, the perfection of beauty,"}
              , {class: "q2", text: "God shines out."}
              ]
        , 3:  [ {class: "q", text: "Our God comes, and does not keep silent."}
              , {class: "q2", text: "A fire devours before him."}
              , {class: "q2", text: "It is very stormy around him."}
              ]
        , 4:  [ {class: "q", text: "He calls to the heavens above,"}
              , {class: "q2", text: "to the earth, that he may judge his people:"}
              ]
        , 5:  [ {class: "q", text: "“Gather my saints together to me,"}
              , {class: "q2", text: "those who have made a covenant with me by sacrifice.”"}
              ]
        , 6:  [ {class: "q", text: "The heavens shall declare his righteousness,"}
              , {class: "q2", text: "for God himself is judge."}
              , {class: "qs", text: "Selah"}
              ]
        , 7:  [ {class: "q", text: "“Hear, my people, and I will speak."}
              ,  {class: "q2", text: "Israel, I will testify against you."}
              , {class: "q", text: "I am God, your God."}
              ]
        , 8:  [ {class: "q", text: "I don’t rebuke you for your sacrifices."}
              , {class: "q2", text: "Your burnt offerings are continually before me."}
              ]
        , 9:  [ {class: "q", text: "I have no need for a bull from your stall,"}
              , {class: "q2", text: "nor male goats from your pens."}
              ]
        , 10: [ {class: "q", text: "For every animal of the forest is mine,"}
              , {class: "q2", text: "and the livestock on a thousand hills."}
              ]
        , 11: [ {class: "q", text: "I know all the birds of the mountains."}
              , {class: "q2", text: "The wild animals of the field are mine."}
              ]
        , 12: [ {class: "q", text: "If I were hungry, I would not tell you,"}
              , {class: "q2", text: "for the world is mine, and all that is in it."}
              ]
        , 13: [ {class: "q", text: "Will I eat the meat of bulls,"}
              ,  {class: "q2", text: "or drink the blood of goats?"}
              ]
        , 14: [ {class: "q", text: "Offer to God the sacrifice of thanksgiving."}
              , {class: "q2", text: "Pay your vows to the Most High."}
              ]
        , 15: [ {class: "q", text: "Call on me in the day of trouble."}
              , {class: "q2", text: "I will deliver you, and you will honor me.”"}
              ]
        , 16: [ {class: "q", text: "But to the wicked God says,"}
              , {class: "q2", text: "“What right do you have to declare my statutes,"}
              , {class: "q2", text: "that you have taken my covenant on your lips,"}
              ]
        , 17: [ {class: "q2", text: "</span> since you hate instruction,"}
              , {class: "q2", text: "and throw my words behind you?"}
              ]
        , 18: [ {class: "q", text: "When you saw a thief, you consented with him,"}
              , {class: "q2", text: "and have participated with adulterers."}
              ]
        , 19: [ {class: "q", text: "“You give your mouth to evil."}
              , {class: "q2", text: "Your tongue frames deceit."}
              ]
        , 20: [ {class: "q", text: "You sit and speak against your brother."}
              , {class: "q2", text: "You slander your own mother’s son."}
              ]
        , 21: [ {class: "q", text: "You have done these things, and I kept silent."}
              , {class: "q2", text: "You thought that I was just like you."}
              , {class: "q2", text: "I will rebuke you, and accuse you in front of your eyes."}
              ]
        , 22: [ {class: "q", text: "“Now consider this, you who forget God,"}
              , {class: "q2", text: "lest I tear you into pieces, and there be no one to deliver."}
              ]
        , 23: [ {class: "q", text: "Whoever offers the sacrifice of thanksgiving glorifies me,"}
              , {class: "q2", text: "and prepares his way so that I will show God’s salvation to him.”"}
              ]
        }
  , 51: { 1:  [ {class: "q", text: "Have mercy on me, God, according to your loving kindness."}
              , {class: "q2", text: "According to the multitude of your tender mercies, blot out my transgressions."}
              ]
        , 2:  [ {class: "q", text: "Wash me thoroughly from my iniquity."}
              , {class: "q2", text: "Cleanse me from my sin."}
              ]
        , 3:  [ {class: "q", text: "For I know my transgressions."}
              , {class: "q2", text: "My sin is constantly before me."}
              ]
        , 4:  [ {class: "q", text: "Against you, and you only, I have sinned,"}
              , {class: "q2", text: "and done that which is evil in your sight,"}
              , {class: "q", text: "so you may be proved right when you speak,"}
              , {class: "q2", text: "and justified when you judge."}
              ]
        , 5:  [ {class: "q", text: "Behold, I was born in iniquity."}
              , {class: "q2", text: "My mother conceived me in sin."}
              ]
        , 6:  [ {class: "q", text: "Behold, you desire truth in the inward parts."}
              , {class: "q2", text: "You teach me wisdom in the inmost place."}
              ]
        , 7:  [ {class: "q", text: "Purify me with hyssop, and I will be clean."}
              , {class: "q2", text: "Wash me, and I will be whiter than snow."}
              ]
        , 8:  [ {class: "q", text: "Let me hear joy and gladness,"}
              , {class: "q2", text: "that the bones which you have broken may rejoice."}
              ]
        , 9:  [ {class: "q", text: "Hide your face from my sins,"}
              , {class: "q2", text: "and blot out all of my iniquities."}
              ]
        , 10: [ {class: "q", text: "Create in me a clean heart, O God."}
              , {class: "q2", text: "Renew a right spirit within me."}
              ]
        , 11: [ {class: "q", text: "Don’t throw me from your presence,"}
              , {class: "q2", text: "and don’t take your Holy Spirit from me."}
              ]
        , 12: [ {class: "q", text: "Restore to me the joy of your salvation."}
              , {class: "q2", text: "Uphold me with a willing spirit."}
              ]
        , 13: [ {class: "q", text: "Then I will teach transgressors your ways."}
              , {class: "q2", text: "Sinners will be converted to you."}
              ]
        , 14: [ {class: "q", text: "Deliver me from the guilt of bloodshed, O God, the God of my salvation."}
              , {class: "q2", text: "My tongue will sing aloud of your righteousness."}
              ]
        , 15: [ {class: "q", text: "Lord, open my lips."}
              , {class: "q2", text: "My mouth will declare your praise."}
              ]
        , 16: [ {class: "q", text: "For you don’t delight in sacrifice, or else I would give it."}
              , {class: "q2", text: "You have no pleasure in burnt offering."}
              ]
        , 17: [ {class: "q", text: "The sacrifices of God are a broken spirit."}
              , {class: "q2", text: "O God, you will not despise a broken and contrite heart."}
              ]
        , 18: [ {class: "q", text: "Do well in your good pleasure to Zion."}
              , {class: "q2", text: "Build the walls of Jerusalem."}
              ]
        , 19: [ {class: "q", text: "Then you will delight in the sacrifices of righteousness,"}
              , {class: "q2", text: "in burnt offerings and in whole burnt offerings."}
              , {class: "q", text: "Then they will offer bulls on your altar."}
              ]
        }
  , 52: { 1:  [ {class: "q", text: "Why do you boast of mischief, mighty man?"}
              , {class: "q2", text: "God’s loving kindness endures continually."}
              ]
        , 2:  [ {class: "q", text: "Your tongue plots destruction,"}
              , {class: "q2", text: "like a sharp razor, working deceitfully."}
              ]
        , 3:  [ {class: "q", text: "You love evil more than good,"}
              , {class: "q2", text: "lying rather than speaking the truth."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "You love all devouring words,"}
              , {class: "q2", text: "you deceitful tongue."}
              ]
        , 5:  [ {class: "q", text: "God will likewise destroy you forever."}
              , {class: "q2", text: "He will take you up, and pluck you out of your tent,"}
              , {class: "q2", text: "and root you out of the land of the living."}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "The righteous also will see it, and fear,"}
              , {class: "q2", text: "and laugh at him, saying,"}
              ]
        , 7:  [ {class: "q", text: "“Behold, this is the man who didn’t make God his strength,"}
              , {class: "q2", text: "but trusted in the abundance of his riches,"}
              , {class: "q2", text: "and strengthened himself in his wickedness.”"}
              ]
        , 8:  [ {class: "q", text: "But as for me, I am like a green olive tree in God’s house."}
              , {class: "q2", text: "I trust in God’s loving kindness forever and ever."}
              ]
        , 9:  [ {class: "q", text: "I will give you thanks forever, because you have done it."}
              , {class: "q2", text: "I will hope in your name, for it is good,"}
              , {class: "q2", text: "in the presence of your saints."}
              ]
        }
  , 53: { 1:  [ {class: "q", text: "The fool has said in his heart, “There is no God.”"}
              , {class: "q2", text: "They are corrupt, and have done abominable iniquity."}
              , {class: "q2", text: "There is no one who does good."}
              ]
        , 2:  [ {class: "q", text: "God looks down from heaven on the children of men,"}
              , {class: "q2", text: "to see if there are any who understood,"}
              , {class: "q2", text: "who seek after God."}
              ]
        , 3:  [ {class: "q", text: "Every one of them has gone back."}
              , {class: "q2", text: "They have become filthy together."}
              , {class: "q2", text: "There is no one who does good, no, not one."}
              ]
        , 4:  [ {class: "q", text: "Have the workers of iniquity no knowledge,"}
              , {class: "q2", text: "who eat up my people as they eat bread,"}
              , {class: "q2", text: "and don’t call on God?"}
              ]
        , 5:  [ {class: "q", text: "There they were in great fear, where no fear was,"}
              , {class: "q2", text: "for God has scattered the bones of him who encamps against you."}
              , {class: "q", text: "You have put them to shame,"}
              , {class: "q2", text: "because God has rejected them."}
              ]
        , 6:  [ {class: "q", text: "Oh that the salvation of Israel would come out of Zion!"}
              , {class: "q2", text: "When God brings back his people from captivity,"}
              , {class: "q2", text: "then Jacob shall rejoice,"}
              , {class: "q2", text: "and Israel shall be glad."}
              ]
        }
  , 54: { 1:  [ {class: "q", text: "Save me, God, by your name."}
              , {class: "q2", text: "Vindicate me in your might."}
              ]
        , 2:  [ {class: "q", text: "Hear my prayer, God."}
              , {class: "q2", text: "Listen to the words of my mouth."}
              ]
        , 3:  [ {class: "q", text: "For strangers have risen up against me."}
              , {class: "q2", text: "Violent men have sought after my soul."}
              , {class: "q2", text: "They haven’t set God before them."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "Behold, God is my helper."}
              , {class: "q2", text: "The Lord is the one who sustains my soul."}
              ]
        , 5:  [ {class: "q", text: "He will repay the evil to my enemies."}
              , {class: "q2", text: "Destroy them in your truth."}
              ]
        , 6:  [ {class: "q", text: "With a free will offering, I will sacrifice to you."}
              , {class: "q2", text: "I will give thanks to your name, Yahweh, for it is good."}
              ]
        , 7:  [ {class: "q", text: "For he has delivered me out of all trouble."}
              , {class: "q2", text: "My eye has seen triumph over my enemies."}
              ]
        }
  , 55: { 1:  [ {class: "q", text: "Listen to my prayer, God."}
              , {class: "q2", text: "Don’t hide yourself from my supplication."}
              ]
        , 2:  [ {class: "q", text: "Attend to me, and answer me."}
              , {class: "q2", text: "I am restless in my complaint,"}
              , {class: "q2", text: "and moan"}
              ]
        , 3:  [ {class: "endpara", text: "because of the voice of the enemy,"}
              , {class: "q2", text: "because of the oppression of the wicked."}
              , {class: "q", text: "For they bring suffering on me."}
              , {class: "q2", text: "In anger they hold a grudge against me."}
              ]
        , 4:  [ {class: "q", text: "My heart is severely pained within me."}
              , {class: "q2", text: "The terrors of death have fallen on me."}
              ]
        , 5:  [ {class: "q", text: "Fearfulness and trembling have come on me."}
              , {class: "q2", text: "Horror has overwhelmed me."}
              ]
        , 6:  [ {class: "q", text: "I said, “Oh that I had wings like a dove!"}
              , {class: "q2", text: "Then I would fly away, and be at rest."}
              ]
        , 7:  [ {class: "q", text: "Behold, then I would wander far off."}
              , {class: "q2", text: "I would lodge in the wilderness.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "“I would hurry to a shelter from the stormy wind and storm.”"} ]
        , 9:  [ {class: "q", text: "Confuse them, Lord, and confound their language,"}
              , {class: "q2", text: "for I have seen violence and strife in the city."}
              ]
        , 10: [ {class: "q", text: "Day and night they prowl around on its walls."}
              ,  {class: "q2", text: "Malice and abuse are also within her."}
              ]
        , 11: [ {class: "q", text: "Destructive forces are within her."}
              , {class: "q2", text: "Threats and lies don’t depart from her streets."}
              ]
        , 12: [ {class: "q", text: "For it was not an enemy who insulted me,"}
              , {class: "q2", text: "then I could have endured it."}
              , {class: "q", text: "Neither was it he who hated me who raised himself up against me,"}
              , {class: "q2", text: "then I would have hidden myself from him."}
              ]
        , 13: [ {class: "q", text: "But it was you, a man like me,"}
              , {class: "q2", text: "my companion, and my familiar friend."}
              ]
        , 14: [ {class: "q", text: "We took sweet fellowship together."}
              , {class: "q2", text: "We walked in God’s house with company."}
              ]
        , 15: [ {class: "q", text: "Let death come suddenly on them."}
              , {class: "q2", text: "Let them go down alive into Sheol."}
              , {class: "q2", text: "For wickedness is among them, in their dwelling."}
              ]
        , 16: [ {class: "q", text: "As for me, I will call on God."}
              , {class: "q2", text: "Yahweh will save me."}
              ]
        , 17: [ {class: "q", text: "Evening, morning, and at noon, I will cry out in distress."}
              , {class: "q2", text: "He will hear my voice."}
              ]
        , 18: [ {class: "q", text: "He has redeemed my soul in peace from the battle that was against me,"}
              , {class: "q2", text: "although there are many who oppose me."}
              ]
        , 19: [ {class: "q", text: "God, who is enthroned forever,"}
              , {class: "q2", text: "will hear and answer them."}
              , {class: "qs", text: "Selah."}
              , {class: "q2", text: "They never change,"}
              , {class: "q2", text: "who don’t fear God."}
              ]
        , 20: [ {class: "q", text: "He raises his hands against his friends."}
              , {class: "q2", text: "He has violated his covenant."}
              ]
        , 21: [ {class: "q", text: "His mouth was smooth as butter,"}
              , {class: "q2", text: "but his heart was war."}
              , {class: "q", text: "His words were softer than oil,"}
              , {class: "q2", text: "yet they were drawn swords."}
              ]
        , 22: [ {class: "q", text: "Cast your burden on Yahweh and he will sustain you."}
              , {class: "q2", text: "He will never allow the righteous to be moved."}
              ]
        , 23: [ {class: "q", text: "But you, God, will bring them down into the pit of destruction."}
              , {class: "q2", text: "Bloodthirsty and deceitful men shall not live out half their days,"}
              , {class: "q2", text: "but I will trust in you."}
              ]
        }
  , 56: { 1:  [ {class: "q", text: "Be merciful to me, God, for man wants to swallow me up."}
              , {class: "q2", text: "All day long, he attacks and oppresses me."}
              ]
        , 2:  [ {class: "q", text: "My enemies want to swallow me up all day long,"}
              , {class: "q2", text: "for they are many who fight proudly against me."}
              ]
        , 3:  [ {class: "q", text: "When I am afraid,"}
              ,  {class: "q2", text: "I will put my trust in you."}
              ]
        , 4:  [ {class: "q", text: "In God, I praise his word."}
              , {class: "q2", text: "In God, I put my trust."}
              , {class: "q", text: "I will not be afraid."}
              , {class: "q2", text: "What can flesh do to me?"}
              ]
        , 5:  [ {class: "q", text: "All day long they twist my words."}
              , {class: "q2", text: "All their thoughts are against me for evil."}
              ]
        , 6:  [ {class: "q", text: "They conspire and lurk,"}
              , {class: "q2", text: "watching my steps."}
              , {class: "q2", text: "They are eager to take my life."}
              ]
        , 7:  [ {class: "q", text: "Shall they escape by iniquity?"}
              , {class: "q2", text: "In anger cast down the peoples, God."}
              ]
        , 8:  [ {class: "q", text: "You count my wanderings."}
              , {class: "q2", text: "You put my tears into your container."}
              , {class: "q2", text: "Aren’t they in your book?"}
              ]
        , 9:  [ {class: "q", text: "Then my enemies shall turn back in the day that I call."}
              , {class: "q2", text: "I know this: that God is for me."}
              ]
        , 10: [ {class: "q", text: "In God, I will praise his word."}
              , {class: "q2", text: "In Yahweh, I will praise his word."}
              ]
        , 11: [ {class: "q", text: "I have put my trust in God."}
              , {class: "q2", text: "I will not be afraid."}
              , {class: "q2", text: "What can man do to me?"}
              ]
        , 12: [ {class: "q", text: "Your vows are on me, God."}
              , {class: "q2", text: "I will give thank offerings to you."}
              ]
        , 13: [ {class: "q", text: "For you have delivered my soul from death,"}
              , {class: "q2", text: "and prevented my feet from falling,"}
              , {class: "q2", text: "that I may walk before God in the light of the living."}
              ]
        }
  , 57: { 1:  [ {class: "q", text: "Be merciful to me, God, be merciful to me,"}
              , {class: "q2", text: "for my soul takes refuge in you."}
              , {class: "q", text: "Yes, in the shadow of your wings, I will take refuge,"}
              , {class: "q2", text: "until disaster has passed."}
              ]
        , 2:  [ {class: "q", text: "I cry out to God Most High,"}
              , {class: "q", text: "to God who accomplishes my requests for me."}
              ]
        , 3:  [ {class: "q", text: "He will send from heaven, and save me,"}
              , {class: "q2", text: "he rebukes the one who is pursuing me."}
              , {class: "qs", text: "Selah"}
              , {class: "q", text: "God will send out his loving kindness and his truth."}
              ]
        , 4:  [ {class: "q", text: "My soul is among lions."}
              , {class: "q2", text: "I lie among those who are set on fire,"}
              , {class: "q2", text: "even the sons of men, whose teeth are spears and arrows,"}
              , {class: "q2", text: "and their tongue a sharp sword."}
              ]
        , 5:  [ {class: "q", text: "Be exalted, God, above the heavens!"}
              , {class: "q2", text: "Let your glory be above all the earth!"}
              ]
        , 6:  [ {class: "q", text: "They have prepared a net for my steps."}
              , {class: "q2", text: "My soul is bowed down."}
              , {class: "q", text: "They dig a pit before me."}
              , {class: "q2", text: "They fall into the middle of it themselves."}
              , {class: "qs", text: "Selah"}
              ]
        , 7:  [ {class: "q", text: "My heart is steadfast, God."}
              , {class: "q2", text: "My heart is steadfast."}
              , {class: "q2", text: "I will sing, yes, I will sing praises."}
              ]
        , 8:  [ {class: "q", text: "Wake up, my glory! Wake up, lute and harp!"}
              , {class: "q2", text: "I will wake up the dawn."}
              ]
        , 9:  [ {class: "q", text: "I will give thanks to you, Lord, among the peoples."}
              , {class: "q2", text: "I will sing praises to you among the nations."}
              ]
        , 10: [ {class: "q", text: "For your great loving kindness reaches to the heavens,"}
              , {class: "q2", text: "and your truth to the skies."}
              ]
        , 11: [ {class: "q", text: "Be exalted, God, above the heavens."}
              , {class: "q2", text: "Let your glory be over all the earth."}
              ]
        }
  , 58: { 1:  [ {class: "q", text: "Do you indeed speak righteousness, silent ones?"}
              , {class: "q2", text: "Do you judge blamelessly, you sons of men?"}
              ]
        , 2:  [ {class: "q", text: "No, in your heart you plot injustice."}
              , {class: "q2", text: "You measure out the violence of your hands in the earth."}
              ]
        , 3:  [ {class: "q", text: "The wicked go astray from the womb."}
              , {class: "q2", text: "They are wayward as soon as they are born, speaking lies."}
              ]
        , 4:  [ {class: "q", text: "Their poison is like the poison of a snake,"}
              , {class: "q2", text: "like a deaf cobra that stops its ear,"}
              ]
        , 5:  [ {class: "q2", text: "/span> which doesn’t listen to the voice of charmers,"}
              , {class: "q2", text: "no matter how skillful the charmer may be."}
              ]
        , 6:  [ {class: "q", text: "Break their teeth, God, in their mouth."}
              , {class: "q2", text: "Break out the great teeth of the young lions, Yahweh."}
              ]
        , 7:  [ {class: "q", text: "Let them vanish like water that flows away."}
              , {class: "q2", text: "When they draw the bow, let their arrows be made blunt."}
              ]
        , 8:  [ {class: "q", text: "Let them be like a snail which melts and passes away,"}
              , {class: "q2", text: "like the stillborn child, who has not seen the sun."}
              ]
        , 9:  [ {class: "q", text: "Before your pots can feel the heat of the thorns,"}
              , {class: "q2", text: "he will sweep away the green and the burning alike."}
              ]
        , 10: [ {class: "q", text: "The righteous shall rejoice when he sees the vengeance."}
              , {class: "q2", text: "He shall wash his feet in the blood of the wicked,"}
              ]
        , 11: [ {class: "q", text: "so that men shall say, “Most certainly there is a reward for the righteous."}
              , {class: "q2", text: "Most certainly there is a God who judges the earth.”"}
              ]
        }
  , 59: { 1:  [ {class: "q", text: "Deliver me from my enemies, my God."}
              , {class: "q2", text: "Set me on high from those who rise up against me."}
              ]
        , 2:  [ {class: "q", text: "Deliver me from the workers of iniquity."}
              , {class: "q2", text: "Save me from the bloodthirsty men."}
              ]
        , 3:  [ {class: "q", text: "For, behold, they lie in wait for my soul."}
              , {class: "q2", text: "The mighty gather themselves together against me,"}
              , {class: "q2", text: "not for my disobedience, nor for my sin, Yahweh."}
              ]
        , 4:  [ {class: "q", text: "I have done no wrong, yet they are ready to attack me."}
              , {class: "q2", text: "Rise up, behold, and help me!"}
              ]
        , 5:  [ {class: "q", text: "You, Yahweh God of Armies, the God of Israel,"}
              , {class: "q2", text: "rouse yourself to punish the nations."}
              , {class: "q2", text: "Show no mercy to the wicked traitors."}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "They return at evening, howling like dogs,"}
              , {class: "q2", text: "and prowl around the city."}
              ]
        , 7:  [ {class: "q", text: "Behold, they spew with their mouth."}
              , {class: "q2", text: "Swords are in their lips,"}
              , {class: "q2", text: "“For”, they say, “who hears us?”"}
              ]
        , 8:  [ {class: "q", text: "But you, Yahweh, laugh at them."}
              , {class: "q2", text: "You scoff at all the nations."}
              ]
        , 9:  [ {class: "q", text: "Oh, my Strength, I watch for you,"}
              , {class: "q2", text: "for God is my high tower."}
              ]
        , 10: [ {class: "q", text: "My God will go before me with his loving kindness."}
              ,  {class: "q2", text: "God will let me look at my enemies in triumph."}
              ]
        , 11: [ {class: "q", text: "Don’t kill them, or my people may forget."}
              , {class: "q2", text: "Scatter them by your power, and bring them down, Lord our shield."}
              ]
        , 12: [ {class: "q", text: "For the sin of their mouth, and the words of their lips,"}
              , {class: "q2", text: "let them be caught in their pride,"}
              , {class: "q2", text: "for the curses and lies which they utter."}
              ]
        , 13: [ {class: "q", text: "Consume them in wrath."}
              ,  {class: "q2", text: "Consume them, and they will be no more."}
              , {class: "q", text: "Let them know that God rules in Jacob,"}
              , {class: "q2", text: "to the ends of the earth."}
              , {class: "qs", text: "Selah"}
              ]
        , 14: [ {class: "q", text: "At evening let them return."}
              , {class: "q2", text: "Let them howl like a dog, and go around the city."}
              ]
        , 15: [ {class: "q", text: "They shall wander up and down for food,"}
              , {class: "q2", text: "and wait all night if they aren’t satisfied."}
              ]
        , 16: [ {class: "q", text: "But I will sing of your strength."}
              , {class: "q2", text: "Yes, I will sing aloud of your loving kindness in the morning."}
              , {class: "q", text: "For you have been my high tower,"}
              , {class: "q2", text: "a refuge in the day of my distress."}
              ]
        , 17: [ {class: "q", text: "To you, my strength, I will sing praises."}
              , {class: "q2", text: "For God is my high tower, the God of my mercy."}
              ]
        }
  , 6:  { 1:  [ {class: "q", text: "Yahweh, don’t rebuke me in your anger,"}
              , {class: "q2", text: "neither discipline me in your wrath."}
              ]
        , 2:  [ {class: "q", text: "Have mercy on me, Yahweh, for I am faint."}
              , {class: "q2", text: "Yahweh, heal me, for my bones are troubled."}
              ]
        , 3:  [ {class: "q", text: "My soul is also in great anguish."}
              , {class: "q2", text: "But you, Yahweh—how long?"}
              ]
        , 4:  [ {class: "q", text: "Return, Yahweh. Deliver my soul,"}
              , {class: "q2", text: "and save me for your loving kindness’ sake."}
              ]
        , 5:  [ {class: "q", text: "For in death there is no memory of you."}
              , {class: "q2", text: "In Sheol, who shall give you thanks?"}
              ]
        , 6:  [ {class: "q", text: "I am weary with my groaning."}
              , {class: "q2", text: "Every night I flood my bed."}
              , {class: "q2", text: "I drench my couch with my tears."}
              ]
        , 7:  [ {class: "q", text: "My eye wastes away because of grief."}
              , {class: "q2", text: "It grows old because of all my adversaries."}
              ]
        , 8:  [ {class: "q", text: "Depart from me, all you workers of iniquity,"}
              , {class: "q2", text: "for Yahweh has heard the voice of my weeping."}
              ]
        , 9:  [ {class: "q", text: "Yahweh has heard my supplication."}
              ,  {class: "q2", text: "Yahweh accepts my prayer."}
              ]
        , 10: [ {class: "q", text: "May all my enemies be ashamed and dismayed."}
              , {class: "q2", text: "They shall turn back, they shall be disgraced suddenly."}
              ]
        }
  , 60: { 1:  [ {class: "q", text: "God, you have rejected us."}
              , {class: "q2", text: "You have broken us down."}
              , {class: "q", text: "You have been angry."}
              , {class: "q2", text: "Restore us, again."}
              ]
        , 2:  [ {class: "q", text: "You have made the land tremble."}
              , {class: "q2", text: "You have torn it."}
              , {class: "q", text: "Mend its fractures,"}
              , {class: "q2", text: "for it quakes."}
              ]
        , 3:  [ {class: "q", text: "You have shown your people hard things."}
              ,  {class: "q2", text: "You have made us drink the wine that makes us stagger."}
              ]
        , 4:  [ {class: "q", text: "You have given a banner to those who fear you,"}
              , {class: "q2", text: "that it may be displayed because of the truth."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "So that your beloved may be delivered,"}
              , {class: "q2", text: "save with your right hand, and answer us."}
              ]
        , 6:  [ {class: "q", text: "God has spoken from his sanctuary:"}
              , {class: "q2", text: "“I will triumph."}
              , {class: "q2", text: "I will divide Shechem,"}
              , {class: "q2", text: "and measure out the valley of Succoth."}
              ]
        , 7:  [ {class: "q", text: "Gilead is mine, and Manasseh is mine."}
              , {class: "q2", text: "Ephraim also is the defense of my head."}
              , {class: "q2", text: "Judah is my scepter."}
              ]
        , 8:  [ {class: "q", text: "Moab is my wash basin."}
              , {class: "q2", text: "I will throw my sandal on Edom."}
              , {class: "q2", text: "I shout in triumph over Philistia.”"}
              ]
        , 9:  [ {class: "q", text: "Who will bring me into the strong city?"}
              , {class: "q2", text: "Who has led me to Edom?"}
              ]
        , 10: [ {class: "q", text: "Haven’t you, God, rejected us?"}
              , {class: "q2", text: "You don’t go out with our armies, God."}
              ]
        , 11: [ {class: "q", text: "Give us help against the adversary,"}
              , {class: "q2", text: "for the help of man is vain."}
              ]
        , 12: [ {class: "q", text: "Through God we will do valiantly,"}
              ,  {class: "q2", text: "for it is he who will tread down our adversaries."}
              ]
        }
  , 61: { 1:  [ {class: "q", text: "Hear my cry, God."}
              , {class: "q2", text: "Listen to my prayer."}
              ]
        , 2:  [ {class: "q", text: "From the end of the earth, I will call to you when my heart is overwhelmed."}
              , {class: "q2", text: "Lead me to the rock that is higher than I."}
              ]
        , 3:  [ {class: "q", text: "For you have been a refuge for me,"}
              , {class: "q2", text: "a strong tower from the enemy."}
              ]
        , 4:  [ {class: "q", text: "I will dwell in your tent forever."}
              , {class: "q2", text: "I will take refuge in the shelter of your wings."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "For you, God, have heard my vows."}
              , {class: "q2", text: "You have given me the heritage of those who fear your name."}
              ]
        , 6:  [ {class: "q", text: "You will prolong the king’s life."}
              , {class: "q2", text: "His years will be for generations."}
              ]
        , 7:  [ {class: "q", text: "He shall be enthroned in God’s presence forever."}
              , {class: "q2", text: "Appoint your loving kindness and truth, that they may preserve him."}
              ]
        , 8:  [ {class: "q", text: "So I will sing praise to your name forever,"}
              , {class: "q2", text: "that I may fulfill my vows daily."}
              ]
        }
  , 62: { 1:  [ {class: "q", text: "My soul rests in God alone."}
              , {class: "q2", text: "My salvation is from him."}
              ]
        , 2:  [ {class: "q", text: "He alone is my rock, my salvation, and my fortress."}
              , {class: "q2", text: "I will never be greatly shaken."}
              ]
        , 3:  [ {class: "q", text: "How long will you assault a man?"}
              , {class: "q2", text: "Would all of you throw him down,"}
              , {class: "q2", text: "like a leaning wall, like a tottering fence?"}
              ]
        , 4:  [ {class: "q", text: "They fully intend to throw him down from his lofty place."}
              , {class: "q2", text: "They delight in lies."}
              , {class: "q2", text: "They bless with their mouth, but they curse inwardly."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "My soul, wait in silence for God alone,"}
              , {class: "q2", text: "for my expectation is from him."}
              ]
        , 6:  [ {class: "q", text: "He alone is my rock and my salvation, my fortress."}
              ,  {class: "q2", text: "I will not be shaken."}
              ]
        , 7:  [ {class: "q", text: "My salvation and my honor is with God."}
              , {class: "q2", text: "The rock of my strength, and my refuge, is in God."}
              ]
        , 8:  [ {class: "q", text: "Trust in him at all times, you people."}
              , {class: "q2", text: "Pour out your heart before him."}
              , {class: "q2", text: "God is a refuge for us."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "Surely men of low degree are just a breath,"}
              , {class: "q2", text: "and men of high degree are a lie."}
              , {class: "q", text: "In the balances they will go up."}
              , {class: "q2", text: "They are together lighter than a breath."}
              ]
        , 10: [ {class: "q", text: "Don’t trust in oppression."}
              , {class: "q2", text: "Don’t become vain in robbery."}
              , {class: "q", text: "If riches increase,"}
              , {class: "q2", text: "don’t set your heart on them."}
              ]
        , 11: [ {class: "q", text: "God has spoken once;"}
              , {class: "q2", text: "twice I have heard this,"}
              , {class: "q2", text: "that power belongs to God."}
              ]
        , 12: [ {class: "q", text: "Also to you, Lord, belongs loving kindness,"}
              , {class: "q2", text: "for you reward every man according to his work."}
              ]
        }
  , 63: { 1:  [ {class: "q", text: "God, you are my God."}
              , {class: "q2", text: "I will earnestly seek you."}
              , {class: "q", text: "My soul thirsts for you."}
              , {class: "q2", text: "My flesh longs for you,"}
              , {class: "q2", text: "in a dry and weary land, where there is no water."}
              ]
        , 2:  [ {class: "q", text: "So I have seen you in the sanctuary,"}
              , {class: "q2", text: "watching your power and your glory."}
              ]
        , 3:  [ {class: "q", text: "Because your loving kindness is better than life,"}
              , {class: "q2", text: "my lips shall praise you."}
              ]
        , 4:  [ {class: "q", text: "So I will bless you while I live."}
              , {class: "q2", text: "I will lift up my hands in your name."}
              ]
        , 5:  [ {class: "q", text: "My soul shall be satisfied as with the richest food."}
              , {class: "q2", text: "My mouth shall praise you with joyful lips,"}
              ]
        , 6:  [ {class: "q2", text: "/span> when I remember you on my bed,"}
              , {class: "q2", text: "and think about you in the night watches."}
              ]
        , 7:  [ {class: "q", text: "For you have been my help."}
              , {class: "q2", text: "I will rejoice in the shadow of your wings."}
              ]
        , 8:  [ {class: "q", text: "My soul stays close to you."}
              , {class: "q2", text: "Your right hand holds me up."}
              ]
        , 9:  [ {class: "q", text: "But those who seek my soul to destroy it"}
              , {class: "q2", text: "shall go into the lower parts of the earth."}
              ]
        , 10: [ {class: "q", text: "They shall be given over to the power of the sword."}
              , {class: "q2", text: "They shall be jackal food."}
              ]
        , 11: [ {class: "q", text: "But the king shall rejoice in God."}
              , {class: "q2", text: "Everyone who swears by him will praise him,"}
              , {class: "q2", text: "for the mouth of those who speak lies shall be silenced."}
              ]
        }
  , 64: { 1:  [ {class: "q", text: "Hear my voice, God, in my complaint."}
              , {class: "q2", text: "Preserve my life from fear of the enemy."}
              ]
        , 2:  [ {class: "q", text: "Hide me from the conspiracy of the wicked,"}
              , {class: "q2", text: "from the noisy crowd of the ones doing evil;"}
              ]
        , 3:  [ {class: "q", text: "who sharpen their tongue like a sword,"}
              , {class: "q2", text: "and aim their arrows, deadly words,"}
              ]
        , 4:  [ {class: "q2", text: "/span> to shoot innocent men from ambushes."}
              , {class: "q2", text: "They shoot at him suddenly and fearlessly."}
              ]
        , 5:  [ {class: "q", text: "They encourage themselves in evil plans."}
              , {class: "q2", text: "They talk about laying snares secretly."}
              , {class: "q2", text: "They say, “Who will see them?”"}
              ]
        , 6:  [ {class: "q", text: "They plot injustice, saying, “We have made a perfect plan!”"}
              , {class: "q2", text: "Surely man’s mind and heart are cunning."}
              ]
        , 7:  [ {class: "q", text: "But God will shoot at them."}
              , {class: "q2", text: "They will be suddenly struck down with an arrow."}
              ]
        , 8:  [ {class: "q", text: "Their own tongues shall ruin them."}
              , {class: "q2", text: "All who see them will shake their heads."}
              ]
        , 9:  [ {class: "q", text: "All mankind shall be afraid."}
              , {class: "q2", text: "They shall declare the work of God,"}
              , {class: "q2", text: "and shall wisely ponder what he has done."}
              ]
        , 10: [ {class: "q", text: "The righteous shall be glad in Yahweh,"}
              , {class: "q2", text: "and shall take refuge in him."}
              , {class: "q2", text: "All the upright in heart shall praise him!"}
              ]
        }
  , 65: { 1:  [ {class: "q", text: "Praise waits for you, God, in Zion."}
              , {class: "q2", text: "Vows shall be performed to you."}
              ]
        , 2:  [ {class: "q", text: "You who hear prayer,"}
              , {class: "q2", text: "all men will come to you."}
              ]
        , 3:  [ {class: "q", text: "Sins overwhelmed me,"}
              , {class: "q2", text: "but you atoned for our transgressions."}
              ]
        , 4:  [ {class: "q", text: "Blessed is the one whom you choose and cause to come near,"}
              , {class: "q2", text: "that he may live in your courts."}
              , {class: "q2", text: "We will be filled with the goodness of your house,"}
              , {class: "q2", text: "your holy temple."}
              ]
        , 5:  [ {class: "q", text: "By awesome deeds of righteousness, you answer us,"}
              , {class: "q2", text: "God of our salvation."}
              , {class: "q", text: "You who are the hope of all the ends of the earth,"}
              , {class: "q2", text: "of those who are far away on the sea."}
              ]
        , 6:  [ {class: "q", text: "By your power, you form the mountains,"}
              , {class: "q2", text: "having armed yourself with strength."}
              ]
        , 7:  [ {class: "q", text: "You still the roaring of the seas,"}
              , {class: "q2", text: "the roaring of their waves,"}
              , {class: "q2", text: "and the turmoil of the nations."}
              ]
        , 8:  [ {class: "q", text: "They also who dwell in faraway places are afraid at your wonders."}
              , {class: "q2", text: "You call the morning’s dawn and the evening with songs of joy."}
              ]
        , 9:  [ {class: "q", text: "You visit the earth, and water it."}
              , {class: "q2", text: "You greatly enrich it."}
              , {class: "q", text: "The river of God is full of water."}
              , {class: "q2", text: "You provide them grain, for so you have ordained it."}
              ]
        , 10: [ {class: "q", text: "You drench its furrows."}
              , {class: "q2", text: "You level its ridges."}
              , {class: "q2", text: "You soften it with showers."}
              , {class: "q2", text: "You bless it with a crop."}
              ]
        , 11: [ {class: "q", text: "You crown the year with your bounty."}
              , {class: "q2", text: "Your carts overflow with abundance."}
              ]
        , 12: [ {class: "q", text: "The wilderness grasslands overflow."}
              , {class: "q2", text: "The hills are clothed with gladness."}
              ]
        , 13: [ {class: "q", text: "The pastures are covered with flocks."}
              , {class: "q2", text: "The valleys also are clothed with grain."}
              , {class: "q", text: "They shout for joy!"}
              , {class: "q2", text: "They also sing."}
              ]
        }
  , 66: { 1:  [ {class: "q", text: "Make a joyful shout to God, all the earth!"} ]
        , 2:  [ {class: "q2", text: "/span> Sing to the glory of his name!"}
              , {class: "q2", text: "Offer glory and praise!"}
              ]
        , 3:  [ {class: "q", text: "Tell God, “How awesome are your deeds!"}
              , {class: "q2", text: "Through the greatness of your power, your enemies submit themselves to you."}
              ]
        , 4:  [ {class: "q", text: "All the earth will worship you,"}
              , {class: "q2", text: "and will sing to you;"}
              , {class: "q2", text: "they will sing to your name.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "Come, and see God’s deeds—"}
              , {class: "q2", text: "awesome work on behalf of the children of men."}
              ]
        , 6:  [ {class: "q", text: "He turned the sea into dry land."}
              , {class: "q2", text: "They went through the river on foot."}
              , {class: "q2", text: "There, we rejoiced in him."}
              ]
        , 7:  [ {class: "q", text: "He rules by his might forever."}
              , {class: "q2", text: "His eyes watch the nations."}
              , {class: "q2", text: "Don’t let the rebellious rise up against him."}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "Praise our God, you peoples!"}
              , {class: "q2", text: "Make the sound of his praise heard,"}
              ]
        , 9:  [ {class: "q", text: "who preserves our life among the living,"}
              , {class: "q2", text: "and doesn’t allow our feet to be moved."}
              ]
        , 10: [ {class: "q", text: "For you, God, have tested us."}
              , {class: "q2", text: "You have refined us, as silver is refined."}
              ]
        , 11: [ {class: "q", text: "You brought us into prison."}
              , {class: "q2", text: "You laid a burden on our backs."}
              ]
        , 12: [ {class: "q", text: "You allowed men to ride over our heads."}
              , {class: "q2", text: "We went through fire and through water,"}
              , {class: "q2", text: "but you brought us to the place of abundance."}
              ]
        , 13: [ {class: "q", text: "I will come into your temple with burnt offerings."}
              , {class: "q2", text: "I will pay my vows to you,"}
              ]
        , 14: [ {class: "endpara", text: "which my lips promised,"}
              , {class: "q2", text: "and my mouth spoke, when I was in distress."}
              ]
        , 15: [ {class: "q", text: "I will offer to you burnt offerings of fat animals,"}
              , {class: "q2", text: "with the offering of rams,"}
              , {class: "q2", text: "I will offer bulls with goats."}
              , {class: "qs", text: "Selah"}
              ]
        , 16: [ {class: "q", text: "Come and hear, all you who fear God."}
              , {class: "q2", text: "I will declare what he has done for my soul."}
              ]
        , 17: [ {class: "q", text: "I cried to him with my mouth."}
              , {class: "q2", text: "He was extolled with my tongue."}
              ]
        , 18: [ {class: "q", text: "If I cherished sin in my heart,"}
              , {class: "q2", text: "the Lord wouldn’t have listened."}
              ]
        , 19: [ {class: "q", text: "But most certainly, God has listened."}
              , {class: "q2", text: "He has heard the voice of my prayer."}
              ]
        , 20: [ {class: "q", text: "Blessed be God, who has not turned away my prayer,"}
              , {class: "q2", text: "nor his loving kindness from me."}
              ]
        }
  , 67: { 1:  [ {class: "q", text: "May God be merciful to us, bless us,"}
              , {class: "q2", text: "and cause his face to shine on us."}
              , {class: "qs", text: "Selah"}
              ]
        , 2:  [ {class: "q", text: "That your way may be known on earth,"}
              , {class: "q2", text: "and your salvation among all nations,"}
              ]
        , 3:  [ {class: "q", text: "let the peoples praise you, God."}
              , {class: "q2", text: "Let all the peoples praise you."}
              ]
        , 4:  [ {class: "q", text: "Oh let the nations be glad and sing for joy,"}
              , {class: "q2", text: "for you will judge the peoples with equity,"}
              , {class: "q2", text: "and govern the nations on earth."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "Let the peoples praise you, God."}
              , {class: "q2", text: "Let all the peoples praise you."}
              ]
        , 6:  [ {class: "q", text: "The earth has yielded its increase."}
              , {class: "q2", text: "God, even our own God, will bless us."}
              ]
        , 7:  [ {class: "q", text: "God will bless us."}
              , {class: "q2", text: "All the ends of the earth shall fear him."}
              ]
        }
  , 68: { 1:  [ {class: "q", text: "Let God arise!"}
              , {class: "q2", text: "Let his enemies be scattered!"}
              , {class: "q2", text: "Let them who hate him also flee before him."}
              ]
        , 2:  [ {class: "q", text: "As smoke is driven away,"}
              , {class: "q2", text: "so drive them away."}
              , {class: "q", text: "As wax melts before the fire,"}
              , {class: "q2", text: "so let the wicked perish at the presence of God."}
              ]
        , 3:  [ {class: "q", text: "But let the righteous be glad."}
              , {class: "q2", text: "Let them rejoice before God."}
              , {class: "q2", text: "Yes, let them rejoice with gladness."}
              ]
        , 4:  [ {class: "q", text: "Sing to God! Sing praises to his name!"}
              , {class: "q2", text: "Extol him who rides on the clouds:"}
              , {class: "q", text: "to Yah, his name!"}
              , {class: "q2", text: "Rejoice before him!"}
              ]
        , 5:  [ {class: "q", text: "A father of the fatherless, and a defender of the widows,"}
              , {class: "q2", text: "is God in his holy habitation."}
              ]
        , 6:  [ {class: "q", text: "God sets the lonely in families."}
              , {class: "q", text: "He brings out the prisoners with singing,"}
              , {class: "q2", text: "but the rebellious dwell in a sun-scorched land."}
              ]
        , 7:  [ {class: "q", text: "God, when you went out before your people,"}
              , {class: "q2", text: "when you marched through the wilderness..."}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "The earth trembled."}
              , {class: "q2", text: "The sky also poured down rain at the presence of the God of Sinai—"}
              , {class: "q2", text: "at the presence of God, the God of Israel."}
              ]
        , 9:  [ {class: "q", text: "You, God, sent a plentiful rain."}
              , {class: "q2", text: "You confirmed your inheritance when it was weary."}
              ]
        , 10: [ {class: "q", text: "Your congregation lived therein."}
              , {class: "q2", text: "You, God, prepared your goodness for the poor."}
              ]
        , 11: [ {class: "q", text: "The Lord announced the word."}
              , {class: "q2", text: "The ones who proclaim it are a great company."}
              ]
        , 12: [ {class: "q", text: "“Kings of armies flee! They flee!”"}
              , {class: "q2", text: "She who waits at home divides the plunder,"}
              ]
        , 13: [ {class: "q2", text: "</span> while you sleep among the camp fires,"}
              , {class: "q2", text: "the wings of a dove sheathed with silver,"}
              , {class: "q2", text: "her feathers with shining gold."}
              ]
        , 14: [ {class: "q", text: "When the Almighty scattered kings in her,"}
              , {class: "q2", text: "it snowed on Zalmon."}
              ]
        , 15: [ {class: "q", text: "The mountains of Bashan are majestic mountains."}
              , {class: "q2", text: "The mountains of Bashan are rugged."}
              ]
        , 16: [ {class: "q", text: "Why do you look in envy, you rugged mountains,"}
              , {class: "q2", text: "at the mountain where God chooses to reign?"}
              , {class: "q2", text: "Yes, Yahweh will dwell there forever."}
              ]
        , 17: [ {class: "q", text: "The chariots of God are tens of thousands and thousands of thousands."}
              , {class: "q2", text: "The Lord is among them, from Sinai, into the sanctuary."}
              ]
        , 18: [ {class: "q", text: "You have ascended on high."}
              , {class: "q2", text: "You have led away captives."}
              , {class: "q", text: "You have received gifts among people,"}
              , {class: "q2", text: "yes, among the rebellious also, that Yah God might dwell there."}
              ]
        , 19: [ {class: "q", text: "Blessed be the Lord, who daily bears our burdens,"}
              , {class: "q2", text: "even the God who is our salvation."}
              , {class: "qs", text: "Selah"}
              ]
        , 20: [ {class: "q", text: "God is to us a God of deliverance."}
              , {class: "q2", text: "To Yahweh, the Lord, belongs escape from death."}
              ]
        , 21: [ {class: "q", text: "But God will strike through the head of his enemies,"}
              , {class: "q2", text: "the hairy scalp of such a one as still continues in his guiltiness."}
              ]
        , 22: [ {class: "q", text: "The Lord said, “I will bring you again from Bashan,"}
              , {class: "q2", text: "I will bring you again from the depths of the sea,"}
              ]
        , 23: [ {class: "q", text: "that you may crush them, dipping your foot in blood,"}
              , {class: "q2", text: "that the tongues of your dogs may have their portion from your enemies.”"}
              ]
        , 24: [ {class: "q", text: "They have seen your processions, God,"}
              , {class: "q2", text: "even the processions of my God, my King, into the sanctuary."}
              ]
        , 25: [ {class: "q", text: "The singers went before, the minstrels followed after,"}
              , {class: "q2", text: "among the ladies playing with tambourines,"}
              ]
        , 26: [ {class: "q", text: "“Bless God in the congregations,"}
              , {class: "q2", text: "even the Lord in the assembly of Israel!”"}
              ]
        , 27: [ {class: "q", text: "There is little Benjamin, their ruler,"}
              , {class: "q2", text: "the princes of Judah, their council,"}
              , {class: "q2", text: "the princes of Zebulun, and the princes of Naphtali."}
              ]
        , 28: [ {class: "q", text: "Your God has commanded your strength."}
              , {class: "q2", text: "Strengthen, God, that which you have done for us."}
              ]
        , 29: [ {class: "q", text: "Because of your temple at Jerusalem,"}
              , {class: "q2", text: "kings shall bring presents to you."}
              ]
        , 30: [ {class: "q", text: "Rebuke the wild animal of the reeds,"}
              , {class: "q2", text: "the multitude of the bulls, with the calves of the peoples."}
              , {class: "q", text: "Being humbled, may it bring bars of silver."}
              , {class: "q2", text: "Scatter the nations that delight in war."}
              ]
        , 31: [ {class: "q", text: "Princes shall come out of Egypt."}
              , {class: "q2", text: "Ethiopia shall hurry to stretch out her hands to God."}
              ]
        , 32: [ {class: "q", text: "Sing to God, you kingdoms of the earth!"}
              , {class: "q2", text: "Sing praises to the Lord!"}
              , {class: "qs", text: "Selah"}
              ]
        , 33: [ {class: "q", text: "To him who rides on the heaven of heavens, which are of old;"}
              , {class: "q2", text: "behold, he utters his voice, a mighty voice."}
              ]
        , 34: [ {class: "q", text: "Ascribe strength to God!"}
              , {class: "q2", text: "His excellency is over Israel,"}
              , {class: "q2", text: "his strength is in the skies."}
              ]
        , 35: [ {class: "q", text: "You are awesome, God, in your sanctuaries."}
              , {class: "q2", text: "The God of Israel gives strength and power to his people."}
              , {class: "q2", text: "Praise be to God!"}
              ]
        }
  , 69: { 1:  [ {class: "q", text: "Save me, God,"}
              , {class: "q2", text: "for the waters have come up to my neck!"}
              ]
        , 2:  [ {class: "q", text: "I sink in deep mire, where there is no foothold."}
              ,  {class: "q2", text: "I have come into deep waters, where the floods overflow me."}
              ]
        , 3:  [ {class: "q", text: "I am weary with my crying."}
              , {class: "q2", text: "My throat is dry."}
              , {class: "q2", text: "My eyes fail looking for my God."}
              ]
        , 4:  [ {class: "q", text: "Those who hate me without a cause are more than the hairs of my head."}
              , {class: "q2", text: "Those who want to cut me off, being my enemies wrongfully, are mighty."}
              , {class: "q2", text: "I have to restore what I didn’t take away."}
              ]
        , 5:  [ {class: "q", text: "God, you know my foolishness."}
              , {class: "q2", text: "My sins aren’t hidden from you."}
              ]
        , 6:  [ {class: "q", text: "Don’t let those who wait for you be shamed through me, Lord Yahweh of Armies."}
              , {class: "q2", text: "Don’t let those who seek you be brought to dishonor through me, God of Israel."}
              ]
        , 7:  [ {class: "q", text: "Because for your sake, I have borne reproach."}
              , {class: "q2", text: "Shame has covered my face."}
              ]
        , 8:  [ {class: "q", text: "I have become a stranger to my brothers,"}
              , {class: "q2", text: "an alien to my mother’s children."}
              ]
        , 9:  [ {class: "q", text: "For the zeal of your house consumes me."}
              , {class: "q2", text: "The reproaches of those who reproach you have fallen on me."}
              ]
        , 10: [ {class: "q", text: "When I wept and I fasted,"}
              , {class: "q2", text: "that was to my reproach."}
              ]
        , 11: [ {class: "q", text: "When I made sackcloth my clothing,"}
              , {class: "q2", text: "I became a byword to them."}
              ]
        , 12: [ {class: "q", text: "Those who sit in the gate talk about me."}
              , {class: "q2", text: "I am the song of the drunkards."}
              ]
        , 13: [ {class: "q", text: "But as for me, my prayer is to you, Yahweh, in an acceptable time."}
              , {class: "q2", text: "God, in the abundance of your loving kindness, answer me in the truth of your salvation."}
              ]
        , 14: [ {class: "q", text: "Deliver me out of the mire, and don’t let me sink."}
              , {class: "q2", text: "Let me be delivered from those who hate me, and out of the deep waters."}
              ]
        , 15: [ {class: "q", text: "Don’t let the flood waters overwhelm me,"}
              , {class: "q2", text: "neither let the deep swallow me up."}
              , {class: "q2", text: "Don’t let the pit shut its mouth on me."}
              ]
        , 16: [ {class: "q", text: "Answer me, Yahweh, for your loving kindness is good."}
              , {class: "q2", text: "According to the multitude of your tender mercies, turn to me."}
              ]
        , 17: [ {class: "q", text: "Don’t hide your face from your servant,"}
              , {class: "q2", text: "for I am in distress."}
              , {class: "q2", text: "Answer me speedily!"}
              ]
        , 18: [ {class: "q", text: "Draw near to my soul and redeem it."}
              , {class: "q2", text: "Ransom me because of my enemies."}
              ]
        , 19: [ {class: "q", text: "You know my reproach, my shame, and my dishonor."}
              , {class: "q2", text: "My adversaries are all before you."}
              ]
        , 20: [ {class: "q", text: "Reproach has broken my heart, and I am full of heaviness."}
              , {class: "q2", text: "I looked for some to take pity, but there was none;"}
              , {class: "q2", text: "for comforters, but I found none."}
              ]
        , 21: [ {class: "q", text: "They also gave me poison for my food."}
              ,  {class: "q2", text: "In my thirst, they gave me vinegar to drink."}
              ]
        , 22: [ {class: "q", text: "Let their table before them become a snare."}
              , {class: "q2", text: "May it become a retribution and a trap."}
              ]
        , 23: [ {class: "q", text: "Let their eyes be darkened, so that they can’t see."}
              , {class: "q2", text: "Let their backs be continually bent."}
              ]
        , 24: [ {class: "q", text: "Pour out your indignation on them."}
              , {class: "q2", text: "Let the fierceness of your anger overtake them."}
              ]
        , 25: [ {class: "q", text: "Let their habitation be desolate."}
              , {class: "q2", text: "Let no one dwell in their tents."}
              ]
        , 26: [ {class: "q", text: "For they persecute him whom you have wounded."}
              , {class: "q2", text: "They tell of the sorrow of those whom you have hurt."}
              ]
        , 27: [ {class: "q", text: "Charge them with crime upon crime."}
              , {class: "q2", text: "Don’t let them come into your righteousness."}
              ]
        , 28: [ {class: "q", text: "Let them be blotted out of the book of life,"}
              , {class: "q2", text: "and not be written with the righteous."}
              ]
        , 29: [ {class: "q", text: "But I am in pain and distress."}
              , {class: "q2", text: "Let your salvation, God, protect me."}
              ]
        , 30: [ {class: "q", text: "I will praise the name of God with a song,"}
              , {class: "q2", text: "and will magnify him with thanksgiving."}
              ]
        , 31: [ {class: "q", text: "It will please Yahweh better than an ox,"}
              , {class: "q2", text: "or a bull that has horns and hoofs."}
              ]
        , 32: [ {class: "q", text: "The humble have seen it, and are glad."}
              , {class: "q2", text: "You who seek after God, let your heart live."}
              ]
        , 33: [ {class: "q", text: "For Yahweh hears the needy,"}
              , {class: "q2", text: "and doesn’t despise his captive people."}
              ]
        , 34: [ {class: "q", text: "Let heaven and earth praise him;"}
              , {class: "q2", text: "the seas, and everything that moves therein!"}
              ]
        , 35: [ {class: "q", text: "For God will save Zion, and build the cities of Judah."}
              , {class: "q2", text: "They shall settle there, and own it."}
              ]
        , 36: [ {class: "q", text: "The children also of his servants shall inherit it."}
              , {class: "q2", text: "Those who love his name shall dwell therein."}
              ]
        }
  , 7:  { 1:  [ {class: "q", text: "Yahweh, my God, I take refuge in you."}
              , {class: "q2", text: "Save me from all those who pursue me, and deliver me,"}
              ]
        , 2:  [ {class: "q", text: "lest they tear apart my soul like a lion,"}
              , {class: "q2", text: "ripping it in pieces, while there is no one to deliver."}
              ]
        , 3:  [ {class: "q", text: "Yahweh, my God, if I have done this,"}
              , {class: "q2", text: "if there is iniquity in my hands,"}
              ]
        , 4:  [ {class: "q", text: "if I have rewarded evil to him who was at peace with me"}
              ,  {class: "q2", text: "(yes, I have delivered him who without cause was my adversary),"}
              ]
        , 5:  [ {class: "q2", text: "/span> let the enemy pursue my soul, and overtake it;"}
              , {class: "q", text: "yes, let him tread my life down to the earth,"}
              , {class: "q2", text: "and lay my glory in the dust."}
              , {class: "qs", text: "Selah"}
              ]
        , 6:  [ {class: "q", text: "Arise, Yahweh, in your anger."}
              , {class: "q2", text: "Lift up yourself against the rage of my adversaries."}
              , {class: "q", text: "Awake for me. You have commanded judgment."}
              ]
        , 7:  [ {class: "q2", text: "/span> Let the congregation of the peoples surround you."}
              , {class: "q2", text: "Rule over them on high."}
              ]
        , 8:  [ {class: "q", text: "Yahweh administers judgment to the peoples."}
              , {class: "q2", text: "Judge me, Yahweh, according to my righteousness,"}
              , {class: "q2", text: "and to my integrity that is in me."}
              ]
        , 9:  [ {class: "q", text: "Oh let the wickedness of the wicked come to an end,"}
              , {class: "q2", text: "but establish the righteous;"}
              , {class: "q2", text: "their minds and hearts are searched by the righteous God."}
              ]
        , 10: [ {class: "q", text: "My shield is with God,"}
              , {class: "q2", text: "who saves the upright in heart."}
              ]
        , 11: [ {class: "q", text: "God is a righteous judge,"}
              , {class: "q2", text: "yes, a God who has indignation every day."}
              ]
        , 12: [ {class: "q", text: "If a man doesn’t repent, he will sharpen his sword;"}
              , {class: "q2", text: "he has bent and strung his bow."}
              ]
        , 13: [ {class: "q", text: "He has also prepared for himself the instruments of death."}
              , {class: "q2", text: "He makes ready his flaming arrows."}
              ]
        , 14: [ {class: "q", text: "Behold, he travails with iniquity."}
              , {class: "q2", text: "Yes, he has conceived mischief,"}
              , {class: "q2", text: "and brought out falsehood."}
              ]
        , 15: [ {class: "q", text: "He has dug a hole,"}
              , {class: "q2", text: "and has fallen into the pit which he made."}
              ]
        , 16: [ {class: "q", text: "The trouble he causes shall return to his own head."}
              , {class: "q2", text: "His violence shall come down on the crown of his own head."}
              ]
        , 17: [ {class: "q", text: "I will give thanks to Yahweh according to his righteousness,"}
              , {class: "q2", text: "and will sing praise to the name of Yahweh Most High."}
              ]
        }
  , 70: { 1:  [ {class: "q", text: "Hurry, God, to deliver me."}
              , {class: "q2", text: "Come quickly to help me, Yahweh."}
              ]
        , 2:  [ {class: "q", text: "Let them be disappointed and confounded who seek my soul."}
              , {class: "q2", text: "Let those who desire my ruin be turned back in disgrace."}
              ]
        , 3:  [ {class: "q", text: "Let them be turned because of their shame"}
              , {class: "q2", text: "who say, “Aha! Aha!”"}
              ]
        , 4:  [ {class: "q", text: "Let all those who seek you rejoice and be glad in you."}
              , {class: "q2", text: "Let those who love your salvation continually say,"}
              , {class: "q2", text: "“Let God be exalted!”"}
              ]
        , 5:  [ {class: "q", text: "But I am poor and needy."}
              , {class: "q2", text: "Come to me quickly, God."}
              , {class: "q", text: "You are my help and my deliverer."}
              , {class: "q2", text: "Yahweh, don’t delay."}
              ]
        }
  , 71: { 1:  [ {class: "q", text: "In you, Yahweh, I take refuge."}
              , {class: "q2", text: "Never let me be disappointed."}
              ]
        , 2:  [ {class: "q", text: "Deliver me in your righteousness, and rescue me."}
              , {class: "q2", text: "Turn your ear to me, and save me."}
              ]
        , 3:  [ {class: "q", text: "Be to me a rock of refuge to which I may always go."}
              , {class: "q2", text: "Give the command to save me,"}
              , {class: "q2", text: "for you are my rock and my fortress."}
              ]
        , 4:  [ {class: "q", text: "Rescue me, my God, from the hand of the wicked,"}
              ,  {class: "q2", text: "from the hand of the unrighteous and cruel man."}
              ]
        , 5:  [ {class: "q", text: "For you are my hope, Lord Yahweh,"}
              , {class: "q2", text: "my confidence from my youth."}
              ]
        , 6:  [ {class: "q", text: "I have relied on you from the womb."}
              , {class: "q2", text: "You are he who took me out of my mother’s womb."}
              , {class: "q2", text: "I will always praise you."}
              ]
        , 7:  [ {class: "q", text: "I am a marvel to many,"}
              , {class: "q2", text: "but you are my strong refuge."}
              ]
        , 8:  [ {class: "q", text: "My mouth shall be filled with your praise,"}
              , {class: "q2", text: "with your honor all day long."}
              ]
        , 9:  [ {class: "q", text: "Don’t reject me in my old age."}
              , {class: "q2", text: "Don’t forsake me when my strength fails."}
              ]
        , 10: [ {class: "q", text: "For my enemies talk about me."}
              , {class: "q2", text: "Those who watch for my soul conspire together,"}
              ]
        , 11: [ {class: "q2", text: "</span> saying, “God has forsaken him."}
              , {class: "q2", text: "Pursue and take him, for no one will rescue him.”"}
              ]
        , 12: [ {class: "q", text: "God, don’t be far from me."}
              , {class: "q2", text: "My God, hurry to help me."}
              ]
        , 13: [ {class: "q", text: "Let my accusers be disappointed and consumed."}
              , {class: "q2", text: "Let them be covered with disgrace and scorn who want to harm me."}
              ]
        , 14: [ {class: "q", text: "But I will always hope,"}
              , {class: "q2", text: "and will add to all of your praise."}
              ]
        , 15: [ {class: "q", text: "My mouth will tell about your righteousness,"}
              , {class: "q2", text: "and of your salvation all day,"}
              , {class: "q2", text: "though I don’t know its full measure."}
              ]
        , 16: [ {class: "q", text: "I will come with the mighty acts of the Lord Yahweh."}
              , {class: "q2", text: "I will make mention of your righteousness, even of yours alone."}
              ]
        , 17: [ {class: "q", text: "God, you have taught me from my youth."}
              , {class: "q2", text: "Until now, I have declared your wondrous works."}
              ]
        , 18: [ {class: "q", text: "Yes, even when I am old and gray-haired, God, don’t forsake me,"}
              , {class: "q2", text: "until I have declared your strength to the next generation,"}
              , {class: "q2", text: "your might to everyone who is to come."}
              ]
        , 19: [ {class: "q", text: "Your righteousness also, God, reaches to the heavens;"}
              , {class: "q2", text: "you have done great things."}
              , {class: "q2", text: "God, who is like you?"}
              ]
        , 20: [ {class: "q", text: "You, who have shown us many and bitter troubles,"}
              , {class: "q2", text: "you will let me live."}
              , {class: "q2", text: "You will bring us up again from the depths of the earth."}
              ]
        , 21: [ {class: "q", text: "Increase my honor"}
              , {class: "q2", text: "and comfort me again."}
              ]
        , 22: [ {class: "q", text: "I will also praise you with the harp for your faithfulness, my God."}
              , {class: "q2", text: "I sing praises to you with the lyre, Holy One of Israel."}
              ]
        , 23: [ {class: "q", text: "My lips shall shout for joy!"}
              , {class: "q2", text: "My soul, which you have redeemed, sings praises to you!"}
              ]
        , 24: [ {class: "q", text: "My tongue will also talk about your righteousness all day long,"}
              , {class: "q2", text: "for they are disappointed, and they are confounded,"}
              , {class: "q2", text: "who want to harm me."}
              ]
        }
  , 72: { 1:  [ {class: "q", text: "God, give the king your justice;"}
              , {class: "q2", text: "your righteousness to the royal son."}
              ]
        , 2:  [ {class: "q", text: "He will judge your people with righteousness,"}
              , {class: "q2", text: "and your poor with justice."}
              ]
        , 3:  [ {class: "q", text: "The mountains shall bring prosperity to the people."}
              , {class: "q2", text: "The hills bring the fruit of righteousness."}
              ]
        , 4:  [ {class: "q", text: "He will judge the poor of the people."}
              , {class: "q2", text: "He will save the children of the needy,"}
              , {class: "q2", text: "and will break the oppressor in pieces."}
              ]
        , 5:  [ {class: "q", text: "They shall fear you while the sun endures;"}
              , {class: "q2", text: "and as long as the moon, throughout all generations."}
              ]
        , 6:  [ {class: "q", text: "He will come down like rain on the mown grass,"}
              , {class: "q2", text: "as showers that water the earth."}
              ]
        , 7:  [ {class: "q", text: "In his days, the righteous shall flourish,"}
              , {class: "q2", text: "and abundance of peace, until the moon is no more."}
              ]
        , 8:  [ {class: "q", text: "He shall have dominion also from sea to sea,"}
              , {class: "q2", text: "from the River to the ends of the earth."}
              ]
        , 9:  [ {class: "q", text: "Those who dwell in the wilderness shall bow before him."}
              , {class: "q2", text: "His enemies shall lick the dust."}
              ]
        , 10: [ {class: "q", text: "The kings of Tarshish and of the islands will bring tribute."}
              , {class: "q2", text: "The kings of Sheba and Seba shall offer gifts."}
              ]
        , 11: [ {class: "q", text: "Yes, all kings shall fall down before him."}
              , {class: "q2", text: "All nations shall serve him."}
              ]
        , 12: [ {class: "q", text: "For he will deliver the needy when he cries;"}
              , {class: "q2", text: "the poor, who has no helper."}
              ]
        , 13: [ {class: "q", text: "He will have pity on the poor and needy."}
              , {class: "q2", text: "He will save the souls of the needy."}
              ]
        , 14: [ {class: "q", text: "He will redeem their soul from oppression and violence."}
              , {class: "q2", text: "Their blood will be precious in his sight."}
              ]
        , 15: [ {class: "q", text: "They shall live, and to him shall be given of the gold of Sheba."}
              , {class: "q2", text: "Men shall pray for him continually."}
              , {class: "q2", text: "They shall bless him all day long."}
              ]
        , 16: [ {class: "q", text: "Abundance of grain shall be throughout the land."}
              , {class: "q2", text: "Its fruit sways like Lebanon."}
              , {class: "q2", text: "Let it flourish, thriving like the grass of the field."}
              ]
        , 17: [ {class: "q", text: "His name endures forever."}
              , {class: "q2", text: "His name continues as long as the sun."}
              , {class: "q", text: "Men shall be blessed by him."}
              , {class: "q2", text: "All nations will call him blessed."}
              ]
        , 18: [ {class: "q", text: "Praise be to Yahweh God, the God of Israel,"}
              , {class: "q2", text: "who alone does marvelous deeds."}
              ]
        , 19: [ {class: "q", text: "Blessed be his glorious name forever!"}
              , {class: "q2", text: "Let the whole earth be filled with his glory!"}
              , {class: "q", text: "Amen and amen."}
              ]
        , 20: [ {class: "vs", text: "This ends the prayers by David, the son of Jesse."} ]
        }
  , 73: { 1:  [ {class: "q", text: "Surely God is good to Israel,"}
              , {class: "q2", text: "to those who are pure in heart."}
              ]
        , 2:  [ {class: "q", text: "But as for me, my feet were almost gone."}
              , {class: "q2", text: "My steps had nearly slipped."}
              ]
        , 3:  [ {class: "q", text: "For I was envious of the arrogant,"}
              , {class: "q2", text: "when I saw the prosperity of the wicked."}
              ]
        , 4:  [ {class: "q", text: "For there are no struggles in their death,"}
              , {class: "q2", text: "but their strength is firm."}
              ]
        , 5:  [ {class: "q", text: "They are free from burdens of men,"}
              , {class: "q2", text: "neither are they plagued like other men."}
              ]
        , 6:  [ {class: "q", text: "Therefore pride is like a chain around their neck."}
              , {class: "q2", text: "Violence covers them like a garment."}
              ]
        , 7:  [ {class: "q", text: "Their eyes bulge with fat."}
              , {class: "q2", text: "Their minds pass the limits of conceit."}
              ]
        , 8:  [ {class: "q", text: "They scoff and speak with malice."}
              , {class: "q2", text: "In arrogance, they threaten oppression."}
              ]
        , 9:  [ {class: "q", text: "They have set their mouth in the heavens."}
              , {class: "q2", text: "Their tongue walks through the earth."}
              ]
        , 10: [ {class: "q", text: "Therefore their people return to them,"}
              , {class: "q2", text: "and they drink up waters of abundance."}
              ]
        , 11: [ {class: "q", text: "They say, “How does God know?"}
              , {class: "q2", text: "Is there knowledge in the Most High?”"}
              ]
        , 12: [ {class: "q", text: "Behold, these are the wicked."}
              , {class: "q2", text: "Being always at ease, they increase in riches."}
              ]
        , 13: [ {class: "q", text: "Surely I have cleansed my heart in vain,"}
              , {class: "q2", text: "and washed my hands in innocence,"}
              ]
        , 14: [ {class: "q", text: "For all day long I have been plagued,"}
              , {class: "q2", text: "and punished every morning."}
              ]
        , 15: [ {class: "q", text: "If I had said, “I will speak thus”;"}
              , {class: "q2", text: "behold, I would have betrayed the generation of your children."}
              ]
        , 16: [ {class: "q", text: "When I tried to understand this,"}
              , {class: "q2", text: "it was too painful for me,"}
              ]
        , 17: [ {class: "q", text: "until I entered God’s sanctuary,"}
              , {class: "q2", text: "and considered their latter end."}
              ]
        , 18: [ {class: "q", text: "Surely you set them in slippery places."}
              , {class: "q2", text: "You throw them down to destruction."}
              ]
        , 19: [ {class: "q", text: "How they are suddenly destroyed!"}
              , {class: "q2", text: "They are completely swept away with terrors."}
              ]
        , 20: [ {class: "q", text: "As a dream when one wakes up,"}
              , {class: "q2", text: "so, Lord, when you awake, you will despise their fantasies."}
              ]
        , 21: [ {class: "q", text: "For my soul was grieved."}
              , {class: "q2", text: "I was embittered in my heart."}
              ]
        , 22: [ {class: "q", text: "I was so senseless and ignorant."}
              , {class: "q2", text: "I was a brute beast before you."}
              ]
        , 23: [ {class: "q", text: "Nevertheless, I am continually with you."}
              , {class: "q2", text: "You have held my right hand."}
              ]
        , 24: [ {class: "q", text: "You will guide me with your counsel,"}
              , {class: "q2", text: "and afterward receive me to glory."}
              ]
        , 25: [ {class: "q", text: "Whom do I have in heaven?"}
              , {class: "q2", text: "There is no one on earth whom I desire besides you."}
              ]
        , 26: [ {class: "q", text: "My flesh and my heart fails,"}
              , {class: "q2", text: "but God is the strength of my heart and my portion forever."}
              ]
        , 27: [ {class: "q", text: "For, behold, those who are far from you shall perish."}
              , {class: "q2", text: "You have destroyed all those who are unfaithful to you."}
              ]
        , 28: [ {class: "q", text: "But it is good for me to come close to God."}
              , {class: "q2", text: "I have made the Lord Yahweh my refuge,"}
              , {class: "q2", text: "that I may tell of all your works."}
              ]
        }
  , 74: { 1:  [ {class: "q", text: "God, why have you rejected us forever?"}
              , {class: "q2", text: "Why does your anger smolder against the sheep of your pasture?"}
              ]
        , 2:  [ {class: "q", text: "Remember your congregation, which you purchased of old,"}
              , {class: "q2", text: "which you have redeemed to be the tribe of your inheritance:"}
              , {class: "q2", text: "Mount Zion, in which you have lived."}
              ]
        , 3:  [ {class: "q", text: "Lift up your feet to the perpetual ruins,"}
              , {class: "q2", text: "all the evil that the enemy has done in the sanctuary."}
              ]
        , 4:  [ {class: "q", text: "Your adversaries have roared in the middle of your assembly."}
              , {class: "q2", text: "They have set up their standards as signs."}
              ]
        , 5:  [ {class: "q", text: "They behaved like men wielding axes,"}
              , {class: "q2", text: "cutting through a thicket of trees."}
              ]
        , 6:  [ {class: "q", text: "Now they break all its carved work down with hatchet and hammers."} ]
        , 7:  [ {class: "q2", text: "/span> They have burned your sanctuary to the ground."}
              , {class: "q2", text: "They have profaned the dwelling place of your Name."}
              ]
        , 8:  [ {class: "q", text: "They said in their heart, “We will crush them completely.”"}
              , {class: "q2", text: "They have burned up all the places in the land where God was worshiped."}
              ]
        , 9:  [ {class: "q", text: "We see no miraculous signs."}
              , {class: "q2", text: "There is no longer any prophet,"}
              , {class: "q2", text: "neither is there among us anyone who knows how long."}
              ]
        , 10: [ {class: "q", text: "How long, God, shall the adversary reproach?"}
              , {class: "q2", text: "Shall the enemy blaspheme your name forever?"}
              ]
        , 11: [ {class: "q", text: "Why do you draw back your hand, even your right hand?"}
              , {class: "q2", text: "Take it from your chest and consume them!"}
              ]
        , 12: [ {class: "q", text: "Yet God is my King of old,"}
              , {class: "q2", text: "working salvation throughout the earth."}
              ]
        , 13: [ {class: "q", text: "You divided the sea by your strength."}
              , {class: "q2", text: "You broke the heads of the sea monsters in the waters."}
              ]
        , 14: [ {class: "q", text: "You broke the heads of Leviathan in pieces."}
              , {class: "q2", text: "You gave him as food to people and desert creatures."}
              ]
        , 15: [ {class: "q", text: "You opened up spring and stream."}
              , {class: "q2", text: "You dried up mighty rivers."}
              ]
        , 16: [ {class: "q", text: "The day is yours, the night is also yours."}
              , {class: "q2", text: "You have prepared the light and the sun."}
              ]
        , 17: [ {class: "q", text: "You have set all the boundaries of the earth."}
              , {class: "q2", text: "You have made summer and winter."}
              ]
        , 18: [ {class: "q", text: "Remember this, that the enemy has mocked you, Yahweh."}
              , {class: "q2", text: "Foolish people have blasphemed your name."}
              ]
        , 19: [ {class: "q", text: "Don’t deliver the soul of your dove to wild beasts."}
              , {class: "q2", text: "Don’t forget the life of your poor forever."}
              ]
        , 20: [ {class: "q", text: "Honor your covenant,"}
              , {class: "q2", text: "for haunts of violence fill the dark places of the earth."}
              ]
        , 21: [ {class: "q", text: "Don’t let the oppressed return ashamed."}
              ,  {class: "q2", text: "Let the poor and needy praise your name."}
              ]
        , 22: [ {class: "q", text: "Arise, God! Plead your own cause."}
              , {class: "q2", text: "Remember how the foolish man mocks you all day."}
              ]
        , 23: [ {class: "q", text: "Don’t forget the voice of your adversaries."}
              , {class: "q2", text: "The tumult of those who rise up against you ascends continually."}
              ]
        }
  , 75: { 1:  [ {class: "q", text: "We give thanks to you, God."}
              , {class: "q2", text: "We give thanks, for your Name is near."}
              , {class: "q2", text: "Men tell about your wondrous works."}
              ]
        , 2:  [ {class: "q", text: "When I choose the appointed time,"}
              , {class: "q2", text: "I will judge blamelessly."}
              ]
        , 3:  [ {class: "q", text: "The earth and all its inhabitants quake."}
              , {class: "q2", text: "I firmly hold its pillars."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "I said to the arrogant, “Don’t boast!”"}
              , {class: "q2", text: "I said to the wicked, “Don’t lift up the horn."}
              ]
        , 5:  [ {class: "q", text: "Don’t lift up your horn on high."}
              , {class: "q2", text: "Don’t speak with a stiff neck.”"}
              ]
        , 6:  [ {class: "q", text: "For neither from the east, nor from the west,"}
              , {class: "q2", text: "nor yet from the south, comes exaltation."}
              ]
        , 7:  [ {class: "q", text: "But God is the judge."}
              , {class: "q2", text: "He puts down one, and lifts up another."}
              ]
        , 8:  [ {class: "q", text: "For in Yahweh’s hand there is a cup,"}
              , {class: "q2", text: "full of foaming wine mixed with spices."}
              , {class: "q", text: "He pours it out."}
              , {class: "q2", text: "Indeed the wicked of the earth drink and drink it to its very dregs."}
              ]
        , 9:  [ {class: "q", text: "But I will declare this forever:"}
              , {class: "q2", text: "I will sing praises to the God of Jacob."}
              ]
        , 10: [ {class: "q", text: "I will cut off all the horns of the wicked,"}
              , {class: "q2", text: "but the horns of the righteous shall be lifted up."}
              ]
        }
  , 76: { 1:  [ {class: "q", text: "In Judah, God is known."}
              , {class: "q2", text: "His name is great in Israel."}
              ]
        , 2:  [ {class: "q", text: "His tabernacle is also in Salem;"}
              , {class: "q2", text: "His dwelling place in Zion."}
              ]
        , 3:  [ {class: "q", text: "There he broke the flaming arrows of the bow,"}
              , {class: "q2", text: "the shield, and the sword, and the weapons of war."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "Glorious are you, and excellent,"}
              , {class: "q2", text: "more than mountains of game."}
              ]
        , 5:  [ {class: "q", text: "Valiant men lie plundered,"}
              , {class: "q2", text: "they have slept their last sleep."}
              , {class: "q2", text: "None of the men of war can lift their hands."}
              ]
        , 6:  [ {class: "q", text: "At your rebuke, God of Jacob,"}
              , {class: "q2", text: "both chariot and horse are cast into a dead sleep."}
              ]
        , 7:  [ {class: "q", text: "You, even you, are to be feared."}
              , {class: "q2", text: "Who can stand in your sight when you are angry?"}
              ]
        , 8:  [ {class: "q", text: "You pronounced judgment from heaven."}
              , {class: "q2", text: "The earth feared, and was silent,"}
              ]
        , 9:  [ {class: "q2", text: "/span> when God arose to judgment,"}
              , {class: "q2", text: "to save all the afflicted ones of the earth."}
              , {class: "qs", text: "Selah"}
              ]
        , 10: [ {class: "q", text: "Surely the wrath of man praises you."}
              , {class: "q2", text: "The survivors of your wrath are restrained."}
              ]
        , 11: [ {class: "q", text: "Make vows to Yahweh your God, and fulfill them!"}
              , {class: "q2", text: "Let all of his neighbors bring presents to him who is to be feared."}
              ]
        , 12: [ {class: "q", text: "He will cut off the spirit of princes."}
              , {class: "q2", text: "He is feared by the kings of the earth."}
              ]
        }
  , 77: { 1:  [ {class: "q", text: "My cry goes to God!"}
              , {class: "q2", text: "Indeed, I cry to God for help,"}
              , {class: "q2", text: "and for him to listen to me."}
              ]
        , 2:  [ {class: "q", text: "In the day of my trouble I sought the Lord."}
              , {class: "q2", text: "My hand was stretched out in the night, and didn’t get tired."}
              , {class: "q2", text: "My soul refused to be comforted."}
              ]
        , 3:  [ {class: "q", text: "I remember God, and I groan."}
              , {class: "q2", text: "I complain, and my spirit is overwhelmed."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "You hold my eyelids open."}
              , {class: "q2", text: "I am so troubled that I can’t speak."}
              ]
        , 5:  [ {class: "q", text: "I have considered the days of old,"}
              , {class: "q2", text: "the years of ancient times."}
              ]
        , 6:  [ {class: "q", text: "I remember my song in the night."}
              , {class: "q2", text: "I consider in my own heart;"}
              , {class: "q2", text: "my spirit diligently inquires:"}
              ]
        , 7:  [ {class: "q", text: "“Will the Lord reject us forever?"}
              , {class: "q2", text: "Will he be favorable no more?"}
              ]
        , 8:  [ {class: "q", text: "Has his loving kindness vanished forever?"}
              , {class: "q2", text: "Does his promise fail for generations?"}
              ]
        , 9:  [ {class: "q", text: "Has God forgotten to be gracious?"}
              , {class: "q2", text: "Has he, in anger, withheld his compassion?”"}
              , {class: "qs", text: "Selah"}
              ]
        , 10: [ {class: "q", text: "Then I thought, “I will appeal to this:"}
              , {class: "q2", text: "the years of the right hand of the Most High.”"}
              ]
        , 11: [ {class: "q", text: "I will remember Yah’s deeds;"}
              , {class: "q2", text: "for I will remember your wonders of old."}
              ]
        , 12: [ {class: "q", text: "I will also meditate on all your work,"}
              ,  {class: "q2", text: "and consider your doings."}
              ]
        , 13: [ {class: "q", text: "Your way, God, is in the sanctuary."}
              , {class: "q2", text: "What god is great like God?"}
              ]
        , 14: [ {class: "q", text: "You are the God who does wonders."}
              , {class: "q2", text: "You have made your strength known among the peoples."}
              ]
        , 15: [ {class: "q", text: "You have redeemed your people with your arm,"}
              , {class: "q2", text: "the sons of Jacob and Joseph."}
              , {class: "qs", text: "Selah"}
              ]
        , 16: [ {class: "q", text: "The waters saw you, God."}
              , {class: "q2", text: "The waters saw you, and they writhed."}
              , {class: "q2", text: "The depths also convulsed."}
              ]
        , 17: [ {class: "q", text: "The clouds poured out water."}
              , {class: "q2", text: "The skies resounded with thunder."}
              , {class: "q2", text: "Your arrows also flashed around."}
              ]
        , 18: [ {class: "q", text: "The voice of your thunder was in the whirlwind."}
              , {class: "q2", text: "The lightnings lit up the world."}
              , {class: "q2", text: "The earth trembled and shook."}
              ]
        , 19: [ {class: "q", text: "Your way was through the sea;"}
              , {class: "q2", text: "your paths through the great waters."}
              , {class: "q2", text: "Your footsteps were not known."}
              ]
        , 20: [ {class: "q", text: "You led your people like a flock,"}
              , {class: "q2", text: "by the hand of Moses and Aaron."}
              ]
        }
  , 78: { 1:  [ {class: "q", text: "Hear my teaching, my people."}
              , {class: "q2", text: "Turn your ears to the words of my mouth."}
              ]
        , 2:  [ {class: "q", text: "I will open my mouth in a parable."}
              , {class: "q2", text: "I will utter dark sayings of old,"}
              ]
        , 3:  [ {class: "q", text: "Which we have heard and known,"}
              , {class: "q2", text: "and our fathers have told us."}
              ]
        , 4:  [ {class: "q", text: "We will not hide them from their children,"}
              , {class: "q2", text: "telling to the generation to come the praises of Yahweh,"}
              , {class: "q2", text: "his strength, and his wondrous deeds that he has done."}
              ]
        , 5:  [ {class: "q", text: "For he established a covenant in Jacob,"}
              , {class: "q2", text: "and appointed a teaching in Israel,"}
              , {class: "q2", text: "which he commanded our fathers,"}
              , {class: "q2", text: "that they should make them known to their children;"}
              ]
        , 6:  [ {class: "q", text: "that the generation to come might know, even the children who should be born;"}
              , {class: "q2", text: "who should arise and tell their children,"}
              ]
        , 7:  [ {class: "q", text: "that they might set their hope in God,"}
              , {class: "q2", text: "and not forget God’s deeds,"}
              , {class: "q2", text: "but keep his commandments,"}
              ]
        , 8:  [ {class: "q", text: "and might not be as their fathers,"}
              , {class: "q2", text: "a stubborn and rebellious generation,"}
              , {class: "q2", text: "a generation that didn’t make their hearts loyal,"}
              , {class: "q2", text: "whose spirit was not steadfast with God."}
              ]
        , 9:  [ {class: "q", text: "The children of Ephraim, being armed and carrying bows,"}
              , {class: "q2", text: "turned back in the day of battle."}
              ]
        , 10: [ {class: "q", text: "They didn’t keep God’s covenant,"}
              , {class: "q2", text: "and refused to walk in his law."}
              ]
        , 11: [ {class: "q", text: "They forgot his doings,"}
              , {class: "q2", text: "his wondrous deeds that he had shown them."}
              ]
        , 12: [ {class: "q", text: "He did marvelous things in the sight of their fathers,"}
              , {class: "q2", text: "in the land of Egypt, in the field of Zoan."}
              ]
        , 13: [ {class: "q", text: "He split the sea, and caused them to pass through."}
              , {class: "q2", text: "He made the waters stand as a heap."}
              ]
        , 14: [ {class: "q", text: "In the daytime he also led them with a cloud,"}
              , {class: "q2", text: "and all night with a light of fire."}
              ]
        , 15: [ {class: "q", text: "He split rocks in the wilderness,"}
              , {class: "q2", text: "and gave them drink abundantly as out of the depths."}
              ]
        , 16: [ {class: "q", text: "He brought streams also out of the rock,"}
              , {class: "q2", text: "and caused waters to run down like rivers."}
              ]
        , 17: [ {class: "q", text: "Yet they still went on to sin against him,"}
              , {class: "q2", text: "to rebel against the Most High in the desert."}
              ]
        , 18: [ {class: "q", text: "They tempted God in their heart"}
              , {class: "q2", text: "by asking food according to their desire."}
              ]
        , 19: [ {class: "q", text: "Yes, they spoke against God."}
              , {class: "q2", text: "They said, “Can God prepare a table in the wilderness?"}
              ]
        , 20: [ {class: "q", text: "Behold, he struck the rock, so that waters gushed out,"}
              , {class: "q2", text: "and streams overflowed."}
              , {class: "q", text: "Can he give bread also?"}
              , {class: "q2", text: "Will he provide meat for his people?”"}
              ]
        , 21: [ {class: "q", text: "Therefore Yahweh heard, and was angry."}
              , {class: "q2", text: "A fire was kindled against Jacob,"}
              , {class: "q2", text: "anger also went up against Israel,"}
              ]
        , 22: [ {class: "q", text: "because they didn’t believe in God,"}
              , {class: "q2", text: "and didn’t trust in his salvation."}
              ]
        , 23: [ {class: "q", text: "Yet he commanded the skies above,"}
              , {class: "q2", text: "and opened the doors of heaven."}
              ]
        , 24: [ {class: "q", text: "He rained down manna on them to eat,"}
              , {class: "q2", text: "and gave them food from the sky."}
              ]
        , 25: [ {class: "q", text: "Man ate the bread of angels."}
              , {class: "q2", text: "He sent them food to the full."}
              ]
        , 26: [ {class: "q", text: "He caused the east wind to blow in the sky."}
              , {class: "q2", text: "By his power he guided the south wind."}
              ]
        , 27: [ {class: "q", text: "He also rained meat on them as the dust,"}
              , {class: "q2", text: "winged birds as the sand of the seas."}
              ]
        , 28: [ {class: "q", text: "He let them fall in the middle of their camp,"}
              , {class: "q2", text: "around their habitations."}
              ]
        , 29: [ {class: "q", text: "So they ate, and were well filled."}
              , {class: "q2", text: "He gave them their own desire."}
              ]
        , 30: [ {class: "q", text: "They didn’t turn from their cravings."}
              , {class: "q2", text: "Their food was yet in their mouths,"}
              ]
        , 31: [ {class: "q2", text: "</span> when the anger of God went up against them,"}
              , {class: "q2", text: "killed some of their fattest,"}
              , {class: "q2", text: "and struck down the young men of Israel."}
              ]
        , 32: [ {class: "q", text: "For all this they still sinned,"}
              , {class: "q2", text: "and didn’t believe in his wondrous works."}
              ]
        , 33: [ {class: "q", text: "Therefore he consumed their days in vanity,"}
              , {class: "q2", text: "and their years in terror."}
              ]
        , 34: [ {class: "q", text: "When he killed them, then they inquired after him."}
              , {class: "q2", text: "They returned and sought God earnestly."}
              ]
        , 35: [ {class: "q", text: "They remembered that God was their rock,"}
              , {class: "q2", text: "the Most High God, their redeemer."}
              ]
        , 36: [ {class: "q", text: "But they flattered him with their mouth,"}
              , {class: "q2", text: "and lied to him with their tongue."}
              ]
        , 37: [ {class: "q", text: "For their heart was not right with him,"}
              , {class: "q2", text: "neither were they faithful in his covenant."}
              ]
        , 38: [ {class: "q", text: "But he, being merciful, forgave iniquity, and didn’t destroy them."}
              , {class: "q2", text: "Yes, many times he turned his anger away,"}
              , {class: "q2", text: "and didn’t stir up all his wrath."}
              ]
        , 39: [ {class: "q", text: "He remembered that they were but flesh,"}
              , {class: "q2", text: "a wind that passes away, and doesn’t come again."}
              ]
        , 40: [ {class: "q", text: "How often they rebelled against him in the wilderness,"}
              , {class: "q2", text: "and grieved him in the desert!"}
              ]
        , 41: [ {class: "q", text: "They turned again and tempted God,"}
              , {class: "q2", text: "and provoked the Holy One of Israel."}
              ]
        , 42: [ {class: "q", text: "They didn’t remember his hand,"}
              , {class: "q2", text: "nor the day when he redeemed them from the adversary;"}
              ]
        , 43: [ {class: "q", text: "how he set his signs in Egypt,"}
              , {class: "q2", text: "his wonders in the field of Zoan,"}
              ]
        , 44: [ {class: "q", text: "he turned their rivers into blood,"}
              , {class: "q2", text: "and their streams, so that they could not drink."}
              ]
        , 45: [ {class: "q", text: "He sent among them swarms of flies, which devoured them;"}
              , {class: "q2", text: "and frogs, which destroyed them."}
              ]
        , 46: [ {class: "q", text: "He gave also their increase to the caterpillar,"}
              , {class: "q2", text: "and their labor to the locust."}
              ]
        , 47: [ {class: "q", text: "He destroyed their vines with hail,"}
              , {class: "q2", text: "their sycamore fig trees with frost."}
              ]
        , 48: [ {class: "q", text: "He gave over their livestock also to the hail,"}
              , {class: "q2", text: "and their flocks to hot thunderbolts."}
              ]
        , 49: [ {class: "q", text: "He threw on them the fierceness of his anger,"}
              , {class: "q2", text: "wrath, indignation, and trouble,"}
              , {class: "q2", text: "and a band of angels of evil."}
              ]
        , 50: [ {class: "q", text: "He made a path for his anger."}
              , {class: "q2", text: "He didn’t spare their soul from death,"}
              , {class: "q2", text: "but gave their life over to the pestilence,"}
              ]
        , 51: [ {class: "q", text: "and struck all the firstborn in Egypt,"}
              , {class: "q2", text: "the chief of their strength in the tents of Ham."}
              ]
        , 52: [ {class: "q", text: "But he led out his own people like sheep,"}
              , {class: "q2", text: "and guided them in the wilderness like a flock."}
              ]
        , 53: [ {class: "q", text: "He led them safely, so that they weren’t afraid,"}
              , {class: "q2", text: "but the sea overwhelmed their enemies."}
              ]
        , 54: [ {class: "q", text: "He brought them to the border of his sanctuary,"}
              , {class: "q2", text: "to this mountain, which his right hand had taken."}
              ]
        , 55: [ {class: "q", text: "He also drove out the nations before them,"}
              , {class: "q2", text: "allotted them for an inheritance by line,"}
              , {class: "q2", text: "and made the tribes of Israel to dwell in their tents."}
              ]
        , 56: [ {class: "q", text: "Yet they tempted and rebelled against the Most High God,"}
              , {class: "q2", text: "and didn’t keep his testimonies,"}
              ]
        , 57: [ {class: "q", text: "but turned back, and dealt treacherously like their fathers."}
              , {class: "q2", text: "They were twisted like a deceitful bow."}
              ]
        , 58: [ {class: "q", text: "For they provoked him to anger with their high places,"}
              , {class: "q2", text: "and moved him to jealousy with their engraved images."}
              ]
        , 59: [ {class: "q", text: "When God heard this, he was angry,"}
              , {class: "q2", text: "and greatly abhorred Israel,"}
              ]
        , 60: [ {class: "q", text: "so that he abandoned the tent of Shiloh,"}
              , {class: "q2", text: "the tent which he placed among men,"}
              ]
        , 61: [ {class: "q", text: "and delivered his strength into captivity,"}
              , {class: "q2", text: "his glory into the adversary’s hand."}
              ]
        , 62: [ {class: "q", text: "He also gave his people over to the sword,"}
              , {class: "q2", text: "and was angry with his inheritance."}
              ]
        , 63: [ {class: "q", text: "Fire devoured their young men."}
              , {class: "q2", text: "Their virgins had no wedding song."}
              ]
        , 64: [ {class: "q", text: "Their priests fell by the sword,"}
              , {class: "q2", text: "and their widows couldn’t weep."}
              ]
        , 65: [ {class: "q", text: "Then the Lord awakened as one out of sleep,"}
              , {class: "q2", text: "like a mighty man who shouts by reason of wine."}
              ]
        , 66: [ {class: "q", text: "He struck his adversaries backward."}
              , {class: "q2", text: "He put them to a perpetual reproach."}
              ]
        , 67: [ {class: "q", text: "Moreover he rejected the tent of Joseph,"}
              , {class: "q2", text: "and didn’t choose the tribe of Ephraim,"}
              ]
        , 68: [ {class: "q", text: "But chose the tribe of Judah,"}
              , {class: "q2", text: "Mount Zion which he loved."}
              ]
        , 69: [ {class: "q", text: "He built his sanctuary like the heights,"}
              , {class: "q2", text: "like the earth which he has established forever."}
              ]
        , 70: [ {class: "q", text: "He also chose David his servant,"}
              , {class: "q2", text: "and took him from the sheepfolds;"}
              ]
        , 71: [ {class: "q", text: "from following the ewes that have their young,"}
              , {class: "q2", text: "he brought him to be the shepherd of Jacob, his people,"}
              , {class: "q2", text: "and Israel, his inheritance."}
              ]
        , 72: [ {class: "q", text: "So he was their shepherd according to the integrity of his heart,"}
              , {class: "q2", text: "and guided them by the skillfulness of his hands."}
              ]
        }
  , 79: { 1:  [ {class: "q", text: "God, the nations have come into your inheritance."}
              , {class: "q2", text: "They have defiled your holy temple."}
              , {class: "q2", text: "They have laid Jerusalem in heaps."}
              ]
        , 2:  [ {class: "q", text: "They have given the dead bodies of your servants to be food for the birds of the sky,"}
              , {class: "q2", text: "the flesh of your saints to the animals of the earth."}
              ]
        , 3:  [ {class: "q", text: "They have shed their blood like water around Jerusalem."}
              , {class: "q2", text: "There was no one to bury them."}
              ]
        , 4:  [ {class: "q", text: "We have become a reproach to our neighbors,"}
              , {class: "q2", text: "a scoffing and derision to those who are around us."}
              ]
        , 5:  [ {class: "q", text: "How long, Yahweh?"}
              , {class: "q2", text: "Will you be angry forever?"}
              , {class: "q2", text: "Will your jealousy burn like fire?"}
              ]
        , 6:  [ {class: "q", text: "Pour out your wrath on the nations that don’t know you,"}
              , {class: "q2", text: "on the kingdoms that don’t call on your name;"}
              ]
        , 7:  [ {class: "q", text: "for they have devoured Jacob,"}
              , {class: "q2", text: "and destroyed his homeland."}
              ]
        , 8:  [ {class: "q", text: "Don’t hold the iniquities of our forefathers against us."}
              , {class: "q2", text: "Let your tender mercies speedily meet us,"}
              , {class: "q2", text: "for we are in desperate need."}
              ]
        , 9:  [ {class: "q", text: "Help us, God of our salvation, for the glory of your name."}
              ,  {class: "q2", text: "Deliver us, and forgive our sins, for your name’s sake."}
              ]
        , 10: [ {class: "q", text: "Why should the nations say, “Where is their God?”"}
              , {class: "q2", text: "Let it be known among the nations, before our eyes,"}
              , {class: "q2", text: "that vengeance for your servants’ blood is being poured out."}
              ]
        , 11: [ {class: "q", text: "Let the sighing of the prisoner come before you."}
              , {class: "q2", text: "According to the greatness of your power, preserve those who are sentenced to death."}
              ]
        , 12: [ {class: "q", text: "Pay back to our neighbors seven times into their bosom"}
              , {class: "q2", text: "their reproach with which they have reproached you, Lord."}
              ]
        , 13: [ {class: "q", text: "So we, your people and sheep of your pasture,"}
              , {class: "q2", text: "will give you thanks forever."}
              , {class: "q2", text: "We will praise you forever, to all generations."}
              ]
        }
  , 8:  { 1:  [ {class: "q", text: "Yahweh, our Lord, how majestic is your name in all the earth!"}
              , {class: "q2", text: "You have set your glory above the heavens!"}
              ]
        , 2:  [ {class: "q", text: "From the lips of babes and infants you have established strength,"}
              , {class: "q2", text: "because of your adversaries, that you might silence the enemy and the avenger."}
              ]
        , 3:  [ {class: "q", text: "When I consider your heavens, the work of your fingers,"}
              , {class: "q2", text: "the moon and the stars, which you have ordained;"}
              ]
        , 4:  [ {class: "q", text: "what is man, that you think of him?"}
              , {class: "q2", text: "What is the son of man, that you care for him?"}
              ]
        , 5:  [ {class: "q", text: "For you have made him a little lower than the angels,"}
              , {class: "q2", text: "and crowned him with glory and honor."}
              ]
        , 6:  [ {class: "q", text: "You make him ruler over the works of your hands."}
              , {class: "q2", text: "You have put all things under his feet:"}
              ]
        , 7:  [ {class: "q", text: "All sheep and cattle,"}
              , {class: "q2", text: "yes, and the animals of the field,"}
              ]
        , 8:  [ {class: "q2", text: "/span> the birds of the sky, the fish of the sea,"}
              , {class: "q2", text: "and whatever passes through the paths of the seas."}
              ]
        , 9:  [ {class: "q", text: "Yahweh, our Lord,"}
              , {class: "q2", text: "how majestic is your name in all the earth!"}
              ]
        }
  , 80: { 1:  [ {class: "q", text: "Hear us, Shepherd of Israel,"}
              , {class: "q2", text: "you who lead Joseph like a flock,"}
              , {class: "q2", text: "you who sit above the cherubim, shine out."}
              ]
        , 2:  [ {class: "q", text: "Before Ephraim and Benjamin and Manasseh, stir up your might!"}
              , {class: "q2", text: "Come to save us!"}
              ]
        , 3:  [ {class: "q", text: "Turn us again, God."}
              , {class: "q2", text: "Cause your face to shine,"}
              , {class: "q2", text: "and we will be saved."}
              ]
        , 4:  [ {class: "q", text: "Yahweh God of Armies,"}
              , {class: "q2", text: "how long will you be angry against the prayer of your people?"}
              ]
        , 5:  [ {class: "q", text: "You have fed them with the bread of tears,"}
              , {class: "q2", text: "and given them tears to drink in large measure."}
              ]
        , 6:  [ {class: "q", text: "You make us a source of contention to our neighbors."}
              , {class: "q2", text: "Our enemies laugh among themselves."}
              ]
        , 7:  [ {class: "q", text: "Turn us again, God of Armies."}
              , {class: "q2", text: "Cause your face to shine,"}
              , {class: "q2", text: "and we will be saved."}
              ]
        , 8:  [ {class: "q", text: "You brought a vine out of Egypt."}
              , {class: "q2", text: "You drove out the nations, and planted it."}
              ]
        , 9:  [ {class: "q", text: "You cleared the ground for it."}
              , {class: "q2", text: "It took deep root, and filled the land."}
              ]
        , 10: [ {class: "q", text: "The mountains were covered with its shadow."}
              , {class: "q2", text: "Its boughs were like God’s cedars."}
              ]
        , 11: [ {class: "q", text: "It sent out its branches to the sea,"}
              , {class: "q2", text: "its shoots to the River."}
              ]
        , 12: [ {class: "q", text: "Why have you broken down its walls,"}
              , {class: "q2", text: "so that all those who pass by the way pluck it?"}
              ]
        , 13: [ {class: "q", text: "The boar out of the wood ravages it."}
              , {class: "q2", text: "The wild animals of the field feed on it."}
              ]
        , 14: [ {class: "q", text: "Turn again, we beg you, God of Armies."}
              , {class: "q2", text: "Look down from heaven, and see, and visit this vine,"}
              ]
        , 15: [ {class: "q", text: "the stock which your right hand planted,"}
              , {class: "q2", text: "the branch that you made strong for yourself."}
              ]
        , 16: [ {class: "q", text: "It’s burned with fire."}
              , {class: "q2", text: "It’s cut down."}
              , {class: "q2", text: "They perish at your rebuke."}
              ]
        , 17: [ {class: "q", text: "Let your hand be on the man of your right hand,"}
              , {class: "q2", text: "on the son of man whom you made strong for yourself."}
              ]
        , 18: [ {class: "q", text: "So we will not turn away from you."}
              , {class: "q2", text: "Revive us, and we will call on your name."}
              ]
        , 19: [ {class: "q", text: "Turn us again, Yahweh God of Armies."}
              , {class: "q2", text: "Cause your face to shine, and we will be saved."}
              ]
        }
  , 81: { 1:  [ {class: "q", text: "Sing aloud to God, our strength!"}
              , {class: "q2", text: "Make a joyful shout to the God of Jacob!"}
              ]
        , 2:  [ {class: "q", text: "Raise a song, and bring here the tambourine,"}
              , {class: "q2", text: "the pleasant lyre with the harp."}
              ]
        , 3:  [ {class: "q", text: "Blow the trumpet at the New Moon,"}
              , {class: "q2", text: "at the full moon, on our feast day."}
              ]
        , 4:  [ {class: "q", text: "For it is a statute for Israel,"}
              , {class: "q2", text: "an ordinance of the God of Jacob."}
              ]
        , 5:  [ {class: "q", text: "He appointed it in Joseph for a covenant,"}
              , {class: "q2", text: "when he went out over the land of Egypt,"}
              , {class: "q2", text: "I heard a language that I didn’t know."}
              ]
        , 6:  [ {class: "q", text: "“I removed his shoulder from the burden."}
              , {class: "q2", text: "His hands were freed from the basket."}
              ]
        , 7:  [ {class: "q", text: "You called in trouble, and I delivered you."}
              , {class: "q2", text: "I answered you in the secret place of thunder."}
              , {class: "q2", text: "I tested you at the waters of Meribah.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "“Hear, my people, and I will testify to you,"}
              , {class: "q2", text: "Israel, if you would listen to me!"}
              ]
        , 9:  [ {class: "q", text: "There shall be no strange god in you,"}
              , {class: "q2", text: "neither shall you worship any foreign god."}
              ]
        , 10: [ {class: "q", text: "I am Yahweh, your God,"}
              , {class: "q2", text: "who brought you up out of the land of Egypt."}
              , {class: "q2", text: "Open your mouth wide, and I will fill it."}
              ]
        , 11: [ {class: "q", text: "But my people didn’t listen to my voice."}
              , {class: "q2", text: "Israel desired none of me."}
              ]
        , 12: [ {class: "q", text: "So I let them go after the stubbornness of their hearts,"}
              , {class: "q2", text: "that they might walk in their own counsels."}
              ]
        , 13: [ {class: "q", text: "Oh that my people would listen to me,"}
              , {class: "q2", text: "that Israel would walk in my ways!"}
              ]
        , 14: [ {class: "q", text: "I would soon subdue their enemies,"}
              , {class: "q2", text: "and turn my hand against their adversaries."}
              ]
        , 15: [ {class: "q", text: "The haters of Yahweh would cringe before him,"}
              , {class: "q2", text: "and their punishment would last forever."}
              ]
        , 16: [ {class: "q", text: "But he would have also fed them with the finest of the wheat."}
              , {class: "q2", text: "I will satisfy you with honey out of the rock.”"}
              ]
        }
  , 82: { 1:  [ {class: "q", text: "God presides in the great assembly."}
              , {class: "q2", text: "He judges among the gods."}
              ]
        , 2:  [ {class: "q", text: "“How long will you judge unjustly,"}
              , {class: "q2", text: "and show partiality to the wicked?”"}
              , {class: "qs", text: "Selah"}
              ]
        , 3:  [ {class: "q", text: "“Defend the weak, the poor, and the fatherless."}
              , {class: "q2", text: "Maintain the rights of the poor and oppressed."}
              ]
        , 4:  [ {class: "q", text: "Rescue the weak and needy."}
              , {class: "q2", text: "Deliver them out of the hand of the wicked.”"}
              ]
        , 5:  [ {class: "q", text: "They don’t know, neither do they understand."}
              , {class: "q2", text: "They walk back and forth in darkness."}
              , {class: "q2", text: "All the foundations of the earth are shaken."}
              ]
        , 6:  [ {class: "q", text: "I said, “You are gods,"}
              , {class: "q2", text: "all of you are sons of the Most High."}
              ]
        , 7:  [ {class: "q", text: "Nevertheless you shall die like men,"}
              , {class: "q2", text: "and fall like one of the rulers.”"}
              ]
        , 8:  [ {class: "q", text: "Arise, God, judge the earth,"}
              , {class: "q2", text: "for you inherit all of the nations."}
              ]
        }
  , 83: { 1:  [ {class: "q", text: "God, don’t keep silent."}
              , {class: "q2", text: "Don’t keep silent,"}
              , {class: "q2", text: "and don’t be still, God."}
              ]
        , 2:  [ {class: "q", text: "For, behold, your enemies are stirred up."}
              , {class: "q2", text: "Those who hate you have lifted up their heads."}
              ]
        , 3:  [ {class: "q", text: "They conspire with cunning against your people."}
              , {class: "q2", text: "They plot against your cherished ones."}
              ]
        , 4:  [ {class: "q", text: "“Come,” they say, “let’s destroy them as a nation,"}
              , {class: "q2", text: "that the name of Israel may be remembered no more.”"}
              ]
        , 5:  [ {class: "q", text: "For they have conspired together with one mind."}
              , {class: "q2", text: "They form an alliance against you."}
              ]
        , 6:  [ {class: "q", text: "The tents of Edom and the Ishmaelites;"}
              , {class: "q2", text: "Moab, and the Hagrites;"}
              ]
        , 7:  [ {class: "q", text: "Gebal, Ammon, and Amalek;"}
              , {class: "q2", text: "Philistia with the inhabitants of Tyre;"}
              ]
        , 8:  [ {class: "q", text: "Assyria also is joined with them."}
              , {class: "q2", text: "They have helped the children of Lot."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "Do to them as you did to Midian,"}
              , {class: "q2", text: "as to Sisera, as to Jabin, at the river Kishon;"}
              ]
        , 10: [ {class: "q", text: "who perished at Endor,"}
              , {class: "q2", text: "who became as dung for the earth."}
              ]
        , 11: [ {class: "q", text: "Make their nobles like Oreb and Zeeb,"}
              , {class: "q2", text: "yes, all their princes like Zebah and Zalmunna,"}
              ]
        , 12: [ {class: "q2", text: "</span> who said, “Let’s take possession of God’s pasture lands.”"} ]
        , 13: [ {class: "q", text: "My God, make them like tumbleweed,"}
              , {class: "q2", text: "like chaff before the wind."}
              ]
        , 14: [ {class: "q", text: "As the fire that burns the forest,"}
              , {class: "q2", text: "as the flame that sets the mountains on fire,"}
              ]
        , 15: [ {class: "q2", text: "</span> so pursue them with your tempest,"}
              , {class: "q2", text: "and terrify them with your storm."}
              ]
        , 16: [ {class: "q", text: "Fill their faces with confusion,"}
              , {class: "q2", text: "that they may seek your name, Yahweh."}
              ]
        , 17: [ {class: "q", text: "Let them be disappointed and dismayed forever."}
              , {class: "q2", text: "Yes, let them be confounded and perish;"}
              ]
        , 18: [ {class: "q", text: "that they may know that you alone, whose name is Yahweh,"}
              , {class: "q2", text: "are the Most High over all the earth."}
              ]
        }
  , 84: { 1:  [ {class: "q", text: "How lovely are your dwellings,"}
              , {class: "q2", text: "Yahweh of Armies!"}
              ]
        , 2:  [ {class: "q", text: "My soul longs, and even faints for the courts of Yahweh."}
              , {class: "q2", text: "My heart and my flesh cry out for the living God."}
              ]
        , 3:  [ {class: "q", text: "Yes, the sparrow has found a home,"}
              , {class: "q2", text: "and the swallow a nest for herself, where she may have her young,"}
              , {class: "q2", text: "near your altars, Yahweh of Armies, my King, and my God."}
              ]
        , 4:  [ {class: "q", text: "Blessed are those who dwell in your house."}
              , {class: "q2", text: "They are always praising you."}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "Blessed are those whose strength is in you,"}
              , {class: "q2", text: "who have set their hearts on a pilgrimage."}
              ]
        , 6:  [ {class: "q", text: "Passing through the valley of Weeping, they make it a place of springs."}
              , {class: "q2", text: "Yes, the autumn rain covers it with blessings."}
              ]
        , 7:  [ {class: "q", text: "They go from strength to strength."}
              , {class: "q2", text: "Every one of them appears before God in Zion."}
              ]
        , 8:  [ {class: "q", text: "Yahweh, God of Armies, hear my prayer."}
              , {class: "q2", text: "Listen, God of Jacob."}
              , {class: "qs", text: "Selah"}
              ]
        , 9:  [ {class: "q", text: "Behold, God our shield,"}
              , {class: "q2", text: "look at the face of your anointed."}
              ]
        , 10: [ {class: "q", text: "For a day in your courts is better than a thousand."}
              , {class: "q2", text: "I would rather be a doorkeeper in the house of my God,"}
              , {class: "q2", text: "than to dwell in the tents of wickedness."}
              ]
        , 11: [ {class: "q", text: "For Yahweh God is a sun and a shield."}
              , {class: "q2", text: "Yahweh will give grace and glory."}
              , {class: "q2", text: "He withholds no good thing from those who walk blamelessly."}
              ]
        , 12: [ {class: "q", text: "Yahweh of Armies,"}
              , {class: "q2", text: "blessed is the man who trusts in you."}
              ]
        }
  , 85: { 1:  [ {class: "q", text: "Yahweh, you have been favorable to your land."}
              , {class: "q2", text: "You have restored the fortunes of Jacob."}
              ]
        , 2:  [ {class: "q", text: "You have forgiven the iniquity of your people."}
              , {class: "q2", text: "You have covered all their sin."}
              , {class: "qs", text: "Selah"}
              ]
        , 3:  [ {class: "q", text: "You have taken away all your wrath."}
              , {class: "q2", text: "You have turned from the fierceness of your anger."}
              ]
        , 4:  [ {class: "q", text: "Turn us, God of our salvation,"}
              , {class: "q2", text: "and cause your indignation toward us to cease."}
              ]
        , 5:  [ {class: "q", text: "Will you be angry with us forever?"}
              , {class: "q2", text: "Will you draw out your anger to all generations?"}
              ]
        , 6:  [ {class: "q", text: "Won’t you revive us again,"}
              , {class: "q2", text: "that your people may rejoice in you?"}
              ]
        , 7:  [ {class: "q", text: "Show us your loving kindness, Yahweh."}
              , {class: "q2", text: "Grant us your salvation."}
              ]
        , 8:  [ {class: "q", text: "I will hear what God, Yahweh, will speak,"}
              , {class: "q2", text: "for he will speak peace to his people, his saints;"}
              , {class: "q2", text: "but let them not turn again to folly."}
              ]
        , 9:  [ {class: "q", text: "Surely his salvation is near those who fear him,"}
              , {class: "q2", text: "that glory may dwell in our land."}
              ]
        , 10: [ {class: "q", text: "Mercy and truth meet together."}
              , {class: "q2", text: "Righteousness and peace have kissed each other."}
              ]
        , 11: [ {class: "q", text: "Truth springs out of the earth."}
              , {class: "q2", text: "Righteousness has looked down from heaven."}
              ]
        , 12: [ {class: "q", text: "Yes, Yahweh will give that which is good."}
              , {class: "q2", text: "Our land will yield its increase."}
              ]
        , 13: [ {class: "q", text: "Righteousness goes before him,"}
              , {class: "q2", text: "And prepares the way for his steps."}
              ]
        }
  , 86: { 1:  [ {class: "q", text: "Hear, Yahweh, and answer me,"}
              , {class: "q2", text: "for I am poor and needy."}
              ]
        , 2:  [ {class: "q", text: "Preserve my soul, for I am godly."}
              ,  {class: "q2", text: "You, my God, save your servant who trusts in you."}
              ]
        , 3:  [ {class: "q", text: "Be merciful to me, Lord,"}
              , {class: "q2", text: "for I call to you all day long."}
              ]
        , 4:  [ {class: "q", text: "Bring joy to the soul of your servant,"}
              ,  {class: "q2", text: "for to you, Lord, do I lift up my soul."}
              ]
        , 5:  [ {class: "q", text: "For you, Lord, are good, and ready to forgive,"}
              ,  {class: "q2", text: "abundant in loving kindness to all those who call on you."}
              ]
        , 6:  [ {class: "q", text: "Hear, Yahweh, my prayer."}
              , {class: "q2", text: "Listen to the voice of my petitions."}
              ]
        , 7:  [ {class: "q", text: "In the day of my trouble I will call on you,"}
              , {class: "q2", text: "for you will answer me."}
              ]
        , 8:  [ {class: "q", text: "There is no one like you among the gods, Lord,"}
              ,  {class: "q2", text: "nor any deeds like your deeds."}
              ]
        , 9:  [ {class: "q", text: "All nations you have made will come and worship before you, Lord."}
              , {class: "q2", text: "They shall glorify your name."}
              ]
        , 10: [ {class: "q", text: "For you are great, and do wondrous things."}
              , {class: "q2", text: "You are God alone."}
              ]
        , 11: [ {class: "q", text: "Teach me your way, Yahweh."}
              , {class: "q2", text: "I will walk in your truth."}
              , {class: "q2", text: "Make my heart undivided to fear your name."}
              ]
        , 12: [ {class: "q", text: "I will praise you, Lord my God, with my whole heart."}
              , {class: "q2", text: "I will glorify your name forever more."}
              ]
        , 13: [ {class: "q", text: "For your loving kindness is great toward me."}
              , {class: "q2", text: "You have delivered my soul from the lowest Sheol."}
              ]
        , 14: [ {class: "q", text: "God, the proud have risen up against me."}
              , {class: "q2", text: "A company of violent men have sought after my soul,"}
              , {class: "q2", text: "and they don’t hold regard for you before them."}
              ]
        , 15: [ {class: "q", text: "But you, Lord, are a merciful and gracious God,"}
              , {class: "q2", text: "slow to anger, and abundant in loving kindness and truth."}
              ]
        , 16: [ {class: "q", text: "Turn to me, and have mercy on me!"}
              , {class: "q2", text: "Give your strength to your servant."}
              , {class: "q2", text: "Save the son of your servant."}
              ]
        , 17: [ {class: "q", text: "Show me a sign of your goodness,"}
              , {class: "q2", text: "that those who hate me may see it, and be shamed,"}
              , {class: "q2", text: "because you, Yahweh, have helped me, and comforted me."}
              ]
        }
  , 87: { 1:  [ {class: "q", text: "His foundation is in the holy mountains."} ]
        , 2:  [ {class: "q2", text: "/span> Yahweh loves the gates of Zion more than all the dwellings of Jacob."} ]
        , 3:  [ {class: "q", text: "Glorious things are spoken about you, city of God."}
              , {class: "qs", text: "Selah"}
              ]
        , 4:  [ {class: "q", text: "I will record Rahab and Babylon among those who acknowledge me."}
              , {class: "q2", text: "Behold, Philistia, Tyre, and also Ethiopia:"}
              , {class: "q2", text: "“This one was born there.”"}
              ]
        , 5:  [ {class: "q", text: "Yes, of Zion it will be said, “This one and that one was born in her;”"}
              , {class: "q2", text: "the Most High himself will establish her."}
              ]
        , 6:  [ {class: "q", text: "Yahweh will count, when he writes up the peoples,"}
              , {class: "q2", text: "“This one was born there.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 7:  [ {class: "q", text: "Those who sing as well as those who dance say,"}
              , {class: "q2", text: "“All my springs are in you.”"}
              ]
        }
  , 88: { 1:  [ {class: "q", text: "Yahweh, the God of my salvation,"}
              , {class: "q2", text: "I have cried day and night before you."}
              ]
        , 2:  [ {class: "q", text: "Let my prayer enter into your presence."}
              , {class: "q2", text: "Turn your ear to my cry."}
              ]
        , 3:  [ {class: "q", text: "For my soul is full of troubles."}
              , {class: "q2", text: "My life draws near to Sheol."}
              ]
        , 4:  [ {class: "q", text: "I am counted among those who go down into the pit."}
              , {class: "q2", text: "I am like a man who has no help,"}
              ]
        , 5:  [ {class: "q2", text: "/span> set apart among the dead,"}
              , {class: "q2", text: "like the slain who lie in the grave,"}
              , {class: "q2", text: "whom you remember no more."}
              , {class: "q2", text: "They are cut off from your hand."}
              ]
        , 6:  [ {class: "q", text: "You have laid me in the lowest pit,"}
              , {class: "q2", text: "in the darkest depths."}
              ]
        , 7:  [ {class: "q", text: "Your wrath lies heavily on me."}
              , {class: "q2", text: "You have afflicted me with all your waves."}
              , {class: "qs", text: "Selah"}
              ]
        , 8:  [ {class: "q", text: "You have taken my friends from me."}
              , {class: "q2", text: "You have made me an abomination to them."}
              , {class: "q2", text: "I am confined, and I can’t escape."}
              ]
        , 9:  [ {class: "q", text: "My eyes are dim from grief."}
              , {class: "q2", text: "I have called on you daily, Yahweh."}
              , {class: "q2", text: "I have spread out my hands to you."}
              ]
        , 10: [ {class: "q", text: "Do you show wonders to the dead?"}
              , {class: "q2", text: "Do the departed spirits rise up and praise you?"}
              , {class: "qs", text: "Selah"}
              ]
        , 11: [ {class: "q", text: "Is your loving kindness declared in the grave?"}
              , {class: "q2", text: "Or your faithfulness in Destruction?"}
              ]
        , 12: [ {class: "q", text: "Are your wonders made known in the dark?"}
              , {class: "q2", text: "Or your righteousness in the land of forgetfulness?"}
              ]
        , 13: [ {class: "q", text: "But to you, Yahweh, I have cried."}
              , {class: "q2", text: "In the morning, my prayer comes before you."}
              ]
        , 14: [ {class: "q", text: "Yahweh, why do you reject my soul?"}
              , {class: "q2", text: "Why do you hide your face from me?"}
              ]
        , 15: [ {class: "q", text: "I am afflicted and ready to die from my youth up."}
              , {class: "q2", text: "While I suffer your terrors, I am distracted."}
              ]
        , 16: [ {class: "q", text: "Your fierce wrath has gone over me."}
              , {class: "q2", text: "Your terrors have cut me off."}
              ]
        , 17: [ {class: "q", text: "They came around me like water all day long."}
              , {class: "q2", text: "They completely engulfed me."}
              ]
        , 18: [ {class: "q", text: "You have put lover and friend far from me,"}
              , {class: "q2", text: "and my friends into darkness."}
              ]
        }
  , 89: { 1:  [ {class: "q", text: "I will sing of the loving kindness of Yahweh forever."}
              , {class: "q2", text: "With my mouth, I will make known your faithfulness to all generations."}
              ]
        , 2:  [ {class: "q", text: "I indeed declare, “Love stands firm forever."}
              , {class: "q2", text: "You established the heavens."}
              , {class: "q2", text: "Your faithfulness is in them.”"}
              ]
        , 3:  [ {class: "q", text: "“I have made a covenant with my chosen one,"}
              , {class: "q2", text: "I have sworn to David, my servant,"}
              ]
        , 4:  [ {class: "q", text: "‘I will establish your offspring forever,"}
              , {class: "q2", text: "and build up your throne to all generations.’”"}
              , {class: "qs", text: "Selah"}
              ]
        , 5:  [ {class: "q", text: "The heavens will praise your wonders, Yahweh,"}
              , {class: "q2", text: "your faithfulness also in the assembly of the holy ones."}
              ]
        , 6:  [ {class: "q", text: "For who in the skies can be compared to Yahweh?"}
              , {class: "q2", text: "Who among the sons of the heavenly beings is like Yahweh,"}
              ]
        , 7:  [ {class: "q", text: "a very awesome God in the council of the holy ones,"}
              , {class: "q2", text: "to be feared above all those who are around him?"}
              ]
        , 8:  [ {class: "q", text: "Yahweh, God of Armies, who is a mighty one, like you?"}
              , {class: "q2", text: "Yah, your faithfulness is around you."}
              ]
        , 9:  [ {class: "q", text: "You rule the pride of the sea."}
              , {class: "q2", text: "When its waves rise up, you calm them."}
              ]
        , 10: [ {class: "q", text: "You have broken Rahab in pieces, like one of the slain."}
              , {class: "q2", text: "You have scattered your enemies with your mighty arm."}
              ]
        , 11: [ {class: "q", text: "The heavens are yours."}
              , {class: "q2", text: "The earth also is yours,"}
              , {class: "q2", text: "the world and its fullness."}
              , {class: "q2", text: "You have founded them."}
              ]
        , 12: [ {class: "q", text: "You have created the north and the south."}
              , {class: "q2", text: "Tabor and Hermon rejoice in your name."}
              ]
        , 13: [ {class: "q", text: "You have a mighty arm."}
              , {class: "q2", text: "Your hand is strong, and your right hand is exalted."}
              ]
        , 14: [ {class: "q", text: "Righteousness and justice are the foundation of your throne."}
              , {class: "q2", text: "Loving kindness and truth go before your face."}
              ]
        , 15: [ {class: "q", text: "Blessed are the people who learn to acclaim you."}
              , {class: "q2", text: "They walk in the light of your presence, Yahweh."}
              ]
        , 16: [ {class: "q", text: "In your name they rejoice all day."}
              , {class: "q2", text: "In your righteousness, they are exalted."}
              ]
        , 17: [ {class: "q", text: "For you are the glory of their strength."}
              , {class: "q2", text: "In your favor, our horn will be exalted."}
              ]
        , 18: [ {class: "q", text: "For our shield belongs to Yahweh,"}
              , {class: "q2", text: "our king to the Holy One of Israel."}
              ]
        , 19: [ {class: "q", text: "Then you spoke in vision to your saints,"}
              , {class: "q2", text: "and said, “I have given strength to the warrior."}
              , {class: "q2", text: "I have exalted a young man from the people."}
              ]
        , 20: [ {class: "q", text: "I have found David, my servant."}
              , {class: "q2", text: "I have anointed him with my holy oil,"}
              ]
        , 21: [ {class: "q", text: "with whom my hand shall be established."}
              , {class: "q2", text: "My arm will also strengthen him."}
              ]
        , 22: [ {class: "q", text: "No enemy will tax him."}
              , {class: "q2", text: "No wicked man will oppress him."}
              ]
        , 23: [ {class: "q", text: "I will beat down his adversaries before him,"}
              , {class: "q2", text: "and strike those who hate him."}
              ]
        , 24: [ {class: "q", text: "But my faithfulness and my loving kindness will be with him."}
              , {class: "q2", text: "In my name, his horn will be exalted."}
              ]
        , 25: [ {class: "q", text: "I will set his hand also on the sea,"}
              , {class: "q2", text: "and his right hand on the rivers."}
              ]
        , 26: [ {class: "q", text: "He will call to me, ‘You are my Father,"}
              , {class: "q2", text: "my God, and the rock of my salvation!’"}
              ]
        , 27: [ {class: "q", text: "I will also appoint him my firstborn,"}
              , {class: "q2", text: "the highest of the kings of the earth."}
              ]
        , 28: [ {class: "q", text: "I will keep my loving kindness for him forever more."}
              , {class: "q2", text: "My covenant will stand firm with him."}
              ]
        , 29: [ {class: "q", text: "I will also make his offspring endure forever,"}
              , {class: "q2", text: "and his throne as the days of heaven."}
              ]
        , 30: [ {class: "q", text: "If his children forsake my law,"}
              , {class: "q2", text: "and don’t walk in my ordinances;"}
              ]
        , 31: [ {class: "q", text: "if they break my statutes,"}
              , {class: "q2", text: "and don’t keep my commandments;"}
              ]
        , 32: [ {class: "q", text: "then I will punish their sin with the rod,"}
              , {class: "q2", text: "and their iniquity with stripes."}
              ]
        , 33: [ {class: "q", text: "But I will not completely take my loving kindness from him,"}
              , {class: "q2", text: "nor allow my faithfulness to fail."}
              ]
        , 34: [ {class: "q", text: "I will not break my covenant,"}
              , {class: "q2", text: "nor alter what my lips have uttered."}
              ]
        , 35: [ {class: "q", text: "Once I have sworn by my holiness,"}
              , {class: "q2", text: "I will not lie to David."}
              ]
        , 36: [ {class: "q", text: "His offspring will endure forever,"}
              , {class: "q2", text: "his throne like the sun before me."}
              ]
        , 37: [ {class: "q", text: "It will be established forever like the moon,"}
              , {class: "q2", text: "the faithful witness in the sky.”"}
              , {class: "qs", text: "Selah"}
              ]
        , 38: [ {class: "q", text: "But you have rejected and spurned."}
              , {class: "q2", text: "You have been angry with your anointed."}
              ]
        , 39: [ {class: "q", text: "You have renounced the covenant of your servant."}
              , {class: "q2", text: "You have defiled his crown in the dust."}
              ]
        , 40: [ {class: "q", text: "You have broken down all his hedges."}
              , {class: "q2", text: "You have brought his strongholds to ruin."}
              ]
        , 41: [ {class: "q", text: "All who pass by the way rob him."}
              , {class: "q", text: "He has become a reproach to his neighbors."}
              ]
        , 42: [ {class: "q", text: "You have exalted the right hand of his adversaries."}
              , {class: "q2", text: "You have made all of his enemies rejoice."}
              ]
        , 43: [ {class: "q", text: "Yes, you turn back the edge of his sword,"}
              , {class: "q2", text: "and haven’t supported him in battle."}
              ]
        , 44: [ {class: "q", text: "You have ended his splendor,"}
              , {class: "q2", text: "and thrown his throne down to the ground."}
              ]
        , 45: [ {class: "q", text: "You have shortened the days of his youth."}
              , {class: "q2", text: "You have covered him with shame."}
              , {class: "qs", text: "Selah"}
              ]
        , 46: [ {class: "q", text: "How long, Yahweh?"}
              , {class: "q2", text: "Will you hide yourself forever?"}
              , {class: "q2", text: "Will your wrath burn like fire?"}
              ]
        , 47: [ {class: "q", text: "Remember how short my time is,"}
              , {class: "q2", text: "for what vanity you have created all the children of men!"}
              ]
        , 48: [ {class: "q", text: "What man is he who shall live and not see death,"}
              ,  {class: "q2", text: "who shall deliver his soul from the power of Sheol?"}
              , {class: "qs", text: "Selah"}
              ]
        , 49: [ {class: "q", text: "Lord, where are your former loving kindnesses,"}
              , {class: "q2", text: "which you swore to David in your faithfulness?"}
              ]
        , 50: [ {class: "q", text: "Remember, Lord, the reproach of your servants,"}
              , {class: "q2", text: "how I bear in my heart the taunts of all the mighty peoples,"}
              ]
        , 51: [ {class: "q", text: "With which your enemies have mocked, Yahweh,"}
              , {class: "q2", text: "with which they have mocked the footsteps of your anointed one."}
              ]
        , 52: [ {class: "q", text: "Blessed be Yahweh forever more."}
              , {class: "q", text: "Amen, and Amen."}
              ]
        }
  , 9:  { 1:  [ {class: "q", text: "I will give thanks to Yahweh with my whole heart."}
              , {class: "q2", text: "I will tell of all your marvelous works."}
              ]
        , 2:  [ {class: "q", text: "I will be glad and rejoice in you."}
              , {class: "q2", text: "I will sing praise to your name, O Most High."}
              ]
        , 3:  [ {class: "q", text: "When my enemies turn back,"}
              , {class: "q2", text: "they stumble and perish in your presence."}
              ]
        , 4:  [ {class: "q", text: "For you have maintained my just cause."}
              , {class: "q2", text: "You sit on the throne judging righteously."}
              ]
        , 5:  [ {class: "q", text: "You have rebuked the nations."}
              , {class: "q2", text: "You have destroyed the wicked."}
              , {class: "q2", text: "You have blotted out their name forever and ever."}
              ]
        , 6:  [ {class: "q", text: "The enemy is overtaken by endless ruin."}
              , {class: "q2", text: "The very memory of the cities which you have overthrown has perished."}
              ]
        , 7:  [ {class: "q", text: "But Yahweh reigns forever."}
              , {class: "q2", text: "He has prepared his throne for judgment."}
              ]
        , 8:  [ {class: "q", text: "He will judge the world in righteousness."}
              , {class: "q2", text: "He will administer judgment to the peoples in uprightness."}
              ]
        , 9:  [ {class: "q", text: "Yahweh will also be a high tower for the oppressed;"}
              , {class: "q2", text: "a high tower in times of trouble."}
              ]
        , 10: [ {class: "q", text: "Those who know your name will put their trust in you,"}
              , {class: "q2", text: "for you, Yahweh, have not forsaken those who seek you."}
              ]
        , 11: [ {class: "q", text: "Sing praises to Yahweh, who dwells in Zion,"}
              ,  {class: "q2", text: "and declare among the people what he has done."}
              ]
        , 12: [ {class: "q", text: "For he who avenges blood remembers them."}
              , {class: "q2", text: "He doesn’t forget the cry of the afflicted."}
              ]
        , 13: [ {class: "q", text: "Have mercy on me, Yahweh."}
              , {class: "q2", text: "See my affliction by those who hate me,"}
              , {class: "q", text: "and lift me up from the gates of death,"}
              ]
        , 14: [ {class: "q2", text: "</span> that I may show all of your praise."}
              , {class: "q2", text: "I will rejoice in your salvation in the gates of the daughter of Zion."}
              ]
        , 15: [ {class: "q", text: "The nations have sunk down in the pit that they made."}
              , {class: "q2", text: "In the net which they hid, their own foot is taken."}
              ]
        , 16: [ {class: "q", text: "Yahweh has made himself known."}
              , {class: "q2", text: "He has executed judgment."}
              , {class: "q2", text: "The wicked is snared by the work of his own hands.<div"}
              , {class: "qs", text: " Selah"}
              ]
        , 17: [ {class: "q", text: "The wicked shall be turned back to Sheol,"}
              , {class: "q2", text: "even all the nations that forget God."}
              ]
        , 18: [ {class: "q", text: "For the needy shall not always be forgotten,"}
              , {class: "q2", text: "nor the hope of the poor perish forever."}
              ]
        , 19: [ {class: "q", text: "Arise, Yahweh! Don’t let man prevail."}
              , {class: "q2", text: "Let the nations be judged in your sight."}
              ]
        , 20: [ {class: "q", text: "Put them in fear, Yahweh."}
              , {class: "q2", text: "Let the nations know that they are only men."}
              , {class: "qs", text: "Selah"}
              ]
        }
  , 90: { 1:  [ {class: "q", text: "Lord, you have been our dwelling place for all generations."} ]
        , 2:  [ {class: "q2", text: "/span> Before the mountains were born,"}
              , {class: "q2", text: "before you had formed the earth and the world,"}
              , {class: "q2", text: "even from everlasting to everlasting, you are God."}
              ]
        , 3:  [ {class: "q", text: "You turn man to destruction, saying,"}
              , {class: "q2", text: "“Return, you children of men.”"}
              ]
        , 4:  [ {class: "q", text: "For a thousand years in your sight are just like yesterday when it is past,"}
              , {class: "q2", text: "like a watch in the night."}
              ]
        , 5:  [ {class: "q", text: "You sweep them away as they sleep."}
              , {class: "q2", text: "In the morning they sprout like new grass."}
              ]
        , 6:  [ {class: "q", text: "In the morning it sprouts and springs up."}
              , {class: "q2", text: "By evening, it is withered and dry."}
              ]
        , 7:  [ {class: "q", text: "For we are consumed in your anger."}
              , {class: "q2", text: "We are troubled in your wrath."}
              ]
        , 8:  [ {class: "q", text: "You have set our iniquities before you,"}
              , {class: "q2", text: "our secret sins in the light of your presence."}
              ]
        , 9:  [ {class: "q", text: "For all our days have passed away in your wrath."}
              , {class: "q2", text: "We bring our years to an end as a sigh."}
              ]
        , 10: [ {class: "q", text: "The days of our years are seventy,"}
              , {class: "q2", text: "or even by reason of strength eighty years;"}
              , {class: "q2", text: "yet their pride is but labor and sorrow,"}
              , {class: "q2", text: "for it passes quickly, and we fly away."}
              ]
        , 11: [ {class: "q", text: "Who knows the power of your anger,"}
              ,  {class: "q2", text: "your wrath according to the fear that is due to you?"}
              ]
        , 12: [ {class: "q", text: "So teach us to count our days,"}
              , {class: "q2", text: "that we may gain a heart of wisdom."}
              ]
        , 13: [ {class: "q", text: "Relent, Yahweh!"}
              , {class: "q2", text: "How long?"}
              , {class: "q2", text: "Have compassion on your servants!"}
              ]
        , 14: [ {class: "q", text: "Satisfy us in the morning with your loving kindness,"}
              , {class: "q2", text: "that we may rejoice and be glad all our days."}
              ]
        , 15: [ {class: "q", text: "Make us glad for as many days as you have afflicted us,"}
              , {class: "q2", text: "for as many years as we have seen evil."}
              ]
        , 16: [ {class: "q", text: "Let your work appear to your servants,"}
              ,  {class: "q2", text: "your glory to their children."}
              ]
        , 17: [ {class: "q", text: "Let the favor of the Lord our God be on us."}
              , {class: "q2", text: "Establish the work of our hands for us."}
              , {class: "q2", text: "Yes, establish the work of our hands."}
              ]
        }
  , 91: { 1:  [ {class: "q", text: "He who dwells in the secret place of the Most High"}
              , {class: "q2", text: "will rest in the shadow of the Almighty."}
              ]
        , 2:  [ {class: "q", text: "I will say of Yahweh, “He is my refuge and my fortress;"}
              , {class: "q2", text: "my God, in whom I trust.”"}
              ]
        , 3:  [ {class: "q", text: "For he will deliver you from the snare of the fowler,"}
              , {class: "q2", text: "and from the deadly pestilence."}
              ]
        , 4:  [ {class: "q", text: "He will cover you with his feathers."}
              , {class: "q2", text: "Under his wings you will take refuge."}
              , {class: "q2", text: "His faithfulness is your shield and rampart."}
              ]
        , 5:  [ {class: "q", text: "You shall not be afraid of the terror by night,"}
              , {class: "q2", text: "nor of the arrow that flies by day,"}
              ]
        , 6:  [ {class: "q2", text: "/span> nor of the pestilence that walks in darkness,"}
              , {class: "q2", text: "nor of the destruction that wastes at noonday."}
              ]
        , 7:  [ {class: "q", text: "A thousand may fall at your side,"}
              , {class: "q2", text: "and ten thousand at your right hand;"}
              , {class: "q2", text: "but it will not come near you."}
              ]
        , 8:  [ {class: "q", text: "You will only look with your eyes,"}
              , {class: "q2", text: "and see the recompense of the wicked."}
              ]
        , 9:  [ {class: "q", text: "Because you have made Yahweh your refuge,"}
              , {class: "q2", text: "and the Most High your dwelling place,"}
              ]
        , 10: [ {class: "q", text: "no evil shall happen to you,"}
              , {class: "q2", text: "neither shall any plague come near your dwelling."}
              ]
        , 11: [ {class: "q", text: "For he will put his angels in charge of you,"}
              , {class: "q2", text: "to guard you in all your ways."}
              ]
        , 12: [ {class: "q", text: "They will bear you up in their hands,"}
              , {class: "q2", text: "so that you won’t dash your foot against a stone."}
              ]
        , 13: [ {class: "q", text: "You will tread on the lion and cobra."}
              , {class: "q2", text: "You will trample the young lion and the serpent underfoot."}
              ]
        , 14: [ {class: "q", text: "“Because he has set his love on me, therefore I will deliver him."}
              , {class: "q2", text: "I will set him on high, because he has known my name."}
              ]
        , 15: [ {class: "q", text: "He will call on me, and I will answer him."}
              , {class: "q2", text: "I will be with him in trouble."}
              , {class: "q2", text: "I will deliver him, and honor him."}
              ]
        , 16: [ {class: "q", text: "I will satisfy him with long life,"}
              , {class: "q2", text: "and show him my salvation.”"}
              ]
        }
  , 92: { 1:  [ {class: "q", text: "It is a good thing to give thanks to Yahweh,"}
              , {class: "q2", text: "to sing praises to your name, Most High,"}
              ]
        , 2:  [ {class: "q", text: "to proclaim your loving kindness in the morning,"}
              , {class: "q2", text: "and your faithfulness every night,"}
              ]
        , 3:  [ {class: "q", text: "with the ten-stringed lute, with the harp,"}
              , {class: "q2", text: "and with the melody of the lyre."}
              ]
        , 4:  [ {class: "q", text: "For you, Yahweh, have made me glad through your work."}
              , {class: "q2", text: "I will triumph in the works of your hands."}
              ]
        , 5:  [ {class: "q", text: "How great are your works, Yahweh!"}
              , {class: "q2", text: "Your thoughts are very deep."}
              ]
        , 6:  [ {class: "q", text: "A senseless man doesn’t know,"}
              , {class: "q2", text: "neither does a fool understand this:"}
              ]
        , 7:  [ {class: "q", text: "though the wicked spring up as the grass,"}
              , {class: "q2", text: "and all the evildoers flourish,"}
              , {class: "q2", text: "they will be destroyed forever."}
              ]
        , 8:  [ {class: "q", text: "But you, Yahweh, are on high forever more."} ]
        , 9:  [ {class: "q", text: "For, behold, your enemies, Yahweh,"}
              , {class: "q2", text: "for, behold, your enemies shall perish."}
              , {class: "q2", text: "All the evildoers will be scattered."}
              ]
        , 10: [ {class: "q", text: "But you have exalted my horn like that of the wild ox."}
              , {class: "q2", text: "I am anointed with fresh oil."}
              ]
        , 11: [ {class: "q", text: "My eye has also seen my enemies."}
              , {class: "q2", text: "My ears have heard of the wicked enemies who rise up against me."}
              ]
        , 12: [ {class: "q", text: "The righteous shall flourish like the palm tree."}
              , {class: "q2", text: "He will grow like a cedar in Lebanon."}
              ]
        , 13: [ {class: "q", text: "They are planted in Yahweh’s house."}
              , {class: "q2", text: "They will flourish in our God’s courts."}
              ]
        , 14: [ {class: "q", text: "They will still produce fruit in old age."}
              , {class: "q2", text: "They will be full of sap and green,"}
              ]
        , 15: [ {class: "q2", text: "</span> to show that Yahweh is upright."}
              , {class: "q", text: "He is my rock,"}
              , {class: "q2", text: "and there is no unrighteousness in him."}
              ]
        }
  , 93: { 1:  [ {class: "q", text: "Yahweh reigns!"}
              , {class: "q2", text: "He is clothed with majesty!"}
              , {class: "q2", text: "Yahweh is armed with strength."}
              , {class: "q", text: "The world also is established."}
              , {class: "q2", text: "It can’t be moved."}
              ]
        , 2:  [ {class: "q", text: "Your throne is established from long ago."}
              , {class: "q2", text: "You are from everlasting."}
              ]
        , 3:  [ {class: "q", text: "The floods have lifted up, Yahweh,"}
              , {class: "q2", text: "the floods have lifted up their voice."}
              , {class: "q2", text: "The floods lift up their waves."}
              ]
        , 4:  [ {class: "q", text: "Above the voices of many waters,"}
              , {class: "q2", text: "the mighty breakers of the sea,"}
              , {class: "q2", text: "Yahweh on high is mighty."}
              ]
        , 5:  [ {class: "q", text: "Your statutes stand firm."}
              , {class: "q2", text: "Holiness adorns your house,"}
              , {class: "q2", text: "Yahweh, forever more."}
              ]
        }
  , 94: { 1:  [ {class: "q", text: "Yahweh, you God to whom vengeance belongs,"}
              , {class: "q2", text: "you God to whom vengeance belongs, shine out."}
              ]
        , 2:  [ {class: "q", text: "Rise up, you judge of the earth."}
              , {class: "q2", text: "Pay back the proud what they deserve."}
              ]
        , 3:  [ {class: "q", text: "Yahweh, how long will the wicked,"}
              , {class: "q2", text: "how long will the wicked triumph?"}
              ]
        , 4:  [ {class: "q", text: "They pour out arrogant words."}
              , {class: "q2", text: "All the evildoers boast."}
              ]
        , 5:  [ {class: "q", text: "They break your people in pieces, Yahweh,"}
              , {class: "q2", text: "and afflict your heritage."}
              ]
        , 6:  [ {class: "q", text: "They kill the widow and the alien,"}
              , {class: "q2", text: "and murder the fatherless."}
              ]
        , 7:  [ {class: "q", text: "They say, “Yah will not see,"}
              , {class: "q2", text: "neither will Jacob’s God consider.”"}
              ]
        , 8:  [ {class: "q", text: "Consider, you senseless among the people;"}
              , {class: "q2", text: "you fools, when will you be wise?"}
              ]
        , 9:  [ {class: "q", text: "He who implanted the ear, won’t he hear?"}
              , {class: "q2", text: "He who formed the eye, won’t he see?"}
              ]
        , 10: [ {class: "q", text: "He who disciplines the nations, won’t he punish?"}
              , {class: "q2", text: "He who teaches man knows."}
              ]
        , 11: [ {class: "q", text: "Yahweh knows the thoughts of man,"}
              , {class: "q2", text: "that they are futile."}
              ]
        , 12: [ {class: "q", text: "Blessed is the man whom you discipline, Yah,"}
              , {class: "q2", text: "and teach out of your law,"}
              ]
        , 13: [ {class: "q", text: "that you may give him rest from the days of adversity,"}
              , {class: "q2", text: "until the pit is dug for the wicked."}
              ]
        , 14: [ {class: "q", text: "For Yahweh won’t reject his people,"}
              , {class: "q2", text: "neither will he forsake his inheritance."}
              ]
        , 15: [ {class: "q", text: "For judgment will return to righteousness."}
              , {class: "q2", text: "All the upright in heart shall follow it."}
              ]
        , 16: [ {class: "q", text: "Who will rise up for me against the wicked?"}
              , {class: "q2", text: "Who will stand up for me against the evildoers?"}
              ]
        , 17: [ {class: "q", text: "Unless Yahweh had been my help,"}
              , {class: "q2", text: "my soul would have soon lived in silence."}
              ]
        , 18: [ {class: "q", text: "When I said, “My foot is slipping!”"}
              , {class: "q2", text: "Your loving kindness, Yahweh, held me up."}
              ]
        , 19: [ {class: "q", text: "In the multitude of my thoughts within me,"}
              , {class: "q2", text: "your comforts delight my soul."}
              ]
        , 20: [ {class: "q", text: "Shall the throne of wickedness have fellowship with you,"}
              , {class: "q2", text: "which brings about mischief by statute?"}
              ]
        , 21: [ {class: "q", text: "They gather themselves together against the soul of the righteous,"}
              , {class: "q2", text: "and condemn the innocent blood."}
              ]
        , 22: [ {class: "q", text: "But Yahweh has been my high tower,"}
              , {class: "q2", text: "my God, the rock of my refuge."}
              ]
        , 23: [ {class: "q", text: "He has brought on them their own iniquity,"}
              , {class: "q2", text: "and will cut them off in their own wickedness."}
              , {class: "q2", text: "Yahweh, our God, will cut them off."}
              ]
        }
  , 95: { 1:  [ {class: "q", text: "Oh come, let’s sing to Yahweh."}
              , {class: "q2", text: "Let’s shout aloud to the rock of our salvation!"}
              ]
        , 2:  [ {class: "q", text: "Let’s come before his presence with thanksgiving."}
              , {class: "q2", text: "Let’s extol him with songs!"}
              ]
        , 3:  [ {class: "q", text: "For Yahweh is a great God,"}
              , {class: "q2", text: "a great King above all gods."}
              ]
        , 4:  [ {class: "q", text: "In his hand are the deep places of the earth."}
              , {class: "q2", text: "The heights of the mountains are also his."}
              ]
        , 5:  [ {class: "q", text: "The sea is his, and he made it."}
              , {class: "q2", text: "His hands formed the dry land."}
              ]
        , 6:  [ {class: "q", text: "Oh come, let’s worship and bow down."}
              , {class: "q2", text: "Let’s kneel before Yahweh, our Maker,"}
              ]
        , 7 : [ {class: "q2", text: "/span> for he is our God."}
              , {class: "q", text: "We are the people of his pasture,"}
              , {class: "q2", text: "and the sheep in his care."}
              , {class: "q", text: "Today, oh that you would hear his voice!"}
              ]
        , 8:  [ {class: "q2", text: "/span> Don’t harden your heart, as at Meribah,"}
              , {class: "q2", text: "as in the day of Massah in the wilderness,"}
              ]
        , 9:  [ {class: "q", text: "when your fathers tempted me,"}
              , {class: "q2", text: "tested me, and saw my work."}
              ]
        , 10: [ {class: "q", text: "Forty long years I was grieved with that generation,"}
              , {class: "q2", text: "and said, “It is a people that errs in their heart."}
              , {class: "q2", text: "They have not known my ways.”"}
              ]
        , 11: [ {class: "q", text: "Therefore I swore in my wrath,"}
              , {class: "q2", text: "“They won’t enter into my rest.”"}
              ]
        }
  , 96: { 1:  [ {class: "q", text: "Sing to Yahweh a new song!"}
              , {class: "q2", text: "Sing to Yahweh, all the earth."}
              ]
        , 2:  [ {class: "q", text: "Sing to Yahweh!"}
              , {class: "q2", text: "Bless his name!"}
              , {class: "q2", text: "Proclaim his salvation from day to day!"}
              ]
        , 3:  [ {class: "q", text: "Declare his glory among the nations,"}
              , {class: "q2", text: "his marvelous works among all the peoples."}
              ]
        , 4:  [ {class: "q", text: "For Yahweh is great, and greatly to be praised!"}
              , {class: "q2", text: "He is to be feared above all gods."}
              ]
        , 5:  [ {class: "q", text: "For all the gods of the peoples are idols,"}
              , {class: "q2", text: "but Yahweh made the heavens."}
              ]
        , 6:  [ {class: "q", text: "Honor and majesty are before him."}
              , {class: "q2", text: "Strength and beauty are in his sanctuary."}
              ]
        , 7:  [ {class: "q", text: "Ascribe to Yahweh, you families of nations,"}
              , {class: "q2", text: "ascribe to Yahweh glory and strength."}
              ]
        , 8:  [ {class: "q", text: "Ascribe to Yahweh the glory due to his name."}
              , {class: "q2", text: "Bring an offering, and come into his courts."}
              ]
        , 9:  [ {class: "q", text: "Worship Yahweh in holy array."}
              , {class: "q2", text: "Tremble before him, all the earth."}
              ]
        , 10: [ {class: "q", text: "Say among the nations, “Yahweh reigns.”"}
              , {class: "q2", text: "The world is also established."}
              , {class: "q2", text: "It can’t be moved."}
              , {class: "q2", text: "He will judge the peoples with equity."}
              ]
        , 11: [ {class: "q", text: "Let the heavens be glad, and let the earth rejoice."}
              , {class: "q2", text: "Let the sea roar, and its fullness!"}
              ]
        , 12: [ {class: "q2", text: "</span> Let the field and all that is in it exult!"}
              , {class: "q2", text: "Then all the trees of the woods shall sing for joy"}
              ]
        , 13: [ {class: "q2", text: "</span> before Yahweh; for he comes,"}
              , {class: "q2", text: "for he comes to judge the earth."}
              , {class: "q", text: "He will judge the world with righteousness,"}
              , {class: "q2", text: "the peoples with his truth."}
              ]
        }
  , 97: { 1:  [ {class: "q", text: "Yahweh reigns!"}
              , {class: "q2", text: "Let the earth rejoice!"}
              , {class: "q2", text: "Let the multitude of islands be glad!"}
              ]
        , 2:  [ {class: "q", text: "Clouds and darkness are around him."}
              , {class: "q2", text: "Righteousness and justice are the foundation of his throne."}
              ]
        , 3:  [ {class: "q", text: "A fire goes before him,"}
              , {class: "q2", text: "and burns up his adversaries on every side."}
              ]
        , 4:  [ {class: "q", text: "His lightning lights up the world."}
              , {class: "q2", text: "The earth sees, and trembles."}
              ]
        , 5:  [ {class: "q", text: "The mountains melt like wax at the presence of Yahweh,"}
              , {class: "q2", text: "at the presence of the Lord of the whole earth."}
              ]
        , 6:  [ {class: "q", text: "The heavens declare his righteousness."}
              , {class: "q2", text: "All the peoples have seen his glory."}
              ]
        , 7:  [ {class: "q", text: "Let all them be shamed who serve engraved images,"}
              , {class: "q2", text: "who boast in their idols."}
              , {class: "q2", text: "Worship him, all you gods!"}
              ]
        , 8:  [ {class: "q", text: "Zion heard and was glad."}
              , {class: "q2", text: "The daughters of Judah rejoiced"}
              , {class: "q2", text: "because of your judgments, Yahweh."}
              ]
        , 9:  [ {class: "q", text: "For you, Yahweh, are most high above all the earth."}
              , {class: "q2", text: "You are exalted far above all gods."}
              ]
        , 10: [ {class: "q", text: "You who love Yahweh, hate evil!"}
              , {class: "q2", text: "He preserves the souls of his saints."}
              , {class: "q2", text: "He delivers them out of the hand of the wicked."}
              ]
        , 11: [ {class: "q", text: "Light is sown for the righteous,"}
              , {class: "q2", text: "and gladness for the upright in heart."}
              ]
        , 12: [ {class: "q", text: "Be glad in Yahweh, you righteous people!"}
              , {class: "q2", text: "Give thanks to his holy Name."}
              ]
        }
  , 98: { 1:  [ {class: "q", text: "Sing to Yahweh a new song,"}
              , {class: "q2", text: "for he has done marvelous things!"}
              , {class: "q2", text: "His right hand and his holy arm have worked salvation for him."}
              ]
        , 2:  [ {class: "q", text: "Yahweh has made known his salvation."}
              , {class: "q2", text: "He has openly shown his righteousness in the sight of the nations."}
              ]
        , 3:  [ {class: "q", text: "He has remembered his loving kindness and his faithfulness toward the house of Israel."}
              , {class: "q2", text: "All the ends of the earth have seen the salvation of our God."}
              ]
        , 4:  [ {class: "q", text: "Make a joyful noise to Yahweh, all the earth!"}
              , {class: "q2", text: "Burst out and sing for joy, yes, sing praises!"}
              ]
        , 5:  [ {class: "q", text: "Sing praises to Yahweh with the harp,"}
              , {class: "q2", text: "with the harp and the voice of melody."}
              ]
        , 6:  [ {class: "q", text: "With trumpets and sound of the ram’s horn,"}
              , {class: "q2", text: "make a joyful noise before the King, Yahweh."}
              ]
        , 7:  [ {class: "q", text: "Let the sea roar with its fullness;"}
              , {class: "q2", text: "the world, and those who dwell therein."}
              ]
        , 8:  [ {class: "q", text: "Let the rivers clap their hands."}
              , {class: "q2", text: "Let the mountains sing for joy together."}
              ]
        , 9:  [ {class: "q", text: "Let them sing before Yahweh,"}
              , {class: "q2", text: "for he comes to judge the earth."}
              , {class: "q", text: "He will judge the world with righteousness,"}
              , {class: "q2", text: "and the peoples with equity."}
              ]
        }
  , 99: { 1:  [ {class: "q", text: "Yahweh reigns! Let the peoples tremble."}
              , {class: "q2", text: "He sits enthroned among the cherubim."}
              , {class: "q2", text: "Let the earth be moved."}
              ]
        , 2:  [ {class: "q", text: "Yahweh is great in Zion."}
              , {class: "q2", text: "He is high above all the peoples."}
              ]
        , 3:  [ {class: "q", text: "Let them praise your great and awesome name."}
              , {class: "q2", text: "He is Holy!"}
              ]
        , 4:  [ {class: "q", text: "The King’s strength also loves justice."}
              , {class: "q2", text: "You establish equity."}
              , {class: "q2", text: "You execute justice and righteousness in Jacob."}
              ]
        , 5:  [ {class: "q", text: "Exalt Yahweh our God."}
              , {class: "q2", text: "Worship at his footstool."}
              , {class: "q2", text: "He is Holy!"}
              ]
        , 6:  [ {class: "q", text: "Moses and Aaron were among his priests,"}
              , {class: "q2", text: "Samuel was among those who call on his name."}
              , {class: "q2", text: "They called on Yahweh, and he answered them."}
              ]
        , 7:  [ {class: "q", text: "He spoke to them in the pillar of cloud."}
              , {class: "q2", text: "They kept his testimonies,"}
              , {class: "q2", text: "the statute that he gave them."}
              ]
        , 8:  [ {class: "q", text: "You answered them, Yahweh our God."}
              , {class: "q2", text: "You are a God who forgave them,"}
              , {class: "q2", text: "although you took vengeance for their doings."}
              ]
        , 9:  [ {class: "q", text: "Exalt Yahweh, our God."}
              , {class: "q2", text: "Worship at his holy hill,"}
              , {class: "q2", text: "for Yahweh, our God, is holy!"}
              ]
        }
  }