exports.amos =
  { 1:  { 1: [ {class: "vs", text: "The words of Amos, who was among the herdsmen of Tekoa, which he saw concerning Israel in the days of Uzziah king of Judah, and in the days of Jeroboam the son of Joash king of Israel, two years before the earthquake."} ]
        , 2:  [ {class: "endpara", text: "He said:"}
              , {class: "q", text: "“Yahweh will roar from Zion,"}
              , {class: "q2", text: "and utter his voice from Jerusalem;"}
              , {class: "q", text: "and the pastures of the shepherds will mourn,"}
              , {class: "q2", text: "and the top of Carmel will wither.”"}
              ]
        , 3:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Damascus, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they have threshed Gilead with threshing instruments of iron;"}
              ]
        , 4:  [ {class: "q", text: "but I will send a fire into the house of Hazael,"}
              , {class: "q2", text: "and it will devour the palaces of Ben Hadad."}
              ]
        , 5:  [ {class: "q", text: "I will break the bar of Damascus,"}
              , {class: "q2", text: "and cut off the inhabitant from the valley of Aven,"}
              , {class: "q2", text: "and him who holds the scepter from the house of Eden;"}
              , {class: "q2", text: "and the people of Syria shall go into captivity to Kir,”"}
              , {class: "para", text: " says Yahweh."}
              ]
        , 6:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Gaza, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they carried away captive the whole community,"}
              , {class: "q2", text: "to deliver them up to Edom;"}
              ]
        , 7:  [ {class: "q", text: "but I will send a fire on the wall of Gaza,"}
              , {class: "q2", text: "and it will devour its palaces."}
              ]
        , 8:  [ {class: "q", text: "I will cut off the inhabitant from Ashdod,"}
              , {class: "q2", text: "and him who holds the scepter from Ashkelon;"}
              , {class: "q" , text: "and I will turn my hand against Ekron;"}
              , {class: "q2", text: "and the remnant of the Philistines will perish,”"}
              , {class: "p" , text: " says the Lord Yahweh."}
              ]
        , 9:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Tyre, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they delivered up the whole community to Edom,"}
              , {class: "q2", text: "and didn’t remember the brotherly covenant;"}
              ]
        , 10: [ {class: "q", text: "but I will send a fire on the wall of Tyre,"}
              , {class: "q2", text: "and it will devour its palaces.”"}
              ]
        , 11: [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Edom, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because he pursued his brother with the sword,"}
              , {class: "q2", text: "and cast off all pity,"}
              , {class: "q2", text: "and his anger raged continually,"}
              , {class: "q2", text: "and he kept his wrath forever;"}
              ]
        , 12: [ {class: "q", text: "but I will send a fire on Teman,"}
              , {class: "q2", text: "and it will devour the palaces of Bozrah.”"}
              ]
        , 13: [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of the children of Ammon, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they have ripped open the pregnant women of Gilead,"}
              , {class: "q2", text: "that they may enlarge their border."}
              ]
        , 14: [ {class: "q", text: "But I will kindle a fire in the wall of Rabbah,"}
              , {class: "q2", text: "and it will devour its palaces,"}
              , {class: "q2", text: "with shouting in the day of battle,"}
              , {class: "q2", text: "with a storm in the day of the whirlwind;"}
              ]
        , 15: [ {class: "q", text: "and their king will go into captivity,"}
              , {class: "q2", text: "he and his princes together,”</div><p> says Yahweh."}
              ]
        }
        , 2:  { 1:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Moab, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because he burned the bones of the king of Edom into lime;"}
              ]
        , 2:  [ {class: "q", text: "but I will send a fire on Moab,"}
              , {class: "q2", text: "and it will devour the palaces of Kerioth;"}
              , {class: "q2", text: "and Moab will die with tumult, with shouting, and with the sound of the trumpet;"}
              ]
        , 3:  [ {class: "q", text: "and I will cut off the judge from among them,"}
              , {class: "q2", text: "and will kill all its princes with him,”"}
              , {class: "para", text: " says Yahweh."}
              ]
        , 4:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Judah, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they have rejected Yahweh’s law,"}
              , {class: "q2", text: "and have not kept his statutes,"}
              , {class: "q2", text: "and their lies have led them astray,"}
              , {class: "q2", text: "after which their fathers walked;"}
              ]
        , 5:  [ {class: "q", text: "but I will send a fire on Judah,"}
              , {class: "q2", text: "and it will devour the palaces of Jerusalem.”"}
              ]
        , 6:  [ {class: "endpara", text: "Yahweh says:"}
              , {class: "q", text: "“For three transgressions of Israel, yes, for four,"}
              , {class: "q2", text: "I will not turn away its punishment;"}
              , {class: "q2", text: "because they have sold the righteous for silver,"}
              , {class: "q2", text: "and the needy for a pair of sandals;"}
              ]
        , 7:  [ {class: "q2", text: "They trample on the dust of the earth on the head of the poor,"}
              , {class: "q2", text: "and deny justice to the oppressed;"}
              , {class: "q2", text: "and a man and his father use the same maiden, to profane my holy name;"}
              ]
        , 8:  [ {class: "q2", text: "and they lay themselves down beside every altar on clothes taken in pledge;"}
              , {class: "q2", text: "and in the house of their God they drink the wine of those who have been fined."}
              ]
        , 9:  [ {class: "q", text: "Yet I destroyed the Amorite before them,"}
              , {class: "q2", text: "whose height was like the height of the cedars,"}
              , {class: "q2", text: "and he was strong as the oaks;"}
              , {class: "q2", text: "yet I destroyed his fruit from above,"}
              , {class: "q2", text: "and his roots from beneath."}
              ]
        , 10: [ {class: "q", text: "Also I brought you up out of the land of Egypt,"}
              , {class: "q2", text: "and led you forty years in the wilderness,"}
              , {class: "q2", text: "to possess the land of the Amorite."}
              ]
        , 11: [ {class: "q", text: "I raised up some of your sons for prophets,"}
              , {class: "q2", text: "and some of your young men for Nazirites."}
              , {class: "q", text: "Isn’t this true,"}
              , {class: "q2", text: "you children of Israel?” says Yahweh."}
              ]
        , 12: [ {class: "q", text: "“But you gave the Nazirites wine to drink,"}
              , {class: "q2", text: "and commanded the prophets, saying, ‘Don’t prophesy!’"}
              ]
        , 13: [ {class: "q", text: "Behold, I will crush you in your place,"}
              , {class: "q2", text: "as a cart crushes that is full of grain."}
              ]
        , 14: [ {class: "q", text: "Flight will perish from the swift;"}
              , {class: "q2", text: "and the strong won’t strengthen his force;"}
              , {class: "q2", text: "neither shall the mighty deliver himself;"}
              ]
        , 15: [ {class: "q2", text: "neither shall he stand who handles the bow;"}
              , {class: "q2", text: "and he who is swift of foot won’t escape;"}
              , {class: "q2", text: "neither shall he who rides the horse deliver himself;"}
              ]
        , 16: [ {class: "q", text: "and he who is courageous among the mighty will flee away naked on that day,”"}
              , {class: "para", text: "says Yahweh."}
              ]
        }
        , 3:  { 1: [ {class: "vs", text: "Hear this word that Yahweh has spoken against you, children of Israel, against the whole family which I brought up out of the land of Egypt, saying:"} ]
        , 2:  [ {class: "q", text: "“I have only chosen you of all the families of the earth."}
              , {class: "q2", text: "Therefore I will punish you for all of your sins.”"}
              ]
        , 3:  [ {class: "q", text: "Do two walk together,"}
              , {class: "q2", text: "unless they have agreed?"}
              ]
        , 4:  [ {class: "q", text: "Will a lion roar in the thicket,"}
              , {class: "q2", text: "when he has no prey?"}
              , {class: "q", text: "Does a young lion cry out of his den,"}
              , {class: "q2", text: "if he has caught nothing?"}
              ]
        , 5:  [ {class: "q", text: "Can a bird fall in a trap on the earth,"}
              , {class: "q2", text: "where no snare is set for him?"}
              , {class: "q", text: "Does a snare spring up from the ground,"}
              , {class: "q2", text: "when there is nothing to catch?"}
              ]
        , 6:  [ {class: "q", text: "Does the trumpet alarm sound in a city,"}
              , {class: "q2", text: "without the people being afraid?"}
              , {class: "q", text: "Does evil happen to a city,"}
              , {class: "q2", text: "and Yahweh hasn’t done it?"}
              ]
        , 7:  [ {class: "q", text: "Surely the Lord Yahweh will do nothing,"}
              , {class: "q2", text: "unless he reveals his secret to his servants the prophets."}
              ]
        , 8:  [ {class: "q", text: "The lion has roared."}
              , {class: "q2", text: "Who will not fear?"}
              , {class: "q", text: "The Lord Yahweh has spoken."}
              , {class: "q2", text: "Who can but prophesy?"}
              ]
        , 9:  [ {class: "q", text: "Proclaim in the palaces at Ashdod,"}
              , {class: "q2", text: "and in the palaces in the land of Egypt,"}
              , {class: "q", text: "and say, “Assemble yourselves on the mountains of Samaria,"}
              , {class: "q2", text: "and see what unrest is in her,"}
              , {class: "q2", text: "and what oppression is among them.”"}
              ]
        , 10: [ {class: "q", text: "“Indeed they don’t know to do right,” says Yahweh,"}
              , {class: "q2", text: "“Who hoard plunder and loot in their palaces.”"}
              ]
        , 11: [ {class: "endpara", text: "Therefore the Lord Yahweh says:"}
              , {class: "q", text: "“An adversary will overrun the land;"}
              , {class: "q2", text: "and he will pull down your strongholds,"}
              , {class: "q2", text: "and your fortresses will be plundered.”"}
              ]
        , 12: [ {class: "p", text: "Yahweh says:"}
              , {class: "q", text: "“As the shepherd rescues out of the mouth of the lion two legs,"}
              , {class: "q2", text: "or a piece of an ear,"}
              , {class: "q2", text: "so shall the children of Israel be rescued who sit in Samaria on the corner of a couch,"}
              , {class: "q2", text: "and on the silken cushions of a bed.”"}
              ]
        , 13: [ {class: "vs", text: "“Listen, and testify against the house of Jacob,” says the Lord Yahweh, the God of Armies."} ]
        , 14: [ {class: "q", text: "“For in the day that I visit the transgressions of Israel on him,"}
              , {class: "q2", text: "I will also visit the altars of Bethel;"}
              , {class: "q2", text: "and the horns of the altar will be cut off,"}
              , {class: "q2", text: "and fall to the ground."}
              ]
        , 15: [ {class: "q", text: "I will strike the winter house with the summer house;"}
              , {class: "q2", text: "and the houses of ivory will perish,"}
              , {class: "q2", text: "and the great houses will have an end,”</div><p> says Yahweh."}
              ]
        }
        , 4:  { 1: [ {class: "vs", text: "Listen to this word, you cows of Bashan, who are on the mountain of Samaria, who oppress the poor, who crush the needy, who tell their husbands, “Bring us drinks!”"} ]
        , 2:  [ {class: "q", text: "The Lord Yahweh has sworn by his holiness that behold,"}
              , {class: "q2", text: "“The days shall come on you that they will take you away with hooks,"}
              , {class: "q2", text: "and the last of you with fish hooks."}
              ]
        , 3:  [ {class: "q", text: "You will go out at the breaks in the wall,"}
              , {class: "q2", text: "everyone straight before her;"}
              , {class: "q2", text: "and you will cast yourselves into Harmon,” says Yahweh."}
              ]
        , 4:  [ {class: "q", text: "“Go to Bethel, and sin;"}
              , {class: "q2", text: "to Gilgal, and sin more."}
              , {class: "q", text: "Bring your sacrifices every morning,"}
              , {class: "q2", text: "your tithes every three days,"}
              ]
        , 5:  [ {class: "q2", text: "offer a sacrifice of thanksgiving of that which is leavened,"}
              , {class: "q2", text: "and proclaim free will offerings and brag about them:"}
              , {class: "q2", text: "for this pleases you, you children of Israel,” says the Lord Yahweh."}
              ]
        , 6:  [ {class: "q", text: "“I also have given you cleanness of teeth in all your cities,"}
              , {class: "q2", text: "and lack of bread in every town;"}
              , {class: "q2", text: "yet you haven’t returned to me,” says Yahweh."}
              ]
        , 7:  [ {class: "q", text: "“I also have withheld the rain from you,"}
              , {class: "q2", text: "when there were yet three months to the harvest;"}
              , {class: "q2", text: "and I caused it to rain on one city,"}
              , {class: "q2", text: "and caused it not to rain on another city."}
              , {class: "q", text: "One place was rained on,"}
              , {class: "q2", text: "and the piece where it didn’t rain withered."}
              ]
        , 8:  [ {class: "q", text: "So two or three cities staggered to one city to drink water,"}
              , {class: "q2", text: "and were not satisfied:"}
              , {class: "q2", text: "yet you haven’t returned to me,” says Yahweh."}
              ]
        , 9:  [ {class: "q", text: "“I struck you with blight and mildew many times in your gardens and your vineyards;"}
              , {class: "q2", text: "and the swarming locusts have devoured your fig trees and your olive trees;"}
              , {class: "q2", text: "yet you haven’t returned to me,” says Yahweh."}
              ]
        , 10: [ {class: "q", text: "“I sent plagues among you like I did Egypt."}
              , {class: "q2", text: "I have slain your young men with the sword,"}
              , {class: "q2", text: "and have carried away your horses;"}
              , {class: "q2", text: "and I filled your nostrils with the stench of your camp,"}
              , {class: "q2", text: "yet you haven’t returned to me,” says Yahweh."}
              ]
        , 11: [ {class: "q", text: "“I have overthrown some of you,"}
              , {class: "q2", text: "as when God overthrew Sodom and Gomorrah,"}
              , {class: "q2", text: "and you were like a burning stick plucked out of the fire;"}
              , {class: "q2", text: "yet you haven’t returned to me,” says Yahweh."}
              ]
        , 12: [ {class: "q", text: "“Therefore thus I will do to you, Israel;"}
              , {class: "q2", text: "because I will do this to you,"}
              , {class: "q2", text: "prepare to meet your God, Israel."}
              ]
        , 13: [ {class: "q", text: "For, behold, he who forms the mountains,"}
              , {class: "q2", text: "and creates the wind,"}
              , {class: "q2", text: "and declares to man what is his thought;"}
              , {class: "q2", text: "who makes the morning darkness,"}
              , {class: "q2", text: "and treads on the high places of the earth:"}
              , {class: "q2", text: "Yahweh, the God of Armies, is his name.”"}
              ]
        }
        , 5:  { 1: [ {class: "vs", text: "Listen to this word which I take up for a lamentation over you, O house of Israel."} ]
        , 2:  [ {class: "q", text: "“The virgin of Israel has fallen;"}
              , {class: "q2", text: "She shall rise no more."}
              , {class: "q", text: "She is cast down on her land;"}
              , {class: "q2", text: "there is no one to raise her up.”"}
              ]
        , 3:  [ {class: "endpara", text: "For the Lord Yahweh says:"}
              , {class: "q", text: "“The city that went out a thousand shall have a hundred left,"}
              , {class: "q2", text: "and that which went out one hundred shall have ten left to the house of Israel.”"}
              ]
        , 4:  [ {class: "endpara", text: "For Yahweh says to the house of Israel:"}
              , {class: "q", text: "“Seek me, and you will live;"}
              ]
        , 5:  [ {class: "q", text: "but don’t seek Bethel,"}
              , {class: "q2", text: "nor enter into Gilgal,"}
              , {class: "q2", text: "and don’t pass to Beersheba:"}
              , {class: "q", text: "for Gilgal shall surely go into captivity,"}
              , {class: "q2", text: "and Bethel shall come to nothing."}
              ]
        , 6:  [ {class: "q", text: "Seek Yahweh, and you will live;"}
              , {class: "q2", text: "lest he break out like fire in the house of Joseph,"}
              , {class: "q2", text: "and it devour, and there be no one to quench it in Bethel."}
              ]
        , 7:  [ {class: "q", text: "You who turn justice to wormwood,"}
              , {class: "q2", text: "and cast down righteousness to the earth:"}
              ]
        , 8:  [ {class: "q", text: "seek him who made the Pleiades and Orion,"}
              , {class: "q2", text: "and turns the shadow of death into the morning,"}
              , {class: "q2", text: "and makes the day dark with night;"}
              , {class: "q2", text: "who calls for the waters of the sea,"}
              , {class: "q2", text: "and pours them out on the surface of the earth, Yahweh is his name,"}
              ]
        , 9:  [ {class: "q", text: "who brings sudden destruction on the strong,"}
              , {class: "q2", text: "so that destruction comes on the fortress."}
              ]
        , 10: [ {class: "q", text: "They hate him who reproves in the gate,"}
              , {class: "q2", text: "and they abhor him who speaks blamelessly."}
              ]
        , 11: [ {class: "q", text: "Therefore, because you trample on the poor,"}
              , {class: "q2", text: "and take taxes from him of wheat:"}
              , {class: "q2", text: "You have built houses of cut stone,"}
              , {class: "q2", text: "but you will not dwell in them."}
              , {class: "q", text: "You have planted pleasant vineyards,"}
              , {class: "q2", text: "but you shall not drink their wine."}
              ]
        , 12: [ {class: "q", text: "For I know how many your offenses,"}
              , {class: "q2", text: "and how great are your sins—"}
              , {class: "q2", text: "you who afflict the just,"}
              , {class: "q2", text: "who take a bribe,"}
              , {class: "q2", text: "and who turn away the needy in the courts."}
              ]
        , 13: [ {class: "q", text: "Therefore a prudent person keeps silent in such a time,"}
              , {class: "q2", text: "for it is an evil time."}
              ]
        , 14: [ {class: "q", text: "Seek good, and not evil,"}
              ,  {class: "q2", text: "that you may live;"}
              , {class: "q2", text: "and so Yahweh, the God of Armies, will be with you,"}
              , {class: "q2", text: "as you say."}
              ]
        , 15: [ {class: "q", text: "Hate evil, love good,"}
              , {class: "q2", text: "and establish justice in the courts."}
              , {class: "q2", text: "It may be that Yahweh, the God of Armies, will be gracious to the remnant of Joseph.”"}
              ]
        , 16: [ {class: "endpara", text: "Therefore Yahweh, the God of Armies, the Lord, says:"}
              , {class: "q", text: "“Wailing will be in all the wide ways;"}
              , {class: "q2", text: "and they will say in all the streets, ‘Alas! Alas!’"}
              , {class: "q2", text: "and they will call the farmer to mourning,"}
              , {class: "q2", text: "and those who are skillful in lamentation to wailing."}
              ]
        , 17: [ {class: "q", text: "In all vineyards there will be wailing;"}
              , {class: "q2", text: "for I will pass through the middle of you,” says Yahweh."}
              ]
        , 18: [ {class: "q", text: "“Woe to you who desire the day of Yahweh!"}
              ,  {class: "q2", text: "Why do you long for the day of Yahweh?"}
              , {class: "q", text: "It is darkness,"}
              , {class: "q2", text: "and not light."}
              ]
        , 19: [ {class: "q", text: "As if a man fled from a lion,"}
              , {class: "q2", text: "and a bear met him;"}
              , {class: "q", text: "Or he went into the house and leaned his hand on the wall,"}
              , {class: "q2", text: "and a snake bit him."}
              ]
        , 20: [ {class: "q", text: "Won’t the day of Yahweh be darkness, and not light?"}
              , {class: "q2", text: "Even very dark, and no brightness in it?"}
              ]
        , 21: [ {class: "q", text: "I hate, I despise your feasts,"}
              , {class: "q2", text: "and I can’t stand your solemn assemblies."}
              ]
        , 22: [ {class: "q", text: "Yes, though you offer me your burnt offerings and meal offerings,"}
              , {class: "q2", text: "I will not accept them;"}
              , {class: "q2", text: "neither will I regard the peace offerings of your fat animals."}
              ]
        , 23: [ {class: "q", text: "Take away from me the noise of your songs!"}
              , {class: "q2", text: "I will not listen to the music of your harps."}
              ]
        , 24: [ {class: "q", text: "But let justice roll on like rivers,"}
              , {class: "q2", text: "and righteousness like a mighty stream."}
              ]
        , 25: [ {class: "vs", text: "“Did you bring to me sacrifices and offerings in the wilderness forty years, house of Israel?"} ]
        , 26: [ {class: "vs", text: "You also carried the tent of your king and the shrine of your images, the star of your god, which you made for yourselves."} ]
        , 27: [ {class: "vs", text: "Therefore I will cause you to go into captivity beyond Damascus,” says Yahweh, whose name is the God of Armies."} ]
        }
        , 6:  { 1:  [ {class: "endpara", text: "Woe to those who are at ease in Zion,"}
              , {class: "q2", text: "and to those who are secure on the mountain of Samaria,"}
              , {class: "q2", text: "the notable men of the chief of the nations,"}
              , {class: "q2", text: "to whom the house of Israel come!"}
              ]
        , 2:  [ {class: "q", text: "Go to Calneh, and see;"}
              , {class: "q2", text: "and from there go to Hamath the great;"}
              , {class: "q2", text: "then go down to Gath of the Philistines."}
              , {class: "q", text: "are they better than these kingdoms?"}
              , {class: "q2", text: "or is their border greater than your border?"}
              ]
        , 3:  [ {class: "q", text: "Those who put far away the evil day,"}
              , {class: "q2", text: "and cause the seat of violence to come near;"}
              ]
        , 4:  [ {class: "q2", text: "Who lie on beds of ivory,"}
              , {class: "q2", text: "and stretch themselves on their couches,"}
              , {class: "q2", text: "and eat the lambs out of the flock,"}
              , {class: "q2", text: "and the calves out of the middle of the stall;"}
              ]
        , 5:  [ {class: "q2", text: "who strum on the strings of a harp;"}
              , {class: "q2", text: "who invent for themselves instruments of music, like David;"}
              ]
        , 6:  [ {class: "q2", text: "who drink wine in bowls,"}
              , {class: "q2", text: "and anoint themselves with the best oils;"}
              , {class: "q2", text: "but they are not grieved for the affliction of Joseph."}
              ]
        , 7:  [ {class: "q", text: "Therefore they will now go captive with the first who go captive;"}
              , {class: "q2", text: "and the feasting and lounging will end."}
              ]
        , 8:  [ {class: "q", text: "“The Lord Yahweh has sworn by himself,” says Yahweh, the God of Armies:"}
              , {class: "q2", text: "“I abhor the pride of Jacob,"}
              , {class: "q2", text: "and detest his fortresses."}
              , {class: "q2", text: "Therefore I will deliver up the city with all that is in it."}
              ]
        , 9:  [ {class: "q", text: "It will happen, if there remain ten men in one house,"}
              , {class: "q2", text: "that they shall die."}
              ]
        , 10: [ {class: "vs", text: "“When a man’s relative carries him, even he who burns him, to bring bodies out of the house, and asks him who is in the innermost parts of the house, ‘Is there yet any with you?’ And he says, ‘No;’ then he will say, ‘Hush! Indeed we must not mention Yahweh’s name.’"} ]
        , 11: [ {class: "q", text: "“For, behold, Yahweh commands, and the great house will be smashed to pieces,"}
              , {class: "q2", text: "and the little house into bits."}
              ]
        , 12: [ {class: "q", text: "Do horses run on the rocky crags?"}
              , {class: "q2", text: "Does one plow there with oxen?"}
              , {class: "q", text: "But you have turned justice into poison,"}
              , {class: "q2", text: "and the fruit of righteousness into bitterness;"}
              ]
        , 13: [ {class: "q", text: "you who rejoice in a thing of nothing, who say,"}
              , {class: "q2", text: "‘Haven’t we taken for ourselves horns by our own strength?’"}
              ]
        , 14: [ {class: "q", text: "For, behold, I will raise up against you a nation, house of Israel,”"}
              , {class: "q2", text: "says Yahweh, the God of Armies;"}
              , {class: "q2", text: "“and they will afflict you from the entrance of Hamath to the brook of the Arabah.”"}
              ]
        }
        , 7:  { 1: [ {class: "vs", text: "Thus the Lord Yahweh showed me: and behold, he formed locusts in the beginning of the shooting up of the latter growth; and behold, it was the latter growth after the king’s harvest."} ]
        , 2: [ {class: "vs", text: "When they finished eating the grass of the land, then I said, “Lord Yahweh, forgive, I beg you! How could Jacob stand? For he is small.”"} ]
        , 3: [ {class: "vs", text: "Yahweh relented concerning this. “It shall not be,” says Yahweh."} ]
        , 4: [ {class: "vs", text: "Thus the Lord Yahweh showed me and behold, the Lord Yahweh called for judgment by fire; and it dried up the great deep, and would have devoured the land."} ]
        , 5: [ {class: "vs", text: "Then I said, “Lord Yahweh, stop, I beg you! How could Jacob stand? For he is small.”"} ]
        , 6: [ {class: "vs", text: "Yahweh relented concerning this. “This also shall not be,” says the Lord Yahweh."} ]
        , 7: [ {class: "vs", text: "Thus he showed me and behold, the Lord stood beside a wall made by a plumb line, with a plumb line in his hand."} ]
        , 8: [ {class: "vs", text: "Yahweh said to me, “Amos, what do you see?”</p><p> I said, “A plumb line.”</p><p> Then the Lord said, “Behold, I will set a plumb line in the middle of my people Israel. I will not again pass by them any more."} ]
        , 9: [ {class: "vs", text: "The high places of Isaac will be desolate, the sanctuaries of Israel will be laid waste; and I will rise against the house of Jeroboam with the sword.”"} ]
        , 10: [ {class: "vs", text: "Then Amaziah the priest of Bethel sent to Jeroboam king of Israel, saying, “Amos has conspired against you in the middle of the house of Israel. The land is not able to bear all his words."} ]
        , 11: [ {class: "vs", text: "For Amos says, ‘Jeroboam will die by the sword, and Israel shall surely be led away captive out of his land.’”"} ]
        , 12: [ {class: "vs", text: "Amaziah also said to Amos, “You seer, go, flee away into the land of Judah, and there eat bread, and prophesy there:"} ]
        , 13: [ {class: "vs", text: "but don’t prophesy again any more at Bethel; for it is the king’s sanctuary, and it is a royal house!”"} ]
        , 14: [ {class: "vs", text: "Then Amos answered Amaziah, “I was no prophet, neither was I a prophet’s son; but I was a herdsman, and a farmer of sycamore figs;"} ]
        , 15: [ {class: "vs", text: "and Yahweh took me from following the flock, and Yahweh said to me, ‘Go, prophesy to my people Israel.’"} ]
        , 16: [ {class: "vs", text: "Now therefore listen to Yahweh’s word: ‘You say, Don’t prophesy against Israel, and don’t preach against the house of Isaac.’"} ]
        , 17: [ {class: "vs", text: "Therefore Yahweh says: ‘Your wife shall be a prostitute in the city, and your sons and your daughters shall fall by the sword, and your land shall be divided by line; and you yourself shall die in a land that is unclean, and Israel shall surely be led away captive out of his land.’”"} ]
        }
        , 8:  { 1: [ {class: "vs", text: "Thus the Lord Yahweh showed me: behold, a basket of summer fruit."} ]
        , 2:  [ {class: "endpara", text: "He said, “Amos, what do you see?”"}
              , {class: "endpara", text: "I said, “A basket of summer fruit.”"}
              , {class: "endpara", text: "Then Yahweh said to me,"}
              , {class: "q", text: "“The end has come on my people Israel."}
              , {class: "q2", text: "I will not again pass by them any more."}
              ]
        , 3:  [ {class: "q", text: "The songs of the temple will be wailing in that day,” says the Lord Yahweh."}
              , {class: "q2", text: "“The dead bodies will be many. In every place they will throw them out with silence."}
              ]
        , 4:  [ {class: "q", text: "Hear this, you who desire to swallow up the needy,"}
              , {class: "q2", text: "and cause the poor of the land to fail,"}
              ]
        , 5:  [ {class: "q2", text: "Saying, ‘When will the new moon be gone, that we may sell grain?"}
              , {class: "q2", text: "And the Sabbath, that we may market wheat,"}
              , {class: "q2", text: "making the ephah small, and the shekel large,"}
              , {class: "q2", text: "and dealing falsely with balances of deceit;"}
              ]
        , 6:  [ {class: "q", text: "that we may buy the poor for silver,"}
              , {class: "q2", text: "and the needy for a pair of sandals,"}
              , {class: "q2", text: "and sell the sweepings with the wheat?’”"}
              ]
        , 7:  [ {class: "q", text: "Yahweh has sworn by the pride of Jacob,"}
              , {class: "q2", text: "“Surely I will never forget any of their works."}
              ]
        , 8:  [ {class: "q", text: "Won’t the land tremble for this,"}
              , {class: "q2", text: "and everyone mourn who dwells in it?"}
              , {class: "q", text: "Yes, it will rise up wholly like the River;"}
              , {class: "q2", text: "and it will be stirred up and sink again, like the River of Egypt."}
              ]
        , 9:  [ {class: "q", text: "It will happen in that day,” says the Lord Yahweh,"}
              , {class: "q2", text: "“that I will cause the sun to go down at noon,"}
              , {class: "q2", text: "and I will darken the earth in the clear day."}
              ]
        , 10: [ {class: "q", text: "I will turn your feasts into mourning,"}
              , {class: "q2", text: "and all your songs into lamentation;"}
              , {class: "q", text: "and I will make you wear sackcloth on all your bodies,"}
              , {class: "q2", text: "and baldness on every head."}
              , {class: "q", text: "I will make it like the mourning for an only son,"}
              , {class: "q2", text: "and its end like a bitter day."}
              ]
        , 11: [ {class: "q", text: "Behold, the days come,” says the Lord Yahweh,"}
              , {class: "q2", text: "“that I will send a famine in the land,"}
              , {class: "q2", text: "not a famine of bread,"}
              , {class: "q2", text: "nor a thirst for water,"}
              , {class: "q2", text: "but of hearing Yahweh’s words."}
              ]
        , 12: [ {class: "q", text: "They will wander from sea to sea,"}
              , {class: "q2", text: "and from the north even to the east;"}
              , {class: "q2", text: "they will run back and forth to seek Yahweh’s word,"}
              , {class: "q2", text: "and will not find it."}
              ]
        , 13: [ {class: "q", text: "In that day the beautiful virgins"}
              , {class: "q2", text: "and the young men will faint for thirst."}
              ]
        , 14: [ {class: "q", text: "Those who swear by the sin of Samaria,"}
              , {class: "q2", text: "and say, ‘As your god, Dan, lives;’"}
              , {class: "q2", text: "and, ‘As the way of Beersheba lives;’"}
              , {class: "q2", text: "they will fall, and never rise up again.”"}
              ]
        }
        , 9:  { 1: [ {class: "vs", text: "I saw the Lord standing beside the altar, and he said, “Strike the tops of the pillars, that the thresholds may shake; and break them in pieces on the head of all of them; and I will kill the last of them with the sword: there shall not one of them flee away, and there shall not one of them escape."} ]
        , 2: [ {class: "vs", text: "Though they dig into Sheol, there my hand will take them; and though they climb up to heaven, there I will bring them down."} ]
        , 3: [ {class: "vs", text: "Though they hide themselves in the top of Carmel, I will search and take them out there; and though they be hidden from my sight in the bottom of the sea, there I will command the serpent, and it will bite them."} ]
        , 4: [ {class: "vs", text: "Though they go into captivity before their enemies, there I will command the sword, and it will kill them. I will set my eyes on them for evil, and not for good."} ]
        , 5: [ {class: "vs", text: "For the Lord, Yahweh of Armies, is he who touches the land and it melts, and all who dwell in it will mourn; and it will rise up wholly like the River, and will sink again, like the River of Egypt."} ]
        , 6: [ {class: "vs", text: "It is he who builds his rooms in the heavens, and has founded his vault on the earth; he who calls for the waters of the sea, and pours them out on the surface of the earth; Yahweh is his name."} ]
        , 7: [ {class: "vs", text: "Are you not like the children of the Ethiopians to me, children of Israel?” says Yahweh. “Haven’t I brought up Israel out of the land of Egypt, and the Philistines from Caphtor, and the Syrians from Kir?"} ]
        , 8: [ {class: "vs", text: "Behold, the eyes of the Lord Yahweh are on the sinful kingdom, and I will destroy it from off the surface of the earth; except that I will not utterly destroy the house of Jacob,” says Yahweh."} ]
        , 9: [ {class: "vs", text: "“For, behold, I will command, and I will sift the house of Israel among all the nations, as grain is sifted in a sieve, yet not the least kernel will fall on the earth."} ]
        , 10: [ {class: "vs", text: "All the sinners of my people will die by the sword, who say, ‘Evil won’t overtake nor meet us.’"} ]
        , 11: [ {class: "vs", text: "In that day I will raise up the tent of David who is fallen, and close up its breaches, and I will raise up its ruins, and I will build it as in the days of old;"} ]
        , 12: [ {class: "vs", text: "that they may possess the remnant of Edom, and all the nations who are called by my name,” says Yahweh who does this."} ]
        , 13: [ {class: "q", text: "“Behold, the days come,” says Yahweh,"}
              , {class: "q2", text: "“that the plowman shall overtake the reaper,"}
              , {class: "q2", text: "and the one treading grapes him who sows seed;"}
              , {class: "q2", text: "and sweet wine will drip from the mountains,"}
              , {class: "q2", text: "and flow from the hills."}
              ]
        , 14: [ {class: "q", text: "I will bring my people Israel back from captivity,"}
              , {class: "q2", text: "and they will rebuild the ruined cities, and inhabit them;"}
              , {class: "q2", text: "and they will plant vineyards, and drink wine from them."}
              , {class: "q", text: "They shall also make gardens,"}
              , {class: "q2", text: "and eat their fruit."}
              ]
        , 15: [ {class: "q", text: "I will plant them on their land,"}
              , {class: "q2", text: "and they will no more be plucked up out of their land which I have given them,”"}
              , {class: "q2", text: "says Yahweh your God."}
              ]
        }
  }  