        , 10: [ {class: "q", text: "I am my beloved’s."}
              , {class: "q2", text: "His desire is toward me."}
              ]
        , 11: [ {class: "q", text: "Come, my beloved! Let’s go out into the field."}
              , {class: "q2", text: "Let’s lodge in the villages."}
              ]
        , 12: [ {class: "q", text: "Let’s go early up to the vineyards."}
              , {class: "q2", text: "Let’s see whether the vine has budded,"}
              , {class: "q2", text: "its blossom is open,"}
              , {class: "q2", text: "and the pomegranates are in flower."}
              , {class: "q2", text: "There I will give you my love."}
              ]
        , 13: [ {class: "q", text: "The mandrakes produce fragrance."}
              , {class: "q2", text: "At our doors are all kinds of precious fruits, new and old,"}
              , {class: "q2", text: "which I have stored up for you, my beloved."}
              ]
