exports.zechariah =
  { 1:  { 1:  [ {class: "vs", text: "In the eighth month, in the second year of Darius, Yahweh’s word came to Zechariah the son of Berechiah, the son of Iddo, the prophet, saying,"} ]
        , 2:  [ {class: "vs", text: "“Yahweh was very displeased with your fathers."} ]
        , 3:  [ {class: "vs", text: "Therefore tell them: Yahweh of Armies says: ‘Return to me,’ says Yahweh of Armies, ‘and I will return to you,’ says Yahweh of Armies."} ]
        , 4:  [ {class: "vs", text: "Don’t you be like your fathers, to whom the former prophets proclaimed, saying: Yahweh of Armies says, ‘Return now from your evil ways, and from your evil doings;’ but they didn’t hear, nor listen to me, says Yahweh."} ]
        , 5:  [ {class: "vs", text: "Your fathers, where are they? And the prophets, do they live forever?"} ]
        , 6:  [ {class: "vs", text: "But my words and my decrees, which I commanded my servants the prophets, didn’t they overtake your fathers?</p><p> “Then they repented and said, ‘Just as Yahweh of Armies determined to do to us, according to our ways, and according to our practices, so he has dealt with us.’”"} ]
        , 7:  [ {class: "vs", text: "On the twenty-fourth day of the eleventh month, which is the month Shebat, in the second year of Darius, Yahweh’s word came to Zechariah the son of Berechiah, the son of Iddo, the prophet, saying,"} ]
        , 8:  [ {class: "vs", text: "“I had a vision in the night, and behold, a man riding on a red horse, and he stood among the myrtle trees that were in a ravine; and behind him there were red, brown, and white horses."} ]
        , 9:  [ {class: "vs", text: "Then I asked, ‘My lord, what are these?’”</p><p> The angel who talked with me said to me, “I will show you what these are.”"} ]
        , 10: [ {class: "vs", text: "The man who stood among the myrtle trees answered, “They are the ones Yahweh has sent to go back and forth through the earth.”"} ]
        , 11: [ {class: "vs", text: "They reported to Yahweh’s angel who stood among the myrtle trees, and said, “We have walked back and forth through the earth, and behold, all the earth is at rest and in peace.”"} ]
        , 12: [ {class: "vs", text: "Then Yahweh’s angel replied, “O Yahweh of Armies, how long will you not have mercy on Jerusalem and on the cities of Judah, against which you have had indignation these seventy years?”"} ]
        , 13: [ {class: "vs", text: "Yahweh answered the angel who talked with me with kind and comforting words."} ]
        , 14: [ {class: "vs", text: "So the angel who talked with me said to me, “Proclaim, saying, ‘Yahweh of Armies says: “I am jealous for Jerusalem and for Zion with a great jealousy."} ]
        , 15: [ {class: "vs", text: "I am very angry with the nations that are at ease; for I was but a little displeased, but they added to the calamity.”"} ]
        , 16: [ {class: "vs", text: "Therefore Yahweh says: “I have returned to Jerusalem with mercy. My house shall be built in it,” says Yahweh of Armies, “and a line shall be stretched out over Jerusalem.”’"} ]
        , 17: [ {class: "vs", text: "“Proclaim further, saying, ‘Yahweh of Armies says: “My cities will again overflow with prosperity, and Yahweh will again comfort Zion, and will again choose Jerusalem.”’”"} ]
        , 18: [ {class: "vs", text: "I lifted up my eyes, and saw, and behold, four horns."} ]
        , 19: [ {class: "vs", text: "I asked the angel who talked with me, “What are these?”</p><p> He answered me, “These are the horns which have scattered Judah, Israel, and Jerusalem.”"} ]
        , 20: [ {class: "vs", text: "Yahweh showed me four craftsmen."} ]
        , 21: [ {class: "vs", text: "Then I asked, “What are these coming to do?”</p><p> He said, “These are the horns which scattered Judah, so that no man lifted up his head; but these have come to terrify them, to cast down the horns of the nations, which lifted up their horn against the land of Judah to scatter it.”"} ]
        }
  , 10: { 1:  [ {class: "q", text: "Ask of Yahweh rain in the spring time,"}
              , {class: "q2", text: "Yahweh who makes storm clouds,"}
              , {class: "q2", text: "and he gives rain showers to everyone for the plants in the field."}
              ]
        , 2:  [ {class: "q", text: "For the teraphim have spoken vanity,"}
              , {class: "q2", text: "and the diviners have seen a lie;"}
              , {class: "q2", text: "and they have told false dreams."}
              , {class: "q", text: "They comfort in vain."}
              , {class: "q2", text: "Therefore they go their way like sheep."}
              , {class: "q2", text: "They are oppressed, because there is no shepherd."}
              ]
        , 3:  [ {class: "q", text: "My anger is kindled against the shepherds,"}
              , {class: "q2", text: "and I will punish the male goats;"}
              , {class: "q2", text: "For Yahweh of Armies has visited his flock, the house of Judah,"}
              , {class: "q2", text: "and will make them as his majestic horse in the battle."}
              ]
        , 4:  [ {class: "q", text: "From him will come the cornerstone,"}
              , {class: "q2", text: "from him the nail,"}
              , {class: "q2", text: "from him the battle bow,"}
              , {class: "q2", text: "from him every ruler together."}
              ]
        , 5:  [ {class: "q2", text: "/span> They shall be as mighty men,"}
              , {class: "q2", text: "treading down muddy streets in the battle;"}
              , {class: "q2", text: "and they shall fight, because Yahweh is with them;"}
              , {class: "q2", text: "and the riders on horses will be confounded."}
              ]
        , 6:  [ {class: "q", text: "“I will strengthen the house of Judah,"}
              , {class: "q2", text: "and I will save the house of Joseph,"}
              , {class: "q2", text: "and I will bring them back;"}
              , {class: "q2", text: "for I have mercy on them;"}
              , {class: "q2", text: "and they will be as though I had not cast them off:"}
              ,  {class: "q2", text: "for I am Yahweh their God, and I will hear them."}
              ]
        , 7:  [ {class: "q", text: "Ephraim will be like a mighty man,"}
              ,  {class: "q2", text: "and their heart will rejoice as through wine;"}
              , {class: "q2", text: "yes, their children will see it, and rejoice."}
              , {class: "q2", text: "Their heart will be glad in Yahweh."}
              ]
        , 8:  [ {class: "q", text: "I will signal for them, and gather them;"}
              , {class: "q2", text: "for I have redeemed them;"}
              , {class: "q2", text: "and they will increase as they have increased."}
              ]
        , 9:  [ {class: "q", text: "I will sow them among the peoples;"}
              , {class: "q2", text: "and they will remember me in far countries;"}
              , {class: "q2", text: "and they will live with their children, and will return."}
              ]
        , 10: [ {class: "q", text: "I will bring them again also out of the land of Egypt,"}
              , {class: "q2", text: "and gather them out of Assyria;"}
              , {class: "q", text: "and I will bring them into the land of Gilead and Lebanon;"}
              , {class: "q2", text: "and there won’t be room enough for them."}
              ]
        , 11: [ {class: "q", text: "He will pass through the sea of affliction,"}
              , {class: "q2", text: "and will strike the waves in the sea,"}
              , {class: "q2", text: "and all the depths of the Nile will dry up;"}
              , {class: "q2", text: "and the pride of Assyria will be brought down,"}
              , {class: "q2", text: "and the scepter of Egypt will depart."}
              ]
        , 12: [ {class: "q", text: "I will strengthen them in Yahweh;"}
              , {class: "q2", text: "and they will walk up and down in his name,” says Yahweh."}
              ]
        }
  , 11: { 1:  [ {class: "q", text: "Open your doors, Lebanon,"}
              , {class: "q2", text: "that the fire may devour your cedars."}
              ]
        , 2:  [ {class: "q", text: "Wail, cypress tree, for the cedar has fallen,"}
              , {class: "q2", text: "because the stately ones are destroyed."}
              , {class: "q", text: "Wail, you oaks of Bashan,"}
              , {class: "q2", text: "for the strong forest has come down."}
              ]
        , 3:  [ {class: "q", text: "A voice of the wailing of the shepherds!"}
              , {class: "q2", text: "For their glory is destroyed: a voice of the roaring of young lions!"}
              , {class: "q2", text: "For the pride of the Jordan is ruined."}
              ]
        , 4:  [ {class: "vs", text: "Yahweh my God says: “Feed the flock of slaughter."} ]
        , 5:  [ {class: "vs", text: "Their buyers slaughter them, and go unpunished. Those who sell them say, ‘Blessed be Yahweh, for I am rich;’ and their own shepherds don’t pity them."} ]
        , 6:  [ {class: "vs", text: "For I will no more pity the inhabitants of the land,” says Yahweh; “but, behold, I will deliver the men everyone into his neighbor’s hand, and into the hand of his king. They will strike the land, and out of their hand I will not deliver them.”"} ]
        , 7:  [ {class: "vs", text: "So I fed the flock of slaughter, especially the oppressed of the flock. I took for myself two staffs. The one I called “Favor”, and the other I called “Union”, and I fed the flock."} ]
        , 8:  [ {class: "vs", text: "I cut off the three shepherds in one month; for my soul was weary of them, and their soul also loathed me."} ]
        , 9:  [ {class: "vs", text: "Then I said, “I will not feed you. That which dies, let it die; and that which is to be cut off, let it be cut off; and let those who are left eat each other’s flesh.”"} ]
        , 10: [ {class: "vs", text: "I took my staff Favor, and cut it apart, that I might break my covenant that I had made with all the peoples."} ]
        , 11: [ {class: "vs", text: "It was broken in that day; and thus the poor of the flock that listened to me knew that it was Yahweh’s word."} ]
        , 12: [ {class: "vs", text: "I said to them, “If you think it best, give me my wages; and if not, keep them.” So they weighed for my wages thirty pieces of silver."} ]
        , 13: [ {class: "vs", text: "Yahweh said to me, “Throw it to the potter, the handsome price that I was valued at by them!” I took the thirty pieces of silver, and threw them to the potter, in Yahweh’s house."} ]
        , 14: [ {class: "vs", text: "Then I cut apart my other staff, even Union, that I might break the brotherhood between Judah and Israel."} ]
        , 15: [ {class: "vs", text: "Yahweh said to me, “Take for yourself yet again the equipment of a foolish shepherd."} ]
        , 16: [ {class: "vs", text: "For, behold, I will raise up a shepherd in the land, who will not visit those who are cut off, neither will seek those who are scattered, nor heal that which is broken, nor feed that which is sound; but he will eat the meat of the fat sheep, and will tear their hoofs in pieces."} ]
        , 17: [ {class: "vs", text: "Woe to the worthless shepherd who leaves the flock! The sword will be on his arm, and on his right eye. His arm will be completely withered, and his right eye will be totally blinded!”"} ]
        }
  , 12: { 1:  [ {class: "vs", text: "A revelation, Yahweh’s word concerning Israel. Yahweh, who stretches out the heavens, and lays the foundation of the earth, and forms the spirit of man within him says:"} ]
        , 2:  [ {class: "vs", text: "“Behold, I will make Jerusalem a cup of reeling to all the surrounding peoples, and it will also be on Judah in the siege against Jerusalem."} ]
        , 3:  [ {class: "vs", text: "It will happen in that day, that I will make Jerusalem a burdensome stone for all the peoples. All who burden themselves with it will be severely wounded, and all the nations of the earth will be gathered together against it."} ]
        , 4:  [ {class: "vs", text: "In that day,” says Yahweh, “I will strike every horse with terror, and his rider with madness; and I will open my eyes on the house of Judah, and will strike every horse of the peoples with blindness."} ]
        , 5:  [ {class: "vs", text: "The chieftains of Judah will say in their heart, ‘The inhabitants of Jerusalem are my strength in Yahweh of Armies their God.’"} ]
        , 6:  [ {class: "vs", text: "In that day I will make the chieftains of Judah like a pan of fire among wood, and like a flaming torch among sheaves; and they will devour all the surrounding peoples, on the right hand and on the left; and Jerusalem will yet again dwell in their own place, even in Jerusalem."} ]
        , 7:  [ {class: "vs", text: "Yahweh also will save the tents of Judah first, that the glory of David’s house and the glory of the inhabitants of Jerusalem not be magnified above Judah."} ]
        , 8:  [ {class: "vs", text: "In that day Yahweh will defend the inhabitants of Jerusalem. He who is feeble among them at that day will be like David, and David’s house will be like God, like Yahweh’s angel before them."} ]
        , 9:  [ {class: "vs", text: "It will happen in that day, that I will seek to destroy all the nations that come against Jerusalem."} ]
        , 10: [ {class: "vs", text: "I will pour on David’s house, and on the inhabitants of Jerusalem, the spirit of grace and of supplication; and they will look to me whom they have pierced; and they shall mourn for him, as one mourns for his only son, and will grieve bitterly for him, as one grieves for his firstborn."} ]
        , 11: [ {class: "vs", text: "In that day there will be a great mourning in Jerusalem, like the mourning of Hadadrimmon in the valley of Megiddon."} ]
        , 12: [ {class: "vs", text: "The land will mourn, every family apart; the family of David’s house apart, and their wives apart; the family of the house of Nathan apart, and their wives apart;"} ]
        , 13: [ {class: "vs", text: "the family of the house of Levi apart, and their wives apart; the family of the Shimeites apart, and their wives apart;"} ]
        , 14: [ {class: "vs", text: "all the families who remain, every family apart, and their wives apart."} ]
        }
  , 13: { 1:  [ {class: "vs", text: "“In that day there will be a spring opened to David’s house and to the inhabitants of Jerusalem, for sin and for uncleanness."} ]
        , 2:  [ {class: "vs", text: "It will come to pass in that day, says Yahweh of Armies, that I will cut off the names of the idols out of the land, and they will be remembered no more. I will also cause the prophets and the spirit of impurity to pass out of the land."} ]
        , 3:  [ {class: "vs", text: "It will happen that, when anyone still prophesies, then his father and his mother who bore him will tell him, ‘You must die, because you speak lies in Yahweh’s name;’ and his father and his mother who bore him will stab him when he prophesies."} ]
        , 4:  [ {class: "vs", text: "It will happen in that day, that the prophets will each be ashamed of his vision, when he prophesies; neither will they wear a hairy mantle to deceive:"} ]
        , 5:  [ {class: "vs", text: "but he will say, ‘I am no prophet, I am a tiller of the ground; for I have been made a bondservant from my youth.’"} ]
        , 6:  [ {class: "vs", text: "One will say to him, ‘What are these wounds between your arms?’ Then he will answer, ‘Those with which I was wounded in the house of my friends.’"} ]
        , 7:  [ {class: "q", text: "“Awake, sword, against my shepherd,"}
              , {class: "q2", text: "and against the man who is close to me,” says Yahweh of Armies."}
              , {class: "q", text: "“Strike the shepherd, and the sheep will be scattered;"}
              , {class: "q2", text: "and I will turn my hand against the little ones."}
              ]
        , 8:  [ {class: "q", text: "It shall happen that in all the land,” says Yahweh,"}
              , {class: "q2", text: "“two parts in it will be cut off and die;"}
              , {class: "q2", text: "but the third will be left in it."}
              ]
        , 9:  [ {class: "q", text: "I will bring the third part into the fire,"}
              , {class: "q2", text: "and will refine them as silver is refined,"}
              , {class: "q2", text: "and will test them like gold is tested."}
              , {class: "q", text: "They will call on my name, and I will hear them."}
              , {class: "q2", text: "I will say, ‘It is my people;’"}
              , {class: "q2", text: "and they will say, ‘Yahweh is my God.’”"}
              ]
        }
  , 14: { 1:  [ {class: "vs", text: "Behold, a day of Yahweh comes, when your plunder will be divided within you."} ]
        , 2:  [ {class: "vs", text: "For I will gather all nations against Jerusalem to battle; and the city will be taken, the houses rifled, and the women ravished. Half of the city will go out into captivity, and the rest of the people will not be cut off from the city."} ]
        , 3:  [ {class: "vs", text: "Then Yahweh will go out and fight against those nations, as when he fought in the day of battle."} ]
        , 4:  [ {class: "vs", text: "His feet will stand in that day on the Mount of Olives, which is before Jerusalem on the east; and the Mount of Olives will be split in two, from east to west, making a very great valley. Half of the mountain will move toward the north, and half of it toward the south."} ]
        , 5:  [ {class: "vs", text: "You shall flee by the valley of my mountains; for the valley of the mountains shall reach to Azel; yes, you shall flee, just like you fled from before the earthquake in the days of Uzziah king of Judah. Yahweh my God will come, and all the holy ones with you."} ]
        , 6:  [ {class: "vs", text: "It will happen in that day, that there will not be light, cold, or frost."} ]
        , 7:  [ {class: "vs", text: "It will be a unique day which is known to Yahweh; not day, and not night; but it will come to pass, that at evening time there will be light."} ]
        , 8:  [ {class: "vs", text: "It will happen in that day, that living waters will go out from Jerusalem: half of them toward the eastern sea, and half of them toward the western sea. It will be so in summer and in winter."} ]
        , 9:  [ {class: "vs", text: "Yahweh will be King over all the earth. In that day Yahweh will be one, and his name one."} ]
        , 10: [ {class: "vs", text: "All the land will be made like the Arabah, from Geba to Rimmon south of Jerusalem; and she will be lifted up, and will dwell in her place, from Benjamin’s gate to the place of the first gate, to the corner gate, and from the tower of Hananel to the king’s wine presses."} ]
        , 11: [ {class: "vs", text: "Men will dwell therein, and there will be no more curse; but Jerusalem will dwell safely."} ]
        , 12: [ {class: "vs", text: "This will be the plague with which Yahweh will strike all the peoples who have fought against Jerusalem: their flesh will consume away while they stand on their feet, and their eyes will consume away in their sockets, and their tongue will consume away in their mouth."} ]
        , 13: [ {class: "vs", text: "It will happen in that day, that a great panic from Yahweh will be among them; and they will each hold onto the hand of his neighbor, and his hand will rise up against the hand of his neighbor."} ]
        , 14: [ {class: "vs", text: "Judah also will fight at Jerusalem; and the wealth of all the surrounding nations will be gathered together: gold, and silver, and clothing, in great abundance."} ]
        , 15: [ {class: "vs", text: "A plague like this will fall on the horse, on the mule, on the camel, on the donkey, and on all the animals that will be in those camps."} ]
        , 16: [ {class: "vs", text: "It will happen that everyone who is left of all the nations that came against Jerusalem will go up from year to year to worship the King, Yahweh of Armies, and to keep the feast of booths."} ]
        , 17: [ {class: "vs", text: "It will be, that whoever of all the families of the earth doesn’t go up to Jerusalem to worship the King, Yahweh of Armies, on them there will be no rain."} ]
        , 18: [ {class: "vs", text: "If the family of Egypt doesn’t go up, and doesn’t come, neither will it rain on them. This will be the plague with which Yahweh will strike the nations that don’t go up to keep the feast of booths."} ]
        , 19: [ {class: "vs", text: "This will be the punishment of Egypt, and the punishment of all the nations that don’t go up to keep the feast of booths."} ]
        , 20: [ {class: "vs", text: "In that day there will be on the bells of the horses, “HOLY TO YAHWEH”; and the pots in Yahweh’s house will be like the bowls before the altar."} ]
        , 21: [ {class: "vs", text: "Yes, every pot in Jerusalem and in Judah will be holy to Yahweh of Armies; and all those who sacrifice will come and take of them, and cook in them. In that day there will no longer be a Canaanite in the house of Yahweh of Armies."} ]
        }
  , 2:  { 1:  [ {class: "vs", text: "I lifted up my eyes, and saw, and behold, a man with a measuring line in his hand."} ]
        , 2:  [ {class: "vs", text: "Then I asked, “Where are you going?”</p><p> He said to me, “To measure Jerusalem, to see what is its width and what is its length.”"} ]
        , 3:  [ {class: "vs", text: "Behold, the angel who talked with me went out, and another angel went out to meet him,"} ]
        , 4:  [ {class: "vs", text: "and said to him, “Run, speak to this young man, saying, ‘Jerusalem will be inhabited as villages without walls, because of the multitude of men and livestock in it."} ]
        , 5:  [ {class: "vs", text: "For I,’ says Yahweh, ‘will be to her a wall of fire around it, and I will be the glory in the middle of her."} ]
        , 6:  [ {class: "vs", text: "Come! Come! Flee from the land of the north,’ says Yahweh; ‘for I have spread you abroad as the four winds of the sky,’ says Yahweh."} ]
        , 7:  [ {class: "vs", text: "‘Come, Zion! Escape, you who dwell with the daughter of Babylon.’"} ]
        , 8:  [ {class: "vs", text: "For Yahweh of Armies says: ‘For honor he has sent me to the nations which plundered you; for he who touches you touches the apple of his eye."} ]
        , 9:  [ {class: "vs", text: "For, behold, I will shake my hand over them, and they will be a plunder to those who served them; and you will know that Yahweh of Armies has sent me."} ]
        , 10: [ {class: "vs", text: "Sing and rejoice, daughter of Zion; for, behold, I come, and I will dwell within you,’ says Yahweh."} ]
        , 11: [ {class: "vs", text: "Many nations shall join themselves to Yahweh in that day, and shall be my people; and I will dwell among you, and you shall know that Yahweh of Armies has sent me to you."} ]
        , 12: [ {class: "vs", text: "Yahweh will inherit Judah as his portion in the holy land, and will again choose Jerusalem."} ]
        , 13: [ {class: "vs", text: "Be silent, all flesh, before Yahweh; for he has roused himself from his holy habitation!”"} ]
        }
  , 3:  { 1:  [ {class: "vs", text: "He showed me Joshua the high priest standing before Yahweh’s angel, and Satan standing at his right hand to be his adversary."} ]
        , 2:  [ {class: "vs", text: "Yahweh said to Satan, “Yahweh rebuke you, Satan! Yes, Yahweh who has chosen Jerusalem rebuke you! Isn’t this a burning stick plucked out of the fire?”"} ]
        , 3:  [ {class: "vs", text: "Now Joshua was clothed with filthy garments, and was standing before the angel."} ]
        , 4:  [ {class: "vs", text: "He answered and spoke to those who stood before him, saying, “Take the filthy garments off him.” To him he said, “Behold, I have caused your iniquity to pass from you, and I will clothe you with rich clothing.”"} ]
        , 5:  [ {class: "vs", text: "I said, “Let them set a clean turban on his head.”</p><p> So they set a clean turban on his head, and clothed him; and Yahweh’s angel was standing by."} ]
        , 6:  [ {class: "vs", text: "Yahweh’s angel protested to Joshua, saying,"} ]
        , 7:  [ {class: "vs", text: "“Yahweh of Armies says: ‘If you will walk in my ways, and if you will follow my instructions, then you also shall judge my house, and shall also keep my courts, and I will give you a place of access among these who stand by."} ]
        , 8:  [ {class: "vs", text: "Hear now, Joshua the high priest, you and your fellows who sit before you; for they are men who are a sign: for, behold, I will bring out my servant, the Branch."} ]
        , 9:  [ {class: "vs", text: "For, behold, the stone that I have set before Joshua; on one stone are seven eyes: behold, I will engrave its engraving,’ says Yahweh of Armies, ‘and I will remove the iniquity of that land in one day."} ]
        , 10: [ {class: "vs", text: "In that day,’ says Yahweh of Armies, ‘you will invite every man his neighbor under the vine and under the fig tree.’”"} ]
        }
  , 4:  { 1:  [ {class: "vs", text: "The angel who talked with me came again, and wakened me, as a man who is wakened out of his sleep."} ]
        , 2:  [ {class: "vs", text: "He said to me, “What do you see?”</p><p> I said, “I have seen, and behold, a lamp stand all of gold, with its bowl on the top of it, and its seven lamps on it; there are seven pipes to each of the lamps, which are on the top of it;"} ]
        , 3:  [ {class: "vs", text: "and two olive trees by it, one on the right side of the bowl, and the other on the left side of it.”"} ]
        , 4:  [ {class: "vs", text: "I answered and spoke to the angel who talked with me, saying, “What are these, my lord?”"} ]
        , 5:  [ {class: "vs", text: "Then the angel who talked with me answered me, “Don’t you know what these are?”</p><p> I said, “No, my lord.”"} ]
        , 6:  [ {class: "vs", text: "Then he answered and spoke to me, saying, “This is Yahweh’s word to Zerubbabel, saying, ‘Not by might, nor by power, but by my Spirit,’ says Yahweh of Armies."} ]
        , 7:  [ {class: "vs", text: "Who are you, great mountain? Before Zerubbabel you are a plain; and he will bring out the capstone with shouts of ‘Grace, grace, to it!’”"} ]
        , 8:  [ {class: "vs", text: "Moreover Yahweh’s word came to me, saying,"} ]
        , 9:  [ {class: "vs", text: "“The hands of Zerubbabel have laid the foundation of this house. His hands shall also finish it; and you will know that Yahweh of Armies has sent me to you."} ]
        , 10: [ {class: "vs", text: "Indeed, who despises the day of small things? For these seven shall rejoice, and shall see the plumb line in the hand of Zerubbabel. These are Yahweh’s eyes, which run back and forth through the whole earth.”"} ]
        , 11: [ {class: "vs", text: "Then I asked him, “What are these two olive trees on the right side of the lamp stand and on the left side of it?”"} ]
        , 12: [ {class: "vs", text: "I asked him the second time, “What are these two olive branches, which are beside the two golden spouts, that pour the golden oil out of themselves?”"} ]
        , 13: [ {class: "vs", text: "He answered me, “Don’t you know what these are?”</p><p> I said, “No, my lord.”"} ]
        , 14: [ {class: "vs", text: "Then he said, “These are the two anointed ones who stand by the Lord of the whole earth.”"} ]
        }
  , 5:  { 1:  [ {class: "vs", text: "Then again I lifted up my eyes, and saw, and behold, a flying scroll."} ]
        , 2:  [ {class: "vs", text: "He said to me, “What do you see?”</p><p> I answered, “I see a flying scroll; its length is twenty cubits, and its width ten cubits.”"} ]
        , 3:  [ {class: "vs", text: "Then he said to me, “This is the curse that goes out over the surface of the whole land; for everyone who steals shall be cut off according to it on the one side; and everyone who swears falsely shall be cut off according to it on the other side."} ]
        , 4:  [ {class: "vs", text: "I will cause it to go out,” says Yahweh of Armies, “and it will enter into the house of the thief, and into the house of him who swears falsely by my name; and it will remain in the middle of his house, and will destroy it with its timber and its stones.”"} ]
        , 5:  [ {class: "vs", text: "Then the angel who talked with me came forward, and said to me, “Lift up now your eyes, and see what this is that is appearing.”"} ]
        , 6:  [ {class: "vs", text: "I said, “What is it?”</p><p> He said, “This is the ephah basket that is appearing.” He said moreover, “This is their appearance in all the land"} ]
        , 7:  [ {class: "vs", text: "(and behold, a talent of lead was lifted up); and this is a woman sitting in the middle of the ephah basket.”"} ]
        , 8:  [ {class: "vs", text: "He said, “This is Wickedness;” and he threw her down into the middle of the ephah basket; and he threw the weight of lead on its mouth."} ]
        , 9:  [ {class: "vs", text: "Then I lifted up my eyes and saw, and behold, there were two women, and the wind was in their wings. Now they had wings like the wings of a stork, and they lifted up the ephah basket between earth and the sky."} ]
        , 10: [ {class: "vs", text: "Then I said to the angel who talked with me, “Where are these carrying the ephah basket?”"} ]
        , 11: [ {class: "vs", text: "He said to me, “To build her a house in the land of Shinar. When it is prepared, she will be set there in her own place.”"} ]
        }
  , 6:  { 1:  [ {class: "vs", text: "Again I lifted up my eyes, and saw, and behold, four chariots came out from between two mountains; and the mountains were mountains of bronze."} ]
        , 2:  [ {class: "vs", text: "In the first chariot were red horses; in the second chariot black horses;"} ]
        , 3:  [ {class: "vs", text: "in the third chariot white horses; and in the fourth chariot dappled horses, all of them powerful."} ]
        , 4:  [ {class: "vs", text: "Then I asked the angel who talked with me, “What are these, my lord?”"} ]
        , 5:  [ {class: "vs", text: "The angel answered me, “These are the four winds of the sky, which go out from standing before the Lord of all the earth."} ]
        , 6:  [ {class: "vs", text: "The one with the black horses goes out toward the north country; and the white went out after them; and the dappled went out toward the south country.”"} ]
        , 7:  [ {class: "vs", text: "The strong went out, and sought to go that they might walk back and forth through the earth: and he said, “Go around and through the earth!” So they walked back and forth through the earth."} ]
        , 8:  [ {class: "vs", text: "Then he called to me, and spoke to me, saying, “Behold, those who go toward the north country have quieted my spirit in the north country.”"} ]
        , 9:  [ {class: "vs", text: "Yahweh’s word came to me, saying,"} ]
        , 10: [ {class: "vs", text: "“Take of them of the captivity, even of Heldai, of Tobijah, and of Jedaiah; and come the same day, and go into the house of Josiah the son of Zephaniah, where they have come from Babylon."} ]
        , 11: [ {class: "vs", text: "Yes, take silver and gold, and make crowns, and set them on the head of Joshua the son of Jehozadak, the high priest;"} ]
        , 12: [ {class: "vs", text: "and speak to him, saying, ‘Yahweh of Armies says, “Behold, the man whose name is the Branch: and he shall grow up out of his place; and he shall build Yahweh’s temple;"} ]
        , 13: [ {class: "vs", text: "even he shall build Yahweh’s temple; and he shall bear the glory, and shall sit and rule on his throne; and he shall be a priest on his throne; and the counsel of peace shall be between them both."} ]
        , 14: [ {class: "vs", text: "The crowns shall be to Helem, and to Tobijah, and to Jedaiah, and to Hen the son of Zephaniah, for a memorial in Yahweh’s temple."} ]
        , 15: [ {class: "vs", text: "Those who are far off shall come and build in Yahweh’s temple; and you shall know that Yahweh of Armies has sent me to you. This will happen, if you will diligently obey Yahweh your God’s voice.”’”"} ]
        }
  , 7:  { 1:  [ {class: "vs", text: "In the fourth year of king Darius, Yahweh’s word came to Zechariah in the fourth day of the ninth month, the month of Chislev."} ]
        , 2:  [ {class: "vs", text: "The people of Bethel sent Sharezer and Regem Melech, and their men, to entreat Yahweh’s favor,"} ]
        , 3:  [ {class: "vs", text: "and to speak to the priests of the house of Yahweh of Armies, and to the prophets, saying, “Should I weep in the fifth month, separating myself, as I have done these so many years?”"} ]
        , 4:  [ {class: "vs", text: "Then the word of Yahweh of Armies came to me, saying,"} ]
        , 5:  [ {class: "vs", text: "“Speak to all the people of the land, and to the priests, saying, ‘When you fasted and mourned in the fifth and in the seventh month for these seventy years, did you at all fast to me, really to me?"} ]
        , 6:  [ {class: "vs", text: "When you eat, and when you drink, don’t you eat for yourselves, and drink for yourselves?"} ]
        , 7:  [ {class: "vs", text: "Aren’t these the words which Yahweh proclaimed by the former prophets, when Jerusalem was inhabited and in prosperity, and its cities around her, and the South and the lowland were inhabited?’”"} ]
        , 8:  [ {class: "vs", text: "Yahweh’s word came to Zechariah, saying,"} ]
        , 9:  [ {class: "vs", text: "“Thus has Yahweh of Armies spoken, saying, ‘Execute true judgment, and show kindness and compassion every man to his brother."} ]
        , 10: [ {class: "vs", text: "Don’t oppress the widow, nor the fatherless, the foreigner, nor the poor; and let none of you devise evil against his brother in your heart.’"} ]
        , 11: [ {class: "vs", text: "But they refused to listen, and turned their backs, and stopped their ears, that they might not hear."} ]
        , 12: [ {class: "vs", text: "Yes, they made their hearts as hard as flint, lest they might hear the law, and the words which Yahweh of Armies had sent by his Spirit by the former prophets. Therefore great wrath came from Yahweh of Armies."} ]
        , 13: [ {class: "vs", text: "It has come to pass that, as he called, and they refused to listen, so they will call, and I will not listen,” said Yahweh of Armies;"} ]
        , 14: [ {class: "vs", text: "“but I will scatter them with a whirlwind among all the nations which they have not known. Thus the land was desolate after them, so that no man passed through nor returned: for they made the pleasant land desolate.”"} ]
        }
  , 8:  { 1:  [ {class: "vs", text: "The word of Yahweh of Armies came to me."} ]
        , 2:  [ {class: "vs", text: "Yahweh of Armies says: “I am jealous for Zion with great jealousy, and I am jealous for her with great wrath.”"} ]
        , 3:  [ {class: "vs", text: "Yahweh says: “I have returned to Zion, and will dwell in the middle of Jerusalem. Jerusalem shall be called ‘The City of Truth;’ and the mountain of Yahweh of Armies, ‘The Holy Mountain.’”"} ]
        , 4:  [ {class: "vs", text: "Yahweh of Armies says: “Old men and old women will again dwell in the streets of Jerusalem, every man with his staff in his hand for very age."} ]
        , 5:  [ {class: "vs", text: "The streets of the city will be full of boys and girls playing in its streets.”"} ]
        , 6:  [ {class: "vs", text: "Yahweh of Armies says: “If it is marvelous in the eyes of the remnant of this people in those days, should it also be marvelous in my eyes?” says Yahweh of Armies."} ]
        , 7:  [ {class: "vs", text: "Yahweh of Armies says: “Behold, I will save my people from the east country, and from the west country;"} ]
        , 8:  [ {class: "vs", text: "and I will bring them, and they will dwell within Jerusalem; and they will be my people, and I will be their God, in truth and in righteousness.”"} ]
        , 9:  [ {class: "vs", text: "Yahweh of Armies says: “Let your hands be strong, you who hear in these days these words from the mouth of the prophets who were in the day that the foundation of the house of Yahweh of Armies was laid, even the temple, that it might be built."} ]
        , 10: [ {class: "vs", text: "For before those days there was no wages for man, nor any wages for an animal; neither was there any peace to him who went out or came in, because of the adversary. For I set all men everyone against his neighbor."} ]
        , 11: [ {class: "vs", text: "But now I will not be to the remnant of this people as in the former days,” says Yahweh of Armies."} ]
        , 12: [ {class: "vs", text: "“For the seed of peace and the vine will yield its fruit, and the ground will give its increase, and the heavens will give their dew; and I will cause the remnant of this people to inherit all these things."} ]
        , 13: [ {class: "vs", text: "It shall come to pass that, as you were a curse among the nations, house of Judah and house of Israel, so I will save you, and you shall be a blessing. Don’t be afraid. Let your hands be strong.”"} ]
        , 14: [ {class: "vs", text: "For Yahweh of Armies says: “As I thought to do evil to you, when your fathers provoked me to wrath,” says Yahweh of Armies, “and I didn’t repent;"} ]
        , 15: [ {class: "vs", text: "so again I have thought in these days to do good to Jerusalem and to the house of Judah. Don’t be afraid."} ]
        , 16: [ {class: "vs", text: "These are the things that you shall do: speak every man the truth with his neighbor. Execute the judgment of truth and peace in your gates,"} ]
        , 17: [ {class: "vs", text: "and let none of you devise evil in your hearts against his neighbor, and love no false oath: for all these are things that I hate,” says Yahweh."} ]
        , 18: [ {class: "vs", text: "The word of Yahweh of Armies came to me."} ]
        , 19: [ {class: "vs", text: "Yahweh of Armies says: “The fasts of the fourth, fifth, seventh, and tenth months shall be for the house of Judah joy and gladness, and cheerful feasts. Therefore love truth and peace.”"} ]
        , 20: [ {class: "vs", text: "Yahweh of Armies says: “Many peoples, and the inhabitants of many cities will yet come;"} ]
        , 21: [ {class: "vs", text: "and the inhabitants of one shall go to another, saying, ‘Let’s go speedily to entreat the favor of Yahweh, and to seek Yahweh of Armies. I will go also.’"} ]
        , 22: [ {class: "vs", text: "Yes, many peoples and strong nations will come to seek Yahweh of Armies in Jerusalem, and to entreat the favor of Yahweh.”"} ]
        , 23: [ {class: "vs", text: "Yahweh of Armies says: “In those days, ten men will take hold, out of all the languages of the nations, they will take hold of the skirt of him who is a Jew, saying, ‘We will go with you, for we have heard that God is with you.’”"} ]
        }
  , 9:  { 1:  [ {class: "endpara", text: "A revelation."}
              , {class: "endpara", text: "Yahweh’s word is against the land of Hadrach,"}
              , {class: "q2", text: "and will rest upon Damascus;"}
              , {class: "q", text: "for the eye of man"}
              , {class: "q2", text: "and of all the tribes of Israel is toward Yahweh;"}
              ]
        , 2:  [ {class: "q", text: "and Hamath, also, which borders on it;"}
              , {class: "q2", text: "Tyre and Sidon, because they are very wise."}
              ]
        , 3:  [ {class: "q", text: "Tyre built herself a stronghold,"}
              , {class: "q2", text: "and heaped up silver like the dust,"}
              , {class: "q2", text: "and fine gold like the mire of the streets."}
              ]
        , 4:  [ {class: "q", text: "Behold, the Lord will dispossess her,"}
              , {class: "q2", text: "and he will strike her power in the sea;"}
              , {class: "q2", text: "and she will be devoured with fire."}
              ]
        , 5:  [ {class: "q", text: "Ashkelon will see it, and fear;"}
              , {class: "q2", text: "Gaza also, and will writhe in agony;"}
              , {class: "q2", text: "as will Ekron, for her expectation will be disappointed;"}
              , {class: "q2", text: "and the king will perish from Gaza,"}
              , {class: "q2", text: "and Ashkelon will not be inhabited."}
              ]
        , 6:  [ {class: "q", text: "Foreigners will dwell in Ashdod,"}
              , {class: "q2", text: "and I will cut off the pride of the Philistines."}
              ]
        , 7:  [ {class: "q", text: "I will take away his blood out of his mouth,"}
              , {class: "q2", text: "and his abominations from between his teeth;"}
              , {class: "q", text: "and he also will be a remnant for our God;"}
              , {class: "q2", text: "and he will be as a chieftain in Judah,"}
              , {class: "q2", text: "and Ekron as a Jebusite."}
              ]
        , 8:  [ {class: "q", text: "I will encamp around my house against the army,"}
              , {class: "q2", text: "that no one pass through or return;"}
              , {class: "q2", text: "and no oppressor will pass through them any more:"}
              , {class: "q2", text: "for now I have seen with my eyes."}
              ]
        , 9:  [ {class: "q", text: "Rejoice greatly, daughter of Zion!"}
              , {class: "q2", text: "Shout, daughter of Jerusalem!"}
              , {class: "q", text: "Behold, your King comes to you!"}
              , {class: "q2", text: "He is righteous, and having salvation;"}
              , {class: "q2", text: "lowly, and riding on a donkey,"}
              , {class: "q2", text: "even on a colt, the foal of a donkey."}
              ]
        , 10: [ {class: "q", text: "I will cut off the chariot from Ephraim,"}
              , {class: "q2", text: "and the horse from Jerusalem;"}
              , {class: "q", text: "and the battle bow will be cut off;"}
              , {class: "q2", text: "and he will speak peace to the nations:"}
              , {class: "q2", text: "and his dominion will be from sea to sea,"}
              , {class: "q2", text: "and from the River to the ends of the earth."}
              ]
        , 11: [ {class: "q", text: "As for you also,"}
              , {class: "q2", text: "because of the blood of your covenant,"}
              , {class: "q2", text: "I have set free your prisoners from the pit in which is no water."}
              ]
        , 12: [ {class: "q", text: "Turn to the stronghold, you prisoners of hope!"}
              , {class: "q2", text: "Even today I declare that I will restore double to you."}
              ]
        , 13: [ {class: "q", text: "For indeed I bend Judah as a bow for me."}
              , {class: "q2", text: "I have filled the bow with Ephraim;"}
              , {class: "q", text: "and I will stir up your sons, Zion,"}
              , {class: "q2", text: "against your sons, Greece,"}
              , {class: "q2", text: "and will make you like the sword of a mighty man."}
              ]
        , 14: [ {class: "q", text: "Yahweh will be seen over them;"}
              , {class: "q2", text: "and his arrow will go flash like lightning;"}
              , {class: "q2", text: "and the Lord Yahweh will blow the trumpet,"}
              , {class: "q2", text: "and will go with whirlwinds of the south."}
              ]
        , 15: [ {class: "q", text: "Yahweh of Armies will defend them;"}
              , {class: "q2", text: "and they will destroy and overcome with sling stones;"}
              , {class: "q2", text: "and they will drink, and roar as through wine;"}
              , {class: "q2", text: "and they will be filled like bowls,"}
              , {class: "q2", text: "like the corners of the altar."}
              ]
        , 16: [ {class: "q", text: "Yahweh their God will save them in that day as the flock of his people;"}
              , {class: "q2", text: "for they are like the jewels of a crown,"}
              , {class: "q2", text: "lifted on high over his land."}
              ]
        , 17: [ {class: "q", text: "For how great is his goodness,"}
              , {class: "q2", text: "and how great is his beauty!"}
              , {class: "q", text: "Grain will make the young men flourish,"}
              , {class: "q2", text: "and new wine the virgins."}
              ]
        }
  }